INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(1, TIMESTAMP '2019-08-14 15:46:34.871', TIMESTAMP '2019-08-14 15:46:39.4', '42', STRINGDECODE('\uac15\uc6d0\ub3c4 \ucd98\ucc9c\uc2dc\uc640 \ud64d\ucc9c\uad70\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c, \uc0b0 \uc815\uc0c1\uc5d0 \uc11c\uba74 \ud0c1 \ud2b8\uc778 \uc2dc\uc57c\uc640 \ubc1c \uc544\ub798\ub85c \ud3bc\uccd0\uc9c4 \uc18c\uc591\ud638\uc758 \ud48d\uacbd\uc774 \ub4f1\uc0b0\uac1d\ub4e4\uc758 \ubc1c\uac78\uc74c\uc744 \uba48\ucd94\uac8c \ud558\ub294 \uacf3\uc774\ub2e4. \uc0b0\uc790\ub77d\uc744 \uac10\uc2f8 \ud750\ub974\ub294 \uc870\uadf8\ub9c8\ud55c \ud3ed\ud3ec\uc758 \ubb3c\uc18c\ub9ac\uac00 \uc0ac\ub78c\ub4e4\uc758 \ub9c8\uc74c\uc744 \uc2dc\uc6d0\ud558\uac8c \ud574\uc8fc\ub294 \uac00\ub9ac\uc0b0\uc740 \uc6b0\uac70\uc9c4 \uc232\uacfc \ub178\uc1a1\ub4e4\uc774 \uc5b4\ub8e8\ub7ec\uc838 \ube44\uacbd\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4.\r\n\uc18c\uc591\ud638 \ucabd\uc73c\ub85c \ud558\uc0b0\ud558\uc5ec \ubb3c\ub178\ub9ac \uc120\ucc29\uc7a5\uc5d0\uc11c \uc720\ub78c\uc120\uc744 \ud0c0\uace0 \uc18c\uc591\ub310 \uc120\ucc29\uc7a5\uae4c\uc9c0 \ub098\uc62c \uc218 \uc788\uc5b4, \uc0b0\ud589\ub3c4 \ud558\uace0 \ubc30\ub97c \ud0c0\ub294 \uc7ac\ubbf8\uac00 \uc788\ub2e4.<BR>\uc0b0\uc774\ub984\uc778 \uac00\ub9ac\ub294 ''\ub2e8\uc73c\ub85c \ubb36\uc740 \uace1\uc2dd\uc774\ub098 \ub554\ub098\ubb34 \ub530\uc704\ub97c \ucc28\uace1\ucc28\uace1 \uc313\uc544\ub454 \ud070 \ub354\ubbf8''\ub97c \ub73b\ud558\ub294 \uc21c\uc6b0\ub9ac\ub9d0\ub85c\uc11c, \uc0b0\ubd09\uc6b0\ub9ac\uac00 \ub178\uc801\uac00\ub9ac\ucc98\ub7fc \uace0\uae54 \ubaa8\uc591\uc73c\ub85c \uc0dd\uae34 \ub370\uc11c \uc720\ub798\ud55c\ub2e4. \ud0dc\ubc31\uc0b0\ub9e5 \uc911 \ub0b4\uc9c0(\u5167\u5730) \uc0b0\ub9e5\uc758 \uc77c\ubd80\ub97c \uc774\ub8ec\ub2e4. \uc81c1\ubd09 \ub0a8\ucabd\uc5d0\uc11c \ud64d\ucc9c\uac15\uc774 \ubc1c\uc6d0\ud558\uc5ec \ubd81\ud55c\uac15\uc758 \uc9c0\ub958\uc778 \uc18c\uc591\uac15\uc758 \uc218\uc6d0(\u6c34\u6e90)\uc744 \uc774\ub8ec\ub2e4.\ub2a5\uc120\uc740 \uc644\ub9cc\ud55c \ud3b8\uc774\ub098, \uc815\uc0c1 \uc77c\ub300\ub294 \uc881\uc740 \ud611\uace1\uc744 \uc0ac\uc774\uc5d0 \ub454 3\uac1c\uc758 \uc554\ubd09\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc73c\uba70 \uac15\uc6d0 \uc81c1\uc758 \uc804\ub9dd\ub300\ub77c\uace0 \ud560 \ub9cc\ud07c \uc870\ub9dd\uc774 \ub6f0\uc5b4\ub098, \uc18c\uc591\ud638\ub97c \ube44\ub86f\ud558\uc5ec \ubd81\ucabd\uc73c\ub85c \ud5a5\ub85c\ubd09\uc5d0\uc11c \uc124\uc545\uc0b0\uc744 \uac70\uccd0 \uc624\ub300\uc0b0\uc73c\ub85c \ud798\ucc28\uac8c \ubed7\uc5b4\ub098\uac04 \ubc31\ub450\ub300\uac04 \ub4f1 \uac15\uc6d0 \ub0b4\ub959\uc758 \uace0\uc0b0\uc900\ub839\uc774 \ud55c\ub208\uc5d0 \ubcf4\uc778\ub2e4. \uc815\uc0c1 \ubd80\uadfc\uc5d0\uc11c\ub294 \uc18c\uc591\ud638\ub85c \uac08 \uc218 \uc788\ub294 \uac00\uc0bd\uace0\uac1c\uac00 \uc788\ub294\ub370, \uadf8 \ud615\ud0dc\uac00 \uacc4\ub2e8\uc2dd \ubd84\uc9c0\ud615\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub2e4.<BR><BR>\ubd81\ucabd \uc0b0\ub85d\uc740 \uc18c\uc591\ud638\uc5d0 \ubbf8\uce58\uace0 \ub3d9\ucabd \uc0b0\ub85d\uc5d0 \ud64d\ucc9c\uad11\uc0b0\uc774 \uc788\ub2e4. \uc0b0\uae30\uc2ad\uc5d0\ub294 \uc232\uc774 \uc6b0\uac70\uc838 \uc788\uace0 \uac16\uac00\uc9c0 \uae30\uc554\uad34\uc11d\uc774 \uc990\ube44\ud558\uba70, \uc0b0 \uc815\uc0c1\uacfc \uacc4\uace1\uc5d0\ub294 \ud5a5\ud1a0 \uc218\uc885\uc778 \ucc38\ub098\ubb34\uac00 \uc8fc\uc885\uc744 \uc774\ub8e8\uba70 \uc790\ub77c\uace0 \uc788\ub2e4. \uc544\ub798\ucabd\uc5d0\ub294 \ub450\ub985\ub098\ubb34\u00b7\ucca0\ucb49\u00b7\uc2f8\ub9ac\ub098\ubb34\u00b7\uc0b0\ucd08\ub098\ubb34 \ub4f1 \uad00\ubaa9\ub958\uc640 \uc57d\uc6a9\uc73c\ub85c \uc0ac\uc6a9\ub418\ub294 \ud53c\ub098\ubb3c\u00b7\uc560\uae30\ub625\ud480\u00b7\uc591\uc9c0\uaf43 \ub4f1 \uc57c\uc0dd\ud654\uac00 \uc11c\uc2dd\ud558\uace0 \uc788\ub2e4. \uac15\uc6d0\ub3c4\uc5d0\uc11c \uc9c4\ub2ec\ub798\uac00 \uac00\uc7a5 \ub9ce\uc774 \ud53c\ub294 \uc0b0\uc73c\ub85c\ub3c4 \uc720\uba85\ud558\ub2e4.\r\n1998\ub144 \uac00\ub9ac\uc0b0 \uc790\uc5f0\ud734\uc601\ub9bc\uc73c\ub85c \uac1c\uc7a5\ub418\uc5b4 \ud1b5\ub098\ubb34\uc9d1\u00b7\uc57c\uc601\uc7a5\u00b7\uccb4\uc721\uc2dc\uc124 \ub4f1 \ud3b8\uc758\uc2dc\uc124\uc774 \uc788\ub2e4. \ud734\uc591\ub9bc \uc785\uad6c\uc5d0\ub294 \ub192\uc774 8m\uc758 \uc6a9\uc18c\ud3ed\ud3ec\uac00 \uc788\uace0, \uc8fc\ubcc0\uc5d0 \uc2a4\ud0a4\uc7a5\u00b7\uc628\ucc9c\u00b7\uc218\ud0c0\uc0ac\u00b7\ud314\ubd09\uc0b0 \ub4f1\uc758 \uad00\uad11\uc9c0\uac00 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uac00\ub9ac\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-\uc0bc\uac70\ub9ac-\ubb34\uc1e0\ub9d0\uc7ac-\uc815\uc0c1-\uac00\uc0bd\uace0\uac1c-\uc18c\uc591\ud638 \ubb3c\ub85c\ub9ac (\ucd1d 4\uc2dc\uac04 30\ubd84)<BR>\u2461 \uac00\ub9ac\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-\uac00\uc0bd\uace0\uac1c-\uc815\uc0c1-\uac00\ub9ac\uc0b0\uad50-\ucca0\uc815\ub9ac (\ucd1d 4\uc2dc\uac04 30\ubd84)'), '37.817645', '127.715623', STRINGDECODE('\uac15\uc6d0\ub3c4 \ucd98\ucc9c\uc2dc \ubd81\uc0b0\uba74\u318d\ub3d9\uba74, \ud64d\ucc9c\uad70 \ub450\ucd0c\uba74\u318d\ud654\ucd0c\uba74'), 2213100200, 1051, STRINGDECODE('\uac00\ub9ac\uc0b0'), STRINGDECODE('&lt;\uc18c\uc591\uac15 \ube44\uacbd \ub2f4\uc740 \uc2e0\ube44\ud55c \uc554\ubd09&gt;<BR>\uac00\ub9ac\uc0b0\uc740 \uac15\uc6d0\ub3c4 \ud64d\ucc9c\uad70 \ub450\ucd0c\uba74\uacfc \ud654\ucd0c\uba74, \ucd98\ucc9c\uc2dc \ubd81\uc0b0\uba74, \ub3d9\uba74\uc5d0 \uac78\uccd0\uc788\ub294 \ud574\ubc1c 1050.9m\uc758 \uc0b0\uc774\ub2e4. \uc815\uc0c1\ubd80\ub97c \uc774\ub8e8\ub294 \uc0b0\uc138\uac00 \ub9c8\uce58 \uace1\uc2dd\uc744 \ucc28\uace1\ucc28\uace1 \uc313\uc544\ub454 \u2018\ub09f\uac00\ub9ac\u2019\uc640 \ub2ee\uc558\ub2e4\uace0 \ud558\uc5ec \u2018\uac00\ub9ac\uc0b0\u2019\uc774\ub77c \uc774\ub984 \ubd99\uc5ec\uc84c\ub2e4\uace0 \uc804\ud55c\ub2e4. \ub300\uccb4\ub85c \uc721\uc0b0\uc744 \uc774\ub8e8\uace0 \uc788\ub294 \uc0b0\uc138\uc774\ub098 \uc815\uc0c1\ubd80\ub294 \uac70\ub300\ud55c 3\uac1c\uc758 \ubc14\uc704\ubd09\uc6b0\ub9ac\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub2e4. \ubd81\uc11c\ucabd\uc5d0 \uc790\ub9ac\uc7a1\uc740 \uc18c\uc591\ud638\uc758 \uc870\ub9dd\uc774 \uc77c\ud488\uc774\uba70, \uc11c\ucabd\uc73c\ub85c\ub294 \ub300\ub8e1\uc0b0 \uc5b4\ub984\uc774 \ubcf4\uc778\ub2e4. \ub610 \uc815\uc0c1 \uc544\ub798\uc758 \ubc14\uc704 \uc808\ubcbd\uc5d0\uc11c \uc0ac\uc2dc\uc0ac\ucca0 \uc19f\ub294 \uc11d\uac04\uc218\uac00 \uc720\uba85\ud558\ub2e4. \uac00\ub9ac\uc0b0\uc740 \ud64d\ucc9c9\uacbd \uc911 2\uacbd\uc73c\ub85c \uaf3d\ud78c\ub2e4.'), STRINGDECODE('\uac15\uc6d0\ub3c4\uc5d0\uc11c \uc9c4\ub2ec\ub798\uac00 \uac00\uc7a5 \ub9ce\uc774 \ud53c\ub294 \uc0b0\uc73c\ub85c \uc54c\ub824\uc838 \uc788\uace0, \ucc38\ub098\ubb34 \uc911\uc2ec\uc758 \uc6b8\ucc3d\ud55c \uc0b0\ub9bc\uacfc \ubd80\ub4dc\ub7ec\uc6b4 \uc0b0\uc904\uae30 \ub4f1 \uc6b0\ub9ac\ub098\ub77c \uc0b0\uc758 \uc804\ud615\uc801\uc778 \ubaa8\uc2b5\uc744 \uac16\ucd94\uace0 \uc788\uc73c\uba70, \ud64d\ucc9c\uac15\uc758 \ubc1c\uc6d0\uc9c0 \ubc0f \uc18c\uc591\uac15\uc758 \uc218\uc6d0(\u6c34\u6e90)\uc744 \uc774\ub8e8\uace0 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\uc554\ubd09\uc774 \uc19f\uc544\uc788\ub294 \uc815\uc0c1\uc5d0\uc11c \uc18c\uc591\ud638\ub97c \uc870\ub9dd\ud560 \uc218 \uc788\uace0, \uc57c\uc0dd\ud654\uac00 \ub9ce\uc774 \uc11c\uc2dd\ud558\uc5ec \uc790\uc5f0\ud559\uc2b5\uad00\ucc30\uc5d0\ub3c4 \uc88b\uc740 \uc5ec\uac74\uc744 \uac16\ucd94\uace0 \uc788\uc74c. \"98\ub144 \uac15\uc6d0\ub3c4\uc5d0\uc11c \uc790\uc5f0\ud734\uc591\ub9bc\uc73c\ub85c \uc9c0\uc815.'), STRINGDECODE('\uc18c\uc591\ud638\uc5d0\uc11c \ud3ed\ud3ec \ub530\ub77c \uc815\uc0c1\uae4c\uc9c0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc0b0\ud589 \ub4e4\uba38\ub9ac\uc778 \uac00\ub9ac\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e033-435-6034~5)\uc744 \uc774\uc6a9\ud558\ub294 \uac83\uc774 \ud3b8\ub9ac\ud558\uba70 \ucd5c\ub300 \uc218\uc6a9\uc778\uc6d0\uc740 1500\uc5ec\uba85\uc774\ub2e4. <BR>\uadf8\uc678 \ub300\uba85\ud64d\ucc9c\uc2a4\ud0a4\uc7a5\ucf58\ub3c4 \u260e033-434-8311, \ub300\uba85\ube44\ubc1c\ub514\ud30c\ud06c \uc720\uc2a4\ud638\uc2a4\ud154 \u260e033-434-2188?<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud64d\ucc9c\uad70 \uacbd\uc81c\uad00\uad11\uacfc \u260e033-430-2544, \ud64d\ucc9c\uad70 \ub450\ucd0c\uba74\uc0ac\ubb34\uc18c \u260e033-435-3327, \uac00\ub9ac\uc0b0\ud734\uc591\ub9bc\uc0ac\ubb34\uc18c \u260e033-435-6034'), STRINGDECODE('\ud64d\ucc9c\uc5d0\uc11c \uc5ed\ub0b4\ub9ac, \ucc9c\ud604\ub9ac\ud589 \ubc84\uc2a4\ub97c \uc774\uc6a9\ud574 \uac00\ub9ac\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc\uc5d0\uc11c \ub0b4\ub9b0\ub2e4.<BR>- \ud64d\ucc9c\u2192\uc5ed\ub0b4\ub9ac 60\ubd84 \uac04\uaca9 \uc6b4\ud589<BR>- \ud64d\ucc9c\u2192\ucc9c\ud604\ub9ac 1\uc77c 10\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(2, TIMESTAMP '2019-08-14 15:46:34.896', TIMESTAMP '2019-08-14 15:46:40.02', '42', STRINGDECODE('\uac00\ub9ac\uc655\uc0b0\uc740 \ud0dc\ubc31\uc0b0\uc758 \uc9c0\ubd95\uc5ed\ud65c\uc744 \ud558\uace0 \uc788\uc73c\uba70 \uacbd\uc0ac\ub3c4\uac00 \uc644\ub9cc\ud55c \ub4f1\uc0b0\ub85c\ub85c \uc720\uba85\ud558\ub2e4. \uc0b0 \ub2a5\uc120\uc5d0\ub294 \uace0\uc0b0\uc2dd\ubb3c\uc778 \uc8fc\ubaa9, \uc7a3\ub098\ubb34, \ub2e8\ud48d\ub098\ubb34\ub4f1 \uac01\uc885 \uc218\ubaa9\uc774 \uc6b8\ucc3d\ud558\uba70 \uc0b0\uc0bc\ub4f1 \ub9ce\uc740 \uc0b0\uc57d\ucd08\uac00 \uc790\ub77c\uace0 \uc788\ub2e4.<BR><BR>\ud68c\ub3d9\ub9ac \uc785\uad6c\uc5d0\ub294 \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc870\uc131\ub418\uc5b4 \uac01\uc885 \ud3b8\uc758\uc2dc\uc124\uc774 \uc124\uce58\ub418\uc5b4 \uc788\uace0 \ub4f1\uc0b0\ub85c \uc0b0\ud589\uc2dc\uac04\uc740 \ucd1d 6\uc2dc\uac04 \uc815\ub3c4 \uc18c\uc694\ub41c\ub2e4. \uad70\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815, \uac1c\ubc1c\uacc4\ud68d\uc5d0 \uc788\uc73c\uba70 \uc219\uc554\ubc29\uba74 \uc785\uad6c\ub294 \uc57d 4km \uad6c\uac04\uc5d0 \ucca0\ucb49\uc774 \ubc00\uc9d1 \uc790\uc0dd\ud558\uace0 \uc788\ub2e4.'), STRINGDECODE('\uac00\ub9ac\uc655\uc0b0 \uc790\uc5f0\ud734\uc591\ub9bc-\uc548\ubd80 \uc804 \uac08\ub9bc\uae38-\uc548\ubd80-\uc0c1\ubd09 \uc815\uc0c1 (\ucd1d 2\uc2dc\uac04 10\ubd84)'), '37.380417', '128.622825', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc815\uc120\uad70 \uc815\uc120\uc74d \ud68c\ub3d9\ub9ac\u318d\ubd81\ud3c9\uba74, \ud3c9\ucc3d\uad70 \uc9c4\ubd80\uba74'), 2211050100, 1562, STRINGDECODE('\uac00\ub9ac\uc655\uc0b0'), STRINGDECODE('\uc815\uc120\uc544\ub9ac\ub791 \ud750\ub974\ub294 \uc815\uc120\uc758 \uc9c0\ubd95 <BR>\uac15\uc6d0\ub3c4 \ud3c9\ucc3d\uad70\uacfc \uc815\uc120\uad70\uc740 \uc774 \uace0\uc7a5 \uc9c0\ubd95 \uc5ed\ud560\uc744 \ud558\uace0 \uc788\ub294 \uac00\ub9ac\uc655\uc0b0\uc744 \uc911\uc2ec\uc73c\ub85c \ud5d8\ub09c\ud55c \uace0\uc0b0\uc900\ub839\uc774 \uc774\ub8e8\uc5b4\uc9c4\ub2e4\uace0 \ud574\ub3c4 \uacfc\uc5b8\uc774 \uc544\ub2c8\ub2e4. \uc815\uc120\uc758 \uc8fc\uc0b0 \uac00\ub9ac\uc655\uc0b0\uc740 \uac08\uc655(\u845b\u738b)\uc774 \ub09c\uc744 \ud53c\ud558\uc5ec \uc228\uc5b4\ub4e0 \uacf3\uc774\ub77c \ud558\uc5ec \uac08\uc655\uc0b0(\u845b\u738b\u5c71)\uc774\ub77c \ubd88\ub9ac\ub2e4\uac00 \uc77c\uc81c\uc2dc\ub300\ub97c \uac70\uce58\uba74\uc11c \uac00\ub9ac\uc655\uc0b0(\u52a0\u91cc\u738b\u5c71)\uc73c\ub85c \ubd88\ub9ac\uace0 \uc788\ub2e4.<BR><BR>\uac00\ub9ac\uc655\uc0b0 \uc0c1\ubd09 \uc11c\ucabd\uc758 \uc911\uc655\uc0b0(1381.4m)\uacfc \uc0c1\ubd09 \uc0ac\uc774 \uc548\ubd80\uc778 \ub9c8\ud56d\uce58(\u99ac\u9805\u5cd9)\uc5d0\ub294 \uad6d\ub0b4 \uc720\uc77c\uc758 \u2018\uac15\ub989\ubd80\uc0bc\uc0b0\ubd09\ud45c(\u6c5f\u9675\u5e9c\u8518\u5c71\u5c01\u6a19)\u2019\ub77c \uc0c8\uaca8\uc9c4 \ube44\uac00 \uc138\uc6cc\uc838 \uc788\ub2e4. \uc774\ub294 \uc77c\ubc18\uc778\ub4e4\uc758 \ucc44\uc0bc\uc744 \uae08\uc9c0\ud558\ub294 \uad6c\uc5ed\uc744 \ub73b\ud558\ub294 \uac83\uc73c\ub85c \uac00\ub9ac\uc655\uc0b0\uc774 \uc870\uc120\uc2dc\ub300 \ub54c \uc0b0\uc0bc\uc758 \uc8fc\uc0b0\uc9c0\uc600\uc74c\uc744 \uc54c \uc218 \uc788\ub294 \ub300\ubaa9\uc774\ub2e4. \uc0c1\ubd09\uc744 \uc911\uc2ec\uc73c\ub85c \ub0a8\ub3d9\ucabd\uc73c\ub85c \uc911\ubd09(1436m), \ud558\ubd09(1381.7m)\uc5d0 \uc774\ub974\uae30\uae4c\uc9c0 \uc721\uc911\ud55c \uc0b0\uc138\uac00 \ubd80\ub4dc\ub7fd\uac8c \uc774\uc5b4\uc9c4\ub2e4.'), STRINGDECODE('\uac00\ub9ac\uc655\uc0b0 8\uacbd\uc774 \uc804\ud574\uc9c8 \ub9cc\ud07c \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0, \ud65c\uc5fd\uc218 \uadf9\uc0c1\ub9bc\uc774 \ubd84\ud3ec\ud574 \uc788\uc73c\uba70, \uc804\uad6d\uc801\uc778 \uc0b0\ub098\ubb3c \uc790\uc0dd\uc9c0\ub85c \uc720\uba85. \ud2b9\ud788 \ubc31\ub450\ub300\uac04\uc758 \uc911\uc2ec\uc73c\ub85c \uc8fc\ubaa9\uad70\ub77d\uc9c0\uac00 \uc788\uc5b4 \uc0b0\ub9bc\uc720\uc804\uc790\uc6d0\ubcf4\ud638\ub9bc\uacfc \uc790\uc5f0\ud734\uc591\ub9bc\uc73c\ub85c \uc9c0\uc815\ub418\ub294 \ub4f1 \uacbd\uad00\u00b7\uc0dd\ud0dc\uc801\uc73c\ub85c \uac00\uce58\uac00 \ud070 \uc810\uc5d0\uc11c \uc120\uc815\ub3d9\uac15(\u6771\u6c5f)\uc5d0 \ud758\ub7ec\ub4dc\ub294 \uc624\ub300\ucc9c\uacfc \uc870\uc591\uac15\uc758 \ubc1c\uc6d0\uc9c0\uc774\uba70 \uc11d\ud68c\uc554 \uc808\ub9ac\ub3d9\uad74\uc778 \uc5bc\uc74c\ub3d9\uad74\uc774 \uc720\uba85. \uc0b0\uc758 \uc774\ub984\uc740 \uadf8 \ubaa8\uc2b5\uc774 \ud070 \uac00\ub9ac(\ubcbc\ub098 \ub098\ubb34\ub97c \uc313\uc740 \ub354\ubbf8)\uac19\ub2e4\uace0 \ud558\uc5ec \uc720\ub798'), STRINGDECODE('\uc57d\ucd08 \ub9ce\uc740 \ud0dc\ubc31\uc0b0\uc758 \uc9c0\ubd95'), STRINGDECODE('\uac00\ub9ac\uc655\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc \u260e033-562-5833<BR>\uac00\ud3c9\ud5c8\ube0c\ucc1c\uc9c8\ubc29 \u260e033-332-2828<BR>\ucc9c\ub2f9\uace8\uc0b0\uc7a5 \u260e033-332-9943<BR>\uac00\ub9ac\uc655\uc0b0\uad00\uad11\ub18d\uc6d0 \u260e033-563-6100~1<BR><BR><BR><BR>\uc774\uc6a9\ubb38\uc758 : \uc815\uc120\uad70\uccad \uad00\uad11\ubb38\ud654\uacfc \u260e033-560-2569, \uac00\ub9ac\uc655\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e033-562-5833'), STRINGDECODE('\uc7a5\ud3c9(\uc7a5\ud3c9\uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110 \u260e033-332-4209)\uacfc \uc815\uc120(\uc815\uc120\ud130\ubbf8\ub110 \u260e033-563-1094)\uc774 \uae30\uc810\uc774\ub2e4. <BR>\ud558\uc548\ubbf8 5\ub9ac \ubc31\uc77c\ub3d9 \ubc84\uc2a4\uc885\uc810\uc5d0 \uac00\ud3c9\ub9e4\uc810(\u260e033-332-8622)\uc774 \uc788\uc73c\ub2c8 \ucd9c\ubc1c \uc804\uc5d0 \ubb38\uc758\ud558\uba74 \uc0c1\uc138\ud788 \uc54c \uc218 \uc788\ub2e4.<BR>- \uc7a5\ud3c9\u2192\ud558\uc548\ubbf8\ub9ac(\uac00\ud3c9\ud589) 1\uc77c 5\ud68c \uc6b4\ud589<BR>- \uc815\uc120\u2192\ud68c\ub3d9 1\uc77c 8\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(3, TIMESTAMP '2019-08-14 15:46:34.897', TIMESTAMP '2019-08-14 15:46:40.623', '48', STRINGDECODE('\uac00\uc57c\uc0b0\uc740 \uacbd\ub0a8 \ud569\ucc9c\uad70 \uac00\uc57c\uba74\uacfc \uac70\ucc3d\uad70 \uac00\ubd81\uba74, \uacbd\ubd81 \uc131\uc8fc\uad70 \uac00\ucc9c\uba74 \uc218\ub95c\uba74\uc744 \ud55c\ubab8\uc5d0 \ud488\uace0\uc788\ub2e4. \uc6b0\ub9ac\ub098\ub77c 12\ub300 \uba85\uc0b0\uc911\uc758 \ud558\ub098\ub85c \uc0b0\uc138\uac00 \ucc9c\ud558\uc5d0\uc11c \uc73c\ub738\uc774\uace0, \uc9c0\ub355\uc740 \ud574\ub3d9\uc5d0\uc11c \uc81c\uc77c\uc774\ub77c \ud558\uc5ec, \ub300\ud55c 8\uacbd\uc5d0 \uc18d\ud558\ub294 \uba85\uc0b0\uc774\ub2e4. \uac00\uc57c\uc0b0 \uc9c0\uc5ed\uc774 \uc61b\ub0a0 \uac00\uc57c\uad6d\uc774 \uc788\uc5c8\ub358 \uacf3\uc774\uace0 \uc774 \uc0b0\uc774 \uac00\uc57c\uad6d\uc5d0\uc11c \uac00\uc7a5 \ub192\uace0 \ud6cc\ub96d\ud55c \uc0b0\uc774\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uc790\uc5f0\uc2a4\ub7fd\uac8c \uac00\uc57c\uc758 \uc0b0\uc774\ub77c \ubd88\ub800\ub2e4\uace0 \ud55c\ub2e4. <BR><BR>\uac00\uc57c\uc0b0\uc740 \uc18c\uba38\ub9ac \uac19\ub2e4 \ud574\uc11c \uc6b0\ub450\uc0b0(\uc0b0 \uba38\ub9ac\uc758 \ud070 \ubc14\uc704 \uc544\ub798\uc5d0 \uc18c\uc758 \ucf54\ub77c\ub294 \ub73b\uc758 \uc6b0\ube44\uc815\uc774\ub780 \uc0d8\ub3c4 \uc788\ub2e4) \uc774\ub77c\ub294 \uc774\ub984\uc678\uc5d0 \uc0c1\uc655\uc0b0, \uc124\uc0b0, \uc911\ud5a5\uc0b0 \ub4f1\uc73c\ub85c\ub3c4 \ubd88\ub9ac\uc6cc\uc84c\ub2e4. ''\ud0dd\ub9ac\uc9c0''\uc5d0 \uae30\uc554\uad34\ubd09\uc744 \ubd88\uaf43\uc5d0 \ube44\uc720\ud558\uc5ec \uc11d\ud654\uc131(\u77f3\u706b\u661f)\uc774\ub77c\uace0 \ud558\uc600\ub294\ub370, \uac00\uc57c\uc0b0\uc740 \ubcf4\ub294 \ubc29\ud5a5\uc5d0 \ub530\ub77c\uc11c \ud55c\uc1a1\uc774 \uc5f0\uaf43\uc73c\ub85c\ub3c4 \ubcf4\uc600\ub2e4\uac00 \uc11c\ucabd\uc73c\ub85c \uacb9\uacb9\uc774 \uc19f\uc740 \uc0b0\ubd09\uc6b0\ub9ac \uc0ac\uc774\uc0ac\uc774 \ub610\ub294 \uace8\uc9dc\uae30\uc5d0 \ud558\uc580\uad6c\ub984\uc774 \uc7a0\uae30\uba74 \ub9ce\uc740 \uc12c\uc774 \ub5a0 \uc788\ub294 \ubc14\ub2e4\uac00 \ub41c\ub2e4.<BR><BR>\ud574\ubc1c 1\ucc9cm\uac00 \ub118\ub294 \uace0\ubd09\ub4e4\uc774 \ubd88\uaf43\ucc98\ub7fc \uc19f\uc544 \uc788\ub294 \uc790\ud0dc\ud558\uba70, \ubd81\uc5d0\uc11c \ub0a8\uc73c\ub85c \uc774\ub974\ub294 \uc7a5\ucf8c\ud55c \ub300\ub355\uc720\uc758 \uc904\uae30\uc640 \uc544\uc2a4\ub77c\ud788 \ub5a0\uc624\ub978 \uad6c\ub984\uc704\ub85c \uc9c0\ub9ac\uc0b0\uc744 \ubcfc \uc218 \uc788\ub294 \uc870\ub9dd, \ud64d\ub958\ub3d9\ucc9c\uc758 \uc544\ub984\ub2e4\uc6b4 \uacc4\uace1 \ub4f1 \uc7a5\uc911\ud558\uace0 \ub355\uc131\uc2a4\ub7ec\uc6b4 \ubaa8\uc2b5\uc744 \uacf3\uacf3\uc5d0\uc11c \ubcfc \uc218 \uc788\ub2e4.\uac00\uc57c\uc0b0 \uace0\uc2a4\ub77d\uc5d0 \uc11c\uba74 \uae08\uc624\uc0b0, \ud314\uacf5\uc0b0, \ube44\uc2ac\uc0b0\uc774 \ubcf4\uc774\uace0 \ud654\uc655\uc0b0, \uc790\uad74\uc0b0\uc774 \ubcf4\uc774\ub294\uac00 \ud558\uba74 \uac00\uae4c\uc774\uc5d0 \ub450\ubb34\uc0b0, \uc624\ub3c4\uc0b0, \ube44\uacc4\uc0b0, \uc870\uae08 \uba40\ub9ac\uc5d0 \ubc31\uc6b4\uc0b0, \uc218\ub3c4\uc0b0, \ub300\ub355\uc0b0 \ub4f1\uc774 \ubcf4\uc778\ub2e4.<BR><BR>\uc5ec\uae30\uc5d0 \uc6b0\ub9ac\ub098\ub77c 3\ubcf4 \uc0ac\ucc30\uc911 \ud558\ub098\uc778 \ud574\uc778\uc0ac\uac00 \ub4e4\uc5b4\uc11c\uace0, \uc870\uc120\uc2dc\ub300 \ub54c \uac15\ud654\ub3c4\uc5d0\uc11c \ud314\ub9cc\ub300\uc7a5\uacbd\uc744 \uc62e\uaca8\uc628 \uc774\ud6c4 \ubd88\uad50\uc758 \uc131\uc9c0\ub85c \uc790\ub9ac\uba54\uae40\ud558\uc600\ub2e4. \uadfc\ub798\uc5d0\ub294 \ubc31\ub828\uc554\uc5d0\uc11c \uc218\ub3c4\ud588\ub358 \uc131\ucca0 \uc2a4\ub2d8\uc73c\ub85c \uc778\ud558\uc5ec \ub354\uc6b1 \uc720\uba85\ud574\uc84c\ub2e4.'), STRINGDECODE('\u2460 \uccad\ub7c9\ub3d9-\uccad\ub7c9\uc0ac-\ub0a8\uc0b0\uc81c\uc77c\ubd09-\ub2e8\uc9c0\ubd09-\ud070\uc7ac(1\ubc15)-\ub9c8\ub839-\uae43\ub300\ubd09-\ub450\ub9ac\ubd09(2\ubc15)-\uc815\uc0c1-\uc11c\uc7a5\ub300-\ubc31\ub828\uc554 (\ucd1d 3\ubc154\uc77c)<BR>\u2461 \uce58\uc778\ub9ac-(30\ubd84)-\uc6a9\ud0d1\uc120\uc6d0-(50\ubd84)-\ub9c8\uc560\ubd88-(20\ubd84)-\ud1a0\uc2e0\uace8 \uc0bc\uac70\ub9ac-(1\uc2dc\uac04)-\uc815\uc0c1-(1\uc2dc\uac04)-\uc11c\uc7a5\ub300-(1\uc2dc\uac04 10\ubd84)-\uc2ec\uc6d0\uace8 \ub9e4\ud45c\uc18c (\ucd1d 4\uc2dc\uac04 50\ubd84)<BR>\u2462 \uccad\ub7c9\ub3d9-(50\ubd84)-\uccad\ub7c9\uc0ac-(20\ubd84)-\ub2a5\uc120\uc0bc\uac70\ub9ac-(1\uc2dc\uac04 20\ubd84)-\ub0a8\uc0b0\uc81c\uc77c\ubd09-(1\uc2dc\uac04)-\uce58\uc778\ub9ac (\ucd1d 3\uc2dc\uac04 30\ubd84)'), '35.566615', '128.16587', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ud569\ucc9c\uad70\u318d\uac70\ucc3d\uad70, \uacbd\uc0c1\ubd81\ub3c4 \uc131\uc8fc\uad70'), 2710070100, 1433, STRINGDECODE('\uac00\uc57c\uc0b0'), STRINGDECODE('&lt;\uc0b0\ubd09\u00b7\uacc4\uace1\ubbf8, \ubb38\ud654 \uac16\ucd98 \uac00\uc57c\uc758 \uc9c4\uc0b0&gt;<BR>\uacbd\uc0c1\ub0a8\ubd81\ub3c4 \uacbd\uacc4\uc5d0 \uc788\ub294 \uac00\uc57c\uc0b0\uc740 \uc6b0\ub9ac \uc0b0\uc904\uae30\uc758 \uc6a9\ub9c8\ub8e8 \ubc31\ub450\ub300\uac04 \uc704\uac00 \uc544\ub2c8\uba74\uc11c\ub3c4 1400m\uac00 \ub118\ub294 \ub4dc\ubb38 \uc0b0\uc774\ub2e4. \uc774\ub984\ucc98\ub7fc \uac00\uc57c\uc758 \uc9c4\uc0b0\uc73c\ub85c\uc11c \uadf8 \uc790\ub77d\uc5d0 \ub300\uac00\uc57c(\ud569\ucc9c\uc774\ub098 \uace0\ub839), \uace0\ub839\uac00\uc57c, \uc131\uc0b0\uac00\uc57c, \uc804\uae30 \uac00\uc57c\uc5f0\ub9f9\uc758 \uc77c\uc6d0\uc774\uc5c8\uc744 \uae40\ucc9c\uc744 \ud488\uc5c8\ub2e4. <BR><BR>\uc815\uc0c1 \uc77c\ub300\uc640 \ub0a8\uc0b0\uc774\ub77c \ud560 \ub9e4\ud654\uc0b0\uc774 \ubc14\uc704\ub85c \ub36e\uc5ec\uc788\uc5b4 \uba85\uc0b0\uc758 \ubc18\uc5f4\uc5d0 \ub4e4\uc5c8\ub2e4. \ub458\uc744 \uc774\uc5b4\uc8fc\ub294 \ub450\ub9ac\ubd09, \uae43\ub300\ubd09, \ub9c8\ub839, \ub2e8\uc9c0\ubd09\uc758 \uc721\uc0b0(\u8089\u5c71)\uc5d0\uc11c\ub294 \uc0ac\ucca0 \ub9d1\uc740 \ubb3c\uc744 \ud758\ub824\ubcf4\ub0b4 \uc790\uad81\ud615 \uc0b0\uc138 \uac00\uc6b4\ub370\ub97c \ud750\ub974\ub294 \ud64d\ub958\ub3d9\uc744 \uc808\uacbd\uc73c\ub85c \uafb8\uba70\ub193\uc558\ub2e4. \uc544\uc6b8\ub7ec \uc0b0 \ud55c\uac00\uc6b4\ub370 \uc138\uacc4\ubb38\ud654\uc720\uc0b0 \ud314\ub9cc\ub300\uc7a5\uacbd\uc744 \ub450\uc5c8\uc73c\ub2c8 \uc0b0\ubd09\uacfc \uacc4\uace1, \ubb38\ud654, \ubb34\uc5c7 \ud558\ub098 \ube60\uc9c4 \uac8c \uc5c6\ub2e4.<BR><BR>\uc774 \uac16\ucd98 \uc0b0\uc744 \uce58\uc778\ub9ac\uae4c\uc9c0 \ubc84\uc2a4\ub97c \ud0c0\uace0\uac00 \uc815\uc0c1\uc744 \ub2e4\ub140\uc628 \ub4a4 \ud574\uc778\uc0ac \uad6c\uacbd\uc73c\ub85c \ub05d\ub0b8\ub2e4\uba74 \ub108\ubb34 \ud5c8\ub9dd\ud560 \uac83\uc774\ub2e4. \uadf8\uac74 \ub9e4\ud654\uc0b0 \uccad\ub7c9\uc0ac\uc5d0\uc11c \uc2dc\uc791, \uc790\uad81\uc758 \uc6b4\ub450\ub97c \ud55c \ubc14\ud034 \ub3c8 \ub2e4\uc74c \ubcf4\uc544\ub3c4 \ub2a6\uc9c0 \uc54a\ub2e4. 26\ud0ac\ub85c\ubbf8\ud130 \uac70\ub9ac\uc5d0 2\ubc153\uc77c\uc774 \uac78\ub9ac\uc9c0\ub9cc \uacf5\ub4e4\uc778 \uac12\uc740 \ud55c\ub2e4. <BR><BR>\uadf8\ub807\uac8c \ub9cc\ub098\ub294 \ubc95\ubcf4\uc0ac\ucc30\uc758 \uad6d\ubcf4 \uc14b\uacfc \ubcf4\ubb3c \ub2e4\uc12f\uc774 \uc5ec\ub290 \ub208\uacfc\ub294 \ub2e4\ub974\uac8c \ub4e4\uc5b4\uc624\ub9ac\ub2c8...'), STRINGDECODE('\uc608\ub85c\ubd80\ud130 \uc6b0\ub9ac\ub098\ub77c\uc758 12\ub300 \uba85\uc0b0 \ub610\ub294 8\uacbd\uc5d0 \uc18d\ud558\ub294 \uc0b0\uc73c\ub85c\uc11c 72\ub144 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\uc73c\uba70, \ud2b9\ud788 95\ub144 \uc138\uacc4\ubb38\ud654\uc720\uc0b0\uc73c\ub85c \uc9c0\uc815\ub41c \uad6d\ubcf4 \ud314\ub9cc\ub300\uc7a5\uacbd\uacfc \ud574\uc778\uc0ac\uac00 \uc788\ub294 \ub4f1 \uc5ed\uc0ac\u00b7\ubb38\ud654\uc801 \uac00\uce58\uac00 \ub192\uc740 \uc810\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815,`\uac00\uc57c\uad6d\"\uc774 \uc788\uc5c8\ub358 \uacf3\uc73c\ub85c \uc804\ud574\uc9c0\uba70, `\ud0dd\ub9ac\uc9c0\"\uc5d0\uc11c\ub294 \uac00\uc57c\uc0b0\uc758 \uae30\uc554\uad34\ubd09\uc744 \ubd88\uaf43\uc5d0 \ube44\uc720\ud558\uc5ec \uc11d\ud654\uc131(\u77f3\u706b\u661f)\uc774\ub77c \ud558\uc600\uc74c. \uc0b0\uc704\uc5d0\uc11c\uc758 \uc870\ub9dd\uc774 \uc88b\uace0, \ud2b9\ud788 \uc6a9\ubb38\ud3ed\ud3ec\uc640 \ud64d\ub958\ub3d9 \uacc4\uace1 \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\uc5f0\uaf43\uc73c\ub85c \ud53c\uc5b4\uc624\ub978 \ubd88\uad50\uc758 \uc131\uc9c0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc219\ubc15\uc2dc\uc124\uc740 \uce58\uc778\ub9ac\uc5d0 \ub9ce\uace0 \ubc31\uc6b4\ub9ac\uc5d0\ub294 \uac70\uc758 \uc5c6\ub2e4. <BR>\ud574\uc778\uc0ac\uad00\uad11\ud638\ud154 \u260e063-582-7281<BR>\uc740\uc131\uc7a5\uc5ec\uad00 \u260e063-581-4644<BR>88\uc7a5\uc5ec\uad00 \u260e063-582-7651<BR>\ubc31\uc6b4\ub9ac \uac00\uc57c\uc0b0\uad6d\ubbfc\ud638\ud154 \u260e063-581-2064<BR><BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uac00\uc57c\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e055-932-7810'), STRINGDECODE('\ub300\uad6c\uac00 \uae30\uc810\uc774\ub2e4. <BR>\ub300\uad6c \ub300\uba8511\ub3d9\uc5d0 \uc788\ub294 \uc11c\ubd80\uc2dc\uc678\ubc84\uc2a4\uc815\ub958\uc7a5\uc5d0\uc11c \uc9c1\ud589\ubc84\uc2a4\uac00 40\ud68c \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(4, TIMESTAMP '2019-08-14 15:46:34.898', TIMESTAMP '2019-08-14 15:46:41.203', '31', ' ', STRINGDECODE('\u2460 \uc11d\ub0a8\uc0ac-\uc11d\ub0a8\uacc4\uace1-\uc300\ubc14\uc704-\uc815\uc0c1-\uc11c\ub0a8\ub989-1080\ubd09-\ub2a5\uc120 \uc0bc\uac70\ub9ac-812\ubd09-\ubc31\uc5f0\ud3ed\ud3ec-\ub9e4\ud45c\uc18c(\ucd1d 5\uc2dc\uac04)<BR>\u2461 \uc6b4\ubb38\uc7ac-\uadc0\ubc14\uc704-\uc0c1\uc6b4\uc0b0-\uc300\ubc14\uc704-\uc815\uc0c1-\ubc00\uc591\uace0\uac1c-\ud638\ubc15\uc18c\uacc4\uace1-\ub9e4\ud45c\uc18c(\ucd1d 4\uc2dc\uac04)<BR>\u2462 \ubd88\ub2f9\ub9c8\uc744-\ub3d9\ubd81\ub989-\uadc0\ubc14\uc704-\uc0c1\uc6b4\uc0b0-\uc300\ubc14\uc704-\uc815\uc0c1-\uc11d\ub0a8\ub839-\uc1e0\uc810\uace8-\ud638\ubc15\uc18c-\ub9e4\ud45c\uc18c(\ucd1d 6\uc2dc\uac04 5\ubd84)'), '35.64736', '128.734012', STRINGDECODE('\uc6b8\uc0b0\uad11\uc5ed\uc2dc, \uacbd\uc0c1\ubd81\ub3c4 \uccad\ub3c4\uad70, \uacbd\uc0c1\ub0a8\ub3c4 \ubc00\uc591\uc2dc'), 1704060100, 1241, STRINGDECODE('\uac00\uc9c0\uc0b0'), STRINGDECODE('&lt;\uac1c\uc131 \uc5c6\ub294 \uc601\ub0a8\uc54c\ud504\uc2a4 \uadf8 \ucd5c\uace0\ubd09&gt;<BR>\ubd80\uc0b0\uc0b0\uc545\uc778\ub4e4\uc774 \uc774\ub984\ubd99\uc778 \uc601\ub0a8\uc54c\ud504\uc2a4\ub294 \ub099\ub3d9\uc815\ub9e5 \uc544\ub7ab\ubd80\ubd84\uc758 \ucc9c \ubbf8\ud130 \ub118\ub294 \uc0b0\ub4e4\uc744 \ucd1d\uce6d\ud558\ub294 \uac1c\ub150\uc774\ub2e4. \uc774 \uac70\uc6b8\uc5d0 \ube44\uce5c \u4e0b\uc790 \ubaa8\uc591 \uc0b0\uc904\uae30\ub294 \uadf8\ub7ec\ub098 \uace0\ubd09\ub2a5\uc120\uc73c\ub85c \uc774\uc5b4\uc9c4 \ud0d3\uc5d0 \ub0b1\ub0b1\uc73c\ub85c\ub294 \uc369 \ube7c\uc5b4\ub09c \uac83\uc774 \uc5c6\ub2e4. <BR><BR>\ub2a5\uc120 \uc548\ubd80\uc640 \uc815\uc0c1\uc758 \uace0\ub3c4\ucc28\uac00 \uc5bc\ub9c8 \ub418\uc9c0 \uc54a\uc740 \ud0d3\ub3c4 \uc788\ub2e4. \uac1c\ud65c\uc9c0\uac00 \ud3bc\uccd0\uc9c4 \ub3d9\ucabd\uc5d0\uc11c \ubcfc \ub54c \uc0c1\ubd09\ub4e4\uc774 \ub2a5\uc120 \uc804\uba74\uc73c\ub85c \ud280\uc5b4\ub098\uc640 \uc788\uc9c0 \uc54a\uace0 \ub4a4\ub85c \ubb3c\ub7ec\ub098\uc788\ub2e4\ub294 \uc810\ub3c4 \uc774\uc720\ub2e4. \uadf8\ub798 \uac00\uc7a5 \ub0a8\ucabd\uc758 \uc601\ucde8\uc0b0\uc744 \uc81c\uc678\ud558\uace0\ub294 \ub2e4\ub4e4 \uc800 \ub192\uc740 \ub370\uc11c \uc57d\uac04\uc529 \uc624\ub974\ub0b4\ub9ac\ub294 \ub300\uc7a5\ubcbd\uc758 \uc77c\ubd80\uc77c \ub530\ub984\uc774\ub2e4. <BR><BR>\ucd5c\uace0\ubd09\uc774\ub77c\ub294 \uac00\uc9c0\uc0b0 \uc5ed\uc2dc \uc5ec\ub7ff \uc911\uc758 \ud558\ub098\uc5d0 \ub2e4\ub984\uc544\ub2c8\ub2e4. \uac8c\ub2e4\uac00, \ub2a5\ub3d9\uc0b0\ucc98\ub7fc \ub2a5\uc120\uc0bc\uac70\ub9ac\uc5d0 \uc790\ub9ac\uc7a1\uc544 \uac1c\uc131\uc774 \ub354\uc6b1 \ubb3b\ud600\ubc84\ub838\ub2e4. \ub3c4\ub9bd\uacf5\uc6d0\uc774\ub77c\uc9c0\ub9cc \ubd81\ucabd \uccad\ub3c4\ucabd\uc740 \ub610 \ub458\ubc16\uc5d0 \uc548 \ub418\ub294 \uc0b0\uae38\uc744 \ubaa8\ub450 \ub9c9\uc544\ubc84\ub9b0 \uc0c1\ud0dc\ub2e4.'), STRINGDECODE('\ubc31\ub450\ub300\uac04 \ub0a8\ub2e8\uc758 \uc911\uc2ec\uc73c\ub85c \"\uc601\ub0a8\uc54c\ud504\uc2a4\"\uc5d0\uc11c \uac00\uc7a5 \ub192\uc740 \uc0b0\uc73c\ub85c ''79\ub144 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\uc74c. \uc218\ub7c9\uc774 \ud48d\ubd80\ud55c \ud3ed\ud3ec\uc640 \uc544\ub984\ub2e4\uc6b4 \uc18c(\u6cbc)\uac00 \ub9ce\uace0, \ucc9c\uc5f0\uae30\ub150\ubb3c 224\ud638\uc778 \uc5bc\uc74c\uace8\uacfc \ub3c4\uc758\uad6d\uc0ac \uc0ac\ub9ac\ud0d1\uc778 \"8\uac01\uc6b4\ub2f9\ud615\ubd80\ub3c4(\ubcf4\ubb3c \uc81c369\ud638)\"\uac00 \ubcf4\uc874\ub418\uc5b4 \uc788\ub294 \uc11d\ub0a8\uc0ac(\u77f3\u5357\u5bfa)\uac00 \uc18c\uc7ac\ud558\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\ub2a5\uc120 \uacf3\uacf3\uc5d0 \ubc14\uc704\ubd09\uacfc \uc5b5\uc0c8\ubc2d\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uc788\uace0 \uc804\ub9dd\uc774 \uc88b\uc73c\uba70 \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc788\uc74c. \ubc00\uc591\uac15\uc758 \uc9c0\ub958\uc778 \uc0b0\ub0b4\ucc9c\uacfc \ubb34\uc801\ucc9c\uc758 \ubc1c\uc6d0\uc9c0\uc774\ub2e4.'), STRINGDECODE('\uc601\ub0a8\uc54c\ud504\uc2a4\uc758 \ucd5c\uace0\ubd09'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uc11d\ub0a8\uc0ac \ubd80\uadfc\uc5d0 \uc624\ub9ac\ubd88\uace0\uae30, \uc0b0\ucc44\ube44\ube54\ubc25\uc744 \ud558\ub294 \uba39\uac70\ub9ac\ucd0c\uacfc \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. <BR>\uc6b8\uc0b0\uac00\ub4e0(\u260e052-254-0077)<BR>\ud30c\ud06c\ubaa8\ud154(\u260e052-247-3030)<BR>\ub274\ud504\ub9b0\uc2a4\uc5ec\uad00(\u260e052-264-9190)<BR><BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758 <BR>\uac00\uc9c0\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e055-356-1915'), STRINGDECODE('\uc5b8\uc591\uc774 \uae30\uc810\uc774\ub2e4. \uc5b8\uc591\uc5d0\uc11c 20\ubd84 \uac04\uaca9\uc758 \uc11d\ub0a8\uc0ac\ud589 \ubc84\uc2a4(328\ubc88, 807\ubc88)\ub97c \uc774\uc6a9\ud55c\ub2e4. \uc5b8\uc591\uc73c\ub85c\ub294 \uc6b8\uc0b0\uc5d0\uc11c 15\ubd84, \ubd80\uc0b0\uc5d0\uc11c 20\ubd84 \uac04\uaca9\uc73c\ub85c \ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4. \ud638\ubc15\uc18c\ucabd\uc740 \uc11d\ub0a8\uc0ac\uc5d0\uc11c 10\ud68c \uc788\ub294 \ubc00\uc591\ud589 \uc9c1\ud589\ubc84\uc2a4\ub97c \ud0c0\uace0\uac00\ub2e4 \uc6b4\uc804\uc0ac\uc5d0\uac8c \ubd80\ud0c1\ud558\uac70\ub098 \ub0a8\uba85\ub9ac\uc5d0\uc11c \ub0b4\ub9b0\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(5, TIMESTAMP '2019-08-14 15:46:34.898', TIMESTAMP '2019-08-14 15:46:41.792', '41', STRINGDECODE('\uc608\ub85c\ubd80\ud130 \uc218\ub355\uc774 \ub6f0\uc5b4\ub098 \uc0bc\uac01\uc0b0\uc758 \uc601\uad11\uc744 \ub4b7\ubc1b\uce68\ud55c \uacbd\uae30 \uc624\uc545\uc911 \ubd81\uc545\uc5d0 \uc18d\ud558\ub294 \uc774 \uc0b0\uc740 \uc9c0\ud615\uc801 \ud2b9\uc131\uc73c\ub85c \uc0bc\uad6d\uc2dc\ub300\uc5d0\ubd80\ud130 \uc804\ub7b5\uc801\uc778 \uc694\ucda9\uc9c0\ub85c 6.25\uc804\uc7c1 \ub54c\uae4c\uc9c0 \uce58\uc5f4\ud55c \uaca9\uc804\uc774 \uc788\ub358 \uacf3\uc774\ub2e4. \uadf8\ub7f0 \uc5f0\uc720\ub85c \uc0b0\uc790\ub77d \uc124\ub9c8\uce58\uacc4\uace1 \uc591\ucabd\uc73c\ub85c \uc601\uad6d\uad70 \uc804\uc804\ube44\uc640 \ub300\ud55c\uc758\uc5f4\ub2e8 \uc804\uc801\ube44\uac00 \uc138\uc6cc\uc838 \uc788\ub2e4. \ubc14\uc704 \uc0ac\uc774\ub85c \uac80\uc740\ube5b\uacfc \ud478\ub978\ube5b\uc774 \ub3d9\uc2dc\uc5d0 \ud758\ub7ec\ub098\uc628\ub2e4 \ud558\uc5ec \uac10\uc545(\u7d3a\u5cb3), \uc989 \uac10\uc0c9\ubc14\uc704\ub77c\uace0 \ud558\uc600\ub2e4. <BR><BR>\ub610\ud55c \uc870\uc120 \uba85\uc885\ub54c \uad6c\uc6d4\uc0b0 \uccad\uc11d\uace8\uc744 \uac70\uc810\uc73c\ub85c \ud65c\uc57d\ud558\ub358 \uc784\uaebd\uc815\uc758 \uc911\uac04\uac70\uc810\uc774 \uc788\ub358 \uacf3\uc73c\ub85c \uadf8\uc640 \uc5f0\uad00\ub41c \uc9c0\uba85\uc774 \uacf3\uacf3\uc5d0 \uc0b0\uc7ac\ud574 \uc788\ub2e4. \uc815\uc0c1\uc5d0\ub294 \ud604\uc7ac\ub3c4 \uad70\uc0ac\uc2dc\uc124\uc774 \uc788\uc73c\uba70, \ubd81\ud55c\uc0b0 \uc9c4\ud765\uc655 \uc21c\uc218\ube44\uc640 \uc720\uc0ac\ud55c \uc77c\uba85 \uc090\ub730\ub300\uc655\ube44 \uc124\uc778\uadc0\ube44\uac00 \uc704\uce58\ud574 \uc788\ub2e4. \uc774 \ube44\ub294 \uc544\uc9c1\ub3c4 \uc804\uc7c1\ub54c \ud0c4\ud754\uc790\uad6d\uc744 \uac00\uc9c0\uace0 \uc788\uc73c\uba70 \ud30c\uc8fc\uad70 \ud5a5\ud1a0\uc720\uc801 \uc81c 8\ud638\ub85c \uc9c0\uc815\ub418\uc5b4 \uc788\ub2e4. \uc804\uccb4\uc801\uc778 \uc0b0\uc138\ub294 \uc554\ub989\uacfc \uc791\uc740 \uc554\ubd09\ub4e4\uc774 \uc870\ud654\ub97c \uc774\ub8e8\uace0 \uc788\uc73c\uba70 \uac04\uac04\uc774 \uc808\ubcbd\uc9c0\ub300\uac00 \uc788\uc73c\ubbc0\ub85c \uc8fc\uc758\ub97c \uc694\ud55c\ub2e4.<BR><BR>\uc6d0\ub798 \uac10\uc545\uc0ac, \uc6b4\uacc4\uc0ac, \ubc94\ub95c\uc0ac, \uc6b4\ub9bc\uc0ac \ub4f1\uc758 4\uac1c \uc0ac\ucc30\uc774 \uc788\uc5c8\ub2e4\ub294\ub370 \ud604\uc7ac\ub294 1970\ub144 \uc61b \uc6b4\uacc4\uc0ac \ud130\uc5d0 \uc7ac\ucc3d\uac74\ud55c \ubc94\ub95c\uc0ac\ub9cc \ub0a8\uc544 \uc788\ub2e4. <BR>\uc11c\uc6b8\uc5d0\uc11c \uadf8\ub9ac \uba40\uc9c0 \uc54a\uc73c\uba70, \uc758\uc815\ubd80 \ubd81\ucabd \ud68c\ucc9c\uc5d0\uc11c \uc591\uc8fc\uc2dc \ub0a8\uba74\uc744 \uc9c0\ub098 \uc124\ub9c8\ub9ac\ub97c \uac70\uccd0 \uac10\uc545\uc0b0 \uacc4\uace1\uc744 \ub530\ub77c \ub4e4\uc5b4\uac00\uba74 \ub192\uc774 20\uc5ec \ubbf8\ud130\uc5d0 \ub2ec\ud558\ub294 \uc6b4\uacc4\ud3ed\ud3ec\uac00 \ub098\uc628\ub2e4. \ud3ed\ud3ec \ub4a4\ub85c \ubc94\ub95c\uc0ac\uac00 \uc788\uace0 \uadf8\ub4a4\ub85c \uc804\ud615\uc801\uc778 \uc554\uc0b0\uc758 \ubaa8\uc2b5\uc744 \ub764 \uac10\uc545\uc0b0\uc774 \ubcf4\uc778\ub2e4. \ub9d1\uc740 \ub0a0\uc5d0\ub294 \uac1c\uc131\uc758 \uc1a1\uc545\uc0b0\uacfc \ubd81\ud55c\uc0b0\uc774 \ubcf4\uc778\ub2e4.'), STRINGDECODE('\u2460 \uc124\ub9c8\ub9ac-\ubc94\ub95c\uc0ac-\uc815\uc0c1-\uc784\uaebd\uc815\ubd09-\uc2e0\uc554\ub9ac (\ucd1d 3\uc2dc\uac04 40\ubd84)<BR>\u2461 \uc2e0\uc554\ub9ac-\uc2e0\uc554\uc800\uc218\uc9c0-\uc815\uc0c1-\ubc94\ub95c\uc0ac-\uc6b4\uacc4\ud3ed\ud3ec-\uc124\ub9c8\ub9ac (\ucd1d 4\uc2dc\uac04)'), '37.896039', '126.979788', STRINGDECODE('\uacbd\uae30\ub3c4 \uc591\uc8fc\uc2dc \ub0a8\uba74, \uc5f0\ucc9c\uad70 \uc804\uace1\uc74d, \ud30c\uc8fc\uc2dc \uc801\uc131\uba74'), 2140140100, 675, STRINGDECODE('\uac10\uc545\uc0b0'), STRINGDECODE('&lt;\uac80\ud478\ub978 \uac10\uc0c9\ubc14\uc704\uc758 \uc0b0&gt;<BR>\uac10\uc545\uc0b0\uc740 \uacbd\uae30 \ud30c\uc8fc\uc2dc \uc801\uc131\uba74\uacfc \uc591\uc8fc\uad70 \ub0a8\uba74 \uc5f0\ucc9c\uad70 \uc804\uace1\uc74d\uc5d0 \uac78\uccd0\uc788\ub2e4. &lt;\uc0b0\uacbd\ud45c&gt;\uc5d0 \uc758\ud558\uba74 \ud55c\ubd81\uc815\ub9e5\uc758 \ud55c\uac15\ubd09\uacfc \uc5f0\uacb0\ub418\uc5b4 \uadf8 \uc9c0\ub9e5\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4. \uc608\ub85c\ubd80\ud130 \uac1c\uc131 \uc1a1\uc545\uc0b0, \uac00\ud3c9 \ud654\uc545\uc0b0, \uacfc\ucc9c \uad00\uc545\uc0b0, \ud3ec\ucc9c \uc6b4\uc545\uc0b0\uacfc \ud568\uaed8 \uacbd\uae30 5\uc545\uc758 \ud558\ub098\ub85c \uc54c\ub824\uc838 \uc654\uc73c\ub098 \uc77c\ubc18\uc778\uc5d0\uac8c\ub294 \uadf8\ub9ac \uc798 \uc54c\ub824\uc838 \uc788\uc9c0 \uc54a\uc558\ub2e4. <BR><BR>\ud734\uc804\uc120\uc5d0 \uc778\uc811\ud574 \uc788\ub294 \ud0d3\uc5d0 \uc785\uc0b0\uae08\uc9c0\uad6c\uc5ed\uc73c\ub85c \ubb36\uc5ec \uc788\ub2e4\uac00 \uba87 \ub144 \uc804\ubd80\ud130 \ud1b5\uc81c\uac00 \ub2e4\uc18c \uc644\ud654\ub418\uc5c8\uae30 \ub54c\ubb38\uc774\ub2e4. \uadf8\ub7ec\ub098 \uc5ec\uc804\ud788 \ub4f1\uc0b0\ub85c\uac00 \uc81c\ud55c\ub41c\ub2e4. \uc815\uc0c1\uc744 \uc911\uc2ec\uc73c\ub85c \uc0b0\ud589\uc774 \uac00\ub2a5\ud55c \ub4f1\uc0b0\ub85c\ub294 \ubc94\ub95c\uc0ac, \uc2e0\uc554\ub9ac, \uc6d0\ub2f9\ub9ac \ubc29\uba74\uc774\ub2e4. \uac10\uc545\uc0ac, \uc6b4\uacc4\uc0ac, \ubc94\ub95c\uc0ac, \uc6b4\ub9bc\uc0ac \ub4f1\uc758 4\uac1c \uc0ac\ucc30\uc774 \uc788\uc5c8\ub2e4\ub294\ub370 \ud604\uc7ac\ub294 1970\ub144 \uc61b \uc6b4\uacc4\uc0ac \ud130\uc5d0 \uc7ac\ucc3d\uac74\ud55c \ubc94\ub95c\uc0ac\ub9cc \ub0a8\uc544\uc788\ub2e4. \uc7a5\uad70\ubd09 \uc544\ub798\ub294 \uc870\uc120 \uba85\uc885 \ub54c \uc758\uc801 \uc784\uaebd\uc815\uc774 \uad00\uad70\uc758 \ucd94\uc801\uc744 \ud53c\ud574 \uc228\uc5b4\uc788\uc5c8\ub2e4\ub294 \uc784\uaebd\uc815\uad74\uc774 \uc788\ub2e4. <BR><BR>6\u00b725\uc804\uc7c1 \ub54c\ub294 \uaca9\uc804\uc9c0\ub85c \uc720\uba85\ud588\ub294\ub370 \uc124\ub9c8\ub9ac \uacc4\uace1\uc5d0\ub294 \uc601\uad6d\uad70 \uc804\uc801\ube44\uc640 \ub300\ud55c\uc758\uc5f4\ub2e8 \uc804\uc801\ube44\uac00 \uc788\ub2e4.'), STRINGDECODE('\uc608\ub85c\ubd80\ud130 \uacbd\uae30 5\uc545\uc758 \ud558\ub098\ub85c\uc11c \ud3ed\ud3ec\u00b7\uacc4\uace1\u00b7\uc554\ubcbd \ub4f1\uc744 \uace0\ub8e8 \uac16\ucd94\uace0 \uc788\uc73c\uba70, \uc784\uc9c4\uac15\u00b7\uac1c\uc131 \uc1a1\uc545\uc0b0 \ub4f1\uc758 \uc870\ub9dd\uc774 \uc88b\uc740 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\uc218\ub7c9\uc774 \ud48d\ubd80\ud55c \uc6b4\uacc4\ud3ed\ud3ec\uac00 \uc788\uace0, \uc815\uc0c1\uc5d0\ub294 \uae00\uc790\uac00 \ubaa8\ub450 \ub9c8\uba78\ub418\uc5b4 \ud310\ub3c5\uc774 \ubd88\uac00\ub2a5\ud55c \ube44\ub730\ub300\uc655\ube44(\ud30c\uc8fc\uad70 \ud5a5\ud1a0\uc720\uc801 \uc81c8\ud638)\uac00 \uc788\ub294\ub370 \"\uc124\uc778\uadc0(\u859b\u4eba\u8cb4)\"\uc124\uacfc \"\uc9c4\ud765\uc655 \uc21c\uc218\ube44\"\uc124\uc774 \ub098\ub258\uc5b4 \uc18d\uc804\ub418\uace0 \uc788\uc74c. \uc784\uaebd\uc815\uc774 \uad00\uad70\uc758 \ucd94\uaca9\uc744 \ud53c\ud558\uae30 \uc704\ud574 \uc228\uc5b4 \uc9c0\ub0c8\ub2e4\ub294 \uc7a5\uad70\ubd09 \uc544\ub798 \uc784\uaebd\uc815 \uad74\uc774 \uc788\uc74c.'), STRINGDECODE('\uc758\uc801 \uc784\uaebd\uc815\uc774 \uc26c\uc5b4 \uac00\ub358 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\ubc94\ub95c\uc0ac\uc640 \uc6b4\uacc4\ud3ed\ud3ec \uadfc\ucc98\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \uc788\ub2e4. <BR>\ub300\uc131\uc5ec\uad00 \u260e031-959-3551<BR>\uc2b9\uc6d0\uc7a5 \u260e031-959-4085<BR>\uc218\uc131\uc5ec\uad00 \u260e031-959-4343<BR>\uac10\uc545\ud30c\ud06c \u260e031-959-4071 \ub4f1\uc774 \uc788\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud30c\uc8fc\uc2dc\uccad \uc0b0\ub9bc\uacfc \u260e031-940-4621'), STRINGDECODE('\uae30\uc810\uc740 \uc758\uc815\ubd80\uc774\ub2e4. <BR>\uc758\uc815\ubd80\uc5d0\uc11c \uc801\uc131\uc73c\ub85c \uac00\ub294 25\ubc88 \uc2dc\ub0b4\ubc84\uc2a4\uac00 15\ubd84 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud55c\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(6, TIMESTAMP '2019-08-14 15:46:34.899', TIMESTAMP '2019-08-14 15:46:42.383', '45', STRINGDECODE('<p>\uc21c\ucc3d\uc74d\uc5d0\uc11c 10km\uc758 \uac00\uae4c\uc6b4 \uac70\ub9ac\uc5d0 \uc788\ub294 \uac15\ucc9c\uc0b0\uc740 \ud638\ub0a8\uc758 \uc18c\uae08\uac15\uc774\ub77c\uace0 \ubd88\ub9b4 \uc815\ub3c4\ub85c \ub3c4\ucc98\uc5d0 \uae30\ubd09\uc774 \uc19f\uc544 \uc788\uace0, \ud06c\uace0 \uc791\uc740 \uc218\ub9ce\uc740 \ubc14\uc704 \uc0ac\uc774\ub85c \ud3ed\ud3ec\ub97c \uc774\ub8e8\uace0 \uc788\uc73c\uba70, 4km\uc5d0 \uc774\ub974\ub294 \uae4a\uc740 \uacc4\uace1\uacfc \uacc4\uace1\uc744 \ub4a4\ub36e\uc740 \uc6b8\ucc3d\ud55c \uc232\uc740 \uc790\uc5f0 \uadf8\ub300\ub85c\uc758 \uc544\ub984\ub2e4\uc6c0\uc744 \uace0\uc774 \uac04\uc9c1\ud558\uace0 \uc788\ub2e4.<br><br>\uc6d0\ub798\ub294 \uc0dd\uae40\uc0c8\uac00 \uc6a9\uc774 \uaf2c\ub9ac\ub97c \uce58\uba70 \uc2b9\ucc9c\ud558\ub294 \ubaa8\uc2b5\uacfc \ub2ee\uc558\ub2e4 \ud558\uc5ec \uc6a9\ucc9c\uc0b0(\u9f8d\u5929\u5c71)\uc774\ub77c \ubd88\ub838\ub2e4. \ub610\ud55c \uc720\uc11c\uae4a\uc740 \uac15\ucc9c\uc0ac\uc640 \uc0bc\uc778\ub300 5\uce35 \uc11d\ud0d1, \uae08\uc131\uc0b0\uc131 \ub4f1 \ubb38\ud654\uc720\uc801\uc774 \uc0b0\uc7ac\ud558\uace0, \ube44\uacbd\uc774 \ub9ce\uc774 \uc228\uaca8\uc838 \uc788\ub2e4. \uc77c\uba85 \uad11\ub355\uc0b0\uc774\ub77c\uace0\ub3c4 \ubd88\ub9ac\ub294 \uac15\ucc9c\uc0b0\uc740 1981\ub144 \uad6d\ub0b4\uc5d0\uc11c \ucd5c\ucd08\ub85c \uad70\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \uacf3\uc774\uae30\ub3c4 \ud558\uba70, \uae38\uc774 76m\uc758 \ud604\uc218\uad50\uac00 \uc9c0\uc0c1 50m \ub192\uc774\uc5d0 \uc124\uce58\ub3fc \uc788\uc5b4 \uc6c5\uc7a5\ud568\uc744 \ub354\ud574\uc8fc\uace0 \uc788\ub2e4.<br><br>\ubcfc\uac70\ub9ac\ub294 11\uc6d4 \ucd08\uc21c\uc5d0 \uc808\uc815\uc744 \uc774\ub8e8\ub294 \ub2e8\ud48d\uacfc 4\uc6d4 \ucd08\uc21c\uc5d0 \ub9cc\uac1c\ud558\ub294 \uc0b0\ubc9a\uaf43\uc774 \uc720\uba85\ud55c\ub370, \uc0b0 \uc785\uad6c\uc758 \uac15\ucc9c\ud638 \uc8fc\ubcc0\ubfd0 \uc544\ub2c8\ub77c \ub4f1\uc0b0\ub85c \uc5b4\ub514\uc5d0\uc11c\ub098 \uc990\uae38 \uc218 \uc788\ub2e4. \uc0b0 \uc554\ubd09 \uc544\ub798\uc5d0\ub294 887\ub144(\uc2e0\ub77c \uc9c4\uc131\uc5ec\uc655 1) \ub3c4\uc120\uad6d\uc0ac(\u9053\u8a75\u570b\u5e2b)\uac00 \uc138\uc6b4 \uac15\ucc9c\uc0ac\uac00 \uc788\ub2e4.<br><br>\uc774 \uacf3\uc758 \uc11d\ud0d1\uc740 \uc804\ub77c\ubd81\ub3c4 \uc720\ud615\ubb38\ud654\uc7ac 92\ud638\ub85c \uc9c0\uc815\ub418\uc5c8\uace0, \uc808 \uc785\uad6c\uc758 \ubaa8\uacfc\ub098\ubb34\ub294 \uc804\ub77c\ubd81\ub3c4\uae30\ub150\ubb3c 97\ud638\uc774\ub2e4. \uadf8 \ubc16\uc5d0 \uc21c\ucc3d \uc0bc\uc778\ub300(\u4e09\u5370\u81fa:\uc804\ubd81\uc720\ud615\ubb38\ud654\uc7ac 27), \uae08\uc131\uc0b0\uc131(\u91d1\u57ce\u5c71\u57ce:\uc804\ubd81\uae30\ub150\ubb3c 52) \ub4f1\uc758 \ubb38\ud654\uc720\uc801\uc774 \uc788\ub2e4. \ub0b4\uc7a5\uc0b0(\u5167\u85cf\u5c71:763.5m)\u00b7\ubc31\uc591\uc0ac(\u767d\u7f8a\u5bfa)\u00b7\ub2f4\uc591\ub310 \ub4f1\uacfc\ub3c4 \uac00\uae5d\ub2e4.</p>'), STRINGDECODE('<p>\u2460 \ub9e4\ud45c\uc18c-\uac15\ucc9c\uc0ac-\uc2e0\uc120\ubd09-\uad6c\ub984\ub2e4\ub9ac-\uc815\uc0c1-\uae43\ub300\ubd09-\ub9e4\ud45c\uc18c(\ucd1d 3\uc2dc\uac04 35\ubd84)<br> \u2461 \ub9e4\ud45c\uc18c-\uac15\ucc9c\uc0ac-\uad6c\ub984\ub2e4\ub9ac-\uc815\uc0c1-\ub3c4\uacc4 \uc885\uc8fc-\uc5f0\ub300\ubd09-\uc5f0\ub300\uc554\uc9c0-\uac15\ucc9c\uc0ac(\ucd1d 5\uc2dc\uac04 15\ubd84)<br>\u2462 \ub9e4\ud45c\uc18c-\uac15\ucc9c\uc0ac-\uad6c\ub984\ub2e4\ub9ac-\uc815\uc0c1-530\ubd09-\uc11c\ubd81\ub989-\ubd84\ud1b5\ub9c8\uc744(\ucd1d 2\uc2dc\uac04 45\ubd84)<br>\u2463 \ub9e4\ud45c\uc18c-\uac15\ucc9c\uc0ac-\uad6c\ub984\ub2e4\ub9ac-\uc815\uc0c1-\uac15\ucc9c\ud638-\uc1a1\ub099\ubc14\uc704-\uc5f0\ub300\ubd09-\uc0b0\uc131\uc0b0-\uc5f0\ub300\uc554\uc9c0-\uad6c\ub984\ub2e4\ub9ac-\uac15\ucc9c\uc0ac-\ub9e4\ud45c\uc18c(\ucd1d 4\uc2dc\uac04 30\ubd84)</p>'), '35.39421', '127.084938', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \uc21c\ucc3d\uad70 \ud314\ub355\uba74, \uc804\ub77c\ub0a8\ub3c4 \ub2f4\uc591\uad70 \uc6a9\uba74'), 2607102300, 586, STRINGDECODE('\uac15\ucc9c\uc0b0'), STRINGDECODE('<p>&lt;\uacc4\ub958\ubbf8 \ub6f0\uc5b4\ub09c \ud638\ub0a8\uc758 \ubb18\ud5a5&gt;<br>1981\ub144 \uc0ac\uc0c1 \ucd5c\ucd08\ub85c \uad70\ub9bd\uacf5\uc6d0\uc774 \ub41c \uc0b0\uc774\ub2e4. ''\uad70\ub9bd''\uc774\ub77c\ub294 \uaddc\ubaa8\uc5d0 \uac78\ub9de\uac8c 600\ubbf8\ud130\uac00 \ucc44 \uc548\ub418\uc9c0\ub9cc ''\uacf5\uc6d0''\ub2f5\uac8c \ub6f0\uc5b4\ub09c \uacc4\ub958\ubbf8\ub97c \uc790\ub791\ud55c\ub2e4. <br><br>\uc704\ub294 \uc721\uc0b0, \uc544\ub798\ub294 \uace8\uc0b0(\u9aa8\u5c71)\uc774\uc5b4\uc11c \ubc14\uc704\ub4e4\uc774 \uac70\uc758 \uc0b0\uc911\ud131 \uc544\ub7ab\ub158\uc5d0 \ubab0\ub824\uc788\ub2e4. \ud558\uc5ec 5\ud0ac\ub85c\ubbf8\ud130 \uc554\ubcbd\ud611\uace1\uc73c\ub85c \ube44\ub8e1\uacc4\uace1\uc758 \ube44\ub8e1\ud3ed\ud3ec, \ubb3c\ud1b5\uace8\uc758 \uc57d\uc218\ud3ed\ud3ec, \uc18c\ubaa9\uace8 \uc6a9\ub450\ud3ed\ud3ec, \uacc4\uace1 \ubcf8\ub958\ub85c\ub294 \ubcd1\ud48d\ubc14\uc704\uc758 \uc778\uacf5\ud3ed\ud3ec, \uc7a5\ub300\ud55c 9\uc7a5\uad70\ud3ed\ud3ec\uac00 \uc3df\uc544\uc838 \ub0b4\ub9ac\uace0 \uc788\ub2e4. \uc5ec\uae30\uc5d0 \uc6a9\ubc14\uc704, \ud638\ub450\uc554, \ud22c\uad6c\ubd09\uc758 \ubc14\uc704\ub4e4\uacfc \uae08\uac15\uad74, \uc218\uc88c\uad74, \ud615\uc81c\uad74\uc758 \ub3d9\uad74\uc774 \uc5b4\uc6b0\ub7ec\uc9c0\uace0 \uac15\ucc9c\uc0ac\uc640 \uc0bc\uc778\ub300\uc758 \uc804\uc124\uae4c\uc9c0 \uae43\ub4e4\uc5b4\uc788\uc73c\ub2c8 \ud55c \ub9c8\ub514\ub85c \uc791\uc740 \ubb18\ud5a5\uc0b0, \uc7a5\uc5c4\ud558\uba74\uc11c\ub3c4 \ube7c\uc5b4\ub09c \uc5ed\uc7a5\uc5ed\uc218(\u4ea6\u58ef\u4ea6\u79c0)\uc758 \uae30\uacbd\uc774\ub77c \ud560 \uac83\uc774\ub2e4. <br><br>\uc608\uc804\uc5d0\ub294 \uc6a9\ucc9c\uc0b0\uc774\ub77c \ubd88\ub838\ub294\ub370 \uc1a1\uc775\ud544\uc774\ub77c\ub294 \uc0ac\ub78c\uc758 \uc2dc ''\uc219(\u5bbf)\uac15\ucc9c\uc0ac''\uac00 \ub728\uba74\uc11c \uac15\ucc9c\uc0b0\uc73c\ub85c \ubc14\uafe8\ub2e4\uace0 \ud55c\ub2e4. \uc815\ub9d0 \uc5b4\ucc98\uad6c\ub2c8\uc5c6\ub294 \ucc3d\uc528\uac1c\uba85\uc774 \uc544\ub2d0 \uc218 \uc5c6\uc9c0\ub9cc \uc775\ud544\uc758 \uc790(\u5b57)\ub85c \uc0b0\uc774\ub984\uc744 \uc0bc\uc740 \uc9c4\uc548 \uc6b4\uc7a5\uc0b0\uc774 \uac00\uc7a5 \uba4b\uc9c4 \ubd80\ubd84\uae4c\uc9c0 \ud638 \uad6c\ubd09\uc5d0 \ube8f\uae34 \uac83\uc5d0 \ub300\uba74 \uadf8\ub798\ub3c4 \ub0ab\ub2e4 \ud560 \uac83\uc774\ub2e4. \uadf8 \uc1a1\uc775\ud544\uc740 \uc120\uc870\ub54c \uc11c\uc778\uc758 \ub9c9\ud6c4\uc2e4\ub825\uc790\uc600\ub2e4.</p>'), STRINGDECODE('<p>\uad70\ub9bd\uacf5\uc6d0(1981\ub144 \uc9c0\uc815)\uc73c\ub85c \uc9c0\uc815\ub418\uc5b4 \uc788\uc73c\uba70, \uac15\ucc9c\uacc4\uace1 \ub4f1 \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0 \uc870\ub9dd\uc774 \uc88b\uc740 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\uc2e0\ub77c \uc9c4\uc131\uc5ec\uc655\ub54c(887\ub144) \ub3c4\uc120\uad6d\uc0ac\uac00 \uac1c\ucc3d\ud55c \uac15\ucc9c\uc0ac(\u525b\u6cc9\u5bfa)\uac00 \uc788\uc73c\uba70, \uc0b0 \uc774\ub984\ub3c4 \uac15\ucc9c\uc0ac(\u525b\u6cc9\u5bfa)\uc5d0\uc11c \uc720\ub798. \uc0bc\uad6d\uc2dc\ub300\uc5d0 \ucd95\uc870\ub41c \uac83\uc73c\ub85c \ucd94\uc815\ub418\ub294 \uae08\uc131\uc0b0\uc131(\u91d1\u57ce\u5c71\u57ce)\uc774 \uc720\uba85</p>'), STRINGDECODE('\uad6d\ub0b4 \ucd5c\ucd08\uc758 \uad70\ub9bd\uacf5\uc6d0'), STRINGDECODE('<p>\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<br>\uc0b0 \uc548\uc5d0 \uac15\ucc9c\uac01\ubaa8\ub378(\u260e652-9920)\uacfc \uc5ec\ub7ec \uac1c\uc758 \ubbfc\ubc15\uc9d1\uc774 \uc788\ub2e4. <br>\ud68c\ubb38\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e063-653-4779)\ub3c4 \uba40\uc9c0 \uc54a\ub2e4. <br><br>\u25cf \uc774\uc6a9\ubb38\uc758<br>\uac15\ucc9c\uc0b0\uad70\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-650-1533</p>'), STRINGDECODE('<p>\uae30\uc810\uc740 \uc21c\ucc3d\uc774\ub2e4. \uac15\ucc9c\uc0b0 \uc8fc\ucc28\uc7a5\uae4c\uc9c0 \uad70\ub0b4\ubc84\uc2a4\uac00 5\ubd84 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2cc\ub2e4. 9\ud0ac\ub85c\ubbf8\ud130 \uac70\ub9ac\ub2c8 \uac78\uc5b4\uac00\ub3c4 \ub41c\ub2e4. \uc21c\ucc3d\uc73c\ub85c\ub294 \uc11c\uc6b8 \uac15\ub0a8\ud130\ubbf8\ub110\uc5d0\uc11c \uace0\uc18d\ubc84\uc2a4\uac00 6\ud68c \uc788\ub2e4. \uc804\ub77c\ubd81\ub3c4 \uc774\uc678 \ub3c4\uc2dc\ub85c\ub294 \uc778\ucc9c, \ub300\uad6c, \uad11\uc8fc\uc5d0\uc11c \ubc84\uc2a4\uac00 \uc788\ub2e4.</p>'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(7, TIMESTAMP '2019-08-14 15:46:34.899', TIMESTAMP '2019-08-14 15:46:43.003', '30', STRINGDECODE('\uacc4\ub8e1\uc0b0\uc740 \ubc31\ub450\ub300\uac04\uc5d0\uc11c \uac08\ub77c\uc838\ub098\uc628 \uae08\ub0a8\uc815\ub9e5\uc758 \ud55c \uc904\uae30\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c, \ucda9\ub0a8 \uacf5\uc8fc\uc2dc\uc640 \ub17c\uc0b0\uc2dc\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\uace0 \uc788\ub2e4. \uc0b0\uc138\uac00 \ub9c8\uce58 \ub2ed\uc758 \ubcbc\uc2ac\uc744 \uc4f4 \uc6a9\uc758 \ud615\uc0c1\uc744 \ud588\ub2e4\uace0 \ud574\uc11c \uacc4\ub8e1\uc0b0\uc73c\ub85c \ubd88\ub9ac\uac8c \ub418\uc5c8\ub2e4\uace0 \ud55c\ub2e4.<BR>\uacc4\ub8e1\uc0b0\uc740 \uc870\uc120\uc870 \ucd08\uae30\uc5d0 \uc50c\uc5b4\uc9c4 \uc608\uc5b8\uc11c\uc778 \u3008\uc815\uac10\ub85d\u3009\uc758 `\uc655\ub3c4\uc785\uc9c0\uc124''\ub85c \uc720\uba85\ud55c \uc0b0\uc73c\ub85c,\uc815\uc0c1\uc778 \ucc9c\ud669\ubd09(\u5929\u7687\u5cf0)\uc744 \uc911\uc2ec\uc73c\ub85c \uc300\uac1c\ubd09(830.6m), \uad00\uc74c\ubd09(765.8m), \ubb38\ud544\ubd09(735.6m), \uc0bc\ubd88\ubd09(777.1m), \uc5f0\ucc9c\ubd09(742.9m)\uc774 \uc8fc\ub2a5\uc120\uc5d0 \uc904\uc9c0\uc5b4 \uc19f\uc544 \uc788\ub2e4. \uacc4\ub8e1\uc0b0 \uc0b0\uc790\ub77d \uacf3\uacf3\uc5d0 \ubb38\ud654 \uc720\uc801\uc774 \uc0b0\uc7ac\ud574 \uc788\ub294\ub370, \ub3d9\ubd81\ucabd\uc5d0\ub294 \ub3d9\ud559\uc0ac\uac00,\uc11c\ubd81\ucabd\uc5d0\ub294 \uac11\uc0ac\uac00,\uc11c\ub0a8\ucabd\uc5d0\ub294 \uc2e0\uc6d0\uc0ac \uc0ac\ucc30\uc774 \uc790\ub9ac\uc7a1\uace0 \uc788\ub2e4. \ud2b9\ud788 \uac11\uc0ac\uc5d0\ub294 \ubcf4\ubb3c \uc81c257\ud638\uc778 \ubd80\ub3c4(\u6d6e\u5c60)\uc640 \ubcf4\ubb3c \uc81c256\ud638\uc778 \ucca0\ub2f9\uac04 \ubc0f \uc9c0\uc8fc.\ubcf4\ubb3c \uc81c478\ud638\uc778 \ub3d9\uc885(\u9285\u9418)\ub4f1\uc758 \ubb38\ud654\uc7ac\uac00 \uc788\uc73c\uba70, \u3008\uc6d4\uc778\uc11d\ubcf4\u3009\ub97c \ucc0d\uc5b4\ub0b8 \ubaa9\ud310\ub3c4 \uc18c\uc7a5\ub418\uc5b4 \uc788\ub2e4. \u3008\uc6d4\uc778\uc11d\ubcf4\u3009\ub294 \uc138\uc88529\ub144(1447\ub144)\uc5d0 \uac04\ud589\ub41c \u3008\uc11d\ubcf4\uc0c1\uc808\u3009\uacfc \uc138\uc885 31\ub144\uc5d0 \uac04\ud589\ub41c \u3008\uc6d4\uc778\ucc9c\uac15\uc9c0\uace1\u3009\uc744 \ud569\ud3b8\ud558\uc5ec, \uc138\uc870\uac00 1459\ub144\uc5d0 \uac04\ud589\ud55c \uac83\uc774\ub2e4. <BR>\uc0c1\ubd09\uc744 \uc911\uc2ec\uc73c\ub85c \ub3d9\ucabd\uc5d0 \ub3d9\ud559\uc0ac, \uc11c\ucabd\uc5d0 \uac11\uc0ac, \ub0a8\ucabd\uc5d0 \uc2e0\uc6d0\uc0ac\uac00 \uc790\ub9ac\ud558\uc5ec \ud604\uc7ac\uae4c\uc9c0\ub3c4 \ubcf4\uc874\ub418\uace0 \ubd81\ucabd\uc758 \uad6c\ub8e1\uc0ac\ub294 \uc808\ud130\ub9cc \ub0a8\uc544 \uc788\ub2e4. \uacc4\ub8e1\uc0ac\uc5d0\ub294 \ub178\ub8e8, \ub2f4\ube44, \uccad\uc124\ubaa8, \ud669\ub9e4\ud654 \ub4f1 \ud76c\uadc0 \ub3d9.\uc2dd\ubb3c 1227\uc885\uc774 \uc11c\uc2dd\ud558\uace0 \uc788\uc73c\uba70, \uacc4\ub8e1 8\uacbd\uc73c\ub85c \uaf3d\ud788\ub294 \ucc9c\ud669\ubd09(\uc77c\ucd9c), \uc0bc\ubd88\ubd09(\uc124\ud654), \uc5f0\ucc9c\ubd09(\ub099\uc870), \uad00\uc74c\ubd09(\ud55c\uc6b4), \ub3d9\ud559\uacc4\uace1(\uc2e0\ub85d), \uc740\uc120\ud3ed\ud3ec(\uc6b4\ubb34), \uac11\uc0ac\uacc4\uace1(\ub2e8\ud48d), \ub0a8\ub9e4\ud0d1(\uba85\uc6d4) \ub4f1\uc740 \uc6b8\ucc3d\ud55c \uc232\uacfc \uae30\uc554\uc808\ubcbd\uc744 \ub354\ubd88\uc5b4 \uc7a5\uad00\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uac11\uc0ac-\uc5f0\ucc9c\ubd09-\uad00\uc74c\ubd09-\uc0bc\ubd88\ubd09-\ub0a8\ub9e4\ud0d1-\ud070\ubc30\uc7ac-\uc0c1\uc2e0\ub9ac(\ucd1d 4\uc2dc\uac04 40\ubd84) <BR>\u2461 \ub3d9\ud559\uc0ac-\uc740\uc120\ud3ed\ud3ec-\uad00\uc74c\ubd09-\uc0bc\ubd88\ubd09-\uc2e0\uc120\ubd09-\uc7a5\uad70\ubd09-\ubc15\uc815\uc790 \uc0bc\uac70\ub9ac(\ucd1d 9\uc2dc\uac04 40\ubd84)<BR>\u2462 \uc2e0\uc6d0\uc0ac-\uace0\uc655\uc554-\uc5f0\ucc9c\ubd09-\uc0bc\ubd88\ubd09-\ub0a8\ub9e4\ud0d1-\ub3d9\ud559\uc0ac(\ucd1d 3\uc2dc\uac04 10\ubd84)'), '36.368371', '127.157854', STRINGDECODE('\ub300\uc804\uad11\uc5ed\uc2dc, \ucda9\uccad\ub0a8\ub3c4 \uacf5\uc8fc\uc2dc \uacc4\ub8e1\uba74\u318d\ub17c\uc0b0\uc2dc \uc0c1\uc6d4\uba74\u318d\uacc4\ub8e1\uc2dc \uc2e0\ub3c4\uc548\uba74'), 2402020400, 847, STRINGDECODE('\uacc4\ub8e1\uc0b0'), STRINGDECODE('&lt;\ucda9\ub0a8 \uc81c\uc77c\uc758 \uc601\uc0b0\uc774\uc790 \uba85\uc0b0&gt;<BR>\uacc4\ub8e1\uc0b0\uc740 \uc218\ub824\ud55c \uc0b0\uc138\uc640 \uc2b9\uc9c0 \uc2e0\ub3c4\uc548, \uc5ec\ub7ec \uace0\ucc30\ub4e4\uc744 \ud488\uc740 \ucda9\ub0a8 \uc81c\uc77c\uc758 \uba85\uc0b0\uc774\ub2e4. \uc774\ubbf8 \uc2e0\ub77c\uc2dc\ub300 5\uc545\uc758 \ud558\ub098\uc600\uace0 \uc870\uc120\uc2dc\ub300\uc5d0\ub294 \ubb18\ud5a5\uc0b0, \uc9c0\ub9ac\uc0b0\uacfc \ud568\uaed8 \uc0b0\uc2e0\uc81c\ub97c \uc62c\ub9ac\ub358 \uc601\uc0b0\uc774\uc5c8\ub2e4. <BR>\uc8fc\ubd09\uc778 \ucc9c\ud669\ubd09\uc5d0\uc11c \uc300\uac1c\ubd09\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub2a5\uc120\ubbf8\uac00 \ud0c0\uc758 \ucd94\uc885\uc744 \ubd88\ud5c8\ud55c\ub2e4. \uc774 \ub9e8\ub4dc\ub77c\ubbf8 \uac19\uc740 \uc6a9\uba38\ub9ac\uac00 \uc624\uc885\uc885\ud558\ub2c8 \uac00\uc6b4\ub370\uac00 \ub192\uc740 \uc554\ud0c9 \ubcbc\uc2ac\ucc98\ub7fc \ubcf4\uc778\ub2e4 \ud558\uc5ec \uacc4\ub8e1\uc774\ub77c\ub294 \uc774\ub984\uc774 \ubd99\uc5c8\ub2e4. <BR>\uc6a9\uc720\uc18c, \uc774\uc77c\ucc9c, \ubc31\ub8e1\uac15, \ub2ec\ubb38\ud0dd, \uad70\uc790\ud0dd, \uba85\uc6d4\ub2f4, \uacc4\ub8e1\uba85\uc554, \uc218\uc815\ubd09 \ub4f1\uc758 \uba85\uc18c\uac00 \uacf3\uacf3\uc5d0 \ubc8c\uc5ec\uc788\ub2e4. \uacc4\ub8e110\uacbd\uc758 \ud558\ub098\uc778 \uc740\uc120\ud3ed\ud3ec, \uc601\ud5d8\ud568 \ub54c\ubb38\uc5d0 \ubb34\uc18d\ud589\uc0ac \uc7a5\uc18c\ub85c \uac01\uad11\ubc1b\ub294(\ud639\uc740 \ubc1b\uc558\ub358) \uac11\uc0ac\uacc4\uace1\uc758 \uc6a9\ubb38\ud3ed\ud3ec, \uc2e0\ub3c4\uc548 \uc6a9\ucd94\ud3ed\ud3ec\ub85c \ub4dc\ub7ec\ub098\ub4ef \uacc4\uace1\ubbf8 \ub610\ud55c \ube7c\uc5b4\ub098\ub2e4. <BR>\ud3ed\ud3ec\uc640 \uae30\uc554\uc808\ubcbd\uc774 \uc870\ud654\ub97c \uc774\ub8ec \uacc4\ub8e1\uc0b0\uc740 \ub2e8\ud48d\uc73c\ub85c\ub3c4 \uc720\uba85\ud558\ub2e4. 10\uc6d4 \ud558\uc21c\uc5d0 \uc808\uc815\uc744 \uc774\ub8e8\ub294 \ub3d9\ud559\uc0ac\uacc4\uace1, \ub0a8\ub9e4\ud0d1, \uc0bc\ubd88\ubd09\uacfc \uad00\uc74c\ubd09\uc5d0 \uc774\ub974\ub294 \uad6c\uac04\uc774 \ubc31\ubbf8\ub2e4.<BR>\ub3d9\ucabd \ub3d9\ud559\uc0ac, \uc11c\ucabd\uc758 \uac11\uc0ac, \ub0a8\ucabd \uc2e0\uc6d0\uc0ac \ub4f1 \uc0ac\ubc29\uc758 \uace0\ucc30\uc5d0\ub294 \ubb38\ud654\uc7ac\ub3c4 \ub9ce\ub2e4. \uc774 \uc911 \uad6d\ubcf4 \ud558\ub098\uc640 \ubcf4\ubb3c \ub137\uc744 \uac04\uc9c1\ud55c \uac11\uc0ac, \uace0\ub824\ub9d0\uc758 \uc138 \ucda9\uc2e0\uc744 \ubaa8\uc2e0 \uc0bc\uc740\uac01, \ub3d9\ud559\uc0ac\ub294 \uaf2d \ub4e4\ub7ec\ubcfc \uc77c\uc774\ub2e4.'), STRINGDECODE('\uc608\ub85c\ubd80\ud130 \uc2e0\ub77c 5\uc545\uc758 \ud558\ub098\uc778 \uc11c\uc545(\u897f\u5cb3)\uc73c\ub85c \uc9c0\uce6d\ub418\uc5c8\uace0, \uc870\uc120\uc2dc\ub300\uc5d0\ub294 3\uc545 \uc911 \uc911\uc545(\u4e2d\u5cb3)\uc73c\ub85c \ubd88\ub9ac\uc6b4 \uc0b0\uc73c\ub85c\uc11c \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1968\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\uc0b0 \ub2a5\uc120\uc774 \ub9c8\uce58 \ub2ed\uc758 \ubcbc\uc2ac\uc744 \uc4f4 \uc6a9\uc758 \ubaa8\uc2b5\uacfc \ub2ee\uc558\ub2e4\uace0 \ud558\uc5ec \uacc4\ub8e1\uc0b0\uc774\ub77c\ub294 \uc774\ub984\uc774 \uc720\ub798\ub418\uc5c8\uc73c\uba70, \"\uc815\uac10\ub85d(\u912d\u9451\u9304)\"\uc5d0 \uc5b8\uae09\ub41c \uc2ed\uc2b9\uc9c0\uc9c0(\u5341\u52dd\u4e4b\u5730)\uc911 \ud558\ub098\uc784. \uc2e0\ub77c \uc131\ub355\uc655 2\ub144(724\ub144) \ud68c\uc758\ud654\uc0c1\uc774 \ucc3d\uac74\ud55c \ub3d9\ud559\uc0ac(\u6771\u9db4\u5bfa)\uc640 \ubc31\uc81c \uad6c\uc774\uc2e0\uc655(420\ub144)\ub54c \uace0\uad6c\ub824\uc758 \uc544\ub3c4\ud654\uc0c1\uc5d0 \uc758\ud558\uc5ec \ucc3d\uac74\ub41c \uac11\uc0ac(\u7532\u5bfa)\ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\uc815\uac10\ub85d\uc5d0\uc11c \uc608\uc5b8\ud55c \uc655\ub3c4\uac00 \ub4e4\uc5b4\uc124 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uac11\uc0ac, \ub3d9\ud559\uc0ac, \uc2e0\uc6d0\uc0ac \uc785\uad6c\uc5d0 \uc219\uc2dd\ud560 \uacf3\uc774 \ub9ce\ub2e4. <BR>\uac11\uc0ac\ucabd \uc911\uc7a5\ub9ac\uc5d0\ub294 \uacc4\ub8e1\uc0b0\uac11\uc0ac\uc720\uc2a4\ud638\uc2a4\ud154 \u260e041-856-4666, \uac11\uc0ac\ubbfc\ubc15 \u260e041-857-3234<BR>\ub3d9\ud559\uc0ac\ucabd \ud559\ubd09\ub9ac\uc5d0\ub294 \uacc4\ub8e1\uc0b0\uc7a5\uc5ec\uad00 \u260e041-825-4019, \uad81\uc804\ubaa8\ud154 \u260e041-825-7840<BR>\uc2e0\uc6d0\uc0ac\ucabd \uc591\ud654\ub9ac\uc5d0\ub294 \uc2e0\uc6d0\uc7a5\uc5ec\uad00 \u260e041-852-4405<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uacc4\ub8e1\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e042-825-3002<BR><BR>'), STRINGDECODE('\uae30\uc810\uc740 \uacf5\uc8fc\uc640 \ub300\uc804\uc774\ub2e4. \uad50\ud1b5\uc785\uc9c0\ub85c \uc131\uc7a5\ud55c \ub300\uc804\uc740 \uc124\uba85\ud560 \ud544\uc694\uac00 \uc5c6\uc744 \uac83\uc774\uace0 \uacf5\uc8fc\ub85c\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 30\ubd84 \uac04\uaca9, \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c 52\ud68c\ub098 \ub2e4\ub2cc\ub2e4. \uc774 \uc678\uc5d0 \uc778\ucc9c, \ub300\uc804, \uc218\uc6d0, \uc131\ub0a8, \uccad\uc8fc, \uc6d0\uc8fc\uc640 \ucda9\ub0a8 \uac01\uc9c0\uc5d0\uc11c \uc9c1\ud589\ubc84\uc2a4\ub97c \ud0c8 \uc218 \uc788\ub2e4. <BR>&gt; \ub300\uc804\u2192\uac11\uc0ac, \ub3d9\ud559\uc0ac : \uc218\uc2dc\ub85c \uc6b4\ud589 , <BR>&gt; \uacf5\uc8fc-&gt;\uac11\uc0ac, \uc2e0\uc6d0\uc0ac: \uc218\uc2dc\ub85c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(8, TIMESTAMP '2019-08-14 15:46:34.9', TIMESTAMP '2019-08-14 15:46:43.593', '42', STRINGDECODE('\ub0a8\ud55c\uc5d0\uc11c \ud55c\ub77c\uc0b0(1947.3m) \uc9c0\ub9ac\uc0b0(1915.4m) \uc124\uc545\uc0b0(1708.1m) \ub355\uc720\uc0b0(1614.2m)\uc5d0 \uc774\uc5b4 \ub2e4\uc12f \ubc88\uc9f8\ub85c \ub192\uc740 \uacc4\ubc29\uc0b0\uc740 \uaca8\uc6b8\ucca0 \uc801\uc124\ub4f1\ubc18 \uc0b0\ud589\uc9c0\ub85c \uc720\uba85\ud55c \uc0b0\uc774\ub2e4.<BR>\uac15\uc6d0\ub3c4 \ud64d\ucc9c\uad70 \ub0b4\uba74\uacfc \ud3c9\ucc3d\uad70 \uc9c4\ubd80\uba74\uc5d0 \uac78\uccd0 \uc788\ub294 \uad11\ub300\ud55c \uc0b0\ub9e5\uc744 \uac70\ub290\ub9ac\uace0 \uc788\uc73c\ub098, \ubd81\ub3d9\ucabd\uc73c\ub85c \uc5f0\uacb0\ub418\uc5b4 \uc788\ub294 \uc624\ub300\uc0b0\uc758 \uba85\uc131\uc5d0 \uac00\ub824 \ube5b\uc744 \ubcf4\uc9c0 \ubabb\ud588\ub358 \uba85\uc0b0\uc774\ub2e4. \ub192\uc740 \uc0b0\uc774\uba74\uc11c\ub3c4 \uc720\uc21c\ud55c \uc0b0\uc138\uc640 \ub2a5\uc120\uc744 \uac00\uc9c0\uace0 \uc788\uc73c\uba70 \uac00\uc744\uc774\uba74 \uc628 \uc0b0\uc744 \ub2e8\ud48d\uc73c\ub85c \ubb3c\ub4e4\uc774\uace0 \uaca8\uc6b8\ucca0\uc5d0\uc11c \ubb34\ub98e\uc774\uc0c1 \ube60\uc9c8\uc815\ub3c4\uc758 \uc801\uc124\ub7c9\uc73c\ub85c \ub4f1\uc0b0\uc778\ub4e4\uc758 \uc0ac\ub791\uc744 \ub4ec\ubfcd \ubc1b\uace0 \uc788\ub294 \uc0b0\uc774\ub2e4. \uc0b0\uae30\uc2ad\uc5d0 \uc788\ub294 \ubc29\uc544\ub2e4\ub9ac \uc57d\uc218\ub294 \uc704\uc7a5\ubcd1, \ud53c\ubd80\ubcd1\uc5d0 \ud6a8\ud5d8\uc774 \uc788\ub294\ub370 \uba54\ubc00\uaf43 \ud544 \ubb34\ub835 \ub9c8\uc2dc\uba74 \ud2b9\ud6a8\uac00 \uc788\ub2e4\uace0 \ud55c\ub2e4.'), STRINGDECODE('\u2460 \uc6b4\ub450\ub839-1166\ubd09-1492\ubd09(\ud5ec\uae30\uc7a5)-\uc815\uc0c1-\uc548\ubd80-\uc774\uc2b9\ubcf5\uc0dd\uac00\ud130-\ubc29\uc544\ub2e4\ub9ac\uc57d\uc218 (\ucd1d 4\uc2dc\uac04 45\ubd84)<BR>\u2461 \uc544\ub7ab\uc0bc\uac70\ub9ac(\ud639\uc740 \ubc29\uc544\ub2e4\ub9ac \uc57d\uc218)-\uc774\uc2b9\ubcf5\uc0dd\uac00\ud130-\uc8fc\ubaa9\uad70\ub77d\uc9c0-\uc548\ubd80-\uc815\uc0c1-\uc544\ub7ab\uc0bc\uac70\ub9ac (\ucd1d 4\uc2dc\uac04 50\ubd84)'), '37.794724', '128.408329', STRINGDECODE('\uac15\uc6d0\ub3c4 \ud64d\ucc9c\uad70 \ub0b4\uba74, \ud3c9\ucc3d\uad70 \uc6a9\ud3b8\uba74\u318d\uc9c4\ubd80\uba74'), 2215064300, 1579, STRINGDECODE('\uacc4\ubc29\uc0b0'), STRINGDECODE('&lt;\uc624\ub300\uc0b0\uacfc \uc774\uc6c3\ud55c \ub0a8\ud55c 5\uc704\ubd09&gt;<BR>\ubc31\ub450\ub300\uac04\uc758 \uc624\ub300\uc0b0\uc5d0\uc11c \uacc1\uac00\uc9c0\ub97c \uccd0 \ub0a8\ud55c\uac15\uacfc \ubd81\ud55c\uac15\uc744 \uac00\ub974\ub294 \uc9c0\ub989\uc758 \ucd5c\uace0\ubd09\uc774\ub2e4. <BR>\ud3c9\ucc3d\uad70\uc758 \uc720\uba85\ud55c \uac00\ub9ac\uc655\uc0b0\uc758 \ubaa8\uc0b0\uc774\uae30\ub3c4 \ud55c \uc624\ub300\uc0b0 \ube44\ub85c\ubd09\ubcf4\ub2e4\ub3c4 \ub354 \ub192\uc544 \ubc31\ub450\ub300\uac04\uc744 \ubc14\ub77c\ubcf4\ub294 \ucd5c\uace0\uc758 \uc804\ub9dd\ub300\ub97c \uc81c\uacf5\ud558\ub294 \uacc4\ubc29\uc0b0\uc740 \ud55c\ub77c, \uc9c0\ub9ac, \uc124\uc545, \ub355\uc720\uc0b0\uc5d0 \uc774\uc5b4 \ub0a8\ud55c \uc81c5\uc704\ubd09\uc774\ub2e4. \uadf8\ub7ec\ub098 \ub0a8\ud55c \uc81c5\uc704\ubd09\uc774\ub77c\ub294 \uc0ac\uc2e4\uc744 \uc81c\uc678\ud558\uace0\ub3c4 \uacc4\ubc29\uc0b0\uc5d0\ub294 \ud765\ubbf8\ub85c\uc6b4 \uac83\ub4e4\uc774 \uc788\ub2e4. <BR>\ucc28\ub7c9\uc774 \ub2e4\ub2c8\ub294 \ub0a8\ud55c\uc5d0\uc11c \uac00\uc7a5 \ub192\uc740 \uace0\uac1c \uc6b4\ub450\ub839\uc774 \uadf8 \ud558\ub098\ub2e4. \uc6b4\ub450\ub839\uc5d0\uc11c \uc815\uc0c1\uae4c\uc9c0\uc758 \ud45c\uace0\ucc28\ub294 488\ubbf8\ud130\ub85c \ucd08\ubcf4\uc790\ub3c4 \uc27d\uac8c \uc624\ub974\uba74\uc11c \uace0\ub3c4\uac10\uc744 \ud55c\uaecf \ub9cc\ub07d\ud560 \uc218 \uc788\uc5b4 \uacc4\ubc29\uc0b0\uc758 \uc778\uae30\ub97c \ub192\uc5ec\uc900\ub2e4. \ub0a8\ucabd \uc0b0\uc790\ub77d\uc5d0\ub294 \uc774\uc2b9\ubcf5 \uc0dd\uac00\ud130\uc640 \uc804\uad6d\uc5d0\uc11c \uc774\ub984\ub09c \ubc29\uc544\ub2e4\ub9ac\uc57d\uc218\ub97c \ud488\uace0 \uc788\ub2e4. \uc601\ub3d9\uc9c0\ubc29\uc758 \ubc14\ub2f7\ubc14\ub78c\uacfc \ub300\ub959\uc5d0\uc11c \ubd88\uc5b4\uc624\ub294 \ubd81\uc11c\ud48d\uc774 \ubd80\ub52a\uccd0\uc11c \ub9ce\uc740 \uc801\uc124\ub7c9\uc744 \ubcf4\uc778\ub2e4. \uadf8\ub798\uc11c \uc544\ub984\ub2e4\uc6b4 \uc124\uacbd\uc744 \uc5f0\ucd9c, \uadf8 \uc9c4\uac00\ub97c \ubc1c\ud718\ud558\ub294 \uacc4\ubc29\uc0b0\uc740 \uaca8\uc6b8 \uc0b0\ud589\ucf54\uc2a4\ub85c \ub2e8\uc5f0 \uc190\uaf3d\ud78c\ub2e4. <BR>\uadf8\ub7ec\ub098 \uc0b0\ud589\uc2dc\uac04\uc740 \ub208\uc774 \uc788\uc744 \ub54c\uc640 \uc5c6\uc744 \ub54c\uc5d0 \ub9ce\uc740 \ucc28\uc774\ub97c \ubcf4\uc778\ub2e4. \ubcf4\ud1b5 \ub54c\uc5d0 \ube44\ud574 \ub2a5\uc120\uc774\ub4e0 \uace8\uc9dc\uae30\uc774\ub4e0 2\ubc30 \uc774\uc0c1\uc744 \uc608\uc0c1\ud574\uc57c \ud55c\ub2e4.'), STRINGDECODE('\ub0a8\ud55c\uc5d0\uc11c \ud55c\ub77c\uc0b0, \uc9c0\ub9ac\uc0b0, \uc124\uc545\uc0b0, \ub355\uc720\uc0b0\uc5d0 \uc774\uc5b4 \ub2e4\uc12f \ubc88\uc9f8\ub85c \ub192\uc740 \uc0b0\uc73c\ub85c\uc11c \uc0b0\uc57d\ucd08\u00b7\uc57c\uc0dd\ud654 \ub4f1\uc774 \ub9ce\uc774 \uc11c\uc2dd\ud558\uace0, \ud76c\uadc0\uc218\ubaa9\uc778 \uc8fc\ubaa9\u00b7\ucca0\ucb49\ub098\ubb34 \ub4f1\uc774 \uad70\ub77d\uc744 \uc774\ub8e8\uace0 \uc788\uc5b4 \uc0dd\ud0dc\uacc4 \ubcf4\ud638\uc9c0\uc5ed\uc73c\ub85c \uc9c0\uc815\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\ubc31\ub450\ub300\uac04\uc744 \ud55c \ub208\uc5d0 \uc870\ub9dd\ud560 \uc218 \uc788\uc73c\uba70 \uaca8\uc6b8\ucca0 \uc124\uacbd\uc774 \ubc31\ubbf8. \uc6b0\ub9ac\ub098\ub77c\uc5d0\uc11c \uc790\ub3d9\ucc28\ub85c \uc624\ub97c \uc218 \uc788\ub294 \uace0\uac1c \uc911 \uac00\uc7a5 \ub192\uc740 \uc6b4\ub450\ub839\uc774 \uc788\uc73c\uba70 \ub0b4\ub9b0\ucc9c(\u5167\u9e9f\u5ddd)\uc73c\ub85c \ud750\ub974\ub294 \uacc4\ubc29\ucc9c\uc758 \ubc1c\uc6d0\uc9c0\uc784.'), STRINGDECODE('\ud574\ubc1c\uc740 \ub192\ub418 \uc0b0\uc138\ub294 \uc720\uc21c\ud558\ub2c8'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uc9c4\ubd80\ub098 \uc7a5\ud3c9\uc758 \uc5ec\uad00\uc7