INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(9, TIMESTAMP '2019-08-14 15:46:34.901', TIMESTAMP '2019-08-14 15:46:44.315', '42', STRINGDECODE('\uacf5\uc791\uc0b0\uc740 \uac15\uc6d0\ub3c4 \ud64d\ucc9c\uad70 \ub3d9\uba74\uacfc \ud654\ucd0c\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c, \uacf5\uc791\uc774 \ub0a0\uac1c\ub97c \ud3bc\uce5c \ub4ef\ud55c \uc0b0\uc138\uc5d0 \uc554\ubd09\uacfc \ub178\uc1a1\uc774 \uc5b4\uc6b0\ub7ec\uc838 \ud55c \ud3ed\uc758 \ub3d9\uc591\ud654\ub97c \uc5f0\uc0c1\ucf00 \ud558\ub294 \uc0b0\uc774\ub2e4.<BR>\uc815\uc0c1\uc5d0\uc11c \uc11c\ub0a8\ucabd \ub2a5\uc120\uc744 \ub530\ub77c 6km \uc0b0\uc790\ub77d\uc5d0 \uc788\ub294 \uc218\ud0c0\uc0ac\uc5d0\uc11c \ub178\ucc9c\ub9ac\uc5d0 \uc774\ub974\ub294 \uc57d 8km\uae38\uc774\uc758 \uc218\ud0c0\uacc4\uace1\uc740 \uc554\ubc18\uacfc \uacc4\uace1\uc758 \uacbd\uce58\uac00 \ub6f0\uc5b4\ub098 \uc5ec\ub984\ucca0 \uacc4\uace1 \ud53c\uc11c\uc9c0\ub85c \uc774\ub984\ub09c \uacf3\uc774\ub2e4. \uc815\uc0c1\uc5d0\uc11c \ubc14\ub77c\ubcf4\uba74 \ud64d\ucc9c\uad70 \uc77c\uc6d0\uc774 \ud55c\ub208\uc5d0 \ub4e4\uc5b4\uc624\uba70, \ud48d\uce58\uac00 \uc544\ub984\ub2f5\uace0 \uae4e\uc544 \uc138\uc6b4 \ub4ef\ud55c \uc554\ubcbd\uc774 \uc7a5\uad00\uc744 \uc774\ub8e8\ub294 \uacf3\uc774\ub2e4. <BR>\uc815\uc0c1\uc5d0\uc11c \uc11c\ub0a8\ucabd \ub2a5\uc120 \uc57d 6km \uc544\ub798\uc5d0 \uc788\ub294 \uc218\ud0c0\uc0ac\uc640 \ub178\ucc9c\ub9ac\uae4c\uc9c0 \uc774\uc5b4\uc9c0\ub294 \uc57d 8km\uc758 \uc218\ud0c0\uacc4\uace1\uc740 \uac16\uac00\uc9c0 \uba4b\uc9c4 \ubc14\uc704\ub4e4\uacfc \uc544\ub984\ub2e4\uc6b4 \ud48d\uacbd\uc774 \ube44\ud560 \ub370 \uc5c6\ub2e4. \uc0b0 \uc815\uc0c1\uc5d0\uc11c \ubc14\ub77c\ubcf4\uba74 \ud64d\ucc9c\uad70 \uc77c\uc6d0\uc774 \ud55c\ub208\uc5d0 \ub4e4\uc5b4\uc624\uba70, \ubd04\uc5d0\ub294 \ucca0\ucb49\uacfc \uac00\uc744\ucca0\uc5d0\ub294 \ub2e8\ud48d\uc774 \ub178\uc1a1\uacfc \ud568\uaed8 \uae4d\uc544 \uc9c0\ub978 \ub4ef\ud55c \ubc14\uc704\uc640\uc758 \ubaa8\uc2b5\uc774 \ubcf4\ub294\uc774\uc758 \ub9c8\uc74c\uc744 \uc0ac\ub85c\uc7a1\uace0 \ub208\ub36e\uc778 \uaca8\uc6b8\uc0b0 \uc5ed\uc2dc \ub4f1\uc0b0\uac1d\ub4e4\uc744 \ub9e4\ub8cc\uc2dc\ud0a4\ub294 \uacf3\uc774\uae30\ub3c4 \ud558\ub2e4.'), STRINGDECODE('\u2460 \uad74\uc6b4\ub9ac \ubc84\uc2a4\uc885\uc810-\ucc9c\uc9c0\uc0ac-\uc548\uacf5\uc791\uc7ac-\uc815\uc0c1-\ub3d9\ub989 \uc548\ubd80-\uc548\uace8-\uacf5\uc791\uace8\uac00\ub4e0 (\ucd1d 4\uc2dc\uac04 35\ubd84)<BR>\u2461 \uad70\uc5c5\uad50-\ub4f1\uc0b0\ub85c \uc548\ub0b4\ud310-\uacf5\uc791\ud3ed\ud3ec-\ub728\uba54\uae30\uace8-\uc815\uc0c1-\uc548\uacf5\uc791\uc7ac-\uc218\ub9ac\ubd09-\uc57d\uc218\ubd09-\uc218\ud0c0\uc0ac\uacc4\uace1-\uc218\ud0c0\uc0ac (\ucd1d6\uc2dc\uac04 40\ubd84)<BR>\u2462 \uc2e0\ubd09\uad50-\ub3d9\ubd09\uc0ac-\uc218\ub9ac\ubd09\uae30\ub3c4\uc6d0-\uc218\ub9ac\ubd09-\uc548\uacf5\uc791\uc7ac-\uc815\uc0c1-735\ubd09-\ubb38\ubc14\uc704\uace8-\uacf5\uc791\uace8\uac00\ub4e0 (\ucd1d 4\uc2dc\uac04 40\ubd84)'), '37.663457', '127.993009', STRINGDECODE('\uac15\uc6d0\ub3c4 \ud64d\ucc9c\uad70 \ub3d9\uba74, \ud654\ucd0c\uba74'), 2216100300, 887, STRINGDECODE('\uacf5\uc791\uc0b0'), STRINGDECODE('&lt;\ube44\uacbd\uc758 \uc218\ud0c0\uacc4\uace1 \ud488\uc740 \ud64d\ucc9c\uc758 \uba85\uc0b0&gt;<BR>\uc0b0\uc774 \ub3d9\ubb3c \ubaa8\uc2b5\uc744 \ube7c\ub2e4\ubc15\uc73c\uba74 \uc601\uae30\uac00 \uc788\ub2e4\uace0 \ud55c\ub2e4. \uc601\ucde8\uc0b0, \uc0ac\uc790\uc0b0, \uc815\uc0c1\ubd80\ub97c \ubd09\uc815\uc774\ub77c \ubd88\ub800\ub358 \uc124\uc545\uc0b0\u2026. \uadf8\ub798\uc11c \uc790\uc7a5\uc728\uc0ac\ub294 \uc774\ub4e4 \uae30\uc2ad\uc5d0 \uc9c4\uc2e0\uc0ac\ub9ac\ub97c \ubb3b\uc5c8\ub2e4. \ud558\uc9c0\ub9cc \uc9c4\uc2e0\uc0ac\ub9ac\uac00 \ub2e4 \ub5a8\uc5b4\uc84c\uc744 \ub54c \uadf8\ub7f0 \uc0b0\uc774 \ubc1c\uacac\ub418\uc5c8\ub2e4\uba74 \ubb34\ub98e\uc744 \uce60 \uc218\ubc16\uc5d0 \uc5c6\uc5c8\uc744 \uac83\uc774\ub2e4. \uacf5\uc791\uc0b0\uc774 \uadf8 \ub300\ud45c\uc801 \uc0ac\ub840\ub2e4. <BR>\ud64d\ucc9c\uac15 \uc591\ucabd\uc5d0 \ubc8c\uc5ec\uc788\ub294 \ud654\ucd0c\uba74\uc758 \ub3d9\ubd80 \uc804\uc5ed\uc744 \ubb3c\ubc1b\uc774\ub85c \uc0bc\uace0\uc788\ub294 \uad70\uc5c5\ucc9c\uc744 \uac70\uc2ac\ub7ec\uac00\uba70 \ubcf4\uba74 \uc0b0 \uba38\ub9ac\uac00 \uc601\ub77d\uc5c6\uc774 \uc0c8 \ubaa8\uc591\uc774\ub2e4. \uc591 \ub0a0\uac1c\ub97c \ub0b4\uc0bc\ud3ec\ub9ac\uc640 \uc751\ubd09\uc0b0 \uacbd\uacc4\uae4c\uc9c0 \ud3bc\uce58\uace0\uc788\ub294. \uc548\uacf5\uc791\uc7ac \uc774\uc11c(\u4ee5\u897f)\uc758 \uc0b0\uc904\uae30\ub4e4\uc740 \uc18c\uc6a9\ub3cc\uc774\ucc98\ub7fc \ub3cc\uc544\uac00\uba74\uc11c \ud64d\ucc9c\uac15\uc73c\ub85c \uae34 \uaf2c\ub9ac\ub97c \ud754\ub4e4\uace0\uc788\ub2e4. \uc774\ub97c \ubc1c\uacac\ud55c \uc0b0 \uac10\uc815\uc0ac\uc758 \uc548\ud0c0\uae4c\uc6b4 \ub9c8\uc74c\uc774 \uc190\uc5d0 \uc7a1\ud788\ub294 \uc21c\uac04\uc774\ub2e4. <BR>\ub0a8\ucabd \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc218\ud0c0\uc0ac\uacc4\uace1\uc73c\ub85c \ub354 \uc798 \uc54c\ub824\uc838\uc788\ub2e4. \ub4e4\ud310\uc744 \ud750\ub974\ub358 \ubb3c\uc774 \uc0b0 \uc18d\uc73c\ub85c \ub4e4\uc5b4\uac00 \ube44\uacbd\uc744 \uc5f0\ucd9c\ud558\ub294 \uae4c\ub2ed\uc774\ub2e4. \ub204\uc6cc\uc788\uae34 \ud558\uc9c0\ub9cc 40m \uae38\uc774\uc758 \uacf5\uc791\ud3ed\ud3ec\ub3c4 \uc788\uace0 \ubcf4\ubb3c(745, 746\ud638\uc758 \uc6d4\uc778\uc11d\ubcf4)\uc774 \ubc1c\uacac\ub41c \uc720\uc11c \uae4a\uc740 \uc808\ub3c4 \uc788\uc73c\ub2c8 \uac16\ucd9c \uac74 \ub2e4 \uac16\ucdc4\ub2e4. \uc774 \uc5b4\ucc0c \uba85\uc0b0\uc774 \uc544\ub2c8\ub77c \ud558\uaca0\ub294\uac00?'), STRINGDECODE('\uc6b8\ucc3d\ud55c \uc0b0\ub9bc\uacfc \uc218\ud0c0\uacc4\uace1 \ub4f1 \uacbd\uad00\uc774 \uc218\ub824\ud55c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\uc0b0\uc758 \ud615\uc138\uac00 \ub9c8\uce58 \ud55c \ub9c8\ub9ac\uc758 \uacf5\uc791\uc774 \ub0a0\uac1c\ub97c \ud3bc\uce5c \ub4ef\ud558\ub2e4\ub294\ub370\uc11c \uc0b0 \uc774\ub984\uc774 \uc720\ub798. \ubcf4\ubb3c \uc81c745\ud638\uc778 \uc6d4\uc778\uc11d\ubcf4 \uc81c17\uad8c\uacfc 18\uad8c\uc774 \ubcf4\uc874\ub418\uc5b4 \uc788\ub294 \uc218\ud0c0\uc0ac(\u58fd\u9640\u5bfa)\uc640 \uc218\ud0c0\uc0ac\uc5d0\uc11c \ub178\ucc9c\ub9ac\uc5d0 \uc774\ub974\ub294 20\ub9ac\uacc4\uace1\uc778 \uc218\ud0c0\uacc4\uace1\uc774 \ud2b9\ud788 \uc720\uba85'), STRINGDECODE('\uc554\ubd09\uacfc \ub178\uc1a1\uc774 \ud55c\ud3ed \ub3d9\uc591\ud654\ub85c \uac78\ub824 \uc788\uc5b4'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uacf5\uc791\uc0b0\uc5d0 \uc788\ub294 \uacf5\uc791\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e033-434-4987)\uc744 \uc774\uc6a9\ud558\uba74 \uc88b\ub2e4. <BR>\uc9c1\uc811 \uc7a5\uc791\ubd88\uc744 \ub54c\ub294 \ud669\ud1a0\ubc29 \uc0b0\ub9c9\uc774 \ud2b9\uc9d5\uc774\uba70 \ucea0\ud504\ud30c\uc774\uc5b4\ub3c4 \uac00\ub2a5\ud55c\ub370 \ubbfc\uc601\uc774\ub77c \uc774\uc6a9\ub8cc\uac00 \ud39c\uc158 \uc218\uc900\uc774\ub2e4. <BR>\uc0bc\ub9c8\uce58\ub9ac \ubd88\ud55c\uc99d\ub9c9\uc5d0\uc11c \uc798 \uc218\ub3c4 \uc788\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud64d\ucc9c\uad6d\uc720\ub9bc\uad00\ub9ac\uc18c \u260e033-4363-2497'), STRINGDECODE('\uae30\uc810\uc740 \ud64d\ucc9c\uc774\ub2e4. <BR>\ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 52\ud68c, \uc0c1\ubd09\ud130\ubbf8\ub110\uc5d0\uc11c 41\ud68c \uc788\ub2e4. <BR>\uad70\uc5c5\ub9ac\ub97c \ub4e4\uba38\ub9ac\ub85c \ud560 \ub54c\ub294 1\uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \uc788\ub294 \uc5b4\ub860\ud589 \uc644\ud589\ubc84\uc2a4, \uad74\uc6b4\ub9ac\ub294 15\ud68c \uc788\ub294 \uc131\uc0b0\ud589\uc744 \ud0c0\uace0\uac00\ub2e4 \uc1a1\uc815\uc5d0\uc11c \ud558\ucc28, 2km\ucbe4 \uac78\uc5b4 \ub4e4\uc5b4\uac04\ub2e4. <BR>\uc218\ud0c0\uc0ac\uc640 \uc2e0\ubd09\ub9ac\ub85c\ub294 \uc644\ud589\ubc84\uc2a4\uac00 3\ud68c\uc529 \ub2e4\ub2c8\uba70 \uacf5\uc791\uace8 \uc785\uad6c\ub294 3\ud68c \uc788\ub294 \ubb3c\uace8\ud589\uc744 \ud0c0 \ub178\ucc9c\uc0bc\uac70\ub9ac\uc5d0\uc11c \ub0b4\ub9b0\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(10, TIMESTAMP '2019-08-14 15:46:34.901', TIMESTAMP '2019-08-14 15:46:44.99', '11', STRINGDECODE('\uad00\uc545\uc0b0\uc740 \uc11c\uc6b8\uc2dc \uad00\uc545\uad6c\uc640 \uae08\ucc9c\uad6c, \uacbd\uae30\ub3c4 \uacfc\ucc9c\uc2dc\uc640 \uc548\uc591\uc2dc\uc5d0 \uac78\uccd0 \uc788\uc5b4 \ubd81\ud55c\uc0b0,\ub3c4\ubd09\uc0b0\uacfc \ub354\ubd88\uc5b4 \ub204\uad6c\ub098 \uc27d\uac8c \ucc3e\ub294 \uce5c\uadfc\ud55c \uc0b0\uc774\ub2e4. \uba40\ub9ac\uc11c \ubcf4\uba74 \uc628\ud1b5 \ubc14\uc704\ub85c \ub4a4\ub36e\uc5ec \uc788\ub294 \uc0b0\uc138\ub97c \uac00\uc9c4 \uad00\uc545\uc0b0\uc740 \ud574\ubc1c\uc740 \ub0ae\uc73c\ub098 \ub4f1\uc0b0\ub85c \uacf3\uacf3\uc5d0 \uc704\ud5d8\ud55c \uc554\ub989\uc774 \uc788\uc5b4 \uc870\uc2ec\ud574\uc57c \ud55c\ub2e4.<BR>\uad00\uc545\uc0b0\uc740 \uc608\ub85c\ubd80\ud130 \ubd88\uc758 \uc0b0(\u706b\u5c71)\uc774\ub77c \ud558\uc5ec \uc870\uc120 \ud0dc\uc870\uac00 \uad81\ud130\ub97c \uc9c0\uae08\uc758 \uacbd\ubcf5\uad81 \uc790\ub9ac\ub85c \uc62e\uae38 \ub54c, \ubb34\ud559\ub300\uc0ac\uac00 \uc774\uacf3\uc740 \uad00\uc545\uc0b0\uacfc \ub9c8\uc8fc \ubcf4\uc774\ub294 \uc790\ub9ac\ub85c \uad00\uc545\uc0b0\uc758 \ud654\uae30\uac00 \uad81\uc744 \ub20c\ub7ec\uc11c \ub0b4\uc6b0\uc678\ud658\uc774 \ub04a\uc774\uc9c0 \uc54a\uc744 \uac83\uc774\ub77c\uba70 \ubc18\ub300\ud588\uc9c0\ub9cc, \uc815\ub3c4\uc804\uc758 \ub0a8\ucabd\uc5d0 \ud55c\uac15\uc774 \uac00\ub85c\uc9c8\ub7ec \uc788\uc5b4\uc11c \uc601\ud5a5\uc774 \uc5c6\uc744 \uac83\uc774\ub77c\ub294 \uc8fc\uc7a5\uc744 \ubc1b\uc544 \ub4e4\uc5ec \uc9c0\uae08\uc758 \uacbd\ubcf5\uad81\uc744 \ucc3d\uac74\ud558\uc600\ub2e4 \ud55c\ub2e4. \uadf8\ud6c4 \ud0dc\uc885\ub54c \uc655\uc790\uc758 \ub09c, \uc138\uc870\uc758 \uc655\uc704 \ucc2c\ud0c8, \uc784\uc9c4\uc65c\ub780, \ubcd1\uc790\ud638\ub780, \uadf8\ub9ac\uace0 \uacbd\ubcf5\uad81\uc5d0 \ubc1c\uc0dd\ud55c \uc218\ucc28\ub840\uc758 \ud654\uc7ac\uac00 \ubc1c\uc0dd\ud55c \uac83\uc744 \ud48d\uc218\uc9c0\ub9ac\uc124\ub85c \ud574\uc11d\ud558\ub294 \uc774\ub3c4 \uc788\ub2e4. \ub300\uc6d0\uad70\uc740 \uacbd\ubcf5\uad81\uc744 \uc7ac\uac74\ud560 \ub54c \uad00\uc545\uc0b0\uc758 \ud654\uae30\ub97c \ub204\ub974\uae30 \uc704\ud574 \uacbd\ubcf5\uad81 \uc815\ubb38\uc778 \uad11\ud654\ubb38 \uc55e\uc5d0 \ubc14\ub2e4\uc758 \uc2e0\uc73c\ub85c \uc0c1\uc0c1\uc758 \ub3d9\ubb3c\uc778 \ud574\ud0dc \uc870\uac01\uc0c1\uc744 \ub9cc\ub4e4\uc5b4 \uc138\uc6e0\ub2e4.<BR>\uad00\uc545\uc0b0 \uc5f0\uc8fc\ub300\ub294 \uace0\ub824\uac00 \ub9dd\ud558\uc790 \ub0a8\uc740 \uc720\uc2e0 \uc5f4 \uc0ac\ub78c\uc774 \uad00\uc545\uc0b0 \uc808\uc5d0 \uc228\uc5b4\uc0b4\uba70 \uacbd\ubcf5\uad81\uc744 \ubc14\ub77c\ubcf4\uba70 \ud1b5\uace1\uc744 \ud588\ub2e4 \ud558\uc5ec, \uc784\uae08\uc744 \uc0ac\ubaa8\ud55c\ub2e4\ub294 \ub73b\uc73c\ub85c \uc5f0\uc8fc\ub300(\u6200\u4e3b\u81fa)\ub77c \ubd88\ub824 \uc9c0\uac8c \ub418\uc5c8\ub2e4 \ud55c\ub2e4. \uc774\uc131\uacc4\uac00 \uc5f0\uc8fc\uc554\uc744 \uc911\ucc3d\ud55c \ub4a4, \ud0dc\uc885\uc758 \ub450 \uc544\ub4e4\uc778 \uc591\ub155\ub300\uad70\uacfc \ud6a8\ub839\ub300\uad70\uc740 \ud0dc\uc885\uc774 \uc655\uc704\ub97c \uc14b\uc9f8 \ucda9\ub155\ub300\uad70(\uc138\uc885)\uc5d0\uac8c \ubb3c\ub824\uc904 \ub73b\uc744 \uc54c\uace0 \uad00\uc545\uc0b0\uc5d0 \uc785\uc0b0\ud558\uc600\ub2e4 \ud55c\ub2e4. \uc608\uc804\uc5d0 \uad00\uc545\uc0b0\uc744 \uc0bc\uc131\uc0b0\uc774\ub77c \ubd80\ub978 \uac83\uc740 \uc2e0\ub77c\uc758 \uace0\uc2b9 \uc6d0\ud6a8,\uc758\uc0c1,\uc724\ud544\uc774 \uc774 \uc0b0\uc5d0\uc11c \uc138 \uc2b9\ub824\uac00 \uc77c\ub9c9,\uc774\ub9c9,\uc0bc\ub9c9\uc758 \uc138 \uc554\uc790\ub97c \uc9d3\uace0 \ub530\ub85c \uc218\ub3c4\ud558\uc5ec \ub4dd\ub3c4\ud558\uc600\ub2e4 \ud558\uc5ec \ubd99\uc5ec\uc9c4 \uac83\uc73c\ub85c \uc804\ud574\uc9c0\uace0 \uc788\ub2e4. \uc784\uc9c4\uc65c\ub780\ub54c \uc77c\ub9c9,\uc774\ub9c9\uc740 \uc18c\uc2e4\ub418\uace0 \uc0bc\ub9c9\uc0ac(\u4e09\u5e55\u5bfa)\ub9cc \ub0a8\uc558\ub2e4\ub294 \uac83\uc774\ub2e4.'), STRINGDECODE('\u2460 \uc0ac\ub2f9\ub3d9 \ub300\ud559\ucd0c-333\ubd09-414\ubd09-559\ubd09-\uc5f0\uc8fc\uc554-\uc815\uc0c1-\uc7a5\uad70\ubc14\uc704-\uc7a5\uad70\ubc14\uc704\uace8-\uacfc\ucc9c \uacf5\uc5c5\uc9c4\ud765\uccad(\ucd1d 3\uc2dc\uac04 40\ubd84)<BR>\u2461 \uc548\uc591\uc720\uc6d0\uc9c0-\uc18c\uacf5\uc6d0-\ubd88\uc131\uc0ac-\uc815\uc0c1-\uc5f0\uc8fc\uc554-\uc81c4\uc57c\uc601\uc7a5-\uc81c1\uad11\uc7a5-\uc11c\uc6b8\ub300 \uc785\uad6c(\ucd1d 4\uc2dc\uac04 10\ubd84)<BR>\u2462 \ud638\uc554\uc0ac-\uc7a5\uad70\ubd09-\uc0bc\uc131\uc0b0-\uc0bc\ub9c9\uc0ac-\ub9dd\uc6d4\uc554-\uc18c\uacf5\uc6d0-\ubd88\uc131\uc0ac-\uacfc\ucc9c \uacf5\uc5c5\uc9c4\ud765\uccad(\ucd1d 4\uc2dc\uac04 30\ubd84)'), '37.478167', '126.951514', STRINGDECODE('\uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uad00\uc545\uad6c\u318d\uae08\ucc9c\uad6c, \uacbd\uae30\ub3c4 \uc548\uc591\uc2dc\u318d\uacfc\ucc9c\uc2dc'), 1105040200, 632, STRINGDECODE('\uad00\uc545\uc0b0'), STRINGDECODE('&lt;\uad11\ud654\ubb38 \ud574\ud0dc \uc138\uc6b0\uac8c\ud55c \ud654\uc131\uc758 \uadf9\uce58&gt;<BR>\uc61b \uacfc\ucc9c\ud604\uc758 \uc9c4\uc0b0\uc774\uc9c0\ub9cc \uc815\uba74\uc740 \uc11c\uc6b8\ucabd\uc774\ub2e4. \uac70\uae30\ub2e4 \uc0b0\uc138\uac00 \ud654\uc131(\u706b\u661f)\uc758 \uadf9\uce58\ub97c \uc774\ub904 \ubd88\uaf43 \ub108\uc6b8\uc774 \uc0c1\ubd09\uc73c\ub85c \uc218\ub834, \ud558\ub298\uc744 \ud0dc\uc6b0\ub294 \ub4ef\ud558\ub2e4. \ud574\uc9c8\ub141\uc5d0 \ub69d\uc12c\ucabd\uc5d0\uc11c \ubcf4\uba74 \uc815\ub9d0 \ubd88 \ubd99\uc740 \ub4ef\ud55c \ubc14 \uad11\ud654\ubb38 \uc55e\uc5d0 \ud574\ud0dc\uc0c1\uc744 \ub454 \uc774\uc720\ub294 \uadf8 \ubd88\uae30\uc6b4\uc774 \uacbd\ubcf5\uad81\uc73c\ub85c \uc62e\uaca8\ubd99\uc744\uae4c \uc6b0\ub824\ud574\uc11c\ub2e4. <BR>\uacfc\ucc9c\ucabd\uc740 \uadf8\ub7ec\ub098 \uc9c0\ub989\ub4e4\uc758 \uae30\uc138\uac00 \ub108\ubb34 \uc13c \uc911\uad6c\ub09c\ubc29\uc758 \uc0b0\uc138\ub2e4. \uc62c\ub824\ub2e4\ubcf4\ub294 \uc2dc\uc120\uc774 \uae09\ud574 \ubc8c\ub5bc\uc5d0 \uc3d8\uc778 \uae4c\uae4c\uba38\ub9ac \uac19\ub2e4. \uadf8\ub807\uc9c0\ub9cc \ub4e4\ud310\uc774 \uc881\uc544 \uc800\uc73c\uae30 \ubc14\ub77c\ubcfc \uc218\ub3c4 \uc5c6\ub2e4. <BR>\ubd09\ub9cc\ubbf8\uac00 \ub108\ubb34 \ube7c\uc5b4\ub098 \uacc4\uace1\ubbf8\ub294 \ubcc4\ub85c\ub2e4. \ub0a8\ubd81\uc5d0 \uc554\ubc18\uacc4\ub958\ub294 \uc788\uc73c\ub098 \uc218\ub7c9\uc774 \uc801\uc5b4 \ud3ed\ud3ec\uc640 \ub2f4\uc18c(\u6f6d\u6cbc)\ub97c \uc774\ub8e8\uc9c0 \ubabb\ud55c\ub2e4. \ubd81\ucabd \uc11c\uc6b8\ub300 \uc785\uad6c\uc5d0\uc11c \uc2e0\ub9bc\uc0ac\uac70\ub9ac\uae4c\uc9c0\ub294 \uad7d\uc774\uad7d\uc774 \uacc4\uace1\uc5d0 \uac04\uac04\uc774 \uc554\ubc18\uc774 \ub4dc\ub7ec\ub098\uc9c0\ub9cc \uc8fc\ubcc0\uc774 \uc77c\ucc0c\uae30 \uac1c\uac04\ub418\uace0 \uc2dc\uac00\ud654\ub418\uba74\uc11c \uc790\ucde8\ub97c \uac10\ucdb0\ubc84\ub838\ub2e4. <BR>\ubc18\uba74 \ud55c\uac15 \ub108\uba38\ub85c \ub0a8\uc0b0\uacfc \ubcf4\ud604\ubd09, \uc544\ub4dd\ud788 \uc0bc\uac01\uc0b0 \uc138 \ubfd4\uc774 \uadf8\ub9bc \uac19\uc73c\ub2c8 \uac00\ud788 \uba85\uc0b0\uc774\ub77c \ud560 \ub9cc\ud558\ub2e4. \ub9d1\uc740 \ub0a0\uc5d0\ub294 \uc11c\ud574\ubc14\ub2e4\ub3c4 \ubcf4\uc774\ub294 \"\ud070 \uac15\uacfc \ubc14\ub2e4\ub97c \ub07c\uace0\uc788\ub294 \uc0b0\" \uc774\ub2e4.'), STRINGDECODE('\uc608\ub85c\ubd80\ud130 \uacbd\uae30 5\uc545\uc758 \ud558\ub098\ub85c\uc11c \uacbd\uad00\uc774 \uc218\ub824\ud558\uba70, \ub3c4\uc2ec\uc9c0 \uac00\uae4c\uc774 \uc704\uce58\ud55c \ub3c4\uc2dc\uc790\uc5f0\uacf5\uc6d0(1968\ub144 \uc9c0\uc815)\uc73c\ub85c \uc218\ub3c4\uad8c \uc8fc\ubbfc\ub4e4\uc758 \ud734\uc2dd\ucc98\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\uc8fc\ubd09\uc740 \uc5f0\uc8fc\ub300(\u6200\u4e3b\u81fa)\ub85c\uc11c \uc815\uc0c1\uc5d0 \uae30\uc0c1 \ub808\uc774\ub354 \uc2dc\uc124\uc774 \uc788\uc74c. \uc2e0\ub77c\uc2dc\ub300 \uc758\uc0c1\uc774 \ucc3d\uac74\ud558\uace0 \uc870\uc120 \ud0dc\uc870\uac00 \uc911\uc218(1392\ub144)\ud55c \uc5f0\uc8fc\uc554\uacfc \uc57d\uc0ac\uc5ec\ub798\uc785\uc0c1\uc774 \uc720\uba85'), STRINGDECODE('\uc218\ucc28\ub840 \ud654\ub9c8\uac00 \uc4f8\uace0 \uac14\ub358 \ubd88\uc758 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uc57c\uc601\uc7a5\uc774\ub098 \uc790\uc5f0\ud734\uc591\ub9bc \uac19\uc740 \ud2b9\uae30\ud560 \ub9cc\ud55c \uc219\ubc15\uc2dc\uc124\uc774 \uc5c6\ub2e4. \uc880 \ub5a8\uc5b4\uc84c\uc9c0\ub9cc \uc9c0\ud558\ucca0\uc774 \ud3b8\ud55c \uc11c\uc6b8 \uc911\uad6c \uc608\uc7a5\ub3d9 \uc61b \uc548\uae30\ubd80 \uc790\ub9ac\uc5d0 \ud55c\uc625\ub9c8\uc744\uacfc \ud568\uaed8 \ub4e4\uc5b4\uc120 \uc11c\uc6b8\uc720\uc2a4\ud638\uc2a4\ud154\uc774 \uad1c\ucc2e\uc744 \uac83\uc774\ub2e4. 6\uc778\uc2e4\uc774 \ud68c\uc6d0\uc740 17000\uc6d0, \ube44\ud68c\uc6d0\uc740 19000\uc774\ub2e4. \u260e02-319-1318<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758 <BR>\uad00\uc545\uad6c\uccad \uad00\uc545\uc0b0\uad00\ub9ac\uc0ac\ubb34\uc18c \u260e02-886-4701, \uc548\uc591\uc2dc\uccad \ub179\uc9c0\uacf5\uc6d0\uacfc \uc0b0\ub9bc\ud300 \u260e031-389-2416'), STRINGDECODE('\uc9c0\ud558\ucca0\uc774 \uac00\uc7a5 \ud3b8\ub9ac\ud558\ub2e4. \uc11c\uc6b8\ub300 \ubc29\uba74\uc740 2\ud638\uc120 \uc2e0\ub9bc\uc5ed\uacfc \ubd09\ucc9c\uc5ed, \uacfc\ucc9c \ubc29\uba74\uc740 4\ud638\uc120 \uc815\ubd80\uccad\uc0ac\uc5ed, \uc548\uc591 \ubc29\uba74\uc740 1\ud638\uc120 \uad00\uc545\uc5ed\uc774\ub098 \uc2dc\ud765\uc5ed\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR>&gt; 2\ud638\uc120 \uc2e0\ub9bc\uc5ed\u21925613, 6514\ubc88 \ubc84\uc2a4 <BR>&gt; \uc11c\uc6b8\ub300\uc785\uad6c\uc5ed\u2192750\ubc88 \ubc84\uc2a4'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(11, TIMESTAMP '2019-08-14 15:46:34.902', TIMESTAMP '2019-08-14 15:46:45.631', '47', STRINGDECODE('\uc138\uc885\uc2dd\ub85d\uc9c0\ub9ac\uc9c0\uc5d0 \uc0c1\uc8fc \uc0bc\uba85\uc0b0\uc758 \ud558\ub098\ub77c\uace0 \ud588\uc744 \uc815\ub3c4\ub85c \ube7c\uc5b4\ub09c \uc0b0\uc138\ub97c \uac00\uc9c0\uace0 \uc788\ub2e4. \uadf8\ub798\uc11c \uc18d\ub9ac\uc0b0\uc774 \uc544\ub2c8\uba74\uc11c\ub3c4 \uc18d\ub9ac\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc758 \ub0a8\ucabd \uacbd\uacc4\uc5d0 \ud3ec\ud568\ub418\uc5c8\ub2e4. \uc9c0\uae08\uc740 \uac70\uc758\uac00 \ubcf4\uc740\uad70 \ub545\uc774\ub2e4. <BR>25\ubc88 \uad6d\ub3c4\ub97c \ud0c0\uace0\uac00\ub2e4 \ubcf4\uba74 \uae08\ubc29 \ub208\uc5d0 \ub744\ub2e4. \uad70\uacc4\uc77c\ud559\uc778 \uae4c\ub2ed\uc774\ub2e4. \uc790\ub77d\uc5d0\ub294 \uc2dc\ub8e8\ub97c \uc5ce\uc5b4\ub193\uc740 \ub4ef\ud55c \ubd09\uc6b0\ub9ac\uae4c\uc9c0 \uc788\uc5b4 \uc815\ub9d0 \uc2e0\ub839\uc2a4\ub7f0 \uc0b0\uc774\ub77c\ub294 \uc0dd\uac01\uc774 \ub4e4\uac8c \ud55c\ub2e4. \uadf8\ub798\uc11c\uc778\uc9c0 \uad6c\ub839\uc0b0(\u4e5d\u9748\u5c71)\uc774\ub77c\uace0\ub3c4 \ud588\ub2e4. \uad6c\ubcd1\uc0b0\uc740 \ucda9\ubd81 \ubcf4\uc740\uad70\uacfc \uacbd\ubd81 \uc0c1\uc8fc\uad70\uc758 \uc18d\ub9ac\uc0b0 \uad6d\ub9bd\uacf5\uc6d0 \ub0a8\ucabd \uad6d\ub3c4\ubcc0\uc5d0 \uc790\ub9ac\uc7a1\uace0 \uc788\ub2e4. <BR>\uc608\ub85c\ubd80\ud130 \ubcf4\uc740 \uc9c0\ubc29\uc5d0\uc11c\ub294 \uc18d\ub9ac\uc0b0\uc758 \ucc9c\ud669\ubd09\uc740 \uc9c0\uc544\ube44 \uc0b0, \uad6c\ubcd1\uc0b0\uc740 \uc9c0\uc5b4\ubbf8 \uc0b0, \uae08\uc801\uc0b0\uc740 \uc544\ub4e4 \uc0b0\uc774\ub77c \ud558\uc5ec \uc774\ub4e4\uc744 ''\uc0bc\uc0b0''\uc774\ub77c \uc77c\uceeb\ub294\ub2e4. \uc18d\ub9ac\uc0b0\uc758 \uba85\uc131\uc5d0 \uac00\ub824 \uc77c\ubc18\uc778\uc5d0\uac8c\ub294 \uc798 \uc54c\ub824\uc838 \uc788\uc9c0 \uc54a\uc544 \uc0b0 \uc804\uccb4\uac00 \uae68\ub057\ud558\uace0 \uc870\uc6a9\ud558\uba70 \ubcf4\uc874\uc774 \uc798 \ub418\uc5b4 \uc788\ub294 \ud3b8\uc774\ub2e4. \ubcf4\uc74