INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(12, TIMESTAMP '2019-08-14 15:46:34.902', TIMESTAMP '2019-08-14 15:46:46.364', '48', STRINGDECODE('\ub0a8\ud574 \uae08\uc0b0\uc740 \ub0a8\ud574\uc12c\uc744 \ub300\ud45c\ud558\ub294 \uc0b0\uc73c\ub85c, \ud55c\ub824\ud574\uc0c1\uad6d\ub9bd\uacf5\uc6d0\uc774 \ub9d0 \uadf8\ub300\ub85c \uac70\uc758 \ub2e4 \ubc14\ub2e4\ub85c \uc9c0\uc815\ub418\uc5b4 \uc788\ub294\ub370 \uc720\uc77c\ud558\uac8c \uc0b0\uc774 \ud574\uc0c1 \uacf5\uc6d0\uc5d0 \ud3ec\ud568\ub41c \uac83\uc774 \uae08\uc0b0\uc774\ub2e4. <BR><BR>\uae08\uc0b0\uc740 \ube44\ub2e8\uc744 \ub450\ub97c \ubed4\ud55c \uc0b0\uc73c\ub85c \uc720\uba85\ud558\ub2e4. \uae08\uc0b0\uc758 \ubcf8\ub798 \uc774\ub984\uc740 \ubcf4\uad11\uc0b0(\u666e\u5149\u5c71)\uc774\ub2e4. \uc774\uc131\uacc4\uac00 \uc655\uc774 \ub418\uace0\uc790 \ubcf4\ub9ac\uc554 \uc544\ub798 \uc788\ub294 ''\uc774\ud0dc\uc870\uae30\ub2e8''\uc5d0\uc11c \ubc31\uc77c\uae30\ub3c4\ub97c \ub4dc\ub9ac\uba74\uc11c \uc790\uc2e0\uc774 \uc655\uc774 \ub418\uba74 \uc774 \uc0b0\uc744 \ube44\ub2e8\uc73c\ub85c \ub36e\uaca0\ub2e4\ub294 \uc57d\uc18d\uc744 \ud588\ub2e4. \uc774\uc131\uacc4\uac00 \uc870\uc120\uc744 \uac1c\uad6d\ud55c \ub4a4, \uc57d\uc18d\uc744 \uc9c0\ud0ac \uc77c\uc744 \uc0dd\uac01\ud558\ub2c8 \ub09c\uac10\ud588\ub2e4. <BR>\uc774\ub54c \uc0ac\ub824 \uae4a\uc740 \uc2e0\ud558\uac00 \uc0b0 \uc774\ub984\uc744 \ud558\uc0ac\ud558\uba74 \ube44\ub2e8\ubcf4\ub2e4 \uc624\ub798 \uac08 \uac83\uc774\ub77c\uace0 \uc81c\uc548\ud574 \uc774\ub984\uc744 \ube44\ub2e8 ''\uae08(\u9326)''\uc790\ub97c \uc368 \uc774\uc804\uc758 \uc0b0 \uc774\ub984\uc744 \uae08\uc0b0\uc73c\ub85c \ubc14\uafc8\uc73c\ub85c \uc774\uc131\uacc4\ub294 \uc790\uc2e0\uc758 \uc57d\uc18d\uc744 \uc808\ubb18\ud558\uac8c \uc9c0\ud0ac \uc218 \uc788\uc5c8\ub2e4\uace0 \uc804\ud574\uc9c0\uace0 \uc788\ub2e4. <BR><BR>\ub9e4\ud45c\uc18c\uc5d0\uc11c \ubcf4\ub9ac\uc554\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub4f1\uc0b0\ub85c \uc67c\ud3b8\uc73c\ub85c \uc0ac\uc120\ub300\uac00 \uc62c\ub824\ub2e4 \ubcf4\uc778\ub2e4. \uc0ac\uc120\ub300\ub294 \uba3c \uc61b\ub0a0 \ub3d9\uc11c\ub0a8\ubd81\uc758 \ub124 \uc2e0\uc120\uc774 \uc870\uadf8\ub9cc \uc554\ubd09\uc5d0\uc11c \ub180\uc558\ub2e4\ub294 \uacf3\uc774\ub2e4. \uc0ac\uc120\ub300 \ub9de\uc740\ud3b8\uc5d0\ub294 \uc808\ubcbd\uc744 \uc774\ub8ec \uc6c5\uc7a5\ud55c \ubc14\uc704\ub294 \ub9cc\uc7a5\ub300\uc774\ub2e4.'), STRINGDECODE('\u2460 \ubcf5\uace1\ub9e4\ud45c\uc18c-\ubcf4\ub9ac\uc554-\uc815\uc0c1-\uc0c1\uc8fc\ub9ac (\ucd1d 2\uc2dc\uac04 30\ubd84)<BR>\u2461 \ub0a8\ud574\ud3b8\ubc31\uc790\uc5f0\ud734\uc591\ub9bc-\uae08\uc0b0\uc81c1\ucd08\uc18c-\uc815\uc0c1-\uc30d\ud64d\ubb38-\uae08\uc0b0\ub9e4\ud45c\uc18c (\ucd1d 3\uc2dc\uac04)'), '34.799561', '127.954622', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ub0a8\ud574\uad70 \uc0c1\uc8fc\uba74\u318d\uc774\ub3d9\uba74\u318d\uc0bc\ub3d9\uba74'), 2805090900, 705, STRINGDECODE('\uae08\uc0b0'), STRINGDECODE('&lt;\ub0a8\ub158 \ubc14\ub2e4\uc5d0 \uc19f\uc740 \ube44\ub2e8\uc0b0&gt;<BR>\ub0a8\ud574\uc5d0\ub294 \ube44\ub2e8\uc73c\ub85c \ubab8\uc744 \uac10\uc2fc \uc0b0\uc774 \uc788\ub2e4. \ube44\ub2e8 \uae08(\u9326)\uc790\uc5d0 \ubafc \uc0b0(\u5c71)\uc790\ub97c \uc774\ub984\uc73c\ub85c \ud55c \uae08\uc0b0\uc774\ub2e4. <BR>\uae08\uc0b0\uc740 \uc6d0\ub798 \uc6d0\ud6a8\ub300\uc0ac\uac00 \uc0b0\uc790\ub77d\uc5d0 \ubcf4\uad11\uc0ac\ub77c\ub294 \uc0ac\ucc30\uc744 \uc9c0\uc74c\uc73c\ub85c \ud574\uc11c \ubcf4\uad11\uc0b0\uc774\ub77c \ubd88\ub838\ub2e4\uace0 \ud55c\ub2e4. \uadf8\ub7f0\ub370 \uc774\uc131\uacc4\uac00 \uc784\uae08\uc774 \ub418\uae30 \uc804 \uc774 \uc0b0\uc5d0\uc11c \uae30\ub3c4\ub97c \ud558\uba70 \uc784\uae08\uc774 \ub418\uac8c \ud574\uc8fc\uba74 \uae08\uc0b0 \uc804\uccb4\ub97c \ube44\ub2e8\uc73c\ub85c \ub458\ub7ec\uc8fc\uae30\ub85c \uc57d\uc18d\ud588\ub2e4. <BR><BR>\uadf8\ub7ec\ub098 \ub9c9\uc0c1 \uadf8 \ub113\uc740 \uc0b0\uc744 \ube44\ub2e8\uc73c\ub85c \ub450\ub97c \uc77c\uc744 \uac71\uc815\ud558\ub2e4 \uc9c4\uc9dc \ube44\ub2e8\uc774 \uc544\ub2cc \ube44\ub2e8 \uae08\uc790\ub97c \uc0ac\uc6a9\ud574 \uae08\uc0b0\uc774\ub77c \uc774\ub984\uc744 \uc9c0\uc5b4\uc90c\uc73c\ub85c \uc57d\uc18d\uc744 \uc9c0\ucf30\ub2e4\uace0 \ud55c\ub2e4. \uae08\uc0b0\uc5d0 \uc624\ub974\uba74 \uc6d0\ud6a8\ub300\uc0ac\uac00 \uc2e0\ub77c \uc2e0\ubb38\uc655 3\ub144\uc5d0 \ucc3d\uac74\ud588\ub2e4\ub294 \ubcf4\ub9ac\uc554\uacfc \uac00\uc57c\uc758 \uae40\uc218\ub85c\uc655\uc758 \ubd80\uc778\uc778 \ud5c8\ud0dc\ud6c4\uac00 \uc778\ub3c4 \uc544\uc720\ud0c0\uad6d\uc5d0\uc11c \uac00\uc9c0\uace0 \uc628 \ub3cc\ub85c \ub9cc\ub4e4\uc5c8\ub2e4\uace0 \uc804\ud574\uc9c0\ub294 \uc0bc\uce35\uc11d\ud0d1\ubfd0 \uc544\ub2c8\ub77c \uad00\uc74c\ubd09, \ud654\uc5c4\ubd09, \uc77c\uc6d4\ubd09, \uc790\ub77c\ubc14\uc704, \uc88c\uc120\ub300, \uc0c1\uc0ac\ubc14\uc704, \ubd80\uc18c\uc554, \uc30d\ud64d\ubb38 \ub4f1\uc758 \ubc14\uc704\uc640 \ub9dd\uc6d4\ub300\uac00 \uc788\ub2e4. <BR><BR>\uae08\uc0b0 \uc815\uc0c1\uc5d0\uc11c\ub294 \ub0a0\uc528\uac00 \uc88b\uc740 \ub0a0\uc5d0\ub294 \uae08\uc0b0 38\uacbd\uc774 \ubaa8\ub450 \ubcf4\uc778\ub2e4\uace0 \ud560 \uc815\ub3c4\ub85c \uc870\ub9dd\uc774 \ub6f0\uc5b4\ub098\ub2e4.'), STRINGDECODE('<SPAN id=_mce_caret data-mce-bogus=\"true\"><SPAN style=\"TEXT-DECORATION: underline\" data-mce-style=\"text-decoration: underline;\">?</SPAN></SPAN>\ud55c\ub824\ud574\uc0c1\uad6d\ub9bd\uacf5\uc6d0\uc758 \uc720\uc77c\ud55c \uc0b0\uc545\uacf5\uc6d0\uc73c\ub85c \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0, \ubc14\ub2e4\uc640 \uc12c, \uc77c\ucd9c\uc744 \uc870\ub9dd\ud560 \uc218 \uc788\uc73c\uba70 \uacbd\uc0c1\ub0a8\ub3c4 \uae30\ub150\ubb3c\ub85c \uc9c0\uc815(1974\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\ubcf8\ub798 \ubcf4\uad11\uc0b0\uc774\ub77c\uace0 \ubd88\ub9ac\ub2e4\uac00 \uc870\uc120 \ud0dc\uc870\uc640 \uad00\ub828\ub41c \uc804\uc124\uc5d0 \ub530\ub77c \uae08\uc0b0\uc73c\ub85c \uc774\ub984\uc774 \ubc14\ub00c\uc5c8\ub2e4\uace0 \ud568. <BR><BR>\uc870\uc120 \ud0dc\uc870\uac00 \uae30\ub3c4\ud588\ub2e4\ub294 \uc774\uc528\uae30\ub2e8\uc744 \ube44\ub86f\ud558\uc5ec, \uc0ac\uc790\uc554, \ucd09\ub300\ubd09, \ud5a5\ub85c\ubd09 \ub4f1 38\uacbd\uc774 \uc720\uba85\ud558\uba70, \uc815\uc0c1\uc5d0\ub294 \uc6b0\ub9ac\ub098\ub77c 3\ub300 \uae30\ub3c4\ucc98\uc758 \ud558\ub098\uc778 \ubcf4\ub9ac\uc554\uc774 \uc18c\uc7ac'), STRINGDECODE('\uc774\uc131\uacc4\uac00 \ube44\ub2e8\uc73c\ub85c \ub36e\uaca0\ub2e4\ub358 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uc0c1\uc8fc\ub9ac\uc5d0 \uc5ec\uad00\uacfc \ubbfc\ubc15\ucd0c\uc774 \ud615\uc131\ub418\uc5b4 \uc788\ub2e4. <BR>\uc0c1\uc8fc\ubbfc\ubc15\ub9c8\uc744(\u260e055-862-6051)<BR>\uc0c1\uc8fc\uc5ec\uad00(\u260e055-863-0807)<BR>\ud0dc\uc591\uc7a5\uc5ec\uad00(\u260e055-862-9955)<BR>\ub3d9\ubd80\uc7a5\uc5ec\uad00(\u260e055-863-1453)<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud55c\ub824\ud574\uc0c1 \uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e055-863-3521'), STRINGDECODE('\uae30\uc810\uc740 \ub0a8\ud574\ub2e4. <BR>\ub0a8\ud574 \uacf5\uc6a9\uc5ec\uac1d\ud130\ubbf8\ub110\uc5d0\uc11c \uc0c1\uc8fc, \ubbf8\uc870\ud589 \uad70\ub0b4\ubc84\uc2a4\ub97c \uc774\uc6a9\ud558\uba74 \uae08\uc0b0\uc5d0\uc11c \ud558\ucc28\ud560 \uc218 \uc788\ub2e4.<BR>- \ub0a8\ud574\u2192\uc0c1\uc8fc, \ubbf8\uc870 : 30~40\ubd84 \uac04\uaca9 \uc6b4\ud589<BR>- \ub0a8\ud574\u2192\ubcf5\uace1 : 1\uc77c 4\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(13, TIMESTAMP '2019-08-14 15:46:34.903', TIMESTAMP '2019-08-14 15:46:46.987', '43', STRINGDECODE('\uae08\uc218\uc0b0, \uc774\ub984 \uadf8\ub300\ub85c \ube44\ub2e8\uc5d0 \uc218\ub97c \ub193\uc740 \ub4ef\ud55c \ubaa8\uc591\uc744 \ud558\uace0 \uc788\ub2e4. \uc6d4\uc545\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\uad8c \ucd5c\ubd81\ub2e8\uc5d0 \uc704\uce58\ud55c \uc774 \uc0b0\uc758 \uc774\ub984\uc740 \ubcf8\ub798 \ubc31\uc545\uc0b0\uc774\uc5c8\ub2e4. \uc870\uc120\uc870 \uc911\uc5fd \ud1f4\uacc4 \uc774\ud669 \uc120\uc0dd\uc774 \ub2e8\uc591 \uad70\uc218\ub85c \uc788\ub358 \uc2dc\uc808, \ub108\ubb34\ub3c4 \uc544\ub984\ub2e4\uc6b4 \uacbd\uce58\uc5d0 \uac10\ud0c4\ud574 \uae08\uc218\uc0b0\uc73c\ub85c \uc774\ub984\uc744 \uace0\uccd0 \ubd80\ub974\uac8c \ub418\uc5c8\ub2e4\uace0 \ud55c\ub2e4.\ud2b9\ud788 \uac00\uc744 \uacbd\uce58\uac00 \ube7c\uc5b4\ub09c \uc544\ub984\ub2e4\uc6b4 \uc554\uc0b0\uc73c\ub85c, \ubd04\uc18d\uc5d0 \uaca8\uc6b8\uc744 \ub9cc\ub0a0 \uc218 \uc788\ub294 \uc2e0\ube44\ud55c \uc0b0\uc774\ub2e4.<BR>\ub9e4\ub144 4\uc6d4\ucd08\uae4c\uc9c0 \uc5bc\uc74c\uc774 \uc5bc\ub2e4\uac00 \ucc98\uc11c\uac00 \uc9c0\ub098\uba74 \uc5bc\uc74c\uc774 \ub179\ub294 \uc5bc\uc74c\uace8\uc5d0\ub294 \ub3cc\uad6c\ub369\uc774\ub97c 30cm\uc815\ub3c4 \ub4e4\ucd94\uba74 \ubc24\ud1a8\ub9cc\ud55c \uc5bc\uc74c \ub369\uc5b4\ub9ac\uac00 \uac00\uc744\uae4c\uc9c0 \ub098\uc624\uace0 \uc788\uc5b4 \uc790\uc5f0\uc758 \uc2e0\ube44\uac10\uc744 \ub354\ud574\uc900\ub2e4. \uae08\uc218\uc0b0 \uc8fc\ub2a5\uc120\uc740 \uc0c1\uc5b4 \uc774\ube68\uc744 \uc5f0\uc0c1\ucf00\ud558\ub294 \uc554\ub989\uae38\ub85c \uc2a4\ub9b4 \ub9cc\uc810\uc774\ub2e4. <BR>\uc0b0 \uc911\ud131\uc5d0\ub294 \ubc14\uc704\ud2c8\uc5d0\uc11c \ud55c\ud574\ub098 \uc7a5\ub9c8\uc5d0\ub3c4 \uafb8\uc900\ud55c \ubb3c\uc904\uae30\ub97c \ubfdc\uc5b4\ub0b4\uace0 \uc788\uc5b4, \uc0b0\uc744 \ucc3e\ub294 \uc774\ub4e4\uc758 \ubaa9\uc744 \ucd95\uc5ec\uc8fc\uace0 \uc788\ub2e4. \ubc1c\uae38\ub9c8\ub2e4 \ub208\uae38\ub9c8\ub2e4 \uc740\uc740\ud788 \ucc28\uace0 \ub3c4\ub294 \ud48d\uacbd\uc5d0 \ucde8\ud574 \uac77\ub2e4 \ubcf4\uba74, \uc5b4\ub290\uc0c8 \uc0b0 \uc815\uc0c1. \uadf8\uacf3\uc5d0\uc11c \ub0b4\ub824\ub2e4 \ubcf4\uba74 \ub2e4\uc18c\uacf3\uc774 \ud2b8\uc778 \uc0b0\uc138\uc640 \ucda9\uc8fc\ud638\uc758 \ud478\ub978 \ubb3c\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uc740\uc740\ud55c \ucc44\uc0c9\uc774 \ubca0\uc778 \ud654\uc120\uc9c0 \uc704\uc5d0 \uc120 \uae30\ubd84\uc774 \ub4e0\ub2e4.'), STRINGDECODE('\u2460 \ub2a5\uac15\ub9ac-\uc5bc\uc74c\uace8-\uc5bc\uc74c\uace8\uc7ac-\uc815\uc0c1-\ubc31\uc6b4\ub3d9\ub9e4\ud45c\uc18c (\ucd1d 5\uc2dc\uac04 40\ubd84)<BR>\u2461 \uc0c1\ud559\ub9ac-\uc808\uace8, \uc0b4\ubc14\uc704\uace0\uac1c-\uc815\uc0c1-\uc6a9\ub2f4\ud3ed\ud3ec\uc0bc\uac70\ub9ac-\ubc31\uc6b4\ub3d9\ub9e4\ud45c\uc18c (\ucd1d 2\uc2dc\uac0450\ubd84)'), '36.948792', '128.208562', STRINGDECODE('\ucda9\uccad\ubd81\ub3c4 \uc81c\ucc9c\uc2dc \uc218\uc0b0\uba74\u318d\ub2e8\uc591\uad70 \uc801\uc131\uba74'), 2307050900, 1016, STRINGDECODE('\uae08\uc218\uc0b0'), STRINGDECODE('&lt;\uccad\ud48d\ud638\ubc18 \ube44\ub2e8\ubb3c\uacb0 \uc774\ub8ec \uc0b0&gt;<BR>\uae08\uc218\uc0b0(1015.8m)\uc740 \uc0b0\uc138\uac00 \uc218\ub824\ud558\uace0, \uace8\uc774 \uae4a\uace0 \uae30\uc554\uc808\ubcbd\uc774 \uc808\uacbd\uc744 \uc774\ub904 \uc0ac\ucca0 \ub4f1\uc0b0\uc778\ub4e4\uc758 \ubc1c\uae38\uc774 \uc774\uc5b4\uc9c0\ub294 \uacf3\uc774\ub2e4. \ube44\ub2e8\ud3ed\uc744 \uae54\uc544\ub193\uc740 \ub4ef\ud55c \ub179\uc74c \uc18d\uc5d0 \uacc4\uace1\uc758 \ub9d1\uc740 \ubb3c\uc18c\ub9ac, \ubb3c\ud655\uc5d0 \ub5a8\uc5b4\uc9c0\ub294 \ud3ed\ud3ec\uc18c\ub9ac, \uaf43, \ub179\uc74c, \ub2e8\ud48d, \uc124\uacbd \ub4f1 \uc5b4\ub290 \ud55c\uacc4\uc808\ub3c4 \ube7c\ub193\uc9c0 \uc54a\uace0 \uc544\ub984\ub2e4\uc6c0\uc744 \uc790\ub791\ud55c\ub2e4. \uae08\uc218\uc0b0\uc740 \ubcf8\ub514 \ubc31\uc6b4\uc0b0\uc774\uc5c8\ub2e4. \ub2e8\uc591\uad70\uc218\ub97c \uc9c0\ub0b8 \ud1f4\uacc4 \uc774\ud669\uc774 ''\ube44\ub2e8\uc5d0 \uc218 \ub193\uc740 \uac83\ucc98\ub7fc \uc544\ub984\ub2f5\ub2e4''\uba70 \uac10\ud0c4, \uae08\uc218\uc0b0\uc774\ub77c\uace0 \ud588\ub2e4\uace0 \ud55c\ub2e4. <BR>\uccad\ud48d\ud638\ubc18\uc744 \ub07c\uace0 \uc788\ub294 \uae08\uc218\uc0b0\uc740 \ubd81\ucabd\uc73c\ub85c\ub294 \uc81c\ucc9c\uc2dc\ub0b4\uae4c\uc9c0, \ub0a8\ucabd\uc73c\ub85c\ub294 \ub2e8\uc591 \ub9d0\ubaa9\uc0b0\uae4c\uc9c0 \ubed7\uc5b4\ub0b4\ub9b0 \uc81c\ubc95 \uae34 \uc0b0\uc904\uae30\ub2e4. \uc8fc\ub2a5\uc120\uc5d0\ub294 \uc791\uc131\uc0b0, \ub3d9\uc0b0, \ub9d0\ubaa9\uc0b0 \ub4f1 \ud574\ubc1c 700~800\ubbf8\ud130 \uc0b0\ub4e4\uc774 \uc5ec\ub7ff\uc774\ub2e4. \uc911\uac04\ub9c8\ub2e4 \uc11c\ucabd\uc73c\ub85c \ubed7\uc740 \uc9c0\ub989\uc5d0\ub294 \uc911\ubd09, \uc2e0\uc120\ubd09, \uc800\uc2b9\ubd09, \ub9dd\ub355\ubd09 \ub4f1 \uc218\ub824\ud55c \uc0b0\ub4e4\uc744 \uac70\ub290\ub9ac\uace0 \uc788\ub2e4.'), STRINGDECODE('\uc6d4\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \ubd81\ub2e8\uc5d0 \uc704\uce58\ud558\uace0 \uc6b8\ucc3d\ud55c \uc18c\ub098\ubb34 \uc232\uacfc \ub9d1\uace0 \uae68\ub057\ud55c \uacc4\ub958 \ub4f1 \uacbd\uad00\uc774 \ub6f0\uc5b4\ub09c \uc810\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubd04\ucca0\uc758 \ucca0\ucb49\uacfc \uac00\uc744\ucca0\uc758 \ub2e8\ud48d\uc774 \ud2b9\ud788 \uc720\uba85\ud558\uace0 \ub2a5\uac15\uacc4\uace1\uacfc \uc5bc\uc74c\uace8\uc774 \uc788\uc74c. \uc815\uc0c1\uc5d0\uc11c \uc18c\ubc31\uc0b0\uc758 \uc6c5\uc7a5\ud55c \uc0b0\uc904\uae30\uc640 \ucda9\uc8fc\ud638\ub97c \uc870\ub9dd\ud560 \uc218 \uc788\uc74c'), STRINGDECODE('\ub208 \uc2dc\ub9b0 \uac00\uc744 \ube44\ub2e8\uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc0c1\ucc9c\ub9ac \ubc31\uc6b4\ub3d9\uc785\uad6c\uc5d0 \uc219\uc2dd\ud560 \uacf3\uc774 \uc788\ub2e4. <BR>\uc0c1\ucc9c\ud734\uac8c\uc18c(\u260e043-651-3735)\uc5d0\uc11c \uc219\ubc15\uacfc \uc2dd\uc0ac \uac00\ub2a5\ud558\uba70 \ubbfc\ubc15\ub3c4 \uc54c\uc120\ud574 \uc8fc\uba70 \uae08\uc218\uc0b0\uc2dd\ub2f9(\u260e043-653-2558)\ub3c4 \uc788\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc