INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(14, TIMESTAMP '2019-08-14 15:46:34.904', TIMESTAMP '2019-08-14 15:46:47.59', '47', STRINGDECODE('\uad6c\ubbf8\uc5ed\uc5d0\uc11c \ub0a8\uc11c\ucabd\uc73c\ub85c \uc57d 4km \ub5a8\uc5b4\uc838 \uc6b0\ub69d \uc19f\uc740 \uae08\uc624\uc0b0\uc740 1970\ub144 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \uc0b0\uc774\ub2e4. \uc0b0 \uc804\uccb4\uac00 \ubc14\uc704\ub85c \uc774\ub8e8\uc5b4\uc838 \uae30\uc554\uc808\ubcbd\uacfc \uacbd\uc0ac\uac00 \uc2ec\ud55c \uc0b0\uc138\ub97c \uc774\ub8e8\uace0 \uc788\uc73c\uba70, \uc0b0\uc544\ub798 \ub300\ud61c\ud3ed\ud3ec\uae4c\uc9c0 \ucf00\uc774\ube14\uce74\uac00 \uc124\uce58\ub418\uc5b4 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc57c\uc601\uc7a5-\ub4f1\uc0b0\ub85c \uad50\ucc28\uc810-\uce7c\ub2e4\ubd09-\uc11c\ubb38-\uc815\uc0c1-\ud560\ub531\uace0\uac1c-\ub9e4\ud45c\uc18c (\ucd1d 4\uc2dc\uac04 20\ubd84)<BR>\u2461 \uae08\uc624\ub3d9\ucc9c-\ub0a8\ubb38-\uc131\uc548 \uc0ac\uac70\ub9ac-\uc815\uc0c1-\uc57d\uc0ac\uc554-\uacc4\uace1\uae38-\ubc95\uc131\uc0ac (\ucd1d 3\uc2dc\uac04)<BR>\u2462 \uc6b0\uc7a5-\uc11c\ubb38-\uc131\uc548 \uc0ac\uac70\ub9ac-\uc815\uc0c1-\ub3c4\uc218\ub839-\ub2a5\uc120 \uc0ac\uac70\ub9ac-\uc0c1\ubaa8\ub3d9 (\ucd1d 4\uc2dc\uac04 10\ubd84)'), '36.119638', '128.344292', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uad6c\ubbf8\uc2dc, \uce60\uace1\uad70 \ubd81\uc0bc\uc74d, \uae40\ucc9c\uc2dc \ub0a8\uba74'), 2704390100, 977, STRINGDECODE('\uae08\uc624\uc0b0'), STRINGDECODE('&lt;\ubd09\ub9cc\ubbf8 \ub6f0\uc5b4\ub09c \uae08\uae4c\ub9c8\uadc0\uc758 \uc2e0\ud654&gt;<BR>\uae08\uc624\uc0b0\uc740 \uc2e0\ud654\ub2e4. \ud0dc\uc591 \uc18d\uc5d0 \uc0b0\ub2e4\ub294 \uc138 \ubc1c \ub2ec\ub9b0 \uae4c\ub9c8\uadc0\uac00 \uae08\ube5b \ub0a0\uac1c\ub97c \ud3bc\uce5c \ubaa8\uc2b5\uc774\ub2e4. <BR>\ub54c\ub294 \uc11d\uc591\uc774\uc5c8\uc73c\ub9ac\ub77c. \ubcf8 \uc790\ub9ac\ub294 \ub3d9\ucabd\uc774\uc5c8\uc744 \uac70\uace0. \uc774\ub984\uc9c0\uc740 \uc0ac\ub78c\uc740 \uace0\uad6c\ub824\uc778\uc774\uc5c8\uc744 \ud130. \uadf8\ub807\ub2e4\uba74 \uadf8\ub294 \ubd84\uba85 \ub099\ub3d9\uac15 \uac74\ub108 \ub3c4\ub9ac\uc0ac\uc758 \uc544\ub3c4\uc600\uc744 \uac83\uc774\ub2e4. <BR>\uc2e0\ud654\uac00 \uc785\ud600\uc9c4 \uc0b0\uc740 \uadf8\uc5d0 \uac78\ub9de\ub294 \ud48d\ubaa8\ub97c \uc9c0\ub154\ub2e4. \ud558\ub298 \uc7a5\ub9c9\uc744 \uccd0\ub4e4\uace0 \uc19f\uc544\uc624\ub978 \ubd09\ub9cc\uc758 \ubb34\uc30d\ud568\uc774 \uc788\uace0 \ubc14\uc717\uace8 \uc548\ud1b5 \ubc94\uc811 \ubabb\ud558\uac8c \ud3ed\ud3ec\ub97c \ubc30\uce58\ud588\ub2e4. 2.5km\uc758 \uace8\uc9dc\uae30 \ubb3c\uc774 \ub300\ud61c\ub2f4\uc5d0 \uba38\ubb34\ub974\ub2e4 \uba85\uae08\ud3ed\ud3ec\ub85c \ub5a8\uc5b4\uc9c0\uac8c \ub9cc\ub4e4\uc5c8\ub2e4. \uc774\uac78 \ud65c\uc6a9\ud574 \uc0ac\ub78c\ub4e4\uc740 \ub610 \uc131\uc744 \uc313\uc558\uc73c\ub2c8\u2026 \ube7c\uc5b4\ub09c \uc790\uc5f0\uacfc \uc778\uc704\uac00 \uc5b4\uc6b0\ub7ec\uc9c4 \ucd5c\ucd08\uc758 \ub3c4\ub9bd\uacf5\uc6d0\uc774\ub2e4. <BR>\uc0c1\ubd09 \ud604\uc6d4\ubd09\uacfc \uadf8 \uc606\uc758 \uc57d\uc0ac\ubd09(958m), \uc6a9\ube44\ub298\ubc14\uc704\ub77c\uace0 \ud558\ub294 \ubcf4\ubd09(933m)\uc774 900\ubbf8\ud130\ub97c \uc6c3\ub3c8\ub2e4. \ub0a8\ubb38 \uc606\uc5d0\ub294 \ub0a8\ubd09(873m)\uc774, \ud604\uc6d4\ubd09 \uac74\ub108\uc5d0\ub294 \uc11c\ubd09(851m)\uc774 \uc788\uc5b4 2km \ub458\ub808\uc758 \uc131\uc548\uc744 \uc2fc\ub2e4. <BR>\uae08\uc624\ub3d9\ucc9c\uc740 \uc120\ube44\ub4e4\uc758 \ub180\uc774\ud130\ub2e4. \uc554\ubc18\uacc4\ub958\uc640 \ub2f4\uc18c(\u6f6d\u6cbc)\uac00 \uae30\uc554 \uc0ac\uc774\ub85c \ud3bc\uccd0\uc9c4\ub2e4. \uc544\ub798\uc5d0\uc11c\ubd80\ud130 \ubc85\uc2dc\uc18c, \uc6a9\uc2dc\uc18c, \uad6c\uc2dc\uc18c\u2026 \ub9c8\uc9c0\ub9c9\uc73c\ub85c \uc120\ub140\ud0d5\uc774 12m \uc640\ud3ed \ub05d\uc5d0\uc11c \ub9f4\ub3cc\uc774\ud55c\ub2e4.'), STRINGDECODE('\uae30\uc554\uc808\ubcbd\uacfc \uc6b8\ucc3d\ud55c \uc0b0\ub9bc\uc774 \uc870\ud654\ub418\uc5b4 \uacbd\uad00\uc774 \uc218\ub824\ud558\uba70, \ubb38\ud654\uc720\uc0b0\uc774 \ub9ce\uace0 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1970\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ub192\uc774 38m\uc758 \uba85\uae08\ud3ed\ud3ec\uac00 \uc788\uc73c\uba70, \uc815\uc0c1\ubd80\uadfc\uc5d0\ub294 \uc790\uc5f0\uc554\ubcbd\uc744 \uc774\uc6a9\ud574 \ucd95\uc131\ud55c \uae38\uc774 2\u339e\uc758 \uae08\uc624\uc0b0\uc131\uc774 \uc788\uc74c. \ud574\uc6b4\uc0ac, \uc57d\uc0ac\uc554 \ub4f1\uc758 \uace0\ucc30\uacfc \uae08\uc624\uc0b0\ub9c8\uc560\ubcf4\uc0b4\uc785\uc0c1(\ubcf4\ubb3c \uc81c490\ud638), \uc120\ubd09\uc0ac\ub300\uac01\uad6d\uc0ac\ube44(\ubcf4\ubb3c \uc81c251\ud638), \uc11d\uc870\uc11d\uac00\uc5ec\ub798\uc88c\uc0c1(\ubcf4\ubb3c \uc81c245\ud638) \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ub9c8\uc560\ubcf4\uc0b4\uc785\uc0c1\uc774 \uc9c0\ud0a4\ub294 \ub0a8\uc22d\uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>34\ub9cc\uc758 \uad6c\ubbf8\uc2dc\ub77c \ud638\ud154\uc774\ub098 \ubaa8\ud154\uc740 \uc77c\uc77c\uc774 \uc5f4\uac70\ud560 \uc218 \uc5c6\uc774 \ub9ce\ub2e4. <BR>\ub3c4\ub9bd\uacf5\uc6d0\uc774\ub2c8 \ud150\ud2b8\ub97c \uc900\ube44\ud574 \uc57c\uc601\ud558\ub294 \uac83\ub3c4 \uad1c\ucc2e\uc744 \uac83\uc774\ub2e4<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uae08\uc624\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e054-450-5760'), STRINGDECODE('\uae30\uc810\uc740 \uad6c\ubbf8\ub2e4. <BR>\uacbd\ubd80\uc120 \ucca0\ub3c4\uc640 \uacbd\ubd80\uace0\uc18d\uad6d\ub3c4\ubfd0 \uc544\ub2c8\ub77c \uc911\ubd80\ub0b4\ub959\uace0\uc18d\uad6d\ub3c4\uae4c\uc9c0 \uc9c0\ub098\uac00 \uad50\ud1b5\uc774 \uc544\uc8fc \ud3b8\ub9ac\ud558\ub2e4. <BR>\uae08\uc624\uc0b0\uc73c\ub85c \uac00\ub824\uba74 \uad6c\ubbf8\uc5ed\uc774\ub098 \uace0\uc18d\ud130\ubbf8\ub110\uc5d0\uc11c 12\ubc88, 12-1\ubc88 \uc2dc\ub0b4\ubc84\uc2a4\ub97c \ud0c4\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(15, TIMESTAMP '2019-08-14 15:46:34.904', TIMESTAMP '2019-08-14 15:46:48.195', '26', STRINGDECODE('\ubd80\uc0b0\uc2dc\uc758 \ub3d9\ub798\uc628\ucc9c \ubd81\ucabd 4km\ub418\ub294 \uacf3\uc5d0 \uc788\uc73c\uba70, \uc77c\ubd80\ub294 \uacbd\ub0a8 \uc591\uc0b0\uad70\uc5d0 \uc18d\ud574 \uc788\ub2e4.<BR>\ubb34\ud55c\uc758 \ubcf4\uace0\uc778 \ubc14\ub2e4\uc640 \ub354\ubd88\uc5b4 \uc0b0\uc790\ub77d\uc740 \ud48d\uc694\ub85c\uc6b4 \uc0b6\uc758 \ud130\uc804\uc774 \ub418\uace0 \uc788\ub294 \ubd80\uc0b0\uc758 \ubaa8\uc0b0\uc774\uae30\ub3c4 \ud558\ub2e4.<BR>\uc0b0\uc138\ub294 \ub2a5\uc120\uc774 \uc644\ub9cc\ud558\uac8c \ubed7\uc5b4\ub0b4\ub824 \uc694\ub780\uc2a4\ub7ec\uc6c0\uc774 \uc5c6\uace0, \uace0\ub2f4\ubd09\uc744 \ube44\ub86f\ud558\uc5ec \ubd80\ucc44\ubc14\uc704,\ub098\ube44\ubc14\uc704,\ub300\ub959\ubd09 \ub4f1 \uc554\uc7a5\uc774 \uc788\uc73c\uba70, \ub2a5\uc120 \uc5b8\uc800\ub9ac\uc5d0\ub294 \uacf3\uacf3\uc5d0 \uc5b5\uc0c8 \ubc2d\uc774 \uc788\uace0 \ub0a0\ub4f1 \uc5b4\ub514\uc11c \ubcf4\ub098 \ub099\ub3d9\uac15\uacfc \ubd80\uc0b0\uc2dc\ub0b4\ub97c \ub0b4\ub824\ub2e4 \ubcfc \uc218 \uc788\uc5b4 \uac00\uc2b4\uc774 \ud6c4\ub828\ud558\ub2e4. <BR><BR>\uace0\ub2f9\ubd09,\uc758\uc0c1\ubd09,\uc0c1\uacc4\ubd09,\uc11c\ubb38\uc744 \uc5f0\uacb0\ud55c \ub0a0\ub4f1\uc5d0\ub294 \uc678\uce68\uc744 \ub300\ube44\ud55c \uc870\uc120 \uc219\uc885 29\ub144\uc5d0 \uc313\uc740 \uad6d\ub0b4 \ucd5c\ub300\uaddc\ubaa8\uc758 \uae08\uc815\uc0b0\uc131\uc774 \uc790\uc5f0\uc11d\uc73c\ub85c \ucd95\uc870\ub418\uc5b4 \uc788\uace0, \ub3d9\ucabd \uc0b0\ub85d\uc5d0\ub294 \ub300\ucc30 \ubc94\uc5b4\uc11c\uc640 \ub9ce\uc740 \uc554\uc790\uac00 \uc788\ub2e4.<BR>\ubc94\uc5b4\uc0ac\ub294 \ud569\ucc9c \ud574\uc778\uc0ac, \uc591\uc0b0 \ud1b5\ub3c4\uc0ac, \uc21c\ucc9c \uc1a1\uad11\uc0ac, \uad6c\ub840 \ud654\uc5c4\uc0ac\uc640 \ub354\ubd88\uc5b4 \uc6b0\ub9ac\ub098\ub77c 5\ub300 \uc0ac\ucc30 \uc911\uc758 \ud558\ub098\ub85c \ub9ce\uc740 \ubd88\uad50 \uc5ed\uc0ac\uc720\uc801\uc744 \uac04\uc9c1\ud55c \uc720\uba85\ud55c \uc0ac\ucc30\uc774\uba70 \uc778\uadfc\uc5d0\uae08\uac15\uacf5\uc6d0\uacfc \ub3d9\ub798\uc628\ucc9c\uc774 \uc788\ub2e4.<BR><BR>\uc0b0\uc815\uc5d0\ub294 \ub192\uc774 3\uc7a5(\u4e08:1\uc7a5\uc740 10\uc790) \uc815\ub3c4\uc758 \ub3cc\uc774 \uc788\uace0 \uc0d8\uc740 \ub458\ub808\uac00 10\uc5ec \uc790[\u5c3a]\uc774\uace0 \uae4a\uc774\uac00 7\uce58[\u5bf8]\ub85c\uc11c \ub298 \ubb3c\uc774 \ucc28 \uc788\uc73c\uba70 \uac00\ubb44\uc5d0\ub3c4 \ub9c8\ub974\uc9c0 \uc54a\uace0 \uae08\ube5b\uc774 \ub0ac\ub294\ub370, \uae08\uc0c9 \ubb3c\uace0\uae30\uac00 5\uac00\uc9c0 \uc0c9\uc758 \uad6c\ub984\uc744 \ud0c0\uace0 \ud558\ub298\uc5d0\uc11c \ub0b4\ub824\uc640 \uadf8 \uc0d8\uc5d0\uc11c \ub180\uc558\ub2e4\ub294 \uc804\uc124\uc5d0\uc11c \uc0b0\uc774\ub984\uc774 \uc720\ub798\ub418\uc5c8\ub2e4\uace0 \uc804\ud574\uc9c4\ub2e4.'), STRINGDECODE('\u2460 \ud638\ud3ec-\uace0\ub2f9\ubd09-\ubd81\ubb38-\uc815\uc218\uc554-\uae08\uc131\ub3d9 (\ucd1d 3\uc2dc\uac04 5\ubd84)<BR>\u2461 \uc591\uc0b0 \uc678\uc1a1\ub9ac-\uae08\ub95c\uc0ac-\uc7a5\uad70\ubd09-\uace0\ub2f9\ubd09-\ubd81\ubb38-\ubc94\uc5b4\uc0ac (\ucd1d 3\uc2dc\uac04 30\ubd84)<BR>\u2462 \ub450\uc2e4\uc5ed-\ud68c\ub8e1\uc554-\uc758\uc0c1\ubd09-\uc6d0\ud6a8\ubd09-\uace0\ub2f9\ubd09-\ubbf8\ub975\ubd88 \ud45c\uc9c0\uc11d-\ubc94\uc5b4\uc0ac (\ucd1d 3\uc2dc\uac04 20\ubd84)'), '35.243074', '129.092124', STRINGDECODE('\ubd80\uc0b0\uad11\uc5ed\uc2dc \uae08\uc815\uad6c\u318d\ubd81\uad6c, \uacbd\uc0c1\ub0a8\ub3c4 \uc591\uc0b0\uc2dc'), 2810040200, 801, STRINGDECODE('\uae08\uc815\uc0b0'), STRINGDECODE('&lt;\ubd80\uc0b0\uc744 \uc9c0\ud0a4\ub294 \uc0b0\uc131 \uba85\uc0b0&gt;<BR>\ud56d\ub3c4 \ubd80\uc0b0\uc758 \uc9c4\uc0b0 \uae08\uc815\uc0b0\uc740 \uc2dc\uc6d0\ud55c \uc870\ub9dd\uacfc \uc544\uae30\uc790\uae30\ud55c \uc0b0\uc131, \uba85\ucc30 \ubc94\uc5b4\uc0ac\uac00 \uc5b4\uc6b0\ub7ec\uc9c4 \uc0b0\uc774\ub2e4. <BR>\uae08\uc815\uc0b0\uc774\ub780 \uc774\ub984\uc740 \uc0b0\uaf2d\ub300\uae30\uc5d0 \uae08\ube5b\uc744 \ub764, \uc0d8\uc774 \uc788\ub2e4\ub294 \ub370\uc11c \uc720\ub798\ud55c\ub2e4. \uc218\ub9ce\uc740 \ubd80\uc0b0\uc2dc\ubbfc\uc774 \ucc3e\ub294 \uae08\uc815\uc0b0\uc740 \ub192\uc740 \uc0b0\uc138\uc5d0\ub2e4 \ub3d9\ub798\uc628\ucc9c\uc7a5, \uae08\uac15\uacf5\uc6d0, \uc0b0\uc131\uc720\uc6d0\uc9c0 \ub4f1 \uac01\uc885 \uc704\ub77d\uc2dc\uc124\ub4e4\uae4c\uc9c0 \uc788\uc5b4 \ud734\uc2dd\ucc98\ub85c \uc190\uc0c9 \uc5c6\ub294 \uc0b0\uc774\ub2e4. <BR>\ubcd1\ud48d\ubc14\uc704, \uc740\ubcbd, \ub300\ub959\ubd09\uc554\uc7a5 \ub4f1 \ubd80\uc0b0 \ud074\ub77c\uc774\uba38\ub4e4\uc744 \ud0a4\uc6cc\ub0b8 \uc88b\uc740 \ubc14\uc704\ub4e4\ub3c4 \ub9ce\ub2e4. \ub610 \uae08\uc815\uc0b0\uc131\uc740 \ube44\uad50\uc801 \uc798 \ubcf4\uc874\ub418\uc5b4 \ud6cc\ub96d\ud55c \uc5ed\uc0ac\uad50\uc721\uc758 \uc7a5\uc774\uae30\ub3c4 \ud558\ub2e4.'), STRINGDECODE('\uc0b0\ub9bc\uc774 \uc6b8\ucc3d\ud558\uace0 \uc0b0\uc138\uac00 \ube44\uad50\uc801 \uc6c5\uc7a5\ud558\uba70 \ub3c4\uc2ec\uc9c0 \uac00\uae4c\uc774 \uc704\uce58\ud55c \uc2dc\ubbfc\ub4e4\uc758 \ud734\uc2dd\ucc98\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc5ed\uc0ac\uc801\uc73c\ub85c \ub098\ub77c\ub97c \uc9c0\ud0a4\ub294 \ud638\uad6d\uc758 \uc0b0\uc73c\ub85c\uc11c \ud638\uad6d\uc0ac\ucc30 \ubc94\uc5b4\uc0ac\uc640 \uc6b0\ub9ac\ub098\ub77c 5\ub300 \uc0b0\uc131\uc758 \ud558\ub098\uc778 \uae08\uc815\uc0b0\uc131\uc774 \uc788\uc74c. <BR>\ub099\ub3d9\uac15 \uc9c0\ub958\uc640 \uc218\uc601\uac15\uc758 \ubd84\uc218\uacc4(\u5206\u6c34\u754c)\ub97c \uc774\ub8e8\uace0, \uae08\uac15\uacf5\uc6d0 \ubc0f \uc131\uc9c0\uace1\uacf5\uc6d0 \ub4f1\uc774 \uc788\uc74c'), STRINGDECODE('\ubd80\uc0b0 \uc2dc\ubbfc\uc758 1\uc77c \ub4f1\uc0b0\ucf54\uc2a4'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc0b0\uc131\ub9c8\uc744\uc5d0\uc11c\ub294 \uc219\ubc15\uc774 \uac00\ub2a5\ud558\uba70 \ub3d9\ub798\uc628\ucc9c\uc7a5\uc758 \uc219\ubc15\uc2dc\uc124\uc744 \uc774\uc6a9\ud574\ub3c4 \ub41c\ub2e4. <BR>\ubc94\uc5b4\uc7a5\uc5ec\uad00 \u260e051-508-5805<BR>\ud5c8\uc2ec\uccad \u260e051-555-1121<BR>\uc0b0\uc131\uc9d1 \u260e051-517-7900<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uae08\uc815\uad6c\uccad \u260e051-514-5501'), STRINGDECODE('\uae30\uc810\uc740 \ubd80\uc0b0 \uc9c0\ud558\ucca0\uc5ed\uc774\ub2e4. <BR>\ubc94\uc5b4\uc0ac\ub294 \ubc94\uc5b4\uc0ac\uc5ed 5\ubc88 \ucd9c\uad6c\ub85c \ub098\uc640 5\ubd84 \uc815\ub3c4 \uac78\uc73c\uba74 \ub9cc\ub098\ub294 \uc0bc\uc2e0\uad50\ud1b5 \uc885\uc810\uc5d0\uc11c 90\ubc88 \ubc84\uc2a4\ub97c \uc774\uc6a9\ud55c\ub2e4. \uc628\ucc9c\uc7a5\uc5ed\uc5d0\uc11c 15\ubd84 \uac70\ub9ac\uc778 \uae08\uac15\uacf5\uc6d0\uc740 8\ubc88 \ubc84\uc2a4\ub098 203\ubc88 \uc0b0\uc131\ubc84\uc2a4\ub97c \ud0c4\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(16, TIMESTAMP '2019-08-14 15:46:34.906', TIMESTAMP '2019-08-14 15:46:48.809', '46', STRINGDECODE('<P>\uc804\ub77c\ub0a8\ub3c4 \uc2e0\uc548\uad70 \ud751\uc0b0\uba74 \ud64d\ub3c4\ub9ac\uc5d0 \uc704\uce58\ud558\uace0 \uc788\ub294 \ud64d\ub3c4\ub294 \ub300\ud751\uc0b0 \ubcf8\uc12c\uc758 \ubd80\uc18d \ub3c4\uc11c\ub85c\uc11c \ub2e4\ub3c4\ud574 \ud574\uc0c1 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \ub9e4\uac00\ub3c4\ub77c\uace0\ub3c4 \ud55c\ub2e4. \ud64d\ub3c4\ub294 \ubcf8 \uc12c\uc744 \ube44\ub86f\ud55c 20\uc5ec \uac1c\uc758 \ubd80\uc18d \uc12c\uc774 \uc808\uc815\uc744 \uc774\ub8e8\uc5b4 \ub0a8\ud574\uc758 \uc18c\uae08\uac15\uc73c\ub85c \ubd88\ub9b0\ub2e4. \uadf8 \uc911\uc5d0\uc11c \ub179\uc12c\uc758 \ud574\ub3cb\uc774\ub294 \uac00\ud788 \uc7a5\uad00\uc774 \uc544\ub2d0 \uc218 \uc5c6\ub2e4. \ud30c\ub3c4\uc640 \ubc14\ub2f7\ubb3c\uc774 \ucd9c\ub801\uac70\ub9ac\ub294 \uac00\uc6b4\ub370 2\uac1c\uc758 \ubc14\uc704 \uc0ac\uc774\ub85c \ud574\uac00 \ub5a0\uc624\ub978 \uad11\uacbd\uc740 \ub9d0\ub85c \ud45c\ud604\ud560 \uc218 \uc5c6\uc744 \uc815\ub3c4\ub85c \uc544\ub984\ub2f5\ub2e4. \uc5ec\uae30\uc5d0 \ub367\ubd99\uc5ec \ud64d\ub3c4\uc758 \ub099\uc870 \ub610\ud55c \ub193\uce60 \uc218 \uc5c6\ub294 \ube44\uacbd\uc774\ub2e4.<BR>\uc5b4\ubbf8\uc12c\uc758 \uc8fc\ubd09\uc778 \uae43\ub300\ubd09(\ud574\ubc1c 367m)\uacfc \ub0a8\ucabd\uc758 \uae43\ub300\ubd09 \uc8fc\ubcc0\uc5d0\ub294 \uc544\ub984\ub4dc\ub9ac \ub3d9\ubc31\ub098\ubb34 \uc232, \ud6c4\ubc15\ub098\ubb34, \uc2dd\ub098\ubb34 \ub4f1 \ud718\uadc0\uc2dd\ubb3c 5\ubc31\uc5ec\uc885\uc774 \uc788\uc73c\uba70 2\ubc31\uc5ec \uc885\uc758 \ub3d9\ubb3c\uacfc \uace4\ucda9\uc774 \ud568\uaed8 \uc11c\uc2dd\ud558\uace0 \uc788\ub2e4. <BR>\uae43\ub300\ubd09 \uc0b0\ud589\uc740 \uac00\ud30c\ub974\uae30 \uadf8\uc9c0 \uc5c6\uace0 \ucca0\ucb49,\ub3d9\ubc31\ub4f1 \uc774\ub984\ubaa8\ub97c \ub098\ubb34\ub4e4\uc774 \uc628\ud1b5 \ube7d\ube7d\uc774 \uc790\ub77c\uace0, \uc8fc\ubd09\uc5d0 \ub2ff\uc73c\uba74 \ubfb0\uc871\ud55c \ubaa8\uc591\uc774 \ub9c8\uce58 \ubc14\ub298\uac19\ub2e4. <BR>\ub0a8\uc11c\ub85c \uc591\uc0c1\ubd09\uc758 \uc5f0\ubd09\uc774 \ud55c \ud3ed\uc758 \ub3d9\uc591\ud654\ub97c \ud3bc\uccd0 \ub193\uc740 \ub4ef\ud558\uace0 \ub3d9\ucabd\uc73c\ub85c\ub294 \uc124\ud48d\uc11c\uc804\uc758 \uc6b8\ucc3d\ud55c \uc232\uc774 \uc7a5\uad00\uc774\ub2e4.</P>'), '<P>&nbsp;</P>', '34.711344', '125.203965', STRINGDECODE('\uc804\ub77c\ub0a8\ub3c4 \uc2e0\uc548 \ud751\uc0b0\uba74 \ud64d\ub3c4'), 2612140500, 368, STRINGDECODE('\uae43\ub300\ubd09'), STRINGDECODE('<P>&lt;\ub2e4\ub3c4\ud574\uc758 \ubcf4\uc11d \ud64d\ub3c4\uac00 \uae43\ub300\ubd09\uc774\ub2e4&gt;<BR>\ub2e4\ub3c4\ud574\ud574\uc0c1\uad6d\ub9bd\uacf5\uc6d0\uc758 \uc791\uc740 \uc12c\uc774\uc790 \uc12c \uc804\uccb4\uac00 \ucc9c\uc5f0\uae30\ub150\ubb3c(170\ud638)\uc778 \ud64d\ub3c4\ub294 360.7\ubbf8\ud130 \ub192\uc774\uc758 \uae43\ub300\ubd09\uacfc 231.8\ubbf8\ud130\uc758 \uc591\uc0b0\ubd09\uc774 \uac1c\ubbf8\ucc98\ub7fc \ub5a0\uc788\ub294 \uc12c\uc774\ub2e4. \uc12c \uc804\uccb4\uac00 \ud64d\uac08\uc0c9 \uaddc\uc554\uc9c8 \ubc14\uc704\uc5ec\uc11c \ud64d\ub3c4(\u7d05\u5cf6)\uc778\ub370 \ubc14\ub2e4 \uc704\uc5d0 \ub5a0\uc788\ub294 \ub9e4\ud654\uaf43 \uac19\ub2e4\uace0 \ud574\uc11c \ub9e4\uac00\ub3c4\ub85c\ub3c4 \ubd88\ub838\ub2e4. <BR>\ud64d\ub3c4\ub294 \ud574\uc218\uc695, \uc2a4\ucfe0\ubc84\ub2e4\uc774\ube59, \uc720\ub78c\uc120 \uc77c\uc8fc, \ub4f1\uc0b0\uae4c\uc9c0 \ubaa8\ub4e0 \uac83\uc744 \uc990\uae38 \uc218 \uc788\ub294 \ucc9c\ud61c\uc758 \uad00\uad11\uc9c0\uc774\ub2e4. \uadf8\ub7ec\ub098 \ud574\uc548\uc120 \uae38\uc774\uac00 20.8\ud0ac\ub85c\ubbf8\ud130\ubc16\uc5d0 \uc548\ub3fc \ub4f1\uc0b0\uc740 \ud64d\ub3c4 1\uad6c\uc640 2\uad6c \uc8fc\ubbfc\ub4e4\uc774 \uc655\ub798\ud558\ub358 \ub8e8\ud2b8, \uae43\ub300\ubd09 \ub118\ub294 \uac83\uc774 \uace0\uc791\uc774\ub2e4. \ud558\uc9c0\ub9cc \uc720\ub78c\uc120\uc744 \ud0c0\uace0 \ube60\ub3cc(\uc790\uac08)\ud574\uc218\uc695\uc7a5\uc744 \ucd9c\ubc1c\ud558\uba74 \uc12c \uc804\uccb4\ub97c \ub3cc\uba70 \uae43\ub300\ubd09\uc744 \ubc14\ub77c\ubcfc \uc218 \uc788\ub2e4. <BR>\uc12c\uc778 \uad00\uacc4\ub85c \ud0dc\ud48d \ub4f1 \uae30\uc0c1\uc870\uac74\uc5d0 \uc88c\uc6b0\ub418\uc5b4 \ubc30\uac00 \ucd9c\ud56d\uc744 \ubabb\ud558\ub294 \uacbd\uc6b0\ub3c4 \ub9ce\uc73c\ub2c8 2\ubc153\uc77c\uc5d0 \ud50c\ub7ec\uc2a4 \uc54c\ud30c\uac00 \uc788\uc5b4\uc57c \ud64d\ub3c4\ub97c \uac14\ub2e4\uc62c \uc218 \uc788\ub2e4. \uc8fc\uc758\ud574\uc57c\ud560 \uc810\uc740 \uc12c \uc804\uccb4\uac00 \ucc9c\uc5f0\uae30\ub150\ubb3c\ub85c \uc9c0\uc815\ub3fc \uc788\uc5b4 \ud480 \ud55c \ud3ec\uae30, \ub3cc \ud558\ub098\ub3c4 \uac00\uc9c0\uace0 \ub098\uc62c \uc218 \uc5c6\ub2e4\ub294 \uc0ac\uc2e4\uc774\ub2e4. \ubc14\ub2f7\ubc14\ub78c\uc744 \ubc1b\uc73c\uba70 \uc790\ub77c\ub294 \ud64d\ub3c4\ud48d\ub780\uc740 \ub300\ud45c\uc801\uc778 \ud76c\uadc0\uc2dd\ubb3c\uc774\ub2e4.</P>'), STRINGDECODE('<P>\ub369\uad74\uc0ac\ucca0, \uc2dd\ub098\ubb34 \ubc0f \ub3d9\ubc31\ub9bc \ub4f1\uc774 \uc790\uc0dd\ud558\ub294 \ub4f1 \uc0dd\ud0dc\uc801 \uac00\uce58\uac00 \ucee4 \uc12c \uc804\uccb4\uac00 \ucc9c\uc5f0\ubcf4\ud638\uad6c\uc5ed\uc73c\ub85c \uc9c0\uc815(1965\ub144)\ub418\uc5b4 \uc788\uc73c\uba70, \ub2e4\ub3c4\ud574\ud574\uc0c1\uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1981\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc774\ub984 \uadf8\ub300\ub85c \uae43\ub300\ucc98\ub7fc \uc0dd\uae34 \uc554\ubd09\uc774\uba70, \ud64d\ub3c4\uc758 \ucd5c\uace0\ubd09\uc784. <BR>\uae43\ub300\ubd09\uc740 \ub3c5\ub9bd\ubb38, \uc11d\ud654\uad74 \ub4f1 \ud574\uc548\uacbd\uad00\uacfc \uc870\ud654\ub97c \uc774\ub904 \ud64d\ub3c4\uc758 \uc218\ub824\ud55c \uacbd\uad00\uc744 \uc774\ub8e8\uace0 \uc788\uc74c.</P>'), ' ', STRINGDECODE('<P>\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ud64d\ub3c4 1\uad6c\uc640 2\uad6c\uc5d0 50\uac1c\uac00 \ub118\ub294 \uc5ec\uad00\uacfc \ubbfc\ubc15\uc774 \ubc00\uc9d1\ub418\uc5b4 \uc788\uc73c\uba70 2000\uba85\uac00\ub7c9\uc758 \uad00\uad11\uac1d\uc774 \ub3d9\uc2dc\uc5d0 \uc219\ubc15 \uac00\ub2a5\ud55c \uc2dc\uc124\uc744 \uac16\ucd94\uace0 \uc788\ub2e4. \ub300\ubd80\ubd84 \uc544\uce68\uc2dd\uc0ac\ub3c4 \uacb8\ud55c\ub2e4. <BR>\uad11\uc131\uc5ec\uad00 \u260e061-246-2094, \ud64d\ub3c4\uc5ec\uad00 \u260e246-2500 \uac00\ub4e0\uc7a5 \ubbfc\ubc15 \u260e246-2937 \ub4f1\uc774 \uc788\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc2e0\uc548\uad70 \ud64d\ub3c4\uad00\ub9ac\uc0ac\ubb34\uc18c \u260e061-246-3700</P>'), STRINGDECODE('<P>\uae30\uc810\uc740 \ubaa9\ud3ec\uc774\ub2e4.<BR>&gt; \ubaa9\ud3ec\u2192\ud64d\ub3c4 : 1\uc77c 2\ud68c \uc6b4\ud589 <BR>&gt; \ud64d\ub3c4\u2192\ubaa9\ud3ec : 1\uc77c 2\ud68c \uc6b4\ud589</P>'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(17, TIMESTAMP '2019-08-14 15:46:34.906', TIMESTAMP '2019-08-14 15:46:49.453', '47', STRINGDECODE('\uacbd\uc8fc \ub0a8\uc0b0\uc740 \uace7 \uc2e0\ub77c\ub77c\ud574\ub3c4 \uacfc\uc5b8\uc774 \uc544\ub2c8\ub2e4. \uadf8\ub9cc\ud07c \uc2e0\ub77c \uac74\uad6d\uc5d0\uc11c\ubd80\ud130 \uba78\ub9dd\uc5d0 \uc774\ub974\ub294 \ub3d9\uc548\uc758 \uc218\ub9ce\uc740 \uc720\uc801\uacfc \uc2e0\ub77c\ub97c \uc9c0\ud0f1\ud574 \uc628 \ubd88\uad50\uc720\uc801\uc758 \ubcf4\uace0\uc774\ub2e4. <BR>\uc2e0\ub77c\uc758 \uac74\uad6d\uc124\ud654\uc5d0 \ub098\uc624\ub294 \ub098\uc815(\u7f85\u4e95)\uc5d0\uc11c\ubd80\ud130 \uc885\ub9d0\uae30\uc758 \ud3ec\uc11d\uc815\uc774 \ub0a8\uc0b0 \uae30\uc2ad\uc5d0 \uc790\ub9ac\uc7a1\uc558\ub2e4. \uadf8 \uc0ac\uc774\uc758 \uc2dc\uac04 \uac04\uaca9\uc774 900\ub144\uc774\ub2e4.'), STRINGDECODE('\u2460 \ud3ec\uc11d\uc815-\uc720\ub290\ub9ac\uace8 \ub9c8\uc560\uc0bc\uc874\ubd88-\uc0c1\uc2e4\uc808\ud130-\ubd80\uc5c9\uc774 \ub9c8\uc560\uc5ec\ub798\uc88c\uc0c1-\ubd80\ud765\uc0ac-\ub298\ube44\ubd09 \uc11d\ud0d1-\uae08\uc624\uc815-\uad6d\uc0ac\uace8 \uc0c1\uc2dc\ubc14\uc704-\uc0ac\uc790\ubd09(\ud314\uac01\uc815\ud130)-\uae08\uc624\uc0b0-\uc57d\uc218\uace8 \ub9c8\uc560\ub300\ubd88-\uc81c2\uc808\ud130-\uc8fc\ucc28\uc7a5 (\ucd1d 3\uc2dc\uac04) <BR>\u2461 \ubd80\ucc98\uace8 \uac10\uc2e4\ubd88\uc0c1-\ud0d1\uace8 \ubd80\ucc98\ubc14\uc704 \ub9c8\uc560\uc870\uc0c1\uad70-\ubcf4\ub9ac\uc0ac \uc11d\uc870\uc5ec\ub798\uc88c\uc0c1-\ubbf8\ub975\uace8 \ub9c8\uc560\uc5ec\ub798\uc88c\uc0c1-\ud5cc\uac15\uc655\ub989-\uc815\uac15\uc655\ub989-\ud1b5\uc77c\uc804-\uc11c\ucd9c\uc9c0-\uc0ac\uc9c0\uc30d\ud0d1-\uc5fc\ubd88\uc0ac\uc9c0-\uce60\ubd88\uc554 \ub9c8\uc560\ubd88\uc0c1\uad70-\uc2e0\uc120\uc554 \ub9c8\uc560\ubcf4\uc0b4\uc720\ud76c\uc88c\uc0c1-\ubc31\uc6b4\uc554-\uace0\uc704\uc0b0-\ucc9c\ub8e1\uc0ac-\uc640\ub8e1\uc0ac (\ucd1d 4\uc2dc\uac04 30\ubd84)'), '35.790909', '129.240633', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uacbd\uc8fc\uc2dc \ub0a8\uc0b0\ub3d9\u318d\ub0b4\ub0a8\uba74'), 2702090600, 495, STRINGDECODE('\ub0a8\uc0b0'), STRINGDECODE('&lt;\uc2dc\uc120 \uba38\ubb34\ub294 \uacf3\ub9c8\ub2e4 \uc720\uc801\uc774\uc694 \uc720\ubb3c\uc774\ub2e4&gt;<BR>\uacbd\uc8fc \ub0a8\ucabd\uc5d0 \uc19f\uc740 \ub0a8\uc0b0\uc740 \uc2e0\ub77c\uc778\ub4e4\uc758 \uc2e0\uc559\uc758 \ub300\uc0c1\uc774\uc5c8\ub2e4. \uae08\uc624\uc0b0(495.1m)\uacfc \uace0\uc704\ubd09(494m) \ub450 \ubd09\uc6b0\ub9ac\uc5d0\uc11c \ud758\ub7ec\ub0b4\ub9ac\ub294 40\uc5ec \uac1c\uc758 \uacc4\uace1\uacfc \uc0b0\uc904\uae30\ub85c \uc774\ub8e8\uc5b4\uc84c\uc73c\uba70 \ub3d9\uc11c 4km, \ub0a8\ubd81 8km\ub85c \uae38\uac8c \ubed7\uc5b4\ub0b4\ub9b0 \ud0c0\uc6d0\ud615\uc774\ub2e4. <BR>100\uc5ec \uacf3\uc758 \uc808\ud130, 80\uc5ec \uad6c\uc758 \uc11d\ubd88, 60\uc5ec \uae30\uc758 \uc11d\ud0d1\uc774 \uc0b0\uc7ac\ud574 \uc788\ub294 \ub178\ucc9c\ubc15\ubb3c\uad00\uc73c\ub85c \uc2e0\ub77c \ud0dc\ub3d9\uc758 \uc131\uc9c0 \uc11c\ub0a8\uc0b0, \ubbf8\ub975\uace8\u00b7\ud0d1\uace8\u00b7\ubd80\ucc98\uace8 \ub4f1 \uc218\ub9ce\uc740 \ub3cc\ubd80\ucc98\uac00 \uc788\ub294 \ub3d9\ub0a8\uc0b0\uc73c\ub85c \uad6c\ubd84\ub41c\ub2e4. <BR>\uc790\uc5f0\uacbd\uad00\ub3c4 \ub6f0\uc5b4\ub098 \ubcc0\ud654\ubb34\uc30d\ud55c \ub9ce\uc740 \uacc4\uace1\uc774 \uc788\uace0 \uae30\uc554\uad34\uc11d\ub4e4\uc774 \ub9cc\ubb3c\uc0c1\uc744 \uc774\ub8e8\uc5b4 \ub4f1\uc0b0\uac1d\uc758 \ubc1c\uae38\ub9cc\ud07c\uc774\ub098 \uc218\ub9ce\uc740 \ub4f1\uc0b0\ub85c\uac00 \uc788\ub2e4. \uadf8\ub798\uc11c \ub0a8\uc0b0\uc744 \uc544\ub294 \uc0ac\ub78c\ub4e4\uc740 \"\"\ub0a8\uc0b0\uc5d0 \uc624\ub974\uc9c0 \uc54a\uace0\uc11c\ub294 \uacbd\uc8fc\ub97c \ubcf4\uc558\ub2e4\uace0 \ub9d0\ud560 \uc218 \uc5c6\ub2e4\"\"\uace0 \ud55c\ub2e4. \ub0a8\uc0b0\uc740 \uc790\uc5f0\uc758 \uc544\ub984\ub2e4\uc6c0\uc5d0\ub2e4 \uc2e0\ub77c\uc758 \uc624\ub79c \uc5ed\uc0ac, \uc2e0\ub77c\uc778\uc758 \ubbf8\uc758\uc2dd\uacfc \uc885\uad50\uc758\uc2dd\uc774 \uc608\uc220\ub85c \uc2b9\ud654\ub41c \uacf3\uc774\ub2e4.'), STRINGDECODE('\uae38\uc774 \uc57d 8km, \ud3ed \uc57d 4\u339e\uc758 \uc0b0\uc904\uae30\uc548\uc5d0 \ubd88\uc0c1 80\uc5ec\uccb4, \ud0d1 60\uc5ec\uae30, \uc808\ud130 110\uc5ec \uac1c\uc18c\uac00 \uc0b0\uc7ac\ud558\uc5ec \uacbd\uc8fc\uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5b4 \uc788\ub294 \ub4f1 \uc2e0\ub77c\uc2dc\ub300 \uc5ed\uc0ac \uc720\ubb3c\u00b7\uc720\uc801\uc758 \ubcf4\uace0\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, `\uacbd\uc8fc\ub0a8\uc0b0\ubd88\uc801\uc9c0\"\ub85c \ub9c8\uc560\uc5ec\ub798\uc88c\uc0c1(\ubcf4\ubb3c \uc81c913\ud638), \uce60\ubd88\uc554\ub9c8\uc560\uc11d\ubd88 \ub4f1\uc774 \uc720\uba85. \ub3d9\ucabd\uc5d0\ub294 \ub0a8\uc0b0\uc0b0\uc131 \ub4f1\uc774 \uc788\uc74c'), STRINGDECODE('\uc2e0\ub77c\uc758 \ucc9c\ub144 \uc5ed\uc0ac\uac00 \uc228\uc26c\ub294 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uad00\uad11\ud2b9\uad6c\uc778 \ub9cc\ud07c \uacbd\uc8fc\uc5d0\ub294 \uc219\ubc15\uc2dc\uc124\uc774 \uc218\ub3c4 \uc5c6\uc774 \ub9ce\ub2e4. <BR>\uc774 \uc911 \ub0a8\uc0b0\uc9c0\uad6c\uc5d0\ub294 \ubc18\uc11d\uc7a5\uc5ec\uad00 \u260e054-748-9798, \uc0bc\ucc9c\uc0b0\ubc29 \u260e054-748-7868, \uacbd\uc8fc\uc720\uc2a4\ud638\uc2a4\ud154 \u260e054-772-4441 \ub4f1\uc774 \uc788\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uacbd\uc8fc\uc2dc\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e054-779-6396'), STRINGDECODE('\uae30\uc810\uc740 \uacbd\uc8fc\uc774\ub2e4. <BR>- \uacbd\uc8fc\u2192\uc0bc\ub989 &gt;\uc6a9\uc7a5\ub9ac\u2192\uacbd\uc8fc \ubaa8\ub450 \uc2dc\ub0b4\ubc84\uc2a4\ub97c \uc774\uc6a9\ud55c\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(18, TIMESTAMP '2019-08-14 15:46:34.907', TIMESTAMP '2019-08-14 15:46:50.091', '47', STRINGDECODE('\ubcf4\uacbd\uc0ac\uc640 \uccad\ud558\uace8 \uacc4\uace1\uc73c\ub85c \uc720\uba85\ud55c \ub0b4\uc5f0\uc0b0\uc740 \uc0c1\uc0dd\ud3ed\ud3ec, \uad00\uc74c\ud3ed\ud3ec, \uc5f0\uc0b0\ud3ed\ud3ec \ub4f1 12\uac1c\uc758 \ud3ed\ud3ec\ub97c \uac70\ub290\ub9ac\uace0 \uc788\ub294 \uc0b0\uc73c\ub85c \uc0b0 \uc804\uccb4\uac00 \uac70\uc758 \uc721\uc0b0\uc73c\ub85c \uc644\ub9cc\ud55c \ub2a5\uc120\uc73c\ub85c \uc774\uc5b4\uc838 \uc788\ub2e4. <BR>\ub0b4\uc5f0\uc0b0 \uc0b0\ud589 \ub4e4\uba38\ub9ac\ub294 \uc720\uc11c \uae4a\uc740 \ubcf4\uacbd\uc0ac\ub85c \uc774 \uc808\uc740 \uc2e0\ub77c \uc9c4\ud3c9\uc655 25\ub144\uc5d0 \uc9c0\uba85\ubc95\uc0ac\uac00 \ucc3d\uac74\ud558\uc600\ub2e4\uace0 \ud55c\ub2e4.\uc9c0\uba85\ubc95\uc0ac\uac00 \uc9c4\ub098\ub77c\uc5d0\uc11c \uc720\ud559\ud558\uace0 \ub3cc\uc544\uc62c \ub54c \uc2e0\ube44\ud55c \ud314\uba74\uacbd\uc744 \uac00\uc838\uc640 \uc9c4\ud3c9\uc655\uc5d0\uac8c \uc9c4\uc5b8, \ub0b4\uc5f0\uc0b0 \uc544\ub798 \ud070 \uc5f0\ubabb\uc5d0 \uac70\uc6b8\uc744 \ubb3b\uace0 \uadf8 \uc704\uc5d0 \uae08\ub2f9\uc744 \uc138\uc6b4 \ub4a4 \uc0ac\ucc30\uc744 \uc138\uc6cc \ubcf4\uacbd\uc0ac\ub77c \uc774\ub984\uc744 \uc9c0\uc5c8\ub2e4\uace0 \ud55c\ub2e4.'), STRINGDECODE('\u2460 \ubcf4\uacbd\uc0ac-\ubb38\uc218\uc0b0 \uac08\ub9bc\uae38-\uc740\ud3ed-\uc2dc\uba85\ub9ac-\ud5a5\ub85c\ubd09-\uc0bc\uc9c0\ubd09-\ubb38\uc218\uc0b0-\ubcf4\uacbd\uc0ac (\ucd1d 7\uc2dc\uac04 30\ubd84)<BR>\u2461 \ubcf4\uacbd\uc0ac-\ubb38\uc218\uc0b0 \uac08\ub9bc\uae38-\uc740\ud3ed-\uc0bc\uc9c0\ubd09-\ubb38\uc218\uc0b0-\ubcf4\uacbd\uc0ac (\ucd1d 5\uc2dc\uac04 30\ubd84)<BR>\u2462 \uc0d8\uc7ac-\uc0bc\uac70\ub9ac-\ucc9c\ub839\uc0b0-\uc5f0\uc0b0\ud3ed-\ubcf4\uacbd\uc0ac (\ucd1d 6\uc2dc\uac04)'), '36.244766', '129.320734', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \ud3ec\ud56d\uc2dc \uc1a1\ub77c\uba74\u318d\uccad\ud558\uba74\u318d\uc8fd\uc7a5\uba74, \uc601\ub355\uad70 \u