INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(19, TIMESTAMP '2019-08-14 15:46:34.907', TIMESTAMP '2019-08-14 15:46:50.677', '45', STRINGDECODE('\uadf8 \uc0b0 \ubc16\uc5d0\uc11c \ubcfc \uc218 \uc5c6\ub294 \ucc9c\ud558\uc758 \uba85\uc2b9\uc744 \uadf8 \uc0b0\uc758 \ub0b4\ubd80\uc5d0 \uc228\uae30\uace0 \uc788\ub2e4'' \ub294 \ub73b\uc758 \uc774\ub984\uc744 \uc9c0\ub2cc \ub0b4\uc7a5\uc0b0. \uae30\uc554\uc808\ubcbd, \uacc4\uace1, \ud3ed\ud3ec\uc640 \ub2e8\ud48d \ub4f1 \uc0b0\uc774 \uac16\ucdb0\uc57c \ud560 \ud488\uc138\ub97c \ube60\uc9d0\uc5c6\uc774 \uac16\ucd98 \ucc9c\ud61c\uc758 \uac00\uc744\uc0b0\uc774\ub2e4. <BR>\ub0b4\uc7a5\uc0b0\uc740 \uc804\ubd81 \uc815\uc74d\uc2dc\uc640 \uc21c\ucc3d\uad70\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c \ucc9c\ud558 \uc81c\uc77c\uc758 \ub2e8\ud48d \uba85\uc18c\ub85c \uc190\uaf3d\ud788\uace0 \uc788\ub2e4. \ub0b4\uc7a5\uc0ac \uacbd\ub0b4\uc5d0 \uc788\ub294 \uc815\ud61c\ub8e8\uae30\uc5d0 \uc758\ud558\uba74 \ub0b4\uc7a5\uc0b0\uc740 \uad6c\ub840\uc758 \uc9c0\ub9ac\uc0b0, \uc601\uc554\uc758 \uc6d4\ucd9c\uc0b0, \uc7a5\ud765\uc758 \ucc9c\uad00\uc0b0, \ubd80\uc548\uc758 \ub2a5\uac00\uc0b0(\ubcc0\uc0b0)\uacfc \ub354\ubd88\uc5b4 \ud638\ub0a8\uc758 5\ub300 \uba85\uc0b0\uc758 \ud558\ub098\ub85c \uae30\ub85d\ub3fc \uc788\ub2e4. <BR>\ub9d0\ubc1c\uad7d \ud615\ud0dc\ub85c \uc774\ub8e8\uc5b4\uc9c4 \ub2a5\uc120\uc5d0 \uae30\uae30\ubb18\ubb18\ud558\uac8c \uc19f\uc740 \uae30\uc554\uc808\ubcbd\uacfc \uc6b8\ucc3d\ud55c \ub2e8\ud48d\uc232\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uac00\uc744\ucca0\uc774\uba74 \ub2e8\ud48d \uc0b0\ud589\uc9c0\ub85c \uac01\uad11\uc744 \ubc1b\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ub0b4\uc7a5\uc0ac-\ubcbd\ub828\uc554-\uc11c\ub798\ubd09-\ubd88\ucd9c\ubd09-\uc6d0\uc801\uc554-\ub0b4\uc7a5\uc0ac(\ucd1d 3\uc2dc\uac04 40\ubd84)<BR>\u2461 \ub0b4\uc7a5\uc0ac-\uc6d0\uc801\uc554-\ub9dd\ud574\ubd09-\uc5f0\uc9c0\ubd09-\uae4c\uce58\ubd09-\uc2e0\uc120\ubd09(\uc815\uc0c1)-\ub0b4\uc7a5\uc0ac(\ucd1d 4\uc2dc\uac04 40\ubd84)<BR>\u2462 \uc77c\uc8fc\ubb38-\ubcbd\ub828\uc554-\uc11c\ub798\ubd09-\ubd88\ucd9c\ubd09-\ub9dd\ud574\ubd09-\uc5f0\uc9c0\ubd09-\uae4c\uce58\ubd09-\uc2e0\uc120\ubd09(\uc815\uc0c1)-\uc5f0\uc790\ubd09-\uc7a5\uad70\ubd09-\uc720\uad70\uce58-\ub0b4\uc7a5\uc0ac \uc9c4\uc785\ub3c4\ub85c(\ucd1d 7\uc2dc\uac04 30\ubd84)'), '35.495146', '126.911589', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \uc815\uc74d\uc2dc \ub0b4\uc7a5\ub3d9, \uc21c\ucc3d\uad70 \ubcf5\ud765\uba74\u318d\uc30d\uce58\uba74'), 2514380500, 764, STRINGDECODE('\ub0b4\uc7a5\uc0b0'), STRINGDECODE('&lt;\ub0a8\ud55c \uc81c\uc77c\uc758 \ub2e8\ud48d\uba85\uc0b0\uc774 \ubc14\ub85c \uc774\uacf3! &gt;<BR>\uc6b0\ub9ac\ub098\ub77c\uc5d0\uc11c \"\"\ub2e8\ud48d\"\" \ud558\uba74 \uc81c\uc77c \uba3c\uc800 \ub5a0\uc624\ub974\ub294 \uc0b0\uc774 \ubc14\ub85c \ub0b4\uc7a5\uc0b0\uc774\ub2e4. \ub3d9\uad6c\ubd80\ud130 \ub0b4\uc7a5\uc0ac\uae4c\uc9c0 3\ud0ac\ub85c\ubbf8\ud130 \ub0a8\uc9d3 \ub418\ub294 \uae38\uac00\uac00 \uc628\ud1b5 \ub2e8\ud48d\ub098\ubb34\ubc2d\uc774\ub2e4. \uadf8\ub798 \ub2e8\ud48d\ucca0\uc774\uba74 \uc0b0 \uc548\ud1b5\uc774 \uc0ac\ub78c\ub4e4\ub85c \uc778\uc0b0\uc778\ud574\ub97c \uc774\ub8ec\ub2e4. <BR>\ub2e8\ud48d\ub180\uc774\ub97c \uc628 \uc0ac\ub78c\ub4e4\uc740 \ub300\uac1c \uace8\uc9dc\uae30 \ub2e8\ud48d\ub9cc \uad6c\uacbd\ud558\uace0 \ub3cc\uc544\uac04\ub2e4. \uadf8\ub7ec\ub098 \uc9c4\uc9dc\ub294 \ub9d0\uad7d\ud615\uc758 \uc6b4\ub450\ub97c \ub3cc\uba70 \uc548\ud1b5\uc758 \ubd89\uc740 \uce74\ud54f\uc744 \ub0b4\ub824\ub2e4\ubcf4\ub294 \uac83\uc774\ub2e4. \uc6d4\uc601\ubd09, \uc11c\ub798\ubd09, \ub9dd\ud574\ubd09, \uc5f0\uc9c0\ubd09, \uae4c\uce58\ubd09, \uc0c1\ubd09 \uc2e0\uc120\ubd09, \uc5f0\uc790\ubd09(\ubb38\ud544\ubd09), \uc7a5\uad70\ubd09\uc774\ub2e4. <BR>\uc368\ub808\ucc98\ub7fc \uc0dd\uacbc\ub2e4 \ud558\uc5ec \uc11c\ub798\ubd09\uc774 \ub41c \ud1b1\ub0a0 \uac19\uc740 \uc554\ubd09\uc740 \ub0b4\uc7a5\uc0b0\uc758 \ucd5c\uace0 \uba85\uacbd\uc774\ub2e4. \uadf8 \uacbd\uce58\ub97c \uac10\uc0c1\ud560 \ub370\ub294 \ubcbd\ub828\uc554 \uc911\ubb38\uc73c\ub85c \uc18c\uc124\uac00 \ubb38\uc21c\ud0dc \uc120\uc0dd\uc774 \uac00\uc7a5 \uc544\ub07c\ub294 \ud48d\uacbd\uc774\uae30\ub3c4 \ud558\ub2e4. <BR>\uc11c\ub798\ubd09 \uc911\ud131\uc5d0\ub294 \uc218\ub839\uc774 700\uc0b4\uc774\ub098 \ub418\ub294 \ub2e8\ud48d\ub098\ubb34\uac00 \uc788\ub2e4. \ub192\uc774\uac00 20m, \ub458\ub808\ub294 4m\ub098 \ub41c\ub2e4. \uace8\uc9dc\uae30\uc758 \ub2e8\ud48d\ub098\ubb34\ub294 \ud574\ubc29 \ud6c4 \uc815\uc74d\uad70\uc5d0\uc11c \uc2ec\uc740 \uac83\uc774\uc9c0\ub9cc \ub0b4\uc7a5\uc0b0 \ub2e8\ud48d \uc5ed\uc0ac\uac00 \uc9e7\uc740 \uac83\ub9cc\uc740 \uc544\ub2c8\ub2e4. <BR>\ubc31\uc81c\uc2dc\ub300 \uc601\uc740\uc0ac(\u9748\u96b1\u5bfa)\uac00 \uc138\uc6cc\uc9c0\uba74\uc11c \uc8fd \uc601\uc740\uc0b0\uc774\ub77c\uace0 \ubd88\ub824\uc654\ub2e4. \uadf8\ub7f0\ub370 \uc5b8\uc81c\ubd80\ud134\uac00 \ub0b4\uc7a5\uc0b0\uc73c\ub85c \ubc14\ub00c\uc5b4 \uc624\ub298\uc5d0 \uc774\ub974\ub800\ub2e4. \uadf8\ub7ec\ub098 \uace0\uc801\uc740 \ubcf4\ubb3c \ud55c \uc810 \uc5c6\uc744 \uc815\ub3c4\ub85c \ube44\ud574 \ube48\uc57d\ud558\ub2e4. \uaf2d \ubd10\uc57c\ud560 \uac83\uc740 \uc6a9\uad74. \uc0ac\ub78c\ub4e4\uc774 \uc313\uc740 \uc791\uc740 \ub3cc\ud0d1\ub4e4\ubc16\uc5d0 \uc5c6\ub294 \uc790\uc5f0\ub3d9\uad74\uc774\uc9c0\ub9cc \uc815\uc720\uc7ac\ub780\ub54c \uc804\uc8fc\uc0ac\uace0\uc758 \uc870\uc120\uc655\uc870\uc2e4\ub85d\uc744 \uc5ec\uae30\ub85c \uc62e\uaca8\uc624\uc9c0 \uc54a\uc558\ub2e4\uba74 \uc720\ub124\uc2a4\ucf54\uac00 \uc9c0\uc815\ud55c \uc138\uacc4\ubb38\ud654\uc720\uc0b0\uc740 \uc874\uc7ac\ud558\uc9c0 \uc54a\uc558\uc744 \uac83\uc774\ub2e4.'), STRINGDECODE('\uae30\uc554\uad34\uc11d\uacfc \uc6b8\ucc3d\ud55c \uc0b0\ub9bc, \ub9d1\uc740 \uacc4\ub958\uac00 \uc5b4\uc6b8\uc5b4\uc9c4 \ud638\ub0a8 5\ub300 \uba85\uc0b0\uc758 \ud558\ub098\ub85c \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1971\ub144)\ub418\uc5b4 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ub0b4\uc7a5\uc0ac\ub97c \uc911\uc2ec\uc73c\ub85c \uc11c\ub798\ubd09\uc5d0\uc11c \ubd88\ucd9c\ubd09, \uc5f0\uc9c0\ubd09, \uae4c\uce58\ubd09, \uc2e0\uc120\ubd09, \uc7a5\uad70\ubd09\uc5d0 \uc774\ub974\uae30\uae4c\uc9c0 \uc0b0\uc904\uae30\uac00 \ub9d0\ubc1c\uad7d\ucc98\ub7fc \ub458\ub7ec\uccd0\uc838 \ub9c8\uce58 \ucca0\uc639\uc131 \uac19\uc740 \ud2b9\uc774\uc9c0\ud615\uc744 \uc774\ub8f8. \ub0b4\uc7a5\uc0ac(\u5167\u85cf\u5bfa) \ubd80\uc18d\uc554\uc790\uc778 \uc6d0\uc801\uc554 \uc77c\ub300\uc5d0 \uc788\ub294 \ube44\uc790\ub9bc(\ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c153\ud638)\uc774 \ud2b9\ud788 \uc720\uba85'), STRINGDECODE('\ubd88\ud0c0\ub294 \ud638\ub0a8\uc758 \uae08\uac15'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub0b4\uc7a5\uc0b0 \uc9d1\ub2e8\uc2dc\uc124\uc9c0\uad6c\uc5d0 \uad00\uad11\ud638\ud154\uacfc \uc5ec\uad00\uc774 \ub9ce\ub2e4. <BR>\ubd09\ub9bc\uc0b0\uc7a5 \u260e063-538-9763, \uc0ac\ub791\ubc29\ubaa8\ud154 \u260e063-538-8183, \uadf8\ub9b0\ud1a0\ud53c\uc544 \u260e063-538-9763. \ube57\uc7ac \uc544\ub798 \uc788\ub294 \uc57c\uc601\uc7a5\uc744 \uc774\uc6a9\ud574\ub3c4 \uc88b\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758 <BR>\ub0b4\uc7a5\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-538-7875'), STRINGDECODE('\uae30\uc810\uc740 \uc815\uc74d\uc774\ub2e4. \uc815\uc74d\uc73c\ub85c\ub294 \ud638\ub0a8\uc120 KTX\uac00 19\ud68c, \uc77c\ubc18\uc5f4\ucc28\ub294 20\ud68c \ub2e4\ub2cc\ub2e4. \uace0\uc18d\ubc84\uc2a4\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 60\ud68c, \uac15\ub0a8\ud130\ubbf8\ub110\uc5d0\uc11c 40\ubd84 \uac04\uaca9\uc774\uba70 \uc2ec\uc57c\ubc84\uc2a4\ub3c4 \ud55c \ud3b8 \uc788\ub2e4. \uc815\uc74d\ud589 \ubc84\uc2a4\ud3b8\uc774 \uc788\ub294 \ub370\ub294 \ubd80\uc0b0, \uc778\ucc9c, \ubd80\ucc9c, \uc131\ub0a8, \uc548\uc0b0, \uc548\uc591, \uad11\uc8fc, \ubaa9\ud3ec\uc640 \uc804\ub77c\ubd81\ub3c4 \ub3c4\uc2dc\ub4e4\uc774\ub2e4. \ub0b4\uc7a5\uc0ac\ud589 \ubc84\uc2a4\ub294 \uc815\uc74d\uc2dc\ub0b4\ubc84\uc2a4\ud130\ubbf8\ub110\uacfc \uc815\uc74d\uc5ed\uc5d0\uc11c \ud0c0\uba74 \ub41c\ub2e4. <BR>&gt; \uc815\uc74d\u2192\ub0b4\uc7a5\uc0ac : 20\ubd84 \uac04\uaca9 \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(20, TIMESTAMP '2019-08-14 15:46:34.909', TIMESTAMP '2019-08-14 15:46:51.3', '44', STRINGDECODE('\ud070 \ub450\uba54\uc758 \uc0b0''\uc744 \ub73b\ud558\ub294 \ub300\ub454\uc0b0\uc740 \uc804\ubd81\uacfc \ucda9\ub0a8 \ub450 \ub3c4\uc5d0 \uac78\uccd0\uc788\ub294 \ub3c4\ub9bd\uacf5\uc6d0\uc774\ub2e4. \ud2b9\uc774\ud558\uac8c\ub3c4 \ub300\ub454\uc0b0\uc740 \ub450 \uc5bc\uad74\uc744 \uc9c0\ub2c8\uace0 \uc788\ub2e4. <BR>\uc8fc\ub2a5\uc120\uc744 \uacbd\uacc4\ub85c \uc644\uc8fc\uad70 \ubc29\ud5a5\uc778 \ub0a8\ucabd\uc740 \ubc14\uc704 \uc5bc\uad74\uc774\uace0, \ubd81\ucabd\uc740 \uc21c\ud6c4\ud55c \uc2dc\uace8 \uc544\ub099\ub124\uc758 \uc5bc\uad74\uc744 \ud55c \uae08\uc0b0\uad70\uacfc \ub17c\uc0b0\uc2dc\uc758 \uc5bc\uad74\uc774\ub2e4. \ub0a8\uba74\uc758 \uc804\ubd81 \uc9c0\uc5ed\uc740 \uac00\ud30c\ub978 \ube44\ud0c8\uc5d0 \uae30\uc554\uad34\ubd09\uc774 \uc232\ucc98\ub7fc \uc19f\uc544 \uc788\uc5b4 \uc544\uae30\uc790\uae30\ud558\uace0 \uba4b\uc2a4\ub7ec\uc6b4\ub370 \ube44\ud574 \ubd81\uba74\uc758 \ucda9\ub0a8 \uc9c0\uc5ed\uc740 \uc6b8\ucc3d\ud55c \uc232\uc774 \uc6b0\uac70\uc838 \uc7a5\uc911\ud574 \ubcf4\uc778\ub2e4. <BR>\uacbd\uad00\uc774 \ub6f0\uc5b4\ub098\uc11c \uc18c\uae08\uac15\uc774\ub77c \ubd88\ub9ac\uae30\ub3c4 \ud558\ub294\ub370, \uba40\ub9ac\uc11c \ubc14\ub77c\ubcf4\uba74 \ub298\uc5b4 \uc120 \uc554\ubcbd\uc774 \ub9c8\uce58 \ud55c \ud3ed\uc758 \ubcd1\ud48d\uc744 \uc5f0\uc0c1\ucf00 \ud55c\ub2e4. \uadf8\ub9ac \ub192\uc9c0 \uc54a\uc740 \uc0b0\uc774\uc9c0\ub9cc \uc815\uc0c1\uc778 \ub9c8\ucc9c\ub300\ub97c \uae30\uc810\uc73c\ub85c \ucda9\ub0a8 \ub17c\uc0b0\uc2dc,\uae08\uc0b0\uad70 \uadf8\ub9ac\uace0 \uc804\ubd81 \uc644\uc8fc\uad70\uc5d0 \uc0b0\uc790\ub77d\uc744 \ud3bc\uce58\uace0 \uc788\ub2e4. \uc774 \uc0b0\uc740 \uc784\uae08\ubc14\uc704,\uc7a5\uad70\ubd09,\ub3d9\uc2ec\ubc14\uc704,\uc2e0\uc120\ubc14\uc704\ub4f1 \uc628\ud1b5 \ubc14\uc704\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc73c\uba70, \uc774 \uc911\uc5d0\uc11c\ub3c4 \uc784\uae08\ubc14\uc704\uc640 \uc785\uc11d\ub300\ub97c \uc787\ub294 \ub192\uc774 70m, \uae38\uc774 50m, \ud3ed 50cm\uc778 \uae08\uac15\uad6c\ub984\ub2e4\ub9ac\uc640 \uc0bc\uc120\uad6c\ub984\ub2e4\ub9ac\ub294 \ub300\ub454\uc0b0\uc758 \uc0c1\uc9d5\uc774\uae30\ub3c4 \ud558\ub2e4.'), STRINGDECODE('\u2460 \uc0b0\ubd81\ub9ac \uad00\uad11\ub2e8\uc9c0-\ucf00\uc774\ube14\uce74-\uc7a5\uad70\ubc14\uc704-\uc6a9\ubb38\uad74-\ub9c8\ucc9c\ub300-\uae08\uac15\uad6c\ub984\ub2e4\ub9ac-\ucf00\uc774\ube14\uce74-\uc0b0\ubd81\ub9ac \uad00\uad11\ub2e8\uc9c0(\ucd1d 4\uc2dc\uac04)<BR>\u2461 \uc0b0\ubd81\ub9ac \uad00\uad11\ub2e8\uc9c0-\ucf00\uc774\ube14\uce74-\uae08\uac15\uad6c\ub984\ub2e4\ub9ac-\ub9c8\ucc9c\ub300-\ub099\uc870\ub300-\ud0dc\uace0\uc0ac(\ucd1d 4\uc2dc\uac04)'), '36.188604', '127.286993', STRINGDECODE('\ucda9\uccad\ub0a8\ub3c4 \ub17c\uc0b0\uc2dc \ubc8c\uace1\uba74\u318d\uae08\uc0b0\uad70 \uc9c4\uc0b0\uba74, \uc804\ub77c\ubd81\ub3c4 \uc644\uc8fc\uad70 \uc628\uc8fc\uba74'), 2404170800, 879, STRINGDECODE('\ub300\ub454\uc0b0'), STRINGDECODE('&lt;\uae30\uc554 \ub9c8\ucc9c\ub300\uc640 \ud3ed\ud3ec \uc5b4\uc6b0\ub7ec\uc9c4 \ud638\ub0a8\uc758 \uae08\uac15&gt;<BR>\ub300\ub454\uc0b0\uc740 \uc804\ubd81 \uc644\uc8fc\uad70 \uc6b4\uc8fc\uba74\uacfc \ucda9\ub0a8 \ub17c\uc0b0\uc2dc \ubc8c\uace1\uba74, \uae08\uc0b0\uad70 \uc9c4\uc0b0\uba74 \uc0ac\uc774\uc5d0 \uc704\uce58\ud55c \uc0b0\uc774\ub2e4. \ud558\ub098\uc758 \uc0b0\uc744 \ub450\uace0 \uc804\ubd81\uacfc \ucda9\ub0a8\uc5d0\uc11c \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ud558\uc600\ub2e4. \uc774 \uc0b0\uc740 \ucd5c\uace0\ubd09\uc778 \ub9c8\ucc9c\ub300\ub97c \uc911\uc2ec\uc73c\ub85c \uae30\uc554\uad34\uc11d\ub4e4\uc774 \uc81c\uac01\uae30 \uc704\uc6a9\uc744 \uc790\ub791\ud558\uba70 \ub298\uc5b4\uc11c \uc788\uace0, \uba40\ub9ac\uc11c \ubc14\ub77c\ubcf4\ub294 \uc0b0\uc138\ub3c4 \ub6f0\uc5b4\ub098 ''\ud638\ub0a8\uc758 \uae08\uac15''\uc774\ub77c \ubd80\ub974\uae30\uc5d0 \uc190\uc0c9\uc774 \uc5c6\ub2e4.<BR>\ub0a8\uacfc \ubd81\uc73c\ub85c \ub450 \uc5bc\uad74\uc744 \uc9c0\ub2cc \ub300\ub454\uc0b0\uc740 \ub4f1\uc0b0\ub85c \uc5ed\uc2dc \uc591\ucabd \uc9c0\ud615\uc774 \uc0c1\ubc18\ub41c\ub2e4. \uc644\uc8fc(\ub0a8) \ucabd\uc740 \uae30\uce58\ucc3d\uac80\uc744 \ub4e0 \uc554\ubd09\ub4e4\uc774 \uc11d\ub9bc\uc744 \uc774\ub904 \ubcf4\ub294 \uc774\ub85c \ud558\uc5ec\uae08 \uc800\uc808\ub85c \uac10\ud0c4\uc744 \uc790\uc544\ub0b4\uac8c \ud55c\ub2e4. \ub300\ub454\uc0b0\uc758 \uba85\ubb3c\uc778 \ucf00\uc774\ube14\uce74, \uae08\uac15\uad6c\ub984\ub2e4\ub9ac \ub4f1\uc774 \uc124\uce58\ub418\uc5b4 \uc788\uc5b4 \ub9ce\uc740 \uad00\uad11\uac1d\uc744 \uc720\ud639\ud55c\ub2e4. <BR>\ubc18\uba74\uc5d0 \ub17c\uc0b0(\ubd81) \ucabd\uc740 \ud611\uace1\uc744 \uac70\ub290\ub9b0 \uae4a\uc740 \uc232\uc744 \uc774\ub904 \uc644\uc8fc \ucabd\ubcf4\ub2e4 \uc218\ub7c9\uc774 \ud48d\ubd80\ud558\uace0 \ud3ed\ud3ec\uac00 \uc7a5\uad00\uc744 \uc774\ub8ec\ub2e4. \uad70\uc9c0\uace8\uc758 \uc81c1\ud3ed\ud3ec\uc640 \ud654\ub791\ud3ed\ud3ec, \uae08\uac15\ud3ed\ud3ec\ub294 \uadf8 \uc55e\uc5d0\uc11c \ub2e8 10\ubd84\ub3c4 \uacac\ub514\uc9c0 \ubabb\ud560 \uc815\ub3c4\ub85c \ud53c\uc11c\uc9c0\ub85c\ub294 \uc81c\uc77c\uc774\ub2e4.<BR><BR>\uff0a&nbsp;\ubcfc\uac70\ub9ac : \ub9c8\ucc9c\ub300 \uc815\uc0c1\uc5d0\uc11c\uc758 \uc804\uacbd, \uac00\uc744\ucca0\uc5d0 \ubc18\ub527\ubd88 \ucd95\uc81c, \ub099\uc870\ub300(\u843d\u7167\u81fa)\uc758 \uc77c\ubab0 \uad11\uacbd<BR>\uff0a&nbsp;\ucd5c\uc801 \ud0d0\ubc29 \uc2dc\uae30 :&nbsp;4 ~ 10\uc6d4 / \ubd04, \uc5ec\ub984, \uac00\uc744'), STRINGDECODE('\uc815\uc0c1\uc778 \ub9c8\ucc9c\ub300\ub97c \ube44\ub86f\ud558\uc5ec \uc0ac\ubc29\uc73c\ub85c \ubed7\uc740 \ubc14\uc704\ub2a5\uc120\uc758 \uae30\uc554\uad34\uc11d\uacfc \uc218\ubaa9\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uacbd\uad00\uc774 \ub6f0\uc5b4\ub098\uace0, \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1980\ub144)\ub41c \uc810 \ub4f1\uc744 \uac10\uc548\ud558\uc5ec \uc120\uc815, \ub9c8\ucc9c\ub300\uc5d0\uc11c \ub099\uc870\ub300\uc5d0 \uc774\ub974\ub294 \ubc14\uc704\ub2a5\uc120\uacfc \uc77c\ubab0\uad11\uacbd\uc774 \ub6f0\uc5b4\ub098\uba70, \uc784\uae08\ubc14\uc704\u00b7\uc7a5\uad70\ubd09\u00b7\ub3d9\uc2ec\ubc14\uc704\u00b7\uc2e0\uc120\ubc14\uc704 \ub4f1\uc774 \uc788\uc74c. \uc784\uae08\ubc14\uc704\uc640 \uc785\uc11d\ub300\ub97c \uc787\ub294 \uae08\uac15\uad6c\ub984\ub2e4\ub9ac\uc640 \ud0dc\uace0\uc0ac(\u592a\u53e4\u5bfa)\uac00 \uc720\uba85'), STRINGDECODE('\uc6d0\ud6a8\ub300\uc0ac\uac00 \ucda4 \ucd94\ub358 \ucc9c\ud558\uc758 \uc2b9\uc9c0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub300\ub454\uc0b0 \uad00\uad11\ub2e8\uc9c0\uc640 17\ubc88 \uad6d\ub3c4\ubcc0\uc5d0 \uc219\uc2dd\ud560 \uacf3\uc774 \ub9ce\uc774 \uc788\ub2e4. <BR>\ub300\ub454\uc0b0\uad00\uad11\ud638\ud154 \u260e063-263-1260, \uc0b0\uc544\ub798\uc7a5\uc2b9\ub9c8\uc744 \u260e063-263-8694, \uc0b0\uc0b0\uc0b0 \u260e063-263-5059, \uc804\uc8fc\uc2dd\ub2f9 \u260e063-263-2829<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub300\ub454\uc0b0 \ub3c4\ub9bd\uacf5\uc6d0 \uc644\uc8fc \uad00\ub9ac\uc18c 063-263-9949, \ub300\ub454\uc0b0 \ub3c4\ub9bd\uacf5\uc6d0 \ub17c\uc0b0\uad00\ub9ac\uc18c041-732-3568'), STRINGDECODE('\uae30\uc810\uc740 \ub300\uc804\uc774\ub2e4. \uae08\uc0b0, \uc804\uc8fc \ub17c\uc0b0\uc5d0\uc11c\ub3c4 \ub300\ub454\uc0b0\ud589 \ubc84\uc2a4\uac00 \ub9ce\uc774 \uc788\ub2e4.<BR>&gt; \ub300\uc804\u2192\ub300\ub454\uc0b0 : 1\uc77c 6\ud68c \uc6b4\ud589 <BR>&gt; \uc804\uc8fc\u2192\ub300\ub454\uc0b0 : 1\uc77c 8\ud68c \uc6b4\ud589<BR>&gt; \ub17c\uc0b0\u2192\ub300\ub454\uc0b0 : \uc218\uc2dc \uc6b4\ud589 <BR>&gt; \uae08\uc0b0-&gt;\ub300\ub454\uc0b0 : 1\uc77c 5\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(21, TIMESTAMP '2019-08-14 15:46:34.909', TIMESTAMP '2019-08-14 15:46:51.889', '42', STRINGDECODE('\ub300\uc554\uc0b0 \uc815\uc0c1 \ubd80\uadfc\uc5d0\ub294 \ud070 \uc6a9\ub2aa, \uc791\uc740 \uc6a9\ub2aa\uc774\ub77c \ubd88\ub9ac\ub294 \uace0\uc9c0\uc2b5\uc6d0\uc774 \uc788\ub294\ub370, \uc791\uc740 \uc6a9\ub2aa\uc740 \uc774\ubbf8 \uadf8 \uc6d0\ub798\uc758 \ubaa8\uc2b5\uc744 \uc0c1\uc2e4\ud558\uc5ec \uc232\uc73c\ub85c \ubcc0\ud574\ubc84\ub9ac\uace0 \ub9d0\uc558\ub2e4. \ud070 \uc6a9\ub2aa\uc740 \uc6b0\ub9ac\ub098\ub77c\uc5d0\uc11c \uc720\uc77c\ud55c \uace0\uc9c0\uc2b5\uc6d0\uc73c\ub85c \uc5f0\uc911 \uc548\uac1c\ub07c\ub294 \ub0a0\uc774 \ub9ce\uc740 \ud2b9\uc218\ud55c \ud658\uacbd\uc774 \uc870\uc131\ub418\uace0 \uc788\uc5b4 \uc0dd\ud0dc\uacc4 \uc5f0\uad6c\uc5d0 \uc88b\uc740 \uc790\ub8cc\ub97c \uc81c\uacf5\ud558\uace0 \uc788\ub2e4. <BR>\ud070 \uc6a9\ub2aa\uc5d0\ub294 \ubb3c\uc774\ub07c, \uc0bf\uac13\uc0ac\ucd08, \uaf2c\ub9ac\uc870\ud31d\ub098\ubb34, \uaf43\uc950\uc190\uc774\ud480 \ub4f1\uc758 \uc2dd\ubb3c\uad70\ub77d\uc774 \uc788\uc73c\uba70, \uc190\ubc14\ub2e5 \ub09c\ucd08, \ube44\ub85c\uc6a9\ub2f4, \ub048\ub048\uc774\uc8fc\uac71 \ub4f1\uc758 \ud76c\uadc0\uc2dd\ubb3c\ub3c4 \uc790\ub77c\uace0 \uc788\ub2e4. \uadf8 \ubc16\uc5d0 \uc2dd\ubb3c\uc131 \ud50c\ub791\ud06c\ud1a4 63\uc885, \ub3cc\ub9d0 19\uc885\uacfc \ucc9c\uc5f0\uae30\ub150\ubb3c\uc778 \uc0b0\uc591\uacfc \uac80\ub3c5\uc218\ub9ac\uac00 \uad00\ucc30\ub41c \ubc14 \uc788\uc73c\uba70, \ub3c4\ub8e1\ub1fd, \ubb34\ub2f9\uac1c\uad6c\ub9ac, \uc904\ud770\ub098\ube44 \ub4f1\ub3c4 \ubcfc \uc218 \uc788\ub2e4. \ub610 \uc774 \uc9c0\uc5ed\uacfc \uc5f0\uacb0\ub41c \ub450\ud0c0\uc5f0\uacc4\uace1\uc5d0\uc11c\ub294 \uc5f4\ubaa9\uc5b4\ub97c \ube44\ub86f\ud55c \ud2b9\uc0b0 \uc5b4\ub958 10\uc5ec \uc885\uc774 \uc0b4\uace0 \uc788\ub2e4. <BR>\ub300\uc554\uc0b0\u00b7\ub300\uc6b0\uc0b0 \ucc9c\uc5f0\ubcf4\ud638\uad6c\uc5ed\uc740 \ubd84\uc9c0\u00b7\uc2b5\uc6d0\ub4f1 \uc9c0\ud615\uc801\uc73c\ub85c \ub2e4\uc591\ud55c \ud2b9\uc9d5\uc744 \uc9c0\ub2c8\uace0 \uc788\uace0, \uae30\ud6c4\uc870\uac74\uc774 \ud2b9\uc774\ud558\uc5ec \ud76c\uadc0\ub3d9\uc2dd\ubb3c\uc774 \uc790\ub77c\uace0 \uc788\ub2e4. \ub610\ud55c \ub3d9\uc2dd\ubb3c\uc758 \ub0a8\ubd81\ud55c\uacc4\u00b7\ub3d9\uc11c \uad6c\ubd84\uc758 \ud604\uc0c1\uc774 \ub098\ud0c0\ub098\ub294 \ub4f1 \uc2dd\ubb3c\uc0dd\ud0dc\ud559\u00b7\uc2dd\ubb3c\uc9c0\ub9ac\ud559\uc801\u00b7\uc2dd\ubb3c\ubd84\ub958\ud559\uc801 \uc5f0\uad6c\uac00\uce58\uac00 \ub9e4\uc6b0 \ud070 \uc9c0\uc5ed\uc77c \ubfd0\ub9cc \uc544\ub2c8\ub77c \ub2e4\uc591\ud55c \ub3d9\ubb3c\uc0c1, \ud2b9\uc774\ud55c \uc9c0\ud615\u00b7\uc9c0\uc138 \ubc0f \uae30\ud6c4\uc801 \ud2b9\uc131 \ub4f1 \ub2e4\uc591\ud55c \uc790\uc5f0 \ud658\uacbd\uc744 \uac00\uc9c0\uace0 \uc788\uc5b4 \ud559\uc220\uc801 \uac00\uce58\uac00 \ud06c\ubbc0\ub85c \uccd4\uc5f0\uae30\ub150\ubb3c\ub85c \uc9c0\uc815\u00b7\ubcf4\ud638\ud558\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460\uad11\uce58\ud734\uc591\ub9bc-\uc639\ub140\ud3ed\ud3ec-\uad11\uce58\ud734\uc591\ub9bc(\ucd1d 2\uc2dc\uac04 30\ubd84)<BR>\u2461\uad11\uce58\ud734\uc591\ub9bc-\uc639\ub140\ud3ed\ud3ec-\uc639\ub140\uc0bc\uac70\ub9ac-\ud6c4\uace1\uc57d\uc218\ud130(\ucd1d 3\uc2dc\uac04 30\ubd84)<BR>\u2462\uad11\uce58\ud734\uc591\ub9bc-\uc639\ub140\ud3ed\ud3ec-\uc194\ubd09-\uc0dd\ud0dc\uc2dd\ubb3c\uc6d0(\ucd1d 4\uc2dc\uac04 30\ubd84)'), '38.239055', '128.054839', STRINGDECODE('\uac15\uc6d0 \uc591\uad6c\uad70 \ub3d9\uba74, \uc778\uc81c\uad70 \uc11c\ud654\uba74'), 2210050800, 1313, STRINGDECODE('\ub300\uc554\uc0b0'), STRINGDECODE('&lt;\uace0\uce35\uc2b5\uc6d0\uc758 \uba85\uc0b0&gt;<BR>\uace0\uce35\uc2b5\uc6d0\uc73c\ub85c \uc798 \uc54c\ub824\uc9c4 \ub300\uc554\uc0b0\uc740 \uc815\uc0c1\ubd80\uadfc\uc5d0 \uc788\ub294 \ubd84\uc9c0\ud615\uc73c\ub85c \ub41c \ud070 \uc6a9\ub2aa\uacfc \uc791\uc740 \uc6a9\ub2aa\uc774 \uc788\ub2e4. \uc774 \uc2b5\uc6d0\uc758 \ud06c\uae30\ub294 \ub3d9\uc11c \uc57d 150\ubbf8\ud130, \ub0a8\ubd81 \uc57d 100\ubbf8\ud130 \ub0b4\uc678\uc758 \ubd80\uc815\ud615\uc73c\ub85c \uc11c\ubd81\uc5d0\uc11c \ub3d9\ub0a8\uc73c\ub85c \uc644\ub9cc\ud55c \uacbd\uc0ac\ub97c \uc774\ub8e8\uace0 \uc788\ub2e4. \ub192\uc740 \ud559\uc220\uc801 \uac00\uce58\ub97c \uc9c0\ub2c8\uace0 \uc788\ub294 \uc774 \uace0\uce35\uc2b5\uc6d0\uc5d0\uc11c 1968\ub144 5\uc6d4\ubd80\ud130 1969\ub144 8\uc6d4\uae4c\uc9c0\uc758 \uae30\uac04\uc911 \uae40\ub450\uc601(\ud604 \uac15\uc6d0\ub3c4 \ud559\uc0dd\uacfc\ud559\uad00 \uadfc\ubb34)\uc5d0 \uc758\ud574 \ubd80\uc720\uc0dd\ubb3c 47\uc885\uc774 \ucc44\uc9d1\ub418\uc5c8\uace0, \ud55c\ud3b8 \ub300\uc554\uc0b0\uc5d0 \ubd84\ud3ec\ud55c \uc2dd\ubb3c\uc758 \uc885\ub958\ub294 \ucd1d 59\uacfc 123\uc885\uc73c\ub85c \uadf8 \uc911 \uace0\uce35\uc2b5\uc6d0\uc758 \ud2b9\uc720\uc885\uc774 19\uc885 \ubbf8\uae30\ub85d\uc885 15\uc885\uc774 \uc54c\ub824\uc838 \uc788\uc5b4 \uc0dd\ubb3c\uc758 \ubcf4\uace0\uc774\uba70 1973\ub144 7\uc6d4 10\uc77c \uc778\uadfc\uc758 \ub300\uc6b0\uc0b0\uacfc \ud568\uaed8 \ucc9c\uc5f0\ubcf4\ud638\uad6c\uc5ed\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\ub2e4. \ub300\uc554\uc0b0 \uc815\uc0c1\uc740 \ub4f1\uc0b0 \ud1b5\uc81c\uad6c\uc5ed\uc73c\ub85c \uc77c\ubc18 \ub4f1\uc0b0\uc740 \uc0dd\ud0dc\uc2dd\ubb3c\uc6d0\uc744 \ub4e4\uba38\ub9ac\ub85c \ud55c \uc815\uc0c1 \uc11c\ub0a8\ucabd \ub2a5\uc120 \ucf54\uc2a4\ub9cc \uac00\ub2a5\ud558\ub2e4.'), STRINGDECODE('\ud734\uc804\uc120\uc774 \uac00\uae4c\uc6b4 \uc9c0\uc5ed\uc73c\ub85c \uac01\uc885 \ud76c\uadc0\uc0dd\ubb3c\uacfc \uc6d0\uc2dc\ub9bc\uc5d0 \uac00\uae4c\uc6b4 \uc232\uc774 \uc798 \ubcf4\uc874\ub418\uc5b4 \ucc9c\uc5f0\ubcf4\ud638\uad6c\uc5ed(\ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c246\ud638)\uc73c\ub85c \uc9c0\uc815(1973\ub144) \uad00\ub9ac\ub418\ub294 \ub4f1 \uc6b0\ub9ac\ub098\ub77c \ucd5c\ub300 \ud76c\uadc0\uc0dd\ubb3c\uc790\uc6d0\uc758 \ubcf4\uace0\uc778 \uc810 \ub4f1\uc744 \uac10\uc548\ud558\uc5ec \uc120\uc815, \ub300\uc554\uc0b0 \uc815\uc0c1\ubd80\uc5d0 \uc788\ub294 \uc57d 9,000\uc5ec\ud3c9\uc774 \ub118\ub294 \ud480\ubc2d \uac19\uc740 \ub113\uc740 \ucd08\uc6d0\uc5d0 \ud070 \uc6a9\ub2aa\uacfc \uc791\uc740\uc6a9\ub2aa\uc758 \uace0\uce35\uc2b5\uc9c0\uac00 \uc788\uc74c. \uadf8 \uc8fc\uc704\uac00 \ub9c8\uce58 \ud654\ucc44(punch) \uadf8\ub987(bowl)\uac19\uc544 \ud380\uce58\ubcfc\ub85c \ubd88\ub9ac\uc6b0\uba70 \ud574\uc548\ubd84\uc9c0(\u4ea5\u5b89\u76c6\u5730)\uac00 \uc720\uba85'), ' ', STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ud314\ub7c9\ud3ed\ud3ec \uc785\uad6c\uc640 \ud6c4\uace1\uc57d\uc218 \uc778\uadfc\uc5d0 \uc57d\uac04\uc758 \ubbfc\ubc15\uc774 \uc788\ub2e4. <BR>\uc0b0\uace8\ub098\uadf8\ub124\u260e033-481-3975, \ud574\uc548\ubbfc\ubc15\u260e481-0664, \ub3d9\ubd80\uc5ec\uad00\u260e481-0687 \ub4f1\uc774 \uc788\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc591\uad6c\uad70\uccad \uad00\uad11\ubb38\ud654\uacfc \u260e033-480-2251'), STRINGDECODE('\uae30\uc810\uc740 \uc591\uad6c\uc774\ub2e4. &gt;\uc591\uad6c\u2192\ud314\ub7c9\ub9ac: \uc2dc\uc678\ubc84\uc2a4 30\ubd84 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(22, TIMESTAMP '2019-08-14 15:46:34.91', TIMESTAMP '2019-08-14 15:46:52.473', '47', STRINGDECODE('\ub300\uc57c\uc0b0\uc740 \ubc31\ub450\ub300\uac04\uc5d0 \uc790\ub9ac\uc7a1\uace0 \uc788\uc73c\uba74\uc11c \ub300\uac04\ub2a5\uc120\uc774 \uafc8\ud2c0\uc774\uace0 \uc9c0\ub098\uba70 \uc544\ub984\ub2e4\uc6b4 \ubcf4\uc11d\ub4e4\uc744 \ud769\ubfcc\ub824 \ub193\uc740 \ubb38\uacbd\uc758 \uc0b0\ub4e4 \uc911\uc5d0\uc11c\ub3c4 \uadf8 \uba85\uc131\uc744 \ub192\uc774 \uc0ac\uace0 \uc788\ub294 \uba85\uc0b0\uc774\ub2e4. \uacbd\ubd81 \ubb38\uacbd\uc2dc(\u805e\u6176\u5e02) \uac00\uc740\uc74d(\u52a0\u6069\u9091) \uc644\uc7a5\ub9ac(\u5b8c\u7ae0\u91cc)\uc5d0 \uc18d\ud55c \ub300\uc57c\uc0b0\uc740 \ub300\uac04 \ub9c8\ub8e8\uae08\uc744 \uacbd\uacc4\ub85c \ucda9\ubd81 \uad34\uc0b0\uad70(\u69d0\u5c71\u90e1) \uccad\ucc9c\uba74(\u9751\u5ddd\u9762) \uc0bc\uc1a1\ub9ac(\u4e09\u677e\u91cc)\uc640 \uc811\ud558\uace0 \uc788\ub2e4. \ub0b4/\uc678\uc120\uc720\ub3d9\uc744 \uac70\ub290\ub9ac\uace0 \uc788\ub294 \ub300\uc57c\uc0b0\uc740 2002\ub144 \uc138\uacc4 \uc0b0\uc758 \ud574\ub97c \ub9de\uc544 \ubb38\uacbd\uc758 \uc8fc\ud758\uc0b0, \ud669\uc7a5\uc0b0, \ud76c\uc591\uc0b0\uacfc \ud568\uaed8 \uc0b0\ub9bc\uccad\uc5d0\uc11c \uc120\uc815\ud55c \ud55c\uad6d 100\ub300 \uba85\uc0b0\uc5d0 \uc62c\ub77c\uc11c \uc788\ub2e4. \uc608\ub85c\ubd80\ud130 \uba85\uc0b0\uc73c\ub85c \ubc1b\ub4e4\uc5b4 \uc628 \ub300\uc57c\uc0b0\uc740 \uc5ec\ub7ec \uae30\ub85d\ub4e4\uc5d0 \u2018\u5927\u8036\u5c71\u2019\uc73c\ub85c \uc801\uace0 \uc788\uc73c\uba70 \ud2b9\ud788 \ucca0 \uc885 \uc870\uc758 \ub300\ub3d9\uc9c0\uc9c0[(\u5927\u6771\u5730\u5fd7(1861\ub144 \uc774\ud6c4 \ucd94\uc815)]\uc5d0\ub294\u300c\u5927\u8036\u5c71 \u66e6\u967d\u5c71\u5357\u652f\u4e0a\u5cef\u66f0\u6bd8\u76e7\u7232\u4ed9\u904a \u6d1e\u4e3b\u5c71\u897f\u8ddd\u6df8\u5dde\u83ef\u967d\u6d1e\u4e09\u5341\u91cc(\ud76c\uc591\uc0b0\ub0a8\uc9c0\uc0c1\ubd09\uc648\ube44\ub85c\uc704\uc120\uc720\ub3d9\uc8fc\uc0b0\uc11c\uac70\uccad\uc8fc\ud654\uc591\ub3d9\uc0bc\uc2ed\ub9ac: \ub300\uc57c\uc0b0\uc740 \ud76c\uc591\uc0b0\uc758 \ub0a8\ucabd \uac08\ub798\ub85c \uc81c\uc77c \ub192\uc740 \ubd09\uc6b0\ub9ac\uac00 \ube44\ub85c\ubd09\uc774\uace0, \uc120\uc720\ub3d9\uc758 \uc8fc\uc0b0\uc774\ub2e4. \uc11c\ucabd\uc758 \uccad\uc8fc \ud654\uc591\ub3d9\uc774 30\ub9ac\ub2e4)\ub77c\uace0 \uae30\ub85d\ud558\uace0 \uc788\uc5b4 \ub300\uc57c\uc0b0 \uc815\uc0c1\uc744\u2018\ube44\ub85c\ubd09(\u6bd8\u76e7\u5cef)\u2019\uc73c\ub85c \ubd80\ub974\uace0 \uc788\uc74c\uc744 \uc54c \uc218\uac00 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ubc8c\ubc14\uc704\ub9c8\uc744-\uc6a9\ucd94-\uc6d4\uc601\ub300-\ubc00\uc7ac-\uc815\uc0c1-\ucd1b\ub300\ubd09-\ubc8c\ubc14\uc704 (\ucd1d 5\uc2dc\uac04 \uc18c\uc694)<BR>\u2461 \ub18d\ubc14\uc704-\ubc00\uc7ac-\uc815\uc0c1-\uc911\ub300\ubd09-\uacf0\ubc14\uc704-\ub18d\ubc14\uc704(\ucd1d 5\uc2dc\uac04 \uc18c\uc694)'), '36.65997', '128.010944', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \ubb38\uacbd\uc2dc \uac00\uc740\uc74d, \ucda9\uccad\ubd81\ub3c4 \uad34\uc0b0\uad70 \uccad\ucc9c\uba74'), 2707010800, 931, STRINGDECODE('\ub300\uc57c\uc0b0'), STRINGDECODE('&lt;\uae30\uc554\uad34\uc11d\uacfc \uc120\uc720\ub3d9 \uacc4\uace1\uc758 \uc870\ud654&gt;<BR>\ubc31\ud654\uc0b0\uacfc \ud76c\uc591\uc0b0\uc744 \uc9c0\ub098 \uc18d\ub9ac\uc0b0 \uc0ac\uc774\uc5d0 \uc19f\uc740 \ub300\uc57c\uc0b0\uc740 \uc18d\ub9ac\uc0b0\uad6d\ub9bd\uacf5\uc6d0\ub0b4\uc5d0 \ud3ec\ud568\ub418\uba70, \ubc31\ub450\ub300\uac04 \uc0c1\uc758 \uc0b0\uc73c\ub85c \uacbd\ubd81\uacfc \ucda9\ubd81 \uc591\ucabd\uc5d0 \ub0b4\uc678 \uc120\uc720\ub3d9\uc744 \ub450\uace0 \uc788\uc744 \uc815\ub3c4\ub85c \uacc4\uace1\uc774 \ud2b9\ud788 \uc720\uba85\ud558\ub2e4. \ub300\uc57c\uc0b0 \uc790\ub77d\uc5d0\uc11c \ud758\ub7ec\ub0b4\ub9b0 \ubb3c\uc740 \uc6a9\ucd94\uacc4\uace1\uc744 \ud615\uc131, \ubb38\uacbd 8\uacbd\uc758 \ud558\ub098\uac00 \ub41c\ub2e4. \uc6a9\ucd94\uace8\uc740 \ub300\uc57c\uc0b0\uacfc \uc774\uc6c3\ud55c \ub454\ub355\uc0b0\uc5d0\uc11c \ud758\ub7ec\ub0b4\ub9b0 \ubb3c\uacfc \ud569\ub958\ud558\uc5ec \ub113\uc740 \uc554\ubc18\uc774 \uc544\ub984\ub2e4\uc6b4 \uc120\uc720\ub3d9 \uacc4\uace1\uc744 \ube5a\uc5b4 \ub193\uc558\ub2e4. \ud2b9\ud788 \uae4e\uc544\uc9c0\ub978 \uc554\ubd09\uacfc \uae30\uc554\uad34\uc11d\uc774 \uc0b0\uc138\ub97c \ub354\uc6b1 \uc544\ub984\ub2f5\uac8c \ud55c\ub2e4.<BR>\ubb38\uacbd 8\uacbd\uc911\uc758 \ud558\ub098\uc778 \uc6a9\ucd94\uc758 \ubaa8\uc2b5\uc740 \ub300\uc57c\uc0b0 \uc81c\uc77c\uc758 \uba85\uc18c\ub85c \uce5c\ub2e4. \uac70\ub300\ud55c \ud654\uac15\uc554\ubc18\uc744 \ub6ab\uace0 \uc3df\uc544\uc9c0\ub294 \ud3ed\ud3ec \uc544\ub798\uc5d0 \ud558\ud2b8\ud615\uc73c\ub85c \ud328\uc778 \uc18c(\u6cbc)\uac00 \ub9e4\ub048\ud55c \uc554\ubc18\uc744 \ud0c0\uace0 \ud758\ub7ec\ub0b4\ub9ac\uba74\uc11c \uc5ec\ub7ec\uac1c\uc758 \uc18c\ub97c \ub9cc\ub4e4\uc5b4 \ub1a8\ub2e4. \ub610\ud55c \ub2ec \ub728\ub294 \ubc24\uc774\uba74, \ubc14\uc704\uc640 \uacc4\uace1\uc5d0 \ub2ec\ube5b\uc774 \ube44\uce5c\ub2e4 \ud574\uc11c \uc6d4\uc601\ub300(\u6708\u5f71\u81fa), \uc219\uc885 \ub54c\uc758 \ud559\uc790\uc778 \uc774\uc7ac\ub97c \uae30\ub9ac\uae30 \uc704\ud574 1906\ub144\uc5d0 \uc138\uc6b4 \uc120\uc720\ub3d9 \uacc4\uace1\uc758 \uc815\uc790\uc778 \ud559\ucc9c\uc815 \ub4f1\uc774 \ub300\uc57c\uc0b0\uc744 \ub354\uc6b1 \uae30\uae30\ubb18\ubb18\ud55c \uc544\ub984\ub2e4\uc6c0\uc73c\ub85c \uac00\ub4dd\ucc28\uac8c \ud55c\ub2e4.<BR><BR>? \uc18c\uc694 \uc2dc\uac04 :&nbsp;5\uc2dc\uac04<BR>? \ucd5c\uc801 \ud0d0\ubc29 \uc2dc\uae30 :&nbsp; 4~9\uc6d4 /&nbsp; \uc5ec\ub984(\uc6a9\ucd94\uacc4\uace1) \uc778\uae30\uc788\ub294 \uc5ec\ub984 \uc0b0\ud589\uc9c0\ub85c 7-8\uc6d4\uc5d0 \uac00\uc7a5 \ub9ce\uc774 \ucc3e\ub294\ub2e4<BR>? \ubcfc\uac70\ub9ac : \uc6a9\ucd94\uacc4\uace1, \ub300\ubb38\ubc14\uc704, \ubb38\uacbd\uc0c8\uc7ac, \ubb38\uacbd\uc628\ucc9c, \ubb38\uacbd\uc11d\ud0c4\ubc15\ubb3c\uad00, \uc6b4\uac15\uae30\ub150\uad00, \uc120\uc720\ub3d9\uacc4\uace1<BR>\uc2dc\uc6d0\ud55c \uacc4\uace1\uacfc \ubc18\uc11d\uc774 \ud2b9\u00a1\uc774\ub2e4. \ud2b9\ud788 \uc6a9\ucd94\uc758 \uae30\ubb18\ud55c \ubaa8\uc2b5\uc740 \ubcf4\ub294 \uc774\uc758 \ud0c4\uc131\uc744 \uc790\uc544\ub0b8\ub2e4. <BR>? \uc232\uae38 \uba85\uc18c : \uc0b0\ud589\uc740 \uc77c\ubc18\uc801\uc73c\ub85c \uc774\ud654\ub839\uc744 \ub118\uc5b4 \ubb38\uacbd\uc2dc\ub97c \uc9c0\ub098 \uac00\uc740\uc74d \ubc8c\ubc14\uc704\uc5d0\uc11c \uc2dc\uc791\ud55c\ub2e4. \uacc4\uace1\uc744 \ub530\ub77c \ub09c \uc2e0\uc791\ub85c\ub97c \uac78\uc5b4\uac00\uba74 \uc11c\ucabd\uc73c\ub85c \uae30\uc554\uc774 \ub450\ub4dc\ub7ec\uc9c4 \uc0b0\uc774 \uc62c\ub824\ub2e4\ubcf4\uc778\ub2e4. \uc774 \uacc4\uace1\uc774 \uc6a9\ucd94\uacc4\uace1\uc778\ub370 \uc785\uad6c\uc5d0 ''\ubb38\uacbd\ud314\uacbd''\uc774\ub77c\uace0 \uc0c8\uae34 \ub3cc\ube44\uc11d\uc774 \uc788\ub2e4. <BR>\uc6a9\ucd94\uacc4\uace1\uc758 \ube44\uacbd \uc911 \uc73c\ub738\uc73c\ub85c \uaf3d\ud788\ub294 \uc6a9\ucd94\ud3ed\ud3ec\ub294 3\ub2e8\uc73c\ub85c \ub418\uc5b4 \uc788\uc73c\uba70 \ud68c\ubc31\uc0c9 \ud654\uac15\uc554 \ud55c\uac00\uc6b4\ub370\ub85c \ud558\ud2b8\ud615\uc758 \ub3c5\ud2b9\ud55c \ud0d5\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4. \uc6a9\ucd94\uc758 \uc591\ucabd \uc606 \ubc14\uc704\uc5d0\ub294 \uc2e0\ub77c\uc2dc\ub300 \ucd5c\uce58\uc6d0\uc774 \uc4f4 \uc138\uc2ec\ub300\u00b7\ud65c\uccad\ub2f4\u00b7\uc625\ud558\ub300\u00b7\uc601\ucc28\uc11d \ub4f1\uc758 \uc74c\uac01 \uae00\uc528\uac00 \uc0c8\uaca8\uc838 \uc788\ub2e4. <BR>\uc6a9\ucd94\uc5d0\uc11c \uc57d 20\ubd84\uc744 \uc624\ub974\uba74 \ubc14\uc704\uc640 \uacc4\uace1\uc5d0 \ub2ec\ube5b\uc774 \ube44\uce5c\ub2e4\ub294 \uc6d4\uc601\ub300\uac00 \ub098\uc628\ub2e4.'), STRINGDECODE('\uae30\uc554\uad34\uc11d\uacfc \ud3ed\ud3ec\u00b7\uc18c(\u6cbc)\uac00 \uc5b4\uc6b0\ub7ec\uc838 \uc218\ub824\ud55c \uacbd\uad00\uc744 \uc774\ub8e8\uace0 \uc788\uc73c\uba70, \uc18d\ub9ac\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\uad6c\uc5ed\uc5d0 \ud3ec\ud568\ub418\uc5b4 \uc788\ub294 \uc810 \ub4f1\uc744 \uac10\uc548\ud558\uc5ec \uc120\uc815, \uc6a9\ucd94\ud3ed\ud3ec\uc640 \ucd1b\ub300\ubc14\uc704\uac00 \uc788\ub294 \uc120\uc720\ub3d9\uacc4\uace1 \ubc0f `\uc6d4\uc601\ub300\"\uac00 \uc720\uba85'), STRINGDECODE('\uc2e0\uc120 \ub178\ub2d0\ub358 \uc601\ud5d8\ud55c \uc0b0 (\uc77c\uba85 \ub300\ud558\uc0b0)'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ubb38\uacbd \uc2dc\ub0b4\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. \ub610\ub294 \uc120\uc720\ub3d9\uacc4\uace1 \ubd80\uadfc\uc758 \ubaa8\ud154\uc774\ub098 \ubbfc\ubc15\uc9d1\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR>\uc120\uc720\ub3d9\uc5ec\uad00 \u260e054-571-3325, \uac10\ub098\ubb34\uc9d1 \ubbfc\ubc15 \u260e016-544-5399, \uccad\uc8fc\uac00\ub4e0\u260e054-571-7698, \ubc8c\ubc14\uc704\uac00\ub4e0 \u260e054-571-5691<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc18d\ub9ac\uc0b0 \uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e054-550-6393'), STRINGDECODE('\uae30\uc810\uc740 \ubb38\uacbd\uc774\ub2e4. \ubb38\uacbd\uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110\uc5d0\uc11c \uac00\uc740\uc744 \uacbd\uc720\ud558\ub294 \ubc84\uc2a4\ub97c \ud0c4 \ud6c4, \uac00\uc740\ubc84\uc2a4\ud130\ubbf8\ub110\uc5d0\uc11c \ubc8c\ubc14\uc704 \ub9c8\uc744\ud589 \ubc84\uc2a4\ub97c \ud0c4\ub2e4.<BR>&gt; \ubb38\uacbd\uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110\u2192\uac00\uc740 : 16\ud68c \uc6b4\ud589 <BR>&gt; \uac00\uc740\u2192\ubc8c\ubc14\uc704 : 4\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(23, TIMESTAMP '2019-08-14 15:46:34.91', TIMESTAMP '2019-08-14 15:46:53.062', '44', STRINGDECODE('\ucda9\ub0a8 \uc608\uc0b0\uc5d0 \uc790\ub9ac\uc7a1\uc740 \ub355\uc22d\uc0b0\uc740 \uae30\uae30\ubb18\ubb18\ud55c \ud615\uc0c1\uc758 \uad34\uc11d\ub4e4\uc774 \ub9ce\uc544 \uc808\ubb18\ud55c \uc0b0\uc138\ub97c \ubf50\ub0b4\uba70 1973\ub144 3\uc6d4 6\uc77c \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\ub2e4. \ud638\uc11c\uc758 \uae08\uac15\uc0b0\uc774\ub77c \ubd88\ub9b4 \ub9cc\ud07c \uc544\ub984\ub2e4\uc6b4 \uacbd\uad00\uc744 \uac16\ucd94\uace0 \uc788\ub294\ub370\ub2e4 \uc544\ub2f4\ud55c \uc0b0\uc138 \uacf3\uacf3\uc5d0 \uc720\uba85\ud55c \uc554\uc790\ub4e4\uc774 \ubc30\uce58\ub418\uc5b4 \uc788\uc5b4 \ucc3e\ub294 \uc774\ub4e4\uc758 \ubc1c\uae38\uc774 \ub04a\uc774\uc9c0 \uc54a\ub294 \uacf3\uc774\ub2e4. <BR>\uadf8 \uc911 \ube44\uad6c\ub2c8\uc758 \ub3c4\ub7c9\uc73c\ub85c \uc54c\ub824\uc9c4 \uc218\ub355\uc0ac\ub294 \uace0\ub824 \ucda9\ub82c\uc655 34\ub144(1308)\uc5d0 \ucc3d\uac74\ub41c \uc0ac\ucc30\ub85c \uad6d\ubcf4 \uc81c 49\ud638\ub85c \uc9c0\uc815\ub41c \ub300\uc6c5\uc804\uc774 \uc788\ub294\ub370 \uc774 \uac74\ubb3c\uc740 \ubb34\ub7c9\uc218\uc804\uacfc \ub354\ubd88\uc5b4 \ud604\uc874\ud558\ub294 \ubaa9\uc870\uac74\ubb3c \uc911 \uac00\uc7a5 \uc624\ub798\ub41c \uac83\uc774\ub2e4. \ubd80\ucc98\ub2d8\uc758 \uc9c4\ubcf8\uc0ac\ub9ac\uac00 \uc548\uce58\ub41c \ud669\ud654\ub8e8\ub97c \ube44\ub86f, \uae40\uc77c\uc5fd \uc2a4\ub2d8\uc758 \uac70\uccd0\uc600\ub358 \ud658\ud76c\ub300, \ub9cc\uacf5\uc2a4\ub2d8\uc744 \uae30\ub9ac\uae30 \uc704\ud55c \ubbf8\ub975\ubd88\uc0c1\ub4f1 \uc218\ud589\uc758 \uc5ed\uc0ac\ub97c \uc870\ub9dd\ud560 \uc218 \uc788\ub294 \ud754\uc801\ub4e4\uc774 \ub208\uae38\uc744 \ub048\ub2e4. \uc218\ub355\uc0ac\uc640 \ucc3d\uac74\uc5f0\ub300\uac00 \uac19\uc740 \uc815\ud61c\uc0ac\ub294 \uad6c\ud55c\ub9d0\uc5d0 \ub9cc\uacf5 \uc2a4\ub2d8\uc774 \ud55c\uad6d \uc120\ubd88\uad50\ub97c \uc911\ud765\uc2dc\ud0a8 \uacf3\uc73c\ub85c \uccad\ubc29, \uc815\uac15, \uc6a9\uc131 \ub4f1\uc758 \ub300\uc2a4\ub2d8\uc774 \uac70\uccd0\uac14\ub358 \uba85\uc18c\ub2e4. \ub355\uc22d\uc0b0\uc740 \uc774\ubc16\uc5d0\ub3c4 \ub9cc\uacf5\ud0d1, \uc5ec\uc2b9\ub2f9, \ubcf4\ub355\uc0ac \ub4f1 \ub9ce\uc740 \ubb38\ud654\uc7ac\ub97c \uac04\uc9c1\ud558\uace0 \uc788\uc73c\uba70 \uc8fc\ubcc0\uc5d0 \uc628\ucc9c\uc774 \uc788\uc5b4 \ubc30\ub0ad \ud558\ub098 \uba54\uace0 \uac00\ubccd\uac8c \ub5a0\ub0a0 \uc218 \uc788\ub294 \uac00\uc871 \ub098\ub4e4\uc774 \ucf54\uc2a4\ub85c \uc190\uc0c9 \uc5c6\ub294 \uc0b0\uc774\ub2e4.'), STRINGDECODE('\u2460 \ub098\ubcf8\ub4e4\uace0\uac1c-\uc815\uc0c1-\uc721\uad34\uc815 (\ucd1d 1\uc2dc\uac04 30\ubd84)<BR>\u2461 \ub178\ub8e8\ubaa9-\uc815\uc0c1-\uacc4\ub2e8\uae38-\uc218\ub355\uc0ac (\ucd1d 1\uc2dc\uac04 20\ubd84)<BR>\u2462 \uc8fc\ucc28\uc7a5-\ub3d9\ub0a8\ub989-\uc815\uc0c1-150\ubd09-\uc0bc\uc2b9\ub9ac (\ucd1d 1\uc2dc\uac04 40\ubd84)'), '36.677879', '126.626891', STRINGDECODE('\ucda9\uccad\ub0a8\ub3c4 \uc608\uc0b0\uad70 \ub355\uc0b0\uba74'), 2412050400, 495, STRINGDECODE('\ub355\uc22d\uc0b0(\uc218\ub355\uc0b0)'), STRINGDECODE('&lt;\uc77c\uacf1 \uaf43\uc78e\uacfc \uc870\ud654 \uc774\ub8ec \uc624\ub86f\ud55c \uaf43\uc220\ub300&gt;<BR>\ub2f9\ub098\ub77c \uc2dc\uc778 \uc720\uc6b0\uc11d\uc740 \"\"\uc0b0\uc774 \ub192\ub2e4\uace0 \ub2e4\uac00 \uc544\ub2c8\uc694 \uc120\ud48d(\u4ed9\u98a8)\uc774 \uc788\uc5b4\uc57c \uba85\uc0b0\"\"\uc774\ub77c\uace0 \ud588\ub2e4. \uadf8\ub798\uc120\uc9c0 \ub355\uc22d\uc0b0\uc740 \uc774\uc6c3\uc758 \ud070 \uc0b0 \uac00\uc57c\uc0b0(678.2m) \uadf8\ub298\uc784\uc5d0\ub3c4 \uc218\ub355\uc0ac\ub77c\ub294 \ucc9c\ub144\uace0\ucc30\uc758 \ubcf8\uc0b0\uc774 \ub418\uc5c8\ub2e4. \ub3d9\ucabd\uc758 \uc218\uc554\uc0b0(260.1m)\ubd80\ud130 \uc2dc\uc791\ud574 \uc6a9\ubd09\uc0b0(381m), \ud64d\ub3d9\uc0b0(308.9m), \uc0bc\uc900\uc0b0(489.4m), \uc5f0\uc554\uc0b0(440.8m), \ub4b7\uc0b0(449.1m), \uac00\uc57c\uc0b0\uc5d0 \uc774\ub974\uae30\uae4c\uc9c0 \ube59 \ub458\ub7ec \uc721\uc0b0(\u8089\u5c71)\ub4e4 \uc5d0\uc6cc\uc2fc \uac00\uc6b4\ub370 \uaf43\uc220\ub300\ucc98\ub7fc \uc624\ub86f\ud55c \ubc14\uc704\uc0b0\uc73c\ub85c \uc19f\uc544 \ud55c \uc1a1\uc774 \uaf43\uc744 \uc774\ub8e8\uace0\uc788\uae30 \ub54c\ubb38\uc77c \uac83\uc774\ub2e4. <BR>\uaf43\uc220\uacfc \uaf43\uc78e\uc774 \uadf8\ub807\uac8c \uc870\ud654\ub85c\uc6b8 \uc218 \uc5c6\ub2e4. \uaf43\uc220\uc774 \ub418\ubc14\ub77c\uc9c0\uac8c \ub192\uc9c0\ub3c4 \uc54a\uace0 \uaf43\uc78e\uc774 \ub304\ud241\ub9de\uac8c \ud06c\uc9c0\ub3c4 \uc54a\ub2e4. \uadf8\ub798 \uc11c\ub85c \uc228\uace0 \uc11c\ub85c \uac00\ub824\uc8fc\uba74\uc11c \uc804\uccb4\ub97c \uc644\uc131\ud558\uace0 \uc788\ub2e4. \ud64d\ub3d9\uc0b0 \ub0a8\ucabd \"\"\uc774\ubcc4\uc758 \uaf43\uc870\uac1c\uace0\uac1c\"\" \uc5f0\uc778\ub4e4\ucc98\ub7fc \uc560\ud2cb\ud558\uac8c. <BR>\uc0b0 \uacf3\uacf3\uc5d0\ub294 \uae30\ubb18\ud55c \ubaa8\uc591\uc758 \ubc14\uc704\ub4e4\uc774 \ud758\ub9bd(\u5c79\u7acb)\ud574\uc788\ub2e4. \uaf43\uc220\ub300 \uc704\uc5d0 \uc11c\uba74 \uaf43\uc78e\ub4e4 \uc704\ub85c \uc11c\ud574\uc640 \uc11c\uc0b0 \uac04\ucc99\uc9c0, \uc608\ub2f9\ud3c9\uc57c\uac00 \uadf8\ub9bc\ucc98\ub7fc \ud3bc\uccd0\uc9c4\ub2e4. \ud3c9\uc9c0\ub3cc\ucd9c\uc758 \uc791\uc740 \uc0b0 \ub355\uc22d\uc0b0\uc774 \uc791\uc740 \uc0b0\uc774\ub77c\ub294 \ub290\ub08c\uc774 \ub4e4\uc9c0 \uc54a\ub294 \ube44\uacb0\uc740 \ubc14\ub85c \uc774\uac83, \uc5d0\uc6cc\uc2fc \uc77c\uacf1 \uc0b0\uc744 \uc544\uc6b8\ub7ec \ud558\ub098\uc758 \uc0b0\uc138\ub97c \ud615\uc131\ud558\uae30 \ub54c\ubb38\uc774\ub9ac\ub77c. <BR>\uc791\uc544\ub3c4 \ub2e4\ubd80\uc9c4, \uae08\ubd81\uc815\ub9e5\uc758 \ub4f1\uc904\uae30\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uace0\uac1c\ub294 \ub0ae\uc544 \ub0b4\ud3ec\uc9c0\ubc29\uacfc \uc11c\ud574\ubc14\ub2f7\uac00 \uc0ac\ub78c\ub4e4\ub0b4\uc655\ub85c\uac00 \ub418\uc5b4\uc654\ub2e4. \uc120\uc0ac\uc2dc\ub300\ubd80\ud130 \uc0bc\uad6d\uc2dc\ub300\uae4c\uc9c0 \uac00\uc7a5 \uc0b4\uae30 \uc88b\uace0 \uc778\uad6c\ubc00\ub3c4\uac00 \uac00\uc7a5 \ub192\uc558\ub358 \uc9c0\uc5ed\ub4e4. \uc218\ub355\uc0ac\uc640 \ub355\uc22d\uc0b0\uc740 \ud06c\uac8c \ub420 \uc6b4\uba85\uc744 \ud0c0\uace0\ub0ac\ub2e4.'), STRINGDECODE('\uc9c0\uc5ed \uc8fc\ubbfc\ub4e4\uc774 \uc18c\uae08\uac15\uc774\ub77c\uace0 \ud560 \ub9cc\ud07c \uae30\uc554\uad34\uc11d\uacfc \uc5b4\uc6b0\ub7ec\uc9c4 \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0, \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1973\ub144)\ub418\uc5b4 \uc788\ub294 \uc810 \ub4f1\uc744 \uac10\uc548\ud558\uc5ec \uc120\uc815, \ubc31\uc81c \uc81c29\ub300 \ubc95\uc655 \uc6d0\ub144(599\ub144) \uc9c0\uba85\ubc95\uc0ac\uac00 \ucc3d\uac74\ud55c \uc218\ub355\uc0ac(\u4fee\u5fb7\u5bfa), \ubcf4\ubb3c \uc81c355\ud638\uc778 \ub9c8\uc560\ubd88\uacfc \ub355\uc0b0\uc628\ucc9c\uc774 \uc720\uba85'), STRINGDECODE('\ud488\uc740 \uc791\uc544\ub3c4 \ub3c4\ub7c9\uc740 \ub113\uc5b4'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub355\uc0b0\uc628\ucc9c \uc9c0\uad6c\uc640 \uc218\ub355\uc0ac \uc778\uadfc\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. <BR>\ub274\uac00\uc57c\uad00\uad11\ud638\ud154 \u260e041-337-0101 \ub355\uc0b0\uad00\uad11\ud638\ud154 \u260e041-338-5000, \ud790\ud2bc\ud30c\ud06c \u260e041-338-3620, \uc218\uc554\uc7a5 \u260e041-338-2048.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub355\uc0b0\ub3c4\ub9bd\uacf5\uc6d0\uad00\ub9ac\uc0ac\ubb34\uc18c \u260e041-338-6112'), STRINGDECODE('\uae30\uc810\uc740 \uc608\uc0b0\uacfc \ud64d\uc131\uc774\ub2e4. \uc218\ub355\uc0ac\ud589 \uc2dc\ub0b4\ubc84\uc2a4\uac00 \uc608\uc0b0\uc5d0\uc11c\ub294 20\ud68c, \ud64d\uc131\uc5d0\uc11c\ub294 19\ud68c \ub2e4\ub2cc\ub2e4. \uc608\uc0b0\uc73c\ub85c\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c \uace0\uc18d\ubc84\uc2a4\uac00 7\ud68c, \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c\ub294 17\ud68c \uc788\ub2e4. \ub300\uc804\uc5d0\uc11c\ub294 \uace0\uc18d\ubc84\uc2a4\uac00 24\ud68c, \uc9c1\ud589\ubc84\uc2a4\ub294 25\ubd84 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2c8\uba70 \ucda9\ub0a8 \uc774\uc678\uc758 \ub3c4\uc2dc\ub85c\ub294 \uc778\ucc9c, \uccad\uc8fc, \uc548\uc591\uc5d0\uc11c \ubc84\uc2a4\uac00 \uc788\ub2e4. \ud64d\uc131\uc73c\ub85c\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 6\ud68c, \uac15\ub0a8\ud130\ubbf8\ub110\uacfc \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c\ub294 8\ud68c\uc529 \ub2e4\ub2cc\ub2e4. \ub300\uc804\uc5d0\uc11c\ub294 \uace0\uc18d\ubc84\uc2a4\uac00 50\ubd84 \uac04\uaca9, \uc9c1\ud589\uc740 18\ud68c \ub2e4\ub2c8\uba70 \ucda9\ub0a8 \uc774\uc678\uc758 \ub3c4\uc2dc\ub85c\ub294 \uc778\ucc9c, \uccad\uc8fc, \uc548\uc0b0, \uc131\ub0a8, \uad70\uc0b0\uc5d0\uc11c \ubc84\uc2a4\uac00 \uc788\ub2e4. \uc7a5\ud56d\uc120 \uc5f4\ucc28\ub85c\ub294 \uc0c8\ub9c8\uc744\ud638\uac00 5\ud68c, \ubb34\uad81\ud654\ud638\uac00 12\ud68c \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(24, TIMESTAMP '2019-08-14 15:46:34.911', TIMESTAMP '2019-08-14 15:46:53.649', '45', STRINGDECODE('\ub355\uc720\uc0b0\uc740 \uc804\ubd81 \ubb34\uc8fc\uad70\uacfc \uc7a5\uc218\uad70, \uacbd\ub0a8 \uac70\ucc3d\uad70\uacfc \ud568\uc591\uad70\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\uba70, 2\ub3c4 4\uad70 8\uac1c\uba74\uc5d0 \uac78\uccd0\uc788\ub2e4. \ud55c\ub77c\uc0b0.\uc9c0\ub9ac\uc0b0.\uc124\uc545\uc0b0\uc5d0 \uc774\uc5b4 \ub0a8\ud55c\uc5d0\uc11c 4\ubc88\uc9f8\ub85c \ub192\uace0 1000\ubbf8\ud130 \uc774\uc0c1\uc758 \ubd09\uc6b0\ub9ac\uac00 20\uac1c\ub3c4 \ub118\ub294 \uac70\ub300\ud55c \uc0b0\uc774\ub2e4. <BR>\ub355\uc720\ub77c\ub294 \uc774\ub984\uc740 \ub355\uc774 \uc788\uace0 \ud06c\uba70 \ub109\ub109\ud55c \uc0b0\uc758 \ubaa8\uc2b5\uc744 \ub098\ud0c0\ub0b8 \ub9d0\uc774\ub2e4. \ubb34\ud559\ub300\uc0ac\uac00 \uace8\uce58\uc544\ud508 \uc815\uad8c\uc5d0\uc11c \ubc97\uc5b4\ub098 \ucca9\ucca9\uc0b0\uc911 \uacbd\uce58 \uc544\ub984\ub2e4\uc6b4 \uc0b0\uc744 \ubb3c\uc0c9\ud558\ub2e4\uac00 \ubc1c\uacac\ud588\ub2e4\ub294 \uc0b0\uc774 \ubc14\ub85c \ub355\uc720\uc0b0\uc774\ub2e4.'), STRINGDECODE('\u2460 \uc0bc\uacf5\ub9ac-\uce60\ubd09-\ud5a5\uc801\ubd09-\uc911\ubd09-\uc624\uc218\uc790\uad74-\ubc31\ub828\uc0ac-\uc0bc\uacf5\ub9ac(\ucd1d 5\uc2dc\uac04 20\ubd84)<BR>\u2461 \uc1a1\uacc4\uc0ac-\uadc0\ubd09-\ubc31\uc554\ubd09-\uc911\ubd09-\ud5a5\uc801\ubd09-\ubc31\ub828\uc0ac-\uc0bc\uacf5\ub9ac(\ucd1d 4\uc2dc\uac04 40\ubd84)<BR>\u2462 \uba85\ucc9c\ub9ac-\uc0bf\uac13\uace8\uc7ac-\uc6d4\uc131\uc7ac-\ub0a8\ub355\uc720\uc0b0-\uc601\uac01\uc0ac(\ucd1d 5\uc2dc\uac04 45\ubd84)'), '36.00684', '127.660798', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \ubb34\uc8fc\uad70\u318d\uc7a5\uc218\uad70, \uacbd\uc0c1\ub0a8\ub3c4 \uac70\ucc3d\uad70\u318d\ud568\uc591\uad70'), 2505040700, 1614, STRINGDECODE('\ub355\uc720\uc0b0(\ud5a5\uc801\ubd09)'), STRINGDECODE('&lt;\ub355\uc774 \uc788\uc5b4 \ub109\ub109\ud55c \uc0b0&gt;<BR>\ubb34\uc8fc\uad6c\ucc9c\ub3d9 33\uacbd\uc73c\ub85c \ub110\ub9ac \uc54c\ub824\uc9c4 \uc0b0\uc774\ub2e4. \uc81c1\uacbd \ub098\uc81c\ud1b5\ubb38\uc5d0\uc11c \uc2dc\uc791\ud574 \ud559\uc18c\ub300, \ucd94\uc6d4\ub2f4, \uc218\uc2ec\ub300, \uc778\uc6d4\ub2f4, \uae08\ud3ec\ud0c4, \uad6c\ucc9c\ud3ed\ud3ec \ub4f1\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ube44\uacbd\uc774 9000\uad6c\ube44\uc5d0 \uc774\ub978\ub2e4\ub294 \uace8\uc9dc\uae30\ub2e4. 31\uacbd\uc740 \uc774\uc18d\ub300(\u96e2\u4fd7\u81fa), 32\uacbd\uc740 \ubc31\ub828\uc0ac(\u767d\u84ee\u5bfa), 33\uacbd\uc740 \ud5a5\uc801\ubd09(\u9999\u7a4d\u5cf0)\u2026 \"\"\uc18d\uc138\ub97c \ub5a0\ub098 \ud770 \uc5f0\uaf43 \uac19\uc740 \uc808\uc744 \uc9c0\ub09c \ub2e4\uc74c \uc815\ud5a5(\u7cbe\u9999)\uc774 \uc0b0\ucc98\ub7fc \uc313\uc778 \ubd09\uc6b0\ub9ac\uc5d0 \uc624\ub974\"\"\ub294 \uacfc\uc815\uc774\ub2e4. <BR>\ud558\uc9c0\ub9cc ''\ub109\ub109\ud55c \ub355''\uc758 \uc758\ubbf8\ub294 \uc624\ud788\ub824 \ub3d9\ub0a8\ucabd, \uac70\ucc3d\ucabd \uc0b0\uc790\ub77d\uc5d0 \uac00\ubd10\uc57c \ub290\ub084 \uc218 \uc788\ub2e4. \ud65c\ucc98\ub7fc \ud718\uc5b4\uc9c4 \uc7a5\ub300\ud55c \uc0b0\uc904\uae30\uac00 \uc11c\ubd81\ud48d\uc744 \ub9c9\uc544\uc8fc\uace0 \ub3d9\ub0a8\ud48d\uc774 \ud761\uc871\ud55c \ube44\ub97c \ubfcc\ub9ac\ub294 \uac70\uae30\ub294 \uc2dd\uc0dd\uc774 \ub2e4\ub978 \uc5b4\ub5a4 \uc9c0\uc5ed\ubcf4\ub2e4 \uc88b\ub2e4. \ub355\ubd84\uc5d0 \uace1\uc2dd\uc740 \uc798 \ub418\uace0 \uacbd\uce58\ub294 \uc218\ub824\ud574 \uccad\uc0ac\uc815, \ubaa8\uc554\uc815, \uc6a9\uc554\uc815, \uc218\uc2b9\ub300 \uac19\uc740 \uba85\ud488\uc758 \uc815\uc790\ub4e4\uc744 \uc549\ud600\ub450\uc5c8\ub2e4. \uac70\ucc3d \ubd81\uc0c1\uba74\uc744 \ubcf4\uc9c0 \uc54a\uace0 \ub355\uc720\uc0b0\uc744 \ub17c\ud558\ub294 \uac83\uc740 \uc5b4\ubd88\uc131\uc124\uc774\ub2e4.'), STRINGDECODE('\ud5a5\uc801\ubd09\uc5d0\uc11c \ub0a8\ub355\uc720\uae4c\uc9c0 17km\uc758 \uc7a5\ub300\ud55c \uc0b0\uc904\uae30\ub97c \uc774\ub8e8\uace0 \uc788\uc73c\uba70, \uae08\uac15\uacfc \ub099\ub3d9\uac15\uc758 \uc218\uc6d0(\u6c34\u6e90)\uc774\uace0 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1975\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ub355\uc720\uc0b0 \ubd81\ucabd\uc73c\ub85c \ud758\ub7ec \ub0b4\ub9ac\ub294 30\uc5eckm\uc758 \ubb34\uc8fc\uad6c\ucc9c\ub3d9\uacc4\uace1(\u8302\u6731\u4e5d\u5343\u6d1e\u6eaa\u8c37)\uacfc \uc790\uc5f0\ud734\uc591\ub9bc, \uc2e0\ub77c \ud765\ub355\uc6555\ub144(830\ub144) \ubb34\uc5fc\uad6d\uc0ac\uac00 \ucc3d\uac74\ud55c \ubc31\ub828\uc0ac(\u767d\u84ee\u793e) \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ucca9\ucca9\uc0b0\uc911, \ub355\uc774 \uacb9\uce5c \ud070 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ubb34\uc8fc\ub9ac\uc870\ud2b8(\u260e063-322-9000)\uc640 \ub355\uc720\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e063-322-1097)\uc774 \uc548\uc131\ub9c8\ucda4\uc774\ub2e4.<BR>\uc8fc\ub989\uc5d0\uc11c\ub294 \uc0bf\uac13\uc7ac\ub300\ud53c\uc18c(\u260e011-423-1452)\uc640 \ud5a5\uc801\ubd09\ub300\ud53c\uc18c(\u260e063-322-1614)\ub97c \uc774\uc6a9\ud55c\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub355\uc720\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-322-3174~5'), STRINGDECODE('\uae30\uc810\uc740 \ubb34\uc8fc\ub2e4. \uad6c\ucc9c\ub3d9 \uac00\ub294 \ubc84\uc2a4\uac00 20\ubd84 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud55c\ub2e4. \ubb34\uc8fc\ud589\uc740 \uacbd\ubd80\uc120 \uc5f0\uacc4\ud3b8\uc774 \uc88b\uc73c\ub2c8 \ub300\uc804\uc5d0\uc11c\ub294 54\ud68c, \uc601\ub3d9\uc5d0\uc11c\ub294 32\ud68c\ub098 \ub2e4\ub2cc\ub2e4. \uc11c\uc6b8 \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c\ub294 \uace0\uc18d\ubc84\uc2a4\uac00 5\ud68c \ub2e4\ub2c8\uba70 \ub300\uad6c, \uad11\uc8fc, \uccad\uc8fc\uc5d0\uc11c\ub3c4 \ubc84\uc2a4\uac00 \uc788\ub2e4. <BR>\ub3d9\ub0a8\ucabd \uc0b0\ud589 \ub4e4\ub0a0\uba38\ub9ac\uc778 \uc1a1\uacc4\uc0ac\u00b7\ubcd1\uace1\u00b7\uc0b0\uc218\u00b7\ud669\uc810\ud589 \ubc84\uc2a4\ub294 \uac70\ucc3d\uc5d0\uc11c 9\ud68c, 3\ud68c, 3\ud68c, 6\ud68c \uc788\ub2e4. \uac70\ucc3d\uc73c\ub85c\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 8\ud68c, \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c 14\ud68c(\uc2ec\uc57c 2\ud68c) \ub2e4\ub2cc\ub2e4. \uacbd\ub0a8 \uc774\uc678\uc758 \ub3c4\uc2dc\ub85c\ub294 \ub300\uad6c, \uc778\ucc9c, \uad11\uc8fc, \ub300\uc804, \uc804\uc8fc, \uae40\ucc9c\uc5d0\uc11c \ubc84\uc2a4\uac00 \uc788\ub2e4. <BR>\uac00\uc7a5 \ub0a8\ucabd\uc758 \uc601\uac01\uc0ac \ubc29\uba74\uc73c\ub85c\ub294 \ud568\uc591\uc5d0\uc11c \uad70\ub0b4\ubc84\uc2a4\uac00 6\ud68c \ub2e4\ub2cc\ub2e4. \ud568\uc591\uc73c\ub85c\ub294 \uc9c1\ud1b5\ubc84\uc2a4\uac00 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 10\ud68c, \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c 10\ud68c(\uc2ec\uc57c 2\ud68c) \uc788\ub2e4. \uacbd\ub0a8 \uc774\uc678\uc758 \ub3c4\uc2dc\ub85c\ub294 \ub300\uad6c, \uc778\ucc9c, \uad11\uc8fc, \ub300\uc804, \uc218\uc6d0, \uc804\uc8fc, \uae40\ucc9c\uc5d0\uc11c \ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(25, TIMESTAMP '2019-08-14 15:46:34.912', TIMESTAMP '2019-08-14 15:46:54.278', '42', STRINGDECODE('''\ud658\uc120\uad74''\ub85c \uc720\uba85\ud574\uc9c4 \ub355\ud56d\uc0b0\uc740 \uac15\uc6d0\ub3c4 \uc0bc\ucc99\uc2dc \uc2e0\uae30\uba74\uc5d0 \uc790\ub9ac\ud574 \uc788\ub2e4. <BR>\uc774 \uc0b0\uc740 \ub3d9\ub0a8\uc73c\ub85c \ud3bc\uccd0\uc9c4 \ubcd1\ud48d\uc554\uc744 \ube44\ub86f\ud558\uc5ec \uae4e\uc544\ub193\uc740 \ub4ef \ubc18\ub4ef\ud55c \uc554\uc11d\uacfc \uac70\ub300\ud55c \uc554\ubcbd\ub4e4\uc774 \uc218\ub824\ud55c \uc0b0\uc138\ub97c \uc774\ub8e8\uba70 \ub3d9\uc73c\ub85c\ub294 \uc624\uc2ed\ucc9c\uc73c\ub85c \ud569\ub958\ud558\ub294 12km\uae38\uc774\uc758 \ubb34\ub989\ucc9c\uc774 \ud750\ub974\uace0 \uc788\uc5b4 \uc0b0\ud589\ud558\uae30 \uc801\ud569\ud55c \uacf3\uc774\ub2e4.'), STRINGDECODE('\u2460 \uace8\ub9d0-\uc790\uc554\uace8-\uc790\uc554\uc7ac-\ud658\uc120\ubd09-\uc0ac\uac70\ub9ac \uc548\ubd80-\uc815\uc0c1-\uc0ac\uac70\ub9ac \uc548\ubd80-\uc678\ub098\ubb34\uace8-\uc810\ucd0c(\ucd1d 4\uc2dc\uac04 10\ubd84)<BR>\u2461 \uc810\ucd0c-\uc678\ub098\ubb34\uace8-\uad6c\ubd80\uc2dc\ub839-\uc815\uc0c1-\uc0ac\uac70\ub9ac \uc548\ubd80-1048\ubd09-\ud070\uac00\ub798\uace8-\ubb34\uc0ac\ub3d9(\ucd1d 3\uc2dc\uac04 20\ubd84)<BR>\u2462 \uace8\ub9d0-\uc790\uc554\ubaa9\uc774\ub2a5\uc120-\uc804\ub9dd\ubc14\uc704-\uc0ac\uac70\ub9ac \uc548\ubd80-\uc815\uc0c1-\uad6c\ubd80\uc2dc\ub839-\uc678\ub098\ubb34\uace8-\uc810\ucd0c(\ucd1d 3\uc2dc\uac04 30\ubd84)'), '37.33025', '129.059409', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc0bc\ucc99\uc2dc \uc2e0\uae30\uba74\u318d\ud0dc\ubc31\uc2dc \ud558\uc0ac\ubbf8\ub3d9'), 2214180500, 1073, STRINGDECODE('\ub355\ud56d\uc0b0'), STRINGDECODE('&lt;\uc5b8\uc81c\ub098 \uc548\uac1c\uc5d0 \uc2f8\uc778 \ud658\uc0c1\uc758 \uc0b0&gt;<BR>\uc5b4\ub290 \uc0b0\uac1d\uc774 \"\"\uc6b0\ub9ac\ub098\ub77c\uc5d0\uc11c \uadf8\ub79c\ub4dc\uce90\ub144\uc744 \ub2ee\uc740 \uacf3\uc774 \uc788\ub2e4\uba74 \uac70\uae30\ub294 \ubc14\ub85c \ub355\ud56d\uc0b0 \uc790\uc554\uace8\"\"\uc774\ub77c\uace0 \uc124\ud30c\ud588\ub358 \uae30\uacbd(\u5947\u666f)\uc758 \uc0b0\uc774\ub2e4. \uacbd\ub3d9\uc9c0\uad34(\u50be\u52d5\u5730\u584a)\uc758 \ud45c\ubcf8\uc744 \uc774\ub8e8\ub294 \uacf3\uc73c\ub85c \ub3d9\ucabd\uc740 \uae4e\uc544\uc9c0\ub978 \uc11d\ud68c\uc554 \uc0ac\uba74, \uc11c\ucabd\uc740 1000\ubbf8\ud130 \uc804\ud6c4\uc758 \uace0\uc704\ud3c9\ud0c4\uba74\uc744 \uc774\ub8ec\ub2e4. <BR>\uc11d\ud68c\uc554 \uc0ac\uba74\uc5d0\ub294 \ud658\uc120\uad74, \ubc14\ub78c\uad74, \uad00\uc74c\uad74 \uac19\uc740 \ub3d9\uad74\ub4e4\uacfc \ucd1b\ub300\ubd09, \uc0ac\ub2e4\ub9ac\ubc14\uc704, \ub098\ud55c\ubd09, \uc218\ub9ac\ubd09, \uae08\uac15\ubd09, \ubbf8\ub975\ubd09 \uac19\uc740 \uae30\uc554\ub4e4\uc774 \uc990\ube44\ud558\ub2e4. \uace8\uc9dc\uae30\ub294 \uac70\uc758 \uc5b8\uc81c\ub098 \uc548\uac1c\uc5d0 \ucc28\uc788\uace0 \uadf8 \uc0ac\uc774\ub85c \uc5b8\ub73b\uc5b8\ub73b \ud5d8\uc0b0\uacfc \uae30\uc554\uc774 \ub4dc\ub7ec\ub098\ub294 \ud658\uc0c1\uc801\uc778 \ud48d\uacbd\uc774\ub2e4. <BR>\uc6d0\ub798 \uc774\ub984\uc740 \ub355\uba54\uae30[\uc0b0]\uc774\uc5c8\ub2e4. \uc0bc\ucc99\ucabd\uc5d0\uc11c \ubd80\ub974\ub358 \uac83\uc73c\ub85c \"\"\uc800 \ub108\uba38\uc5d0 \ud654\uc804\ud558\uae30 \uc88b\uc740 \ub354\uae30(\uace0\uc6d0)\uac00 \uc788\ub294 \ubafc\"\"\ub77c\ub294 \ub73b\uc774\ub77c\uace0 \ud55c\ub2e4. \uadf8\uac83\uc774 \ub355\ubaa9\uc774\ub85c \ubcc0\ud574 ''\ubaa9 \ud56d''\uc790 \ub355\ud56d\uc0b0\uc73c\ub85c \ud45c\uae30\ub41c \ub4ef\ud558\ub2e4. \uadf8 \ub354\uae30\ub294 \uc9c0\uae08 \uad11\ub3d9\ub310 \uc218\ubab0\ubbfc\ub4e4\uc758 \uace0\ub7ad\uc9c0\ucc44\uc18c\ubc2d\uc774 \ub418\uc5b4\uc788\ub2e4. <BR>\uc369 \uc720\uba85\ud558\uc9c0 \uc54a\uc740 \uc0b0\uc774\uc5b4\uc11c \uc9c0\ub9ac\uc801, \uc0dd\ud0dc\uc801 \uc870\uc0ac\uac00 \uac70\uc758 \uc548 \ub3fc\uc788\ub2e4. \uc0b0\uc5ed(\u5c71\u57df) \ud30c\uc545\uc774 \ud2b9\ud788 \uc548 \ub3fc \uad11\ub3d9\ub310 \uc774\uc8fc\ub2e8\uc9c0 \uc11c\ucabd\uc5d0 1130\ubd09\uc774 \uc788\ub294\ub370\ub3c4 \ub300\uc774\ub9ac\uc5d0\uc11c \uac00\uc7a5 \ub192\uac8c \ubcf4\uc774\ub294 1071\ubd09\uc744 \uc8fc\ubd09\uc73c\ub85c \uc5ec\uae34\ub2e4. \uc774\ub294 \uadf8 \uc11c\ubd81\ucabd\uc5d0 \uc788\ub294 \ubc31\ub450\ub300\uac04\uc0c1\uc758 \ud658\uc120\ubd09(1079m)\ubcf4\ub2e4\ub3c4 \ub0ae\ub2e4. <BR>\uc0b0\uacbd\ud45c\uc5d0\ub294 \ub313\uc7ac \uc8fd\ud604(\u7af9\u5cf4) \ub2e4\uc74c\uc5d0 \uac74\uc758\ub839(\u5efa\u5100\u5dba)\uc774 \uc774\uc5b4\uc9c4\ub2e4. \ub530\ub77c\uc11c \ub458 \uc0ac\uc774\uc5d0 \uc788\ub294 \ub355\ud56d\uc0b0\uc740 \ub313\uc7ac\uc5d0\uc11c \uac74\uc758\ub839\uae4c\uc9c0\uac00 \uc601\uc5ed\uc774\uace0 \uc0c1\ubd09\uc740 1130\ubd09\uc774\ub2e4.'), STRINGDECODE('\uc804\ud615\uc801\uc778 \uacbd\ub3d9\uc9c0\uad34(\u50be\u52d5\u5730\u584a) \uc9c0\ud615\uc73c\ub85c \uae30\uc554\uc808\ubcbd\uacfc \ucd08\uc6d0\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uc788\uc73c\uba70 \uac08\ub9e4\uad74, \uc81c\uc554\ud48d\ud608, \uc591\ud130\ubaa9\uc138\uad74, \ub355\ubc1c\uc138\uad74, \ud070\uc7ac\uc138\uad74 \ub4f1 \uc11d\ud68c\ub3d9\uad74\uc774 \ub9ce\uc774 \uc18c\uc7ac\ud558\uace0, \ub300\uc774\ub3d9\uad74 \uad70\ub9bd\uacf5\uc6d0(1996\ub144 \uc9c0\uc815) \uad6c\uc5ed\ub0b4\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc57d 4\u223c5\uc5b5\ub144 \uc804\uc5d0 \uc774\ub8e8\uc5b4\uc9c4 \uae38\uc774 6.9\u339e, \ucc9c\uc7a5\ub192\uc774 30m\uc5d0 \uc774\ub974\ub294 \ub3d9\uc591\ucd5c\ub300\uc758 \ub3d9\uad74\uc778 \ud658\uc120\uad74(\u5e7b\u4ed9\u7a9f : \ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c178\ud638)\uc774 \uc720\uba85'), STRINGDECODE('\ud0dc\uace0\uc801 \uc2e0\ube44\ub97c \uac04\uc9c1\ud55c \ud658\uc120\uad74\uc18d\uc73c\ub85c'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub300\uc774\ub9ac\ub098 \uad11\ub3d9\ub9ac\uc758 \ubbfc\ubc15\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR>\ub300\uc774\ub9ac \uc774\uc885\ub300 \u260e033-541-1554 \ub300\uc774\uc288\ud37c \u260e033-541-5978 \ubbf8\ub85c\uc0c1\uc815\ubd84\uad50(\ud3d0\uad50) \u2