INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(26, TIMESTAMP '2019-08-14 15:46:34.912', TIMESTAMP '2019-08-14 15:46:54.866', '43', STRINGDECODE('\uc6b0\uc554 \uc1a1\uc2dc\uc5f4 \uc120\uc0dd\uc774 \"\ub3c4\ub97c \uae68\ub2eb\uace0 \uc2a4\uc2a4\ub85c \uc990\uae38 \ub9cc\ud55c \uacf3\uc774\ub2e4\" \ud574\uc11c \ub3c4\ub77d\uc0b0\uc774\ub77c \uba85\uba85\ud55c \uc774 \uc0b0\uc740 \uacbd\ubd81\uacfc \ucda9\ubd81\uc758 \ub3c4\uacbd\uacc4\uc120\uc5d0 \uadfc\uc811\ud574 \uc788\ub2e4. <BR>\uc6d4\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uad8c\uc5d0 \uc18d\ud574 \uc788\ub294 \uc0b0\uc73c\ub85c, \ucda9\ubd81 \ub2e8\uc591\uad70 \ub2e8\uc591\uc74d\uacfc \ub300\uac15\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\uace0 \uc788\uc73c\uba70 \ub3c4\ub77d\uc0b0 \uc0b0\uc790\ub77d\uc5d0\ub294 \ub2e8\uc591 8\uacbd \uc911 4\uacbd\uc778 \uc0ac\uc778\uc554,\uc0c1\uc120\uc554,\uc911\uc120\uc554,\ud558\uc120\uc554 \ub4f1\uc774 \uc788\uc5b4 \uad00\uad11\uc758 \ubcf4\uace0\uc774\uae30\ub3c4 \ud558\ub2e4.'), STRINGDECODE('\u2460\uc0c1\uc120\uad50-\uc120\ubc14\uc704-\ucc44\uc6b4\ubd09-\uc0bc\uac70\ub9ac-\uc815\uc0c1-\uc0bc\uac70\ub9ac-\uc81c\ubd09(\uc0c1\uc120\uc0c1\ubd09)-\uc0c1\uc120\uad50 (\ucd1d 5\uc2dc\uac04 10\ubd84)<BR>\u2461\uad81\ud143\uace8-\uc0ac\ubaa8\ud3ed\ud3ec-\ub0b4\uad81\uae30\ub9c8\uc744-\uc2e0\uc120\ubd09-\uc815\uc0c1-\uc0c1\uc120\uc554 (\ucd1d 3\uc2dc\uac04 30\ubd84)'), '36.89775', '128.2901', STRINGDECODE('\ucda9\uccad\ubd81\ub3c4 \ub2e8\uc591\uad70 \ub2e8\uc131\uba74, \ub300\uac15\uba74'), 2307083702, 965, STRINGDECODE('\ub3c4\ub77d\uc0b0'), STRINGDECODE('&lt;\uc131\ubcbd \uc313\uc740 \ub4ef\ud55c \uc554\ub989\ub4f1\ubc18 \ubb18\ubbf8 &gt;<BR>\ub3c4\ub77d\uc0b0(965.3m)\uc740 \uc6d4\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \ub044\ud2b8\uba38\ub9ac, \uc18c\ubc31\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uacfc \uc774\uc6c3\ud55c \uacf3\uc5d0 \uc788\ub2e4. ''\uae68\ub2ec\uc74c\uc744 \uc5bb\ub294 \ub370\ub294 \ub098\ub984\ub300\ub85c \uae38\uc774 \uc788\uc5b4\uc57c \ud558\uace0 \uac70\uae30\uc5d0\ub294 \ub610\ud