INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(27, TIMESTAMP '2019-08-14 15:46:34.913', TIMESTAMP '2019-08-14 15:46:55.446', '11', STRINGDECODE('\ub3c4\ubd09\uc0b0\uc740 \uc11c\uc6b8 \uadfc\uad50\uc758 \uc0b0\uc73c\ub85c \uc608\ub85c\ubd80\ud130 \ubd81\ud55c\uc0b0\uacfc \ud558\ub098\uc758 \uc0b0\uc73c\ub85c \ucde8\uae09\ub418\uc5b4 \uc654\ub2e4. \uc6b0\uc774\ub839\uc744 \uacbd\uacc4\ub85c \ubd81\ud55c\uc0b0\uacfc \ub098\ub780\ud788 \uc19f\uc544 \uc788\uc5b4 \ud604\uc7ac \ub450 \uc0b0\uc744 \ubd81\ud55c\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc5d0 \ud568\uaed8 \ud3ec\ud568\uc2dc\ud0a4\uace0 \uc788\ub2e4. \ubd81\ud55c\uc0b0 \uba74\uc801 55\u33a2\uc5d0 \ube44\ud574 \ub3c4\ubd09\uc0b0\uc740 \uc57d 24\u33a2\ub85c \uc808\ubc18\uc5d0 \ubd88\uacfc\ud558\ub098 \uc0b0\uc138\uac00 \uc544\ub984\ub2e4\uc6cc \ub4f1\uc0b0\uac1d\uc774 \ub04a\uc774\uc9c0 \uc54a\ub294 \uacf3\uc774\ub2e4.<BR>\uc8fc\ub2a5\uc120\uc0c1\uc5d0\ub294 \ucd5c\uace0\ubd09\uc778 \uc790\uc6b4\ubd09\uc744 \ube44\ub86f\ud574 \ub9cc\uc7a5\ubd09, \uc120\uc778\ubd09, \uc8fc\ubd09 \ub4f1\uc758 \uc554\ubd09\uacfc \uc11c\ucabd\uc73c\ub85c \ub2e4\uc12f\uac1c\uc758 \uc554\ubd09\uc774 \ub098\ub780\ud788 \uc904\uc9c0\uc5b4 \uc788\ub294 \uc624\ubd09\uc774 \uc788\ub2e4. \uc120\uc778\ubd09, \ub9cc\uc7a5\ubd09, \uc8fc\ubd09, \uc6b0\uc774\uc554\uc740 \uac01\uae30 \uac70\ub300\ud55c \uc554\ubcbd\ub4e4\uc774\ub2e4. \ub3c4\ubd09\uc0b0\uc740 \uc6b0\ub78c\ud55c \uae30\uc554\uad34\uc11d\uacfc \ubfb0\uc871\ud788 \uc19f\uc740 \uc554\ubd09\ub4e4\uc774 \uc7a5\uad00\uc744 \uc774\ub8e8\uba70, \uc0ac\ubc29\uc73c\ub85c \ubed7\uc740 \uacc4\uace1\uc744 \ub530\ub77c \ub179\uc74c\uc774 \uc6b0\uac70\uc838 \uba85\uc18c\ub97c \ub9cc\ub4e4\uace0 \uc788\ub2e4.<BR>\uc0ac\uacc4\uc808 \ubaa8\ub450 \uc990\uaca8 \ucc3e\ub294 \uc0b0\uc774\uc9c0\ub9cc \uac00\uc744\uc774\uba74 \ub2e8\ud48d\uc758 \ubb3c\uacb0\uc774 \uc5ec\ub290 \uc0b0 \ubabb\uc9c0 \uc54a\ub2e4. \ub3c4\ubd09\uc0b0\uc758 3\ub300 \uacc4\uace1\uc740 \ubb38\uc0ac\ub3d9\uacc4\uace1, \ub9dd\uc6d4\uc0ac\uacc4\uace1(\uc6d0\ub3c4\ubd09\uacc4\uace1), \ubcf4\ubb38\uc0ac\uacc4\uace1(\ubb34\uc218\uace8) \uc774\ub2e4. \uc774 \uacc4\uace1\ub4e4\uc774 \ubc14\ub85c \uc0b0\ud589\uae30\uc810\uacfc \uc5f0\uacb0\ub41c\ub2e4. \ub3c4\ubd09\uc0b0\uc740 \ub4f1\uc0b0\ucf54\uc2a4\uac00 \ub2e4\uc591\ud55c\ub370 \uadf8 \uc911\uc5d0\uc11c \uc6b0\uc774\ub3d9, \ub3c4\ubd09\uc720\uc6d0\uc9c0, \ub9dd\uc6d4\uc0ac\uc5ed(\uc7a5\uc218\uc6d0), \uc131\ud669\ub2f9 \ucf54\uc2a4\uac00 \ub300\ud45c\uc801\uc774\ub2e4. <BR>\ub3c4\ubd09\uc0b0\uc758 \uc8fc\ubd09\uc740 \uc790\uc6b4\ubd09(740.2m)\uc73c\ub85c, \uc815\uc0c1\uc744 \uae30\uc810\uc73c\ub85c \ub4f1\uc0b0\ub85c\uac00 \uc0ac\ubc29\uc73c\ub85c \uac70\ubbf8\uc904 \uac19\uc774 \uc218\uc2ed \uac00\ub2e5\uc774 \ud3bc\uccd0\uc838 \uc788\ub2e4. \ubaa8\ub450 \ud569\uccd0 100\uc5ec\uac1c\uc758 \uc0b0\ud589 \ucf54\uc2a4\uac00 \uc788\ub2e4. \uadf8 \uc911 \ub3c4\ubd09\uc0b0 \ub4f1\uc0b0\ub85c\uc758 \ud575\uc2ec\uc740 \ud3ec\ub300\ub2a5\uc120\uc73c\ub85c, \uc774 \uc0b0\uc758 \ub4f1\ubf08\ub97c \uc774\ub8e8\ub294 \ud3ec\ub300\ub2a5\uc120-\uc790\uc6b4\ubd09-\uce7c\ubc14\uc704-\uc6b0\uc774\uc554 \ub2a5\uc120\uc73c\ub85c \uc774\uc5b4\uc9c4\ub2e4. \ud3ec\ub300\ub2a5\uc120\uc740 \ub2a5\uc120 \uc911\uac04\uc5d0 \ub300\uacf5\ud3ec\uc9c4\uc9c0\uc778 \ud3ec\ub300(\u7832\u81fa)\uac00 \uc788\uc5c8\ub2e4\uace0 \ud574\uc11c \ubd99\uc5ec\uc9c4 \uc774\ub984\uc774\ub2e4. \ub3c4\ubd09\uc0b0 \uc0b0\ud589 \ub4e4\uba38\ub9ac\ub294 \uc218 \uc5c6\uc774 \ub9ce\uc9c0\ub9cc \ud06c\uac8c \ubd84\ub958\ud558\uba74, \uc548\uace8\uc720\uc6d0\uc9c0, \ud68c\ub8e1\uc0ac\uc785\uad6c, \uc6d0\ub3c4\ubd09\uc720\uc6d0\uc9c0, \ub3c4\ubd09\uc720\uc6d0\uc9c0, \uc131\ud669\ub2f9, \uc6b0\uc774\ub3d9, \uc1a1\ucd94 7\uac1c \ucf54\uc2a4\ub97c \uaf3d\uc744 \uc218 \uc788\ub2e4. \ube7c\uc5b4\ub09c \uc554\ub989\ubbf8\ub97c \uc790\ub791\ud558\ub294 \ub3c4\ubd09\uc0b0\uc740 \uc5b4\ub290 \ucf54\uc2a4\uc774\ub4e0 \ubc14\uc704 \uc554\ub989\uc774 \uacf3\uacf3\uc5d0 \ub3c4\uc0ac\ub9ac\uace0 \uc788\uc5b4 \uc8fc\uc758\uac00 \ud544\uc694\ud558\ub2e4. \uc218\ub3c4\uad8c \ub4f1\uc0b0\uac1d\uc774 \ub9ce\uc774 \ubab0\ub9ac\ub294 \ud734\uc77c\uc5d0\ub294 \uc8fc\ub2a5\uc120\uc778 \ud3ec\ub300\ub2a5\uc120 \ub4f1\uc5d0 \uc815\uccb4 \ud604\uc0c1\uc774 \ubc8c\uc5b4\uc9c8 \uc815\ub3c4\ub85c \ubd90\ube48\ub2e4. \uac08\ub9bc\uae38\uc774 \ubb34\uc218\ud788 \ub9ce\uc544 \uae38\uc744 \uc783\uc744 \uc218 \uc788\uc73c\ubbc0\ub85c \uc0ac\uc804\uc5d0 \uc9c0\ub3c4\ub97c \uaf3c\uaf3c\ud788 \ucc59\uaca8\uc57c \ub418\ub294 \uc0b0\uc774\ub2e4.'), STRINGDECODE('\u2460 \uc8fc\ucc28\uc7a5-\ub3c4\ubd09\uc11c\uc6d0-\ub3c4\ubd09\uc0b0\uc7a5-\ub9c8\ub2f9\ubc14\uc704-\uc2e0\uc120\ubd09-\uce7c\ubc14\uc704-\uc6b0\uc774\uc554-\uc6b0\uc774\ub3d9(\ucd1d 3\uc2dc\uac04 30\ubd84)<BR>\u2461 \ub9dd\uc6d4\uc0ac\uc5ed-\uc6d0\ub3c4\ubd09 \ub9e4\ud45c\uc18c-\ub9dd\uc6d4\uc0ac-\uc2e0\uc120\ubd09-\uc1a1\ucd94\ud3ed\ud3ec-\uc1a1\ucd94\uacc4\uace1-\uc1a1\ucd94\uc5ed(\ucd1d 3\uc2dc\uac04 30\ubd84)<BR>\u2462 \ud68c\ub8e1\uace8 \ub9e4\ud45c\uc18c-\ud68c\ub8e1\uc0ac-\ud3ec\ub300\ub2a5\uc120-\uc2e0\uc120\ubd09-\ub9c8\ub2f9\ubc14\uc704-\ub3c4\ubd09\uc0b0\uc7a5-\uc8fc\ucc28\uc7a5(\ucd1d 4\uc2dc\uac04)'), '37.668812', '127.047087', STRINGDECODE('\uc11c\uc6b8\ud2b9\ubcc4\uc2dc \ub3c4\ubd09\uad6c, \uacbd\uae30\ub3c4 \uc758\uc815\ubd80\uc2dc \ud638\uc6d0\ub3d9\u318d\uc591\uc8fc\uc2dc \uc7a5\ud765\uba74'), 1110010100, 740, STRINGDECODE('\ub3c4\ubd09\uc0b0(\uc790\uc6b4\ubd09)'), STRINGDECODE('&lt;\ubd09\ub9cc\ubbf8 \ud0c1\uc6d4\ud55c \ube44 \uc624\ub294 \ub0a0\uc758 \uc0b0\uc218\ud654&gt;<BR>\ubd09\ub9cc\ubbf8(\u5cf0\u5dd2\u7f8e)\uac00 \ud0c1\uc6d4\ud558\uac8c \ube7c\uc5b4\ub09c \uc0b0\uc774\ub2e4. \"\"\ud478\ub978 \ud558\ub298 \uae4e\uc544\uc138\uc6b4 \ub9cc \uae38 \ubd09\uc6b0\ub9ac(\u9751\u5929\u524a\u51fa\u842c\u4e08\u5cf0)\"\"\ub77c \uc74a\uc5c8\ub358 \uc61b \uc2dc\uc778\uc758 \ud45c\ud604\uc774 \ubb34\ub98e\uc744 \uce58\uac8c \ub9cc\ub4dc\ub294 \uc7a5\ucf8c\ud55c \uc120\uc778\ubd09\uc744 \ube44\ub86f\ud574 \uadf8 \ub4a4\uc5d0 \uc911\ucca9\ub41c \ub9cc\uc7a5\ubd09, \uc790\uc6b4\ubd09, \uc2e0\uc120\ubd09, \uc11c\ucabd \ub05d\uc758 \uae30\uc774\ud55c \ub2e4\uc12f \ubd09\uc6b0\ub9ac \uc624\ubd09\uc5d0 \uc774\ub974\uae30\uae4c\uc9c0 \ubaa8\ub450 \ube5b\ub098\ub294 \ud654\uac15\uc554\uc73c\ub85c \ub418\uc5b4\uc788\uc5b4 \uc2dc \uc18d\uc758 \"\"\ud478\ub978 \ud558\ub298\"\"\uc744 \ub354\uc6b1 \ud478\ub974\uac8c \ud55c\ub2e4. <BR>\uc774\ub807\uac8c \uc554\uace8(\u5ca9\u9aa8)\uc774 \ub450\ub4dc\ub7ec\uc9c4 \uc0b0\uc774\ub77c \uc8fc\ub989\uc774 \uac70\uc758 \uc554\ubd09\ub989\uc774\ub2e4. \uc0ac\uc774\uc0ac\uc774 \uc9c4\uc9dc \uc554\ub989\uc778 \ud3ec\ub300\ub2a5\uc120, \uce7c\ubc14\uc704\ub2a5\uc120\ub3c4 \uc788\uc9c0\ub9cc \ub300\uac1c\ub294 \uc194\uc232 \uadf8\ub298 \uc0ac\uc774 \uc554\ubd09\uc744 \ub3cc\uace0 \ub3c4\ub294 \uc2dc\uc6d0\ud55c \uc0b0\uae38\uc774\ub2e4. \uadf8\ub798 \ub3c4\ubd09\uc0b0\ud589\uc740 \uad6c\ub984\uc774 \uc0b0\uc218\ud654\ub97c \uc5f0\ucd9c\ud558\ub294 \uc6b0\uc911(\u96e8\u4e2d)\uc0b0\ud589\uc744 \ucd5c\uace0\ub85c \uaf3d\ub294\ub2e4. <BR>\uc19f\uc74c\uc774 \uc6b0\uc138\ud558\uace0 \ubc14\uc704\uac00 \ub9ce\uc544 \uacc4\uace1 \uc218\ub7c9\uc740 \uc801\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uacc4\ub958\ubbf8\ub294 \ub6f0\uc5b4\ub098\ub2c8 \ube44 \uc624\ub294 \ub0a0\uc774 \uc0c8\uc0bc \uc81c\uaca9\uc774\ub2e4. \ubc18\uba74\uc5d0 \ub4b7\uc790\ub77d \uc1a1\ucd94\uacc4\uace1\uc740 \ub2e4\ub974\ub2e4. \uc628\ud1b5 \ud478\ub978 \uc232\ucc9c\uc9c0\uac00 \ubfdc\uc5b4\ub0b4\ub294 \ub9d1\uc740 \ubb3c\uc774 \uc138 \uac1c\uc758 \uc1a1\ucd94\ud3ed\uc5d0\ub2e4 \uc1a1\ucd94\uc720\uc6d0\uc9c0\uc758 \uae34 \uc640\ud3ed\uacfc \ub2f4(\u6f6d)\uae4c\uc9c0 \ub9cc\ub4e4\uc5b4\ub450\uace0 \uc788\ub2e4. <BR>\uadf8\ub798\uc11c \ub3c4\ubd09\uc740 \uc774\ub545\uc5d0\uc11c \uac00\uc7a5 \ub4f1\uc0b0\ub85c\uac00 \ucd18\ucd18\ud55c \uc0b0, \uac00\uc7a5 \uc0ac\ub791\ubc1b\ub294 \uc0b0\uc774\ub2e4. \uc8fc\ub9d0\ub9c8\ub2e4 \uc0b0\uc744 \ucc3e\ub294 \ube60\uaf3c\uc774 \uc11c\uc6b8\uc0b0\uac1d\ub4e4, \uc77c\uc694\uc77c\uc5d0\ub294 \ub418\ub3c4\ub85d\uc774\uba74 \uc774 \uc0b0\uc744 \ud53c\ud560 \uc815\ub3c4\ub2c8\uae4c.'), STRINGDECODE('\ucd5c\uace0\ubd09\uc778 \uc790\uc6b4\ubd09\uc744 \uc911\uc2ec\uc73c\ub85c \ub9cc\uc7a5\ubd09, \uc120\uc778\ubd09, \uc6d0\ub3c4\ubd09\uacc4\uace1, \uc6a9\uc5b4\ucc9c\uacc4\uace1, \uc1a1\ucd94\uacc4\uace1 \ub4f1 \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1983\ub144)\ub418\uc5b4 \uc788\uc73c\uba70, \uc218\ub3c4\uad8c \uc2dc\ubbfc\uc758 \ud734\uc2dd\ucc98\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc554\ubcbd\ub4f1\uc0b0\uc5d0 \ucd5c\uc801\uc9c0\uc774\uba70, \ud68c\ub8e1\uc0ac(\u56de\u9f8d\u5bfa), \ub9dd\uc6d4\uc0ac(\u671b\u6708\u5bfa), \ucc9c\ucd95\uc0ac(\u5929\u7afa\u5bfa), \ubcf4\ubb38\uc0ac(\u666e\u9580\u5bfa) \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\uac70\ubbf8\uc904\ucc98\ub7fc \uc5bd\ud78c \ubbf8\ub85c\uc758 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uad73\uc774 \uc0b0 \uc544\ub798\uc11c \uc219\ubc15\uc744 \ud558\uaca0\ub2e4\uba74 \uc11c\uc6b8YMCA\uc5d0\uc11c \uc6b4\uc601\ud558\ub294 \ub2e4\ub77d\uc6d0\ucea0\ud504\uc7a5\uc744 \uc774\uc6a9\ud55c\ub2e4. \ucea0\ud504\uc0ac\uc774\ud2b8\ub294 \ubb3c\ub860\uc774\ub824\ub2c8\uc640 15\uba85\uc529 \uc798 \uc218 \uc788\ub294 \uce90\ube48\ub3c4 5\ub3d9 \uc788\ub2e4. \u260e031-873-5624<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ubd81\ud55c\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \ub3c4\ubd09\ubd84\uc18c \u260e02-954-2566'), STRINGDECODE('\uc9c0\ud558\ucca0\uc744 \uc774\uc6a9\ud558\uba74 \ud3b8\ub9ac\ud558\ub2e4. \ub3c4\ubd09\ub3d9 \ubc29\uba74\uc740 1\ud638\uc120\uacfc 7\ud638\uc120 \ub3c4\ubd09\uc0b0\uc5ed, \ub9dd\uc6d4\uc0ac\uc640 \ud68c\ub8e1\uc0ac \ubc29\uba74\uc740 1\ud638\uc120\uc744 \ud0c0\uace0 \uc870\uae08 \ub354 \uac00 \uac19\uc740 \uc774\ub984\uc758 \uc5ed\uc5d0\uc11c \ub0b4\ub9b0\ub2e4. \uc1a1\ucd94\ucabd\uc740 3\ud638\uc120 \uad6c\ud30c\ubc1c\uc5ed\uc5d0\uc11c \ub0b4\ub824 \uc1a1\ucd94\ud589 7023\ubc88 \ubc84\uc2a4\ub098 704\ubc88, \uc758\uc815\ubd80\ud589 34, 36\ubc88 \ubc84\uc2a4\ub97c \ud0c4\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(28, TIMESTAMP '2019-08-14 15:46:34.913', TIMESTAMP '2019-08-14 15:46:56.093', '46', STRINGDECODE('\ud55c\ubc18\ub3c4\uc758 \ucd5c\ub0a8\ub2e8\uc5d0 \uc704\uce58\ud55c \ub450\ub95c\uc0b0(700m)\uc740 \ubc31\ub450\ub300\uac04\uc5d0\uc11c \ubed7\uc740 \ud638\ub0a8\uc815\ub9e5\uc774 \ubc14\ub2e4\uc5d0 \uc774\ub974\uba74\uc11c \ub9c8\uc9c0\ub9c9\uc73c\ub85c \uc19f\uc544 \uc624\ub978 \uc0b0\uc774\ub2e4. <BR>\uc804\ub0a8 \ud574\ub0a8\uad70 \uc0bc\uc0b0\uba74\uacfc \ud604\uc0b0\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c \uc77c\uba85 \ub300\ub454\uc0b0(\u5927\u829a\u5c71)\uc774\ub77c\uace0\ub3c4 \ubd88\ub9b0\ub2e4. \uc784\uc9c4\uc65c\ub780 \ub2f9\uc2dc \uc804\uad6d\uc758 \uc0b0\uc57c\ub97c \ub204\ube44\uba70 \uc65c\uc801\uc744 \ubab0\uc544\ub0b8 \uc11c\uc0b0\ub300\uc0ac\ub294 \uc774\uacf3 \ub450\ub95c\uc0b0\uc744 ''\ub9cc\uace0\uc5d0 \uae68\uc9c0\uc9c0 \uc54a\uace0 \uc0bc\uc7ac\uac00 \ubbf8\uce58\uc9c0 \uc54a\ub294 \uc0b0'' \uc774\ub77c \ud588\ub2e4. \uc0bc\uc7ac\uac00 \ub4e4\uc9c0 \uc54a\ub294\ub2e4\ub294 \uac83\uc740 \ud64d\uc218\ub098 \uc7ac\ud574\uc5d0\uc11c \uc548\uc804\ud558\ub2e4\ub294 \ub73b\uc774\ub2e4. <BR>\uc2e4\uc81c\ub85c \ub450\ub95c\uc0b0\uc740 \ubd81\uc73c\ub85c\ub294 \uc6d4\ucd9c\uc0b0\uc774 \ud558\ub298\uc744 \ubc1b\uccd0\uc8fc\uace0 \ub0a8\uc73c\ub85c\ub294 \ub2ec\ub9c8\uc0b0\uc774 \uc9c0\ucd95\uc744 \ub9fa\uc5b4 \uc8fc\uace0 \uc788\uc5b4 \uc61b \uc870\uc0c1\ub4e4\ub85c\ubd80\ud130 \ud48d\uc218\uc9c0\ub9ac\uc801\uc73c\ub85c \uc778\uc815\ubc1b\uc740 \uc0b0\uc774\ub2e4. \uc0c1\uc0c1\uc758 \uc0b0\uc774\uba70 \uc0b0\uc758 \uc870\uc885\uc73c\ub85c \uc54c\ub824\uc9c4 \uace4\ub95c\uc0b0\uacfc \ubc31\ub450\uc0b0\uc5d0\uc11c \uc5f0\uc720\ud588\ub2e4\ub294 \ub73b\uc5d0\uc11c \ub450 \uc0b0\uc758 \ud55c\uc790\uc529\uc744 \ub530 \ub450\ub95c\uc0b0\uc774\ub77c \uc774\ub984 \uc9c0\uc5c8\ub2e4\uace0 \ud55c\ub2e4.'), STRINGDECODE('\u2460 \ub9e4\ud45c\uc18c-\uc720\uc120\uc5ec\uad00-\uace0\uacc4\ubd09-\uc624\uc2ec\uc7ac-\uac00\ub828\ubd09-\ub450\ub95c\ubd09-\uc9c4\ubd88\uc554-\ub300\ud765\uc0ac-\ub9e4\ud45c\uc18c(\ucd1d 5\uc2dc\uac04 40\ubd84)<BR>\u2461 \ub9e4\ud45c\uc18c-\ud5a5\ub85c\ubd09-\uc624\ub3c4\uce58-\ud608\ub9dd\ubd09-\uc5f0\ud654\ubd09-\ub0a8\uc554-\ub300\ud765\uc0ac-\uc655\ubc9a\ub098\ubb34-\ub9e4\ud45c\uc18c(\ucd1d 5\uc2dc\uac04 50\ubd84)<BR>\u2462 \ucf00\uc774\ube14\uce74 \uc2b9\uac15\uc7a5-\uace0\uacc4\ubd09-\uc624\uc2ec\uc7ac-\uac00\ub828\ubd09-\ub9cc\uc77c\uc7ac-\uc77c\uc9c0\uc554-\ub300\ud765\uc0ac-\ub9e4\ud45c\uc18c(\ucd1d 4\uc2dc\uac04 40\ubd84)'), '34.511766', '126.604086', STRINGDECODE('\uc804\ub77c\ub0a8\ub3c4 \ud574\ub0a8\uad70 \uc0bc\uc0b0\uba74\u318d\ubd81\uc77c\uba74\u318d\ubd81\ud3c9\uba74\u318d\ud604\uc0b0\uba74'), 2621041300, 700, STRINGDECODE('\ub450\ub95c\uc0b0'), STRINGDECODE('&lt;\ubc14\ub2f7\uac00\uc7ac \uc5ec\uc758\uc8fc \uba38\uae08\uc740 \ub4ef \uae30\uc774\ud55c \ud615\uad6d&gt;<BR>\uac00\uc7a5 \ub0a8\ucabd\uc758 ''\uc0b0\uc774\ub77c \ud560 \ub9cc\ud55c \uac83''\uc774\ub2e4. \uc721\uc0b0 \ub369\uc5b4\ub9ac\uc5ec\uc11c \ub4ec\uc9c1\ud558\uae30\ub294 \ud558\ub098 \uc798\ub0ac\ub2e4\uace0 \ud560 \uc218\ub294 \uc5c6\ub2e4. \uadf8\ub7f0\ub370\ub3c4 \uc0b0 \ubcf4\ub294 \ub370 \uc77c\uac00\uacac\uc774 \uc788\ub358 \uc11c\uc0b0\ub300\uc0ac\uac00 \uc790\uc2e0\uc758 \uc758\ubc1c(\u8863\u9262)\u2015\uc637\uac00\uc9c0\uc640 \ubc25\uadf8\ub987\uc744 \uc5ec\uae30 \uac16\ub2e4\ub178\ub77c \ud55c \uac83\uc744 \ubcf4\uba74 \ubb54\uac00 \uc788\uae30\ub294 \ud55c \ub4ef\ud558\ub2e4. <BR>\uadf8\ub0e5 \ub4e4\uc5b4\uac00\uc11c\ub294 \ubc1c\uacac\ud560 \uc218\uac00 \uc5c6\ub2e4. \ubd04 \uae34 \uace8\uc9dc\uae30 \uc7a5\ucd98(\u9577\u6625)\uacc4\uace1\uc744 \uac78\uc5b4\uac00\ubd10\uc57c \ud55c\ub2e4. \ub2e4\uc74c, \uc815\uc0c1\uc774 \uc544\ub2c8\ub77c \ubc18\ub300\ud3b8\uc73c\ub85c \uc62c\ub77c\uac00\ubd10\uc57c \ud55c\ub2e4. \ud5a5\ub85c\ubd09(469m). \uadf8\ub9ac\uace0, \uac00\ubd80\uc88c\ub97c \ud2c0\uace0 \uc549\uc544 \uc0b0\uc758 \ud615\uc138\ub97c \uc0b4\ud3b4\ubd10\uc57c \ud55c\ub2e4. <BR>\uc88c\uccad\ub8e1\uc740 \ub3c4\uc194\ubd09(673.2m)\uc5d0\uc11c \ub0b4\ub824\uc624\uace0 \uc788\ub2e4. \uc6b0\ubc31\ud638\ub294 \uace0\uacc4\ubd09(638m) \uc904\uae30\ub2e4. \uc785\uc218(\u5165\u9996)\ub294 \ub450\ub95c\ubd09(629.3m)\uc5d0\uc11c \ube44\ub86f\ud588\ub294 \ubc14 \ubaa8\ub450 \ub300\ud765\uc0ac \uc55e \uacc4\uace1\uc5d0\uc11c \uba38\ub9ac\ub97c \ub9de\ub300\uace0\uc788\ub2e4. \uadf8 \uc785\uc218\uac00 \uc19f\uad6c\ucce4\ub2e4 \ub0b4\ub824\uc549\uc740 \uac00\ub791\uc774 \uc0ac\uc774\uc758 \uba85\ub2f9\uc5d0\ub294 \uc11c\uc0b0\ub300\uc0ac\uc758 \uc0ac\ub2f9 \ud45c\ucda9\uc0ac(\u8868\u5fe0\u7960)\uac00 \uc788\ub2e4. \ub300\uc0ac\uc758 \uc758\ubc1c\uc740 \uc624\ub300\uc0b0 \uc0c1\uc6d0\uc0ac \uac19\uc740 \uc704\uce58\uc5d0 \ubaa8\uc154\uc84c\uace0 \ub450\ub95c\uc0b0\uc740 \uadf8\ub9cc\ud55c \uc9c0\ub355(\u5730\u5fb7)\uc774 \uc788\uc5c8\ub358 \uac83\uc774\ub2e4. <BR>\uace0\uacc4\ubd09, \uc0c1\ubd09 \uac00\ub828\ubd09, \ub450\ub95c\ubd09, \uad6d\ub9bd\uc9c0\ub9ac\uc6d0 \uc9c0\ub3c4\uc5d0 \ub300\ub454\uc0b0\uc73c\ub85c \ud45c\uae30\ub41c \ub3c4\uc194\ubd09(673.5m), \uc5f0\ud654\ubd09(613m), \ud608\ub9dd\ubd09(379m), \ud5a5\ub85c\ubd09\uc758 7\ubd09\uc774 \uba85\ucc30 \ub300\ud765\uc0ac\ub97c \ub458\ub7ec\uc2f8\uace0 \uc788\ub294 \ub610\uc544\ub9ac \uc9c0\ud615\uc774\ub2e4. 400\ubbf8\ud130 \ub4f1\uace0\uc120\uc744 \uc774\uc5b4\ubcf4\uba74 \uac70\ub300\ud55c \ubc14\ub2f7\uac00\uc7ac\uac00 \uc5ec\uc758\uc8fc\ub97c \uba38\uae08\uace0\uc788\ub294 \ud615\uc138\ub2e4. \uc774\ub7f0 \uae30\uc774\ud568 \uc788\uc5c8\uae30\uc5d0 \ub450\ub95c\uc0b0\uc740 \ub300\ud765\uc0ac\ub97c \ud0a4\uc6b8 \uc218 \uc788\uc5c8\uace0 \uadf8 \ub355\uc5d0 \uba85\uc0b0\uc758 \ubc18\uc5f4\uc5d0 \uc624\ub97c \uc218 \uc788\uc5c8\ub2e4.'), STRINGDECODE('\ud55c\ubc18\ub3c4\uc758 \ucd5c\ub0a8\ub2e8 \ud574\ub0a8\ubc18\ub3c4\uc5d0 \uc19f\uc544 \uc788\ub294 \uc0b0\uc73c\ub85c\uc11c \uc655\ubc9a\ub098\ubb34\uc758 \uc790\uc0dd\uc9c0\uac00 \uc788\uc73c\uba70, \ub2e4\ub3c4\ud574\ub97c \uc870\ub9dd\ud558\uae30\uc5d0 \uc801\ud569\ud558\uace0 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1972\ub144)\ub41c \uc810 \ub4f1\uc744 \uac10\uc548\ud558\uc5ec \uc120\uc815, \ubd04\uc758 \ucd98\ubc31, \uc5ec\ub984\uc758 \ub179\uc74c, \uac00\uc744\uc758 \ub2e8\ud48d, \uaca8\uc6b8\uc758 \ub3d9\ubc31 \ub4f1\uc73c\ub85c \uc720\uba85\ud558\uba70 \uc720\uc790(\u67da\u5b50), \ucc28(\u8336)\uc758 \uc0b0\uc9c0\ub85c \uc54c\ub824\uc838 \uc788\uc74c. \ubcf4\ubb3c \uc81c320\ud638\uc778 \uc0bc\uce35\uc11d\ud0d1\uc744 \ube44\ub86f\ud558\uc5ec \ub9ce\uc740 \ubb38\ud654\uc7ac\ub97c \ubcf4\uc874\ud558\uace0 \uc788\ub294 \ub300\ud765\uc0ac(\u5927\u8208\u5bfa)\uac00 \uc788\uc74c'), STRINGDECODE('\uc0bc\uc7ac\uac00 \ub4e4\uc9c0 \uc54a\ub294 \ucc9c\ud558\uc758 \uc694\uc0c8'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub300\ub454\uc0ac \uc9d1\ub2e8\uc2dc\uc124\uc9c0\uad6c\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. <BR>\ub450\ub95c\uad00 \u260e061-535-5115, \uc6b0\ub9ac\uc0b0\uc7a5 \u260e061-534-9393, \ub300\ub454\uc0ac \ucf54\uc55e\uc758 \uc870\uc120\uae30\uc640\uc9d1 \uc720\uc120\uc5ec\uad00(\u260e061-534-2959)\ub3c4 \uc88b\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub450\ub95c\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e061-533-0088'), STRINGDECODE('\ub300\ub454\uc0ac\ud589 \uad70\ub0b4\ubc84\uc2a4\uac00 30\ubd84 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud558\ub294 \ud574\ub0a8\uc774 \uae30\uc810\uc774\ub2e4. \ud574\ub0a8\ud589 \uace0\uc18d\ubc84\uc2a4\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 40\ubd84 \uac04\uaca9, \uac15\ub0a8\ud130\ubbf8\ub110\uc5d0\uc11c\ub294 7\ud68c \uc788\ub2e4. \uc804\ub77c\ub0a8\ub3c4 \uc774\uc678 \uc9c0\uc5ed\uc73c\ub85c\ub294 \ubd80\uc0b0\uacfc \ub9c8\uc0b0\uc5d0\uc11c \ubc84\uc2a4\uac00 \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(29, TIMESTAMP '2019-08-14 15:46:34.914', TIMESTAMP '2019-08-14 15:46:56.683', '42', STRINGDECODE('<p>\ub450\ud0c0\uc0b0\uc740 \ubd80\ucc98\uac00 \ub204\uc6cc\uc788\ub294 \ud615\uc0c1\uc73c\ub85c \ubc15\ub2ec\ub839\uc744 \uc0ac\uc774\uc5d0 \ub450\uace0 \uccad\uc625\uc0b0\uacfc \ub9c8\uc8fc\ud558\uace0 \uc11c \uc788\ub2e4. \uc6b8\ucc3d\ud55c \uc218\ub9bc\uacfc \uae30\uc554\uc808\ubcbd\uc5d0 \ub178\uc1a1\uc774 \ubfcc\ub9ac\ub97c \ub0b4\ub824 \uc0b0\uc138\uac00 \uc218\ub824\ud55c \ub450\ud0c0\uc0b0\uc740 \uac15\uc6d0\ub3c4 \uad6d\ubbfc\uad00\uad11\uc9c0 1\ud638\ub85c \uc9c0\uc815\ub3fc \uc788\ub2e4. <br>\ub450\ud0c0\uc0b0\uc758 ''\ub450\ud0c0(\u982d\u9640)''\ub780 ''\uc18d\uc138\uc758 \ubc88\ub1cc\ub97c \ubc84\ub9ac\uace0 \ubd88\ub3c4\ub97c \ub2e6\ub294 \uc218\ud589''\uc744 \ub9d0\ud55c\ub2e4. \uc774\ub294 \ub450\ud0c0\uc0b0\uc774 \ubd88\uad50\uc640 \uc778\uc5f0\uc774 \uae4a\uc740 \ubd88\uad50\uc758 \ub3c4\ub7c9\uc784\uc744 \ubbf8\ub8e8\uc5b4 \uc9d0\uc791\ud560 \uc218 \uc788\ub2e4. \uc9c0\uae08\uc740 \uc0bc\ud654\uc0ac,\uad00\uc74c\uc554,\ucc9c\uc740\uc0ac\uac00 \ub0a8\uc544 \uc788\uc9c0\ub9cc 10\uc5ec\uac1c\uc758 \uc0ac\ucc30\uc774 \uc788\ub2e4\ub294 \uc61b\uae30\ub85d\uc73c\ub85c \ubcf4\uc544 \ubb34\ub989\uacc4\ub294 \ubd88\uad50\uac00 \ud06c\uac8c \ubc88\uc131\ud588\ub358 \ub450\ud0c0\uc758 \ub3c4\ub7c9\uc774\uc5c8\ub358 \uac19\ub2e4.</p>'), STRINGDECODE('<p>\u2460 \uc911\ubd09\ubd84\uad50-\ud770\uc801\uace8-\uc808\uace8-\ub9dd\uad70\ub300-\uccad\uc625\uc0b0-\uc9c1\uad00\uc554\ud130-\ud5c8\uacf5\ub2e4\ub9ac-\uc0bc\ud654\uc0ac (\ucd1d 8\uc2dc\uac04 10\ubd84)<br>\u2461 \ub0b4\ubbf8\ub85c\ub9ac-\ucc9c\uc740\uc0ac-\uc270\uc6c0\uc0b0-\ub450\ud0c0\uc0b0-\ubc31\ub450\ub300\uac04-\ud1b5\uace8\ubaa9\uc774-\uba85\uc8fc\ubaa9\uc774-\ub313\uc7ac (\ucd1d 6\uc2dc\uac04 40\ubd84)<br>\u2462 \uc0bc\ud654\uc0ac-\ud5c8\uacf5\ub2e4\ub9ac-\uc9c1\uad00\uc554\ud130-\uccad\uc625\uc0b0-\ubb38\ubc14\uc704-\ubb38\ubc14\uc704\uace8-\uace0\ub0b4-\ubc88\ucc9c\ub9ac (\ucd1d 8\uc2dc\uac04 15\ubd84)</p>'), '37.491976', '129.016909', STRINGDECODE('\uac15\uc6d0\ub3c4 \ub3d9\ud574\uc2dc \uc0bc\ud654\ub3d9, \uc0bc\ucc99\uc2dc \ubbf8\ub85c\uba74\u318d\ud558\uc7a5\uba74'), 2204151100, 1357, STRINGDECODE('\ub450\ud0c0\uc0b0'), STRINGDECODE('<p>&lt;\ubb34\ub989\uacc4\ub97c \ub9cc\ub4e4\uc5b4\ub193\uc740 \ubc14\uc704\uc0b0\uacfc \ud478\ub978\uc0b0&gt;<br>\uacc4\uace1 \uc774\ub984\uc744 \ubb34\ub989\uacc4(\u6b66\u9675\u6eaa)\ub77c\uace0 \ud588\ub2e4. \uc5bc\ub9c8\ub098 \ub300\ub2e8\ud55c \uacbd\uce58\uba74 \uac10\ud788 \u201c\ubb34\ub989\ub3c4\uc6d0\uc740 \ubc14\ub85c \uc5ec\uae30\u201d\ub77c\uace0 \ub0b4\uc138\uc6e0\ub2e4. \ub450\ud0c0\uc0b0\uacfc \uccad\uc625\uc0b0, \uace0\uc801\ub300, \uac08\ubbf8\ubd09, 1243\ubd09\uc73c\ub85c \ub458\ub7ec\uc2f8\uc778 \ubc18\uc11d\uacc4\uace1\uc774\ub2e4. <br>\uac00\ubcf4\uba74 \uace0\uac1c\ub97c \ub044\ub355\uc774\uac8c\ub420 \uac83\uc774\ub2e4. \ubcbc\ub8e8\ucc98\ub7fc \ub9e4\ub044\ub7ec\uc6b4 \ubc14\uc704\uc6b4\ub3d9\uc7a5\uc5d0 \ubb34\uc218\ud55c \uae00\uc528\uac00 \uc0c8\uaca8\uc838\uc788\uace0 \ud55c\ucf20\uc73c\ub85c\ub294 \ub9d1\uc740 \ubb3c\uc774 \ub80c\uc988\ucc98\ub7fc \uc587\uac8c \ud750\ub978\ub2e4. \uace8\uc9dc\uae30\ub85c \ub4e4\uc5b4\uac00\uba74 \uc5ec\uae30\ub3c4 \ud3ed\ud3ec \uc800\uae30\ub3c4 \ud3ed\ud3ec, \uae4e\uc544\uc138\uc6b4 \uc808\ubcbd \uc704\uc5d4 \uce35\uce35\uc774 \uc18c\ub098\ubb34\ub4e4\u2026 \ub0b4\uac00 \uc815\ub9d0 \ubb34\ub989\ub3c4\uc6d0\uc5d0 \ub4e4\uc5b4\uc654\uad6c\ub098 \uc2f6\uc73c\ub9ac\ub77c. <br>&lt;\ud615 \uc9c0\ud615\uc778 \ubb34\ub989\uacc4 \ubb3c\ubc1b\uc774\uc758 \ub0a8\ucabd \uc6b8\ud0c0\ub9ac\ub97c \uc774\ub8e8\ub294 \uccad\uc625\u00b7\ub450\ud0c0\uc0b0\uc740 \uc5b8\uc81c\ub098 \uc30d\ub465\uc774\ucc98\ub7fc \ubd99\uc5b4\ub2e4\ub2cc\ub2e4. \ub5c4 \uc218 \uc5c6\uc774 \ub3c8\ub3c5\ud558\uace0 \uac00\uae4c\uc6b4 \ub3d9\uc2dc\uc5d0 \ubb34\ub989\uacc4\ub77c\ub294 \ubcf4\ubb3c\uc744 \uacf5\uc720\ud558\uace0 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc0dd\uae40\uc0c8\ub294 \uc0ac\ubb47 \ub2ec\ub77c \uccad\uc625\uc740 \ub4ec\uc9c1\ud55c \uc721\uc0b0(\u8089\u5c71)\uc778 \ubc18\uba74 \ub450\ud0c0\ub294 \uc6b8\ub048\ubd88\ub048 \uace8\uc0b0(\u9aa8\u5c71)\uc73c\ub85c \ub418\uc5b4\uc788\ub2e4. <br>\ubb38\uc790\uc18d\uc774 \uc788\ub294 \uc774\ub294 \uc30d\ub465\uc774 \uc774\ub984\uc774 \ub9de\ubc14\ub00c\uc5c8\ub098 \uc758\uc2ec\ud560 \uac83\uc774\ub2e4. \ud478\ub978 \uc625 \uccad\uc625\uc740 \ubc14\uc704\uc0b0\uc5d0 \ubd99\uc5ec\uc57c \ub9de\uc73c\uba70 \uc9d1\ucc29\uc744 \ubc84\ub9ac\uace0 \uc218\ud589\uc758 \uae38\ub85c \ub4e4\uc5b4\uc120\ub2e4\ub294 \ub450\ud0c0\ub294 \uc800 \uc694\ub780\ud55c \ubb34\ub989\uacc4\uace1\uc744 \uc9c0\uae0b\uc774 \uad7d\uc5b4\ubcf4\uace0\uc788\ub294 \ud6c4\ub355\ud55c \ubd09\uc6b0\ub9ac\uc5d0 \uc5b4\uc6b8\ub9ac\ub294\ub370\u2026. <br>\ubaa8\uc21c\uc5c6\ub294 \ub17c\ub9ac\ub294 \uc9c4\uc2e4\uc774 \ub4b7\ubc1b\uce68\ud55c\ub2e4. \uc0b0\uacbd\ud45c\uc640 \ub300\ub3d9\uc5ec\uc9c0\ub3c4\uc5d0\ub294 \ubd84\uba85 \ubd81\ucabd\uac83\uc774 \ub450\ud0c0\ub77c\uace0 \ub098\uc640\uc788\ub2e4. \uccad\uc625\uc740 \ub450\ud0c0\uace0 \ub450\ud0c0\ub294 \uccad\uc625\uc778 \uac83\uc774\ub2e4.</p>'), STRINGDECODE('<p>\ubb34\ub989\uacc4\uace1 \ub4f1 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b4 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0bc\ud654\uc0ac(\u4e09\u548c\u5bfa), \uad00\uc74c\uc554(\u89c0\u97f3\u5eb5), \ub450\ud0c0\uc0b0\uc131(\u982d\u9640\u5c71\u57ce)\uc774 \uc788\uc74c. \ubc14\uc704\uc5d0 50\uc5ec\uac1c\uc758 \ud06c\uace0 \uc791\uc740 \uad6c\uba4d\uc774 \ud328\uc5ec \uc0b0\uc774\ub984\uc774 \ubd99\uc5ec\uc84c\uc73c\uba70, \uc608\ub85c\ubd80\ud130 \uae30\uc6b0\uc81c\ub97c \uc9c0\ub0b4\ub294 \ub4f1 \ud1a0\uc18d\uc2e0\uc559\uc758 \uae30\ub3c4\ucc98\uc778 \uc270\uc6c0\uc0b0(\u4e94\u5341\u4e95\u5c71)\uc774 \uc720\uba85</p>'), STRINGDECODE('\ubc88\ub1cc\ub97c \uc7a0 \uc7ac\uc6b0\ub294 \ubb34\ub989\ub3c4\uc6d0'), STRINGDECODE('<p>\u25cf\uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<br>\ubb34\ub989\uacc4\uace1 \uc0bc\ud654\uc0ac \uc785\uad6c\uc5d0 \uc2dd\ub2f9\uacfc \ubbfc\ubc15\uc9d1\uc774 \uc990\ube44\ud558\ub2e4. <br>\uccad\uc625\ubaa8\ud154 \u260e033-534-8866<br>\ubb34\ub989\ud504\ub77c\uc790\ud638\ud154 \u260e033-534-8855<br>\ubc18\uc11d\uc0c1\ud68c \u260e033-534-8082<br>\uc601\uc9c4\ud68c\uad00 \u260e033-534-9116 \ub4f1<br>\ubbfc\ubc15 \ubb38\uc758\ub294 \ub3d9\ud574\ub18d\ud611 \uc9c0\ub3c4\uacfc \u260e033-521-5547<br><br>\u25cf \uc774\uc6a9\ubb38\uc758<br>\ubb34\ub989\uacc4\uace1 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e033-530-2457</p>'), STRINGDECODE('<p>\uae30\uc810\uc740 \ub3d9\ud574\ub2e4. <br>\ub3d9\ud574\ub85c\ub294 \uc11c\uc6b8 \uac15\ub0a8\ud130\ubbf8\ub110\uc5d0\uc11c \uace0\uc18d\ubc84\uc2a4\uac00 \uc0ac\uc624\uc2ed\ubd84 \uac04\uaca9\uc73c\ub85c \uc788\ub2e4. \ubd80\uc0b0, \ub300\uad6c, \uc6b8\uc0b0, \ud3ec\ud56d, \uacbd\uc8fc, \ucd98\ucc9c, \ud0dc\ubc31\uacfc \uad00\ub3d9\uc9c0\ubc29 \ub3c4\uc2dc\ub4e4\uc5d0\uc11c\ub3c4 \uc9c1\ud589\ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4. <br>- \ub3d9\ud574\u2192\ubb34\ub989\uacc4\uace1: \uc2dc\ub0b4\ubc84\uc2a4 20\ubd84 \uac04\uaca9 \uc6b4\ud589</p>'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(30, TIMESTAMP '2019-08-14 15:46:34.914', TIMESTAMP '2019-08-14 15:46:57.273', '28', STRINGDECODE('\uc6b0\ub9ac\ub098\ub77c\uc5d0\uc11c&nbsp;\ub2e4\uc12f\ubc88\uc9f8\ub85c \ud070 \uc12c\uc778 \uac15\ud654\ub3c4\uc5d0\ub294 \uace0\ub824\uc0b0, \ud608\uad6c\uc0b0, \uc9c4\uac15\uc0b0, \ub9c8\ub2c8\uc0b0 \ub4f1 400m \uc774\uc0c1\uc758 4\uac1c\uc0b0\uc774 \ub0a8\ubd81\uc73c\ub85c \uc77c\uc9c1\uc120\uc0c1\uc5d0 \uc19f\uc544 \uc788\ub294\ub370 \uadf8 \uc911 \uc81c\uc77c \ub192\uc740 \uc0b0\uc774 \ub9c8\ub2c8\uc0b0\uc774\ub2e4. \u3008\uace0\ub824\uc0ac\u3009\u3008\uc138\uc885\uc2e4\ub85d\uc9c0\ub9ac\uc9c0\u3009\u3008\ud0dc\uc885\uc2e4\ub85d\u3009 \ub4f1 \uc870\uc120 \ucd08\uae30\uc5d0 \ubc1c\uac04\ub41c \ubb38\ud5cc\uc5d0\ub294 \uba38\ub9ac\uc0b0, \uc6b0\ub450\uba38\ub9ac\uc0b0\uc774\ub780 \ub73b\uc758 \ub9c8\ub9ac\uc0b0(\u6469\u5229\u5c71) \ub610\ub294 \ub450\uc545(\u982d\u5dbd)\uc73c\ub85c \uc4f0\uc5ec \uc788\ub2e4. \uadf8\ub798\uc11c \uc9c0\uae08\ub3c4 \ub9c8\ub9ac\uc0b0\uc774\ub77c\ub294 \uc774\ub984\uc744 \ud63c\uc6a9\ud574\uc11c \uc0ac\uc6a9\ud558\uace0 \uc788\uc9c0\ub9cc, \uc624\ub7ab \ub3d9\uc548 \ub9c8\ub2c8\uc0b0\uc774\ub77c \ubd88\ub7ec\uc654\ub294\ub370 \uc0c8\uc0bc \ub9c8\ub9ac\uc0b0\uc774\ub77c\uace0 \ud558\uc5ec \ud63c\ub3d9\uc744 \uc904 \ud544\uc694\ub294 \uc5c6\uc744 \uac83\uac19\ub2e4. <BR>\ub9c8\ub2c8\uc0b0\uc740 \ube44\uad50\uc801 \ub0ae\uace0 \uc218\ub3c4\uad8c\uc5d0 \uac00\uae4c\uc6b4 \uac70\ub9ac\uc5d0 \uc788\uc5b4 \uce5c\uc815\uc744 \ucc3e\ub294 \uae30\ubd84\uc73c\ub85c \ud3b8\ud558\uac8c \ucc3e\uc744 \uc218 \uc788\ub2e4. \ud574\ubc1c\uc774 \ub0ae\ub354\ub77c\ub3c4 \uc8fc\ub2a5\uc120\uc774 \uc554\ub989\uc73c\ub85c \ub418\uc5b4 \uc788\uc73c\ub2c8 \ub4f1\uc0b0\uc758 \ubb18\ubbf8\ub3c4 \ud55c\uaecf \ub9cc\ub07d\ud560 \uc218 \uc788\ub294 \uc0b0\uc774\ub2e4. <BR>\ub3d9\ub0a8\uc73c\ub85c \uac00\ub290\ub2e4\ub797\uac8c \ubed7\uc740 \ub2a5\uc120\uc744 \ub530\ub77c \uc624\ub974\ub2e4 \ubcf4\uba74 \ub9dd\ub9dd\ud55c \uc11c\ud574\ub97c \uc870\ub9dd\ud560 \uc218 \uc788\ub2e4. \uc870\uad6d\uc21c\ub840 \uc548\ub0b4\ud310\uc774 \uc788\ub294 ''\uac1c\ubbf8\ud5c8\ub9ac''\uc5d0\uc11c 98\uac1c\uc758 \uacc4\ub2e8\uae38\uc744 \uc62c\ub77c\uac00\uba74 \uc0ac\uc801 \uc81c 136\ud638\uc778 ''\ucc38\uc131\ub2e8''(\u5879\u661f\u58c7)\uc744 \ub9cc\ub0a0 \uc218 \uc788\ub2e4. \uc774\uacf3\uc5d0\uc11c \ub9e4\ub144 \uac1c\ucc9c\uc808\uacfc \uc804\uad6d\uccb4\uc804\ub54c\ub9c8\ub2e4 \uc131\ud654\uac00 \ucc44\ud654\ub41c\ub2e4. \ucc38\uc131\ub2e8\uc740 \ub2e8\uad70 \uc655\uac80 \uc7ac\uc704 51\ub144(BC2283\ub144)\uc5d0 \uc6b4\uc0ac(\u96f2\u5e2b)\uc778 \ubc30\ub2ec\uc2e0(\u500d\u9054\u81e3)\uc774 \ub9c8\ub9ac\uc0b0\uc5d0 \uc313\uc740 \uc81c\ub2e8\uc73c\ub85c, \u3008\uc0bc\uad6d\uc0ac\uae30\u3009\uc5d0 \uc758\ud558\uba74,\uace0\uad6c\ub824.\uc2e0\ub77c.\ubc31\uc81c\uc758 \uc5ec\ub7ec \uc655\ub4e4\uc774 \uc774 \uacf3\uc5d0\uc11c \ud558\ub298\uc5d0 \uc81c\uc0ac\ub97c \uc9c0\ub0c8\ub2e4. \uace0\ub824 \uacf5\ubbfc\uc655 \ub54c\uc640 \uc870\uc120 \uc778\uc870.\uc219\uc885 \ub54c \uac01\uac01 \ubcf4\uc218\ub418\uc5b4 \ud604\uc7ac \uc774\ub978\ub2e4. \uc774 \ub2e8\uc740 \ud654\uac15\uc11d\uc744 \uc313\uc544\uc62c\ub824 \ub9cc\ub4e0 \uac83\uc73c\ub85c, \ubc11\ubd80\ubd84\uc740 \ub465\uae00\uace0 \uc717\ubd80\ubd84\uc740 \uc0ac\uac01\ud615\uc774\uba70, \ub192\uc774\uac00 \ucd1d6m\uc5d0 \ub2ec\ud55c\ub2e4. \ucc38\uc131\ub2e8 \uc704\uc5d0 \uc624\ub974\uba74, \ub3d9\ucabd\uc73c\ub85c \uc815\uc0c1\uc774 \uc190\uc5d0 \uc7a1\ud790 \ub4ef \uac00\uae5d\uac8c \ubcf4\uc774\uace0, \ub0a8\ucabd \uc544\ub798\ub85c\ub294 \ud478\ub978 \ubb3c\uacb0\uc774 \ub118\uc2e4\uac70\ub9ac\ub294 \ub113\uc740 \ubc14\ub2e4\uac00 \ubc1c\uc544\ub798\ub85c \ud3bc\uccd0\uc9c0\uace0, \ub3d9\ub0a8\ucabd \uba40\ub9ac \uc778\ucc9c\uc2dc\uac00\uc9c0\uac00 \uc544\ub4dd\ud788 \ubcf4\uc778\ub2e4. <BR>\uc815\uc0c1 \uc11c\ucabd \uc0b0\uae30\uc2ad\uc5d0\ub294 \uc2e0\ub77c \uc120\ub355\uc5ec\uc655 8\ub144(639\ub144)\uc5d0 \ucc3d\uac74\ud588\ub2e4\ub294 \uc815\uc218\uc0ac\uc640 \ud568\ud5c8\ub300\uc0ac(\u6db5\u865b\u5927\u5e2b)\uac00 \uc218\ub3c4\ud558\uc600\ub2e4\ub294 \ud568\ud5c8\ub3d9\ucc9c\uc774 \uc790\ub9ac\uc7a1\uace0 \uc788\ub2e4. \ud568\ud5c8\ub3d9\ucc9c\uc5d0\ub294 1\ubc31\uc5ecm\uc758 \uc554\ubc18\uc704\ub85c \ubb3c\uc774 \ud750\ub974\uace0, \uc554\ubc18\uc5d0\ub294 \ud568\ud5c8\ub300\uc0ac\uac00 \uc0c8\uacbc\ub2e4\ub294 ''\u6db5\u865b\u6d1e\u5929''(\ud568\ud5c8\ub3d9\ucc9c)\uc774\ub780 \uae00\uc790\uac00 \uc74c\uac01 \ub418\uc5b4 \uc788\ub2e4. \uc678\uce68\uc744 \uc790\uc8fc \ubc1b\uc558\ub358 \uace0\ub824\uac00 \ubd80\ucc98\uc758 \ud798\uc73c\ub85c \ub098\ub77c\ub97c \uc9c0\ucf1c \ubcf4\ub824\uace0 \uac15\ud654\ub3c4\uc5d0\uc11c \ud06c\uac8c \ubd88\uc0ac\ub97c \ud3bc\ucce4\ub358 \uae4c\ub2ed\uc778\uc9c0 \uac15\ud654\ub3c4\uc5d0\ub294 \ub0b4\ub825 \uc788\ub294 \uc808\uc774 \ub9ce\ub2e4.'), STRINGDECODE('\u2460 \uc815\uc218\uc0ac-\uc815\uc0c1-\ucc38\uc131\ub2e8-\uacc4\ub2e8\uae38-\uad6d\ubbfc\uad00\uad11\ub2e8\uc9c0(\ucd1d 2\uc2dc\uac04 15\ubd84)<BR>\u2461 \ud568\ud5c8\ub3d9\ucc9c-\ub2a5\uc120\uac08\ub9bc\uae38-\uc815\uc0c1-\ub2a5\uc120\uac08\ub9bc\uae38-\uc815\uc218\uc0ac(\ucd1d 1\uc2dc\uac04 40\ubd84)<BR>\u2462 \uc9c4\uac1c\ub9c8\uc744-314\ubd09-\uc815\uc0c1-\ucc38\uc131\ub2e8-315\ubd09-\uc791\uc740\ubafc\ub118\uc774-\uc0c1\ubd09-\uc120\uc218\uc57d\uc218(\ucd1d 6\uc2dc\uac04)'), '37.619143', '126.424779', STRINGDECODE('\uc778\ucc9c\uad11\uc5ed\uc2dc \uac15\ud654\uad70 \ud654\ub3c4\uba74'), 1401990600, 472, STRINGDECODE('\ub9c8\ub2c8\uc0b0'), STRINGDECODE('&lt;\uc554\uace8\ubbf8 \ub6f0\uc5b4\ub09c \ucd5c\uace0 \uc804\ub9dd\ub300&gt;<BR>\uacbd\uae30\ub3c4 \ucd5c\ub300\uc758 \uc12c \uac15\ud654\ub3c4\uc5d0\uc11c \ucd5c\uace0 \ub192\uc740 \uc0b0\uc774\ub2e4. \ub2e8\uad70\uc655\uac80\uc774 \uc138 \ubd09\uc6b0\ub9ac \uc911 \uac00\uc6b4\ub370\uac83\uc5d0 \ucc38\uc131\ub2e8(\uc0ac\uc801 136\ud638)\uc744 \uc138\uc6b0\uace0 \ud558\ub298\uc5d0 \uc81c\uc0ac\ub97c \uc9c0\ub0b4\uba70 \uace0\uc870\uc120\uc758 \uc548\ub155\uc744 \uae30\uc6d0\ud588\ub2e4\ub294 \uc0b0\uc73c\ub85c \uba87 \ud574 \uc804 \ud48d\uc218\ub4e4\uc774 \ub9c8\ub2c8\uc0b0\uc744 \uac00\uc7a5 \uae30\uac00 \uc13c \uacf3\uc73c\ub85c \uc9c0\ubaa9\ud558\uae30\ub3c4 \ud588\ub2e4. \uc2e0\uc99d\ub3d9\uad6d\uc5ec\uc9c0\uc2b9\ub78c\uc5d0 \uc788\ub294 \uc804\uc124\uc778\ub370, \uc774 \uc0b0\uc774 \uc4f0\uae30\ub85c\ub294 \ub9c8\ub2c8\uc0b0\uc774\uc9c0\ub9cc \ubd80\ub974\uae30\ub85c\ub294 \ub9c8\ub9ac\uc0b0\uc778 \ub370 \uadfc\uac70\ud588\uc744 \uac83\uc774\ub2e4. 1977\ub144\uc5d0 \uad6d\ubbfc\uad00\uad11\uc9c0\ub85c \uc9c0\uc815\ub418\uc5c8\uc73c\uba70, \ucc38\uc131\ub2e8\uc758 \uc720\uba85\uc138\ub85c \ub9ce\uc740 \uc774\ub4e4\uc774 \uc624\ub798 \uc804\ubd80\ud130 \uc990\uaca8 \ucc3e\ub294 \uba85\uc2b9\uc9c0\ub2e4. <BR>\uc554\uace8\ubbf8\uac00 \ub6f0\uc5b4\ub09c \ub370\ub2e4 \uc774\uc911\ud658\uc758 \uba85\uc0b0 \uc81c2\uc870\uac74 \"\"\ud070 \ubc14\ub2e4\ub97c \ub07c\uace0\uc788\uc744 \uac83\"\"\uc744 \uac16\ucdb0 \uc804\ub9dd\uc744 \ub530\ub97c \ub370\uac00 \uc5c6\ub2e4. \uc5fc\ud558(\u9e7d\u6cb3)\ub098 \uc11d\ubaa8\ub3c4\uc640\uc758 \uc0ac\uc774 \uac1c(\u6d66)\ucc98\ub7fc \uac15 \uac19\uc740 \ubc14\ub2e4, \uc7a5\ubd09\ub3c4\uc640 \uc0ac\uc774\uc758 \ud638\uc218 \uac19\uc740 \ubc14\ub2e4, \uc11c\ucabd\uc758 \ub9dd\ub9dd\ub300\ud574\uac00 \uac00\uc2b4\uc744 \ud0c1 \ud2b8\uc774\uac8c \ud55c\ub2e4. <BR>\uc2e0\ub77c \uc120\ub355\uc5ec\uc655 8\ub144(639\ub144)\uc5d0 \ud76c\uc815\uc120\uc0ac\uac00 \ucc3d\uac74\ud55c \uc815\uc218\uc0ac \ub4f1\uc774 \uc0b0\uc790\ub77d\uc5d0 \uc788\uc5b4 \ubb38\ud654\uc720\uc801 \ub2f5\uc0ac\uc9c0\ub85c\ub3c4 \uc88b\uc73c\uba70 \ud574\uc548\uc758 \uc9c4, \ubcf4, \ub3c8\ub300 \ub4f1 \ud638\uad6d\ubb38\ud654\uc7ac\ub97c \ub458\ub7ec\ubcfc \uc218\ub3c4 \uc788\ub2e4.'), STRINGDECODE('\ub2e8\uad70\uc2dc\uc870\uc758 \uc804\uc124\uc774 \uac04\uc9c1\ub41c \uc0b0\uc73c\ub85c \uc5ed\uc0ac,\ubb38\ud654\uc801 \uac00\uce58 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0ac\uc801 \uc81c136\ud638\uc778 \ucc38\uc131\ub2e8(\u5879\u661f\u58c7), \ud568\ud5c8\ub3d9\ucc9c, \uc0ac\uc801 \uc81c130\ud638\uc778 \uc0bc\ub791\uc0b0\uc131\uc774 \uc788\uc74c. \ub610\ud55c \ub9ce\uc740 \ubcf4\ubb3c\uc744 \ubcf4\uc874\ud558\uace0 \uc788\ub294 \uc815\uc218\uc0ac(\u6de8\u6c34\u5bfa) \ubc0f \uc804\ub4f1\uc0ac(\u50b3\u71c8\u5bfa)\ub4f1\uc774 \uc788\uc73c\uba70, \uc131\ud654\ub97c \ucc44\ud654\ud558\ub294 \uc7a5\uc18c\uc774\uae30\ub3c4 \ud568'), STRINGDECODE('\uc5ed\ub300 \uc655\ub4e4\uc774 \ud558\ub298\uc81c\ub97c \uc62c\ub9ac\ub358 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc0c1\ubc29\ub9ac \uad00\uad11\ub2e8\uc9c0\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. \ud30c\uc778\ud790\uc5ec\uad00 \u260e032-937-9317 \ub9c8\ub2c8\uc0b0\uc7a5\uc5ec\uad00 \u260e032-937-3123, <BR>\uc0ac\uae30\ub9ac\ucabd\uc740 \uce74\ub9ac\ube0c \u260e032-937-3042 \uc815\uc218\uc7a5\uc5ec\uad00 \u260e032-937-5732 \ud568\ud5c8\ub3d9\ucc9c\uc57c\uc601\uc7a5 \u260e032-937-4797, <BR>\ub3d9\ub9c9\ub9ac\ub294 \uadf8\ub9b0\ud30c\ud06c\uc5ec\uad00 \u260e032-937-8845<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub9c8\ub2c8\uc0b0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e032-937-1624'), STRINGDECODE('\uae30\uc810\uc740 \uac15\ud654\uc74d\uc774\ub2e4. \uc74d\ub0b4 \ud130\ubbf8\ub110\uc5d0\uc11c \uc0c1\ubc29\ub9ac\ud589(\uad6d\ubbfc\uad00\uad11\ub2e8\uc9c0)\uc774\ub098 \uc0ac\uae30\ub9ac(\ud568\ud5c8\ub3d9\ucc9c)\ud589 \ubc84\uc2a4\ub97c \uc774\uc6a9\ud558\uba74 \ub41c\ub2e4. \uac15\ud654\ud589 \ubc84\uc2a4\ub294 \uc11c\uc6b8 \uc2e0\ucd0c\uc5d0\uc11c 10\ubd84, \uc601\ub4f1\ud3ec\uc5d0\uc11c\ub294 15\ubd84, \uc778\ucc9c\uc5d0\uc11c\ub294 20\ubd84 \uac04\uaca9\uc73c\ub85c \uc788\ub2e4. \ubd80\ud3c9\uacfc \uc548\uc591\uc5d0\uc11c\ub3c4 \ub2e4\ub2cc\ub2e4. <BR>&gt; \uac15\ud654\uc74d\u2192\uc0c1\ubc29\ub9ac : 30~40\ubd84 \uac04\uaca9\uc73c\ub85c 06:00~21:30\uae4c\uc9c0 \uc6b4\ud589<BR>&gt; \uc0ac\uae30\ub9ac\u2192\uac15\ud654\uc74d : 06:00~20:25\uae4c\uc9c0 1\uc77c 7\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(31, TIMESTAMP '2019-08-14 15:46:34.915', TIMESTAMP '2019-08-14 15:46:57.878', '45', STRINGDECODE('\ub9c8\uc774\uc0b0\uc740 \uc61b\ub0a0 \uc6a9\ucd9c\uc0b0, \ub3d9\uae08\uc0b0 \ub4f1\uc73c\ub85c \ubd88\ub824 \uc624\ub2e4\uac00 \uc774\uc870\ub54c \ud0dc\uc885\uc774 \ub9d0 \uadc0\ub97c \ub2ee\uc558\ub2e4\uace0 \ud55c \ub4a4\ubd80\ud130 \ub9c8\uc774\uc0b0\uc774\ub77c \uce6d\ud558\uac8c \ub410\ub2e4. \uc7ac\ubbf8\uc788\ub294 \uac83\uc740 \ub9c8\uc774\uc0b0\uc740 \ubc14\ub77c\ubcf4\ub294 \ubc29\ud5a5\uc5d0 \ub530\ub77c \uadf8 \ubaa8\uc2b5\uc774 \ub2ec\ub77c \ubcf4\uc778\ub2e4\ub294 \uac83\uc774\ub2e4. <BR>\uadf8\ub798\uc11c \ub9c8\uc774\uc0b0\uc758 \uae30\uc774\ud55c \ubaa8\uc2b5\uacfc \ud2b9\uc774\ud55c \uacbd\uad00 \ub54c\ubb38\uc5d0 \uc5ec\ub7ec \uac00\uc9c0 \uc774\ub984\uc744 \uac16\uace0 \uc788\ub2e4. \ubd93\ub05d\uac19\ub2e4\ud574\uc11c \ubb38\ud544\ubd09, \ubc14\uc704\uac00 \ub9ce\uc544\uc11c \uac1c\uace8\uc0b0, \ubc29\ud5a5\uc744 \ub2ec\ub9ac\ud574 \ubcf4\uba74 \ub3db\ub300\ub97c \ub2ee\uc544 \ub3db\ub300\ubd09, \uc6a9\uc758 \ubfd4 \uac19\ub2e4\ud574\uc11c \uc6a9\uac01\ubd09 \ub4f1\uc73c\ub85c \ubd88\ub824\uc9c4\ub2e4. <BR>\ub9c8\uc774\uc0b0\uc740 \ub3d9\uc11c\ub85c \ud070 \uc554\uc218\ubd09\uc6b0\ub9ac \ub450\uac1c\uac00 \uc788\ub2e4. \ub3d9\ud3b8 \uc22b\ubd09\uc6b0\ub9ac\ub294 \uac70\ub300\ud55c \ub0a8\uc131\uc744 \ub2ee\uc558\ub2e4 \ud558\uc5ec \uc11c\ub2e4\uc0b0(\u897f\u591a\u5c71)\uc774\ub77c\ub294 \uc61b \uc774\ub984\ub3c4 \uc9c0\ub2c8\uace0 \uc788\ub294\ub370, \uac15\ud55c \uc591\uae30\ub97c \ud488\uace0 \uc788\uc5b4 \uc0b0\uae38\uc774 \ud5d8\uc900\ud558\uc5ec \ub4f1\uc0b0\uc744 \ud560 \uc218\uac00 \uc5c6\ub2e4. \uc11c\ud3b8\uc5d0 \uc788\ub294 \uc554\ub9c8\uc774\ubd09\uc740 \ub098\uae0b\ub098\uae0b\ud558\uac8c \uc190\uc9d3\ud558\ub294 \uc5ec\uc131\ucc98\ub7fc \ub9ce\uc740 \ub4f1\uc0b0\uac1d\uc744 \ub9de\uc774\ud558\uace0 \uc788\ub2e4. \ub9c8\uc774\uc0b0\uc5d0\uc11c \ubd81\ucabd\uc73c\ub85c \ud750\ub974\ub294 \ubb3c\uc740 \uae08\uac15 \ubb3c\uc904\uae30\ub97c \uc774\ub8e8\uace0, \ub0a8\ucabd\uc73c\ub85c \ud750\ub974\ub294 \ubb3c\uc904\uae30\ub294 \uc12c\uc9c4\uac15\uc73c\ub85c \ud750\ub978\ub2e4. \uadf8 \ud750\ub984\uc774 \ubc18\uc6d0\uc744 \uadf8\ub9ac\uace0 \uc788\uc5b4 \ub9c8\uc774\uc0b0\uc740 \uae08\uac15\uacfc \uc12c\uc9c4\uac15\uc758 \ubd84\uc218\ub839\uc774 \ub418\uace0 \uc788\uc73c\uba70 \ub450 \uac15\uc758 \ubb3c\uc904\uae30\ub294 \ub9c8\uc774\uc0b0\uc744 \uc911\uc2ec\uc73c\ub85c \ud0dc\uadf9\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4. <BR>\ub9c8\uc774\uc0b0\uc740 \uc870\uc120\uc744 \uac1c\uad6d\ud55c \uc774\uc131\uacc4\uc640 \uc778\uc5f0\uc774 \uae4a\uc740 \uc0b0\uc774\ub2e4. \uc774\uc131\uacc4\ub294 \ub9c8\uc774\uc0b0\uc744 \uc18d\uae08\uc0b0(\u675f\u91d1\u5c71)\uc774\ub77c \ubd88\ub800\ub2e4. \uc77c\ucc0d\uc774 \ud070 \uafc8\uc744 \ud488\uc5c8\ub358 \uc774\uc131\uacc4\ub294 \uae08\uc0b0 \ub4f1 \uc804\uad6d \uac01\uc9c0\uc758 \uba85\uc0b0\uc744 \ucc3e\uc544 \uae30\ub3c4\ub97c \uc62c\ub838\ub294\ub370, \uae30\ub3c4\uac00 \ub05d\ub09c \uc5b4\ub290 \ub0a0 \ubc24 \uafc8\uc5d0 \uc2e0\uc774 \ub098\ud0c0\ub098 \uae08\ucc99(\ubaa8\ub4e0 \uc81c\ub3c4\uc758 \ud45c\uc900\uc784\uc744 \ub9d0\ud558\uba70\ub97c \ub9c8\uc74c\ub300\ub85c \ud5e4\uc544\ub9ac\ub3c4\ub85d \ud558\ub77c\"\" \ub77c\uace0 \ud588\ub2e4 \ud55c\ub2e4. \uadf8 \ub4a4 \uace0\ub824 \uc6b0\uc655 6\ub144(1380\ub144) \uace0\ub824\uc758 \uc7a5\uad70 \uc774\uc131\uacc4\ub294 \uc804\ub77c\ub3c4 \uc6b4\ubd09\ub545 \ud669\uc0b0\uc5d0\uc11c \uc65c\uad6c\ub97c \ubb34\ucc0c\ub974\uace0 \uac1c\uc120 \uae38\uc5d0 \ub9c8\uc774\uc0b0\uc744 \ubcf4\uace0 \ub180\ub790\ub2e4. \uafc8\uc5d0 \uc2e0\uc5d0\uac8c\uc11c \uae08\ucc99\uc744 \ubc1b\uc740 \uc7a5\uc18c\uac00 \ubc14\ub85c \ub9c8\uc774\uc0b0\uc774\uae30 \ub54c\ubb38\uc774\uc5c8\ub2e4.'), STRINGDECODE('\u2460 \ubd81\ubd80\uc8fc\ucc28\uc7a5-\ucc9c\ud669\ubb38-\uc740\uc218\uc0ac-\ud0d1\uc0ac-\ud0d1\uc601\uc81c-\uae08\ub2f9\uc0ac-\ub0a8\ubd80\uc8fc\ucc28\uc7a5(\ucd1d 2\uc2dc\uac04)<BR>\u2461 \ubd81\ubd80\uc8fc\ucc28\uc7a5-\ucc9c\ud669\ubb38-\uc740\uc218\uc0ac-\ud0d1\uc0ac-\ucc9c\ud669\ubb38-\ubd81\ubd80\uc8fc\ucc28\uc7a5(\ucd1d 2\uc2dc\uac04) <BR>\u2462 \ud55c\ubbf8\uc0b0\uc131-\uad11\ub300\ubd09-\ub098\uc639\ub300-\ube44\ub8e1\ub300-\ubd09\ub450\ubd09-\ucc9c\ud669\ubb38-\uc740\uc218\uc0ac-\ud0d1\uc0ac-\ub0a8\ubd80\uc8fc\ucc28\uc7a5(\ucd1d 7\uc2dc\uac04)'), '35.776779', '127.443905', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \uc9c4\uc548\uad70 \uc9c4\uc548\uc74d\u318d\ub9c8\ub839\uba74'), 2515021000, 687, STRINGDECODE('\ub9c8\uc774\uc0b0'), STRINGDECODE('&lt;\uc2e0\uae30\ub8e8\ucc98\ub7fc \uc19f\uc544\uc624\ub978 \ub9d0 \uadc0 \ubaa8\uc591\uc758 \ud0c0\uc6cc&gt;<BR>\uc0b0\uc774\ub77c\uae30\ubcf4\ub2e4\ub294 \ud0d1\uc73c\ub85c \ubd80\ub974\ub294 \uac8c \ub354 \ub9de\uc744 \uae30\uacbd\uc774\ub2e4. \uc601\uc5b4\uc758 \ud0c0\uc6cc\uc640\ub294 \uc644\uc804\ud788 \ubd80\ud569\ud55c\ub2e4. \uc11c\ubd09 \uc554\ub9c8\uc774\uc0b0\uacfc \ub3d9\ubd09 \uc22b\ub9c8\uc774\uc0b0\uc774 \ub098\ub780\ud788 \uc11c\uc788\ub294\ub370 \uadf8 \ubaa8\uc2b5\uc774 \uaf2d \ub9d0\uc758 \uadc0\ucc98\ub7fc \ubcf4\uc778\ub2e4. \ub0a8\ubd81\uc73c\ub85c \ub192\uc740 \uc0b0\uc774 \uc5c6\uc5b4 \ub354\uc6b1 \uc7a5\uad00\uc73c\ub85c \uc9c4\uc548 \uc6b0\uc8fc\ub4e4\uc774\ub098 \uc0ac\uc591\uc81c \uc218\uba74\uc5d0 \ube44\uce5c \ubaa8\uc2b5\uc740 \ud2b9\ud788 \ud658\uc0c1\uc801\uc774\ub2e4. <BR>\ud0c0\ud3ec\ub2c8 \uc9c0\ud615\uc73c\ub85c\ub3c4 \uc720\uba85\ud558\ub2e4. \ubc14\uc704 \ud45c\uba74\uc774 \ud3ed\uaca9\uc744 \ub9de\uc740 \uac83\ucc98\ub7fc \ud328\uc5b4\uc788\ub294 \ud604\uc0c1\uc73c\ub85c \ub0a8\ud55c \ub0b4\ub959\uc5d0\uc11c\ub294 \ucd5c\ub300 \uaddc\ubaa8\ub77c\uace0 \ud55c\ub2e4. \uc8fc\uc131\ubd84\uc778 \uc5ed\uc554\uc774 \ud48d\ud654\uc791\uc6a9\uc73c\ub85c \uc18d\uc5d0\uc11c\ubd80\ud130 \ubd80\uc2a4\ub7ec\uc838 \uc0dd\uacbc\ub2e4. <BR>\uc2e0\ub77c\ub54c\ub294 ''\uc130\ub2e4\uc0b0''\uacfc \uac00\uae4c\uc6b4 \uc774\ub450 \uc11c\ub2e4\uc0b0(\u897f\u591a\u5c71), \uace0\ub824\uc2dc\ub300\uc5d0\ub294 ''\uc19f\uc744 \uc6a9''\uc790 \uc6a9\ucd9c\uc0b0(\u8073\u51fa\u5c71)\uc774\uc5c8\uace0 \uc870\uc120\uc2dc\ub300\ubd80\ud130 \ub9c8\uc774\uc0b0\uc774\ub77c \ubd88\ub838\ub2e4. \uacc4\uc808\uc5d0 \ub530\ub77c\uc11c\ub3c4 \ub2ec\ub77c \ubd04\uc5d0\ub294 \uc548\uac1c \uc18d\uc5d0 \uc2e0\uae30\ub8e8\ucc98\ub7fc \uc19f\uc740 \ubd09\uc6b0\ub9ac\uac00 \uc30d\ub3db\ub300 \uac19\ub2e4 \ud558\uc5ec \ub3db\ub300\ubd09, \uc5ec\ub984\uc5d0\ub294 \uc57c\uc0b0 \uc704\ub85c \uc6b0\ub69d\ud55c \ubd09\uc6b0\ub9ac\uac00 \uc6a9\uc758 \ubfd4\ucc98\ub7fc \ubcf4\uc778\ub2e4 \ud558\uc5ec \uc6a9\uac01\ubd09(\u9f8d\u89d2\u5cf0), \ub2e8\ud48d \ub4e0 \uac00\uc744\uc5d0\ub294 \ub9c8\uc774\ubd09, \uaca8\uc6b8\uc5d0\ub294 \ub208\uc774 \uc313\uc774\uc9c0 \uc54a\uc544 \uba39\ubb3c\uc744 \ucc0d\uc740 \ubd93\ub05d\ucc98\ub7fc \ubcf4\uc778\ub2e4 \ud558\uc5ec \ubb38\ud544\ubd09(\u6587\u7b46\u5cf0)\uc774\ub77c \ubd80\ub978\ub2e4. <BR>\ub450 \ub9c8\uc774\uc0b0 \uc0ac\uc774\uc5d0 \uc788\ub294 \uc808 \ud0d1\uc0ac\uc5d0\ub294 80\uc5ec\uac1c\uc758 \uc778\uacf5\ud0d1\uc774 \uc232\uc744 \uc774\ub8e8\uace0\uc788\ub2e4. \ub458\ub808 20\ubbf8\ud130, \ub192\uc774 15\ubbf8\ud130\uc5d0 \uc774\ub974\ub294 \uac83\uae4c\uc9c0 \uc788\ub294\ub370 \uc790\uc5f0\uc11d \ub3cc\ub2f4\ubd88\uc774 140\uc5ec\ub144 \ub3d9\uc548\uc744 \ubb34\ub108\uc9c0\uc9c0 \uc54a\uace0 \uc788\ub2e4.'), STRINGDECODE('\ud2b9\uc774\ud55c \uc9c0\ud615\uc744 \uc774\ub8e8\uace0 \uc788\uc73c\uba70, \uc12c\uc9c4\uac15\uacfc \uae08\uac15(\u9326\u6c5f) \ubc1c\uc6d0\uc9c0\uc774\uace0 \ub3c4\ub9bd\uacf5\uc6d0(1979\ub144)\uc73c\ub85c \uc9c0\uc815\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc911\uc0dd\ub300 \ubc31\uc545\uae30\uc5d0 \uc2b5\uace1\uc6b4\ub3d9\uc744 \ubc1b\uc544 \uc735\uae30\ub41c \uc5ed\uc554\uc774 \uce68\uc2dd\uc791\uc6a9\uc5d0 \uc758\ud558\uc5ec \ud615\uc131\ub41c \uc0b0\uc73c\ub85c \uc0b0\uc758 \ud615\uc0c1\uc774 \ub9c8\uce58 \ub9d0\uc758 \uadc0\ub97c \ub2ee\uc558\ub2e4 \ud558\uc5ec \ub9c8\uc774\uc0b0\uc73c\ub85c \ubd88\ub824\uc9d0. \uc554\ub9c8\uc774\uc0b0 \ub0a8\ucabd \uc808\ubcbd \ubc11\uc5d0 \uc788\ub294 80\uc5ec\uac1c\uc758 \ud06c\uace0 \uc791\uc740 \ub3cc\ud0d1\uc774 \uc788\ub294 \ud0d1\uc0ac(\u5854\u5bfa)\uc640 \uae08\ub2f9\uc0ac(\u91d1\u5858\u5bfa)\uac00 \uc720\uba85'), STRINGDECODE('\uc74c\uc591\uc758 \uad81\ud569\uc744 \ub9de\ucd98 \uc554\uc218\ubd09\uc6b0\ub9ac'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc9c4\uc548\uc74d\ub0b4\uc758 \uc219\ubc15\uc2dc\uc124\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR>\uace0\ub824\uc5ec\uad00 \u260e063-433-3374, \ub9ac\uc870\ud2b8\uc7a5 \u260e063-433-4371, \uadf8\ub9b0\uc5ec\uad00 \u260e063-433-2671<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub9c8\uc774\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-433-3313'), STRINGDECODE('\uae30\uc810\uc740 \uc9c4\uc548\uc774\ub2e4. \uc9c4\uc548\uc5d0\uc11c \ub9c8\uc774\uc0b0 \ubd81\ubd80\uc8fc\ucc28\uc7a5\uc73c\ub85c\ub294 \uc644\ud589\ubc84\uc2a4\uac00 17\ud68c, \ub0a8\ubd80\uc8fc\ucc28\uc7a5\uc73c\ub85c\ub294 4\ud68c \uc6b4\ud589\ud55c\ub2e4. \uc9c4\uc548\uc73c\ub85c\ub294 \uc11c\uc6b8 \uac15\ub0a8\ud130\ubbf8\ub110\uacfc \ubd80\uc0b0 \uc0ac\uc0c1\ud130\ubbf8\ub110\uc5d0\uc11c \uc9c1\ud589\ubc84\uc2a4\uac00 4\ud68c\uc529 \uc788\uc73c\uba70 \uc804\uc8fc\uc5d0\uc11c\ub294 10\ubd84, \ub300\uad6c\uc5d0\uc11c\ub294 30\ubd84 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2cc\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(32, TIMESTAMP '2019-08-14 15:46:34.915', TIMESTAMP '2019-08-14 15:46:58.464', '42', STRINGDECODE('\uba85\uc131\uc0b0\uc740 \uc11c\uc6b8\uc5d0\uc11c \ub3d9\ubd81\uc73c\ub85c 84Km, \uc6b4\ucc9c\uc5d0\uc11c \uc57d 4Km \uac70\ub9ac\uc5d0 \uc704\uce58\ud574 \uc788\ub2e4. \uc0b0\uc790\ub77d\uc5d0 \uc0b0\uc815\ud638\uc218\ub97c \ub07c\uace0 \uc788\uc5b4 \ub4f1\uc0b0\uacfc \ud638\uc218\uc758 \uc815\ucde8\ub97c \ub9cc\ub07d \ud560 \uc218 \uc788\ub294 \uc0b0\uc774\ub2e4.<BR>\uc77c\uba85 ''\uc6b8\uc74c\uc0b0''\uc774\ub77c \ubd88\ub9ac\uae30\ub3c4 \ud558\ub294\ub370 \uac70\uae30\uc5d0\ub294 \uc548\ud0c0\uae4c\uc6b4 \uc804\uc124\uc774 \uc804\ud574 \ub0b4\ub824\uc624\uace0 \uc788\ub2e4. \uc2e0\ub77c\uc758 \ub9c8\uc9c0\ub9c9 \uc655\uc790\uc778 \ub9c8\uc758\ud0dc\uc790\uac00 \ub9dd\uad6d\uc758 \uc2ac\ud514\uc73c\ub85c \uc774 \uc0b0\uc5d0\uc11c \ud1b5\uace1\uc744 \ud558\uc790 \uc0b0\ub3c4 \ub530\ub77c \uc6b8\uc5c8\ub2e4 \ud55c\ub2e4. \ub098\ub77c\ub97c \uc783\uc740 \uc2ac\ud514\uc744 \uc0b0\ub3c4 \uc54c\uc558\uc744\uae4c. \uadf8\ub7f0 \uc5f0\uc720\ub85c ''\uc6b8 \uba85'' ''\uc18c\ub9ac \uc131''\uc790\ub97c \ubd99\uc5ec \uba85\uc131\uc0b0\uc73c\ub85c \ubd88\ub9ac\uac8c \ub418\uc5c8\ub2e4\ub294 \uac83\uc774\ub2e4. <BR>\uc0b0\uc804\uccb4\uac00 \uc554\ubcbd\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc0b0\uc138\uac00 \ub2f9\ub2f9\ud558\uace0 \uac00\ud30c\ub974\uba70 \uac00\uc744\uc774\uba74 \uc5b5\uc0c8\ud480\uc774 \uc7a5\uad00\uc744 \uc774\ub8ec\ub2e4. \uc554\ub989\uacfc \uc554\ubcbd\uc774 \uac19\uc774 \ud615\uc131\ub41c \uc0b0\uc774\ub77c\uc11c \uc0ac\uc2dc\uc0ac\ucca0 \ub2e4\uc591\ud55c \ud48d\uacbd\uc744 \uc5f0\ucd9c\ud574 \ub4f1\uc0b0\uac1d\ub4e4\ub85c \ud558\uc5ec\uae08 \ucca0\ub530\ub77c \ub2e4\ub978 \ub290\ub08c\uc744 \ub290\ub07c\uac8c \ud574 \uc900\ub2e4. \uc815\uc0c1\uc740 \ubbfc\ub4f1\ubd09\uc774\ub098 \uc804\ub9dd\uc774 \ub9e4\uc6b0 \uc88b\uc73c\uba70, \ub0a8\ucabd\uc73c\ub85c \uc774\uc5b4\uc9c4 12\ubd09 \ub2a5\uc120\uc758 \ubaa8\uc2b5\uc774 \uc7a5\ucf8c\ud558\ub2e4. \ub2a5\uc120\uc5d0\uc11c \uc6b0\uac70\uc9c4 \uc5b5\uc0c8\ud480\ubc2d\uc744 \uc624\ub974\ub77d\ub0b4\ub9ac\ub77d \ud558\ub294 \uc0ac\uc774\uc5d0 \uc9c0\ub8e8\ud55c \uc904 \ubaa8\ub974\uace0 \uac77\uac8c \ub41c\ub2e4.'), STRINGDECODE('\u2460 \uc0b0\uc815\ud638\uc218 \uc8fc\ucc28\uc7a5-\ube44\uc120\ud3ed\ud3ec-\uc5b5\uc0c8\uad70\ub77d\uc9c0-\ub4f1\ub8e1\ud3ed\ud3ec-\ube44\uc120\ud3ed\ud3ec-\uc8fc\ucc28\uc7a5 (\ucd1d 3\uc2dc\uac04)<BR>\u2461 \uc0b0\uc815\ud638\uc218 \uc8fc\ucc28\uc7a5-\ube44\uc120\ud3ed\ud3ec-\ub4f1\ub8e1\ud3ed\ud3ec-\uc0bc\uac01\ubd09-\uba85\uc131\uc0b0 \uc815\uc0c1-\uc0b0\uc548\uace0\uac1c-\uc8fc\ucc28\uc7a5 (\ucd1d 6\uc2dc\uac04)'), '38.199853', '127.329585', STRINGDECODE('\uac15\uc6d0\ub3c4 \ucca0\uc6d0\uad70 \uac08\ub9d0\uc74d, \uacbd\uae30\ub3c4 \ud3ec\ucc9c\uc2dc \uc601\ubd81\uba74\u318d\uc774\ub3d9\uba74'), 2212010400, 922, STRINGDECODE('\uba85\uc131\uc0b0'), STRINGDECODE('&lt;\ud669\uae08\ube5b \uc5b5\uc0c8\uc758 \uc0b0&gt;<BR>\uba85\uc131\uc0b0 \uc0b0\uc790\ub77d\uc5d0 \uac78\uccd0\uc788\ub294 \uc0b0\uc815\ud638\uc218\uc640 \ub354\ubd88\uc5b4 \uba85\uc2b9\uc9c0\ub85c\uc11c \uc720\uba85\ud558\ub2e4. \u2018\uc6b8\uc74c\uc0b0\u2019\uc744 \ub73b\ud558\ub294 \uba85\uc131\uc0b0(\u9cf4\u8072\u5c71)\uc774\ub780 \uc774\ub984\uc740 \uad81\uc608\uac00 \uc790\uc2e0\uc758 \ubd80\ud558\uc600\ub358 \uace0\ub824 \ud0dc\uc870 \uc655\uac74\uc5d0\uc11c \ud328\ud55c \ud6c4 \uc774\uacf3\uc73c\ub85c \ud53c\uc2e0\uc640 \ud06c\uac8c \uc6b8\uc5c8\ub2e4\ub294 \uc804\uc124\uc5d0\uc11c \ube44\ub86f\ub418\uc5c8\ub2e4. \uadf8\ub9ac\ud558\uc5ec \uba85\uc131\uc0b0 \uc0b0\uc790\ub77d\uc5d0\ub294 \uad81\uc608\ubd09\uc744 \ube44\ub86f\ud574 \uad81\uc608\uac00 \ub3c4\ub9dd\ucce4\ub2e4\ub294 \ud328\uc8fc\uace8, \uc655\uac74\uc758 \uad70\uc0ac\uac00 \ucad3\uc544\uc624\ub294 \uac83\uc744 \uc0b4\ud53c\ub358 \uacf3\uc774\ub77c \ud558\ub824 \uc774\ub984 \ubd99\uc5ec\uc9c4 \ub9dd\ubb34\ubd09 \ub4f1, \uba85\uc131\uc0b0 \uacf3\uacf3\uc5d0\ub294 \uad81\uc608\uc758 \uc804\uc124\uc774 \uc544\uc9c1 \ub0a8\uc544 \uc788\ub2e4.<BR>\uacbd\uae30\ub3c4 \ud3ec\ucc9c\uacfc \uac15\uc6d0\ub3c4 \ucca0\uc6d0\uc5d0 \uac78\uccd0 \ube5a\uc5b4\uc9c4 \uba85\uc131\uc0b0\uc740 \uad81\uc608\uc655\uad74\uc744 \ube44\ub86f\ud558\uc5ec \uc790\uc778\uc0ac, \ucc45\ubc14\uc704, \ub4f1\ub8e1\ud3ed\ud3ec \ub4f1\uc758 \uacbd\uce58\uac00 \ub6f0\uc5b4\ub09c \uacf3\uc774 \ub9ce\ub2e4. \ud2b9\ud788 \uc815\uc0c1\uacfc \uc5f0\uacb0\ub41c \ub2a5\uc120\uc740 \uc5b5\uc0c8\uad70\ub77d\uc9c0\ub97c \uc774\ub8e8\uace0 \uc788\uc5b4 \ud3ec\ucc9c\uc2dc\uc5d0\uc11c\ub294 \ub9e4\ub144 10\uc6d4 \ub458\uc9f8\uc8fc \ud1a0, \uc77c \uc591\uc77c\uac04 \uc5b5\uc0c8\ucd95\uc81c\ub97c \uc5f4\uace0 \uc788\ub2e4.'), STRINGDECODE('\ub3c4\ud3c9\ucc9c(\u90fd\u576a\u5ddd), \uc601\ud3c9\ucc9c(\u6c38\u5e73\u5ddd), \ud55c\ud0c4\uac15\uc758 \uc218\uacc4\ub97c \uc774\ub8e8\uba70, \uc0b0\uc138\uac00 \uac00\ud30c\ub974\uace0 \uacf3\uacf3\uc5d0 \ubc14\uc704\uac00 \uc5b4\uc6b0\ub7ec\uc838 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b4 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0b0 \ubd81\ucabd\uc73c\ub85c \uc0bc\ubd80\uc5f0\ud3ed\ud3ec\uc640 \ub0a8\ucabd\uc73c\ub85c \uc0b0\uc815\ud638\uc218\ub97c \ub07c\uace0 \uc788\uc74c. \uc804\uc124\uc5d0 \uc758\ud558\uba74 \uc655\uac74(\u738b\u5efa)\uc5d0\uac8c \ucad3\uae30\ub358 \uad81\uc608(\u5f13\u88d4)\uac00 \ud53c\uc0b4\ub418\uc5c8\ub358 \uacf3\uc73c\ub85c \uc720\uba85'), STRINGDECODE('\ub9c8\uc758\ud0dc\uc790\ub97c \ub530\ub77c \uc6b8\uc5c8\ub358 \uc6b8\uc74c\uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc0b0\uc815\ud638\uc218 \uc8fc\ubcc0\uc5d0 \uc788\ub294 \uc219\ubc15\uc2dc\uc124\uc744 \uc774\uc6a9\ud558\uba74 \ub41c\ub2e4. <BR>\uc0b0\uc815\ud638\uc218\ud638\ud154 \u260e031-534-4061~5, \uc0b0\uc815\ucea0\ud504 \u260e031-532-6224, 5390<BR>\uc0b0\uc815\ud638\uc218 \uc720\uc2a4\ud0c0\uc6b4 \u260e031-533-5011~3, \uc740\uace1 \uc720\uc2a4\ud0c0\uc6b4 \u260e031-532-7425~6<BR>\ubbfc\ubc15\ubb38\uc758 \ud3ec\ucc9c \uc601\ubd81\ub18d\ud611 \uc9c0\ub3c4\uacc4 \u260e031-531-8375, \uc0b0\uc815\ud638\uc218 \uad00\uad11\uc9c0\ubd80 \u260e031-532-6135, 6104 <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud3ec\ucc9c\uc2dc\uccad \ubb38\ud654\uacf5\ubcf4\ub2f4\ub2f9\uad00 \uc0b0\uc815\ud638\uc218 \uad00\uad11\uc9c0\ubd80 \u260e031-532-6135/6104'), STRINGDECODE('\ud3ec\ucc9c\uc774 \uae30\uc810\uc774\ub2e4. <BR>\ud3ec\ucc9c\uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110 \u260e031-532-5217.<BR>- \ud3ec\ucc9c\u2192\ub3d9\uc1a1(\uc0b0\uc815\ud638\uc218) 10\ubd84 \uac04\uaca9 \uc6b4\ud589<BR>- \uc6b4\ucc9c\u2192\uc0b0\uc815\ud638\uc218 30\ubd84 \uac04\uaca9 \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(33, TIMESTAMP '2019-08-14 15:46:34.916', TIMESTAMP '2019-08-14 15:46:59.083', '41', STRINGDECODE('\uacbd\uae30\ub3c4\uc5d0\uc11c \ud654\uc545\uc0b0(1,468.3m)\uc5d0 \uc774\uc5b4 \ub450 \ubc88\uc9f8\ub85c \ub192\uc740 \uba85\uc9c0\uc0b0\uc740 \uc815\uc0c1\uc744 \uc624\ub97c \uc218 \uc788\ub294 \uc0b0\uc73c\ub85c\ub294 \ucd5c\uace0\ubd09\uc774\ub2e4. \uac70\ub300\ud55c \uc0b0\ub9e5\uc744 \uc774\ub8ec \uba85\uc9c0\uc0b0\uc740 \uc0b0 \ud06c\uae30\ub9cc\ud07c\uc774\ub098 \ub4f1\uc0b0\ub85c\ub3c4 \ub9ce\uc774 \uc788\uc73c\ub098 \uc5b4\ub290 \uae38\uc774\ub098 \uc815\uc0c1\uae4c\uc9c0 3\uc2dc\uac04 \uc774\uc0c1 \uc18c\uc694\ub41c\ub2e4. \uc11c\uc6b8\uc5d0\uc11c \uac00\uae4c\uc6b4 \uacf3\uc5d0 \uc788\uc73c\uba74\uc11c\ub3c4 \uc544\uc9c1 \uc624\uc5fc\ub418\uc9c0 \uc54a\uc740 \uae68\ub057\ud55c \uacc4\uace1\uacfc \uc6b8\ucc3d\ud55c \uc218\ub9bc\uc744 \uac04\uc9c1\ud558\uace0 \uc788\ub2e4. <BR>\uac00\ud3c9\ucc9c\uc744 \uc0ac\uc774\uc5d0 \ub450\uace0 \ud654\uc545\uc0b0\uacfc \ub9c8\uc8fc\ubcf4\uace0 \uc788\ub294 \uba85\uc9c0\uc0b0\uc740 \uc815\uc0c1\uc744 \uae30\uc810\uc73c\ub85c \uc0ac\ubc29\uc73c\ub85c \uc0b0\uc790\ub77d\uc744 \ud3bc\uce58\uba70, \uadc0\ubaa9\ubd09, \uc0ac\ud5a5\ubd09, \ubc31\ub454\ubd09 \ub4f1\uc744 \uac70\ub290\ub9ac\uace0 \uc788\ub294 \uc6c5\uc7a5\ud55c \uc0b0\uc774\ub2e4. \uba85\uc9c0\uc0b0\uc758 \uc6b8\ucc3d\ud55c \uc218\ub9bc\uc740 \uc77c\uc0c1\uc5d0 \uc9c0\uccd0 \uc0b0\uc744 \ucc3e\ub294 \uc774\ub4e4\uc744 \ud3ec\uadfc\ud788 \uac10\uc2f8\uc548\ub294\ub370 \uadf8 \uae4a\uc774\ub97c \uac00\ub2a0\ud560 \uc218 \uc5c6\ub2e4. <BR>\uc0ac\uacc4\uc808\uc774 \ub2e4 \uc544\ub984\ub2e4\uc6b4 \uba85\uc9c0\uc0b0\uc740 \ud2b9\ud788 \ubd04\uc5d0\ub294 \uc9c4\ub2ec\ub798\uc640 \uca54\ucb49\uc774 \uc628\uc0b0\uc5d0 \ud750\ub4dc\ub7ec\uc838 \ubd04\ub0a0\uc758 \uc0b0\ud589\uc744 \uc990\uae30\ub294 \uc774\ub4e4\uc744 \ud669\ud640\uacbd\uc5d0 \uc816\uac8c \ub9cc\ub4e0\ub2e4. \uac00\uc744 \ub2e8\ud48d\uc740 \uac00\ud3c9 \ud314\uacbd \uc911 \uc81c 4\uacbd\uc73c\ub85c \uc9c0\uc815 \ub418\uc5c8\ub2e4. \uc6b0\ub9ac\ub098\ub77c \uac00\uc744 \uc0b0\uc740 \uc5b4\ub514\ub098 \uc544\ub984\ub2f5\uc9c0\ub9cc \uba85\uc9c0\uc0b0\uc758 \ub2e8\ud48d\uc740 \uc218\uc2ed\ub144 \ubb35\uc740 \uace0\ubaa9\uacfc \uae30\uc554\uad34\uc11d\ub4e4\uacfc \uc870\ud654\ub97c \uc774\ub8e8\uc5b4 \ub354\uc6b1 \ub354 \uae4a\uc774\ub97c \ub354 \ud55c\ub2e4. \ub610\ud55c \uaca8\uc6b8\uc5d0\ub294 \uc801\uc124\ub7c9\uc774 \ub9ce\uc544 \uc124\ud654\uac00 \uc7a5\uad00\uc744 \uc774\ub904 \uaca8\uc6b8\uc0b0\ud589\uc744 \ub098\uc120 \uc774\ub4e4\uc744 \ubc18\uae34\ub2e4. <BR>\uba85\uc9c0\uc0b0 \uc785\uad6c\uc778 \uc775\uadfc\ub9ac\uc5d0\uc11c \uc57d 1Km\uac00\ub7c9 \uc62c\ub77c\uac00\uba74 \uaddc\ubaa8\uac00 \uc791\uc740 \uc0ac\ucc30\uc778 \uc2b9\ucc9c\uc0ac\uac00 \ub098\ud0c0\ub098\uace0, \uc774\uc5b4\uc11c 2Km\uac00\ub7c9 \ub354 \uac00\uba74 \ub4f1\uc0b0\ub85c \uc67c\ucabd\uc73c\ub85c \ub192\uc774 6m\uc5d0\uc11c \uc2dc\uc6d0\ud55c \ubb3c\uc904\uae30\uac00 \uc3df\uc544 \ub0b4\ub9ac\ub294 \uba85\uc9c0\ud3ed\ud3ec\ub97c \ub9cc\ub098\uac8c \ub41c\ub2e4. \ud55c\uc5ec\ub984 \ubd88\ubcd5\ub354\uc704\ub3c4 \uc2dd\ud600 \ubc84\ub9ac\ub294 \uba85\uc9c0\ud3ed\ud3ec \uc544\ub798 \uae4a\uc740 \uc6c5\ub369\uc774\ub294 \uc61b\ub0a0\uc5d0 \uba85\uc8fc\uc2e4 \ud55c\ud0c0\ub808\uac00 \ub2e4 \ub4e4\uc5b4\uac08 \uc815\ub3c4\ub85c \uae4a\uc5b4 \uba85\uc9c0\ud3ed\ud3ec\ub85c \uc774\ub984\uc774 \ubd99\uc5ec\uc84c\ub2e4 \ud55c\ub2e4.'), STRINGDECODE('\u2460 \uc775\uadfc\ub9ac-\uac15\ubc15\ubd09-\uc0ac\ud5a5\ubd09-\uc815\uc0c1-\ub0a8\ubd09-\uc2b9\ucc9c\ubd09 \uc548\ubd80-\ubc31\ub454\ub9ac (\ucd1d 6\uc2dc\uac04 10\ubd84)<BR>\u2461 \uadc0\ubaa9\ub9ac-\uc544\uc7ac\ube44\uace0\uac1c-1199\ubd09-\uc815\uc0c1-\uc0ac\ud5a5\ubd09 \uc548\ubd80-\uacc4\uace1 \uc0bc\uac70\ub9ac-\uc775\uadfc\ub9ac (\ucd1d 5\uc2dc\uac04 40\ubd84)<BR>\u2462 \uc775\uadfc\ub9ac-\uba85\uc9c0\ud3ed\ud3ec-\uacc4\uace1 \uc0c1\ubd80-\uc815\uc0c1-1199\ubd09-\uadc0\ubaa9\uace0\uac1c-\uadc0\ubaa9\ubd09-\uac15\uc528\ubd09\uace0\uac1c-\uc77c\ub3d9 (\ucd1d 8\uc2dc\uac04 40\ubd84)'), '37.820536', '127.343646', STRINGDECODE('\uacbd\uae30\ub3c4 \uac00\ud3c9\uad70 \ubd81\uba74\u318d\ud558\uba74'), 2101021300, 1252, STRINGDECODE('\uba85\uc9c0\uc0b0'), STRINGDECODE('&lt;\uc218\ub354\ubd84\ud558\ub098 \uae4a\uc740 \ub9db\uc774 \uc788\ub2e4&gt;<BR>\uba85\uc9c0\uc0b0\uc740 \ud558\ub298\uae08 \uc774\uc05c \uba85\uc0b0\uc740 \uc544\ub2c8\uc9c0\ub9cc \ub192\uace0 \uae4a\uc740 \ub9db\uc774 \uc788\ub2e4. \uacbd\uae30\ub3c4\uc5d0\uc11c \uac00\uc7a5 \uc0b0\uc774 \ub9ce\uace0 \uc9c0\ub300\uac00 \ub192\uc740 \uac00\ud3c9\uad70\uc5d0 \uc790\ub9ac\ud55c \uc81c2\ubd09\uc774\uae30 \ub54c\ubb38\uc774\ub2e4. \ud2b9\ud788 \ub0a8\ucabd\uc758 \uccad\uc6b0\uc0b0(617.8m)\uc5d0\uc11c \uc2dc\uc791, \ub300\uae08\uc0b0(705.8m), \ub9e4\ubd09(929.2m), \uc5f0\uc778\uc0b0(1068m)\uc744 \uac70\uccd0 \uc62c\ub77c\uc624\ub294 70\ub9ac \ub2a5\uc120\uae38\uc740 \uc801\uc124\uae30 \uc885\uc8fc\ucf54\uc2a4\ub85c \ud638(\u865f)\uac00 \ub098\uc788\ub2e4. \uc774 \ub2a5\uc120\uc740 \ubd81\uc11c\uacc4\uc808\ud48d\uc744 \uac00\ub85c\ub9c9\ub294 \uccab 1000\ubbf8\ud130 \uc804\ud6c4 \uc0b0\uc904\uae30\ub2e4. <BR>\ub3d9\ucabd\uc758 \uc775\uadfc\ub9ac\uacc4\uace1\uc744 C\uc790\ub85c \ub458\ub7ec\uc2f8\ub294 \uc0b0\uc138\uc5ec\uc11c \uadf8\ucabd\uc73c\ub85c \ub4f1\uc0b0\ub85c\uac00 \ubc1c\ub2ec\ud574 \uc788\ub2e4. \uc11c\ucabd\uc740 \uadc0\ubaa9\uace0\uac1c\uc5d0\uc11c \uc0c1\ud310\ub9ac\ub85c \ud5a5\ud558\uac70\ub098 \uadc0\ubaa9\ubd09(1036m) \ub108\uba38 \uac15\uc528\ubd09\uace0\uac1c\uc5d0\uc11c \ud3ec\ucc9c \uc77c\ub3d9\uc73c\ub85c \ub0b4\ub824\uac00\uac8c \ub3fc\uc788\ub2e4. \uadf8\ub9cc\ud07c \uc5f0\uacc4 \uad50\ud1b5\uc774 \ubd88\ud3b8\ud55c \uae4c\ub2ed\uc5d0 \ucc3e\ub294 \uc774\ub4e4\uc774 \uc801\uc5b4 \ud638\uc813\ud55c \uc787\uc810\uc740 \uc788\ub2e4. \ubd04\ucca0 \ub098\ubb3c\uc0b0\ud589\uc758 \uc801\uc9c0\ub2e4. <BR>\uba85\uc9c0\uc0b0\ud589\uc758 \ubc31\ubbf8\ub294 \uc815\uc0c1\ubd80\uc5d0\uc11c \ubcf4\ub294 \uc0b0\uad6d(\u5c71\u570b)\uc758 \ud30c\ub178\ub77c\ub9c8\ub2e4. \uc0ac\ubc29\uc774 \uc0b0\uc73c\ub85c \ub458\ub7ec\uc2f8\uc778 \uc808\ud574\uace0\ub3c4 \uac19\uc740 \ub9c9\ub9c9\ud568\uc744 \uacbd\uae30\ub3c4\uc5d0\uc11c \uc720\uc77c\ud558\uac8c \uacbd\ud5d8\ud560 \uc218 \uc788\ub2e4. <BR>6km\ub098 \ub418\ub294 \uc775\uadfc\ub9ac\uacc4\uace1\uc740 \uc5ec\ub984\ucca0 \ubb3c\ub180\uc774\ud558\uae30\uc5d0 \uc88b\ub2e4. \uac00\uc744 \ub2e8\ud48d\ub3c4 \uc774\ub984\uc774 \uc788\ub2e4. \uc721\uc0b0\uc774\ub77c \uc5b8\ub73b \uc218\ub354\ubd84\ud55c \uc778\uc0c1\uc774\uc9c0\ub9cc \uc0b0\uc774 \ud06c\ub2c8 \uadf8\ub9cc\ud55c \uac12\uc740 \ud55c\ub2e4.'), STRINGDECODE('\uacbd\uae30\ub3c4\ub0b4\uc5d0\uc11c \ub450 \ubc88\uc9f8\ub85c \ub192\uc740 \uc0b0\uc73c\ub85c \uacbd\uae30\ub3c4\uc758 \ucd5c\uace0\ubd09\uc778 \ud654\uc545\uc0b0(1,468.3m)\uacfc \uac00\ud3c9\ucc9c\uc744 \uc0ac\uc774\uc5d0 \ud558\uace0 \uc788\uc73c\uba70, \uac15\uc528\ubd09, \uadc0\ubaa9\ubd09, \uccad\uacc4\uc0b0, \uc6b0\ubaa9\ubd09 \ub4f1 \uc0b0\uc138\uac00 \uc6c5\uc7a5\ud558\uace0 \uad70\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \uc810 \ub4f1\uc744 \uac10\uc548\ud558\uc5ec \uc120\uc815, 20\uc5eckm\ub97c \ud750\ub974\ub294 \uc0b0 \ub3d9\ucabd\uc758 \uac00\ud3c9\ucc9c \uacc4\uace1\uacfc \uc775\uadfc\ub9ac\uacc4\uace1\uc758 \uba85\uc9c0\ud3ed\ud3ec\uac00 \uc720\uba85. \uba85\uc9c0\uc0b0 \uc77c\ub300\uc758 \uc0b0\uacfc \uacc4\uace1\ub4e4\uc740 \uacbd\uae30\ub3c4\ub0b4\uc5d0\uc11c\ub294 \uccab\uc9f8\uac00\ub294 \uc2ec\uc0b0\uc720\uace1\uc73c\ub85c \uc54c\ub824\uc838 \uc788\uc74c'), STRINGDECODE('\uc0ac\ubc29\uc73c\ub85c \uc0b0\uc790\ub77d\uc744 \ud3bc\uce5c \uba85\uc9c0\uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uac00\ud3c9\uc74d \uc5f0\uc778\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \ub0b4\uc758 \uc6a9\ucd94\uacc4\uace1\uc790\uc5f0\ud734\uc591\ub9bc \u260e031-582-3889<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uba85\uc9c0\uc0b0\uad70\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e031-582-0103'), STRINGDECODE('\uae30\uc810\uc740 \uac00\ud3c9\uc774\ub2e4. <BR>\uacbd\ucd98\uc120 \uc5f4\ucc28\uac00 1\uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \uc9c0\ub098\uace0 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c\ub294 15\ubd84, \uc0c1\ubd09\ub3d9\ud130\ubbf8\ub110\uc5d0\uc11c\ub294 20\ubd84 \uac04\uaca9\uc73c\ub85c \uc9c1\ud589\ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4. \uc778\ucc9c, \uc218\uc6d0, \uc758\uc815\ubd80,\uc548\uc0b0\uc5d0\uc11c\ub3c4 \uc9c1\ud589\uc774 \uc788\ub2e4. <BR>- \uac00\ud3c9\u2192\uc775\uadfc\ub9ac : 5\ud68c \uc788\ub294 \uc801\ubaa9\ub9ac(\uc6a9\uc218\ub3d9)\ud589\uc744 \ud0c0\uace0\uac00\ub2e4 \uba85\uc9c0\uc0b0 \uc785\uad6c\uc5d0\uc11c \ub0b4\ub9b0\ub2e4 <BR>- \uac00\ud3c9\u2192\ubc31\ub454\ub9ac : 4\ud68c \uc6b4\ud589 <BR>- \uc774\ub3d9\u2192\uc0c1\ubd09\ub3d9 : 1\uc77c 9\ud68c \uc6b4\ud589(\uc77c\ub3d9 \uacbd\uc720)'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(34, TIMESTAMP '2019-08-14 15:46:34.917', TIMESTAMP '2019-08-14 15:46:59.702', '45', STRINGDECODE('\ubaa8\uc545\uc0b0\uc740 \uc804\ub77c\ubd81\ub3c4 \uae40\uc81c\uc2dc \uae08\uc0b0\uba74\uacfc \uc804\uc8fc\uc2dc \uc644\uc0b0\uad6c\uc640 \uc644\uc8fc\uad70 \uad6c\uc774\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c \ud638\ub0a8\uc815\ub9e5\uc5d0 \uc19f\uc544 \uc788\ub2e4.<BR>\uc608\ub85c\ubd80\ud130 \ud638\ub0a8\uc0ac\uacbd\uc758 \ud558\ub098\uc778 ''\ubaa8\uc545\ucd98\uacbd''\uc73c\ub85c \uc720\uba85\ud55c \ubaa8\uc545\uc0b0\uc740 \ubd04\uc774\uba74 \uc628 \uc0b0\uc744 \ubc9a\uaf43\uc73c\ub85c \ub4a4\ub36e\ub294\ub2e4. \ud2b9\ud788, \uae08\uc0b0\uc0ac\uc5d0 \uc774\ub974\ub294 \ubc9a\uaf43 \uae38\uc740 \ubc14\ub78c \ubd88\uc5b4 \uaf43\uc78e\uc774 \ud718\ub0a0\ub9ac\uba74 \ub9c8\uce58 \ub208\uc774 \ub0b4\ub9ac\ub294 \ub4ef\ud55c \ud658\uc0c1\uc5d0 \ube60\uc9c8 \uc815\ub3c4\ub85c \ud654\ub824\ud558\ub2e4. \uadf8\ub7ec\ub098 \ubaa8\uc545\uc0b0\uc774\ub780 \uc774\ub984\uc5d0\uc11c \uc54c \uc218 \uc788\ub4ef\uc774 ''\uc545''\uc790\ub97c \ud488\uace0 \uc788\ub294 \uc774 \uc0b0\uc758 \uc0b0\ud589\uc740 \uacb0\ucf54 \ub9cc\ub9cc\uce58 \uc54a\ub2e4. \uad6c\uc774\ucabd\uc5d0\uc11c \uc815\uc0c1\uc744 \ud5a5\ud574 \uc624\ub974\ub294 \uae38\uc740 \ud2b9\ud788 \ud5d8\ud558\uc5ec \uc815\uc0c1\uc744 \uc5bc\ub9c8 \ub0a8\uaca8\ub450\uc9c0 \uc54a\uc744 \ubb34\ub835\uc5d0\ub294 \uc6ec\ub9cc\ud07c \uc0b0\uc5d0 \ub2e8\ub828\uc774 \ub41c \uc0ac\ub78c\uc774\ub77c \ud560\uc9c0\ub77c\ub3c4 \uc228\uc774 \ud131\uc5d0 \ucc28 \uc624\ub978\ub2e4. \ubaa8\ub4e0 \uc0b0\uc774 \uadf8\ub807\ub4ef \ubaa8\uc545\uc0b0 \uc5ed\uc2dc \ub9c8\uc9c0\ub9c9 \uace0\ube44\uc640\uc758 \ud798\uaca8\uc6b4 \uc904\ub2e4\ub9ac\uae30\ub97c \uce58\ub978 \ud6c4\uc5d0\uc57c \ube44\ub85c\uc18c \uc815\uc0c1\uc5d0 \uc624\ub974\ub294 \uae30\uc068\uc744 \ub9db\ubcfc \uc218 \uc788\ub2e4.<BR>\uc815\uc0c1\uc5d0 \uc624\ub974\uba74 \uc804\uc8fc\uc2dc\ub0b4\uc640 \ud638\ub0a8\uc758 \ub113\uc740 \ub4e4\ud310\uc774 \ud55c\ub208\uc5d0 \ub4e4\uc5b4\uc640 \ubcf4\ub294 \uac83\ub9cc\uc73c\ub85c\ub3c4 \ud48d\uc694\ub85c\uc6b4 \ub9c8\uc74c\uc774 \ub4dc\ub294 \ud638\ub0a8\ud3c9\uc57c\uc758 \uc804\uacbd\uc744 \ub9c8\uc74c\uaecf \uc990\uae38 \uc218 \uc788\ub2e4. \uae40\uc81c\ucabd\uc73c\ub85c \ud558\uc0b0\ud558\ub294 \uae38\uc740 \ube44\uad50\uc801 \uc218\uc6d4\ud574 \uc26c\uc5c4\uc26c\uc5c4 \uc8fc\uc704\uc758 \uacbd\uce58\ub97c \uac10\uc0c1\ud558\uba70 \ub0b4\ub824\uc624\uba74 \ub41c\ub2e4. \ube44\ub85d 800m\ub3c4 \ucc44 \uc548\ub418\ub294 \ubaa8\uc545\uc0b0\uc774\uc9c0\ub9cc \ub369\uce58\uc640\ub294 \ub2e4\ub974\uac8c \uad6c\ube44\uad6c\ube44\uc5d0 \ub2e4\uc591\ud55c \ud48d\uacbd\ub4e4\uc744 \uc5f0\ucd9c\ud574 \uc0b0\ud589\ud558\ub294 \uc774\ub4e4\uc758 \uc2dc\uc120\uc744 \uc990\uac81\uac8c \ud55c\ub2e4. 1971\ub144 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \uc774 \uc0b0\uc740 \ubbf8\ub975\uc2e0\uc559\uacfc \ub9ce\uc740 \uc5f0\uad00\uc744 \uac00\uc9c0\uace0 \uc788\uc5b4 \uc0b0\uc790\ub77d \uacf3\uacf3\uc5d0 \uc774\uc640 \uad00\ub828\ub41c \ud754\uc801\uc774 \ub9ce\uc774 \ub0a8\uc544\uc788\ub2e4.'), STRINGDECODE('\u2460 \ub9e4\ud45c\uc18c-\uae08\uc0b0\uc0ac-\uc6a9\ud654\uc0ac-344.5\ubd09-\uc8fc\ub2a5\uc120-730\ubd09-\uc815\uc0c1-\ubc30\uc7ac-\uae08\uc0b0\uc0ac-\ub9e4\ud45c\uc18c(\ucd1d 5\uc2dc\uac04 20\ubd84)<BR>\u2461 \uad6c\uc774\uba74\uc18c-\uc120\ub140\ud3ed\ud3ec-\ub300\uc6d0\uc0ac-\uc815\uc0c1-\uae08\uc0b0\uc0ac-\ub9e4\ud45c\uc18c(\ucd1d 3\uc2dc\uac04 40\ubd84)<BR>\u2462 \uccad\ub3c4\uc7ac-730\ubd09-\uc815\uc0c1-\uc2ec\uc6d0\uc554-\uae08\uc0b0\uc0ac-\ub9e4\ud45c\uc18c(\ucd1d 3\uc2dc\uac04)'), '35.714979', '127.029579', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \uae40\uc81c\uc2dc \uae08\uc0b0\uba74\u318d\uc804\uc8fc\uc2dc \uc644\uc0b0\uad6c\u318d\uc644\uc8fc\uad70 \uad6c\uc774\uba74'), 2503060700, 795, STRINGDECODE('\ubaa8\uc545\uc0b0'), STRINGDECODE('&lt;\uc99d\uc0b0\uad50 \ub0b3\uc740 \ub9c8\ub984\ubaa8\uaf34 \uc0b0\ub989\uc758 \uc5b4\uba38\ub2c8\uc0b0&gt;<BR>\uc774\ub984\ucc98\ub7fc \ud3ec\uadfc\ud55c \uc5b4\uba38\ub2c8\uc0b0\uc774\ub2e4. \uc548\uc73c\ub85c \ub4e4\uc5b4\uac00\ubcf4\uba74 \uc548\ub2e4. \uad6c\uc131\uc0b0(489.7m), \uccad\ub3c4\uc7ac, \uc0c1\ubd09, \uc548\ub355\ubd09(617m)\uc744 \uc787\ub294 \ub9c8\ub984\ubaa8\uaf34 \uc0b0\ub989\uc774 \ubb3c, \ubd88, \ubc14\ub78c \uc0bc\uc7ac(\u4e09\u707d)\ub97c \ub9c9\uc544\uc8fc\ub294 \ud615\uc138\ub2e4. \uc774\ub7f0 \uae4c\ub2ed\uc5d0 \uac15\uc99d\uc0b0\uc740 \ub3d9\ud559 \ub09c\ub9ac \ub05d\uc758 \uc5b4\uc218\uc120\ud55c \uc0c1\ud669\uc5d0\uc11c \ubbfc\uc911\uc758 \uc0c1\ucc98\ub97c \uc4f0\ub2e4\ub4ec\uc5b4\uc8fc\ub294 \uc99d\uc0b0\uad50\ub97c \ucc3d\uc2dc\ud560 \uc218 \uc788\uc5c8\ub2e4. <BR>\ub9c8\ub984\ubaa8\uaf34 \uac00\uc6b4\ub370\ub294 \uae08\uc0b0\uc0ac\uac00 \uc788\ub2e4. \uad6d\ubcf4 \ud558\ub098\uc5d0\ub2e4 \ubcf4\ubb3c\uc744 \ubb34\ub824 \uc5ec\ub35f\uc774\ub098 \ud488\uc5c8\ub2e4. \ubaa8\uc545\uc0b0\uc774 \uc0bc\uc7ac\ubd88\uc785\uc9c0\uc9c0\uc784\uc744 \uc99d\uba85\ud558\ub294 \uc808\uc774\ub2e4. <BR>\uc544\ub984\ub4dc\ub9ac \ubc9a\ub098\ubb34 \ub3c4\uc5f4\ud55c \uc0b0\uc0ac\ub85c \uac00\ub294 \uae38 \ub4e4\uba38\ub9ac\uc5d0\ub294 \uc77c\uc8fc\ubb38\uc774 \uc544\ub2c8\ub77c \uc6ec \uc131\ubb38\uc774 \uae38\uc744 \ub9c9\uace0\uc788\ub2e4. \ud070 \ub9c8\ub984\ubaa8 \uc548 \uac00\uc7a5 \uae4a\uc219\ud55c \uacf3\uc5d0 \uc788\ub294 \uc791\uc740 \ub9c8\ub984\ubaa8\uc758 \uaf2d\uc9c0\uc810\uc774\ub2e4. \uadf8\ub807\uac8c \uacbd\ub0b4\ub85c \ub4e4\uc5b4\uac00 \ubc14\ub77c\ubcf4\ub294 \uad6d\ubcf4 \ubbf8\ub975\uc804. \uadf8 \uc548\uc758 \ub300\ubd88(\u5927\u4f5b)\uc740 \"\"\ubd88\uc30d\ud55c \uc911\uc0dd\ub4e4\uc774\uc5ec, 57\uc5b5\ub144 \ub4a4 \ub098\uc640 \ud568\uaed8 \ud658\uc0dd\ud558\ub9ac\ub77c\"\" \ub9d0\ud558\uace0\uc788\ub2e4. <BR>\ubc29\ub4f1\uacc4\ub2e8 \uc704\uc758 \ubcf4\ubb3c \uc11d\ud0d1\uc740 \ubc31\uc81c\ud45c 5\uce35\ud0d1\uc774\uc9c0\ub9cc \ud48d\uae30\ub294 \ub9db\uc740 \uc2e0\ub77c \uac10\uc740\uc0ac\uc9c0\ud0d1 \uac19\uc740 \ud798\ucc38\uc774\ub2e4. \uc0c1\ubd09\uc744 \ud5a5\ud558\ub294 \uc0b0\uae38\uc740 \uc628\ud1b5 \uc2dc\ub204\ub300\ubc2d\uc774\ub2e4. \ud654\uc0b4 \uacf3\uac04. \uc774\ub7f0 \ub4b7\ubc30\uac00 \uc788\uc5c8\uae30\uc5d0 \ub3d9\ud559\ub18d\ubbfc\uad70\uc740 \uc548\ud1b5\uc758 \ubb3c\uc744 \ubaa8\ub450 \ubaa8\uc544 \ud750\ub974\ub294 \uae08\uc0b0\ucc9c \uc6d0\ud3c9\ub4e4\uc5d0\uc11c \ubd80\ub098\ube44\ucc98\ub7fc \uc0b0\ud654\ud560 \uc218 \uc788\uc5c8\ub2e4.'), STRINGDECODE('\uc9c4\ub2ec\ub798\uc640 \ucca0\ucb49\uc774 \uc720\uba85\ud55c \ud638\ub0a8 4\uacbd\uc758 \ud558\ub098\uc774\uba70, \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1971\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc2e0\ub77c \ub9d0\uc5d0 \uacac\ud6e4\uc774 \uc774 \uacf3\uc744 \uadfc\uac70\ub85c \ud6c4\ubc31\uc81c\ub97c \uc77c\uc73c\ucf30\ub2e4\uace0 \uc804\ud574\uc9d0. \uad6d\ubcf4 \uc81c62\ud638\uc778 \ubbf8\ub975\uc804\uc744 \ube44\ub86f\ud558\uc5ec \ub300\uc801\uad11\uc804(\ubcf4\ubb3c \uc81c467\ud638) \ud61c\ub355\uc655\uc0ac\uc751\ud0d1\ube44(\ubcf4\ubb3c \uc81c24\ud638) 5\uce35\uc11d\ud0d1(\ubcf4\ubb3c \uc81c27\ud638)\ub4f1 \ub9ce\uc740 \ubb38\ud654\uc7ac\uac00 \uc788\ub294 \uae08\uc0b0\uc0ac(\u91d1\u5c71\u5bfa)\uac00 \uc788\uc74c. \ud2b9\ud788 \ubbf8\ub975\uc804\uc5d0 \uc788\ub294 \ub192\uc774 11.82m\ub098 \ub418\ub294 \ubbf8\ub975\ubd88\uc774 \uc720\uba85'), STRINGDECODE('\ud638\ub0a8\uc0ac\uacbd\uc758 \ud558\ub098'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub4e4\uba38\ub9ac\uc640 \ub0a0\uba38\ub9ac\uc5d0\ub294 \uc74c\uc2dd\uc810\uacfc \uc219\ubc15\uc9d1\uc774 \uc5ec\ub7ff \uc788\ub2e4. <BR>\uc911\uc778\ub3d9\uc5d0\ub294 \ud669\ud1a0\uc9d1(\u260e063-222-8475), \uad6c\uc774\ubc29\uba74\uc5d0\ub294 \ud654\uc0c1\uad81(\u260e063-222-8723), \ub465\uc9c0\ubaa8\ud154(\u260e063-221-9390) \ub4f1\uc774 \uc788\ub2e4. <BR>\uae08\uc0b0\uc0ac \uc9d1\ub2e8\uc2dc\uc124\uc9c0\uad6c\uc5d0\ub294 \ubaa8\uc545\uc0b0\uc720\uc2a4\ud638\uc2a4\ud154(\u260e063-548-4402)\uacfc \ubaa8\uc545\uc0b0\uc7a5(\u260e063-548-4411)\uc774 \uc788\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ubaa8\uc545\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-222-7816'), STRINGDECODE('\uae30\uc810\uc740 \uc804\uc8fc\uc9c0\ub9cc \uae40\uc81c\ub97c \uacbd\uc720\ud558\uba74 \ud3b8\ud558\ub2e4. \uae40\uc81c\uc5ed\uc5d0\uc11c \uae08\uc0b0\uc0ac\uae4c\uc9c0 \ud0dd\uc2dc\ub85c 15\ubd84 \uac78\ub9b0\ub2e4. \uae40\uc81c\ub85c\ub294 \ud638\ub0a8\uc120 KTX\uac00 6\ud68c, \uc5f4\ucc28\ub294 19\ud68c \ub2e4\ub2cc\ub2e4. \uc804\uc8fc\uc5d0\uc11c\ub294 \uc911\uc778\ub3d9\uc774\ub098 \uae08\uc0b0\uc0ac, \uad6c\uc774\ud589 \uc2dc\ub0b4\ubc84\uc2a4\ub97c \uc774\uc6a9\ud55c\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(35, TIMESTAMP '2019-08-14 15:46:34.917', TIMESTAMP '2019-08-14 15:47:00.279', '29', STRINGDECODE('\uad11\uc8fc\uc2dc\uac00\uc9c0\uc5d0\uc11c \ub3d9\ucabd\uc73c\ub85c \ubd88\uacfc 10km \uac70\ub9ac\uc5d0 \uc790\ub9ac\ud558\uace0 \uc788\ub294 \ubb34\ub4f1\uc0b0\uc740 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5b4 \uc788\ub2e4.<BR>\uc0b0\uc758 \ud615\uc138\uac00 \ud5d8\ud558\uc9c0 \uc54a\uace0 \ub300\ubd80\ubd84\uc774 \ud759\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc5b4 \ub204\uad6c\ub098 \uc27d\uac8c \uc624\ub97c \uc218 \uc788\uc73c\uba70, \uacf3\uacf3\uc5d0 \ub9d1\uc740 \ubb3c\uc774 \ud750\ub974\uace0 \uc788\ub2e4. \ud2b9\ud788 \uc0b0\uc704\uc5d0\ub294 \uc11c\uc11d\ub300, \uaddc\ubd09, \uc785\uc11d\ub300\ub4f1\uc758 \uc6c5\uc7a5\ud55c \ubc14\uc704\ub4e4\uc774 \uc788\uc73c\uba70 \uc0b0\uae30\uc2ad\uacfc \uc911\ud131\uc5d0\ub294 \uc57d\uc0ac\uc554, \uc99d\uc2ec\uc0ac, \uc6d0\ud6a8\uc0ac \ub4f1\uc758 \uc774\ub984\ub09c \uc808\ub4e4\uc774 \uc790\ub9ac\uc7a1\uace0 \uc788\ub2e4. 1972\ub144 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\uc73c\uba70 \uc0b0 \uc544\ub798\uc5d0\ub294 \uac01\uc885 \ub180\uc774 \ubc0f \ud3b8\uc758\uc2dc\uc124\uc774 \ub4e4\uc5b4\uc11c \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc6d0\ud6a8\uc0ac-\ub3d9\ud654\uc0ac\ud130-\uc911\ubd09-\uc11c\uc11d\ub300-\uc785\uc11d\ub300-\uc7a5\ubd88\uc7ac-\uc548\uc591\uc0b0-\ub454\ubcd1\uc7ac (\ucd1d 4\uc2dc\uac04 30\ubd84)<BR>\u2461 \uc601\ud3c9\ub9ac-\uaddc\ubd09\uc554-\uc7a5\ubd88\uc7ac-\uc785\uc11d\ub300-\uc11c\uc11d\ub300-\uc911\ubd09-\ubc14\ub78c\uc7ac-\uc99d\uc2ec\uad50 (\ucd1d 5\uc2dc\uac04 30\ubd84)<BR>\u2462 \ud654\uc21c\uc74d \uc2e0\uc815\uc554-\uc911\uba38\ub9ac\uc7ac-\uc7a5\ubd88\uc7ac-\uaf2c\ub9c9\uc7ac-\ubb34\ub4f1\uc0b0\uc7a5 (\ucd1d 7\uc2dc\uac04)'), '35.146101', '126.923093', STRINGDECODE('\uad11\uc8fc\uad11\uc5ed\uc2dc \ub3d9\uad6c, \uc804\ub77c\ub0a8\ub3c4 \ub2f4\uc591\uad70 \ub0a8\uba74\u318d\ud654\uc21c\uad70 \uc774\uc11c\uba74'), 2622132600, 1187, STRINGDECODE('\ubb34\ub4f1\uc0b0'), STRINGDECODE('&lt;\uacc4\uace1 \ube7c\uc5b4\ub09c \uc601\ub0a8\uc758 \uae08\uac15\uc0b0&gt;<BR>\ubb34\ub4f1\uc0b0\uc740 \uc0b0\uc904\uae30\uc640 \uace8\uc9dc\uae30\uac00 \ub69c\ub837\ud558\uc9c0 \uc54a\uace0 \ub9c8\uce58 \ucee4\ub2e4\ub780 \ub454\ub355\uacfc \uac19\uc740 \uc0b0\uc138\ub97c \uc9c0\ub2cc \ud651\uc0b0\uc774\ub2e4. \uac00\uc7a5 \ud070 \ud2b9\uc9d5\uc740 \ub108\ub35c\uc9c0\ub300\ub85c, \ucc9c\uc655\ubd09 \ub0a8\ucabd\uc758 \uc9c0\uacf5\ub108\ub35c\uacfc \uc99d\uc2ec\uc0ac \ub3d9\ucabd\uc758 \ub355\uc0b0\ub108\ub35c\uc740 \uc7a5\uad00\uc744 \uc790\ub791\ud55c\ub2e4. <BR>\ucc9c\ud0dc\ub9cc\uc0c1\uc758 \uc554\uc11d\ub4e4\uc774 \uc6b4\uc9d1\ud55c \uc815\uc0c1, \ucc9c\uc655\ubd09\uc744 \uc911\uc2ec\uc73c\ub85c \ud3bc\uccd0\uc9c0\ub294 \ubc14\uc704\ub4e4\uc758 \uc6c5\uc7a5\ud568\uc740 \ub0a8\ub3c4\ubbfc\uc758 \uc2e0\uc559\ub300\uc0c1\uc774 \ub418\uc5b4\uc628 \uc2e0\uc0b0\uc784\uc744 \ubc18\uc99d\ud55c\ub2e4. \ud2b9\ud788 \uc785\uc11d\ub300, \uc11c\uc11d\ub300, \uaddc\ubd09\uc758 \ubc14\uc704\uad70\uc0c1\uc740 \ubcf4\ub294 \uc774\ub97c \uc555\ub3c4\ud560 \ub9cc\ud07c \ub300\ub2e8\ud558\ub2e4. \ud5c8\ub098 \ubb34\ub4f1\uc0b0\uc740 \uc804\ubc18\uc801\uc73c\ub85c \uc644\ub9cc\ud55c \uc0b0\uc138\uc774\uba70 \ub300\ubd80\ubd84\uc774 \ud759\uc0b0\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub2e4. <BR>\uba40\ub9ac\uc11c \ubcf8 \uc804\uacbd\uc740 \ubafc ''\u5c71''\uc790\ub97c \uc5f0\uc0c1\ucf00 \ud558\uba70 \ud638\ub0a8\uc815\ub9e5 \ud750\ub984\uc758 \uc911\uc2ec\uc5d0 \uc19f\uc544 \uc788\ub2e4. \uace8\uc9dc\uae30\ub4e4\uc740 \ub9d1\uace0 \uae4a\uc740 \ubb3c\uc744 \ud488\uc5b4 \uad11\uc8fc\uc758 \uc816\uc904 \ub178\ub987\uc744 \ud574 \uc654\uc73c\uba70, \uc704\uce58\ub098 \uc0b0\uc138 \uba74\uba74\uc774 \ub0a8\ub3c4\uc758 \uc73c\ub738\uc774\ub77c \ud560 \ub9cc\ud55c\uc9c0\ub77c \ube5b\uace0\uc744 \uad11\uc8fc\ub97c \ud488\uae30\uc5d0 \ubd80\uc871\ud568\uc774 \uc5c6\ub294 \uc0b0\uc774\ub2e4.'), STRINGDECODE('\u