INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(36, TIMESTAMP '2019-08-14 15:46:34.918', TIMESTAMP '2019-08-14 15:47:00.87', '48', STRINGDECODE('\ubc31\ub450\ub300\uac04\uc758 \ub05d\uc790\ub77d\uc778 \uc9c0\ub9ac\uc0b0 \uc0bc\uc2e0\ubd09\uc5d0\uc11c \uc2dc\uc791\ub418\ub294 \ub099\ub0a8\uc815\ub9e5\uc758 \uc0c1\uc88c\uaca9\uc778 \ubb34\ud559\ubd09\uc740 \ub9c8\uc0b0\uc758 \uc9c4\uc0b0(\u93ad\u5c71)\uc73c\ub85c \ub9c8\uc0b0\ub9cc\uacfc \uc9c4\ud574\ub9cc\uc744 \uad7d\uc5b4\ubcf4\uba70 \ubc14\ub2e4\ub97c \ud5a5\ud574 \ud559\uc774 \ub0a0\uac1c\ub97c \ud3bc\uce5c \ub4ef \uc544\ub984\ub2e4\uc6b4 \ud615\uc0c1\uc744 \ud558\uace0 \uc788\ub2e4. <BR>\uba85\uc0b0\uc774 \uac16\ucd94\uc5b4\uc57c \ud560 \uba74\ubaa8\ub97c \ub450\ub8e8 \uac16\ucd94\uace0 \uc788\ub294 \uc774 \uc0b0\uc740 \ubc14\ub2f7\uac00\uc5d0 \uc704\uce58\ud55c \uc0b0\ub2f5\uac8c \uc2dc\uc6d0\ud55c \uc870\ub9dd\uc744 \ub9cc\ub07d\ud560 \uc218 \uc788\uc73c\uba70 \uc8fc\ub2a5\uc120 \ub4f1\ub85c\uc5d4 \uc5b5\uc0c8\ubc2d\uacfc \uc554\ubd09 \uc804\ub9dd\ub300\uac00 \uc788\uc5b4 \ud3b8\uc548\ud55c \ub290\ub08c\uc744 \uc900\ub2e4. <BR>\ub300\uace1\uc0b0 \uc544\ub798\ucabd\uc5d0\ub294 `\ub9cc\ub0a0\uace0\uac1c''\uac00 \uc788\ub294\ub370 \uc774\uacf3\uc740 \uac00\uc815\ud615\ud3b8\uc774 \uc5b4\ub824\uc6cc \ub313\uac00\ub97c \ubc1b\uace0 \uc2dc\uc9d1 \ubcf4\ub0b8 \ub538\uc744 \uadf8\ub9ac\uc6cc\ud558\ub358 \uc5b4\ubbf8\uc640 \uadf8 \ub538\uc774 \uadf8\ub9ac\uc6c0\uc744 \ucc38\uc9c0 \ubabb\ud574 \uace0\uac2f\ub9c8\ub8e8\uc5d0 \uc62c\ub790\ub2e4\uac00 \uc6b0\uc5f0\ud788 \ub9cc\ub0ac\ub2e4\ub294 \uc560\ud2cb\ud55c \uc804\uc124\uc774 \uc804\ud574\uc9c0\ub294 \uace0\uac1c\ub2e4. \ubb34\ud559\uc0b0\uc758 \uc61b\uc774\ub984\uc740 \ud48d\uc7a5\uc0b0\uc774\uc5c8\ub294\ub370 \uc2e0\ub77c\ub9d0 \ucd5c\uce58\uc6d0\uc774 \uc774\uacf3\uc5d0 \uba38\ubb3c\uba74\uc11c \uc0b0\uc138\ub97c \ubcf4\ub2c8 \ud559\uc774 \ub098\ub294 \ud615\uc138\uac19\ub2e4\ud558\uc5ec \ubb34\ud559\uc0b0\uc774\ub77c \ubd88\ub9ac\uc6b0\uac8c \ub418\uc5c8\ub2e4\ud55c\ub2e4.'), STRINGDECODE('\u2460 \uc911\ub9ac\uc5ed-\ub099\ub0a8\uc815\ub9e5 \uac08\ub9bc\uae38-\uc2dc\ub8e8\ubd09 \uac08\ub9bc\uae38-\uc815\uc0c1-\uac1c\ub098\ub9ac\ub3d9\uc0b0-\ud559\ubd09-\uc11c\uc6d0\uace1 \uc785\uad6c (\ucd1d 4\uc2dc\uac04)<BR>\u2461 \ub9cc\ub0a0\uace0\uac1c-\ub300\uace1\uc0b0-\uc815\uc0c1-\uac71\uc815\ubc14\uc704-\ubd09\ud654\uc0b0-\ubd09\uad6d\uc0ac (\ucd1d 3\uc2dc\uac04 50\ubd84)'), '35.210289', '128.552512', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ucc3d\uc6d0\uc2dc \uad50\ubc29\ub3d9'), 2806020900, 761, STRINGDECODE('\ubb34\ud559\uc0b0'), STRINGDECODE('&lt;\ub0a8\ucabd \ubc14\ub2e4 \uad7d\uc5b4\ubcf4\ub294 \ucc3d\uc6d0\uc758 \uc9c4\uc0b0 &gt;<BR>\ubb34\ud559\uc0b0\uc740 \ub099\ub0a8\uc815\ub9e5\uc758 \uae30\ub465\uc904\uae30\ub85c \ub0a8\ubd81\uc73c\ub85c \uae38\uac8c \ud758\ub7ec \ub3d9\ucabd\uc73c\ub85c \ucc3d\uc6d0\uc2dc\ub97c \ub04c\uc5b4 \uc548\uace0 \uc788\uc73c\uba70 \ubc1c\uce58 \uc544\ub798\ub294 \ud638\uc218 \uac19\uc740 \uc9c4\ud574\ub9cc\uacfc \uc5b4\uc6b8\ub824 \ud55c\ud3ed\uc758 \uc218\ucc44\ud654\ub97c \uadf8\ub824 \ub193\uc740 \ub4ef\ud558\ub2e4. <BR>\uc138\uac1c\uc758 \uc0b0\uc904\uae30\uac00 \ubaa8\uc5ec \uc774\ub8e8\uc5b4\uc9c4 \uc0bc\uac01\ubfd4\uc758 \ubb34\ud559\uc0b0\uc740 \ucda4\ucd94\ub294 \ud55c \ub9c8\ub9ac\uc758 \ud559\uc758 \ud615\uc0c1\uc5d0 \ube44\uc720\ub41c\ub2e4. \uc815\uc0c1\uc740 \ud480 \ud55c \ud3ec\uae30 \uc5c6\ub294 \ubc14\uc704\ub369\uc774\ub85c \ud615\uc131\ub418\uc5b4 \uc788\ub2e4. \ubb34\ud559\uc0b0\uc740 \ub9c8\uc0b0 \uc0b0\uc545\uc778\ub4e4\uc758 \uace0\ud5a5\uc73c\ub85c \uc54c\ud504\uc2a4 \ubd81\ubcbd \ub4f1\ubc18\uc774 \uc774\uacf3\uc5d0\uc11c \uc2dc\uc791\ub418\uc5c8\uace0 \ud788\ub9d0\ub77c\uc57c \uac70\ubd09\ub3c4 \uc5ec\uae30\uc11c\ubd80\ud130 \uc5f0\uacb0\ub418\uc5c8\ub2e4. <BR>\ud55c\ub54c \uc804\uad6d\uc744 \uac15\ud0c0\ud55c \uc790\uc720\ub4f1\ubc18\uc758 \uac70\uc13c \ubb3c\uacb0\uc758 \ubc1c\uc6d0\uc9c0\ub3c4 \ubb34\ud559\uc0b0\uc774\ub2e4. \ubb34\ud559\uc0b0\uc758 \ubcf8\ub798 \uc774\ub984\uc740 ''\ub9d0 \ub9c8'' ''\uc790 \ucc99''\uc758 \ub450\ucc99\uc0b0(\u6597\u5c3a\u5c71), \uc6b0\ub9ac\ub9d0\ub85c\ub294 \ub9c8\uc7ac\ub2e4. \uc138\ub9c8\uc7ac\ub85c \ubd88\ub9ac\uae30\ub3c4 \ud588\ub2e4.'), STRINGDECODE('\ub3c4\uc2dc\ubbfc\uc758 \ud734\uc2dd\ucc98\ub85c\uc11c \uacbd\uad00\uc774 \uc88b\uc740 \uc544\uae30\uc790\uae30\ud55c \ub2a5\uc120\uacfc \ub2e4\ub3c4\ud574\ub97c \ubc14\ub77c\ub2e4\ubcf4\ub294 \uc870\ub9dd\uc774 \uc88b\uc740 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\uc815\uc0c1 \ubd81\uc11c\ucabd\uc5d0 \uc788\ub294 \uc2dc\ub8e8\ubd09 \uc77c\ub300\uc758 \ubc14\uc704\ub294 \uc88b\uc740 \uc554\ubcbd\ub4f1\ubc18 \ud6c8\ub828\uc7a5\uc784. \uc608\uc804\ubd80\ud130 \uc591\uc870\uc5c5\uc774 \uc131\ud560 \uc815\ub3c4\ub85c \uc218\uc9c8\uc774 \uc88b\uc74c \uc11c\uc6d0\uace8 \uc785\uad6c\uc5d0 \ucd5c\uce58\uc6d0\uc758 \uc81c\uc790\ub4e4\uc774 \uc138\uc6b4 \uad00\ud574\uc815(\u89c0\u6d77\u4ead)\uc774 \uc788\uace0 \ubd80\uadfc \uc6d0\uac01\uc0ac, \ubc31\uc6b4\uc0ac \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\uc0b0\uc138\ub97c \ubcf4\uc544\ud558\ub2c8 \ucda4\ucd94\ub294 \ud559 \uac19\uc544'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ucc3d\uc6d0?\uc2dc\ub0b4\uc5d0 \uc5ec\uad00\uacfc \ud638\ud154, \uc2dd\ub2f9\uc774 \ub9ce\uc774 \uc788\ub2e4. <BR>\uc11c\uc6d0\uc7a5(\u260e055-246-6610)<BR>\uadc0\ube48\uc7a5(\u260e055-248-7878)<BR>\uc624\ub3d9\ub3d9 \uc544\uad6c\ucc1c \uace8\ubaa9\uc5d0\uc11c \uc624\ub3d9\ub3d9\uc544\uad6c\ud560\ub9e4\uc9d1(\u260e055-248-6202), <BR>\uc9c4\uc9dc\ucd08\uac00\uc6d0\uc870\uc544\uad6c\ucc1c(\u260e055-246-0427)\uc774 \uc720\uba85\ud558\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uccad\uc6d0\uc2dc \ubb38\ud654\uacf5\ubcf4\uacfc \u260e055-600-3243'), STRINGDECODE('\uae30\uc810\uc740 \ucc3d\uc6d0\uc774\ub2e4.<BR>\ucc3d\uc6d0\uace0\uc18d\ubc84\uc2a4\ud130\ubbf8\ub110\uacfc \ucc3d\uc6d0\uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110 \uc5b4\ub290 \uacf3\uc774\ub4e0 \uc2dc\ub0b4\ubc84\uc2a4\uc640 \uc5