INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(37, TIMESTAMP '2019-08-14 15:46:34.918', TIMESTAMP '2019-08-14 15:47:01.495', '48', STRINGDECODE('\ud1b5\uc601\uc2dc \uc11c\ub0a8\ub2e8\uc758 \ubbf8\ub975\ub3c4\uc5d0 \uc704\uce58\ud55c \uc57c\ud2b8\ub9c9\ud55c \uc0b0\uc774\ub098 \ubbf8\ub975\uc2e0\uc559\uc758 \ubcf8\uac70\uc9c0\ub85c \ubd88\uad50\uc778\ub4e4 \uc0ac\uc774\uc5d0\ub294 \uaf64 \uc54c\ub824\uc9c4 \uc0b0\uc774\ub2e4. \uadf8\ub798\uc11c\uc778\uc9c0 \uc774 \uc791\uc740 \uc0b0\uc5d0\ub294 \uc6a9\ud654\uc0ac, \uad00\uc74c\uc0ac, \ub3c4\uc194\uc554, \ubbf8\ub798\uc0ac \ub4f1 \ud06c\uace0 \uc791\uc740 \uc0ac\ucc30\uacfc \uc554\uc790\uac00 \ub9ce\ub2e4. <BR>\ub610 \uc784\ud574\ubd09(\u81e8\u6d77\u5cf0)\uc774\ub77c\ub294 \uc774\ub984\uc758 \uc815\uc0c1\uc740 \uc9c0\ub09c\ub0a0 \ubd09\ud654\ub300\ub85c\uc11c \ub2e4\ub3c4\ud574\ub97c \uc9c0\ud0a8 \uc5ed\uc0ac\uc801 \uc7a5\uc18c\ub85c\ub3c4 \uae30\uc5b5\ub420 \ub9cc \ud558\ub2e4. \ubbf8\ub975\uc0b0\uc740 \ud55c\uad6d \uc81c\uc77c\uc758 \ubbf8\ud56d\uc774\uba70 \ucda9\ubb34\uacf5\uc774 \uc65c\uc801\uc744 \uaca9\ud30c\ud55c \uc804\uc2b9\uc9c0\ub85c \uc720\uba85\ud55c \ud1b5\uc601\uacfc \uad6d\ub0b4 \uc720\uc77c\uc758 \ud574\uc800\ud130\ub110\ub85c \uc5f0\uacb0\ub418\uc5b4 \uc788\ub2e4. \uc77c\uba85 `\ud310\ub300\uad74''\ub85c \ubd88\ub9ac\ub294 \uc774 \ud574\uc800\ud130\ub110\uc740 \uc77c\uc81c\uc2dc\ub300 \uc77c\ubcf8\uc778\uc774 \uac74\uc124\ud55c \uac83\uc774\ub098 \uc608\uce21\ub41c \uc218\uba85\uc744 \ub118\uc5b4 \uc9c0\uae08\ub3c4 \uc0ac\uc6a9\ub418\uace0 \uc788\uc5b4 \ub2f9\uc2dc \uc77c\ubcf8\uc758 \uae30\uc220\ub825\uc744 \uc9d0\uc791\ud560 \uc218 \uc788\ub294 \ud45c\ubcf8\uc73c\ub85c \uac01\uad11\ubc1b\uace0 \uc788\ub2e4. <BR>\uc815\uc0c1\uc5d0 \uc624\ub974\uba74 \ube44\uc9c4\ub3c4, \uac70\uc81c\ub3c4 , \ud55c\uc0b0\ub3c4, \uce60\ucc9c\ub3c4 \ub4f1 \ud55c\ub824\uc218\ub3c4\ub97c \ubcf4\uc11d\ucc98\ub7fc \uc7a5\uc2dd\ud558\ub294 \ud06c\uace0 \uc791\uc740 \uc12c\ub4e4\uc744 \ud55c \ub208\uc5d0 \uc870\ub9dd\ud560 \uc218 \u