INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(38, TIMESTAMP '2019-08-14 15:46:34.919', TIMESTAMP '2019-08-14 15:47:02.112', '43', STRINGDECODE('\ubbfc\uc8fc\uc9c0\uc0b0(1,241.7m)\uc740 \ucd94\ud48d\ub839 \ub0a8\uc11c\ucabd \uc57d 25km \uc9c0\uc810\uc5d0 \uc788\uc73c\uba70 \uc0b0\ud589\uc758 \uae30\uc810\uc740 \uc815\uc0c1\uc758 \ub3d9\ubd81\ucabd \ubc29\ud5a5\uc778 \ud55c\ucc9c\ub9c8\uc744\uacfc \ub0a8\ucabd \uc544\ub798\uc758 \ub300\ubd88\ub9ac\ub85c \ud06c\uac8c \ub098\ub20c \uc218 \uc788\ub2e4. \uc0bc\ub3c4\ubd09, \uc11d\uae30\ubd09\uc774 \uba85\uc18c\uc774\uba70, \uc11d\uae30\ubd09 \ub3d9\ucabd\uc5d0\ub294 \uc6d0\uc2dc\uc232\uacfc \ud654\uc804\ubbfc\ud130\uac00 \uc788\uc5b4 \uc61b \uc8fc\ubbfc\ub4e4\uc758 \uc0dd\ud65c\uc0c1\uc744 \uc5ff\ubcfc \uc218 \uc788\uace0, \ubb3c\ud55c\ub9ac\uc5d0\uc11c \uba40\uc9c0 \uc54a\uc740 \uacf3\uc5d0\ub294 1972\ub144\uc5d0 \uc9c0\uc740 \ud669\ub8e1\uc0ac\uac00 \uc788\ub2e4. <BR>\uc11d\uae30\ubd09\uacfc \uc0bc\ub3c4\ubd09\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \uc8fc\ub2a5\uc120\uc740 \ubd04\uc774\uba74 \uc628\ud1b5 \uc0b0\uc8fd\uacfc \uc9c4\ub2ec\ub798\uac00 \uad70\ub77d\uc744 \uc774\ub904 \uaf43\uc0b0\ud589\uc744 \uc990\uae30\uac8c \ub41c\ub2e4. \ub2e4\ub978 \uc0b0\uc758 \uc9c4\ub2ec\ub798\uac00 \ubb34\ub9ac\uc9c0\uc5b4 \uad70\ub77d\uc744 \uc774\ub8e8\ub294\ub370 \ubc18\ud574 \uc774 \uacf3 \uc9c4\ub2ec\ub798\ub294 \ub2a5\uc120\uc744 \ub530\ub77c \ub3c4\uc5f4\ud574 \uc788\ub294 \uac83\uc774 \ud2b9\uc9d5\uc774\ub2e4. <BR>\ubb3c\ud55c\uacc4\uace1\uc744 \ub07c\uace0\uc788\uc5b4 \uc2ec\uc0b0\uc720\uace1\uc73c\ub85c \uc544\uc9c1\ub3c4 \ub54c\ubb3b\uc9c0\uc54a\uc740 \uacc4\uace1\uc774 \ub3cb\ubcf4\uc774\uba70, \uac01\uc885 \uc7a1\ubaa9\uacfc \uc9c4\ub2ec\ub798 \ucca0\ucb49 \ub4f1\uc774 \uaf49 \ub4e4\uc5b4\ucc28 \uc7a5\uad00\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4. \uc625\uc18c(\u7389\u6cbc) \uc751\uc8fc\uc554 \uc758\uc6a9\uace1\ud3ed\ud3ec \ub4f1\uc774 \uc808\uacbd\uc744 \uc774\ub8e8\uba70, \uc0bc\ub3c4\ubd09\uc5d0\ub294 \ucda9\ubd81, \uacbd\ubd81, \uc804\ubd81 \ub4f1 3\ub3c4\uc778\uc774 \ubaa8\uc5ec \uc138\uc6b4 3\ub3c4\ubd09 \ub300\ud654\ud569\ud0d1\uc774 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ubb3c\ud55c\ub9ac \ubc84\uc2a4\uc885\uc810-\ubb34\uc9c0\ub9c9\uace8-\uc11d\uae30\ubd09 \uac08\ub9bc\uae38 \uc548\ubd80-\ubbfc\uc8fc\uc9c0\uc0b0 (\ucd1d 1\uc2dc\uac04 50\ubd84)<BR>\u2461 \ub300\ubd88\ub9ac \ubc84\uc2a4\uc885\uc810-\uacc4\uace1\uac08\ub9bc\uae38-\uc0bc\ub450\ub9c8\uc560\ubd88-\uc11d\uae30\ubd09-\ubbfc\uc8fc\uc9c0\uc0b0 (3\uc2dc\uac04 30\ubd84)'), '36.099917', '127.89492', STRINGDECODE('\ucda9\uccad\ubd81\ub3c4 \uc601\ub3d9\uad70 \uc0c1\ucd0c\uba74\u318d\uc6a9\ud654\uba74, \uc804\ub77c\ubd81\ub3c4 \ubb34\uc8fc\uad70 \uc124\ucc9c\uba74, \uacbd\uc0c1\ubd81\ub3c4 \uae40\ucc9c\uc2dc \ubd80\ud56d\uba74'), 2304021500, 1242, STRINGDECODE('\ubbfc\uc8fc\uc9c0\uc0b0'), STRINGDECODE('&lt;\uc0bc\ub450\ub9c8\uc560\ubd88 \ube44\ubc00 \uac04\uc9c1\ud55c \uc0b0&gt;<BR>\ubbfc\uc8fc\uc9c0\uc0b0(1241.7m)\uc740 \ucda9\ubd81, \uacbd\ubd81, \uc804\ubd81 \ub4f1 3\ub3c4\uc5d0 \uac78\uccd0 \uc788\ub2e4. \ub3d9\uc73c\ub85c\ub294 \uc11d\uae30\ubd09\uacfc \uc0bc\ub3c4\ubd09, \ubd81\uc73c\ub85c\ub294 \uac01\ud638\uc0b0\uc774 \uc88c\uc6b0\ub85c \ub0a0\uac1c\ucc98\ub7fc \uc6b0\ub69d \uc19f\uc544\uc62c\ub77c \uc6c5\uc7a5\ud55c \uae30\uc0c1\uc744 \ud3bc\uce58\uba70 \ubc31\ub450\ub300\uac04\uc744 \uad7d\uc5b4\ubcf4\ub294 \uc0b0\uc774\ub2e4. ''\ub3d9\uad6d\uc5ec\uc9c0\uc2b9\ub78c''\uacfc ''\ub300\ub3d9\uc5ec\uc9c0\ub3c4''\uc5d0\ub294 \ubc31\uc6b4\uc0b0(\u767d\u96f2\u5c71)\uc73c\ub85c \uae30\ub85d\ub418\uc5b4 \uc788\uc73c\ub098 \uc77c\uc81c \uac15\uc810\uae30\uc5d0 \ubbfc\uc8fc\uc9c0\uc0b0\uc73c\ub85c \ubc14\ub00c\uc5c8\ub2e4. <BR>\ubb3c\ud55c\uacc4\uace1\uc744 \uc911\uc2ec\uc73c\ub85c \ud55c \ucda9\uccad\ub3c4 \ucabd \ub4f1\uc0b0\ub85c\ub294 \ubbfc\uc8fc\uc9c0\uc0b0\uc5d0\uc11c \uac00\uc7a5 \uae38\uace0 \uc544\ub984\ub2f5\ub2e4. \uc0bc\ub3c4\ubd09\uc744 \ube44\ub86f\ud55c 1000\ubbf8\ud130\uac00 \ub118\ub294 \ub124 \ubd09\uc6b0\ub9ac\uc5d0\uc11c \ud758\ub7ec\ub0b4\ub9b0 \uacc4\uace1\uc774 \ubaa8\ub450 \ubb3c\ud55c\uacc4\uace1\uc5d0\uc11c \ud569\uccd0\uc9c0\uae30\uc5d0 \uc218\ub7c9\uc774 \ud48d\ubd80\ud558\uace0 \uacbd\uad00\uc774 \uc218\ub824\ud558\ub2e4. \ud2b9\ud788 \ubb3c\ud55c\uacc4\uace1\uc5d0\ub294 \ud669\ub8e1\uc0ac \ubd80\uadfc\uc758 \uc6a9\uc18c\uc640 \uc625\uc18c\ud3ed\ud3ec, \uc758\uc6a9\uace8\ud3ed\ud3ec, \uc74c\uc8fc\uace8\ud3ed\ud3ec(\ubbf8\ub2c8\ubbf8\ud3ed\ud3ec) \ub4f1\uc774 \ubcfc \ub9cc\ud558\ub2e4. \uc0bc\ub3c4\ubd09\uacfc \uc11d\uae30\ubd09\uc744 \uac70\uccd0 \ubbfc\uc8fc\uc9c0\uc0b0 \uc815\uc0c1\uc5d0 \uc774\ub974\ub294 \ub2a5\uc120\uc5d0\ub294 \uc7a1\ubaa9\uacfc \uc9c4\ub2ec\ub798, \ucca0\ucb49 \ub4f1\uc774 \uaf49 \ub4e4\uc5b4\ucc28 \uc7a5\uad00\uc744 \uc774\ub8ec\ub2e4.'), STRINGDECODE('1000m \uc774\uc0c1\uc758 \uace0\uc0b0\uc900\ubd09\uc744 \uac70\ub290\ub9ac\uace0 \uc6b8\ucc3d\ud55c \uc0b0\ub9bc\uacfc \ubc14\uc704\uac00 \uc5b4\uc6b0\ub7ec\uc838 \uc788\uc73c\uba70, \uad6d\ub0b4 \ucd5c\ub300 \uc6d0\uc2dc\ub9bc \uacc4\uace1\uc778 \ubb3c\ud55c\uacc4\uace1\uc774 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\ubb3c\uc774 \ucc28\ub2e4\ub294 \ud55c\ucc9c\ub9c8\uc744 \uc0c1\ub958\uc5d0\uc11c\ubd80\ud130 \uc57d 20\u339e\ub97c \ud750\ub974\ub294 \uae4a\uc740 \uacc4\uace1\uc73c\ub85c, \uc6d0\uc2dc\ub9bc \ub4f1\uc774 \uc798 \ubcf4\uc874\ub41c \uc190\uaf3d\ud788\ub294 \uc0dd\ud0dc\uad00\uad11\uc9c0\uc778 \ubb3c\ud55c\uacc4\uace1(\u52ff\u9591\u6eaa\u8c37)\uc774 \ud2b9\ud788 \uc720\uba85. \uc815\uc0c1 \ub0a8\ucabd 50m\ucbe4 \uc544\ub798\uc5d0\ub294 \uc0bc\ub450\ub9c8\uc560\ubd88\uc0c1\uc774 \uc788\uc74c. \ucda9\ubd81, \uc804\ubd81, \uacbd\ubd81\uc758 \uacbd\uacc4\uc778 \uc0bc\ub3c4\ubd09\uacfc \uc5f0\uc811'), STRINGDECODE('\ud654\uc804\ubbfc\ud130\uac00 \uc788\ub294 \uc6d0\uc2dc\uc232'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ubb3c\ud55c\ub9ac \uc8fc\ubcc0\uc5d0 \ubb3c\ud55c\uc0b0\uc7a5 \u260e043-743-7644, \ud638\ub450\ub098\ubb34 \u260e043-745-3475 \ub4f1 \ubbfc\ubc15\uc9d1\uc774 10\uc5ec\uacf3\uc774 \uc788\ub2e4. <BR>\uc870\ub3d9\ub9ac\uc5d0 \ubbfc\uc8fc\uc9c0\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc \u260e043-740-3437<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ubb38\ud654\uacf5\ubcf4\uacfc \u260e043-740-3203'), STRINGDECODE('\uae30\uc810\uc740 \uc601\ub3d9, \ud669\uac04\uc774\ub2e4. <BR>\uc601\ub3d9- \ubb3c\ud55c\ub9ac 06:20~17:50 1\uc77c 5\ud68c \uc2dc\ub0b4\ubc84\uc2a4(\u260e043-742-3971) \uc6b4\ud589<BR>\ubb34\uc8fc \uc124\ucc9c\uba74\uc5d0\uc11c \ubbf8\ucc9c\ub9ac\ud589 \ubc84\uc2a4(1\uc77c 3\ud68c)\uac00 \ub300\ubd88\ub9ac \uc785\uad6c \ud558\ucc28'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(39, TIMESTAMP '2019-08-14 15:46:34.919', TIMESTAMP '2019-08-14 15:47:02.692', '46', STRINGDECODE('\ubc29\uc7a5\uc0b0\uc740 \uc804\ubd81 \uc815\uc74d\uc2dc\uc640 \uace0\ucc3d, \uc804\ub0a8 \uc7a5\uc131\uc758 \uacbd\uacc4\uc5d0 \uc19f\uc544 \uc788\ub2e4. \ub0b4\uc7a5\uc0b0\uc758 \uc11c\ucabd \uc904\uae30\ub97c \ub530\ub77c \ubed7\uce5c \ub2a5\uc120 \uc911 \uac00\uc7a5 \ub192\uc774 \uc19f\uc740 \ubd09\uc6b0\ub9ac\uc774\ub2e4. <BR>\uc9c0\ub9ac\uc0b0 \ubb34\ub4f1\uc0b0\uacfc \ud568\uaed8 \ud638\ub0a8\uc758 \uc0bc\uc2e0\uc0b0\uc73c\ub85c \ucd94\uc559\ubc1b\uc544 \uc654\uc73c\uba70 \uc8fc\uc704\uc758 \uc774\ub984\ub09c \ub0b4\uc7a5\uc0b0, \uc120\uc6b4\uc0b0, \ubc31\uc554\uc0b0\uc5d0 \ub458\ub7ec\uc2f8\uc5ec \uc788\uc73c\uba74\uc11c\ub3c4 \uae30\uc138\uac00 \ub20c\ub9ac\uc9c0 \uc54a\ub294 \ub2f9\ub2f9\ud568\uc744 \uc790\ub791\ud558\uace0 \uc788\ub2e4. \ubc29\uc7a5\uc0b0 \uc911\ud131\uc5d0\ub294 \ubc29\uc7a5\uc0b0 \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc704\uce58\ud558\uace0 \uc788\ub2e4. <BR>2000\ub144 7\uc6d4 1\uc77c \ubb38\uc744 \uc5f0 \uac83\uc73c\ub85c \uc11c\ubd80\uc9c0\ubc29 \uc0b0\ub9bc\uad00\ub9ac\uccad(\uc804\ubd81 \ub0a8\uc6d0 \uc18c\uc7ac)\uc5d0\uc11c \uc21c\ucc3d \ud68c\ubb38\uc0b0 \uc790\uc5f0\ud734\uc591\ub9bc, \ubb34\uc8fc \ub355\uc720\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc, \uc9c4\uc548 \uc6b4\uc7a5\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc, \uc7a5\ud765 \ucc9c\uad00\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc, \ud568\uc591 \uc9c0\ub9ac\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc, \ub0a8\ud574 \ud3b8\ubc31\uc790\uc5f0\ud734\uc591\ub9bc\uacfc \ud568\uaed8 \ubc29\uc7a5\uc0b0\ud734\uc591\ub9bc\uc744 \uad00\ub9ac\ud558\uace0\uc788\ub2e4. \ud734\uc591\ub9bc \ub0b4\uc5d0\ub294 \ucc38\ub098\ubb34\ub958\uc640 \uc18c\ub098\ubb34, \ud3b8\ubc31, \ub099\uc5fd\uc1a1, \ub9ac\uae30\ub2e4\uc18c\ub098\ubb34 \ub4f1\uc774 \ub9ce\uc774 \uc790\ub77c\uace0 \uc788\uc73c\uba70, \uace0\ucc3d \ubc29\uba74\uc73c\ub85c \ub09c \uc784\ub3c4\ub97c \ub530\ub77c\uac00\uba74 \ubcbd\uc624\ubd09(640m)\uacfc \uace0\ucc3d \uace0\uac1c \uc911\uac04\uc758 \ub2a5\uc120\uc5d0 \ub2ff\ub294\ub2e4. \uc774\uacf3\uc5d0\uc11c\ub294 \uace0\ucc3d \uc74d\ub0b4\uc640 \uc11c\ud574\ubc14\ub2e4\uac00 \ub0b4\ub824\ub2e4\ubcf4\uc778\ub2e4. \uace0\ucc3d\uace0\uac1c\ub97c \uc9c0\ub098 \uc7a5\uc131\uac08\uc7ac \ubc29\uba74\uc73c\ub85c \uc870\uae08 \ub354 \uac00\uba74 \ubc29\uc7a5\uc0b0 \uc815\uc0c1\uc774\ub2e4. <BR>\ubc29\uc7a5\uc0b0 \uc815\uc0c1\uc5d0 \uc624\ub974\uba74 \uc2e0\uc120\uc9c0\uacbd\uc5d0 \uc774\ub974\uba70 \uace0\ucc3d\uc74d\uc744 \ube44\ub86f\ud558\uc5ec \uad11\ud65c\ud55c \uc57c\uc0b0\uac1c\ubc1c\uc9c0\uc640 \uba40\ub9ac\ub294 \uc11c\ud574\ubc14\ub2e4\uac00 \ubcf4\uc774\uba70 \ub3d9\ucabd\uc73c\ub85c\ub294 \uad11\uc8fc \ubb34\ub4f1\uc0b0\uae4c\uc9c0 \ubcf4\uc778\ub2e4.\ud734\uc591\ub9bc\uc5d0\uc11c \uc815\uc0c1\uae4c\uc9c0\ub294 \uc655\ubcf5 3\uc2dc\uac04\uc774 \uc18c\uc694\ub418\uba70 \uc11d\uc815\uc628\ucc9c\uc73c\ub85c \uace7\uc7a5 \ud558\uc0b0\ud558\ub294 \uc0b0\uae38\ub3c4 \ub098\uc788\ub2e4. <BR>\uc8fc\ub2a5\uc120\uc5d0 \uc624\ub974\uba74 \uc11c\ud574\ub85c\ubd80\ud130 \ubd88\uc5b4\uc624\ub294 \uc2dc\uc6d0\ud55c \ubc14\ub78c\uc744 \ub9de\uc774\ud560 \uc218 \uc788\ub294\ub370 \uc774 \ub355\ubd84\uc5d0 \ud328\ub7ec\uae00\ub77c\uc774\ub529 \ub3d9\ud638\uc778\ub4e4\uc774 \uc885\uc885 \uc774\uacf3\uc744 \ucc3e\uae30\ub3c4 \ud55c\ub2e4. \uc815\uc0c1 \uc11c\ud3b8 \uc6a9\ucd94\uace8\uc5d0\ub294 \uc6a9\ucd94 \ud3ed\ud3ec\uac00 \uc788\uc73c\uba70 \ud558\uc0b0\uc9c0\uc5d0\ub294 \uac8c\ub974\ub9c8\ub284 \ucc9c\uc73c\ub85c \uc720\uba85\ud55c \uc11d\uc815\uc628\ucc9c\uc774 \uc788\uc5b4 \ud53c\ub85c\ud68c\ubcf5\uc5d0 \ub354\uc5c6\uc774 \uc88b\uace0, \uace0\ucc3d\uc740 \uc804\ud1b5\uc74c\uc2dd\uc73c\ub85c\ub3c4 \uc720\uba85\ud55c \uace0\uc7a5\uc774\ub2e4.'), STRINGDECODE('\u2460 \uc2e0\ud3c9\ub9ac-\uc6a9\ucd94\ud3ed\ud3ec-\uace0\ucc3d\uace0\uac1c-\uc815\uc0c1-\ubd09\uc218\ub300-\ub2a5\uc120 \uc0bc\uac70\ub9ac-\uc6a9\ucd94\ud3ed\ud3ec-\uc2e0\ud3c9\ub9ac(\ucd1d 3\uc2dc\uac04)<BR>\u2461 \uc591\uace0\uc0b4\uc7ac-\ubc29\uc7a5\uc0ac-579\ubd09-\ubcbd\uc624\ubd09-\uc11c\ub989-\ubbf8\ub975\uc0ac-\uc0c1\uc6d4\ub9c8\uc744(\ucd1d 3\uc2dc\uac04)<BR>\u2462 \ud734\uc591\ub9bc-\uace0\ucc3d\uace0\uac1c-\uc815\uc0c1-\ubd09\uc218\ub300-734\ubd09-\uc18c\uac08\uc7ac-\uc5f0\uc6d4\ub9ac(\ucd1d 3\uc2dc\uac04 20\ubd84)'), '35.301859', '126.784825', STRINGDECODE('\uc804\ub77c\ub0a8\ub3c4 \uc7a5\uc131\uad70, \uc804\ub77c\ubd81\ub3c4 \uace0\ucc3d\uad70 \uc2e0\ub9bc\uba74\u318d\uc815\uc74d\uc2dc \uc785\uc554\uba74'), 2501100600, 734, STRINGDECODE('\ubc29\uc7a5\uc0b0'), STRINGDECODE('&lt;\uace0\ucc3d\ubc8c\ud310\uc5d0 \uc6b0\ub69d \uc120 \ud638\ub0a8\uc815\ub9e5\uc758 \ub9dd\ub300&gt;<BR>\ud638\ub0a8\uace0\uc18d\uad6d\ub3c4\ub97c \ud0c0\uace0 \ub0a8\uc73c\ub85c \uac00\ub2e4\ubcf4\uba74 \ud638\ub0a8\ud3c9\uc57c\uac00 \ub05d\ub098\uba74\uc11c \uae38\uc774 \uc0b0\uc73c\ub85c \ub4e4\uc5b4\uac04\ub2e4. \uc67c\ucabd\uc5d0\uc11c\ub294 \uc785\uc554\uc0b0 \uc815\uc0c1\uc758 \ub18d\ubc14\uc704\uac00 \ubb38\uc778\ud654\ucc98\ub7fc \uc778\uc0c1\uc801\uc778\ub370 \uc624\ub978\ucabd\uc73c\ub85c\ub294 \ud3ec\uc7a5(\u5e03\u5e33)\ucc98\ub7fc \uc7a5\ub300\ud55c \uc0b0\uc774 \uc19f\uc544\uc788\ub2e4. \ubc29\uc7a5\uc0b0\uc774\ub2e4. <BR>\ud3c9\uc9c0\uc5d0\uc11c \ubd88\uc465 \uc19f\uad6c\uce5c \ub370\ub2e4 \ub2a5\uc120\ub4e4\uc774 \uc77c\ud544\ud718\uc9c0\ub85c \uadf8\ub9b0 \ub4ef \ub2e8\uc21c\ud558\ub2e4. \uadf8\ub9cc\ud07c \uc804\ub9dd\uc774 \uc88b\uc544 \uace0\ucc3d\uc74d\ub0b4\uc640 \uc8fc\ubcc0 \ud3c9\uc57c\ub294 \ubb3c\ub860\uc774\uace0 \uadf8\uacf3\uc758 \uba85\uc18c \ubaa8\uc591\uc131\uae4c\uc9c0, \uadf8\ub9bc\uc9c0\ub3c4\ucc98\ub7fc \uc0dd\uc0dd\ud558\uac8c \ubcf4\uc778\ub2e4. ''\uc808\uc9d1\uc758 \uc6d0\ub85c''\ub77c\ub294 \ub73b\uc758 \uc774\ub984\uc774\uc9c0\ub9cc \uad00\ub828\uc740 \uc5c6\ub2e4. \ubc31\uc81c\ub54c\ub294 \ubc29\ub4f1\uc0b0(\u65b9\u7b49\u5c71)\uc73c\ub85c \ubd88\ub838\ub2e4\uace0 \ud558\ub294\ub370 \uadf8\uac8c \uad6c\uac1c\uc74c\ud654\ub418\uc5b4 \ubc29\uc99d\uc0b0, \ubaa8\uc74c\ub3d9\ud654\ub418\uc5b4 \ubc29\uc7a5\uc0b0\uc73c\ub85c \ubc14\ub010 \ub4ef\ud558\ub2e4. ''\ubaa8 \ubc29''\uc790\ub2c8 \uc6d0\ub798\ub294 \ubaa8\ub4f1\uc774\uc5c8\uc744 \uac83\uc774\ub2e4. \uace0\ucc3d\uc758 \uc61b\uc774\ub984\uc774 \ubaa8\uc591\ubd80\ub9ac\ub2c8 ''\ubaa8\uc591\uc758 \ub4f1''\uc774\ub77c\ub294 \ub73b\uc774\uc5c8\uaca0\ub2e4. <BR>\uc0c1\ubd09 \uc11c\ub0a8\ucabd\uc758 \ubcbd\uc624\ubd09\uc744 \ubc29\ubb38\uc0b0\uc774\ub77c\uace0\ub3c4 \ud558\ub294\ub370 \uc5b4\ucc98\uad6c\ub2c8 \uc5c6\ub294 \uc624\ub958\ub2e4. \uad6d\ub9bd\uc9c0\ub9ac\uc6d0\uc774 \ub9cc\ub4e0 5\ub9cc\ubd84\uc758 1 \uc9c0\ub3c4 ''\uace0\ucc3d''\uacfc ''\ub2f4\uc591''\uc758 \uacbd\uacc4\uac00 \ub450 \ubd09\uc6b0\ub9ac \uc0ac\uc774\ub85c \uc9c0\ub098\uac00\ub294\ub370 \uc0c1\ubd09\uc774 \uc788\ub294 ''\ub2f4\uc591''\uc9c0\ub3c4\uc5d0\ub294 \uc774\ub984\uc774 \uc81c\ub300\ub85c \ub418\uc5c8\uc9c0\ub9cc \uc61b\ub0a0 ''\uace0\ucc3d''\uc9c0\ub3c4\uc5d0\ub294 \uc2dd\uc790\uacf5\uc774 ''\uc5b4\ub978 \uc7a5''\uacfc \ube44\uc2b7\ud55c ''\uae00\uc6d4 \ubb38''\uc790\ub97c \uccd0\ub123\uc5b4 \ubc29\ubb38\uc0b0\uc774 \ub41c \uac83\uc774\ub2e4. \uadf8\ub7f0\ub370 \uc774\ub97c \ubaa8\ub974\uace0 \ub9ce\uc740 \uc0ac\ub78c\ub4e4\uc774 \ubc29\ubb38\uc0b0\uc73c\ub85c \uc54c\uace0\uc788\ub294 \ubc14 \ubcc0\uc0b0 \uc758\uc0c1\ubd09\uc758 ''\uc758(\u501a)''\uc790\ub97c \uc798\ubabb \uc77d\uc5b4 \ucd5c\uace0\ubd09\uc774 \uc624\ub7ab\ub3d9\uc548 ''\uae30\uc0c1\ubd09''\uc73c\ub85c \ubd88\ub824\uc654\ub358 \uac83\uacfc \ube44\uc2b7\ud55c \ucc98\uc9c0\ub2e4.'), STRINGDECODE('\uc61b\ubd80\ud130 \uc9c0\ub9ac\uc0b0, \ubb34\ub4f1\uc0b0\uacfc \ud568\uaed8 \ud638\ub0a8\uc758 \uc0bc\uc2e0\uc0b0\uc73c\ub85c \ubd88\ub824\uc838 \uc654\uc73c\uba70, \uc804\ubd81\uacfc \uc804\ub0a8\uc744 \uc591\ubd84\ud558\ub294 \uc0b0\uc73c\ub85c\uc11c \uc0b0\uc138\uac00 \uc6c5\uc7a5\ud558\uace0 \uc790\uc5f0\ud734\uc591\ub9bc\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815. \uc61b\uc774\ub984\uc740 \ubc29\ub4f1\uc0b0\uc73c\ub85c \ubc31\uc81c\uac00\uc694\uc911 \"\ubc29\ub4f1\uc0b0\uac00\"\uc758 \ubc29\ub4f1\uc0b0\uc774 \ubc14\ub85c \ubc29\uc7a5\uc0b0\uc784. \uc815\uc0c1\uc5d0\uc11c \uba40\ub9ac \uc11c\ud574\ubc14\ub2e4\uc640 \ub3d9\ucabd\uc73c\ub85c \ubb34\ub4f1\uc0b0\uc774 \ubcf4\uc784'), STRINGDECODE('\ub098\uadf8\ub124\uc758 \ubd07\uc9d0\uc744 \ud138\ub358 \ub3c4\uc801\ub5bc\uc758 \uc740\ub454\uc9c0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uace0\ucc3d\uc74d\ub0b4\uc758 \uc219\ubc15\uc2dc\uc124\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR>\ub3d9\ubc29\ud638\ud154 \u260e063-563-7070, <BR>\uadf8\ub9b0\ud30c\ud06c\uc7a5 \u260e063-562-5900, <BR>\uc6cc\ucee4\ud790 \u260e063-561-5358 \ub4f1. <BR>\ubc29\uc7a5\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc\uc5d0\ub294 7\u00b710\u00b716\u00b720\ud3c9\ud615 \ubc29\uac08\ub85c\uac00 \uc788\ub2e4. \u260e061-394-5523<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ubd80\uc548\uad70\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e063-580-4191 \uc7a5\uc131\uad70\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e061-390-7224'), STRINGDECODE('\uace0\ucc3d\uc774 \uae30\uc810\uc774\ub2e4. \uace0\ucc3d\uc5d0\uc11c \uc0c1\uc6d4\uc774\ub098 \uc6d4\uc554, \uc11d\uc815\uc628\ucc9c\ud589\uc740 30\ubd84 \uac04\uaca9\uc73c\ub85c \uc788\uace0 \uc2e0\ud3c9\ub9ac\uc758 \uacbd\uc6b0 6\ud68c \uc788\ub294 \uc815\uc74d\ud589\uc744 \ud0c4\ub2e4. <BR>\uace0\ucc3d\uc73c\ub85c\ub294 \uc11c\uc6b8 \uac15\ub0a8\ud130\ubbf8\ub110\uc5d0\uc11c \uace0\uc18d\ubc84\uc2a4\uac00 16\ud68c \uc788\ub2e4. \uc804\ub77c\ubd81\ub3c4 \uc774\uc678 \ub3c4\uc2dc\ub85c\ub294 \uad11\uc8fc\uc640 \ubaa9\ud3ec\uc5d0\uc11c \ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4. <BR>\ud734\uc591\ub9bc\uc73c\ub85c \uac08 \uacbd\uc6b0\uc5d0\ub294 \ud638\ub0a8\uc120 \ubc31\uc591\uc0ac\uc5ed\uc5d0\uc11c, \uac08\uc7ac\ub97c \ub4e4\ub0a0\uba38\ub9ac\ub85c \ud560 \uacbd\uc6b0\uc5d0\ub294 \uc815\uc74d\uc5d0\uc11c \ud0dd\uc2dc\ub97c \uc774\uc6a9\ud55c\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(40, TIMESTAMP '2019-08-14 15:46:34.92', TIMESTAMP '2019-08-14 15:47:03.278', '42', STRINGDECODE('\uad6d\ub0b4 \ucd5c\ub300\uc758 \uba74\uc801\uc744 \uc790\ub791\ud558\ub294 \uc790\uc5f0\ud734\uc591\ub9bc\uc744 \uac70\ub290\ub9ac\uace0 \uc788\ub294 \ubc29\ud0dc\uc0b0\uc740 \uac15\uc6d0\ub3c4 \uc778\uc81c\uad70\uacfc \ud64d\ucc9c\uad70\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c, \uad50\ud1b5\uc774 \ubd88\ud3b8\ud55c \uad00\uacc4\ub85c \uc544\uc9c1\ub3c4 \uc624\uc5fc\ub418\uc9c0 \uc54a\uc740 \uae68\ub057\ud55c \uacc4\uace1\uc744 \uac04\uc9c1\ud558\uace0 \uc788\ub2e4. <BR><BR>\uccad\uc815\ud55c \uc790\uc5f0\ub9bc\uc5d0 \ub4e4\uc5b4\uc11c\uba74 \ub3c4\uc2ec\uc5d0\uc11c \ubd88\uacfc \uba87\uc2dc\uac04 \uac70\ub9ac\ubc16\uc5d0 \ub5a8\uc5b4\uc838 \uc788\uc9c0 \uc54a\ub2e4\ub294 \uc0ac\uc2e4\uc774 \ubbff\uc5b4\uc9c0\uc9c0 \uc54a\ub294\ub2e4. \ube7d\ube7d\ud55c \ub098\ubb34\ub4e4 \uc0ac\uc774\uc5d0 \ub204\uc6cc \ud558\ub298\uc744 \uc62c\ub824\ub2e4\ubcf4\uba74 \ud55c\uc904\uae30\uc758 \ud587\uc0b4\ub3c4 \ud5c8\uc6a9\ud558\uc9c0 \uc54a\ub294 \uc218\ub9bc\uc758 \uae4a\uc774\uac00 \ub290\uaef4\uc9c4\ub2e4.'), STRINGDECODE('\u2460 \uc0b4\ub454-\uc22b\ub3cc\ubd09-\uac1c\uc778\uc0b0-\uad6c\ub8e1\ub355\ubd09-\uc815\uc0c1-\ub2a5\uc120\uc0bc\uac70\ub9ac-\uac1c\uc778\uc57d\uc218-\ub300\uac1c\uc778\ub3d9 (\ucd1d 9\uc2dc\uac04 10\ubd84)<BR>\u2461 \ub300\uac1c\uc778\ub3d9-\uac1c\uc778\uc57d\uc218-\ubc30\ub2ec\uc740\uc11d-\uae43\ub300\ubd09-\ub9e4\ud654\ub3d9\uacc4\uace1 \ubaa8\ub364-\uc1a1\uc5b4\uc591\uc2dd\uc7a5-\ub9e4\ud654\ub3d9 (\ucd1d 6\uc2dc\uac04 20\ubd84)<BR>\u2462 \ubc29\ud0dc\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-\ub9c8\ub2f9\ubc14\uc704\uace8-\uad6c\ub8e1\ub355\ubd09-\uc815\uc0c1-\uae43\ub300\ubd09-1073\ubd09-\uacc4\uace1 \uc544\uc6b0\ub77c\uc9c0 (\ucd1d 8\uc2dc\uac04 20\ubd84)'), '37.96428', '128.389234', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc778\uc81c\uad70 \uae30\ub9b0\uba74, \uc0c1\ub0a8\uba74'), 2210011400, 1446, STRINGDECODE('\ubc29\ud0dc\uc0b0'), STRINGDECODE('&lt;\uc57d\uc218 \ub9ce\uace0 \uc2b9\uc9c0 \ub9ce\uc740 \ud55c\uad6d\uc801 \uc720\ud1a0\ud53c\uc544 &gt;<BR>\uc774\ub545\uc5d0\uc11c \uc2dd\ubb3c\uc0b4\uc774\uac00 \uac00\uc7a5 \uc88b\uc740 \uc0b0\uc774\ub2e4. \uace0\ub7ab\uc7ac \uac19\uc740 \ud759\uc5d0 \ub36e\uc778 \ud6c4\ub355\ud55c \uc721\uc0b0\uc758 \uc2f8\ub9ac\ub098\ubb34, \ubcf5\uc790\uae30, \uc18c\ub098\ubb34, \uc0b0\ubc9a\ub098\ubb34, \ub9cc\ubcd1\ucd08\u2026 \uc218\uc885(\u6a39\u7a2e) \uc804\uc2dc\uc7a5\ucc98\ub7fc \ub2e4\uc591\ud55c \ub098\ubb34\ub4e4\uacfc \uace0\uc0ac\ub9ac, \uad00\uc911, \uc6b0\uc0b0\ub098\ubb3c, \uacf0\ucde8, \uc218\ub9ac\ucde8, \uc5bc\ub808\uc9c0, \uc5f0\ub839\ucd08, \uc591\uc9c0\uaf43, \ud640\uc544\ube44\ubc14\ub78c\uaf43\u2026. <BR>\ud558\ub9c8\ub4f1\ucc98\ub7fc \ub108\ub974\ud3c9\ud3c9\ud55c \ub2a5\uc120 \ubd81\ucabd \uae30\uc2ad\uc5d0\ub294 \uc801\uac00\ub9ac\uc640 \uc544\uce68\uac00\ub9ac\ub77c\ub294 \uc2b9\uc9c0\uac00 \uc788\ub2e4. \uc774\ud3ed\ud3ec\uc800\ud3ed\ud3ec\uac00 \ud2c0\uc5b4\ub9c9\uc740 \uc804\ubcf5\uaecd\uc9c8 \ubaa8\uc591\uc758 \uc801\uac00\ub9ac \uc548\ud1b5\uc5d0\ub294 \ud3c9\ud3c9\ud55c \ub300\uc9c0\uac00 \ud3bc\uccd0\uc84c\uace0 \ud65c\ucc98\ub7fc \ud718\uc6b0\ub4ec\ud55c \uc544\uce68\uac00\ub9ac \uace8\uc9dc\uae30\ub294 \ubc29\ub3d9\uc57d\uc218 \ub4b7\uc7ac\uac00 \uc544\ub2c8\uba74 \uae38\uc774 \uc5c6\ub2e4. <BR>\ub0a8\ucabd\uc5d0\ub294 \uad81\uad81\uc744\uc744(\u5f13\u5f13\u4e59\u4e59) \ub0b4\ub9b0\ucc9c\uc774 \uc0b0\uc790\ub77d\uc744 \uc53b\uc73c\uba70 \ud750\ub978\ub2e4. \uc885\uc774\uae54\ub54c\uae30 \ubaa8\uc591\uc758 \uc5b4\ub450\uc6b0\ub2c8 \uace8\uc9dc\uae30\ub294 9\ub144 \uc0b0\ud310\uc744 \ud588\ub2e4\ub294\ub370\ub3c4 \uc544\uc9c1 \uc5b4\ub461\ub2e4. \uadf8 \uc5b4\ub450\uc6c0\uc774 \ubfdc\uc5b4\ub0b4\ub294 \ub9d1\uc740 \ubb3c \ub0b4\ub9b0\ucc9c\uc73c\ub85c \ud758\ub7ec \ubcf4\ud0dc \uc11c\ucabd\uc73c\ub85c \uac14\ub2e4 \ubd81\ucabd\uc73c\ub85c \ud2c0\uc5c8\ub2e4 \uc11c\ub0a8\ucabd\uc73c\ub85c \ub098\uc544\uac00 3\ub454 5\uac08 \uc5ec\ub35f \uad70\ub370 \uc228\uc5b4\uc0b4\uc774\ub97c \ub9cc\ub4e4\uace0\uc788\ub2e4. \ube7c\uc5b4\ub098\uc9c0\ub294 \uc54a\uc9c0\ub9cc \ub09c\ub9ac\ud1b5\uc5d0 \uc0ac\ub78c \uc0b4\ub9ac\uae30\uc5d0\ub294 \ub354\ud560 \ub098\uc704 \uc5c6\uc774 \uc88b\uace0 \ubb3c \ub9d1\uc73c\uba70 \ud478\uadfc\ud55c, \uc544\uc8fc \uc18d \uae4a\uc740 \uc0b0\uc774\ub2e4.'), STRINGDECODE('\uac00\uce60\ubd09(1,241.1m), \uc751\ubcf5\uc0b0(1,156m), \uad6c\ub8e1\ub355\ubd09(1,389m), \uc8fc\uac71\ubd09(1,444m) \ub4f1 \uace0\uc0b0\uc900\ubd09\uc744 \uac70\ub290\ub9ac\uace0 \uc788\uc73c\uba70 \ud55c\uad6d\uc5d0\uc11c \uac00\uc7a5 \ud070 \uc790\uc5f0\ub9bc\uc774\ub77c\uace0 \ud560 \uc815\ub3c4\ub85c \ub098\ubb34\ub4e4\uc774 \uc6b8\ucc3d\ud558\uace0, \ud76c\uadc0\uc2dd\ubb3c\uacfc \ud76c\uadc0\uc5b4\uc885\uc774 \ub9ce\uc740 \uc0dd\ud0dc\uc801 \ud2b9\uc131 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815\uc815\uac10\ub85d\uc5d0\ub294 \ub09c\uc744 \ud53c\ud574 \uc228\uc744\ub9cc\ud55c \ud53c\ub09c\ucc98\ub85c \uae30\ub85d\ub418\uc5b4 \uc788\uc74c. \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc788\uc73c\uba70, \ub192\uc774 10m\uc758 \uc774\ud3ed\ud3ec\uc640 3m\uc758 \uc800\ud3ed\ud3ec\uac00 \uc788\ub294 \uc801\uac00\ub9ac\uace8 \ubc0f \ubc29\ub3d9\uc57d\uc218, \uac1c\uc778\uc57d\uc218 \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\uae4a\uc740 \uc0b0\uc18d \uc639\ub2ec\uc0d8'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub300\uac1c\uc778\ub3d9\ucabd\uc740 \ubbf8\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e033-461-0866), \uc801\uac00\ub9ac\ucabd\uc740 \ubc29\ud0dc\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e033-463-8590)\uc774 \uc88b\uc9c0\ub9cc \uc131\uc218\uae30\uc5d0\ub294 \uc608\uc57d\uc774 \uc5b4\ub835\ub2e4. <BR>\uac1c\uc778\uc0b0\uc7a5 \u260e033-463-1700 \ub0a8\uc804\ub3d9 \uac15\ub098\ub8e8\ubbfc\ubc15 \u260e033-463-1619 \ubc29\ub3d9\uc57d\uc218\uc0b0\uc7a5 \u260e033-463-5094 \uaf43\ud53c\ub294\uc0b0\uace8 \u260e033-463-7397 \uc0b4\ub454\ucabd\uc740 \ud558\uc580\uc9d1 \u260e033-435-8975<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc778\uc81c\uad70\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e033-460-2080'), STRINGDECODE('\uae30\uc810\uc740 \ud64d\ucc9c\uc774\ub2e4. \uc801\uac00\ub9ac \uac00\uae4c\uc6b4 \ud604\ub9ac\ub85c \uc644\ud589\uc774 15\ud68c, \uc9c1\ud589\uc740 1\uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud55c\ub2e4. \ubbf8\uc0b0\ub9ac\ub85c \uac08 \uacbd\uc6b0\uc5d0\ub294 \uc911\uac04\uc5d0 \uc0c1\ub0a8\uc5d0\uc11c \ub0b4\ub824 \ud0dd\uc2dc\ub97c \ud0c4\ub2e4. \uc0b4\ub454\uc744 \ub4e4\uba38\ub9ac\ub85c \uc7a1\uc744 \ub54c\ub294 \uc9c1\ud589 8\ud68c, \uc644\ud589 4\ud68c\uc758 \ub0b4\uba74\ud589 \ubc84\uc2a4\ub97c \uc774\uc6a9\ud55c\ub2e4. \ud64d\ucc9c\ud589 \ubc84\uc2a4\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 52\ud68c, \uc0c1\ubd09\ud130\ubbf8\ub110\uc5d0\uc11c 41\ud68c \uc788\ub2e4. \uac15\uc6d0\ub3c4 \uc774\uc678\uc758 \ub3c4\uc2dc\ub85c\ub294 \ubd80\uc0b0, \uc6b8\uc0b0, \ud3ec\ud56d, \ub300\uad6c, \uc548\ub3d9, \ub300\uc804, \uc804\uc8fc, \uc778\ucc9c, \uc218\uc6d0, \uc548\uc0b0, \uace0\uc591, \uc758\uc815\ubd80\uc5d0\uc11c \ubc84\uc2a4\uac00 \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(41, TIMESTAMP '2019-08-14 15:46:34.92', TIMESTAMP '2019-08-14 15:47:03.864', '42', STRINGDECODE('\ubc31\ub355\uc0b0\uc740 \uc774\ub984 \uadf8\ub300\ub85c \ud558\uc580 \ub208\uc774 \uc313\uc600\uc744 \ub54c \uc808\uacbd\uc744 \uc774\ub8e8\ub294 \uc0b0\uc774\ub2e4. <BR>\uaca8\uc6b8\uc774\uba74 \ubb34\ub98e\uae4c\uc9c0 \ube60\uc9c0\ub3c4\ub85d \ub9ce\uc740 \ub208\uc774 \ub0b4\ub9b0\ub2e4. 1\ucc9cm \uc774\uc0c1\uc758 \uc8fc\ub2a5\uc120 \ubd09\uc624\ub9ac\ub9c8\ub2e4 \ud53c\uc5b4\ub098\ub294 \uc124\ud654(\u96ea\u82b1)\uac00 \uc740\ubc31\uc0c9\uc758 \uc138\uacc4\ub85c \ube60\uc838\ub4dc\ub294 \ud658\uc0c1\uc744 \ub290\ub07c\uac8c \ud55c\ub2e4.'), STRINGDECODE('\u2460 \uad00\uc74c\uc0ac-\ubc31\ub828\uad11\uc0b0\ud130-\uc8fc\uacc4\uace1\uae38-\uc815\uc0c1-\ubb35\uace8 (\ucd1d 5\uc2dc\uac04 25\ubd84)<BR>\u2461 \ubb38\uc7ac-\uc0ac\uc790\uc0b0-\ub2f9\uc7ac-\uc815\uc0c1-\ub2f9\uc7ac-\uc6b4\uad50 (\ucd1d 4\uc2dc\uac04 15\ubd84)'), '37.452185', '128.326413', STRINGDECODE('\uac15\uc6d0\ub3c4 \ud3c9\ucc3d\uad70 \ubc29\ub9bc\uba74, \ud6a1\uc131\uad70 \uc548\ud765\uba74, \uc601\uc6d4\uad70 \uc218\uc8fc\uba74'), 2215081700, 1350, STRINGDECODE('\ubc31\ub355\uc0b0'), STRINGDECODE('&lt;\uc124\uacbd \ube7c\uc5b4\ub09c \ucc9c\uc5f0\uc6d0\uc2dc\ub9bc\uc758 \uc0b0 &gt;<BR>\ubc31\ub355\uc0b0\uc740 \ud3c9\ucc3d\uad70 \ubc29\ub9bc\uba74\uacfc \ud3c9\ucc3d\uc74d, \uc601\uc6d4\uad70 \uc8fc\ucc9c\uba74\uacfc \uc218\uc8fc\uba74, \ud6a1\uc131\uad70 \uc548\ud765\uba74\uc758 \uacbd\uacc4\uc5d0 \uc19f\uc544\uc788\ub294 \ud574\ubc1c 1350.1m\uc758 \uc0b0\uc774\ub2e4. \ucc9c\uc5f0 \uc6d0\uc2dc\ub9bc\uc744 \uac04\uc9c1\ud55c \uc8fc\uacc4\uace1\uacfc \ud568\uaed8 \uc124\uacbd\uc774 \ub6f0\uc5b4\ub098 \uaca8\uc6b8\ucca0 \uc0b0\ud589\uc9c0\ub85c \uc720\uba85\ud558\ub2e4. \ub354\uc6b1\uc774 \ubc31\ub355\uc0b0 \ub0a8\uc11c\ucabd \uc5f0\ud654\ubd09 \uc544\ub798\uc5d0 \uc124\uc545\uc0b0 \ubd09\uc815\uc554, \uc624\ub300\uc0b0 \uc0c1\uc6d0\uc0ac, \uc601\ucde8\uc0b0 \ud1b5\ub3c4\uc0ac, \uc815\uc554\uc0ac \ud0dc\ubc31\uc0b0\uacfc \ud568\uaed8 5\ub300 \uc801\uba78\ubcf4\uad81 \uc911 \ud558\ub098\uc778 \uc2e0\ub77c\uc2dc\ub300 \ucc9c\ub144\uace0\ucc30 \ubc95\ud765\uc0ac\uac00 \uc788\uc5b4 \ub354\uc6b1 \uc720\uba85\ud558\ub2e4.<BR>\ubc31\ub355\uc0b0\uc744 \uc624\ub974\ub0b4\ub9ac\ub294 \uae38\ubaa9\uc778 \uc0ac\uc790\uc0b0(1180.4m)\uc740 \uc6d0\ub798 \uc0ac\uc7ac\uc0b0(\u56db\u8ca1\u5c71)\uc774\uc5c8\ub2e4. \uc774\ub294 \uc63b, \uafc0, \uc0bc, \ud749\ub144\uc5d0 \uba39\ub294\ub2e4\ub294 \uc804\ub2e8\ud1a0 \ub4f1 4\uac00\uc9c0 \ubcf4\ubb3c\uc774 \uc788\uc5b4 \uc0ac\uc7ac\uc0b0\uc5d0\uc11c\ub294 \uad76\uc5b4 \uc8fd\uc9c0 \uc54a\ub294 \uc804\uc124\uc5d0\uc11c \ub098\uc628 \uc774\ub984\uc774\ub2e4. \uadf8\ub7ec\ub098 \ubc95\ud765\uc0ac\ub97c \ucc3d\uac74\ud55c \uc790\uc7a5\uc728\uc0ac\uac00 \ubd80\ucc98\ub2d8\uc758 \uc0ac\ub9ac\ub97c \uc9c0\uae08\uc758 \uc801\uba78\ubcf4\uad81\uc73c\ub85c \ubaa8\uc154\uc624\uba74\uc11c \uc0ac\uc790\ub97c \ud0c0\uace0 \uc654\ub2e4 \ud558\uc5ec \uc0ac\uc790\uc0b0\uc73c\ub85c \ubd88\ub9ac\uace0 \uc788\ub2e4.'), STRINGDECODE('\uc0ac\uc790\uc0b0(1180.4m), \uc0bf\uac13\ubd09(1028.8m), \uc19f\ub9e4\ubd09(882.7m) \ub4f1\uc774 \uc19f\uc544 \uc788\uc5b4 \uc0b0\uc138\uac00 \uc6c5\uc7a5\ud558\uace0 \uace8\uc774 \uae4a\uc740 \ub4f1 \uacbd\uad00\uc774 \uc88b\uc73c\uba70, \ud3c9\ucc3d\uac15(\u5e73\u660c\u6c5f)\uacfc \uc8fc\ucc9c\uac15(\u9152\u6cc9\u6c5f)\uc758 \uc218\uacc4\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc2e0\ub77c \ub54c \uc790\uc7a5\uc728\uc0ac\uac00 \ucc3d\uac74\ud558\uc600\ub2e4\uace0 \uc804\ud574\uc9c0\ub294 \ubc95\ud765\uc0ac(\u6cd5\u8208\u5bfa)\uc640 \uacbd\ub0b4\uc5d0 \uc788\ub294 \ubcf4\ubb3c \uc81c612\ud638\ub85c \uc9c0\uc815\ub41c \uc9d5\ud6a8\ub300\uc0ac\ubcf4\uc778\ud0d1\uc774 \uc720\uba85'), STRINGDECODE('\uc8fc\ub2a5\uc120\uc5d0 \ud53c\uc5b4\ub098\ub294 \uc124\ud654'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc0b0\ud589 \uae30\uc810\uc778 \ud3c9\ucc