INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(42, TIMESTAMP '2019-08-14 15:46:34.921', TIMESTAMP '2019-08-14 15:47:04.461', '45', STRINGDECODE('\ubc31\uc554\uc0b0\uc740 \ub4dc\ub113\uc740 \ud638\ub0a8\ud3c9\uc57c\ub97c \ub9c8\uc8fc\ud558\uace0 \uc19f\uc544\uc624\ub978 \ub192\uc774 741.2m\uc758 \uc0b0\uc73c\ub85c \ub0b4\uc7a5\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\uc5d0 \uc18d\ud55c\ub2e4. <BR>\uc61b\ubd80\ud130 \ubd04\uc774\uba74 \ubc31\uc591, \uac00\uc744\uc774\uba74 \ub0b4\uc7a5\uc774\ub77c \ud588\ub4ef\uc774 \uc0b0\ud558\uba74 \ub0b4\uc7a5, \uace0\uc801\ud558\uba74 \ubc31\uc554\uc774\ub77c \ud560 \uc815\ub3c4\ub85c \ubc31\uc554\uc0b0\uc758 \uc808\uacbd\uc740 \ub0b4\uc7a5\uc0b0\uc5d0 \ub4a4\uc9c0\uc9c0 \uc54a\ub294\ub2e4. \ubc31\ud559\ubd09\uacfc \uc0c1\uc655\ubd09, \uc0ac\uc790\ubd09, \ub4f1\uc758 \uae30\uc554\uad34\uc11d\uc774 \uacf3\uacf3\uc5d0 \uc788\uc73c\uba70, \uc0b0\uc138\uac00 \ud5d8\uc900\ud55c \ud3b8\uc774\ub2e4. \ud2b9\ud788 \ube44\uc790\ub098\ubb34\uc232\uacfc \ud68c\uc0c9 \uc904\ubb34\ub2ac \ub2e4\ub78c\uc950\uac00 \uc720\uba85\ud55c \uc774\uacf3\uc5d0\ub294 \ub300\ud55c \ubd88\uad50 \uc870\uacc4\uc885 18\uad50\uad6c \ubcf8\uc0b0\uc778 \ub300\uc0ac\ucc30 \ubc31\uc591\uc0ac\ub3c4 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ubc31\uc591\uc0ac-\uc57d\uc0ac\uc554-\ud559\ubc14\uc704-\ubc31\ud559\ubd09-\uc0c1\uc655\ubd09-\uc0ac\uc790\ubd09-\uccad\ub958\uc554-\uac00\uc778\uc57c\uc601\uc7a5-\ub9e4\ud45c\uc18c (\ucd1d 5\uc2dc\uac04 10\ubd84)<BR>\u2461 \ubc31\uc591\uc0ac-\uc6b4\ubb38\uc554-\uc0ac\uc790\ubd09-\uccad\ub958\uc554-\uac00\uc778\uc57c\uc601\uc7a5-\ub9e4\ud45c\uc18c (\ucd1d 3\uc2dc\uac04 40\ubd84)<BR>\u2462 \ubc31\uc591\uc0ac-\uc6b4\ubb38\uc554-\uc0c1\uc655\ubd09-\ubc31\ud559\ubd09-\uc57d\uc0ac\uc554-\ubc31\uc591\uc0ac (\ucd1d 4\uc2dc\uac04 10\ubd84)'), '35.445122', '126.93622', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \uc21c\ucc3d\uad70 \ubcf5\ud765\uba74, \uc804\ub77c\ub0a8\ub3c4 \uc7a5\uc131\uad70 \ubd81\ud558\uba74'), 2617051500, 741, STRINGDECODE('\ubc31\uc554\uc0b0'), STRINGDECODE('&lt;\uce35\uaf43\ub098\ubb34 \ubb34\ub9ac\uc9d3\ub294 \ub2e8\ud48d\uc758 \uba85\uc0b0&gt;<BR>\ubc31\uc554\uc0b0\uc740 \ub0b4\uc7a5\uc0b0,\uc785\uc554\uc0b0\uacfc \ud568\uaed8 \ub0b4\uc7a5\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc5d0 \uc18d\ud574 \uc788\ub2e4. \uae30\uc554\uad34\uc11d\uc73c\ub85c \uc774\ub8e8\uc5b4\uc9c4 \uc0ac\uc790\ubd09\uacfc \ubc31\ud559\ubd09\uc744 \uc0ac\uc774\uc5d0 \ub450\uace0 \uc0c1\uc655\ubd09\uc774 \uc6b0\ub69d \uc19f\uc544 \ub9de\uc740\ud3b8 \ub0b4\uc7a5\uc0b0 \uc2e0\uc120\ubd09\uacfc \uc5b4\uae68\ub97c \uaca8\ub8ec\ub2e4. \uc608\ubd80\ud130 \ubc31\uc591\uc0ac\uc758 \ube44\uc790\ub098\ubb34\uc232\uacfc \ubc9a\uaf43\ub098\ubb34\ub294 \uac00\uc744\uc758 \ub0b4\uc7a5\uc0b0 \ub2e8\ud48d\uacfc \ub354\ubd88\uc5b4 \uc720\uba85\ud558\uba70 \uc0b0\uc138\uac00 \ubd80\ub4dc\ub7ec\uc6cc \uc5b4\ub835\uc9c0 \uc54a\uac8c \ub4f1\uc0b0\uc744 \uc990\uae38 \uc218 \uc788\ub2e4.<BR>\uc0b0 \uae30\uc2ad\uc5d0\ub294 \ub300\ud55c\ubd88\uad50\uc870\uacc4\uc885 18\uad50\uad6c \ubcf8\uc0ac\uc778 \ubc31\uc591\uc0ac\uac00 \uc788\ub294\ub370 632\ub144(\ubb34\uc655 33\ub144) \ucc3d\uac74\ud558\uc5ec \ubc31\uc554\uc0ac\ub77c \ubd80\ub974\ub2e4 \uc870\uc120 \uc120\uc870\ub54c \ud658\uc591\uc774 \ubc31\uc591\uc0ac\ub77c \uace0\uccd0\ubd88\ub800\ub2e4\uace0 \ud55c\ub2e4. \uc804\uc124\uc5d0\ub294 \ud658\uc591\uc774 \ubc31\ud559\ubd09 \uc544\ub798\uc5d0\uc11c \uc81c\uc790\ub4e4\uc5d0\uac8c \uc124\ubc95\ud558\uace0 \uc788\uc744\ub54c \ubc31\uc591 \ud55c\ub9c8\ub9ac\uac00 \uc774\ub97c \ub4e3\uace0 \uae68\uc6b0\uce68\uc758 \ub208\ubb3c\uc744 \ud758\ub824 \ubc31\uc591\uc0ac\ub77c \ubd88\ub800\ub2e4\uace0\ub3c4 \uc804\ud574\uc9c4\ub2e4.'), STRINGDECODE('\ubd04\uc774\uba74 \ubc31\uc591, \uac00\uc744\uc774\uba74 \ub0b4\uc7a5\uc774\ub77c \ud558\ub4ef\uc774 \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0 \ucc9c\uc5f0\uae30\ub150\ubb3c\uc778 \ube44\uc790\ub098\ubb34\uc640 \uad74\uac70\ub9ac\ub098\ubb34\uac00 \uc9d1\ub2e8\ubd84\ud3ec\ud558\uace0 \uc788\uc73c\uba70, \ub0b4\uc7a5\uc0b0