INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(43, TIMESTAMP '2019-08-14 15:46:34.921', TIMESTAMP '2019-08-14 15:47:05.106', '46', STRINGDECODE('\ubc31\ub450\uc0b0\uc5d0\uc11c \uc2dc\uc791\ub418\ub294 \ubc31\ub450\ub300\uac04\uc740 \ub3d9\ud574\uc548 \ub530\ub77c \ubed7\uc5b4 \ub0b4\ub9ac\ub2e4 \uac15\uc6d0\ub3c4 \ud0dc\ubc31 \ud53c\uc7ac\uc5d0\uc11c \ub0a8\uc11c \ud5a5\uc73c\ub85c \ubc29\ud5a5\uc744 \ud2c0\uc5b4 \ud55c\ubc18\ub3c4\uc758 \ub0b4\ub959\uc744 \ub530\ub77c \ub0a8\ud574\uc548\uc73c\ub85c \ub2ec\ub824\uac04\ub2e4. \ubc31\uc6b4\uc0b0\uc740 \ubc31\ub450\ub300\uac04\uc5d0\uc11c \ubed7\uc5b4 \ub098\uc628 \ud638\ub0a8\uc815\ub9e5\uc774 \ub0a8\ud574\ubc14\ub2e4\ub85c \uc7a0\ub4e4\uae30\uc804 \ub9c8\uc9c0\ub9c9 \ud798\uc744 \ub2e4\ud574 \ubc00\uc5b4 \uc62c\ub9b0 \uc0b0\uc73c\ub85c, \ud638\ub0a8\uc815\ub9e5\uc758 \ucd5c\uace0\ubd09\uc774\ub2e4.<BR>\uc12c\uc9c4\uac15\uc744 \uc0ac\uc774\uc5d0 \ub450\uace0 \uc9c0\ub9ac\uc0b0\uacfc \ub0a8\ubd81\uc73c\ub85c \ub9c8\uc8fc\ubcf4\uace0 \uc788\ub294 \ubc31\uc6b4\uc0b0\uc740 \uc815\uc0c1\uc5d0\uc11c \uc11c\ucabd\uc73c\ub85c \ub610\uc544\ub9ac\ubd09(1127m),\ub3c4\uc194\ubd09(1153.2m)\uc774, \ub3d9\uc73c\ub85c\ub294 \ud638\ub0a8\uc815\ub9e5\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub9e4\ubd09(866.9m)\uc774, \ub0a8\uc73c\ub85c\ub294 \uc5b5\ubd88\ubd09(1,007.5m)\uc744 \uac70\ub290\ub9ac\uace0 \uc788\ub294 \ud070 \uc0b0\ub9e5\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4. \ubc31\uc6b4\uc0b0 \uc544\ub798 \uc625\ub8e1\uba74\uc740 \uc0bc\uba74\uc774 \uc0b0\uc904\uae30\ub85c \ub458\ub7ec\uc2f8\uc778 \ud070 \uace8\uc9dc\uae30\ub85c \ub0a8\ucabd\ub9cc \uc5f4\ub824 \uc788\ub294 \uc790\ub8e8 \ubaa8\uc591\uc744 \ud558\uace0 \uc788\ub2e4. \uc774 \uace8\uc9dc\uae30\ub97c \ubd88\ub2f9\uace8\uc774\ub77c\uace0 \ubd80\ub97c \ub9cc\ud07c \ubd88\uad50 \ud754\uc801\uc774 \ub9ce\uc740 \uacf3\uc774\ub2e4.<BR>\uc9c0\ub9ac\ub3c4\ucc38\uc124\uacfc \ud48d\uc218\uc0ac\uc0c1\uc758 \uc6d0\uc870\ub77c \ud560 \uc218 \uc788\ub294 \ub3c4\uc120\uad6d\uc0ac\uac00 37\uc138\ubd80\ud130 35\ub144 \ub3d9\uc548 \uc625\ub8e1\uc0ac(\uc9c0\uae08\uc740 \ub0a8\uc544 \uc788\uc9c0 \uc54a\uc74c)\uc5d0\uc11c \uc8fc\ub85c \uae30\uac70\ud558\ub2e4, 72\uc138\uc5d0 \uc774 \uc808\uc5d0\uc11c \uc785\uc801\ud558\uc600\ub2e4. \ubc31\uc6b4\uc0b0 \uc0b0\ud589\uae30\uc810\uc778 \ub3d9\ub3d9 \ub9c8\uc744 \uc67c\ucabd \uc625\ub8e1\uc0ac \ud130\uac00 \uc788\ub294 \ubc31\uacc4\uc0b0(505.8m)\uc5d0 \ub3c4\uc120\uad6d\uc0ac\uac00 \uc2ec\uc5c8\ub2e4\ub294 \ub3d9\ubc31 \uc232\uc774 \ucc9c\uc5f0\uae30\ub150\ubb3c\ub85c \uc9c0\uc815\ub418\uc5b4 \uc788\ub2e4. \uc774 \uc0b0\uc5d0\ub294 \uace0\ub85c\uc1e0\ub098\ubb34\uac00 \ub9ce\uc774 \uc790\ub77c\uace0 \uc788\ub294\ub370, \uc608\ub85c\ubd80\ud130 \uace0\ub85c\uc1e0\ub098\ubb34\uc5d0\uc11c \ub098\uc628 \uc218\uc561\uc774 \ub9cc\ubcd1\ud1b5\uce58\uc57d\uc774\ub77c\uace0 \ud558\uc5ec \uacbd\uce69\uc744 \uc804\ud6c4\ub85c \ud558\uc5ec \ub9ce\uc740 \uc0ac\ub78c\uc774 \ubc31\uc6b4\uc0b0\uc744 \ucc3e\uc544\uc624\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ub17c\uc2e4\ub9c8\uc744-\ud55c\uc7ac-\uc2e0\uc120\ub300-\uc815\uc0c1-\ub0a8\ub989 \uc0bc\uac70\ub9ac-\ubcd1\uc554\uacc4\uace1 \uc0bc\uac70\ub9ac-\uc9c4\ud2c0\ub9c8\uc744(\ucd1d 4\uc2dc\uac04 30\ubd84)<BR>\u2461 \ub0b4\ud68c\ub9c8\uc744-\ub9e4\ubd09-\uc815\uc0c1-\ub0a8\ub989 \uc0bc\uac70\ub9ac-\ubcd1\uc554\uacc4\uace1 \uc0bc\uac70\ub9ac-\uc9c4\ud2c0\ub9c8\uc744(\ucd1d 4\uc2dc\uac04 50\ubd84)<BR>\u2462 \uc131\ubd88\uad50-\ud615\uc81c\ubd09-\ub3c4\uc194\ubd09-\ub530\ub9ac\ubd09-\uc815\uc0c1-\ud5ec\uae30\uc7a5 \uc0bc\uac70\ub9ac-\ubc31\uc6b4\uc0ac-\uba39\ubc29\ub9c8\uc744(\ucd1d 8\uc2dc\uac04 55\ubd84)'), '35.048756', '127.565514', STRINGDECODE('\uc804\ub77c\ub0a8\ub3c4 \uad11\uc591\uc2dc \ubd09\uac15\uba74\u318d\uc625\ub8e1\uba74\u318d\uc9c4\uc0c1\uba74\u318d\ub2e4\uc555\uba74, \uad6c\ub840\uad70 \uac04\uc804\uba74'), 2604100800, 1222, STRINGDECODE('\ubc31\uc6b4\uc0b0(\uad11\uc591)'), STRINGDECODE('&lt;\ub3c4\uc120\uc758 \uc790\ucde8 \uc11c\ub9b0 \uba85\ub2f9\ud48d\uc218\ub3c4\uc758 \ubcf8&gt;<BR>\ud48d\uc218\uc9c0\ub9ac\uc758 \uae08\uacfc\uc625\uc870 \uba85\ub2f9\ub3c4(\u660e\u5802\u5716)\uc758 \ubcf8\uc744 \uc774\ub8e8\ub294 \uc0b0\uc774\ub2e4. \uc88c\uccad\ub8e1, \uc6b0\ubc31\ud638, \uc785\uc218, \uc548\uc0b0\u2026. \ubaa8\ub450 \uc774 \uc0b0 \ubaa8\uc591\uc0c8\ub97c \ubcf4\uace0 \uccb4\uacc4\ud654\ub410\ub2e4. \uad6d\uc0ac(\u570b\u5e2b)\uc81c\ub3c4\ub3c4 \uc5c6\ub358 \ub098\ub9d0\uc5ec\ucd08(\u7f85\u672b\u9e97\u521d)\uc5d0 \uc0b4\uc558\uc74c\uc5d0\ub3c4 \uad6d\uc0ac\ub85c \uc77c\uceec\uc5b4\uc9c0\ub294 \ub3c4\uc120\uc73c\ub85c \uc778\ud574\uc11c\ub2e4. <BR>\uc9c0\ub3c4\ub97c \ud3b4\uace0 600\ubbf8\ud130 \ub4f1\uace0\uc120\uc744 \ub530\ub77c \ud615\uad11\ud39c\uc744 \uce60\ud574\ubcf4\ub77c. \uac70\uae30\uc11c \ub5a0\uc624\ub974\ub294 \ubc31\uc6b4\uc0b0\uc740 \ubc14\ub2e4\ub85c \ub0a0\uc544\uac00\ub294 \ucee4\ub2e4\ub780 \ud55c \ub9c8\ub9ac \uc0c8\ub2e4. \uc0c8\uc758 \uba38\ub9ac, \ub3c4\uc194\ubd09 \ub0a8\ub989\uc740 \ud574\ubc1c 700\ubbf8\ud130 \ub05d\uc790\ub77d\uc5d0\uc11c \uc88c\uc6b0\ub85c \uac08\ub9ac\uace0\uc788\ub2e4. \uc624\ub978\ucabd \uc904\uae30\ub294 \uc11c\ucabd\uc73c\ub85c \ubd88\ub8e9\ud55c \ud638(\u5f27)\ub97c \uae0b\ub2e4 \uadf8\ub300\ub85c \ub0a8\ub958(\u5357\u6d41), \uad11\uc591 \uc9c4\uc0b0 \ub0b4\uc6b0\uc0b0(\u5167\u725b\u5c71)\uc73c\ub85c \ubed7\uc5b4\uac00\uace0 \uc67c\ucabd \uac00\uc9c0\ub294 \ub3d9\ucabd \ubd88\ub8e9 \ucd08\uc2b9\ub2ec \ubc31\uacc4\uc0b0(505.8m)\uc744 \uc774\ub8ec\ub2e4. <BR>\uaf43\ubd09\uc624\ub9ac\ucc98\ub7fc \uac10\uc2f8\uc778 \uac00\uc6b4\ub370\uc5d0\ub294 \uc554\uc220\ub300 \ubaa8\uc591\uc758 \uc785\uc218(\u5165\u9996)\uac00 \uc788\ub2e4. \ub3c4\uc120\uc774 \uba38\ubb3c\ub800\ub358 \uc625\ub8e1\uc0ac\ud130\ub294 \uadf8 \uc785\uc218\uc758 \ub05d, 7\ucc9c\uc5ec \uadf8\ub8e8 \ub3d9\ubc31\ub098\ubb34 \uc6b0\uac70\uc9c4 \uac00\uc6b4\ub370 \uc790\ub9ac\ud55c\ub2e4. \uba85\ub2f9\uc744 \uac10\uc2fc \uc88c\uc6b0 \uc0b0\uc904\uae30 \uc591\ucabd\uc5d0\ub294 \ub3d9\ucc9c\uacfc \ubd09\uac15\ucc9c\uc774 \ud750\ub974\uace0 \ub450 \ub0b4\ub97c \ub2e4\uc2dc \uc5b5\ubd88\ubd09\ub2a5\uc120\uacfc \ube44\ubd09\uc0b0\ub989\uc774 \uc5d0\uc6b0\uace0\uc788\ub2e4. \ub3c4\uc120\uc73c\ub85c\ubd80\ud130 \ube44\ub86f\ub41c \uba85\ub2f9\ud48d\uc218\ub3c4\uc758 \uc624\ub9ac\uc9c0\ub110\uc774 \ubc14\ub85c \uc774 \uc0b0\uc778 \uac83\uc774\ub2e4. <BR>\uc6d0\ub798 \ubc31\uacc4\uc0b0\uc73c\ub85c \ubd88\ub838\ub2e4. \uc625\ub8e1\uc0ac\ud130 \ub4b7\uc0b0\ub9cc\uc774 \uc544\ub2c8\ub77c \uad11\uc591, \uc21c\ucc9c, \uad6c\ub840\uc758 \uc138 \uad70\uc5d0 \uac78\uce58\ub294 \ud070 \uc0b0\ub369\uc774\uac00 \ubc31\uacc4\uc0b0\uc774\uc5c8\ub2e4. \ub3c4\uc120\uc758 \ud589\uc801\uc744 \uc801\uc740 \uae00\uc5d0 \ub098\uc624\ub294 \uc625\ub8e1\uc0ac\ub294 \uc5b8\uc81c\ub098 \"\"\ubc31\uacc4\uc0b0 \uc625\ub8e1\uc0ac\"\"\ub85c \ud45c\ud604\ub418\uc5c8\ub2e4. \uc774 \ube44\ubc94\ud55c \uc0b0\uc740 \uc804\uad6d \uac01\uc9c0\uc5d0 \uc788\ub294 \ud754\ud558\ub514 \ud754\ud55c \ud770\uad6c\ub984\ubafc\uac00 \uc544\ub2c8\uc5c8\ub2e4.'), STRINGDECODE('\uc8fc\ubd09\uc744 \uc911\uc2ec\uc73c\ub85c \ud558\uc5ec \ub610\uc544\ub9ac\ubd09\uacfc \ub3c4\uc194\ubd09, \ub9e4\ubd09, \uc5b5\ubd88\ubd09 \ub4f1 \uc0b0\uc138\uac00 \uc6c5\uc7a5\ud558\uba70 \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0 \uc5b5\uc0c8\ud480\uacfc \ucca0\ucb49 \uad70\ub77d, \uc628.\ud55c\ub300 900\uc885\uc758 \uc2dd\ubb3c\uc774 \uc11c\uc2dd\ud558\ub294 \ub4f1 \uacbd\uad00.\uc0dd\ud0dc\uc801 \ud2b9\uc9d5\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc788\uc73c\uba70, \ubc31\uc6b4\uc0ac(\u767d\u96f2\u5bfa), \uc131\ubd88\uc0ac(\u6210\u4f5b\u5bfa) \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ub610\uc544\ub9ac\ubd09\uc5d0\uc11c \ubc14\ub77c\ubcf8 \ub099\uc870'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc9c4\ud2c0\ub9c8\uc744 \uc8fc\ubbfc\ub4e4\uc774 \ubbfc\ubc15\uc744 \uce58\uace0\uc788\ub2e4. \uc9c4\ud2c0\ubbfc\ubc15 \u260e061-762-5579, \ubc31\uc6b4\ubbfc\ubc15 \u260e061-762-3860, \uba39\ubc29\ub9c8\uc744\uc758 \ub2e4\uc6b0\ub9ac\ud39c\uc158 \u260e011-9359-9075. \uc625\ub8e1\uc0ac\ud130\ub97c \uc9c0\ub098\uba74 \ub098\uc624\ub294 \ubc31\uc6b4\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e061-763-8615)\ub3c4 \uc88b\uc744 \uac83\uc774\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uad11\uc591\uc2dc\uccad \ubb38\ud654\ud64d\ubcf4\ub2f4\ub2f9\uad00\uc2e4 \u260e061-797-2731'), STRINGDECODE('\uae30\uc810\uc740 \uad11\uc591\uc774\ub2e4. \uad11\uc591\ud130\ubbf8\ub110\uc5d0\uc11c \ub3d9\uace1\u00b7\ubb35\ubc29\ud589 21-2\ubc88\uc774 8\ud68c, \ub17c\uc2e4\ud589 21-3\ubc88\uc774 4\ud68c \uc6b4\ud589\ud55c\ub2e4. \uad11\uc591\uc73c\ub85c\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c \uc6b0\ub4f1\ubc84\uc2a4\uac00 13\ud68c \ub2e4\ub2cc\ub2e4. \ubd80\uc0b0, \ub300\uad6c, \ud3ec\ud56d, \uacbd\uc8fc, \ub9c8\uc0b0, \uc9c4\uc8fc, \ud558\ub3d9, \ubd80\ucc9c, \uc815\uc74d\uc5d0\uc11c \ubc84\uc2a4\uac00 \uc788\uc73c\uba70 \uad11\uc8fc\uc5d0\uc11c\ub294 23\ud68c, \uc21c\ucc9c\uc5d0\uc11c\ub294 10\ubd84 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud55c\ub2e4. \uacbd\uc804\uc120 \uc5f4\ucc28\ub294 \ubd80\uc0b0 \ubd80\uc804\ubc1c\uc774 4\ud68c, \uad11\uc8fc\ubc1c\uacfc \ubaa9\ud3ec\ubc1c\uc774 1\ud68c\uc529 \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(44, TIMESTAMP '2019-08-14 15:46:34.922', TIMESTAMP '2019-08-14 15:47:05.699', '42', STRINGDECODE('\ub3d9\uac15\uc744 \ub07c\uace0 \uc788\ub294 \ubc31\uc6b4\uc0b0. ''\ud0dc\uace0\uc758 \uc2e0\ube44''\uc640 ''\ucc9c\ud61c\uc758 \ube44\uacbd''\uae4c\uc9c0 \uac16\ucd98 \ub3d9\uac15\uc740 \uac15\uc6d0\ub3c4 \uc0b0 \uc18d \uae4a\uc219\uc774 \uc228\uc5b4\uc11c \ub9d0\uc5c6\uc774 \ud758\ub7ec\uac00\uace0 \uc788\ub2e4. \uadf8\ub798\uc11c \uc77c\uae4c. \ubc31\uc6b4\uc0b0 \uc0b0\ud589\uc740 \ub9c8\uce58 \uc2e0\uc120\uc774 \ub41c \ub4ef\ud55c \uae30\ubd84\uc744 \uc120\uc0ac\ud574\uc900\ub2e4.<BR>\ubc31\uc6b4\uc0b0 \uc0b0\ud589\uc744 \uc704\ud574\uc11c\ub294 \ub3d9\uac15\uc744 \uac74\ub108\uc57c \ud55c\ub2e4. \uadf8\ub9ac\uace0 \uc0b0\ud589\uc744 \ub9c8\ucce4\uc744 \ub54c\ub3c4\uac15\uc744 \uac74\ub108\uc640\uc57c \ud55c\ub2e4. \ud558\uc9c0\ub9cc \ubb3c\uc744 \uac74\ub108\uc9c0 \uc54a\uace0 \uc544\uc608 \uce60\uc871\ub839\uc5d0\uc11c \ubb38\ud76c \ub9c8\uc744\ub85c\ub0b4\ub824\uc120\ub4a4 \ubcf4\ud2b8\ub97c \ud0c0\uace0 \uc12d\uc0c8\uae4c\uc9c0 \ub0b4\ub824\uc624\ub294 \ubc29\ubc95\ub3c4 \uc788\ub2e4. \uc0b0\uacfc \uac15\uc744 \ub3d9\uc2dc\uc5d0\uc990\uae30\ub294 \ucf54\uc2a4\ub2e4. \ud558\uc9c0\ub9cc, \ubc31\uc6b4\uc0b0 \uc0b0\ud589\uc740 \uc810\uc7ac \ub9c8\uc744\uc5d0\uc11c \uc2dc\uc791\ud558\ub294 \uac83\uc774 \uc77c\ubc18\uc801\uc774\ub2e4.'), STRINGDECODE('\u2460 \ubb38\ud76c\ub9c8\uc744-\uce60\uc871\ub839-\uc815\uc0c1-955\ubd09-\ud46f\ub300\ubd09-955\ubd09-\uc11c\ub0a8\ub989-\ubb38\ud76c\ub9c8\uc744(\ucd1d 6\uc2dc\uac04 40\ubd84)<BR>\u2461 \uc810\uc7ac\ub9c8\uc744-\uacc4\uace1\uae38-\uc815\uc0c1-\uad6c\ub984\uc7ac\uace8-\ubb38\ud76c\ub9c8\uc744(\ucd1d 3\uc2dc\uac04 20\ubd84)<BR>\u2462 \uc81c\uc7a5\ub9c8\uc744-\uce60\uc871\ub839-\uc815\uc0c1-\uacc4\uace1\uae38-\uc810\uc7ac\ub9c8\uc744(\ucd1d 4\uc2dc\uac04 30\ubd84)'), '37.237859', '128.648015', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc815\uc120\uad70 \uc2e0\ub3d9\uc74d, \ud3c9\ucc3d\uad70 \ubbf8\ud0c4\uba74'), 2215034400, 884, STRINGDECODE('\ubc31\uc6b4\uc0b0(\uc815\uc120)'), STRINGDECODE('&lt;\ube7c\uc5b4\ub098\uba74\uc11c\ub3c4 \uc6c5\uc7a5\ud55c \ubc14\uad6c\ub2e4\ub9ac\uc0b0&gt;<BR>\ub310\uc774 \uac74\uc124\ub41c\ub2e4\uace0 \ub098\ub77c\ub97c \ub5a0\ub4e4\uc369\ud558\uac8c \ud588\ub358 \ub3d9\uac15\ubcc0\uc758, \ub3d9\uac15\uc774 \ubcf4\uc774\ub294 \uc0b0\uc774\ub2e4. \uc0b0\uac04 \uac10\uc785\uace1\ub958(\u5d4c\u5165\u66f2\u6d41)\uc758 \uadf9\uce58\ub97c \ub0b4\ub824\ub2e4\ubcfc \uc218 \uc788\uc73c\uba70 \uc0b0\ud589 \uc804\ud6c4\uc5d0 \ub798\ud504\ud305\uc744 \uc990\uae38 \uc218\ub3c4 \uc788\ub2e4. <BR>\ub0a8\ucabd\uc73c\ub85c \ubed7\uc5b4\ub0b4\ub9ac\ub294 \uc6a9\uac00\ub2a5\uc120, \uc11c\ub0a8\ud5a5\uc758 \uce60\uc871\ub839\ub2a5\uc120\uc774 \uac15 \uac74\ub108 \uc18c\ub3d9\ub9c8\uc744\uc758 \ub204\ub354\uae30 \uac19\uc740 \ubc2d\uc790\ub77d\ub4e4, \uad6c\ud558\ub3c4(\u820a\u6cb3\u9053)\uac00 \ub9cc\ub4e4\uc5b4\ub193\uc740 \uc12c \uc544\ub2cc \uc12c\uc744 \uc6c0\ucf1c\uc950\uace0 \uc788\ub294 \ub4ef\ud55c \ud615\uc138\ub2e4. \uc6a9\uac00\ub2a5\uc120\uc740 \uac1c\ubc1c\ucc98\ub7fc, \uac08\uc218\ub85d \ub0ae\uc544\uc9c0\uace0 \uc881\uc544\uc9c0\ub2e4 \ub05d\ubd80\ubd84\uc774 \ub119\uc801\ud55c \ubaa8\uc591\uc778\ub370 \uce60\uc871\ub839\ub2a5\uc120\uc740 \uc5ec\uc12f \ubd09\uc6b0\ub9ac\ub4e4\uc774 \ub2f4\uc7a5 \uc704 \uc720\ub9ac\uc870\uac01\ucc98\ub7fc \ubfb0\uc871\ubfb0\uc871\ud558\ub2e4. <BR>\uc815\uc0c1\uc5d0 \uc11c\uba74 \uad7d\uc774\uc9c0\ub294 \ub3d9\uac15\uc758 \ud750\ub984\uc774 \uc18d\uc18d\ub4e4\uc774 \ubcf4\uc778\ub2e4. \uac74\ub108\ud3b8\uc758 \uc644\ud0dd\uc0b0, \uace0\uace0\uc0b0, \uacc4\ubd09, \uacf0\ubd09\uc740 \ubb3c\ub860 \uba40\ub9ac \ud568\ubc31\uc0b0\uae4c\uc9c0 \uc870\ub9dd\ub41c\ub2e4. <BR>\ud558\uc9c0\ub9cc \uc11d\ud68c\uc554 \uc0b0\uc774\uc5b4\uc11c \uce7c\ub85c \uc790\ub978 \ub4ef\ud55c \ubcbc\ub791\uc774 \ub9ce\ub2e4. \uc9c0\uc5ed \uc0ac\ud22c\ub9ac\ub85c \ubf1d\ub300\ub77c\uace0 \ubd80\ub974\ub294\ub370 \uc885\uc885 \uc2e4\uc871\uc0ac\uace0\uac00 \uc77c\uc5b4\ub098\ub2c8 \uac01\ubcc4\ud788 \uc870\uc2ec\ud574\uc57c\ud55c\ub2e4. <BR>\ubc31\uc6b4\uc0b0\uc744 \ubc14\ub77c\ubcf4\ub294 \ub9dd\ub300\ub294 \uc18c\ub3d9\ub9c8\uc744 \ub0a8\ucabd\uc758 \uace0\uc131\uc0b0\uc131(425m)\uc774\ub2e4. \uace0\uad6c\ub824\uac00 \uc313\uc740 \uac83\uc73c\ub85c \ubcf4\uc774\ub294 \ub3cc\uc131\uc73c\ub85c \uc77c\ub300\uc758 \ube44\uacbd \uac10\uc0c1\uc740 \ubb3c\ub860, \uc774\ub7f0 \uc0b0\uac04\ud611\uace1\uc5d0 \uc65c \uc131\uc744 \uc313\uc558\ub294\uc9c0 \ucd94\uce21\ud560 \uc5ec\uc9c0\ub3c4 \uc900\ub2e4.'), STRINGDECODE('\ub3d9\uac15\uc758 \uac00\uc6b4\ub370\uc5d0 \uc704\uce58\ud558\uace0 \uc788\uc5b4 \uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0, \uc870\ub9dd\uc774 \uc88b\uc73c\uba70 \uc0dd\ud0dc\uacc4\ubcf4\uc874\uc9c0\uc5ed\uc73c\ub85c \uc9c0\uc815\ub418\uc5b4\uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815 \ud770\uad6c\ub984\uc774 \ub298 \ub07c\uc5b4 \uc788\ub294\ub370\uc11c \uc0b0 \uc774\ub984\uc774 \uc720\ub798, \uc624\ub300\uc0b0\uc5d0\uc11c \ubc1c\uc6d0\ud558\ub294 \uc624\ub300\ucc9c\uacfc \uc870\uc591\uac15(\u671d\u967d\u6c5f)\uc744 \ubaa8\uc544 \ub0a8\ud55c\uac15\uc73c\ub85c \ud750\ub974\ub294 \ub3d9\uac15 \ubc0f \ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c260\ud638\ub85c \uc9c0\uc815(1979\ub144)\ub41c \ubc31\ub8e1\ub3d9\uad74(\u767d\u9f8d\u6d1e\u7a9f)\uc774 \uc720\uba85'), STRINGDECODE('\ud0dc\uace0\uc758 \uc2e0\ube44\ub97c \uac04\uc9c1\ud55c \ucc9c\ud61c\uc758 \ube44\uacbd'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc601\uc6d4 \uc74d\ub0b4 \uc219\ubc15\uc2dc\uc124\uc774\ub098 \uc0b0 \uc544\ub798 \ubbfc\ubc15\uc9d1\uc744 \uc774\uc6a9\ud55c\ub2e4. \uc74d\ub0b4 \uadf8\ub79c\ub4dc\ud30c\ud06c\uc7a5\uc5ec\uad00 \u260e033-373-6110 \uc7a5\ub989\ubaa8\ud154 \u260e033-373-8890 \uc810\uc7ac\ub9c8\uc744 \uc810\uc7ac\uc0c1\ud68c \u260e033-378-1570 \uc81c\uc7a5\ub9c8\uc744 \uc740\ud558\uc27c\ud130 \u260e033-378-8765 \ubb38\ud76c\ub9c8\uc744 \ub3d9\uac15\uc0b0\uc7a5 \u260e033-333-9509 <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud3c9\ucc3d\uad70\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e033-330-2540'), STRINGDECODE('\ud589\uc815\uad6c\uc5ed\uacfc\ub294 \ub2ec\ub9ac \uae30\uc810\uc740 \uc601\uc6d4\uad70 \uc2e0\ub3d9\uc74d\uc774\ub2e4. \uc608\ubbf8\uc5ed \uc55e\uc5d0\uc11c \ucd9c\ubc1c\ud558\ub294 \uc6b4\uce58\ub9ac\ud589 \ubc84\uc2a4\ub97c \ud0c0\uace0 \uc885\uc810 \uc810\uc7ac\ub098\ub8e8\uc5d0\uc11c \ub0b4\ub9b0\ub2e4. \uc608\ubbf8\uc5ed\uc73c\ub85c\ub294 \uccad\ub7c9\ub9ac\ubc1c \ud0dc\ubc31\uc120 \uc5f4\ucc28\uac00 5\ud68c \ub2e4\ub2cc\ub2e4. \uc2e0\ub3d9\uc73c\ub85c\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 11\ud68c \uc6b4\ud589\ud558\ub294 \ud0dc\ubc31\ud589 \uc9c1\ud589\ubc84\uc2a4\ub098 \uc601\uc6d4\uc5d0\uc11c 28\ud68c \uc6b4\ud589\ud558\ub294 \ud0dc\ubc31\ud589 \uc9c1\ud589\ubc84\uc2a4\ub97c \uc774\uc6a9\ud55c\ub2e4. \uc601\uc6d4\ub85c\ub294 \ucd98\ucc9c, \uc6d0\uc8fc, \uac15\ub989, \ub300\uad6c, \uc778\ucc9c, \uc218\uc6d0, \uc131\ub0a8, \uc758\uc815\ubd80, \uc548\uc0b0, \uace0\uc591, \uccad\uc8fc\uc5d0\uc11c \ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(45, TIMESTAMP '2019-08-14 15:46:34.922', TIMESTAMP '2019-08-14 15:47:06.289', '41', STRINGDECODE('<P>\uacbd\uae30\ub3c4 \ud3ec\ucc9c\uad70 \uc774\ub3d9\uba74\uacfc \uac15\uc6d0\ub3c4 \ud654\ucc9c\uad70 \uc0ac\ub0b4\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \ubc31\uc6b4\uc0b0\uc740 \ubd80\ub4dc\ub7ec\uc6b4 \uc0b0\uc138\uc640 \ud574\ubc1c 660m \uc778 \uad11\ub355\uace0\uac1c\uc5d0\uc11c \uc0b0\ud589\uc774 \uc2dc\uc791\ub418\ubbc0\ub85c \uac00\ubccd\uac8c \uc0b0\ud589\uc744 \uc990\uae30\ub824\ub294 \uc0ac\ub78c\ub4e4\uc774 \ub9ce\uc774 \ucc3e\ub294\ub2e4. \uc8fc\ub9d0\uc744 \uc774\uc6a9\ud574 \uac00\uc871\uacfc \ud568\uaed8 \ub4f1\ubc18\uc744 \ud558\uae30\uc5d0\ub3c4 \uc801\uaca9\uc778 \uacf3\uc774\ub2e4. <BR>\uad11\ub355\uace0\uac1c\ub294 \uc77c\uba85 ''\uce74\ub77c\uba5c\uace0\uac1c''\ub77c\uace0 \ubd88\ub9ac\ub294\ub370, \uadf8\ub807\uac8c \ubd88\ub9ac\ub294 \ub370\ub294 \ub450 \uac00\uc9c0 \uc124\uc774 \uc788\ub2e4. \uccab \ubc88\uc9f8 \uc124\uc740 \ub3c4\ub85c\uac00 \ube44\ud3ec\uc7a5\uc774\uc5c8\ub358 6.25\ub54c \uc774 \uc9c0\uc5ed\uc744 \uad00\ud560\ud558\ub358 \uc0ac\ub2e8\uc7a5\uc774 \uad11\ub355\uace0\uac1c\ub97c \uc624\ub97c \ub54c\uba74 \uc6b4\uc804\ubcd1\uc758 \uc878\uc74c\uc744 \ucad3\uae30 \uc704\ud574 \uc6b4\uc804\ubcd1\uc5d0\uac8c \uce74\ub77c\uba5c\uc744 \uba39\uac8c \ud55c \ub370\uc5d0\uc11c \uc720\ub798\ub418\uc5c8\ub2e4\ub294 \uac83\uc774\ub2e4. <BR>\ub450\ubc88\uc9f8 \uc124\uc740 \uad11\ub355\uace0\uac1c\uc758 \uafb8\ubd88\uafb8\ubd88\ud55c \ubaa8\uc591\uc774 \ub099\ud0c0\uc758 \ub4f1\uc744 \uc5f0\uc0c1\ucf00 \ud55c\ub2e4\uace0 \ud574\uc11c, \uce74\uba5c(Camel: \ub099\ud0c0)\uc774 \uce74\ub77c\uba5c\ub85c \ubcc0\ud558\uc5ec \uce74\ub77c\uba5c\uace0\uac1c\ub85c \ubd88\ub9ac\uac8c \ub418\uc5c8\ub2e4\ub294 \uac83\uc774\ub2e4. <BR>\ubc31\uc6b4\uc0b0 \uc815\uc0c1 \uc11c\ucabd \uc544\ub798\ub85c 4km\uc5d0 \uc774\ub974\ub294 \ubc31\uc6b4\uacc4\uace1\uc740 \ud48d\ubd80\ud55c \uc218\ub7c9\uacfc \ube7c\uc5b4\ub09c \uacc4\uace1\ubbf8\ub97c \uc790\ub791\ud55c\ub2e4. \uacc4\uace1 \uacf3\uacf3\uc5d0 \ub113\uc801\ud55c \ubc14\uc704\uac00 \ub110\ub824\uc788\uc5b4 \uc544\ubb34\ub370\uace0 \uc549\uc73c\uba74 \uc27c\ud130\uac00 \ub420 \uc815\ub3c4\ub85c \ub4f1\uc0b0\uc778\ub4e4\uc774 \uc26c\uc5b4\uac00\uae30 \uc88b\uc740 \uacf3\uc774\ub2e4.</P>'), STRINGDECODE('<P>\u2460 \ubc31\uc6b4\ub3d9-\uacc4\uace1\uc0ac\uac70\ub9ac-\ud765\ub8e1\ubd09-\ub3c4\ub9c8\uce58\ubd09-\uc815\uc0c1-\ub2a5\uc120\uc0bc\uac70\ub9ac-\uacc4\uace1\uc0ac\uac70\ub9ac-\ud765\ub8e1\uc0ac \ubc31\uc6b4\ub3d9 (\ucd1d 4\uc2dc\uac04 30\ubd84)<BR>\u2461 \ubc31\uc6b4\ub3d9-\ubc31\uc6b4\uacc4\uace1-\ubc31\uc6b4\uc0b0-\ubd81\ub989-\uad11\ub355\uace0\uac1c (\ucd1d 3\uc2dc\uac04)</P>'), '38.051847', '127.38551', STRINGDECODE('\uacbd\uae30\ub3c4 \ud3ec\ucc9c\uc2dc \uc774\ub3d9\uba74, \uac15\uc6d0\ub3c4 \ud654\ucc9c\uad70 \uc0ac\ub0b4\uba74'), 2142141300, 903, STRINGDECODE('\ubc31\uc6b4\uc0b0(\ud3ec\ucc9c)'), STRINGDECODE('<P>&lt;\ubc31\uc6b4\uacc4\uace1 \uc720\uba85\ud55c \ud3ec\ucc9c\uc758 \uba85\uc0b0&gt;<BR>\ud558\uc580 \uad6c\ub984\uc744 \uba38\ub9ac\uc5d0 \uc774\uace0 \uc788\ub294 \uc0b0, \uc774\ub984\ub9cc \ub4e4\uc5b4\ub3c4 \ub9c8\uc74c \uc124\ub808\uc774\ub294 \uc0b0\uc774 \ubc31\uc6b4\uc0b0\uc774\ub2e4. <BR>\uc6b0\ub9ac\ub098\ub77c\uc5d0\ub294 \ubc31\uc6b4\uc0b0\uc774 \ub9ce\ub2e4. \uad11\uc591\uc758 \ubc31\uc6b4\uc0b0, \ud568\uc591\uc758 \ubc31\uc6b4\uc0b0\uc744 \ube44\ub86f\ud574 \ub0a8\ud55c\uc5d0\ub9cc 23\uac1c\uc758 \ubc31\uc6b4\uc0b0\uc774 \uc788\ub2e4. \uadf8 \uc911 \uacbd\uae30\ub3c4 \ud3ec\ucc9c\uc758 \ubc31\uc6b4\uc0b0\uc740 \uc0b0\uc790\uc218\uba85\ud558\uae30\ub85c \uc774\ub984\ub09c \uc0b0\uc774\ub2e4. <BR>\uacbd\uae30\uc758 \ucd5c\uace0\ubd09\uc778 \ud654\uc545\uc0b0\uc774 \ub3d9\ub0a8\ucabd\uc73c\ub85c \ub9e5\uc774 \ub2ff\uc544 \uc788\uace0 \ubd81\ucabd\uc758 \uad11\ub355\uc0b0\uacfc\ub294 \uad11\ub355\uace0\uac1c(\uc77c\uba85 \uce74\ub77c\uba5c\uace0\uac1c)\ub97c \uc0ac\uc774\uc5d0 \ub450\uc5c8\ub2e4. \ubc31\uc6b4\uacc4\uace1\uc740 \uc601\ud3c9\ud314\uacbd\uc758 \ud558\ub098\ub85c \uaf3d\ud790 \ub9cc\ud07c \uacc4\uace1\ubbf8\uac00 \ub6f0\uc5b4\ub098\uba70 \uac00\uc744 \ub2e8\ud48d\ub3c4 \uc808\uacbd\uc774\ub2e4. \uc77c\ub3d9 \uc628\ucc9c\ud0c0\uc6b4\uc5d0 \ub4e4\ub7ec \uc628\ucc9c\uc695\uc744 \uc990\uae38 \uc218\ub3c4 \uc788\ub2e4.</P>'), STRINGDECODE('<P>\uc218\ub824\ud55c \uacc4\uace1\ubbf8\ub97c \uac00\uc9c0\uace0 \uc788\uc73c\uba70 \uad11\ub355\uc0b0, \uad6d\ub9dd\ubd09, \ubc15\ub2ec\ubd09 \ub4f1\uacfc \uac19\uc740 \ub192\uc740 \ubd09\uc6b0\ub9ac\ub4e4\uacfc \ubb34\ub9ac\ub97c \uc774\ub904 \uacc4\uace1 \ub2e8\uc560(\u65b7\u5d16) \ub4f1 \ub3c5\ud2b9\ud55c \uacbd\uad00\uc744 \uac00\uc9c0\uace0 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubc31\uc6b4\ub3d9 \uacc4\uace1 \ubc0f \uc2e0\ub77c \ub9d0 \ub3c4\uc120\uc774 \ucc3d\uac74\ud558\uc600\ub2e4\uace0 \uc804\ud558\ub294 \ud765\ub8e1\uc0ac(\u8208\u9f8d\u5bfa)\uac00 \uc720\uba85</P>'), STRINGDECODE('\uacbd\uae30\ub3c4\uc640 \uac15\uc6d0\ub3c4\uc758 \uacbd\uacc4, \uce74\ub77c\uba5c\uace0\uac1c'), STRINGDECODE('<P>\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ubc31\uc6b4\uacc4\uace1\uc774 \uc788\ub294 \ubc31\uc6b4\ub3d9\uc5d0 \ubbfc\ubc15\uc9d1\uc774 \ub9ce\ub2e4. <BR>\ubbfc\ubc15\ubb38\uc758\ub294 \uc77c\ub3d9\ub18d\ud611 \uc774\ub3d9\uc9c0\uc18c\uc5d0 \ud558\uba74\ub41c\ub2e4 \u260e031-532-4411<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud3ec\ucc9c\uc2dc\uccad \ubb38\ud654\uacf5\ubcf4\ub2f4\ub2f9\uad00\uc2e4 \uad00\uad11\ub2f4\ub2f9 \u260e031-538-2068</P>'), STRINGDECODE('<P>\uae30\uc810\uc740 \uc11c\uc6b8\uc774\ub2e4. <BR>\uc774\ub3d9 \uacbd\uc720 \uc0ac\ucc3d\ub9ac\ud589\uc744 \ud0c0\uace0 \uad11\ub355\uace0\uac1c\uc5d0\uc11c \ub0b4\ub9b0\ub2e4. \uc6b4\uc804\uae30\uc0ac\uc5d0\uac8c \uc0ac\uc804 \uc591\ud574\uac00 \ud544\uc694\ud558\ub2e4.<BR>- \uc11c\uc6b8\u2192\uc0ac\ucc3d\ub9ac : \ud558\ub8e811\ud68c \uc6b4\ud589</P>'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(46, TIMESTAMP '2019-08-14 15:46:34.922', TIMESTAMP '2019-08-14 15:47:06.878', '45', STRINGDECODE('<P>\uc11c\ud574\ubc14\ub2e4\uc640 \uc778\uc811\ud574 \uc788\ub294 \ubcc0\uc0b0\uc740 \ud638\ub0a8\ud3c9\uc57c\ub97c \uc0ac\uc774\uc5d0 \ub450\uace0 \ud638\ub0a8\uc815\ub9e5\uc904\uae30\uc5d0\uc11c \ub5a8\uc5b4\uc838 \ub3c5\ub9bd\ub41c \uc0b0\uad70\uc744 \ud615\uc131\ud558\uace0 \uc788\ub2e4. \uc774 \uc0b0\uc740 \uc608\ub85c\ubd80\ud130 \ub2a5\uac00\uc0b0, \uc601\uc8fc\uc0b0, \ubd09\ub798\uc0b0\uc774\ub77c \ubd88\ub9ac\uba70 \ud638\ub0a8\uc758 5\ub300 \uba85\uc0b0\uc911\uc758 \ud558\ub098\ub85c \uaf3d\ud600\uc654\ub2e4. <BR>1971\ub144 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \ubcc0\uc0b0\uc5d0\ub294 \uae30\uc0c1\ubd09, \ub9dd\ud3ec\ub300, \uc2e0\uc120\ub300, \uc30d\uc131\ubd09, \uc625\ub140\ubd09, \uc138\ubd09 \ub4f1 400m \uc774\uc0c1\uc758 \uc0b0\uc774 6\uac1c \uc788\uace0 \uacc4\uace1\uc5d0\ub294 \uc640\ub8e1\uc18c, \uac00\ub9c8\uc18c, \uc9c1\uc18c\ud3ed\ud3ec, \uc131\uacc4\ud3ed\ud3ec \ub4f1 \uc7a5\uc5c4\ud55c \uc808\uacbd\uc744 \uc790\ub791\ud558\ub294 \uba85\uc18c\ub4e4\uc774 \uc0b0\uc7ac\ud574 \uc788\ub2e4. \uc774 \ubc16\uc5d0\ub3c4 \uc11c\ud574\uc548 \ud574\uc2dd\ub2e8\uc560(\u6d77\u8755\u65b7\u5d16)\uc758 \uc808\uacbd\uc744 \uc774\ub8e8\ub294 \ucc44\uc11d\uc7a5\uc744 \ube44\ub86f\ud558\uc5ec \uc6b0\uae08\ubc14\uc704\uc640 \uc0b0\uc131, \uad74\ubc14\uc704, \ube59\ubd09 \ub099\uc870\ub300, \ub0b4\uc18c\uc0ac, \uc6d4\uba85\uc554, \uac1c\uc554\uc0ac \ub4f1 \ucc9c\ud0dc\ub9cc\uc0c1\uc758 \uba85\uc18c\ub4e4\uc774 \uc9c4\uc744 \uce58\uace0 \uc788\uc5b4 \ubcfc\uac70\ub9ac\uac00 \ub9ce\uc740 \uacf3\uc774\ub2e4.</P>'), STRINGDECODE('<P>\u2460 \ub0b4\uc18c\uc0ac \ub9e4\ud45c\uc18c-\uad00\uc74c\ubd09-\uc138\ubd09-\uc81c1\uac00\ub9c8\uc18c-\uc81c2\uac00\ub9c8\uc18c-\uccad\ub9bc\ub9ac (\ucd1d 3\uc2dc\uac04 40\ubd84)<BR>\u2461 \ub0b4\uc18c\uc0ac \ub9e4\ud45c\uc18c-\uad00\uc74c\ubd09-\uc2e0\uc120\ubd09-\ub9dd\ud3ec\ub300-\ubd84\ucd08\ub300-\ub099\uc870\ub300-\ub0a8\uc5ec\uce58 (\ucd1d 5\uc2dc\uac04 40\ubd84)<BR>\u2462 \ub0b4\uc18c\uc0ac \ub9e4\ud45c\uc18c-\uad00\uc74c\ubd09-\uc138\ubd09-\ub0b4\uc18c\uc0ac \ub9e4\ud45c\uc18c (\ucd1d 1\uc2dc\uac04 55\ubd84)</P>'), '35.643901', '126.535073', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \ubd80\uc548\uad70 \ubcc0\uc0b0\uba74, \uc0c1\uc11c\uba74, \uc9c4\uc11c\uba74'), 2506112300, 461, STRINGDECODE('\ubcc0\uc0b0'), STRINGDECODE('<P>&lt;\uc0b0 \ud638\uc218 \ubc14\ub2e4 3\uc808\ubbf8 \uac16\ucd98 \uc11c\ud574\uc758 \uc0b0&gt;<BR>\ubd80\uc548\uc74d\uc5d0\uc11c \uc11c\ud574\ub97c \ud5a5\ud574 \ub0b4\ubbfc \ubcc0\uc0b0\ubc18\ub3c4\ub294 \ubc29\ubc29\uacf3\uacf3\uc774 \uc88b\uc740 \uba85\uc2b9\uc9c0\ub2e4. \ubc14\ub2e4\ub97c \uc6d0\ud558\uba74 \ud574\uc218\uc695\uc7a5, \uc0b0\uc744 \uc624\ub974\uace0 \uc2f6\uc73c\uba74 \uc548\ubcc0\uc0b0, \ubc31\ud328\ud0b9\uc758 \ub9db\uc5d0 \ube60\uc9c4 \uc774\ub294 \ubc31\ucc9c\ub0b4\uc758 \ub9d1\uc740 \ubb3c\uc744 \uc990\uae38 \uc218 \uc788\ub294 \uacf3, \ubc14\ub85c \ubcc0\uc0b0(\u908a\u5c71)\uc774\ub2e4.<BR>\uc6a9\ub9c8\ub8e8\uc5d0\uc11c \uc11c\uae4c\ub798 \ubed7\uc5b4\ub098\uac00\ub4ef \ud558\ub294 \uc5ec\ub290 \uc0b0\uacfc\ub294 \ub2ec\ub9ac \ubc14\uae65\uc5d0 \uc0b0\uc744 \uc138\uc6b0\uace0 \uc548\uc740 \ube44\uc6b4 \ud615\ud0dc\ub2e4. \uc0c1\ubd09 \uc758\uc0c1\ubd09(509m)\uc5d0\uc11c \uc2dc\uc791\ud55c \uc0b0\uc904\uae30\ub294 \uc2dc\uacc4\ubc29\ud5a5\uc73c\ub85c \ub3cc\uba70 \uc8fc\ub958\uc0b0\uc131(331m), \uc0c1\uc5ec\ubd09(390m), \ub0a8\uc625\ub140\ubd09(433m), \uc6a9\uac01\ubd09, \uc138\ubd09, \uad00\uc74c\ubd09(424m), \uc2e0\uc120\ub300(486m), \ub9dd\ud3ec\ub300(494.1m), \uc30d\uc120\ubd09(460.7m)\uc744 \ub9cc\ub4e4\ub2e4\uac00 \uadf8 \uc548\uc758 \ubc31\ucc9c\ub0c7\ubb3c\uc774 \ud669\ud574\ub85c \ub4e4\uc5b4\uac00\ub294 \uc5b4\uadc0 \ud574\ucc3d(\u6d77\u5009) \uc55e\uc5d0\uc11c \uc758\uc0c1\ubd09\uc744 \ubc14\ub77c\ubcf4\uba70 \ub9fa\uc74c\ud55c\ub2e4.<BR>\uc774 \ubc16\uc740 \ubaa8\ub450 \ubc14\ub2e4\uac70\ub098 \uc774\uc804\uc5d0 \ubc14\ub2e4\uc600\ub358 \ub545\uc774\ub2e4. \uc721\uc9c0\uc640 \uaca8\uc6b0 \ubaa9\uc744 \ub304 \uc0ac\ucc3d\uc7ac \uc5b4\ub984\uc744 \ube7c\uace0\ub294, \ubc18\uba74\uc5d0 \uc548\ucabd\uc740 \ud3ed\ud3ec\uc640 \ub2f4\uc18c\uac00 \uacf3\uacf3\uc5d0\uc11c \uc6a9\ud2c0\uc784\ud558\ub294 \uc2ec\uc2ec\uc0b0\uace8\uc774\ub2e4.</P>'), STRINGDECODE('<P>\uc6b8\ucc3d\ud55c \uc0b0\uacfc \uacc4\uace1, \ubaa8\ub798\ud574\uc548\uacfc \uc554\uc11d\ud574\uc548 \ubc0f \uc0ac\ucc30 \ub4f1\uc774 \uc5b4\uc6b8\ub824 \ub6f0\uc5b4\ub09c \uacbd\uad00\uc744 \uc774\ub8e8\uace0 \uc788\uc73c\uba70 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1968\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0b0\uc774\uba74\uc11c \ubc14\ub2e4\uc640 \uc9c1\uc811 \ub2ff\uc544 \uc788\ub294 \ud2b9\uc9d5\uc774 \uc788\uc74c. \uc9c1\uc18c\ud3ed\ud3ec, \uac00\ub9c8\uc18c, \ubd09\ub798\uad6c\uace1, \ucc44\uc11d\uac15, \uc801\ubcbd\uac15 \ubc0f \ub0b4\uc18c\uc0ac, \uac1c\uc554\uc0ac \ub4f1 \uc0ac\ucc30\uacfc \ud638\ub791\uac00\uc2dc\ub098\ubb34, \uaf5d\uaf5d\ub098\ubb34 \ub4f1 \ud76c\uadc0\ub3d9.\uc2dd\ubb3c\uc774 \uc11c\uc2dd</P>'), STRINGDECODE('\ud638\ub0a8\uc815\ub9e5\uc5d0\uc11c \ub3c5\ub9bd\ub41c \uc0b0\uad70 \ud615\uc131'), STRINGDECODE('<P>\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ubcc0\uc0b0\ud574\uc218\uc695\uc7a5, \ub0b4\uc18c\uc0ac, \uaca9\ud3ec\ud574\uc218\uc695\uc7a5 \ubd80\uadfc\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. <BR>\ub0b4\uc18c\uc0b0\uc7a5\uc5ec\uad00 \u260e063-582-7281, <BR>\ub3d9\uace1\ucea0\ud504\uc7a5 \u260e063-581-4644, <BR>\ub3d9\ubc31\uc0b0\uc7a5\uc5ec\uad00 \u260e063-582-7651 \ub4f1. <BR>\ubbfc\ubc15\ubb38\uc758 \ubd80\uc548\uc9c4\uc11c\ub18d\ud611 \uc9c0\ub3c4\uacc4 \u260e063-581-2064.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ubcc0\uc0b0\ubc18\ub3c4\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-582-7808</P>'), STRINGDECODE('<P>\uae30\uc810\uc740 \ubd80\uc548\uc774\ub2e4. \ubd80\uc548\ud589 \uc9c1\ud1b5 \ubc84\uc2a4\ub97c \uc774\uc6a9\ud560 \uc218 \uc5c6\uc744 \uacbd\uc6b0 \uc77c\ub2e8 \uc804\uc8fc\ub85c \uac00\uba74 \ubd80\uc548\ud589 \ubc84\uc2a4\uac00 \ub9ce\uc774 \uc788\ub2e4.<BR>&gt; \ubd80\uc548\u2192\ub0b4\uc18c\uc0ac : 30\ubd84 \uac04\uaca9 \uc6b4\ud589 &gt;\ubd80\uc548\u2192\uaca9\ud3ec : 10\ubd84 \uac04\uaca9 \uc6b4\ud589.<BR>&gt; \uaca9\ud3ec\u2192\ub0b4\uc18c\uc0ac : 1\uc77c 7\ud68c \uc6b4\ud589</P>'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(47, TIMESTAMP '2019-08-14 15:46:34.923', TIMESTAMP '2019-08-14 15:47:07.506', '11', STRINGDECODE('\uc11c\uc6b8\uc758 \uc9c4\uc0b0 \ubd81\ud55c\uc0b0\uc740 \uc870\uc120\uc870 \ucd08\uae30\uc5d0\ub294 \uc0bc\uac01\uc0b0(\u4e09\u89d2\u5c71)\uc73c\ub85c \ubd88\ub800\ub2e4. \uc0bc\uac01\uc0b0\uc774\ub780 \uc774\ub984\uc740 \u3008\uc2e0\uc99d\ub3d9\uad6d\uc5ec\uc9c0\uc2b9\ub78c\u3009\uc5d0 ''\uc0bc\uac01\uc0b0\uc740 \uc591\uc8fc\uc758 \uacbd\uacc4\uc5d0 \uc788\ub2e4. \uc77c\uba85 \ud654\uc0b0(\u83ef\u5c71)\uc774\ub77c \ud558\uace0, \uc2e0\ub77c\uc2dc\ub300\uc5d0\ub294 \ubd80\uc544\uc545(\u8ca0\u5152\u5cb3)\uc774\ub77c\uace0 \ubd88\ub800\ub2e4.<BR>\uc774 \uc0b0\uc740 \uacbd\uc131(\u4eac\u57ce)\uc758 \uc9c4\uc0b0\uc73c\ub85c \ub3d9\uba85\uc655\uc758 \uc544\ub4e4 \uc628\uc870\uac00 \ud55c\uc0b0(\u6f22\u5c71)\uc5d0 \uc774\ub974\ub7ec \ubd80\uc544\uc545\uc5d0 \uc62c\ub77c\uac00\uc11c \uc0b4 \ub9cc\ud55c \uacf3\uc744 \uc0b4\ud3c8\ub2e4''\ub294 \uae30\ub85d\uc774 \uc788\uc73c\uba70, ''\ubc31\uc6b4\ubd09 (\u767d\u96f2\u5cf0, \uc9c0\uae08\uc758 \ubc31\uc6b4\ub300), \uc778\uc218\ubd09 (\u4ec1\u58fd\u5cf0), \ub9cc\uacbd\ubd09 (\u842c\u666f\u5cf0,\uc9c0\uae08\uc758 \ub9cc\uacbd\ub300)\ub4f1 \uc138 \ubd09\uc6b0\ub9ac\uac00 \uc788\uc73c\ubbc0\ub85c \uadf8\ub807\uac8c \uc774\ub984\ud55c \uac83\uc774\ub2e4''\ub77c\uace0 \uc720\ub798\ub97c \uae30\ub85d\ud558\uace0 \uc788\ub2e4. \ubd81\ud55c\uc0b0\uc131\uc5d0 \ub300\ud574\uc11c\ub294 \u3008\uc2e0\uc99d\ub3d9\uad6d\uc5ec\uc9c0\uc2b9\ub78c\u3009\uacfc \u3008\ubd81\ud55c\uc9c0\u3009\uc5d0 \uc0c1\uc138\ud788 \uae30\ub85d\ub418\uc5b4 \uc788\ub2e4. \ubd81\ud55c\uc0b0\uc131\uc740 \ubc31\uc81c \uc628\uc870\uc655\uc774 \ud130\ub97c \uc7a1\uc558\uace0 \uadf8\ud6c4 \uac1c\ub8e8\uc655 \ub54c \uc131\ud130\ub97c \uc313\uc558\ub2e4\ub294 \uae30\ub85d\uc774 \uc788\uc73c\uba70, \uc870\uc120\uc2dc\ub300\uc5d0 \uc784\uc9c4\uc65c\ub780, \ubcd1\uc790\ud638\ub780\uc744 \uce58\ub978 \ub4a4 \uc219\uc885 37\ub144(1711\ub144)\uc5d0 \uc2dc\uc791\ud558\uc5ec, 6\uac1c\uc6d4 \ub9cc\uc778 \uadf8\ud574 10\uc6d4\uc5d0 \uc644\uacf5\ud558\uc600\ub2e4 \ud55c\ub2e4. <BR>\ubd81\ud55c\uc0b0\uc131\uc740 \ud5d8\ud55c \uc0b0\uc138\ub97c \uc774\uc6a9\ud558\uc5ec \uc815\uc0c1\uc744 \uae30\uc810\uc73c\ub85c \uc11c\ucabd \uc0b0\uc790\ub77d\ubd80\ud130 \uc6d0\ud6a8\ubd09, \uc5fc\ucd08\ubd09, \ubc31\uc6b4\ub300, \ub9cc\uacbd\ub300, \uc6a9\uc554\ubd09, \ubb38\uc218\ubd09, \ub098\ud55c\ubd09, \ub098\uc6d4\ubd09, \uc6a9\ucd9c\ubd09, \uc758\uc0c1\ubd09\uae4c\uc9c0 \uc5f0\uacb0\ud558\uc5ec \uc313\uc740 \uc0b0\uc131\uc73c\ub85c \ucd1d \uae38\uc774\uac00 10km\uc5d0 \ub2ec\ud55c\ub2e4. \ub2f9\uc2dc \uc0b0\uc131\uc5d0\ub294 14\uac1c\uc758 \uc131\ubb38\uc744 \ub0c8\ub294\ub370, \uc0b0\uc131\uc758 \uc815\ubb38 \uaca9\uc778 \ub300\uc11c\ubb38\uc744 \uc911\uc2ec\uc73c\ub85c \ubd81\ucabd\uc73c\ub85c \uc218\ubb38, \uc11c\uc554\ubb38(\uc2dc\uad6c\ubb38), \ubd81\ubb38, \ubc31\uc6b4\ub300\ub97c \uc9c0\ub098 \uc704\ubb38, \uc6a9\uc554\ubb38, \ub300\ub3d9\ubb38, \ubcf4\uad6d\ubb38, \ub300\uc131\ubb38, \ub300\ub0a8\ubb38, \uccad\uc218\ub3d9\uc554\ubb38, \ub300\uc11c\ubb38\uc5d0\uc11c \ubd81\uc11c\ucabd\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \uc0b0\uc131\uc5d0\ub294, \ubd80\ud669\ub3d9\uc554\ubb38, \uac00\uc0ac\ub3d9\uc554\ubb38\uc774 \uc124\uce58\ub418\uc5c8\ub2e4.<BR>\ud604\uc7ac\ub294 \ub300\ubd80\ubd84 \uc131\ubb38\uc744 \ubcf5\uc6d0\ud558\uc600\ub2e4. \ubd81\ud55c\uc0b0\uc5d0 \uc218\ub9ce\uc740 \ub4f1\uc0b0\ub85c \uac70\ubbf8\uc904 \uac19\uc774 \uc5bd\ud600 \uc788\uc5b4 \uc0b0\ud589\uc2dc \uc8fc\uc758\ub97c \uc694\ud558\ub294 \uacf3\uc774\ub2e4. \ubd81\ud55c\uc0b0\uc758 \uc0b0\ud589 \ub4e4\uba38\ub9ac\ub294 \ub300\ud45c\uc801\uc73c\ub85c \uc5ec\uc12f \uad70\ub370\ub97c \uaf3d\uace0 \uc788\ub2e4. \uc6b0\uc774\ub3d9 \uae30\uc810, 4.19\ud0d1 \uae30\uc810, \uc815\ub989 \uae30\uc810, \uc138\uac80\uc815 \uae30\uc810, \ubd88\uad11\ub3d9 \uae30\uc810, \uad6c\ud30c\ubc1c \ubd81\ud55c\uc0b0\uc131 \uae30\uc810\uc73c\ub85c \uad6c\ubd84\ud560 \uc218 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ubd81\ud55c\uc0b0\uc131 \ub9e4\ud45c\uc18c-\uc704\ubb38-\uc6a9\uc554\ubb38-\ub300\ub3d9\ubb38-\ub300\uc131\ubb38-\ub300\ub0a8\ubb38-\uac00\uc0ac\ub2f9\uc554\ubb38-\ubd81\ud55c\uc0b0\uc131\ub9e4\ud45c\uc18c (\ucd1d 7\uc2dc\uac04)<BR>\u2461 \uad6c\uae30\ub3d9-\uc774\ubd815\ub3c4\uccad-\uae08\uc120\uc0ac-\ud5a5\ub85c\ubd09-\ube44\ubd09-\uc2b9\uac00\ubd09-\ubb38\uc218\ubd09-\ub300\ub0a8\ubb38-\uad6c\uae30\ub3d9 (\ucd1d 4\uc2dc\uac04 20\ubd84)<BR>\u2462 \ubd81\uc545\ud130\ub110-\ud615\uc81c\ubd09-\ubcf4\ud604\ubd09-\ub300\uc131\ubb38-\ubcf4\uad6d\ubb38-\ub300\ub3d9\ubb38-\ub3d9\uc7a5\ub300-\uc6a9\uc554\ubb38-\ubc31\uc6b4\ub300-\uc6b0\uc774\ub3d9 (\ucd1d 7\uc2dc\uac04)'), '37.639816', '127.025534', STRINGDECODE('\uc11c\uc6b8\ud2b9\ubcc4\uc2dc \uac15\ubd81\uad6c\u318d\uc131\ubd81\uad6c\u318d\uc885\ub85c\uad6c\u318d\uc740\ud3c9\uad6c, \uacbd\uae30\ub3c4 \uace0\uc591\uc2dc\u318d\uc591\uc8fc\uc2dc'), 1103030100, 836, STRINGDECODE('\ubd81\ud55c\uc0b0'), STRINGDECODE('&lt;\uba85\uc0b0\uc758 3\ub300\uc870\uac74 \uad6c\ube44\ud55c \ubc14\uc704\uc758 \uc131\ucc44&gt;<BR>\ubd81\ud55c\uc0b0\uc740 \uc131\ucc44\ub2e4. \ucd5c\uace0\ubd09 \ubc31\uc6b4\ub300\ub97c \ube44\ub86f\ud558\uc5ec \uc778\uc218\ubd09, \ub178\uc801\ubd09, \uc758\uc0c1\ubd09, \ubcf4\ud604\ubd09 \ub4f1 \uac78\ucd9c\ud55c \uc554\ubd09\ub4e4\uc774 \ud5d8\uc900\ud55c \uc0b0\uc138\ub97c \uc774\ub8e8\uace0 \uc5ec\uae30 \uc758\uc9c0\ud558\uc5ec \uc0ac\ub78c\ub4e4\uc774 \uc9c4\uc9dc \uc131\uc744 \uc313\uc558\ub2e4. <BR>\uc369 \ub192\uc740 \uc0b0\uc740 \uc544\ub2c8\uc9c0\ub9cc \uc8fc\ubcc0\uc5d0 \uc5b4\uae68\ub97c \uaca8\ub8f0 \ub300\uc0c1\uc774 \uc5c6\uc5b4 \ub208 \ub2ff\ub294 \uacf3\uc774\uba74 \uc5b4\ub514\uc11c\ub098 \ubcf4\uc778\ub2e4. \ubd81\uc73c\ub85c \uac1c\uc131, \ub0a8\uc73c\ub85c \uc548\uc131\uc5d0 \uc774\ub974\uae30\uae4c\uc9c0. \ubc31\uc6b4\uc0b0~\uc6b4\uc545\uc0b0~\uc6a9\ubb38\uc0b0~\uce58\uc545\uc0b0 \ub77c\uc778 \uc774\uc11c(\u4ee5\u897f)\uc5d0\ub294 \ub36e\uc744 \uc790\uac00 \uc5c6\ub294 \uac83\uc774\ub2e4. <BR>\uc138 \ubfd4\ucc98\ub7fc \uc19f\uc740 \uc815\uc0c1\ubd80\uc758 \ubd09\ub9cc\ubbf8\uac00 \ud0c0\uc758 \ucd94\uc885\uc744 \ubd88\ud5c8\ud558\uac8c \uc778\uc0c1\uc801\uc774\ub2e4. \uc6b0\uc774\ub3d9\uacc4\uace1, \ubd81\ud55c\uc0b0\uc131\uacc4\uace1, \ud3c9\ucc3d\ub3d9\uacc4\uace1 \ub4f1\uc5d0\ub294 \ud3ed\ud3ec\uc640 \uc554\ubc18\uacc4\ub958\uc758 \uacc4\uace1\ubbf8\ub3c4 \uac16\ucd94\uc5c8\ub2e4. \uc815\uc0c1\uc5d0 \uc11c\uba74 \uc77c\uc758\ub300\uc218(\u4e00\u8863\u5e36\u6c34) \ud55c\uac15\uc774 \ud587\ubcd5\uc5d0 \ubc18\uc9dd\uc774\uace0\uc788\ub2e4. \uc774\uc57c\ub9d0\ub85c \uba85\uc0b0\uc758 3\ub300 \uc870\uac74\uc744 \uc644\ubcbd\ud558\uac8c \ub9de\ucd94\uace0\uc788\ub294 \uac83\uc774\ub2e4. <BR>\ubc31\uc6b4\ub300\uc5d0\uc11c \ubcf4\ud604\ubd09\uc5d0 \uc774\ub974\ub294 \uc7a5\uc5c4\ud55c \uc8fc\ub989\uc5d0\uc11c \uc6d0\ud6a8\ubd09\ub2a5\uc120, \uc0c1\uc7a5\ubd09\ub2a5\uc120, \uc9c4\ub2ec\ub798\ub2a5\uc120, \uce7c\ubc14\uc704\ub2a5\uc120, \ud615\uc81c\ubd09\ub2a5\uc120, \ud5a5\ub85c\ubd09\ub2a5\uc120, \uc758\uc0c1\ubd09\ub2a5\uc120\uc774 \ubc29\ucd94\ucc28\uc758 \uc2e4\ucc98\ub7fc \ubed7\uc5b4\ub098\uac00\uba70 \uadf8\ub9cc\ud55c \uc218\uc758 \uacc4\uace1\uc744 \ub9cc\ub4e0 \uae4c\ub2ed\uc5d0 \ucc9c\ub9cc \uc11c\uc6b8\uc0ac\ub78c\ub4e4\uc774 \uc2dc\ub3c4 \ub54c\ub3c4 \uc5c6\uc774 \ucc3e\uc544\ub3c4 \ub2e4 \ud488\uc5b4 \uc548\ub294\ub2e4. \uc778\uc218\ubd09, \ub178\uc801\ubd09, \uc218\ub9ac\ubd09\uc744 \uba54\uc8fc\ucc98\ub7fc \ube5a\uc5b4\ub193\uace0 \u201c\uc790\uc77c\uacfc \ud574\uba38, \ud558\ucf04, \uce74\ub77c\ube44\ub108\ub85c \uc80a\uc74c\uc744 \ud0dc\uc6cc\ubcf4\uc138\u201d \uc678\uce58\ub294 \ud074\ub77c\uc774\uba38\ub4e4 \uc138\uacc4\uc758 \uc0b0\uc744 \ub204\ube44\ub3c4\ub85d \ud6c8\ub828\uc2dc\ud0a4\uae30\ub3c4 \ud55c\ub2e4. \ubd81\ud55c\uc0b0\uc740, \ud0dc\uc5b4\ub0a0 \ub54c\ubd80\ud130 \uc218\ub3c4\uc758 \uc9c4\uc0b0\uc774 \ub420 \uc790\uaca9\uc774 \uc788\uc5c8\ub2e4.'), STRINGDECODE('\ucd5c\uace0\ubd09\uc778 \ubc31\uc6b4\ub300\ub97c \uc704\uc2dc\ud558\uc5ec \uc778\uc218\ubd09, \ub9cc\uacbd\ub300, \ub178\uc801\ubd09 \ub4f1 \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0 \ub3c4\uc2dc\ubbfc\ub4e4\uc758 \ud734\uc2dd\ucc98\uc774\uba70 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1983\ub144)\ub418\uc5b4 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubd81\ud55c\uc0b0\uc131, \uc6b0\uc774\ub3d9\uacc4\uace1, \uc815\ub989\uacc4\uace1, \uc138\uac80\uc815\uacc4\uace1 \ub4f1\uc774 \uc720\uba85. \ub3c4\uc120\uad6d\uc0ac\uac00 \ucc3d\uac74\ud55c \ub3c4\uc120\uc0ac(\u9053\u8a75\u5bfa), \ud0dc\uace0\uc0ac(\u592a\u53e4\u5bfa), \ud654\uacc4\uc0ac(\u83ef\u6eaa\u5bfa), \ubb38\uc218\uc0ac(\u6587\u6b8a\u5bfa), \uc9c4\uad00\uc0ac(\u6d25\u5bec\u5bfa) \ub4f1 \uc218\ub9ce\uc740 \uace0\ucc30\uc774 \uc788\uc74c'), STRINGDECODE('\uac00\uac70\ub77c \uc0bc\uac01\uc0b0\uc544, \ub2e4\uc2dc \ubcf4\uc790 \ud55c\uac15\uc218\uc57c'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uac01 \ucf54\uc2a4\ubcc4 \ub4e4\uba38\ub9ac \ubd80\uadfc\uc5d0 \uc219\uc2dd\ud560 \uacf3\uc774 \ub9ce\uc73c\uba70 \uc778\uc218\uc0b0\uc7a5 \uc704\uc5d0\ub294 \uc57c\uc601\uc7a5\uc774 \uc788\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ubd81\ud55c\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e02-909-0497'), STRINGDECODE('\uc9c0\ud558\ucca0 3\ud638\uc120\uacfc 4\ud638\uc120\uc774\ub2e4. \uc6b0\uc774\ub3d9\uc740 4\ud638\uc120 \uc218\uc720\uc5ed\uc5d0\uc11c \ub0b4\ub824 101\ubc88, 1217\ubc88, \ubd81\uc545\ud130\ub110\uc740 \uae38\uc74c\uc5ed\uc5d0\uc11c 7211\ubc88\uc744 \ud0c0\uace0 \uad6d\ubbfc\ub300\uc5d0\uc11c \ub0b4\ub9b0\ub2e4. \uad6c\uae30\ub3d9\uc740 3\ud638\uc120\uacfc 6\ud638\uc120 \ubd88\uad11\uc5ed\uc5d0\uc11c 7022\ubc88\uacfc 7211\ubc88, \ubd81\ud55c\uc0b0\uc131\uc740 \uad6c\ud30c\ubc1c\uc5ed\uc5d0\uc11c 704\ubc88\uc744 \ud0c4\ub2e4.<BR>&gt; \uc218\uc720\uc5ed\u2192\uc6b0\uc774\ub3d9 : 5\ubd84 \uac04\uaca9 \uc6b4\ud589 <BR>&gt; \ubd88\uad11\uc5ed\u2192\uad6c\uae30\ud130\ub110 : 5\ubd84 \uac04\uaca9 \uc6b4\ud589.<BR>&gt; \uad6c\ud30c\ubc1c\u2192\uc0b0\uc131\ub9e4\ud45c\uc18c : 5\ubd84 \uac04\uaca9 \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(48, TIMESTAMP '2019-08-14 15:46:34.923', TIMESTAMP '2019-08-14 15:47:08.101', '27', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uccad\ub3c4\uad70\uacfc \ub300\uad6c\uad11\uc5ed\uc2dc \ub2ec\uc131\uad70\uc758 \uacbd\uacc4\uc5d0 \uc788\ub294\uc0b0, \ucd5c\uace0\ubd09\uc740 \ub300\uac74\ubd09\uc774\uba70 \ub0a8\ucabd\uc73c\ub85c\ub294 \uc870\ud654\ubd09, \uad00\uae30\ubd09\uacfc \uc774\uc5b4\uc9c0\uba70 \uc720\uac00\uc0ac\ucabd\uc5d0\uc11c \uc62c\ub824\ub2e4\ubcf4\uba74 \uc815\uc0c1\uc744 \ub5a0\ubc1b\uce58\uace0 \uc788\ub294 \uac70\ub300\ud55c \ubc14\uc704\ub2a5\uc120\uc774 \uc6b0\ub69d \uc19f\uc544\uc788\ub2e4. <BR>\uc815\uc0c1\uc5d0\uc11c \ubc14\ub77c\ubcf4\ub294 \ub099\ub3d9\uac15\uc758 \uacbd\uce58\uac00 \uc544\ub984\ub2f5\uace0 \ubd04\ucca0\uc5d0\ub294 \ucca0\ucb49, \uc9c4\ub2ec\ub798 \uac00\uc744\uc5d0\ub294 \uc5b5\uc0c8\uad70\ub77d\uc774 \ubcfc\ub9cc\ud558\ub2e4. \uc2a4\ub2d8\ubc14\uc704, \ucf54\ub07c\ub9ac\ubc14\uc704, \ud615\uc81c\ubc14\uc704\ub4f1\uc758 \uc774\ub984\ub09c \ubc14\uc704\uc640 \ub2ec\uc131\uad70 \uc625\ud3ec\uba74\uc758 \uc6a9\uc5f0\uc0ac\ub97c \ube44\ub86f\ud558\uc5ec \uc6a9\ubb38\uc0ac, \uc720\uac00\uc0ac\ub4f1\uc758 \uc0ac\ucc30\uc774 \uc0b0\uc7ac\ud55c\ub2e4. \uc815\uc0c1\uc778 \ub300\uacac\ubd09\uc5d0\uc11c \ub0a8\ucabd \ub2a5\uc120\uc744 \ub530\ub77c 988\ubd09 - \uc870\ud654\ubd09\uc73c\ub85c \uc774\uc5b4\uc9c4\ub2e4. <BR>\uc870\ud654\ubd09 \ub2a5\uc120\uc5d0\uc11c \uc11c\ucabd\uc73c\ub85c \ub300\uacac\uc0ac \ud130 - 1034\ubd09\uc73c\ub85c \uc774\uc5b4\uc9c0\uba70 1034\ubd09\uc5d0 \ud314\uac01\uc815 \uc804\ub9dd\ub300\uac00 \uc124\uce58\ub418\uc5b4 \uc788\ub2e4. \uc815\uc0c1\uc5d0\uc11c \ubd81\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub2a5\uc120\uc740 \uc55e\uc0b0\uc73c\ub85c \uac00\ub294 \uc548\ub0b4\ud45c\uc2dc\uac00 \ub418\uc5b4 \uc788\ub2e4.\uc815\uc0c1\uc5d0\uc11c \uc870\ud654\ubd09 \uae4c\uc9c0 \uc57d 4km\uc5d0 \uac78\uce5c \ub2a5\uc120\uc740 988\ubd09 \uc8fc\ubcc0\uc5d0 \ubc14\uc704\uac00 \uc788\uc744 \ubfd0 \uc721\uc0b0(\ud751\uc0b0)\uc73c\ub85c \ud070 \ub098\ubb34\ub4e4\uc774 \uc5c6\ub294 \uc2dc\uc57c\uac00 \ud0c1 \ud2b8\uc774\ub294 \ucd08\uc6d0 \uac19\uc740 \uc774 \ub2a5\uc120\uc5d0 \uac00\uc744\uc5d0\ub294 \uc5b5\uc0c8\uac00, \ubd04\uc5d0\ub294 \uad70\ub77d\uc744 \uc774\ub8ec \uc9c4\ub2ec\ub798\uac00 \ubd89\uac8c \ubb3c\ub4e4\uc778\ub2e4. <BR>\uc9c4\ub2ec\ub798 \uad70\ub77d\uc0ac\uc774\uc5d0 \uc2f8\ub9ac\ub098\ubb34 \ub4f1 \uc7a1\ubaa9\ub4e4\uc774 \uc11e\uc5ec \uc788\uc73c\ub098 \uc9c4\ub2ec\ub798\uac00 \ub354 \ub9ce\ub2e4.\ube44\uc2ac\uc0b0 \uc9c4\ub2ec\ub798\ub294 \uc815\uc0c1\ubd80\uadfc, 988\ubd09 \ubd80\uadfc \uc544\ub798, \ub300\uacac\uc0ac \ud130 \uc0b0\uc790\ub77d \ub4f1 \ud06c\uac8c 3\uad70\ub370\uc5d0 \uad70\ub77d\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4. \ub300\uacac\uc0ac\ud130 \ubd81\ucabd \uad11\ud65c\ud55c 30\uc5ec\ub9cc\ud3c9\uc758 \uc0b0\uc790\ub77d\uc774 \ub300\uaddc\ubaa8 \uc9c4\ub2ec\ub798 \uad70\ub77d\uc9c0\uc774\uba70, \uc9c4\ub2ec\ub798\uac00 \uac00\uc7a5 \uacf1\uace0 \ubc00\uc9d1\ub418\uc5b4 \uc788\ub294 \uacf3\uc740 988\ubd09 \ubd80\uadfc \uc544\ub798 \uc0b0\uc790\ub77d\uc774\ub2e4. \uc9c4\ub2ec\ub798\ub294 4\uc6d4 \uc911\uc21c\ubd80\ud130 \ubb3c\ub4e4\uae30 \uc2dc\uc791\ud574 4\uc6d4 \ub9d0\uc5d0 \uc808\uc815\uc5d0 \ub2ec\ud55c\ub2e4. 4\uc6d4 \ud558\uc21c\uacbd \ucc38\uaf43(\uc9c4\ub2ec\ub798)\uc81c\uac00 \uc5f4\ub9b0\ub2e4.'), STRINGDECODE('\u2460 \uc6a9\ucc9c\uc0ac-778\ubd09-\ub300\uacac\ubd09-\ub3c4\uc131\uc554-\uc720\uac00\uc0ac-\uc911\ubafc (\ucd1d 3\uc2dc\uac04 30\ubd84)<BR>\u2461 \ube44\uc2ac\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-1005\ubd09-\ub300\uacac\ubd09-\ubd81\ub989-\uc6a9\uc5f0\uc0ac (\ucd1d 5\uc2dc\uac04 10\ubd84)<BR>\u2462 \uc624\uc0b0\ub9ac-\ub300\ub3d9\uace8-\uc0ac\uac70\ub9ac \uc548\ubd80-\ub300\uacac\ubd09-\ubcd1\ud48d\ub4ec-\uc720\uac00\uc0ac (\ucd1d 3\uc2dc\uac04 10\ubd84)'), '35.769768', '128.476892', STRINGDECODE('\ub300\uad6c\uad11\uc5ed\uc2dc \ub2ec\uc131\uad70 \uc625\ud3ec\uba74\u318d\uc720\uac00\uba74\u318d\uac00\ucc3d\uba74, \uacbd\uc0c1\ubd81\ub3c4 \uccad\ub3c4\uad70 \uac01\ubd81\uba74'), 1303011500, 1083, STRINGDECODE('\ube44\uc2ac\uc0b0'), STRINGDECODE('&lt;\ub3cc\uc758 \ubc14\ub2e4 \ud488\uc740 \uc9c4\ub2ec\ub798 \uba85\uc0b0&gt;<BR>\ub300\uad6c\ub97c \ubd84\uc9c0\ub85c \ub9cc\ub4dc\ub294 \ub450 \uc0b0\uc740 \ud314\uacf5\uc0b0\uacfc \ube44\uc2ac\uc0b0\uc774\ub2e4. \ud654\uac15\uc554\uc9c8\uc758 \ud314\uacf5\uc0b0\uc774 \uc0b0\uc138\uac00 \uc6c5\uc7a5\ud558\uc5ec \uc544\ubc84\uc9c0 \uc0b0\uc774\ub77c\uba74 \uc548\uc0b0\uc554\uc9c8\uc758 \ube44\uc2ac\uc0b0\uc740 \uc628\ud654\ud558\uace0 \ubd80\ub4dc\ub7ec\uc6cc \uc5b4\uba38\ub2c8 \uc0b0\uc774\ub77c \ud560 \ub9cc\ud558\ub2e4. <BR>\ube44\uc2ac\uc0b0\uc740 \uc6b0\ub9ac\ub098\ub77c\uc5d0\uc11c \ub2e4\uc12f \uc190\uac00\ub77d\uc5d0 \uaf3d\ub294 \uc9c4\ub2ec\ub798 \uba85\uc0b0\uc774\ub2e4. \ud3c9\ud0c4\ud55c \uc815\uc0c1\ubd80\uc5d0 \uc704\uce58\ud55c \uc9c4\ub2ec\ub798 \uad70\ub77d\uc9c0\ub294 \ubd04\uc774\uba74 \uaf43\ubc14\ub2e4\ub97c \uc774\ub8ec\ub2e4. \uc9c4\ub2ec\ub798\uc640 \ub354\ubd88\uc5b4 \ube44\uc2ac\uc0b0\uc774 \uac16\ucd98 \uba85\ud488\uc740 \ubc14\uc704\ub2e4. <BR>\ube44\uc2ac\uc0b0\uc758 \ubc14\uc704\ub294 \uc544\ub984\ub2e4\uc6b8\ubfd0\ub9cc \uc544\ub2c8\ub77c \uc2e0\uae30\ud558\ub2e4. \uc808\ubcbd \ubc11\uc5d0 \ubd80\ucc44\uaf34 \ubaa8\uc591\uc73c\ub85c \uc313\uc778 \ub3cc\ubb34\ub354\uae30\uc640 \uc554\uad34\ub958\uac00 \ub9cc\ub4e4\uc5b4\ub0b4\ub294 \ud48d\uacbd\uc740 \uacfc\ud788 \ub3cc\uc758 \ubc14\ub2e4\ub77c \ud560 \ub9cc\ud558\ub2e4.'), STRINGDECODE('\ubd04\uc5d0\ub294 \uc9c4\ub2ec\ub798, \uac00\uc744\uc5d0\ub294 \uc5b5\uc0c8 \ub4f1 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70, \uc870\ub9dd\uc774 \uc88b\uace0 \uad70\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubd81\ucabd\uc758 \ud314\uacf5\uc0b0\uacfc \ud568\uaed8 \ub300\uad6c\ubd84\uc9c0\ub97c \ud615\uc131\ud558\uba70 \ub099\ub3d9\uac15\uc744 \ub07c\uace0 \uc788\uc74c. \uace0\ub824\ub9d0 \uacf5\ubbfc\uc655 7\ub144(1358\ub144) \uc9c4\ubcf4\ubc95\uc0ac\uac00 \ucc3d\uac74\ud55c \uc18c\uc7ac\uc0ac(\u6d88\u707d\u5bfa) \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ub300\uad6c \ub0a8\ucabd\uc5d0 \ud488 \ub113\uac8c \uc6b0\ub69d \uc19f\uc740 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc720\uac00\uc0ac \uc8fc\ucc28\uc7a5 \ubd80\uadfc\uc5d0 \uc74c\uc2dd\uc810 \uacb8 \ubbfc\ubc15\uc774 \uc11c\ub108 \uacf3 \uc788\ub2e4. <BR>\ub9cc\ubb3c\uc2dd\ub2f9 \u260e053-614-2456, \ud604\ud48d\uc758 \uba39\uac70\ub9ac\ub85c ''\ud604\ud48d \ud560\ub9e4\uacf0\ud0d5''\uc744 \ube7c\ub193\uc744 \uc218 \uc5c6\ub2e4. <BR>\ud604\ud48d \uc2dc\ub0b4 \ubc15\uc18c\uc120\ud560\ub9e4\uacf0\ud0d5 \u260e053-615-1122<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ube44\uc2ac\uc0b0\uc790\uc5f0\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e053-614-5481~2'), STRINGDECODE('\uae30\uc810\uc740 \ub300\uad6c\ub2e4. <BR>\uccad\ub3c4 \ubc84\uc2a4\uc815\ub958\uc7a5\uc5d0\uc11c 30~40\ubd84 \uc18c\uc694<BR>\ub300\uad6c-\ud604\ud48d-\uc2dc\uc678\ubc84\uc2a4\ub97c \uc774\uc6a9\ud558\uc5ec \ud604\ud48d\uc5d0\uc11c \uc18c\uc7ac\uc0ac\ub098 \uc720\uac00\uc0ac\ud589 \ubc84\uc2a4\ub97c \uc774\uc6a9(\uc57d 1\uc2dc\uac04 \uc18c\uc694)\ud55c\ub2e4. \ub300\uad6c\uc5d0\uc11c \ud604\ud48d\uc744 \uc655\ubcf5\ud558\ub294 \uc2dc\ub0b4\ubc84\uc2a4\uac00 \uc788\uc73c\uba70 \ud1a0\uc694\uc77c\uacfc \uc77c\uc694\uc77c\uc5d0\ub294 \uc0b0\uc785\uad6c \uc8fc\ucc28\uc7a5\uae4c\uc9c0 \uc6b4\ud589\ud55c\ub2e4.(601\ubc88)<BR>\ud604\ud48d\uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110\uc5d0\uc11c \ud604\ud48d\uacfc \uc720\uac00\uc0ac\ub97c 1\uc77c 6\ud68c 2\uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud55c\ub2e4'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(49, TIMESTAMP '2019-08-14 15:46:34.924', TIMESTAMP '2019-08-14 15:47:08.691', '42', STRINGDECODE('\uc0bc\uc545\uc0b0(\u4e09\u5cb3\u5c71)\uc740 \ud55c\ubd81\uc815\ub9e5\uc0c1\uc758 \ud3ec\ucc9c \ubc31\uc6b4\uc0b0 \ubc11\uc5d0\uc11c \ub3d9\ucabd\uc73c\ub85c \uac08\ub77c\uc9c4 \uc9c0\ub9e5\uc774 \uc11d\ub8e1\uc0b0, \ud654\uc545\uc0b0, \uacc4\uad00\uc0b0\uc73c\ub85c \uc774\uc5b4\uc9c0\uace0 \uc61b\ub0a0 \uacbd\ucd98 \uac04 \uc721\ub85c\uad50\ud1b5\uc758 \ucd98\ucc9c\uad00\ubb38\uc774\uc5c8\ub358 \uc11d\ud30c\ub839\uc744 \ub118\uc5b4 \ub044\ud2b8\uba38\ub9ac\uc5d0\uc11c \uc19f\uc544 \uc2e0\uc5f0\uac15 \ud611\uace1\uc744 \uc774\ub8e8\uc5b4 \ub193\uc740 \uba85\uc0b0\uc774\ub2e4. <BR>\ub610\ud55c \ud611\uace1\uc5d0 \uc124\uce58\ub41c \uc758\uc554\ub310\uc73c\ub85c \ud638\uc218\uac00 \uc0dd\uaca8 \ubb3c \uc18d\uc5d0 \ud22c\uc601\ub41c \uc0b0\uc744 \ubcfc \uc218 \uc788\uc5b4 \ub354 \ud55c\uce35 \uc88b\ub2e4. \ub9e4\ud45c\uc18c\uc5d0\uc11c \uacc4\uace1\uc744 \uc624\ub974\ub2e4 \ubcf4\uba74 \ub4f1\uc120\ud3ed\ud3ec, \ube44\uc120\ud3ed\ud3ec, \uc2b9\ud559\ud3ed\ud3ec, \ubc31\ub828\ud3ed\ud3ec\ub4f1 \ud06c\uace0 \uc791\uc740 \ud3ed\ud3ec\uac00 \uc788\uc73c\uba70, \uc6a9\ud654\ubd09, \uccad\uc6b4\ubd09, \ub4f1\uc120\ubd09\uc744 \ube44\ub86f 3\uac1c\uc758 \ubd09\uc6b0\ub9ac\uc5d0\uc11c \ubed7\uc5b4\ub0b4\ub9b0 \ub2a5\uc120\uc774 \uc554\ubd09\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc808\uacbd\uc744 \uc774\ub8ec\ub2e4.\uae30<BR>\uc554\uad34\uc11d\uc774 \ub9ce\uace0 \uc18c\ub098\ubb34, \ucc38\ub098\ubb34\ub4f1\uc758 \uc218\ub9bc\uc774 \uc6b8\ucc3d\ud558\uba70 \uacc4\uace1\ubbf8\uac00 \ube7c\uc5b4\ub098\ub2e4.\uc0bc\uc545\uc0b0\uc758 \uc0b0\ud589\uae30\uc810\uc740 \uc138 \uad70\ub370 \uc774\ub2e4. \ub4f1\uc120\ud3ed,\uc0c1\uc6d0\uc0ac,\uac15\ucd0c\uc5ed\uc5d0\uc11c \ub2e4\ub9ac\uac74\ub108 \ubc14\ub85c \uc2dc\uc791\ud558\ub294 \uc138 \uae30\uc810\uc774 \uc788\ub2e4. \ub300\ubd80\ubd84\uc758 \ub4f1\uc0b0\uac1d\ub4e4\uc774 \ub4f1\uc120\ud3ed\ucabd\uc5d0\uc11c \uc0b0\ud589\uc744 \uc2dc\uc791\ud558\ub098, \uc758\uc554\ub310\uc774 \uc788\ub294 \uc0c1\uc6d0\uc0ac\uc785\uad6c\uc5d0\uc11c \uc2dc\uc791\ud558\uc5ec \ub4f1\uc120\ud3ed\ud3ec\ub85c \ub0b4\ub824\uc624\ub294 \uac83\uc774 \uc88b\ub2e4.'), STRINGDECODE('\uac15\ucd0c-\ub4f1\uc120\ubd09-619\ubd09-\uc815\uc0c1-\uc0c1\uc6d0\uc0ac-\uc758\uc554\ub310 \ub9e4\ud45c\uc18c (\ucd1d 4\uc2dc\uac04 10\ubd84)'), '37.898138', '127.643591', STRINGDECODE('\uac15\uc6d0\ub3c4 \ucd98\ucc9c\uc2dc \uc11c\uba74'), 2213142900, 656, STRINGDECODE('\uc0bc\uc545\uc0b0'), STRINGDECODE('&lt;\uc218\ub3c4\uad8c \ub2f9\uc77c \uc5f4\ucc28\uc0b0\ud589\uc9c0\uc758 \ub300\uba85\uc0ac&gt;<BR>\uc0bc\uc545\uc0b0\uc740 \ub4f1\uc120\ubd09, \uccad\uc6b4\ubd09\uc5d0\uc11c \uc774\uc5b4\uc9c0\ub294 \ud55c \ubd09\uc6b0\ub9ac\ub2e4. \uac15\ucd0c\uc758 \ub300\ud45c\uc801\uc778 \uc0b0\uc73c\ub85c \ud558\ub098\uc758 \ub3c5\ub9bd\ubd09\uc73c\ub85c \uc0ac\ub78c\ub4e4\uc774 \uc990\uaca8 \ucc3e\uace0 \uc788\uc73c\uba70 \uc811\uadfc\uc774 \uc6a9\uc774\ud558\uc5ec \ub2f9\uc77c\uc0b0\ud589\uc9c0\ub85c\uc11c \uac01\uad11\ubc1b\uace0 \uc788\ub2e4. \uac70\ub300\ud55c \uc131\uc744 \uc5f0\uc0c1\uc2dc\ud0a4\ub294 \ub4f1\uc120\ud3ed\ud3ec \ub9e4\ud45c\uc18c\ub97c \uc9c0\ub098 \uae4e\uc544\uc9c0\ub978 \uc808\ubcbd \uc0ac\uc774\ub85c \ub09c \ud3ed\ud3ec\uae38\uc744 \ub4e4\uba38\ub9ac\ub85c \uc0bc\uac70\ub098 \uc758\uc554\ub310 \ub9e4\ud45c\uc18c\uc5d0\uc11c \uc0b0\ud589\uc744 \uc2dc\uc791\ud574\ub3c4 \ubb34\ubc29\ud558\ub2e4. \ud3ed\ud3ec\uae38\uc740 \ucca0\uacc4\ub2e8\uc774 \ub9cc\ub4e4\uc5b4\uc838 \uc788\uc5b4 \uac00\uae4c\uc774\uc5d0\uc11c \ud3ed\ud3ec\uc758 \uc6c5\uc7a5\ud568\uc744 \uc5ff\ubcfc \uc218 \uc788\uc73c\uba70 \ub530\uc2a4\ud55c \ud587\ube5b\uc758 \ud638\uc704\ub97c \ubc1b\uc73c\uba70 \uc0b0\ud589\ud560 \uc218 \uc788\ub2e4. \ubc18\uba74 \uc758\uc554\ub310 \uae38\uc740 \uccad\uc815\ud55c \ub178\uc1a1\uacfc \ubc14\uc704\ub4e4\uc774 \uc5b4\uc6b0\ub7ec\uc9c4 \uc808\uacbd\uc774 \ube7c\uc5b4\ub09c \ubc14\uc717\uae38\uc774\ub2e4. <BR>\ubc14\uc704\ub2a5\uc120\uc73c\ub85c \uad6c\uc131\ub41c \uc0bc\uc545\uc0b0 \uc815\uc0c1\uc5d0 \uc11c\uba74 \ubd81\uc11c\ucabd\uc73c\ub85c \ud654\uc545\uc0b0(1468.3m)\uc774, \uadf8 \uc624\ub978\ucabd\uc73c\ub85c \ubd81\ubc30\uc0b0(869.6m)\uacfc \uacc4\uad00\uc0b0(736m)\uc758 \ub2a5\uc120\uc774 \uc774\uc5b4\uc9c4\ub2e4. \ubd81\ub3d9\ucabd\uc73c\ub85c\ub294 \ubd81\ud55c\uac15\uacfc \uc18c\uc591\uac15\uc744 \uba38\uae08\uace0 \uc788\ub294 \uc758\uc554\ud638\uac00 \ubc1c\uc544\ub798\ub85c \ud3bc\uccd0\uc9c0\uba70 \uc6a9\ud654\uc0b0(877.8m), \uc624\ubd09\uc0b0(777.9m) \ub4f1\ub3c4 \ubcf4\uc778\ub2e4.'), STRINGDECODE('\uace0\uace0\uc2dc\ub300\uc5d0 \ud615\uc131\ub41c \ub4f1\uc120\uacc4\uace1\uacfc \ub9e5\uad6d\uc2dc\ub300\uc758 \uc0b0\uc131\ud130\uac00 \uc788\ub294 \uc720\uc11c\uae4a\uc740 \uc0b0\uc73c\ub85c \uae30\uc554\uad34\uc11d\uc758 \uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0, \uc758\uc554\ud638\uc640 \ubd81\ud55c\uac15\uc744 \uad7d\uc5b4\ubcf4\ub294 \uc870\ub9dd\uc774 \uc88b\uc740 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ub0a8\ucabd \uace8\uc9dc\uae30 \ucd08\uc785\uc758 \ud611\uace1\uacfc \ub4f1\uc120\ud3ed\ud3ec(\u767b\u4ed9\u7011\u5e03)\uac00 \ud2b9\ud788 \uc720\uba85\ud558\uace0, \ud765\uad6d\uc0ac(\u8208\u570b\u5bfa), \uae08\uc120\uc0ac(\u91d1\u4ed9\u5bfa), \uc0c1\uc6d0\uc0ac(\u4e0a\u9662\u5bfa) \ub4f1 7\uac1c \uc0ac\ucc30\uc774 \uc788\uc74c'), STRINGDECODE('\ubb3c, \ub098\ubb34, \uace8\uc9dc\uae30\uac00 \uc5f0\ucd9c\ud558\ub294 \ud48d\ub958\uc758 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uac15\ucd0c\uc5ed\uc5d0\uc11c \uad6c\uace1\ud3ed\ud3ec \ubc29\ud5a5\uacfc \uac15\ucd0c\uc720\uc6d0\uc9c0\uc5d0\ub294 \uc2dd\ub2f9\uacfc \ubb35\uc744 \uacf3\uc774 \uc990\ube44\ud558\ub2e4. <BR>\uc8fc\ub9d0\uc5d0 \uc774\uc6a9\ud560 \uc608\uc815\uc774\ub77c\uba74 \uc608\uc57d\uc740 \ud544\uc218<BR>\uac00\ub78c\ud734\uc6b0 \u260e033-261-5178, \uc0bc\uc545\uc0b0\uc7a5 \u260e033-243-8112<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ucd98\ucc9c\uc2dc \uc2dc\uc124\uad00\ub9ac\uacf5\ub2e8 \uad00\uad11\uc9c0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e033-250-3891, 033-251-3474, \ub4f1\uc120\ud3ed\ud3ec \ub9e4\ud45c\uc18c \u260e033-262-2215, \uc758\uc554\ub310 \ub9e4\ud45c\uc18c \u260e033-244-2331'), STRINGDECODE('\uae30\uc810\uc740 \uac15\ucd0c\uc5ed\uc774\ub2e4. \uccad\ub7c9\ub9ac\uc5ed\uc5d0\uc11c \uac15\ucd0c\ud589 \uc5f4\ucc28\ub97c \uc774\uc6a9\ud560 \uc218 \uc788\ub2e4.<BR>- \uccad\ub7c9\ub9ac\uc5ed\u2192\uac15\ucd0c\uc5ed \ubb34\uad81\ud654\ud638 \uc6b4\ud589<BR>- \uac15\ucd0c\uc5ed\u2192\uc0bc\uc545\uc0b0, \ub4f1\uc120\ud3ed\ud3ec 20\ubd84 \uac04\uaca9 \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(50, TIMESTAMP '2019-08-14 15:46:34.924', TIMESTAMP '2019-08-14 15:47:09.306', '44', STRINGDECODE('\uc11c\ub300\uc0b0\uc758 \ub192\uc774\ub294 904.1m\uc774\ub2e4. \ucda9\uccad\ub0a8\ub3c4 \ub0a8\ub3d9\ubd80\uc758 \uae08\uac15\ubd84\uc9c0\ub97c \ub458\ub7ec\uc2f8\uace0 \uc788\ub294 \uae08\uc0b0\uace0\uc6d0\uc5d0 \uc18d\ud574 \uc788\uc73c\uba70, \ub178\ub839\uc0b0\ub9e5\uc744 \uc774\ub8e8\ub294 \uc815\uc218\uc774\uc790 \ucda9\uccad\ub0a8\ub3c4\uc758 \ucd5c\uace0\ubd09\uc774\ub2e4. \ucda9\ub0a8\uc5d0\uc11c\ub294 \ub192\uc774 904.1m\uc73c\ub85c \uac00\uc7a5 \ub192\uc740 \uc0b0\uc73c\ub85c\uc11c \uae30\uc554 \uc808\ubcbd\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub2e4.<BR>\ud654\uac15\uc554\uc73c\ub85c \uc774\ub8e8\uc5b4\uc9c4 \uc6d0\ucd94\ud615 \uc554\uc0b0\uc778 \uc11c\ub300\uc0b0\uc740 \uae30\uc554\uc808\ubcbd\uacfc \uc232\uc774 \uc5b4\uc6b0\ub7ec\uc9c0\uace0 \uc81c\ubc95 \ud5d8\uc900\ud574 \uc0b0\ud589\uc758 \ubb18\ubbf8\ub97c \ub354\ud55c\ub2e4. \uc815\uc0c1\uc5d0 \uc62c\ub77c\uc11c\uba74 \uc6c5\uc7a5\ud558\uace0 \uc628\ud654\ud55c \uc0b0\uc138\uac00 \ud55c\ub208\uc5d0 \ub4e4\uc5b4\uc624\uace0 \uba40\ub9ac \uc11c\ubd81\ucabd\uc73c\ub85c \ub300\uc804\uc2dc\ub0b4\uac00 \ud3bc\uccd0\uc9c4\ub2e4.<BR>\uc6d0\ud765\uc0ac, \uac1c\ub355\uc0ac\ub4f1 \uc720\uba85\uc0ac\ucc30\uacfc \uc815\uc0c1 \uc9c1\uc804\uc5d0 \uc9c1\ub140 \ud0c4\uae08\ub300, \uc815\uc0c1\uc5d0\uc11c \ubd81\ucabd 546\ubd09\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub2a5\uc120 \uc8fc\ubcc0\uc5d0\ub294 \uc7a5\uba74\ub300, \ubd81\ub450\uce60\uc131 \ubc14\uc704, \uc0ac\uc790\uad74, \uc300\ubc14\uc704 \ub4f1\uc774 \uc0b0\uc7ac\ud574 \uc788\ub2e4.\ud611\uace1\uc744 \uac00\ub85c \uc9c8\ub7ec \ub192\uac8c \uc124\uce58\ub41c \uc57d 50m\uc758 \uad6c\ub984\ub2e4\ub9ac \uc8fc\ubcc0\uc740 \uc2e0\uc120\ubc14\uc704, \ubcbc\uc2ac\ubc14\uc704\ub4f1 \uae30\uc554 \uc808\ubcbd\ub4e4\uc774 \uc5b4\uc6b8\ub824 \uc7a5\uad00\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc11c\ub300\uc0b0\uccad\uc18c\ub144\uc218\ub828\uc6d0-\ub2ed\ubcbc\uc2ac\ubc14\uc704-\uc625\ub140\uc9c1\uae08\ub300-\uc815\uc0c1-\uacac\uc6b0\ud0c4\uae08\ub300-\uccab\uac08\ub9bc\uae38-\ub3d9\ub0a8\ub989-\uc548\ubcf4\uad11\ub9ac(\ucd1d 4\uc2dc\uac04)<BR>\u2461 \ub4dc\ub9bc\ub9ac\uc870\ud2b8-\uacc4\uace1\uae38-\uccab\uac08\ub9bc\uae38-\uc8fc\ub2a5\uc120-\uacac\uc6b0\ud0c4\uae08\ub300-\uc815\uc0c1-\uc625\ub140\uc9c1\uae08\ub300-\uc6d0\ud765\uc0ac(\ucd1d 4\uc2dc\uac04 15\ubd84)<BR>\u2462 \uc7a5\ub8e1\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-546\ubd09-\ub3c4\uacc4\uc0bc\uac70\ub9ac-\uad6c\ub984\ub2e4\ub9ac\uac08\ub9bc\uae38-\uacac\uc6b0\ud0c4\uae08\ub300-\uc815\uc0c1-\uc9c1\ub140\uc9c1\uae08\ub300-\uccad\uc18c\ub144\uc218\ub828\uc6d0(\ucd1d 5\uc2dc\uac04 45\ubd84)'), '36.204613', '127.491667', STRINGDECODE('\ucda9\uccad\ub0a8\ub3c4 \uae08\uc0b0\uad70 \ucd94\ubd80\uba74\u318d\uad70\ubd81\uba74, \ucda9\uccad\ubd81\ub3c4 \uc625\ucc9c\uad70 \uad70\uc11c\uba74'), 2403011500, 905, STRINGDECODE('\uc11c\ub300\uc0b0'), STRINGDECODE('&lt;\uacac\uc6b0\uc9c1\ub140 \uc804\uc124 \ud488\uc740 \ucda9\ub0a8 \ucd5c\uace0\ubd09&gt;<BR>900\ubbf8\ud130\uac00 \ub118\ub294, \ud070 \uc0b0\uc774 \ub9ce\uc9c0 \uc54a\uc740 \ucda9\ub0a8\uc5d0\uc11c \uac00\uc7a5 \ub192\uc740 \uc0b0\uc774\ub2e4. \ub0a8\ubd81\uc73c\ub85c \uae38\uace0 \ub3d9\uc11c\ub85c \uc587\uc73c\uba70 \ud640\ub85c \uc6b0\ub69d, \uc6b0\ub78c\ud55c \ube44\ub798\uc0b0(\u98db\u4f86\u5c71)\uc774\ub2e4. \uadf8\ub7ec\ub098 \uae30\uc2ad\uc5d0 \uac1c\ud65c\uc9c0\ub97c \uc774\ub8e8\ub294 \ud3c9\uc57c\uac00 \uc5c6\uc73c\uba70 \ub3d9\ucabd\uc73c\ub85c\ub294 \uc7a5\ub8e1\uc0b0(654.5m), \ub9e4\ubd09(599.5m), \ub300\uc131\uc0b0(705.9m)\uc774, \uc11c\ucabd\uc740 \ub3c4\ub355\ubd09(372.8m), \uc9c0\ubd09\uc0b0(464.3m), \ub9cc\uc778\uc0b0(537.8m)\uc774 \uac00\ub85c\ub9c9\uace0\uc788\uc5b4\uc11c \uc0b0\uc138\ub97c \ub4dc\ub7ec\ub0bc \uc218 \uc5c6\ub2e4. \uc774\uc911\ud658\uc774 \ub9d0\ud55c \uba85\uc0b0\uc758 \ub450\ubc88\uc9f8 \uc870\uac74 \"\"\ud070 \uac15\uc774\ub098 \ubc14\ub2e4\ub97c \ub07c\uace0\uc788\uc744 \uac83\"\"\uc774 \uc0c8\uc0bc\uc2a4\ub7fd\uac8c \ub5a0\uc624\ub974\ub294 \ub454\uc0b0\uc778 \uac83\uc774\ub2e4. <BR>\uc0b0 \ubaa8\uc591 \ub610\ud55c \uc5ec\uc758\ub3c4\uc885\ud569\uc804\uc2dc\uc7a5 \uac19\uc544 \ubcc4\ub85c\uc778\ub370 \ub2a5\uc120 \uc704\uc5d0 \uacac\uc6b0\ud0c4\uae08\ub300\uc640 \uc625\ub140\uc9c1\uae08\ub300, \ub2ed\ubcbc\uc2ac\ubc14\uc704, \ub9c8\ub2f9\ubc14\uc704, \uc120\ubc14\uc704, \ub0a8\uadfc\ubc14\uc704, \uc300\ubc14\uc704 \ub4f1 \uae30\uc554\uc774 \ub9ce\ub2e4. \uc6a9\uad74\uc774\ub098 \uc0ac\uc790\uad74 \uac19\uc740 \ub3d9\uad74, \uac1c\ub355\ud3ed\ud3ec \uac19\uc740 \ud3ed\ud3ec\ub3c4 \uc788\uc73c\ub098 \uad50\ud1b5\uc774 \ubd88\ud3b8\ud558\uace0 \uc774\ub984\ub09c \uc0b0\ub3c4 \uc544\ub2c8\uc5b4\uc11c \uc544\uc9c1\uaecf \ud638\uc813\ud558\ub2e4. <BR>\ub300\ud45c\uc801\uc778 \ub450 \ubc14\uc704 \uc774\ub984\uc5d0\uc11c \uc608\uac10\ud560 \uc218 \uc788\ub4ef\uc774 \uacac\uc6b0\u00b7\uc9c1\ub140\uc758 \uc804\uc124\uc744 \ub2f4\uace0 \uc788\ub2e4. \ud0c4\uae08\ub300\uc5d0\uc11c \uc9c1\ub140\ub97c \uadf8\ub9ac\uc6cc\ud558\uba70 \uac70\ubb38\uace0\ub97c \ud0c0\ub294 \uacac\uc6b0, \uc9c1\uae08\ub300\uc5d0\uc11c \uc637\uac10\uc744 \uc9dc\ub294 \uc9c1\ub140\uac00 \uce60\uc6d4\uce60\uc11d\ub0a0 \uc11c\ub300\uc0b0 \uc815\uc0c1\uc5d0\uc11c \ub9cc\ub09c\ub2e4\ub294 \uc2a4\ud1a0\ub9ac\ub2e4.<BR><BR>? \ubcfc\uac70\ub9ac : \uac1c\ub355\uc0ac\uc640 \uac1c\ub355\ud3ed\ud3ec\ub97c \ubcf4\uace0 \uc625\ub140\uc9c1\uae08\ub300\ub97c \uac70\uccd0 \uace0\uc2a4\ub77d\uc5d0 \uc624\ub978 \ub2e4\uc74c \uacac\uc6b0\ud0c4\uae08\ub300\ub97c \uc9c0\ub098 \uad6c\ub984\ub2e4\ub9ac\ub85c \ud558\uc0b0\ud558\ub294 \uac00\uc7a5 \uc77c\ubc18\uc801\uc778 \ucf54\uc2a4\uc785\ub2c8\ub2e4. \uac1c\ub355\ud3ed\ud3ec \uc67c\ud3b8 \ub4f1\uc131\uc774\uc5d0\uc11c \uc2dc\uc791\ud558\ub294\ub370 \uc911\uac04\uc5d0 \uc804\ub9dd \uc88b\uace0 \ub110\ucc0d\ud558\uba70 \uc2dc\uc6d0\ud55c \uc57d\uc218\ud130\uac00 \uc788\uc2b5\ub2c8\ub2e4. \ub2ed\ubcbc\uc2ac\ubc14\uc704 \ub2a5\uc120 \uac08\ub9bc\uae38\uc744 \uc9c0\ub098\uba74 \uc624\ub978\ucabd\uc73c\ub85c \ud6a1\ub2e8\ud558\ub294 \uae38\uc774 \ubcf4\uc774\ub294\ub370 \ubc14\ub85c \uc625\ub140\uc9c1\uae08\ub300 \uc9c1\ud589\ub8e8\ud2b8\ub2c8 \ub2a5\uc120\uae38\uc744 \ubc84\ub9ac\uace0 \uadf8\ucabd\uc73c\ub85c \ud5a5\ud574\uc57c\ud569\ub2c8\ub2e4. <BR>\uc808\ubcbd \uc911\uac04\uc5d0 15\ubbf8\ud130\ud3ed\uc758 \ubc14\uc704\uc120\ubc18\uc774 \uc0bc\uc0ac\uc2ed\ubbf8\ud130\ucbe4 \uc774\uc5b4\uc9c0\ub294 \uc625\ub140\uc9c1\uae08\ub300\ub294 \uc804\ub9dd\ub3c4 \uadf8\ub9cc\uc774\ub824\ub2c8\uc640 \uc0d8\uacfc \ub3d9\uad74\uae4c\uc9c0 \uc788\uc2b5\ub2c8\ub2e4. \uc0c1\ubd09 \ubc14\ub85c \uc544\ub798 \uc790\ub9ac\uc7a1\uc740 \uc88b\uc740 \uae30\ub3c4\ud130\uc774\uae30\ub3c4 \ud574 \uae30\ub3c4\uac1d\uc774 \uc0ac\ucca0 \ub04a\uc774\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uc5ec\uae30\uc11c \uc815\uc0c1\uc740 \uc77c\uac01 \uac70\ub9ac\ub3c4 \uc548 \ub429\ub2c8\ub2e4. <BR>\uc815\uc0c1 \uc870\ub9dd\uc740 \uc11c\ubd81\ucabd\uc744 \uc81c\uc678\ud558\uace0 \ubaa8\ub450 \uc88b\uc2b5\ub2c8\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uacac\uc6b0\ud0c4\uae08\ub300\ub9cc\ud07c\uc740 \uc548\ub429\ub2c8\ub2e4. \uc800 \uc720\uba85\ud55c \uc9c1\uc721\uba74\uccb4 \ubc14\uc704\ub294 \ub300\uc804\uacfc \uae08\uc0b0 \uc5b4\ub514\uc11c\ub3c4 \uc120\ub73b \ub208\uc5d0 \ub754\ub2c8\ub2e4. \ubc14\uc704\ub97c \ub418\ub0b4\ub824\uc640 \uac00\ub358 \ubc29\ud5a5\uc73c\ub85c \uacc4\uc18d \uac00\uba74 \"\"\uad6c\ub984\ub2e4\ub9ac \ud558\uc0b0\uae38\"\" \uc774\uc815\ud45c\uac00 \ub098\uc635\ub2c8\ub2e4.<BR><BR>\uae08\uc0b0 \uc778\uc0bc\ucd95\uc81c \uc778\uc0bc\ucd95\uc81c \uc9d1\ud589\uc704\uc6d0\ud68c (041-751-3799) <BR>? \uc232\uae38 \uba85\uc18c : \uc9c1\ub140 \ud0c4\uae08\ub300, \uc7a5\uba74\ub300, \ubd81\ub450\uce60\uc131 \ubc14\uc704, \uc0ac\uc790\uad74, \uc300\ubc14\uc704, \uc2e0\uc120\ubc14\uc704, \ubcbc\uc2ac\ubc14\uc704, \uc7a5\uad70\ubc14\uc704, \uc6a9\ubc14\uc704, \ub9c8\ub2f9\ubc14\uc704, \ub178\uc801\ubd09 \ub4f1'), STRINGDECODE('\ucda9\uccad\ub0a8\ub3c4\uc5d0\uc11c\ub294 \uc81c\uc77c \ub192\uc740 \uc0b0\uc73c\ub85c \uacf3\uacf3\uc5d0 \uae30\uc554\uad34\uc11d\uacfc \ubc14\uc704 \uc808\ubcbd\uc774 \uc788\uc5b4 \uc911\ubd80\uc758 \uae08\uac15\uc774\ub77c\uace0 \uc77c\uceec\uc744 \uc815\ub3c4\ub85c \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70, \uc0b0\uc815\uc5d0\uc11c\uc758 \uc870\ub9dd\uc774 \uc88b\uc740 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc6a9\uad74, \uc0ac\uc790\uad74, \uacac\uc6b0\uc7a5\ub144\ub300, \uc9c1\ub140\ud0c4\uae08\ub300, \ubd81\ub450\uce60\uc131\ubc14\uc704 \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ubc30\ud544\uc744 \ucc3e\uc544\uc8fc\ub294 \uc9c1\ub140\ud0c4\uae08\ub300 \uc57d\uc218'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc11c\ub300\uc0b0\ub4dc\ub9bc\ub9ac\uc870\ud2b8\uc5d0\uc11c \uc219\ubc15\uc774 \uac00\ub2a5\ud558\ub2e4. \u260e041-753-2662 <BR>\ucd94\ubd80\uc5d0\ub3c4 \uc774\uc6a9 \uac00\ub2a5\ud55c \uc219\ubc15\uc5c5\uc18c\uac00 \uc788\ub2e4. \uc2e0\ub370\ub810\ub77c\ud30c\ud06c \u260e041-753-8262 \ud0dc\ubd09\uc0b0\ubaa8\ud154 \u260e041-754-8102<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uae08\uc0b0\uad70\uccad \ubb38\ud654\uacf5\ubcf4\uad00\uad11\uacfc \u260e041-753-4350'), STRINGDECODE('\ub4dc\ub9bc\ub9ac\uc870\ud2b8\ud589 \ubc84\uc2a4\uac00 11\ud68c \uc6b4\ud589\ud558\ub294 \ucd94\ubd80\uac00 \uae30\uc810\uc774\ub2e4. \uc6d0\ud765\uc0ac\ub85c \uac00\ub824\uba74 8\ud68c \uc788\ub294 \uad70\ubd81\ud589\uc744 \ud0c0\uba74 \ub41c\ub2e4. \ucd94\ubd80\uae4c\uc9c0\ub294 \ub300\uc804\uc5ed\uc5d0\uc11c 501, 520\ubc88\uc744 \ud0c0\uba74 \ub41c\ub2e4. \uc7a5\ub8e1\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc\uc774\ub098 \uc548\ubcf4\uad11\ub9ac\ub294 \uc625\ucc9c\uc74d\uc774\ub098 \uad70\ubd81\uba74\uc5d0\uc11c \ud0dd\uc2dc\ub97c \uc774\uc6a9\ud55c\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(51, TIMESTAMP '2019-08-14 15:46:34.925', TIMESTAMP '2019-08-14 15:47:09.934', '45', STRINGDECODE('\ubd04\uc774\uba74 \ub3d9\ubc31\uaf43\uacfc \ubc9a\uaf43\uc73c\ub85c \uc720\uba85\ud55c \uc120\uc6b4\uc0b0\uc740 ''\ud638\ub0a8\uc758 \ub0b4\uae08\uac15''\uc73c\ub85c \ubd88\ub9ac\ub294 \uc0b0\uc73c\ub85c 79\ub144 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \uba85\uc0b0\uc774\ub2e4. \ubcf8\ub798 \ub3c4\uc194\uc0b0\uc774\uc5c8\uc73c\ub098 \ubc31\uc81c \uc704\ub355\uc655 24\ub144(577\ub144)\uc5d0 \ucc3d\uac74\ud55c \uc120\uc6b4\uc0ac(\u79aa\u96f2\u5bfa)\uac00 \uc788\uc5b4 \uc120\uc6b4\uc0b0\uc774\ub77c \ubd88\ub9ac\uac8c \ub418\uc5c8\ub2e4. <BR>\uc120\uc6b4\uc0b0\uc740 \uc774\ub984 \uadf8\ub300\ub85c \uad6c\ub984 \uc18d\uc5d0 \uc120(\u79aa)\uc744 \ub2e6\ub294 \uc0b0\uc73c\ub85c \uc120\uc6b4\uc0ac\ub97c \ube44\ub86f\ud574 \ucc38\ub2f9\uc554, \ub3c4\uc194\uc554, \uc11d\uc0c1\uc554, \ub3d9\uc6b4\uc554 \ub4f1 \uc720\uc11c \uae4a\uc740 \uc0ac\ucc30\uacfc \uc554\uc790\uac00 \uc0b0\uc790\ub77d\uc5d0 \uc790\ub9ac\uc7a1\uace0 \uc788\ub2e4. \uadf8 \uc911 \uc120\uc6b4\uc0ac\ub294 \ubc31\uc81c \uc704\ub355\uc655 24\ub144(577\ub144)\uc5d0 \uac80\ub2e8(\u9ed4\u4e39)\uc120\uc0ac\uac00 \ucc3d\uac74\ud55c \uc0ac\ucc30\ub85c, \ud55c\ub54c 89\uac1c \uc554\uc790\ub97c \uac70\ub290\ub9ac\uae30\ub3c4 \ud558\uc600\uc73c\ub098 \ud604\uc7ac\ub294 4\uac1c\uc758 \uc554\uc790\ub9cc \ub0a8\uc544\uc788\ub2e4. \uc120\uc6b4\uc0ac \ub300\uc6c5\uc804 \ub4a4\uc5d0\ub294 5\ucc9c\uc5ec \ud3c9\uc758 \ub3d9\ubc31\ub098\ubb34\uc232(\ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c184\ud638)\uc774 \uc788\ub294\ub370 \ub9e4\ub144 4\uc6d4 \uc911\uc21c\uc774\uba74 \ubd89\uac8c \ud53c\uc5b4\ub098 \ubcf4\ub294 \uc774\uc758 \ud0c4\uc131\uc744 \uc790\uc544\ub0b4\uac8c \ud55c\ub2e4. \uae40\uc815\ud638\uc758 ''\ub300\ub3d9\uc5ec\uc9c0\ub3c4''\uc5d0\ub294 \uc120\uc6b4\uc0b0\uc774 \ud45c\uc2dc\ub3fc \uc788\uc9c0\ub9cc \ud604\uc7ac \uac01\uc885 \uc9c0\ub3c4\uc5d0\ub294 \uc120\uc6b4\uc0b0\uc774\ub77c \ud45c\uc2dc\ub418\uc5b4 \uc788\uc9c0 \uc54a\uc73c\uba70, \uc120\uc6b4\uc0ac \ub4b7\uc0b0\uc744 ''\ub3c4\uc194\uc0b0'' \ub610\ub294 ''\uc218\ub9ac\ubd09'' \uc73c\ub85c \ud45c\uc2dc\ud558\uace0 \uc788\ub2e4. \uc120\uc6b4\uc0ac \ucc3d\uac74 \uc124\ud654\ub294 \uc5ec\ub7ec \uac00\uc9c0\uac00 \uc788\ub2e4. <BR>\uc6d0\ub798 \uc120\uc6b4\uc0ac \uc790\ub9ac\ub294 \uc6a9\uc774 \uc0ac\ub294 \uc6a9\ucd94\ub85c \uac80\ub2e8\uc120\uc0ac\uac00 \uc6a9\uc744 \ucad3\uc544\ub0c8\uc9c0\ub9cc \uc6a9\ucd94\ub97c \uba54\uc6b8 \uae38\uc774 \uc5c6\uc790, \uc6a9\ucd94\uc5d0 \uaf2d\ub450\uac01\uc2dc \ubc30\ub97c \ub744\uc6b0\uace0 \ub3cc\uc744 \ub358\uc838 \uaf2d\ub450\uac01\uc2dc \ub180\uc774\ub97c \ud558\uac8c \ud558\uc600\ub2e4 \ud55c\ub2e4. \ub610 \uadf8 \ub54c \ub9c8\uc744\uc5d0 \ub208\ubcd1\uc774 \ud37c\uc84c\ub294\ub370 \uc22f \ud55c \uc9d0\uc529\uc744 \uc6a9\ucd94\uc5d0 \ubc84\ub9b0 \ub4a4 \ub208\uc744 \uc53b\uc73c\uba74 \ub0ab\ub294\ub2e4\ub294 \uc18c\ubb38\uc744 \ud37c\ub728\ub824 \uc6a9\ucd94\ub97c \uba54\uc6cc \uc120\uc6b4\uc0ac\ub97c \uc9c0\uc5c8\ub2e4\ub294 \uc804\uc124\uc774 \uc788\ub2e4.<BR>\uc2e4\uc81c\ub85c\ub294 \uac80\ub2e8\uc120\uc0ac\uac00 \uadf8\uc758 \uce5c\uad6c\uc774\uc790 \uc2e0\ub77c 24\ub300 \uc9c4\ud765\uc655\uc758 \uc655\uc0ac\uc778 \uc758\uc6b4(\u7fa9\u96f2)\uc120\uc0ac\uc758 \ub3c4\uc6c0\uc744 \ubc1b\uc544 \uc9c4\ud765\uc655\uc758 \uc2dc\uc8fc\ub85c \uc120\uc6b4\uc0ac\ub97c \ucc3d\uac74\ud558\uc600\ub2e4 \ud55c\ub2e4. \uc9c4\ud765\uc655\uc774 \uc911\uc0dd\uc81c\ub3c4\ub97c \uc704\ud574 \uc655\uc704\ub97c \ubc84\ub9ac\uace0 \ub3c4\uc194\uc655\ube44\uc640 \uc911\uc560\uacf5\uc8fc\ub97c \ub370\ub9ac\uace0 \uc785\uc0b0, \uc218\ub3c4\ud558\uc600\ub2e4\ub294 \uc9c4\ud765\uad74(\u771e\u8208\u7a9f. \uc77c\uba85 \uc88c\ubcc0\uad74(\u5de6\u908a\u7a9f),\uc5f4\uc11d\uad74(\u88c2\u77f3\u7a9f))\uc774 \ub3c4\uc194\uc554 \uc544\ub798\uc5d0 \ub0a8\uc544\uc788\ub2e4. \ub099\uc870\ub300\uc5d0\uc11c \ubc14\ub77c\ubcf4\ub294 \uc11c\ud574\ubc14\ub2e4\uc758 \uc870\ub9dd\uacfc \ud558\ub298\ub85c \ub0a0\uc544\uc624\ub97c \ub4ef\ud55c \ubaa8\uc2b5\uc744 \ud55c \ucc9c\ub9c8\ubd09 \ub4f1 \ubcfc\uac70\ub9ac\uac00 \ud478\uc9d0\ud55c \uc774 \uc0b0\uc740 \uc554\uc790\uc640 \ubc14\uc704, \uad74\ub9c8\ub2e4 \uc231\ud55c \uc804\uc124\uc774 \uae43\ub4e4\uc5b4 \uc788\uc5b4 \uc9c0\ub8e8\ud568 \uc5c6\uc774 \uc0b0\ud589\uc744 \uc990\uae38 \uc218 \uc788\ub2e4.<BR>\ud2b9\ud788 \uc120\uc6b4\uc0b0 \ub9c8\uc560\ubd88\uc5d0\ub294 \uc7ac\ubbf8\ub09c \uc804\uc124\uc774 \uc804\ud574\uc9c0\uace0 \uc788\ub2e4. \uc61b\ub0a0\ubd80\ud130 \ub9c8\uc560\ubd88 \ubc30\uaf3d \uc18d\uc5d0 \uc2e0\uae30\ud55c \ube44\uacb0\uc774 \ub4e4\uc5b4 \uc788\uace0 \uadf8 \ube44\uacb0\uc774 \ub098\uc624\ub294 \ub0a0 \ud55c\uc591\uc758 \uc774\uc528\uac00 \ub9dd\ud55c\ub2e4\ub294 \uc804\uc124\uc774 \uc788\uc5c8\ub2e4. \uc804\ub77c\ub3c4 \uac10\uc0ac \uc774\uc11c\uad6c(\u674e\u66f8\u4e5d)\uac00 \ub9c8\uc560\ubd88\uc758 \ubc30\uaf3d\uc744 \uc5f4\uc5b4 \ubcf4\ub824\ub2e4\uac00 \ub1cc\uc131\ubcbd\ub825\uc774 \uccd0, \uc2e4\ud328\ud55c \uc77c\uc774 \uc788\uc5c8\ub294\ub370 \ub3d9\ud559\ud601\uba85\uc774 \uc77c\uc5b4\ub098\uae30 \uc804 \ub3d9\ud559\uc758 \uac04\ubd80 \uc190\ud654\uc911(\u5b6b\u548c\u4e2d)\uc774 \ubc30\uaf3d\uc744 \uc5f4\uace0 \uadf8 \ube44\uacb0\uc744 \uaebc\ub0b4\uac14\ub2e4\uace0 \ud55c\ub2e4.'), STRINGDECODE('\u2460 \ub9e4\ud45c\uc18c-\uc120\uc6b4\uc0ac-\uc11d\uc0c1\uc554-\ub9c8\uc774\uc7ac-\uacbd\uc218\uc0b0-\ub3d9\ub989-\uc911\ucd0c(\ucd1d 2\uc2dc\uac04 45\ubd84)<BR>\u2461 \ub9e4\ud45c\uc18c-\uc120\uc6b4\uc0ac-\ub3c4\uc194\uc554-\ucc9c\ub9c8\ubd09-\uc18c\ub9ac\uc7ac-\ucc38\ub2f9\uc554-\ub9e4\ud45c\uc18c(\ucd1d 2\uc2dc\uac04 25\ubd84)<BR>\u2462 \uc0bc\uc778\ucd08\uad50-\ud615\uc81c\ubd09-\ub178\uc801\ubd09-\uad6c\ud669\ubd09-\uc548\ubd80-\ud76c\uc5ec\uc7ac\uace8-\uc120\uc6b4\uc0ac-\ub9e4\ud45c\uc18c(\ucd1d 2\uc2dc\uac04 50\ubd84)'), '35.467281', '126.611426', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \uace0\ucc3d\uad70 \uc544\uc0b0\uba74\u318d\uc2ec\uc6d0\uba74\u318d\ud574\ub9ac\uba74'), 2501121000, 335, STRINGDECODE('\uc120\uc6b4\uc0b0'), STRINGDECODE('&lt;\ub215\uc9c0 \uc54a\uc544\ub3c4 \uc120\uc815\uc5d0 \ub4e4 \uc218 \uc788\ub294 \uc0b0&gt;<BR>\uad6c\ub984 \uc18d\uc5d0 \ub204\uc6cc \uc120\uc744 \ub2e6\ub294\ub2e4\ub294 \ucc38\uc120\uc640\uc6b4(\u53c3\u79aa\u81e5\u96f2)\uc758 \uc0b0\uc774\ub2e4. \uc120\uc774\ub780 \uac83\uc774 \ubcf8\ub798 \uad6c\ub984 \uc7a1\ub294 \uac83\uc774\ub2c8 \ucf55 \ucc1d\uc5b4 \ubb34\uc5c7\uc744 \uc5b4\uca50\ub2e4\ub294 \uac74\uc9c0\ub294 \ubaa8\ub974\uaca0\uc9c0\ub9cc \uc774 \uc0b0\uc5d0 \uac00\ubcf4\uba74 \ub9c9\uc5f0\ud788, \uadf8\ub7fc\uc9c1\ub3c4 \ud558\ub2e4\ub294 \ub290\ub08c\uc774 \ub4e0\ub2e4. <BR>\uc0b0\ud615\uc774 \ubc8c\ub808 \uc7a1\ub294 \ud480 \ud1b5\ubc1c\ucc98\ub7fc \uc0dd\uacbc\ub2e4. \uc0c1\ubd09 \uacbd\uc218\uc0b0\uc5d0\uc11c \uc2dc\uc791, \ub3c4\uc194\uc0b0(334.7m), \uac1c\uc774\ube68\uc0b0(346.6m), \uccad\ub8e1\uc0b0(315m), \ube44\ud559\uc0b0(308.5m), \uad6c\ud669\ubd09(297.9m)\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \uc6b4\ub450\uac00 \uace0\uc791 300\ubbf8\ud130 \uc548\ud30e\uc758 \ub0ae\uc740 \uc0b0\uc778\ub370\ub3c4 7\ud0ac\ub85c\ubbf8\ud130\ub098 \ub418\ub294 \ud3c9\ud3c9\ud55c \uae34 \uace8\uc774 \uc120\uc801 \ubd84\uc704\uae30\uc5d0 \uc816\uc5b4\ub4e4\uac8c \ud55c\ub2e4. \uac78\uc74c\ub3c4 \ub9c8\uc74c\ub3c4 \ucc28\ubd84\ud574\uc9c0\ub294 \uac83\uc774\ub2e4. <BR>\ub300\ub0ae\uc5d0\ub3c4 \uc5b4\ub451\ud55c \uc544\ub984\ub4dc\ub9ac \uace0\ubaa9 \uc544\ub798\ub97c \uac77\uace0\uc788\uc5b4\uc11c\ub9cc\uc740 \uc544\ub2c8\ub2e4. \uac00\uc744\uc774\uba74 \uadf8 \ubd89\uc740 \uc78e\ub4e4 \ubd07\ubb3c \uc704\uc5d0 \ud654\uc5c4\uacbd\uc744 \uc5f0\ucd9c\ud574\uc11c\ub3c4 \uc544\ub2c8\ub2e4. \ub208\ubc2d \uc18d\uc5d0 \uc774\ucc44\ub85c\uc6b4 \ub3d9\ubc31\uaf43\uc774\ub098 \uc232 \uc18d\uc758 \ubd84\ud64d \uc8fc\ub2e8 \uc0c1\uc0ac\ud654\ub4e4\uc758 \uc2ef\uc801 \uaf43\ub9d0 \ub54c\ubb38\ub9cc\ub3c4 \uc544\ub2c8\ub2e4. <BR>\uc2e0\ub4e4\uba54\ub97c \ud558\uace0 \uc6b4\ub450 \ub3cc\uae30\ub97c \ud574\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\ub2e4. \uc548\uc7a5\ubc14\uc704, \ubcd1\ud48d\ubc14\uc704, \ubc8c\ubc14\uc704, \ub099\ud0c0\ubc14\uc704, \ubc30\ub9e8\ubc14\uc704, \uc6a9\ubb38\uad74\uc758 \ub744\uc5c4\ub744\uc5c4 \uae30\uc554\ub4e4\uacfc \uc804\uc124\ub4e4\uc5d0\ub2e4 \ub110\ub760\ub108\ub978 \ubb34\uc7a5\ub4e4, \ud558\uc597\uac8c \ube5b\ub098\ub294 \uc7bf\ube5b \ubed8\ubc14\ub2e4\uc5d0 \ub3c4\ubb34\uc9c0 \uc11c\ub450\ub974\uace0 \uc2f6\uc740 \uc0dd\uac01\uc774 \ub4e4\uc9c0 \uc54a\ub294\ub2e4. <BR>\uc0c1\ubd09 \uacbd\uc218\uc0b0\uc740 \uc800\ub9cc\uce58 \ud640\ub85c \ub5a8\uc5b4\uc838\uc788\ub2e4. \uac00\uc744\ub0a0 \uac70\uae30 \uc11c\uba74 \ucd94\uc218 \ub05d\ub0b8 \ud765\ub355 \ub4e4\uc758 \uae4c\ub044\ub77c\uae30 \ud0dc\uc6b4 \uc5f0\uae30\uac00 \ud574\ud48d \ub530\ub77c \ud718\ub3c4\ub294 \ubaa8\uc2b5\uc744 \ubcfc \uc218 \uc788\ub2e4. \uc544\uc544! \uc120\uc6b4\uc0b0\uc740, \ub215\uc9c0 \uc54a\uc544\ub3c4 \uc120\uc815(\u79aa\u5b9a)\uc5d0 \ub4e4 \uc218 \uc788\ub294 \uc0b0\uc774\ub2e4.'), STRINGDECODE('\uc0b0\uc138\ub294 \ubcc4\ub85c \ud06c\uc9c0 \uc54a\uc73c\ub098 \uc232\uc774 \uc6b8\ucc3d\ud558\uace0 \uacf3\uacf3\uc774 \uae30\uc554\uad34\uc11d\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc5b4 \uacbd\uad00\uc774 \ube7c\uc5b4\ub098\uba70 \ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c184\ud638\uc778 \ub3d9\ubc31\ub098\ubb34 \uc232\uc774 \uc788\ub294 \ub4f1 \uc0dd\ud0dc\uc801 \uac00\uce58\uac00 \ud06c\uace0 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1979\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubc31\uc81c \uc704\ub355\uc655 24\ub144(577\ub144) \uac80\ub2e8\uc120\uc0ac\uac00 \ucc3d\uac74\ud55c \uc120\uc6b4\uc0ac(\u79aa\u96f2\u5bfa)\uc640 \uc218\ub839 5\ubc31\ub144\uc758 \ub3d9\ubc31\ub098\ubb34 \uff13\ucc9c\uc5ec \uadf8\ub8e8\uac00 \uad70\ub77d\uc744 \uc774\ub8e8\uace0 \uc788\ub294 \uc120\uc6b4\uc0ac \ub3d9\ubc31 \uc232\uc774 \uc720\uba85'), STRINGDECODE('\uad6c\ub984\uc18d\uc5d0 \uc120\uc744 \ub2e6\ub294 \ub3d9\ubc31\uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc0b0\uc774 \ub3c4\ub9bd\uacf5\uc6d0\uc774\ub77c \uc785\uad6c\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. <BR>\uc5ec\uad00\uc778\ub370\ub3c4 \ud638\ud154 \uc774\ub984\uc744 \ub2e8 \ub3d9\ubc31\ud638\ud154 \u260e063-562-1560, \uc0b0\uc0c8\ub3c4\ud638\ud154 \u260e063-562-0204, \uc120\uc6b4\uc7a5\uc5ec\uad00 \u260e063-561-2035 \ub2e4\uc815\ubbfc\ubc15 \u260e063-564-1050<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc120\uc6b4\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-563-3450'), STRINGDECODE('\uae30\uc810\uc740 \uace0\ucc3d\uc774\ub2e4. \uace0\ucc3d\uc2dc\uc678\ubc84\uc2a4\uc815\ub958\uc7a5\uc5d0\uc11c \uc9c1\ud589\ubc84\uc2a4\uac00 8\ud68c, \uad70\ub0b4\ubc84\uc2a4\ub294 30\ud68c \uc6b4\ud589\ud55c\ub2e4. \uace0\ucc3d\uc73c\ub85c\ub294 \uc11c\uc6b8 \uac15\ub0a8\ud130\ubbf8\ub110\uc5d0\uc11c \uace0\uc18d\ubc84\uc2a4\uac00 16\ud68c \uc788\ub2e4. \uc804\ub77c\ubd81\ub3c4 \uc774\uc678 \ub3c4\uc2dc\ub85c\ub294 \uad11\uc8fc\uc640 \ubaa9\ud3ec\uc5d0\uc11c \ubc84\uc2a4\uac00 \ub2e4\ub2c8\ub294\ub370 \uad11\uc8fc\uc5d0\ub294 \uc120\uc6b4\uc0ac\ud589 \uc9c1\ud589\ub3c4 \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(52, TIMESTAMP '2019-08-14 15:46:34.925', TIMESTAMP '2019-08-14 15:47:10.52', '42', STRINGDECODE('\uc124\uc545\uc0b0\uc740 \ud55c\ub77c\uc0b0(1,947.3m),\uc9c0\ub9ac\uc0b0(1,915.4m)\uc5d0 \uc774\uc5b4 \ub0a8\ud55c\uc5d0\uc11c \uc138 \ubc88\uc9f8\ub85c \ub192\uc740 \uc0b0\uc73c\ub85c \uac15\uc6d0\ub3c4 \uc18d\ucd08\uc2dc\uc640 \uc591\uc591\uad70,\uc778\uc81c\uad70\uc5d0 \uac78\uccd0 \uc788\ub2e4.<BR>\uc61b \ubb38\ud5cc\uc744 \ubcf4\uba74 \uc9c0\uae08\uc758 \ub300\uccad\ubd09\uc774 \uc788\ub294 \uc591\uc591, \uc18d\ucd08\uc758 \uc0b0\ub9cc\uc744 `\uc124\uc545''\uc774\ub77c \uc81c\ud55c\ud558\uc600\uace0 \uadc0\ub54c\uae30\uccad\ubd09\uc774 \uc788\ub294 \uc778\uc81c\ucabd\uc758 \uc0b0\uc744 `\ud55c\uacc4\uc0b0''\uc774\ub77c \ub530\ub85c \uc9c0\uce6d\ud588\ub2e4. \uadf8 \uc608\ub85c \uc548\uc0b0 \ub0a8\ucabd \uc7a5\uc218\ub300 \ubd80\uadfc\uc5d0 \uc788\ub294 \ud55c\uacc4\uc0b0\uc131\uc774 \uc774\ub97c \ub4b7\ubc1b\uce68\ud558\uace0 \uc788\ub2e4. \uc9c4\ubd80\ub839\uc5d0\uc11c \ub300\uccad\ubd09\uae4c\uc9c0 \uc774\uc5b4\uc9c0\ub294 \ubd81\uc8fc\ub2a5\uc758 \ubc31\ubbf8\ub294 \ubb50\ub2c8\ubb50\ub2c8 \ud574\ub3c4 \uc218\ub9ce\uc740 \uc554\ubd09\ub4e4\ub85c \uad6c\uc131\ub41c \uacf5\ub8e1\ub2a5\uc120\uc778\ub370 \uc774 \ucf54\uc2a4\uac00 \ubc14\ub85c \ubd81\uc8fc\ub2a5\uc758 \ub4f1\ubf08 \uc5ed\ud560\uc744 \ud558\ub294 \uacf5\ub8e1\ub2a5\uc120\uc744 \uac00\uc7a5 \uc9e7\uc740 \uc2dc\uac04\uc5d0 \uc8fc\ud30c\ud560 \uc218 \uc788\ub294 \uae38\uc774\ub2e4. \uc774 \ub2a5\uc120\uc740 1963\ub144 \uaca8\uc6b8, \ub2f9\uc2dc \ud55c\uad6d\uc758 \uc554\ubcbd\ub4f1\ubc18 \uc120\uad6c\uc790\uc774\ub358 \uc120\uc6b0\uc99d\uc625, \uc815\uaddc\ud604, \ucc44\ud0dc\uc6c5\uc528 \ub4f1\uc774 \ucc98\uc74c\uc73c\ub85c \uc644\ub4f1\ud55c \uc774\ud6c4 \uc0b0\uc545\uc778\ub4e4\ub85c\ubd80\ud130 \uac01\uad11\uc744 \ubc1b\ub2e4\uac00 \ucd5c\uadfc\uc5d4 \uc77c\ubc18\uc778\ub4e4\ub3c4 \uc27d\uac8c \ud560 \uc218 \uc788\uc744 \ub9cc\ud07c \ub4f1\uc0b0\ub85c\uac00 \uc798 \ub2e6\uc5ec\uc788\ub2e4. <BR>\ubc31\ub450\uc0b0\uc5d0\uc11c \ub0a8\ucabd\uc73c\ub85c \ub0b4\ub9ac\ubed7\uc5b4 \uc774 \ub545\uc758 \uae30\ub098\uae34 \ub4f1\ubf08\ub97c \uc774\ub8e8\ub294 \ubc31\ub450\ub300\uac04\uc758 \ud5c8\ub9ac\ub97c \ubc1b\ub4e4\uace0 \uc788\ub294 \uc124\uc545\uc0b0\uc740 \ubd81\uc758 \uae08\uac15\uc0b0\uacfc \ub0a8\uc758 \uc624\ub300\uc0b0 \uc0ac\uc774\uc5d0 \uc19f\uc544\uc788\ub294 \ucc9c\ud558\uc758 \uba85\uc0b0\uc73c\ub85c \uc6b0\ub9ac\ub098\ub77c \uad00\uad11\uba85\uc18c 1\ud638\ub85c \uaf3d\ud78c\ub2e4. \uc9c0\ub09c 1965\ub144 11\uc6d4 5\uc77c \ucc9c\uc5f0\uae30\ub150\ubb3c\uc9c0\uad6c(163.4\u339e), `69\ub144 \uad00\uad11\uc9c0(16.2\u339e) \uadf8\ub9ac\uace0 ''70\ub144\uc5d0\ub294 \uad6d\ub9bd\uacf5\uc6d0(174\u339e)\uc73c\ub85c \uac01\uac01 \uc9c0\uc815\ub418\uc5c8\ub2e4. \uadf8\ub9ac\uace0 1971\ub144 9\uc6d4\uc5d0\ub294 \uc124\uc545\uc0b0 \uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c\uac00 \uac1c\uc124 \ub418\uc5c8\uace0 `77\ub144 ''78\ub144 \ub450\ucc28\ub840\uc5d0 \uac78\uccd0 354.6\u339e\ub85c \ud655\uc7a5\ub418\uc5c8\uc73c\uba70, \uadf8 \ud6c4 \ub2e4\uc2dc 374\u339e\ub85c \ub113\uc774\ub97c \ud655\ub300\ud558\uc600\ub2e4. \uc6b8\uc0b0\uc554 \ub4f1\uc0b0\ub85c \ucd08\uc785\uc5d0 \uc788\ub294 \uc2e0\ud765\uc0ac\ub294 \ub300\ud55c\ubd88\uad50 \uc870\uacc4\uc885 \uc81c3\uad50\uad6c \ubcf8\uc0ac\ub85c \uc124\uc545\uc0b0\uc758 \ub300\ud45c\uc801 \uc0ac\ucc30\uc774\ub2e4. \uc2e0\ub77c\ub54c \uc790\uc7a5\uc728\uc0ac\uac00 \ub178\ub8e8\ubaa9\uadfc\ucc98\uc5d0 \ud5a5\uc131\uc0ac\ub85c \ucc3d\uac74\ud588\ub2e4\uac00 \uc870\uc120\uc870\ub54c \ud604\uc704\uce58\uc5d0 \ub2e4\uc2dc \uc138\uc6e0\ub2e4\uace0 \ud55c\ub2e4.'), STRINGDECODE('\u2460 \uc124\uc545\ub3d9-\ube44\uc120\ub300-\ub9c8\ub4f1\ub839-\uacf5\ub8e1\ub2a5\uc120-\ud76c\uc6b4\uac01-\uc18c\uccad-\uc124\uc545\uc0b0\uc7a5 (\ucd1d 11\uc2dc\uac04)<BR>\u2461 \uc6a9\ub300\ub9ac-\ubc31\ub2f4\uc0ac-\uc218\ub834\ub3d9\ub300\ud53c\uc18c-\uc30d\ud3ed-\ubd09\uc815\uc554-\uc18c\uccad-\uc124\uc545\uc0b0\uc7a5 (\ucd1d 8\uc2dc\uac04)<BR>\u2462 \ub0a8\uad50\ub9ac-\ud0d5\uc218\ub3d9-\ub300\uc2b9\ub839-\uadc0\uccad-\ud55c\uacc4\ub839 \uac08\ub9bc\uae38-\uc124\uc545\uc0b0\uc7a5 (\ucd1d 13\uc2dc\uac04 30\ubd84)'), '38.162681', '128.484934', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc18d\ucd08\uc2dc \uc124\uc545\ub3d9, \uc778\uc81c\uad70 \ubd81\uba74\u318d\uc778\uc81c\uc74d, \uc591\uc591\uad70 \uc11c\uba74\u318d\uac15\ud604\uba74'), 2207010900, 1708, STRINGDECODE('\uc124\uc545\uc0b0'), STRINGDECODE('&lt;\uae08\uac15\uc0b0\ubcf4\ub2e4 \uba87 \uc218 \uc704\uc778 \ud55c\uad6d\uc758 \uc54c\ud504\uc2a4&gt;<BR>\ud55c\ub9c8\ub514\ub85c \ud55c\uad6d\uc758 \uc54c\ud504\uc2a4\ub2e4. \ud754\ud788 \uae08\uac15\uc0b0\uc5d0 \ube44\uc720\ud558\uc9c0\ub9cc \ub192\uc774\ub3c4 72\ubbf8\ud130\ub098 \ub0ae\uace0 \ub3d9\uc11c\ub85c \uae34 \ub2a5\uc120\uc774 \uc5c6\uc5b4 \uc0b0\uc5ed\uc740 \ud6e8\uc52c \uc881\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \uae08\uac15\uc0b0\uc774 \uc608\ub85c\ubd80\ud130 \ub354 \uc720\uba85\ud588\ub358 \uc774\uc720\ub294 \uad50\ud1b5\uc774 \ud3b8\ub9ac\ud55c \ub370\ub2e4 \uc9da\uc2e0 \uc2e0\uace0\ub3c4 \ubabb \uc62c\ub77c\uac08 \ub370\uac00 \uac70\uc758 \uc5c6\uc744 \ub9cc\ud07c \uc0b0\uc774 \uc21c\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \uc54c\ud53c\ub2c8\uc998\uc758 \uad00\uc810\uc5d0\uc11c\ub294 \ub354\uc6b1, \uc124\uc545\uc0b0\uc774 \uae08\uac15\uc0b0\ubcf4\ub2e4 \uba87 \uc218 \uc704\ub2e4. <BR>\ub192\uc774\ub85c\ub294 \ud55c\ub77c\uc0b0\uacfc \uc9c0\ub9ac\uc0b0 \ub2e4\uc74c\uc774\ub098 \ube7c\uc5b4\ub0a8\uc73c\ub85c\ub294 \ubd84\uba85 \ub0a8\ud55c\uc758 \uc73c\ub738\uc0b0\uc774\ub2e4. \ubc31\ub450\ub300\uac04\uc778 \uacf5\ub8e1\ub2a5\uc120\uc744 \uacbd\uacc4\ub85c \uc11c\ucabd\uc744 \ub0b4\uc124\uc545, \ub3d9\ucabd\uc744 \uc678\uc124\uc545\uc73c\ub85c \ub098\ub204\ub294 \uac83\uc774 \uc77c\ubc18\uc801\uc774\uba70 \uc624\uc0c9\ucabd\uc744 \ub530\ub85c \ub0a8\uc124\uc545\uc73c\ub85c \ubd80\ub974\uae30\ub3c4 \ud55c\ub2e4. \ub0b4\uc124\uc545\uc5d0\ub294 \ubc31\ub2f4\uacc4\uace1, \uc218\ub834\ub3d9, \uac00\uc57c\ub3d9, \ud0d5\uc218\ub3d9(12\uc120\ub140\ud0d5\uacc4\uace1) \ub4f1\uc758 \uc544\ub984\ub2e4\uc6b4 \uacc4\uace1\uc774 \uc788\uace0 \uc678\uc124\uc545\uc5d0\ub294 \uacc4\ub958\ubbf8 \ub6f0\uc5b4\ub09c \ucc9c\ubd88\ub3d9\uacc4\uace1\uacfc \ud611\uace1\uc744 \uc774\ub8ec \uac00\uc9c0\uacc4\uace1\ub4e4, \uc6b8\uc0b0\ubc14\uc704, \ubd09\ud654\ub300, \uc7a5\uad70\ubd09, \ubc94\ubd09 \ub4f1 \uae30\uace8\uc2a4\ub7f0 \uc554\ubd09\uacfc \uae30\uc554\uc808\ubcbd\uc744 \uc790\ub791\ud55c\ub2e4. <BR>\ub208\uc774 \ub9ce\uc740 \uae4c\ub2ed\uc5d0 \uc124\uc0b0\uc774\ub098 \uc124\ubd09\uc0b0, \ud55c\uacc4\uc0b0\uc73c\ub85c\ub3c4 \ubd88\ub838\ub2e4. \ub3d9\uad6d\uc5ec\uc9c0\uc2b9\ub78c\uc5d0\ub294 \"\"\ud55c\uac00\uc704\uc5d0 \ub0b4\ub9ac\uae30 \uc2dc\uc791\ud55c \ub208\uc774 \ud558\uc9c0\uc5d0 \uc774\ub974\ub7ec \uc0ac\ub77c\uc9c0\uae30 \ub54c\ubb38\uc5d0 \uc124\uc545\uc774\ub77c \ud55c\ub2e4\"\"\ub294 \uae30\ub85d\ub3c4 \uc788\ub2e4. \uc9c0\uae08\ub3c4 \uc624\uc6d4\uae4c\uc9c0\ub294 \uace8\uc9dc\uae30\uc5d0 \uc794\uc124\uc774 \ub0a8\uc544\uc788\ub2e4.'), STRINGDECODE('\ub0a8\ud55c\uc5d0\uc11c \uc138 \ubc88\uc9f8\ub85c \ub192\uc740 \ubd09\uc6b0\ub9ac\uc778 \ud55c\uacc4\ub839, \ub9c8\ub4f1\ub839, \ubbf8\uc2dc\ub839 \ub4f1 \uc218\ub9ce\uc740 \uace0\uac1c\uc640 \uc0b0\uc904\uae30 \uacc4\uace1\ub4e4\uc774 \uc5b4\uc6b0\ub7ec\uc838 \ud55c\uad6d\uc744 \ub300\ud45c\ud558\ub294 \uc0b0\uc545\ubbf8\uc758 \uadf9\uce58\ub97c \uc774\ub8e8\uace0 \uc788\uc73c\uba70, \uad6d\ub9bd\uacf5\uc6d0(1970\ub144 \uc9c0\uc815) \ubc0f \uc720\ub124\uc2a4\ucf54\uc758 \uc0dd\ubb3c\uad8c \ubcf4\uc874\uc9c0\uc5ed\uc73c\ub85c \uc9c0\uc815(1982\ub144)\ub418\uc5b4 \uad00\ub9ac\ub418\uace0 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubc31\ub2f4\uc0ac(\u767e\u6f6d\u5bfa), \ubd09\uc815\uc554(\u9cf3\u9802\u83f4), \uc2e0\ud765\uc0ac(\u65b0\u8208\u5bfa), \uacc4\uc870\uc554(\u7e7c\u7956\u83f4), \uc624\uc138\uc554(\u4e94\u6b72\u5eb5), \ud754\ub4e4\ubc14\uc704, \ud1a0\uc655\uc131\ud3ed\ud3ec, \ub300\uc2b9\ud3ed\ud3ec \ub4f1\uc774 \ud2b9\ud788 \uc720\uba85'), STRINGDECODE('\uc12c\uc138\ud55c \ube44\uacbd \ub450\ub8e8 \uac16\ucd98 \ud314\ubc29\ubbf8\uc778'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc124\uc545\ub3d9\uc9c0\uad6c, \ubc31\ub2f4\uc9c0\uad6c, \uc624\uc0c9\uc9c0\uad6c\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \uc990\ube44\ud558\ub2e4. \ud2b9\ud788 \uc124\uc545\ub3d9\uc5d0\ub294 \ud2b9\uae09\ud638\ud154\uc5d0\uc11c \uae38\uac70\ub9ac \ud638\uac1d \ubbfc\ubc15\uc9d1\uc5d0 \uc774\ub974\uae30\uae4c\uc9c0 \ub2e4\uc591\ud55c \uc219\ubc15\uc5c5\uc18c\uac00 \uc788\uc73c\uba70 C\uc9c0\uad6c\uc5d0\ub294 \uc624\ud1a0\ucea0\ud551\uc774 \uac00\ub2a5\ud55c \ub113\uc740 \uc57c\uc601\uc7a5\ub3c4 \uc788\ub2e4. <BR>\ud638\ud154 \uc124\uc545\ud30c\ud06c \u260e033-636-7711, \uc124\uc545\ud06c\ub9ac\uc2a4\ud0c8\ubaa8\ud154 \u260e033-636-7626, \uc124\uc545\uc0b0\ubbfc\ubc15 \u260e033-635-4231 \uc624\uc0c9\uadf8\ub9b0\uc57c\ub4dc\ud638\ud154(\ubaa8\ud154) \u260e033-672-8500 \uc624\uc0c9\uc628\ucc9c\uc7a5 \u260e033-672-3635 \uc6a9\ub300\ub9ac \uac15\ud48d\uacbd\ud30c\ud06c \u260e033-462-9225 \ub378\ub9ac\ud30c\ud06c \u260e033-462-6647 \ub0a8\uad50\ub9ac \uc6a9\uc544\ubbfc\ubc15 \u260e033-462-6399<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc124\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e033-636-7700'), STRINGDECODE('\uae30\uc810\uc740 \uc18d\ucd08\ub2e4. <BR>\uc11c\uc6b8 \ub3d9\uc11c\uc6b8\uc885\ud569\ud130\ubbf8\ub110\uacfc \uac15\ub0a8\uace0\uc18d\ubc84\uc2a4\ud130\ubbf8\ub110\uc5d0\uc11c \uace0\uc18d\ubc84\uc2a4\uac00 30\ubd84 \uac04\uaca9\uc73c\ub85c \uc788\uace0 \uc2ec\uc57c\ubc84\uc2a4\ub3c4 \ub450 \ud3b8 \uc788\ub2e4. <BR>\uc18d\ucd08\ub85c \uc2dc\uc678\ubc84\uc2a4\uac00 \ub2e4\ub2c8\ub294 \ub3c4\uc2dc\ub294 \ubd80\uc0b0, \ub300\uad6c, \uad11\uc8fc, \ub300\uc804, \uc6b8\uc0b0, \uc218\uc6d0, \uc131\ub0a8, \ucd98\ucc9c, \uc548\uc0b0, \uc77c\uc0b0, \uc758\uc815\ubd80, \ucda9\uc8fc, \ud0dc\ubc31, \ud3ec\ucc9c \ub4f1\uc774\ub2e4. <BR>\ub0b4\uc124\uc545 \ubc29\uba74\uc740 \ub3d9\uc11c\uc6b8\ub3d9\ud569\ud130\ubbf8\ub110\uc774\ub098 \uc0c1\ubd09\ud130\ubbf8\ub110\uc5d0\uc11c \ud64d\ucc9c \uacbd\uc720 \uc9c1\ud589\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR>- \uc18d\ucd08\u2192\uc124\uc545\ub3d9: 7\ubc88, 7-1\ubc88 \uc2dc\ub0b4\ubc84\uc2a4 10\ubd84 \uac04\uaca9 \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(53, TIMESTAMP '2019-08-14 15:46:34.926', TIMESTAMP '2019-08-14 15:47:11.115', '47', STRINGDECODE('\uba74\uc801 72\ud3c9\ubc29 \ud0ac\ub85c\ubbf8\ud130\uc5d0 \ub458\ub808\uac00 44.12\ud0ac\ub85c\ubbf8\ud130\uc778 \uc6b8\ub989\ub3c4\ub294 \ud3ec\ud56d\uc5d0\uc11c \ubc43\uae38\ub85c 188\ud0ac\ub85c\ubbf8\ud130 \ub5a8\uc5b4\uc9c4 \uc12c\uc73c\ub85c \ub3c5\ub3c4\ub97c \ud3ec\ud568\ud574\uc11c \ubcf8\ud1a0\uc5d0\uc11c \uac00\uc7a5 \uba40\ub9ac \uc788\ub294 \uc12c\uc774\ub2e4. \uc12c \uc8fc\ubcc0\uc740 \ub300\ubd80\ubd84 \ud654\uc0b0\uc554\uc758 \ud574\uc548\uc808\ubcbd\uc73c\ub85c \ub418\uc5b4 \uc788\ub294\ub370 \ubc14\ub85c \uc774 \uc12c \uc911\uc2ec\ubd80\uc5d0 \uc131\uc778\ubd09\uc774 \uc790\ub9ac\ud574 \uc788\ub2e4. <BR>\uc791\uc740 \ub545, \uc6b8\ub989\ub3c4 \uc704\uc5d0 \ud558\ub098\uc758 \ubd09\uc6b0\ub9ac\uac00 \uc19f\uc544 \uc788\uc73c\ub2c8 \uc131\uc778\ubd09\uc774 \uace7 \uc6b8\ub989\ub3c4\uc778 \uc148\uc774\ub2e4. \uc6b8\ub989\ub3c4\ub294 \uc624\ub798\uc804\ubd80\ud130 \uc655\uc870\ub4e4\uc758 \uc815\ucc45\uc5d0 \uc758\ud574 \ubc31\uc131\ub4e4\uc744 \uc0b4 \uc218 \uc5c6\uac8c \ud558\uc600\ub358 \uacf3\uc774\ub098 \ubc31\uc131\ub4e4\uc774 \ubab0\ub798 \uc228\uc5b4 \ub4e4\uc5b4 \ub05d\ub05d\ub0b4 \uc6b0\ub9ac \ub545\uc73c\ub85c \uc9c0\ucf1c\ub0b8 \uc6b0\ub9ac \ubbfc\uc871\uc758 \uae30\uc0c1\uc774\ub77c\uace0 \ud560 \uc218 \uc788\ub2e4. \uc6b8\ub989\ub3c4\uc5d0 \uad00\ud55c \uae30\ub85d\uc740 \uc2e0\ub77c\uc2dc\ub300\ub85c \uac70\uc2ac\ub7ec \uc62c\ub77c\uac04\ub2e4. <BR>\u3008\uc0bc\uad6d\uc0ac\uae30\u3009\uc640 \u3008\uc0bc\uad6d\uc720\uc0ac\u3009\uc5d0 \uc2e0\ub77c 22\ub300 \uc9c0\uc99d\uc655 \ub54c \uc6b8\ub989\ub3c4\uc5d0 \uad00\ud55c \uae30\ub85d\uc774 \uc788\ub2e4. \u3008\uc0bc\uad6d\uc0ac\uae30\u3009\uc5d0\ub294 \uc9c0\uc99d\uc655 13\ub144\uc5d0 \uc6b0\uc0b0\uad6d(\u4e8e\u5c71\u570b)\uc774 \ud56d\ubcf5\ud588\uace0, \ud558\uc2e4\ub77c \uad70\uc8fc \uc774\uc0ac\ubd80(\u7570\u65af\u592b)\uac00 \ub098\ubb34\ub85c \ub9cc\ub4e0 \uc0ac\uc790\ub97c \uc2e3\uace0 \uac00\uc11c \ud56d\ubcf5\uc744 \ubc1b\uc558\ub2e4\uace0 \uae30\ub85d\ub418\uc5b4 \uc788\ub294\ub370 \u3008\uc0bc\uad6d\uc720\uc0ac\u3009\uc758 \uae30\ub85d\uc740 \uc870\uae08 \ub2e4\ub974\ub2e4. \u3008\uc0bc\uad6d \uc720\uc0ac\u3009\uc5d0 \ub530\ub974\uba74 \uc544\uc2ac\ub77c\uc8fc \ub3d9\ucabd \ubc14\ub2e4\uc5d0\uc11c \uc21c\ud48d\uc73c\ub85c \uc774\ud2c0 \uac78\ub9ac\ub294 \uacf3\uc5d0 \uc6b0\ub989\ub3c4(\u4e8e\u9675\u5cf6)\uac00 \uc788\uace0 \uc774 \uc12c\uc5d0 \uc0ac\ub294 \uc624\ub791\uce90\ub4e4\uc740 \uad50\ub9cc\ud558\uac8c \ubc14\ub2f7\ubb3c\uc774 \uae4a\uc740 \uac83\uc744 \ubbff\uace0 \uc870\uacf5\uc744 \ubc14\uce58\uc9c0 \uc54a\uc544 \uc774\ucc3d \ubc15\uc774\uc885(\u6734\u4f0a\u5b97)\uc774 \ub098\ubb34\uc0ac\uc790\ub85c \uc704\ud611\ud574 \ud56d\ubcf5\uc2dc\ucf30\ub2e4\uace0 \ud55c\ub2e4. <BR>\uc6b8\ub989\ub3c4\ub294 \uaca8\uc6b8\uc5d0\ub294 \ub530\ub73b\ud558\uace0 \uc5ec\ub984\uc5d0\ub294 \uc2dc\uc6d0\ud574\uc11c \ub204\uad6c\ub77c\ub3c4 \uc0b4\uae30 \uc88b\uace0 \ubaa8\ub4e0 \uac83\uc774 \ud48d\uc694\ub86d\ub2e4. \ud2b9\ud788 ''3\ubb34''\ub77c\uace0 \ud574\uc11c \ub3c4\ub451,\uac70\uc9c0,\ubc40\uc774 \uc5c6\uace0 ''5\ub2e4''\ub77c\uace0 \ud558\uc5ec \ud5a5(\u9999),\ud48d(\u98a8),\ubbf8(\u7f8e),\uc218(\u6c34),\uc11d(\u77f3))\uc774 \ub9ce\uae30\ub85c \uc774\ub984\ub098 \uc788\ub2e4. \ud574\uc548\uac00\ub85c \ub098\uac00\uba74 \uad6c\uba4d\ubc14\uc704(\uacf5\uc554), \uac70\ubd81\ubc14\uc704, \uc0ac\uc790\ubc14\uc704, \uacf0\ubc14\uc704, \ub9d0\ubc14\uc704, \uad6d\uc218\ub97c \ub110\uc5b4\ub193\uc740 \ub4ef\ud55c \uad6d\uc218\ubc14\uc704 \ub4f1 \uae30\uc554\uad34\uc11d\uc744 \uc27d\uac8c \ubcfc \uc218 \uc788\ub2e4. \uc131\uc778\ubd09 \ubd81\ucabd \ud558\uc0b0 \uae38\uc5d0 \uc788\ub294 \uc774\uc2ed \uc5ec\ub9cc \ud3c9\uc758 \ub098\ub9ac\ubd84\uc9c0\uc5d0\ub294 \uc5b5\uc0c8 \ubc2d\uacfc \ub098\ub9ac\uaf43 \uad70\ub77d\uc774 \uc11c\ucabd\uc73c\ub85c \uc19f\uc544 \uc788\ub294 \uc54c\ubd09(538)\uacfc \uc5b4\uc6b0\ub7ec\uc838 \ube44\uacbd\uc744 \uc790\uc544\ub0b8\ub2e4. <BR>\uc6b8\ub989\ub3c4 \uc11c\ucabd \ub05d \ud0dc\ud654\ub3d9\uc5d0\ub294 \uc6b8\ub989\ub3c4\uc758 \ub300\ud45c\uc801 \uc0ac\ub2f9\uc778 \uc131\ud558\uc2e0\ub2f9\uc774 \uc790\ub9ac\uc7a1\uace0 \uc788\ub2e4. \uc774\uacf3 \uc8fc\ubbfc\ub4e4\uc740 \ub9e4\ub144 \uc74c\ub825 2\uc6d4\uc5d0 \ub18d\uc0ac\uc640 \uc5b4\uc5c5\uc758 \ud48d\ub144,\ud48d\uc5b4\ub97c \ube4c\uace0 \uc788\ub2e4.\uc804\uc124\uc5d0 \uc758\ud558\uba74 \uc131\ud558\uc2e0\ub2f9\uc740 \uc870\uc120 \ud0dc\uc885 \ub54c \uc548\ubb34\uc0ac\ub85c \uc654\ub358 \uae40\uc778\uc6b0\uac00 \uc138\uc6e0\ub2e4\uace0 \ud55c\ub2e4. \uc548\ubb34\uc0ac \uae40\uc778\uc6b0\uac00 \uc2ec\ud55c \ud48d\ub791\uc73c\ub85c \ubcf8\ud1a0\uc5d0 \ub3cc\uc544\uac00\uc9c0 \ubabb\ud558\uace0 \uc788\uc744 \ub54c \uc2e0\uc758 \uc9c0\uc2dc\ub97c \ubc1b\uace0 \uc80a\uc740 \ub0a8\ub140 \ud55c \uc30d \ud0dc\ud558\ub3d9\uc5d0 \uc5b5\uc9c0\ub85c \ub5bc\uc5b4\ub193\uace0 \ubcf8\ud1a0\ub85c \ub3cc\uc544\uac08 \uc218 \uc788\uc5c8\ub294\ub370 \uba87 \ub144 \ub4a4 \uc6b8\ub989\ub3c4\uc5d0 \ub2e4\uc2dc \uc640 \ubcf4\ub2c8 \ub450 \uc0ac\ub78c\uc774 \uaef4\uc548\uc740 \ucc44 \ubc31\uace8\uc774 \ub418\uc5b4 \uc788\uc5b4 \uadf8\ub4e4\uc758 \uc6d0\ud63c\uc744 \ub2ec\ub798\uae30 \uc704\ud574 \uc138\uc6b4 \uc0ac\ub2f9\uc774\ub77c \ud55c\ub2e4. \uc800\ub3d9\uc5d0\uc11c \ubd09\ub798\ud3ed\ud3ec\ub85c \uc774\uc5b4\uc9c0\ub294 \ub4f1\uc0b0\ub85c\uc5d0\ub294 \ubc14\uc704\ud2c8\uc5d0\uc11c \ucc2c\ubc14\ub78c\uc774 \ub098\uc624\ub294 \u3008\ucc9c\uc5f0\uc5d0\uc5b4\ucee8\u3009\uc774 \ub540\uc744 \uc2dc\ucf1c \uc900\ub2e4. <BR>\ucc9c\uc5f0\uc5d0\uc5b4\ucee8\uc5d0\uc11c \uc815\uc0c1\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub4f1\uc0b0\ub85c\ub97c \ub530\ub77c \uc57d 40\ubd84 \uc624\ub974\uba74 \uc138 \uac08\ub798 \ubb3c\uc904\uae30\uac00 \uc6d0\uc2dc\ub9bc\uc744 \ub6ab\uace0 \uc3df\uc544\uc9c0\ub294 \ubd09\ub798\ud3ed\ud3ec\ub97c \ub9cc\ub098\uac8c \ub41c\ub2e4. \ub098\ub9ac\ubd84\uc9c0\uc5d0\uc11c \uc0b0\ud589\uc774 \ub05d\ub098\ub294 \uc9c0\uc810\uc778 \ucc9c\ubd80\uc120\ucc29\uc7a5\uc744 \ub530\ub77c \uc11c\ucabd\uc73c\ub85c 2\ubc31\ubbf8\ud130\uc815\ub3c4 \uac77\ub2e4\ubcf4\uba74 \ubc14\uc704\uad74 \uc18d\uc5d0\uc11c \ucc2c\ubc14\ub78c\uc774 \ub098\uc624\ub294 \ud48d\ud608(\u98a8\u7a74)\uc774 \uc788\ub2e4. \ubd09\ub798\ud3ed\ud3ec\ucabd \ucc9c\uc5f0\uc5d0\uc5b4\ucee8\ubcf4\ub2e4 \ubabb\ud558\uc9c0\ub9cc \uc5ec\ub984 \ub540\uc744 \uc53b\uc5b4\ub0b4\uae30\uc5d0\ub294 \ucda9\ubd84\ud558\ub2e4.'), STRINGDECODE('\uc77c\uc8fc \ub3c4\ubcf4\uc5ec\ud589(\ucd1d 3\ubc15 4\uc77c) <BR>\uc81c1\uc77c: \ub3c4\ub3d9-\uc800\ub3d9-\ub0b4\uc218\uc804-\ud55c\uc194\uacc4\uace1 \uc0c1\ubd80-\uc5fc\uc18c\uace8\uacc4\uace1 \uc0c1\ubd80-\uc12c\ubaa9(\ubbfc\ubc15\uc774\ub098 \uc57c\uc601) <BR>\uc81c2\uc77c: \uc12c\ubaa9-\uc8fd\uc554-\ucc9c\ubd80-\ud604\ud3ec-\ud5a5\ubaa9\ub839-\ud0dc\ud558\ub4f1\ub300-\ud0dc\ud558(\uc5ec\uad00 \uc788\uc74c) <BR>\uc81c3\uc77c: \ud0dc\ud558-\ud559\ud3ec \ub9cc\ubb3c\uc0c1-\uc218\uce35\ub3d9-\uad6c\ubc14\uc6b0-\uc0ac\ud0dc\uac10-\ub0a8\uc591(\uc5ec\uad00 \uc788\uc74c) <BR>\uc81c4\uc77c: \ub0a8\uc591-\ud1b5\uad6c\ubbf8-\uac00\ub450\ubd09\ub4f1\ub300-\uc911\ub839-\uc0ac\ub3d9-\ub3c4\ub3d9'), '37.491775', '130.899491', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uc6b8\ub989\uad70 \uc6b8\ub989\uc74d \uc11c\uba74\u318d\ubd81\uba74'), 2717030900, 987, STRINGDECODE('\uc131\uc778\ubd09'), STRINGDECODE('&lt;\uc131\uc778\ubd09\uc740 \uc6b0\uc0b0\uc758 \ucd5c\uace0\ubd09\uc774\ub2e4&gt;<BR>\uc6b8\ub989\ub3c4\ub294 \uc12c\uc774\ub2e4. \uc12c\uc740 \ud654\uc0b0\uc73c\ub85c \uc774\ub8e8\uc5b4\uc84c\ub2e4. \uc0b0\uc774 \uace7 \uc12c\uc778 \uac83\uc774\ub2e4. \uadf8\ub7f0\ub370 \uc0b0 \uc774\ub984\uc774 \uc5c6\ub2e4. \uc131\uc778\ubd09, \ub098\ub9ac\ubd09, \ub450\ub9ac\ubd09 \uac19\uc740 \ubd09\uc6b0\ub9ac\ubfd0\uc774\ub2e4. <BR>\ubbf8\ub975\uc0b0, \ucc9c\ub450\uc0b0, \ube44\ud30c\uc0b0\ucc98\ub7fc ''\uc0b0''\uc774 \uc5c6\ub294 \uac83\uc740 \uc544\ub2c8\ub2e4. \uadf8\ub7ec\ub098 \uc774\ub4e4\uc740 \uc131\uc778\ubd09\uc758 \ubd80\uc18d\ubd09\uc77c \ubfd0 \ub300\ud45c\uc131\uc744 \uac16\uc9c0 \ubabb\ud55c\ub2e4. \uc6b8\ub989\ub3c4\ub97c \ud3ec\uad04\ud558\ub294 \uc0b0 \uc774\ub984\uc740 \uc5c6\ub2e4. <BR>\uc774\ub984\uc740 \uc5ed\uc0ac \uc18d\uc5d0 \uc788\ub2e4. &lt;\uc0bc\uad6d\uc0ac\uae30&gt; \uc9c0\uc99d\uc655\uc870\uc5d0 \uc774\uc0ac\ubd80\uac00 \uc810\ub839\ud588\ub2e4\ub294 \uc6b0\uc0b0\uad6d\uc758 \uc6b0\uc0b0\uc774\ub2e4. \ub530\ub77c\uc11c \uc131\uc778\ubd09\uc740 \uc6b0\uc0b0\uc758 \ucd5c\uace0\ubd09\uc774\ub77c \ud558\uaca0\ub2e4. <BR>900m\uac00 \ub118\ub294\ub370\ub3c4 \ud654\uc0b0\uc9c0\ud615\uc774\ub77c \ub4f1\uc0b0\ub85c\ub294 \uc14b\ubc16\uc5d0 \uc5c6\ub2e4. \ub3d9\uad74\uc774\ub098 \ud611\uace1\uc758 \uc704\ud5d8\uc9c0\uc5ed\uc774 \ub9ce\uae30 \ub54c\ubb38\uc774\ub2e4. \ub530\ub77c\uc11c \ub4f1\uc0b0\uc740 \uc131\uc778\ubd09 \uc624\ub974\uae30 \ud55c \ubc88\uc73c\ub85c \ub9cc\uc871\ud558\uace0 \uc12c \uc77c\uc8fc \ubc31\ud328\ud0b9\uc73c\ub85c \ud5c8\uc804\ud55c \ub9c8\uc74c\uc744 \ucc44\uc6b4\ub2e4. \ube44\uacbd\uacfc \ubcfc\uac70\ub9ac\ub294 \uac70\uc758 \ub2e4 \uac70\uae30\uc5d0 \uc788\ub2e4. \ub098\uc544\uac00 \ubc30\ub97c \ud0c0\uace0 \uc12c\uc744 \ud55c \ubc14\ud034 \ub3cc\uc544\ubcf8\ub2e4. <BR>\ucd5c\ub2e4\uc124\uc9c0\uc5ed\uc778 \uae4c\ub2ed\uc5d0 \uaca8\uc6b8\uc5d0\ub294 \uc0b0\uc545\uc2a4\ud0a4\ud22c\uc5b4\ub3c4 \ud560 \ub9cc\ud558\ub2e4. \uc131\uc778\ubd09\u223c\ub098\ub9ac\ub839 \ub2a5\uc120\uc758 \uc12c\uc870\ub9bf\ub300\uc232 \uc704\ub97c \ubbf8\ub044\ub7ec\uc838 \uac08 \ub54c\ub294 \uaca8\uc6b8\ubc14\ub2e4\uac00 \uc815\ud0dc\ucd98\uc758 ''\ub5a0\ub098\uac00\ub294 \ubc30''\ub97c \ubd88\ub7ec\uc904 \uac83\uc774\ub2e4.'), STRINGDECODE('\ud734\ud654\uc0b0\uc778 \uc6b8\ub989\ub3c4\uc758 \ucd5c\uace0\ubd09\uc73c\ub85c\uc11c \uc6b8\ub989\ub3c4 \ubaa8\ub4e0 \ud558\ucc9c\uc758 \uc218\uc6d0\uc744 \uc774\ub8e8\uace0, \uc2dd\uc0dd\uc774 \ud2b9\uc774\ud55c \uc6d0\uc2dc\ub9bc\uc774 \uc798 \ubcf4\uc804\ub418\uc5b4 \uc788\ub294 \uc810 \ub4f1