INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(55, TIMESTAMP '2019-08-14 15:46:34.927', TIMESTAMP '2019-08-14 15:47:12.276', '41', STRINGDECODE('\uc11c\uc6b8\uc5d0\uc11c 44km, \ub3d9\ub450\ucc9c\uc2dc\uccad\uc5d0\uc11c \ub3d9\ubd81\ucabd\uc73c\ub85c \uc57d 5km\uc758 \uac70\ub9ac\uc5d0 \uc788\ub294 \uc18c\uc694\uc0b0\uc740 \uacbd\uce58\uac00 \ub6f0\uc5b4\ub098 \uacbd\uae30\ub3c4\uc758 ''\uc18c\uae08\uac15''\uc774\ub77c \ubd88\ub9b0\ub2e4.'), '&nbsp;', '37.90362', '127.060417', STRINGDECODE('\uacbd\uae30\ub3c4 \ub3d9\ub450\ucc9c\uc2dc, \ud3ec\ucc9c\uc2dc \uc2e0\ubd81\uba74'), 2112130200, 588, STRINGDECODE('\uc18c\uc694\uc0b0'), STRINGDECODE('&lt;\uc6d0\ud6a8\ub300\uc0ac \uc804\uc124 \uc11c\ub9b0 \uacbd\uae30 \uc18c\uae08\uac15&gt;<BR>\uc18c\uc694\uc0b0\uc740 \uc601\uad6d \uae30\uc900\uc73c\ub85c\ub294 \uc0b0 \ucd95\uc5d0\ub3c4 \ubabb \ub4dc\ub294 \uc5b8\ub355\uc774\ub2e4. \uadf8\ub4e4\uc740 2000\ud53c\ud2b8, 609.6\ubbf8\ud130\ubd80\ud130 \ub9c8\uc6b4\ud2f4\uc73c\ub85c \ubcf4\uae30 \ub54c\ubb38\uc774\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc774 \ud48d\uc218\uc9c0\ub9ac\uc758 \ub545 \uc0ac\ub78c\ub4e4\uc740 50\ubbf8\ud130 \ub192\uc774\uc758 \ub3c5\ub9bd\ubd09\uc870\ucc28\ub3c4 \ud608\ub9e5\uc774 \ud750\ub974\ub294 \uacf3\uc774\ub77c\uba74\uc11c \uc0b0 \ub300\uc811\uc744 \ud574\uc8fc\uc5c8\ub2e4. <BR>\uc18c\uc694\uc0b0\ucc98\ub7fc \ubc14\uc704\uac00 \ubd88\ub69d\ubd88\ub69d \uc19f\uc740 \ub370\ub294 \ub354 \ub9d0\ud560 \ub098\uc704\uac00 \uc5c6\ub2e4. \uac8c\ub2e4\uac00 \ud3ed\ud3ec\uc640 \ud0d5, \uc18c\uae4c\uc9c0 \uc990\ube44\ud558\ub2c8 \uaca9\uc744 \ub192\uc5ec \uba85\uc0b0\uc73c\ub85c \ucce4\ub2e4.<BR>\uc6d0\ud6a8\ud3ed\ud3ec, \uccad\ub7c9\ud3ed\ud3ec, \uc120\ub140\ud0d5\uc774 \ub300\ud45c\uc801 \uba85\uc2b9\uc774\ub2e4. \ub4a4\uc9d1\uc5b4\uc9c4 C\uc790 \ubaa8\uc591\uc758 \uc0b0 \uc6b4\ub450\ub97c \ub2e4 \ub3c8\ub2e4 \ud574\ub3c4 \uc11c\ub108 \uc2dc\uac04\ubc16\uc5d0 \uc548 \uac78\ub9b0\ub2e4. <BR>\ub2f9\ub098\ub77c \uc720\ud559\ub3c4 \uac00\ub2e4 \ub9d0\uc558\ub358 \uc6d0\ud6a8\uac00 \uc5ec\uae30\uae4c\uc9c0 \uc654\uc744 \ub9ac \ub9cc\ubb34\ud558\uc9c0\ub9cc \uacf3\uacf3\uc5d0 \uc804\uc124\uc774 \uc11c\ub824\uc788\ub294 \uae4c\ub2ed\uc740 \uac00\uae4c\uc6b4 \uc784\uc9c4\uac15\uc774 \ubc31\uc5ec \ub144 \uac00\ub7c9 \uc2e0\ub77c\uc758 \uad6d\uacbd\uc774\uc5c8\uae30 \ub54c\ubb38\uc77c \uac83\uc774\ub2e4. \uadf8\ub798 \uc0b0 \uc18d\uc5d0 \ucd5c\uc804\ubc29 \uc7a5\uc878\ub4e4\uc744 \uc704\ud55c \uc808\uc774 \ub4e4\uc5b4\uc130\uc744 \ud14c\uace0 \uc124\ubc95 \ub0b4\uc6a9\uc740 \ub300\uccb4\ub85c \"\"\uc8fd\uc73c\uba74 \ubbf8\ub975\uc758 \ub098\ub77c\uc5d0\uc11c \ud658\uc0dd\ud55c\ub2e4\"\"\ub294 \ubbf8\ub975\uc0c1\uc0dd\uacbd\uc774\uc5c8\uc73c\ub9ac\ub2c8 \uadf8 \uc774\ub860\uc744 \uc138\uc6b4 \uc6d0\ud6a8\ub300\uc0ac\uc640 \uc778\uc5f0\uc774 \uc5c6\uc9c0\ub9cc\uc740 \uc54a\ub2e4.'), STRINGDECODE('\uaddc\ubaa8\ub294 \uc791\uc73c\ub098 \uc0c1\ubc31\uc6b4\ub300, \ud558\ubc31\uc6b4\ub300, \uc911\ubc31\uc6b4\ub300 \ub4f1 \uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0, \ub4f1\uc0b0\uc778\uc758 \uc120\ud638\ub3c4\uac00 \ub192\uc544 \"81\ub144 \uad6d\ubbfc\uad00\uad11\uc9c0\ub85c \uc9c0\uc815\ub41c \uc810\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc6d0\ud6a8\ud3ed\ud3ec, \uccad\ub7c9\ud3ed\ud3ec, \uc120\ub140\ud0d5\uc808\ubcbd\uacfc \uac00\uc744\ucca0 \ub2e8\ud48d\uc774 \uc720\uba85\ud558\uba70, \uc2e0\ub77c \ubb34\uc5f4\uc655 1\ub144(654\ub144)\uc5d0 \uc6d0\ud6a8\ub300\uc0ac\uac00 \ucc3d\uac74\ud558\uc600\ub2e4\uace0 \uc804\ud574\uc9c0\ub294 \uc790\uc7ac\uc554\uc774 \uc788\uc74c'), STRINGDECODE('\ub9d0\ubc1c\uad7d \ubaa8\uc591\uc744 \ud55c \uacbd\uae30\ub3c4\uc758 \uc18c\uae08\uac15'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc18c\uc694\uc0b0\uc8fc\ucc28\uc7a5 \uc8fc\ubcc0\uc5d0 \uc5ec\uad00\uacfc \ubbfc\ubc15\uc9d1\uc774 \uc788\ub2e4. <BR>\ub300\uad70\uc7a5\uc5ec\uad00 \u260e031-865-4303<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub3d9\ub450\ucc9c\uc2dc\uccad \ubb38\ud654\uacf5\ubcf4\uacfc \uc18c\uc694\uc0b0\uad00\ub9ac