INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(56, TIMESTAMP '2019-08-14 15:46:34.927', TIMESTAMP '2019-08-14 15:47:12.859', '47', STRINGDECODE('\ud55c\ubc18\ub3c4\uc758 \ub4f1\uc904\uae30\uc778 \ubc31\ub450\ub300\uac04(\u767d\u982d\u5927\u5e79)\uacfc \uc11c\ud574\ubc14\ub2e4\uae4c\uc9c0 \ubed7\uc5b4 \ub098\uac04 \ud55c\ub0a8\uae08\ubd81\uc815\ub9e5(\u6f22\u5357\u9326\u5317\u6b63\u8108)\uc758 \ubd84\uae30\uc810\uc5d0 \uc19f\uc544 \uc62c\ub77c \uc870\uc120\ud314\uacbd\uc758 \ud558\ub098\uc774\uc790 \uc18c\uae08\uac15, \ub610\ub294 \uc81c 2\uc758 \uae08\uac15\uc774\ub77c \ubd88\ub9ac\ub294 \uc18d\ub9ac\uc0b0(1,058.4m)\uc740 \ud589\uc815\uad6c\uc5ed\uc0c1\uc73c\ub85c\ub294 \ucda9\ubd81 \ubcf4\uc740\uacfc \uacbd\ubd81 \uc0c1\uc8fc\uc5d0 \uac78\uccd0 \uc788\ub294 \uba85\uc0b0\uc774\ub2e4.'), STRINGDECODE('\u2460 \ubc95\uc8fc\uc0ac-\ubb38\uc7a5\ub300-\uc2e0\uc120\ub300-\uc138\uc2ec\uc815 \uc0bc\uac70\ub9ac-\ubc95\uc8fc\uc0ac (\ucd1d 5\uc2dc\uac04)<BR>\u2461 \ubc95\uc8fc\uc0ac-\uc0bc\uac70\ub9ac-\uc2e0\uc120\ub300-\ube44\ub85c\ubd09-\uc0c1\ud658\uc554-\uc138\uc2ec\uc815 \uc0bc\uac70\ub9ac-\ubc95\uc8fc\uc0ac (\ucd1d 4\uc2dc\uac04 50\ubd84)<BR>\u2462 \uc2dc\uc5b4\ub3d9\ub9e4\ud45c\uc18c-\ubb38\uc7a5\ub300-\uc911\uc0ac\uc790\uc554-\ubc95\uc8fc\uc0ac (\ucd1d 5\uc2dc\uac04 10\ubd84)'), '36.579568', '127.883412', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uc0c1\uc8fc\uc2dc \ud654\ubd81\uba74, \ucda9\uccad\ubd81\ub3c4 \ubcf4\uc740\uad70 \ub0b4\uc18d\ub9ac\uba74'), 2303021300, 1058, STRINGDECODE('\uc18d\ub9ac\uc0b0'), STRINGDECODE('&lt;\ucca9\ucca9 \uc0b0 \ubb3c\uacb0 \uc774\ub8ec \ud070\uc0b0 &gt;<BR>\uc18d\ub9ac\uc0b0\uc740 1970\ub144 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \ub300\ud55c\ud314\uacbd \uc911 \ud558\ub098\ub2e4. \uc8fc\ubd09\uc778 \ucc9c\ud669\ubd09(1058.4m)\uc744 \ube44\ub86f\ud574 \ube44\ub85c\ubd09, \uae38\uc0c1\ubd09, \ubb38\uc218\ubd09 \ub4f1 8\ubd09\uacfc \uc785\uc11d\ub300, \ubb38\uc7a5\ub300, \uc2e0\uc120\ub300 \uac19\uc774 \ube7c\uc5b4\ub09c \uc790\ud0dc\ub97c \uc790\ub791\ud558\uba70 \uc6b0\ub69d \uc19f\uc740 8\ub3008\uc11d\ubb38\uc774 \ud65c\ucc98\ub7fc \ud718\uc5b4\uc838 \uc790\ub9ac\ud558\uace0 \uc788\ub2e4. \uc774\ub7ec\ud55c \ud654\uac15\uc554\uc758 \uae30\ubd09(\u5947\u5cf0)\uacfc \uc0b0 \uc804\uccb4\ub97c \ub4a4\ub36e\uc740 \uc6b8\ucc3d\ud55c \uc0b0\ub9bc\uc740 \ucc9c\ub144\uace0\ucc30 \ubc95\uc8fc\uc0ac\uc640 \uc798 \uc870\ud654\ub418\uc5b4 \uc2b9\uacbd\uc744 \uc774\ub8ec\ub2e4. <BR>\uac00\ud30c\ub978 \ud654\ubd81\uba74 \ucabd\uc5d0 \ube44\ud574 \uc0b0\uc790\ub77d\uc774 \ub108\ub978 \ub0b4\uc18d\ub9ac\uba74 \ucabd\uc5d0\ub294 \ubc95\uc8fc\uc0ac, \ubcf5\ucc9c\uc554, \uc911\uc0ac\uc790\uc554, \uc0c1\ud658\uc554 \uac19\uc740 \uba85\uc2b9\uace0\uc801\uc744 \ud488\uace0 \uc788\ub2e4. \ucca9\ucca9 \ubd09\uc6b0\ub9ac\ub4e4 \uc0ac\uc774\uc5d4 \uc740\ud3ed\ub3d9\uacc4\uace1, \uc6a9\uc720\ub3d9\uacc4\uace1, \uc30d\ub8e1\ud3ed\ud3ec, \uc624\uc1a1\ud3ed\ud3ec \ub4f1\uc774 \uc788\uc73c\uba70, 1016\uac00\uc9c0\uc758 \ub3d9\uc2dd\ubb3c\uc774 \uc11c\uc2dd\ud558\uace0 \uc788\ub294 \uc790\uc5f0\uc790\uc6d0\uc758 \ubcf4\uace0\uc774\ub2e4. \ucc9c\ud669\ubd09\uc740 \uc870\uc120 3\ub300\uba85\uc218(\uc0bc\ud30c\uc218, \ub2ec\ucc9c\uc218, \uc6b0\ud1b5\uc218)\uc911 \ud558\ub098\uc778 \uc0bc\ud30c\uc218\uc758 \ubc1c\uc6d0\uc9c0\ub85c \uc774\uacf3\uc5d0 \ub0b4\ub9b0 \ube57\ubb3c\uc740 \ub3d9\ucabd \ub099\ub3d9\uac15, \ub0a8\ucabd \uae08\uac15, \uc11c\ucabd \ub0a8\ud55c\uac15\uc73c\ub85c \ud758\ub7ec\uac04\ub2e4.'), STRINGDECODE('\uc608\ub85c\ubd80\ud130 \uc0b0\uc138\uac00 \uc218\ub824\ud558\uc5ec \uc81c2\uae08\uac15 \ub610\ub294 \uc18c\uae08\uac15\uc774\ub77c\uace0\ub3c4 \ubd88\ub9ac\uc6b8 \uc815\ub3c4\ub85c \uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0 \ub9dd\uac1c\ub098\ubb34, \ubbf8\uc120\ub098\ubb34 \ub4f1 1,000\uc5ec \uc885\uc774 \ub118\ub294 \ub3d9\uc2dd\ubb3c\uc774 \uc11c\uc2dd\ud558\uace0 \uc788\uc73c\uba70, \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1970\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubc95\uc8fc\uc0ac(\u6cd5\u4f4f\u5bfa), \ubb38\uc7a5\ub300, \ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c103\ud638\uc778 \uc815\uc774\ud488\uc1a1(\u6b63\u4e8c\u54c1\u677e) \ubc0f \ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c207\ud638\uc778 \ub9dd\uac1c\ub098\ubb34\uac00 \uc720\uba85'), STRINGDECODE('\uc18d\uc138\ub97c \ub4f1\uc9c4 \uc774\ub4e4\uc758 \ud68c\uc2ec\ucc98'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc18d\ub9ac\uc0b0\uad00\uad11\ub9c8\uc744\uc5d0 \uc219\uc2dd\ud560 \uacf3\uc774 \ub9ce\ub2e4. <BR>\uc18d\ub9ac\uc0b0\uad00\uad11\ud638\ud154 \u260e043-542-5181, \ud55c\uc77c\uc7a5 \u260e043-543-1100<BR>\uc1a1\ub9bc\uc5ec\uad00 \u260e043-543-3911, \uccad\uc6b4\uc7a5 \u260e043-543-3906<BR>\ub9d0\ud2f0\uc7ac\uc790\uc5f0\ud734\uc591\ub9bc \u260e043-543-1667, \uacbd\ud76c\uc2dd\ub2f9 \u260e043-543-3736<BR>\uccad\uac15\uc2dd\ub2f9 \u260e043-542-5081<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc18d\ub9ac\uc0b0\uc0ac\ubb34\uc18c \u260e043-542-5267~8'), STRINGDECODE('\uae30\uc810\uc740 \ubcf4\uc740\uc774\ub098 \uccad\uc8fc\ub2e4. <BR>- \ubcf4\uc740(\u260e043-543-1580)\uc5d0\uc11c \uc18d\ub9ac\uc0b0\uc740 07:20~21:30, 10\ubd84 \uac04\uaca9 \uad70\ub0b4\ubc84\uc2a4 \uc6b4\ud589. 20\ubd84 \uc18c\uc694.<BR>- \uccad\uc8fc(\u260e043-234-6543)\uc5d0\uc11c \uc18d\ub9ac\uc0b0\uc740 \ubcf4\uc740\uc744 \uac70\uccd0 06:30~20:40 1\uc77c 29\ud68c \uc2dc\uc678\ubc84\uc2a4 \uc6b4\ud589. 1\uc2dc\uac04 50\ubd84 \uc18c\uc694'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(57, TIMESTAMP '2019-08-14 15:46:34.928', TIMESTAMP '2019-08-14 15:47:13.485', '31', STRINGDECODE('\ucde8\uc11c\uc0b0\uc740 \uc77c\uba85 \uc601\ucde8\uc0b0\uc73c\ub85c \ubd88\ub9ac\uace0 \uc788\uc73c\uba70, \uc774 \uc0b0\uc758 \uc0b0\uc790\ub77d\uc5d0\ub294 3\ub300 \uc0ac\ucc30 \uc911 \ud558\ub098\uc778 \ud1b5\ub3c4\uc0ac\uac00 \uc790\ub9ac\uc7a1\uace0 \uc788\ub2e4. \ucde8\uc11c\uc0b0 \uc815\uc0c1\uc5d0\uc11c \uc2e0\ubd88\uc0b0 \uc815\uc0c1\uae4c\uc9c0 \uc774\uc5b4\uc9c0\ub294 \uc5b5\uc0c8\ub2a5\uc120\uc774 \uc720\uba85\ud558\uba70, \uc2e0\ubd88\uc0b0 \uc0b0\uc790\ub77d\uc5d0\ub294 \ud64d\ub958\ud3ed\ud3ec\uc640 \uc791\ucc9c\uc815\uc774 \uc720\uba85\ud558\ub2e4. <BR><BR>\ucde8\uc11c\uc0b0\uacfc \uc2e0\ubd88\uc0b0\uc740 \uc601\ub0a8 \uc54c\ud504\uc2a4\uc758 7\uac1c \ubd09\uc6b0\ub9ac\uc5d0 \uc18d\ud558\ub294 \uc0b0\uc73c\ub85c \uad11\ud65c\ud55c \uc5b5\uc0c8\ubc2d\uc73c\ub85c \uc774\ub984 \ub09c \uacf3\uc774\ub2e4. \uacbd\ubd80\uace0\uc18d\ub3c4\ub85c\ub97c \ubd80\uc0b0 \ubc29\uba74\uc73c\ub85c \ub0b4\ub824\uac00\ub2e4\uac00 \uc5b8\uc591\uc778\ud130\uccb4\uc778\uc9c0\uc5d0\uc11c\ubd80\ud130 \ud1b5\ub3c4\uc0ac\uc778\ud130\uccb4\uc778\uc9c0 \uc0ac\uc774\uc5d0 \uc624\ub978\ucabd\uc73c\ub85c \uace0\uc18d\ub3c4\ub85c\ub85c \ub098\ub780\ud788 \uae38\uac8c \ubed7\uc5b4 \uc788\uc73c\uba70 \ub450 \uc0b0\uc740 \uac19\uc740 \uc8fc\ub2a5\uc120\uc5d0 \uac00\uae4c\uc774 \ubd99\uc5b4 \uc788\uc5b4 \uc0b0\ud589\ub3c4 \uc5f0\uacb0\ud574\uc11c \ud558\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc2e0\ubd88\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-\ubc31\uc6b4\uc554-\uc2e0\ubd88\uc7ac-\uc815\uc0c1-\uac04\uc6d4\uc7ac-\uc655\ubd09\uace8-\ud30c\ub798\uc18c\ud3ed\ud3ec-\ud734\uc591\ub9bc(\ucd1d 5\uc2dc\uac04 10\ubd84)<BR>\u2461 \ud64d\ub958\uc0b0\uc7a5-\uac04\uc6d4\uc7ac-\uc815\uc0c1-\ud64d\ub958\ud3ed\ud3ec-\ud64d\ub958\uc0b0\uc7a5(\ucd1d 3\uc2dc\uac04 50\ubd84)<BR>\u2462 \uac00\ucc9c\uc800\uc218\uc9c0-\ub3d9\ub989-\uc815\uc0c1-\uc11c\ub989-\ubc31\uc6b4\uc554-\uc2e0\ubd88\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\ucd1d 4\uc2dc\uac04 20\ubd84)'), '35.53224', '129.096047', STRINGDECODE('\uc6b8\uc0b0\uad11\uc5ed\uc2dc \uc6b8\uc8fc\uad70 \uc0bc\ub0a8\uba74\u318d\uc0c1\ubd81\uba74'), 1704041600, 1159, STRINGDECODE('\uc2e0\ubd88\uc0b0'), STRINGDECODE('&lt;\uad11\ud65c\ud55c \uc5b5\uc0c8\ucd08\uc6d0 \uc790\ub791\ud558\ub294 \uc5b8\uc591\uc758 \uc9c4\uc0b0&gt;<BR>\uc601\ucde8\uc0b0\uacfc\uc758 \uc0ac\uc774\uc5d0 \uc2e0\ubd88\ud3c9\uc6d0\uc774\ub77c\ub294 \uad11\ud65c\ud55c \uc0b0\uc0c1 \uc5b5\uc0c8\ucd08\uc6d0\uc744 \ud3bc\uce5c \uc774\uc0c9\uc9c0\ub300\ub2e4. \ub3d9\ucabd\uc73c\ub85c\ub294 \uacf5\ub8e1\ub2a5\uc120\uc774\ub77c\ub294 \uae34 \uc554\ubd09\ub989\uc774 \ubed7\uc5b4\ub098\uac00 \uc0b0\ub85d \uc721\ub355(\u8089\u5fb7) \uc790\ub791\ub9cc \ud558\ub294 \uc8fc\ubcc0 \uc0b0\ub4e4\uacfc \uad6c\ubcc4\ub41c\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \uc369 \ud280\uc9c0\ub294 \uc54a\uc544 \uac04\uc6d4, \uac00\uc9c0, \uc0c1\uc6b4, \uace0\ud5cc\uc758 \ucc9c\ubbf8\ud130\uae09 \uc0b0\uc73c\ub85c \uacc4\uc18d \uc774\uc5b4\uc9c0\uba70 \uc74d\uc131 \ud488\uc740 \uc624\ub79c \uace0\uc744 \uc5b8\uc591\uc758 \uc7a5\ub300\ud55c \ubcd1\ud48d\uc744 \uc774\ub8ec\ub2e4.<BR><BR>\ub3d9\ucabd\uc758 \ud64d\ub958\ud3ed\ud3ec, \uc11c\ucabd\uc758 \ud30c\ub798\uc18c\ud3ed\ud3ec \ub610\ud55c \uc774\uc6c3 \uc0b0\ub4e4\uc5d0\ub294 \uc5c6\ub294 \uc774\ucc44\ub85c\uc6c0\uc774\ub2e4. \ud30c\ub798\uc18c\ud3ed\ud3ec \ud558\ub958\uc5d0\ub294 \ub110\ub530\ub780 \uc554\ubc18\uacc4\ub958\ub97c \ud3bc\uccd0 \uc544\ub984\ub2e4\uc6b4 2\uce35 \ub204\uac01 \uc791\ucc9c\uc815\uc774 \uc790\ub9ac\uc7a1\uc558\ub2e4. \uadf8\ub9ac\uace0 \uc0b0 \uc704\uc5d0\ub294 \ub2e8\uc870\uc0b0\uc131\uc774 \uc313\uc5ec\uc788\uc73c\ub2c8 \ub2e4\uc12f \ubcd1\ud48d\ucd95 \uac00\uc6b4\ub370\uc11c\ub3c4 \ud2b9\ubcc4\ud788 \uc120\ud0dd\ubc1b\uc740 \uc5b8\uc591\uc758 \uc9c4\uc0b0\uc774\ub2e4. <BR><BR>\ub2e4\uc12f \ubcd1\ud48d\ucd95\uc5d0 \uc6b4\ubb38\u00b7\uc7ac\uc57d\uc0b0\uc744 \ud569\ud558\uba74 \uc18c\uc704 \"\uc601\ub0a8\uc54c\ud504\uc2a4\"\ub2e4. \uc720\ub7fd\uc758 \uc6a9\ub9c8\ub8e8 \uc54c\ud504\uc2a4\ub97c \ubcf8\ub530 \ubd99\uc778 \uc77c\ubcf8\uc758 \ub0a8\ubd81\uc54c\ud504\uc2a4\uac00 \ub2e4\uc2dc \uc0c8\ub07c\ub97c \uce5c \uac83\uc774\ub2e4. \uc800\ub4e4\ucc98\ub7fc \ud558\uc580 \ub9cc\ub144\uc124\uc740 \uc5c6\uc9c0\ub9cc \ub300\uc2e0 \ud669\uae08\ube5b \uc5b5\uc0c8\ubc2d\uc744 \uc790\ub791\ud558\ub294 \ub178\ub780 \uc54c\ud504\uc2a4\ub2e4.'), STRINGDECODE('\uc601\ub0a8\uc54c\ud504\uc2a4 \uc0b0\uad70\uc5d0 \uc18d\ud558\ub294 \uc0b0\uc73c\ub85c \ub2a5\uc120\uc5d0\ub294 \uad11\ud65c\ud55c \uc5b5\uc0c8\uc640 \ubc14\uc704\uc808\ubcbd, \uc644\ub9cc\ud55c \uc9c0\ub300\uac00 \uc870\ud654\ub97c \uc774\ub8e8\uace0 \uc788\uc73c\uba70 \uc791\ucc9c\uacc4\uace1, \ud30c\ub798\uc18c\ud3ed\ud3ec \ub4f1\uc774 \uc788\uace0 \uad70\ub9bd\uacf5\uc6d0\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc2e0\ubd88\uc0b0 \ud3ed\ud3ec\uc790\uc5f0\ud734\uc591\ub9bc \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\uad11\ud65c\ud55c \uc5b5\uc0c8\ubc2d\uc774 \ud3bc\uccd0\uc9c4 \uc601\ub0a8 \uc54c\ud504\uc2a4'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ud1b5\ub3c4\uc0ac \ubd80\uadfc\uc5d0 \uc219\ubc15\uc5c5\uc18c\uac00 \ub9ce\ub2e4. \ud1b5\ub3c4\uc0ac\uad00\uad11\ud638\ud154(\u260e055-382-7117), \ub355\ub9bc\uc7a5\uc5ec\uad00(\u260e055-382-6181). <BR>\uc0b0 \uc11c\ucabd\uc5d0\uc11c\ub294 \ubc31\ub828\uacc4\uace1\uc5d0 \uc788\ub294 \uc2e0\ubd88\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e052-254-2123)\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc6b8\uc8fc\uad70\uccad \uad00\uad11\uacfc \u260e052-229-3852'), STRINGDECODE('\uc5b8\uc591\uc774 \uae30\uc810\uc774\ub2e4. 9\ud68c \uc788\ub294 \uc591\uc0b0\ud589 \uc644\ud589\ubc84\uc2a4\ub97c \ud0c0\uba74 \uc0b0 \ub3d9\ucabd \uc5b4\ub514\ub4e0\uc9c0 \ub0b4\ub9b4 \uc218 \uc788\ub2e4. \uc0b0 \uc11c\ucabd\uc740 3\ud68c \uc788\ub294 \uccad\ub3c4\ud589 \uc9c1\ud589\ubc84\uc2a4\ub97c \uc774\uc6a9\ud55c\ub2e4. \uc5b8\uc591\uc73c\ub85c \uac00\ub294 \uace0\uc18d\ubc84\uc2a4\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 4\ud68c(1\ud68c\ub294 \uc2ec\uc57c), \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c 4\ud68c \uc788\ub2e4. \uacbd\uc0c1\ub0a8\ub3c4 \uc774\uc678\uc758 \ub3c4\uc2dc\ub85c\ub294 \uc624\uc0b0, \uc218\uc6d0, \uc548\uc0b0 \ub4f1\uc9c0\uc5d0\uc11c \ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4. \uac00\uae4c\uc6b4 \uc6b8\uc0b0\uacfc \ubd80\uc0b0\uc5d0\uc11c\ub294 \uc140 \uc218 \uc5c6\uc774 \ub9ce\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(58, TIMESTAMP '2019-08-14 15:46:34.928', TIMESTAMP '2019-08-14 15:47:14.085', '48', STRINGDECODE('\uc554\ucc9c\uc744 \ub530\ub77c \uace0\uc131\uc74d \uc11c\ubd81\ucabd 12km \uac70\ub9ac\uc5d0 \uc788\ub294 \uc0b0\uc73c\ub85c \uc625\ub140\ubd09, \uc120\ub3c4\ubd09, \ub9dd\uc120\ubd09\uc758 \uc138 \ubd09\uc6b0\ub9ac\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc73c\uba70 \uc0b0\uc758 \ubd81\ucabd \uae30\uc2ad\uc5d0\ub294 \uc720\uc11c\uae4a\uc740 \uc625\ucc9c\uc0ac\uc640 \ubc31\ub828\uc554, \uccad\uc5f0\uc554, \uc5f0\ub300\uc554 \ub4f1\uc758 \uc554\uc790\uac00 \uc788\uc73c\uba70 \ud2b9\uc0b0\ubb3c\ub85c\ub294 \uc1a1\uc774\ubc84\uc12f\uacfc \uc0b0\ub538\uae30 \ub4f1\uc774 \uc720\uba85\ud558\ub2e4. <BR>\uc625\ucc9c\uc0ac \ub300\uc6c5\uc804 \ub4a4\uc5d0 \uc704\uce58\ud55c \uc625\ucc9c\uc0d8\uc740 \uc0ac\uc2dc\uc0ac\ucca0 \ub9c8\ub974\uc9c0 \uc54a\uace0 \ud56d\uc0c1 \uc218\ub7c9\uacfc \uc218\uc628\uc774 \uc77c\uc815\ud558\uba70 \uc774 \uc0d8\uc758 \ubb3c\uc740 \uc704\uc7a5\ubcd1, \ud53c\ubd80\ubcd1\uc5d0 \ud6a8\ud5d8\uc774 \uc788\ub2e4. 1948\ub144 \uc0d8 \uc704\uc5d0 \uc625\ucc9c\uac01\uc744 \uc138\uc6cc \ubcf4\uc874\ud558\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc625\ucc9c\uc0ac-\uccad\ub828\uc554-\ub0a8\uc0b0-\uc815\uc0c1-\ud669\uc0c8\uace0\uac1c-\uc625\ucc9c\uc0ac (\ucd1d 2\uc2dc\uac04 15\ubd84)<BR>\u2461 \uc5f0\ud654\uad50-\ud654\ucd0c-\uc815\uc0c1-\uc798\ub8e8\ubaa9-\uc625\ucc9c\uc0ac (\ucd1d 3\uc2dc\uac04 30\ubd84)'), '35.096767', '128.286382', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \uace0\uc131\uad70 \uac1c\ucc9c\uba74\u318d\uc601\ud604\uba74'), 2803010100, 524, STRINGDECODE('\uc5f0\ud654\uc0b0'), STRINGDECODE('&lt;\uc5f0\uaf43\uc744 \ub2ee\uc740 \uc0b0&gt;<BR>\uc5f0\uaf43\uc744 \ub2ee\uc558\ub2e4\uace0 \ud574 \uc774\ub984\uc9c0\uc5b4\uc9c4 \uc5f0\ud654\uc0b0\uc740 \uace0\uc131\uc758 \uc0b0\uc744 \ub300\ud45c\ud568\uc5d0 \ubd80\uc871\ud568\uc774 \uc5c6\ub2e4. <BR>\uace0\uc131\uc74d \uc11c\ubd81\ucabd\uc5d0 \uc704\uce58\ud588\uc73c\uba70 1989\ub144 \uac1c\ucc9c\uba74\uacfc \uc601\ud604\uba74\uc744 \ud3ec\ud568\ud55c \uc77c\ub300\uac00 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\ub2e4. \uc625\ucc9c\uc0ac\ub294 \ucc9c\ub144\uace0\ucc30 \uac00\ub78c\uc758 \ubc30\uce58\uac00 \uc12c\uc138\ud55c \ud654\uc5c4 10\ub300 \uc0ac\ucc30\uc758 \ud558\ub098\ub2e4. <BR>\uc5f0\ud654\uc0b0\uc5d0 \uc624\ub974\uba74 \ub3d9\ucabd\uc73c\ub85c \ucabd\ube5b \ubc14\ub2e4\uc758 \ub2f9\ud56d\ud3ec\uac00 \ud55c\ub208\uc5d0 \ub4e4\uc5b4\uc624\uace0 \uc5f0\ubd09\uc18d\uc5d0 \ud30c\ubb3b\ud78c \uc625\ucc9c\uc0ac\uc758 \uc804\uacbd\uacfc \ubd88\uad50\uc720\ubb3c\uc804\uc2dc\uad00\uc744 \ubcfc \uc218 \uc788\ub2e4. \uc0b0\uc138\uac00 \uc21c\ud0c4\ud558\uace0 \uae38\uc774 \uc798 \ub2e6\uc778 \uc0b0\uc774\uc5b4\uc11c \uac00\ubcbc\uc6b4 \ub9c8\uc74c\uc73c\ub85c \uc0b0\ud589\uc744 \uc990\uae38 \uc218 \uc788\ub2e4.'), STRINGDECODE('\uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0 \uc624\ub798\ub41c \uc0ac\ucc30\uacfc \ubb38\ud654\uc7ac\uac00 \ub9ce\uc73c\uba70 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1983\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0b0 \uc911\ud131\uc5d0 \ud070 \ub300\ubc2d\uc774 \uc788\uc74c. \uc720\uc11c \uae4a\uc740 \uc625\ucc9c\uc0ac(\u7389\u6cc9\u5bfa)\uc640 \uc5f0\ub300\uc554,\ubc31\ub828\uc554, \uccad\uc5f0\uc554 \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ubcd1\uc744 \uace0\uce58\ub294 \uc2e0\ube44\uc758 \uc625\ucc9c\uc0d8'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uace0\uc131\uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110\uc5d0\uc11c \uac00\uae4c\uc6b4 \ub2f9\ud56d\ud3ec\uc5d0\ub294 \uc219\ubc15\uc2dc\uc124\uacfc \uc74c\uc2dd\uc810\uc774 \ub9ce\ub2e4. <BR>\uc36c\ud504\ub77c\uc790\uc5ec\uad00\u260e(055-673-9060)<BR>\ub9ac\ubca0\ubaa8\ud154\u260e(055-673-3441) <BR>\uc774\ucda9\ubb34\uacf5 \uc131\uc5ed\uc73c\ub85c \uac00\ub294 \uae38\uc5d0 \uc788\ub294 \ub2f9\ud56d\ud3ec\uad00\uad11\ub18d\uc6d0\u260e(055-673-3441)\uc5d0\ub294 \uc2