INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(59, TIMESTAMP '2019-08-14 15:46:34.929', TIMESTAMP '2019-08-14 15:47:14.672', '42', STRINGDECODE('\uc6b8\ucc3d\ud55c \uc232\uacfc \uc774\ub984\ub09c \uc0ac\ucc30, \uc57d\uc218, \uc720\uc801\uc9c0 \ub4f1\uc774 \uc0b0\uc7ac\ud574 \uc788\uc5b4 \uad00\uad11\uac1d\uacfc \ub4f1\uc0b0\uac1d\uc73c\ub85c \ub298 \ubd90\ube44\ub294 \uc624\ub300\uc0b0\uc740 \uac15\uc6d0\ub3c4 \ud3c9\ucc3d\uad70 \uc9c4\ubd80\uba74\uacfc \ub3c4\uc554\uba74, \ud64d\ucc9c\uad70 \ub0b4\uba74, \uac15\ub989\uc2dc \uc5f0\uace1\uba74\uc758 \uc624\ub300\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc758 \uc11c\ubd80\uc9c0\uad6c\uc5d0 \uc704\uce58\ud574 \uc788\ub2e4. \ud2b9\ud788 \uc9e7\uc740 \uc0b0\ud589\uc774\uc9c0\ub9cc \uc720\uc11c\uae4a\uc740 \uc0ac\ucc30\uacfc \uc554\uc790\ub97c \uacf3\uacf3\uc5d0\uc11c \ubcfc \uc218 \uc788\ub294 \uc0c1\uc6d0\uc0ac\uc640 \uc801\uba78\ubcf4\uad81, \uc815\uc0c1\uc778 \ube44\ub85c\ubd09\uc744 \uac70\uccd0 \ubbf8\ub975\uc554\uc73c\ub85c \ud558\uc0b0\ud558\ub294 \ucf54\uc2a4\uac00 \uac00\uc7a5 \uc720\uba85\ud558\ub2e4. \uc0c1\uc6d0\uc0ac\ub294 6\u00b725\ub54c \ud55c\uc554\uc774\ub77c\ub294 \uc2b9\ub824\uc758 \uc0b0\ud654\ub85c \uc544\uad70\uc758 \uc804\ub7b5\uc0c1 \uc18c\uc2e4\ub420 \ubed4 \ud558\uc600\ub358 \uc704\uae30\ub97c \ud53c\ud574 \ud604\uc7ac\uae4c\uc9c0 \uadf8 \ud615\ud0dc\ub97c \ubcf4\uc874\ud558\uace0 \uc788\ub294 \uc0ac\ucc30\uc774\ub2e4. \uad6d\ubcf4 36\ud638\uc778 \ub3d9\uc885\uc774 \uc788\uc73c\uba70 \uc2e0\ub77c\uc2dc\ub300 \uc790\uc7a5\ubc95\uc0ac\uc5d0 \uc758\ud574 \ucc3d\uac74\ub418\uc5c8\ub2e4.<BR><BR>\uc608\ub85c\ubd80\ud130 \uae08\uac15\uc0b0, \ud55c\ub77c\uc0b0, \uc9c0\ub9ac\uc0b0\uacfc \ub354\ubd88\uc5b4 \uad6d\ub0b4 \uc81c\uc77c\uc758 \uba85\uc0b0\uc73c\ub85c \uc54c\ub824\uc838 \uc628 \uc624\ub300\uc0b0\uc740 \uace8\uc9dc\uae30\ub9c8\ub2e4 \uc544\ub984\ub4dc\ub9ac \ub098\ubb34\uac00 \uc232\uc744 \uc774\ub8e8\uace0 \uc788\uc5b4 \ub0a8\ud55c \ucd5c\ub300\uc758 \uc218\ub9bc\uc744 \uc790\ub791\ud558\ub2e4. \ub610\ud55c \uac15\uc6d0\ub3c4 \uc77c\ub300\uc758 \uc0b0\ub4e4\uc774 \ub300\ubd80\ubd84 \uae30\uc554\uad34\uc11d\uc73c\ub85c \uc774\ub8e8\uc5b4\uc9c4 \uac83\uacfc\ub294 \ud1a0\uc0b0\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc5b4 \ud55c\uad6d\uc0b0\uc758 \uc804\ud615\uc744 \ubcf4\uc5ec\uc900\ub2e4. <BR><BR>\ub192\uc774\ub85c \ubcf8\ub2e4\uba74 \ube44\ub85c\ubd09\uc740 \uc624\ub300\uc0b0\uc758 \ucd5c\uace0\ubd09\uc774\uc790 \uc774 \uc0b0\uc758 \uc815\uc0c1\uc774\ub77c \ud560 \uc218 \uc788\ub2e4. \ubc31\ub450\ub300\uac04\uc758 \uc8fc\ub9e5\uc774 \uc124\uc545\uc5d0\uc11c \uc774\uc5b4\uc838 \ub450\ub85c\ubd09\uacfc \ub3d9\ub300\uc0b0\uc73c\ub85c \uc5f0\uacb0\ub418\ub294 \uc11c\ucabd \uc77c\ub300\uc5d0 \uc790\ub9ac\ud558\uace0 \uc788\ub294 \uc624\ub300\uc0b0\uc740 \uba85\uc0b0\uc73c\ub85c \uc774\ub984\uc774 \ub09c \ub9cc\ud07c \uc720\uc11c\uae4a\uc740 \uc0ac\ucc30\uacfc \uc554\uc790\ub4e4\uc774 \ub9ce\ub2e4. \ub610\ud55c \uc0b0\uc5d0 \uad00\ub828\ub41c \uc2e0\ube44\ub85c\uc6b4 \uc804\uc124\ub4e4\uc774 \ub9ce\uc774 \uc804\ud574 \ub0b4\ub824\uc624\uace0 \uc788\uc5b4 \uc624\ub300\uc0b0\uc758 \uba85\uc131\uc744 \ub354\ud558\uace0 \uc788\ub2e4. <BR>\uc624\ub300\uc0b0\uc758 \ub2e4\uc12f \ubd09\uc6b0\ub9ac\ub294 \ud638\ub839\ubd09, \ube44\ub85c\ubd09, \uc0c1\uc655\ubd09, \ub450\ub85c\ubd09, \ub3d9\ub300\uc0b0\uc774\ub2e4. \uc774 \ub2e4\uc12f \ubd09\uc6b0\ub9ac\uc911 \ube44\ub85c\ubd09\uc774 \uc81c\uc77c \ub192\uc740 \ubd09\uc6b0\ub9ac\uc774\uace0 \uadf8 \ub2e4\uc74c\uc774 \ud638\ub839\ubd09\uc774\ub2e4.'), STRINGDECODE('\u2460 \uc0c1\uc6d0\uc0ac-\ube44\ub85c\ubd09-\uc0c1\uc655\ubd09-\ub450\ub85c\ubd09-\ub3d9\ub300\uc0b0-\uc9c4\uace0\uac1c\uc0b0\uc7a5 (\ucd1d 10\uc2dc\uac04 30\ubd84, \ub3d9\ub300\uc0b0-\uc9c4\uace0\uac1c 2006\ub144\ubd80\ud130 2010\ub144\uae4c\uc9c0 \uc790\uc5f0\ud734\uc2dd\ub144\uc81c \uc2e4\uc2dc)<BR>\u2461 \ubc29\uc544\ub2e4\ub9ac \uc57d\uc218-\ube44\ub85c\ubd09-\uc0c1\uc6d0\uc0ac (\ucd1d 7\uc2dc\uac04)<BR>\u2462 \uc9c4\uace0\uac1c-\ub178\uc778\ubd09-\ub099\uc601\ud3ed\ud3ec-\ub9cc\ubb3c\uc0c1-\uad6c\ub8e1\ud3ed\ud3ec-\uccad\ud559\uc0b0\uc7a5-\uc18c\uae08\uac15 \uc8fc\ucc28\uc7a5 (\ucd1d 5\uc2dc\uac04 30\ubd84)'), '37.632742', '128.56313', STRINGDECODE('\uac15\uc6d0\ub3c4 \ud3c9\ucc3d\uad70 \uc9c4\ubd80\uba74, \ud64d\ucc9c\uad70 \ub0b4\uba74, \uac15\ub989\uc2dc \uc5f0\uace1\uba74'), 2215072100, 1565, STRINGDECODE('\uc624\ub300\uc0b0'), STRINGDECODE('&lt;\uc232 \uc9d9\uace0 \uc554\ubc18 \uc88b\uc740 \ubd88\uad50 \uc131\uc0b0&gt;<BR>\ub2e4\uc12f \uac1c\uc758 \ubd09\uc6b0\ub9ac\ub97c \uac00\uc9c4 \uc0b0\uc774\ub780 \ub73b\uc758 \uc624\ub300\uc0b0\uc740 \ub3d9\uc11c 20km, \ub0a8\ubd81 33km, 660\u33a2\uc758 \uad11\ub300\ud55c \uba74\uc801\uc744 \uc790\ub791\ud55c\ub2e4. \uc8fc\ubd09\uc778 \ube44\ub85c\ubd09(1565.4m)\uc744 \uc911\uc2ec\uc73c\ub85c \uc0c1\uc655\ubd09(1493m), \ud638\ub839\ubd09(1565.5m), \ub450\ub85c\ubd09(1422.7m), \ub3d9\ub300\uc0b0(1433.5m), \ub3d9\ucabd\uc73c\ub85c \uc19f\uc740 \ub178\uc778\ubd09(1338.8m) \ub4f1\uc758 \uace0\ubd09\uc774 \uc7a5\ucf8c\ud558\uac8c \uc19f\uc544 \uc788\uc73c\uba70, \uc6d4\uc815\uc0ac, \uc0c1\uc6d0\uc0ac, \uc911\ub300 \uc0ac\uc790\uc554, \ubd81\ub300 \ubbf8\ub975\uc554, \ub0a8\ub300 \uc9c0\uc7a5\uc554, \ub3d9\ub300 \uad00\uc74c\uc554, \uc11c\ub300 \uc218\uc815\uc554 \ub4f1\uc774 \uc790\ub9ac\uc7a1\uace0 \uc788\uc5b4 \ubd88\uad50\uc131\uc9c0\ub85c \uc704\uc6a9\uc744 \ub5a8\uce58\uace0 \uc788\ub294 \ub300\ud45c\uc801\uc778 \uba85\uc0b0\uc774\ub2e4.<BR><BR>\uc624\ub300\uc0b0\uc740 \ud06c\uac8c \uc6d4\uc815\uc0ac\uc9c0\uad6c, \uc18c\uae08\uac15\uc9c0\uad6c\ub85c \ub098\ub258\ub294\ub370 \uc6d4\uc815\uc0ac\uc9c0\uad6c\ub294 \ubb38\ud654\uc7ac\uc758 \ubcf4\uace0\ub97c \ub458\ub7ec\ubcfc \uc218 \uc788\ub294 \ucf54\uc2a4\ub77c\uba74, \uad6c\ub8e1\ud3ed\ud3ec, \ub099\uc601\ud3ed\ud3ec \ub4f1\uacfc \ud568\uaed8 \ub9cc\ubb3c\uc0c1, \uc2dd\ub2f9\uc554 \ub4f1\uc774 \uae30\uc554\ub4e4\uc774 \ub4e4\uc5b4\uc11c \uc788\ub294 \uc18c\uae08\uac15\uc9c0\uad6c\ub294 \uacc4\uace1\ubbf8\ub97c \ud55c\uaecf \ub290\ub07c\uba70 \uacc4\uace1\uc0b0\ud589\uc744 \uc990\uae38 \uc218 \uc788\ub294 \ucf54\uc2a4\ub2e4. \ub610\ud55c \ubc29\uc544\ub2e4\ub9ac\uc57d\uc218\ub97c \uae30\uc810\uc73c\ub85c \uc57d\uc218\uc0b0\ud589\ub3c4 \uac00\ub2a5\ud558\ub2e4. <BR><BR>\uc624\ub300\uc0b0\uc740 \ubd04\uc5d0\ub294 \uc57c\uc0dd\ud654\uc758 \ubcf4\uace0\ub2f5\uac8c \uaf43\ub4e4\uc774 \ub9cc\ubc1c\ud558\uace0 \uc5ec\ub984\uc5d0\ub294 \uc2dc\uc6d0\ud55c \uacc4\uace1\uacfc \uc6b8\ucc3d\ud55c \uc0b0\ub9bc, \uac00\uc744\uc5d0\ub294 \uc911\ud6c4\ud55c \uc138\ub828\ubbf8\ub97c \ub290\ub07c\uac8c \ud558\ub294 \uc624\uc0c9\ub2e8\ud48d\uacfc \uaca8\uc6b8\uc5d0\ub294 \ub208\uaf43\uc73c\ub85c \ub4a4\ub36e\uc778 \uc7a5\uad00\uc744 \uc5f0\ucd9c\ud568\uc73c\ub85c\uc368 \uc0ac\uc2dc\uc0ac\ucca0 \uc5b4\ub290 \ub54c \ucc3e\ub354\ub77c\ub3c4 \ud669\ud640\ud568\uc744 \ub9db\ubcfc \uc218 \uc788\ub2e4.'), STRINGDECODE('\uad6d\ub0b4 \uc81c\uc77c\uc758 \uc0b0\ub9bc\uc9c0\ub300\ub97c \uc774\ub8e8\uace0 \uc788\uc73c\uba70, \uacbd\uad00\uc774 \uc218\ub824\ud558\uc5ec \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1975\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc5f0\uaf43\ubaa8\uc591\uc73c\ub85c \ub458\ub7ec\uc120 \ub2e4\uc12f \uac1c\uc758 \ubd09\uc6b0\ub9ac\uac00 \ubaa8\ub450 \ubaa8\ub098\uc9c0 \uc54a\uace0 \ud3c9\ud3c9\ud55c \ub300\uc9c0\ub97c \uc774\ub8e8\uace0 \uc788\ub294\ub370\uc11c \uc0b0\uc774\ub984\uc774 \uc720\ub798. \uc6d4\uc815\uc0ac(\u6708\u7cbe\u5bfa), \uc801\uba78\ubcf4\uad81(\u5bc2\u6ec5\u5bf6\u5bae), \uc0c1\uc6d0\uc0ac(\u4e0a\u9662\u5bfa)\uac00 \uc788\uc74c. \uace8\uc9dc\uae30\ub9c8\ub2e4 \uc0ac\ucc30, \uc554\uc790 \ub4f1 \ub9ce\uc740 \ubd88\uad50\uc720\uc801\uc774 \uc0b0\uc7ac\ud574 \uc788\ub294 \ub4f1 \uc6b0\ub9ac\ub098\ub77c \ucd5c\uace0\uc758 \ubd88\uad50 \uc131\uc9c0\ub85c \uc720\uba85'), STRINGDECODE('\ud55c \uc2b9\ub824\uc758 \ud76c\uc0dd\uc73c\ub85c \uc18c\uc2e4\uc704\uae30\ub97c \ubaa8\uba74\ud55c \uc0c1\uc6d0\uc0ac'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc18c\uae08\uac15\uc9c0\uad6c\uc640 \uc6d4\uc815\uc0ac\uc9c0\uad6c \uc77c\ub300\ub97c \uc911\uc2ec\uc73c\ub85c \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. <BR>\uc57c\uc601\uc7a5\uc73c\ub85c\ub294 \uc6d4\uc815\uc0ac\uc9c0\uad6c\uc5d0 \ub3d9\ud53c\uace8\uc57c\uc601\uc7a5 \u260e033-332-6417, \uc18c\uae08\uac15\uc9c0\uad6c\uc5d0 \uc624\ud1a0\ucea0\ud551\uc7a5 \u260e033-661-4101\uc774 \uc788\ub2e4.<BR>\uc6d4\uc815\uc0ac\uc9c0\uad6c-\uc624\ub300\uc0b0\ud638\ud154 \u260e033-330-5000, \ud718\ub2c9\uc2a4\ud30c\ud06c \u260e033-332-6818, \uc18c\uae08\uac15\uc9c0\uad6c-\uc5b8\ub355\uc758 \uc9d1 \u260e033-661-4422, \ubb34\ub989\uc0b0\uc7a5 \u260e033-661-4132<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc624\ub300\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e033-332-6417, (\ud3c9\ucc3d\uc9c0\uc5ed)\ud3c9\ucc3d\uad6d\uc720\ub9bc\uad00\ub9ac\uc18c \u260e033-333-2275, \ud3c9\ucc3d\uad70\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e033-330-2752 \uc6a9\ud3c9\uba74\uc0ac\ubb34\uc18c \u260e033-330-2606 (\ud64d\ucc9c\uc9c0\uc5ed)\ud64d\ucc9c\uad70 \ub0b4\uba74\uc0ac\ubb34\uc18c \u260e033-432-6003'), STRINGDECODE('\uc6d4\uc815\uc0ac\uc9c0\uad6c\uc758 \uae30\uc810\uc740 \uc9c4\ubd80(\uc9c4\ubd80\uc2dc\ub0b4\ubc84\uc2a4\ud130\ubbf8\ub110 \u260e033-335-6963)\uc774\uace0 \uc18c\uae08\uac15\uc9c0\uad6c\ub294 \uac15\ub989(\uac15\ub989\uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110 \ub3d9\uc9c4\ubc84\uc2a4 \u260e033-653-8012, \ub3d9\ud574\uc0c1\uc0ac \u260e033-653-0320)\uc774\ub2e4. <BR>\uc9c4\uace0\uac1c\ub85c \ub2e4\ub2c8\ub294 \uc2dc\ub0b4?\uc2dc\uc678\ubc84\uc2a4\ub294 \ubaa8\ub450 \uc5c6\uc73c\ubbc0\ub85c \uc2b9\uc6a9\ucc28, \ud0dd\uc2dc\ub97c \uc774\uc6a9\ud574\uc57c \ud55c\ub2e4. \uc18c\uae08\uac15 \ubd84\uc18c\uc5d0\uc11c \uc9c4\uace0\uac1c\uae4c\uc9c0 \uc57d 35\ubd84 \uac78\ub9b0\ub2e4.<BR>- \uc9c4\ubd80\u2192\uc6d4\uc815\uc0ac : 1\uc2dc\uac04 \uac04\uaca9 \uc6b4\ud589<BR>- \uc9c4\ubd80\u2192\uc0c1\uc6d0\uc0ac : \uc6d4\uc815\uc0ac\ud589 \ubc84\uc2a4 \uc911 7:30~17:20 \uc2dc\uac04 \ubc84\uc2a4 \uc6b4\ud589<BR>- \uac15\ub989\u2192\uc18c\uae08\uac15 : 303\ubc88 \ubc84\uc2a4 \uc885\uc810 \ud558\ucc28'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(60, TIMESTAMP '2019-08-14 15:46:34.929', TIMESTAMP '2019-08-14 15:47:15.258', '42', STRINGDECODE('\uac15\uc6d0\ub3c4 \ucd98\ucc9c\uc2dc \ubd81\uc0b0\uba74\uacfc \ud654\ucc9c\uad70 \uac04\ub3d9\uba74 \uc0ac\uc774\uc5d0 \uc788\ub294 \uc624\ubd09\uc0b0\uc740 \ud574\ubc1c 779m\uc774\uace0 \ub2e4\uc12f\uac1c\uc758 \ubd09\uc6b0\ub9ac\uac00 \uc788\ub2e4\uace0 \ud574\uc11c \uc624\ubd09\uc0b0\uc774\ub77c\uace0 \ud55c\ub2e4. \uae30\uc554\uc808\ubcbd\uacfc \ub178\uc1a1\uc774 \uc5b4\uc6b8\ub824 \ud55c\ud3ed\uc758 \ub3d9\uc591\ud654 \uac19\uc740 \uc0b0\uc73c\ub85c \uc720\uc11c\uae4a\uc740 \uccad\ud3c9\uc0ac\uac00 \uc788\ub2e4. <BR><BR>\ubc30\ud6c4\ub839\uc5d0\uc11c \uc624\ub974\uac8c \ub418\ub294 \uccab\ubc88\uc9f8 \ubd09\uc6b0\ub9ac\uac00 1\ubd09(\ub098\ud55c\ubd09), \ub450\ubc88\uc9f8 \ubd09\uc6b0\ub9ac\uc778 \uad00\uc74c\ubd09, \uc138\ubc88\uc9f8 \ubd09\uc6b0\ub9ac\uc778 \ubb38\uc218\ubd09, \ub124\ubc88\uc9f8 \ubd09\uc6b0\ub9ac\uc778 \ubcf4\ud604\ubd09, \ub2e4\uc12f\ubc88\uc9f8 \ubd09\uc6b0\ub9ac\uc778 \uc815\uc0c1 \ube44\ub85c\ubd09\uc774 \uc788\ub2e4. \uc774\uc911 \uc81c5\ubd09\uc778 \uc815\uc0c1\uc5d0\uc11c \uccad\ud3c9\uc0ac \ubc29\uba74\uc73c\ub85c \ubed7\uc5b4\ub0b4\ub9b0 \uc554\ub989\uc774 \ud2b9\ud788 \ube7c\uc5b4\ub09c \ud48d\uad11\uc744 \uc9c0\ub154\ub2e4. \uc774 \uc554\ub989\uc744 \ub530\ub77c \uc18c\uc591\ud638\ub97c \ubc14\ub77c\ubcf4\uba70 \ub0b4\ub824\uac00\ub294 \uae38\uc774 \uc624\ubd09\uc0b0\ud589\uc758 \ubc31\ubbf8\ub77c \ud560 \uc218 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uccad\ud3c9\uc0b0\uc7a5-\uccad\ud3c9\uc0ac-\uc1e0\uc904\uc9c0\uc5ed-688\ubd09-\uc815\uc0c1-4\u00b73\u00b72\u00b71\ubd09-\uc11c\ub0a8\ub989 \uc0bc\uac70\ub9ac-\ubc30\ud6c4\ub839(\ucd1d 4\uc2dc\uac04 35\ubd84)<BR>\u2461 \ubc30\ud6c4\ub839-\uc11c\ub0a8\ub989 \uc0bc\uac70\ub9ac-\uc815\uc0c1-\ubc31\uce58\uace0\uac1c-\ubd80\uc6a9\uc0b0-\ubd80\uc6a9\uc0b0 \ub0a8\ub989 \uc548\ubd80-\ud558\ub298\uc18c\ubbfc\ubc15(\ucd1d 5\uc2dc\uac04 10\ubd84)<BR>\u2462 \ubc31\uce58\uace0\uac1c-\uac70\ubd81\ubc14\uc704-\uc815\uc0c1-4\u00b73\u00b72\u00b71\ubd09-\uc120\ub3d9\uacc4\uace1-\uccad\ud3c9\uc0ac-\uccad\ud3c9\uc0b0\uc7a5(\ucd1d 3\uc2dc\uac04 20\ubd84)'), '37.969455', '127.909239', STRINGDECODE('\uac15\uc6d0\ub3c4 \ucd98\ucc9c\uc2dc \ubd81\uc0b0\uba74, \ud654\ucc9c\uad70 \uac04\ub3d9\uba74'), 2213103900, 778, STRINGDECODE('\uc624\ubd09\uc0b0'), STRINGDECODE('&lt;\ubd09\ub9cc\ubbf8, \uacc4\uace1\ubbf8 \uc88b\uc740 \ub0a8\ud55c\uc758 \ub9c9\uc7a5&gt;<BR>\ubd09\ub9cc\ubbf8\uc640 \uacc4\uace1\ubbf8\uac00 \ub6f0\uc5b4\ub09c \ub370\ub2e4 \ub0a8\uc73c\ub85c \uc18c\uc591\ud638, \ubd81\uc73c\ub85c\ub294 \ud30c\ub85c\ud638\uac00 \ubcf4\uc774\ub294 \uba85\ub2f9\uc5d0 \uc790\ub9ac\uc7a1\uc558\ub2e4. \uace0\ub824\uc2dc\ub300 \uc870\uacbd\uc744 \uc790\ub791\ud55c\ub2e4\ub294 \uc808 \uccad\ud3c9\uc0ac\uc640 \ud68c\uc804\ubb38(\u56de\u8f49\u9580)\uc774\ub77c\ub294 \ud2b9\uc774\ud55c \uc774\ub984\uc758 \ubb38\ub3c4 \uc788\ub2e4. \uc774 \ubd81\ucabd\uc73c\ub85c\ub294 \uac08 \uc218 \uc788\ub294 \uc0b0\uc774 \uc5c6\ub294 \ub9c9\uc7a5, \ubd81\uc704 38\ub3c4\ub2e4. \uba85\uc0b0\uc758 \uc870\uac74\uc744 \ub450\ub8e8 \uac16\ucd98 \ub370\ub2e4 \ud638\uae30\uc2ec\uae4c\uc9c0 \uc790\uadf9\ud558\ub294 \ub300\uc0c1\uc9c0\uac00 \uc544\ub2d0 \uc218 \uc5c6\ub2e4.<BR><BR>\ucd98\ucc9c\uc5d0\uc11c \uc591\uad6c\ub85c \ud5a5\ud558\ub294 46\ubc88 \uad6d\ub3c4\uc0c1\uc758 \ubc30\ud6c4\ub839\uc744 \ub4e4\uba38\ub9ac\ub85c \ud558\uba74 \uc0b0\ud589 \ub610\ud55c \uc27d\ub2e4. \uadf8\ub7ec\uba74\uc11c\ub3c4 1\ubd09 \ub098\ud55c\ubd09, 2\ubd09 \uad00\uc74c\ubd09, 3\ubd09 \ubb38\uc218\ubd09, 4\ubd09 \ubcf4\ud604\ubd09, \uadf8\ub9ac\uace0 \uc815\uc0c1 \ube44\ub85c\ubd09\uc758 \ud575\uc2ec\ubd80\ub97c \ub193\uce58\uc9c0 \uc54a\ub294\ub2e4. \ub2e4\uc74c \ube44\uacbd\uc758 \uc120\ub3d9(\u4ed9\u6d1e)\uacc4\uace1\uc73c\ub85c \ud558\uc0b0, \uc544\ud649 \uac00\uc9c0 \uc18c\ub9ac\ub97c \ub0b8\ub2e4\ub294 \uad6c\uc131(\u4e5d\u8072)\ud3ed\ud3ec\uc640 \uccad\ud3c9\uc0ac\ub97c \uad6c\uacbd\ud558\uace0 \ubcf4\ubb3c 146\ud638\uc758 \ud68c\uc804\ubb38\uc774 \uc815\ub9d0 \ub3cc\uc544\uac00\ub294\uc9c0 \uc774\ub984\ub9cc \uadf8\ub7f0 \ubb38\uc778\uc9c0\ub97c \ud655\uc778\ud560 \uc218 \uc788\ub2e4. \ucd98\ucc9c\uc73c\ub85c \ub098\uc624\ub294 \uae38\uc5d0\ub294 \ub0b4\ub959\uc758 \ubc14\ub2e4 \uc18c\uc591\ud638\ub97c \uc120\uc0c1\uc720\ub78c\ud558\ub294 \uae08\uc0c1\ucca8\ud654\uae4c\uc9c0 \uc788\ub2e4. <BR><BR>1\ubd09\ubd80\ud130 5\ubd09\uae4c\uc9c0\uc758 \uc554\ubd09\ub989\ub9cc\uc774 \uc544\ub2c8\ub77c \ub0a8\ub989\ub3c4 \ubc14\uc704\ud22c\uc131\uc774\ub2e4. \uc704\ud5d8 \uad6c\uac04\uc5d0\ub294 \ucca0\uc8fc\uc640 \uc1e0\uc904\uc774 \ube48\ud2c8\uc5c6\uc774 \uc124\uce58\ub3fc\uc788\uc9c0\ub9cc \ub178\uc57d\uc790\uc640 \ub3d9\ud589\ud558\ub294 \uac83\uc740 \uae08\ubb3c\uc774\ub2e4. \ub208\uc774 \uc624\ub294 \uaca8\uc6b8\uc5d0\ub294 \ub354\ub354\uc6b1 \uadf8\ub807\ub2e4. <BR><BR>\uc090\uc8fd\uc090\uc8fd \ub2e4\uc12f \ubd09\uc6b0\ub9ac\uac00 \uc19f\uc544\uc788\ub2e4\uace0 \ud574\uc11c \uc624\ubd09\uc0b0\uc778\ub370 \ubcf8\ub798\ub294 \uc774 \uc774\ub984\uc774 \uc544\ub2c8\uc5c8\ub2e4. \uc61b\uc774\ub984\uc740 \uacbd\uc6b4\uc0b0(\u6176\u96f2\u5c71)\uc774\uc5c8\ub2e4.'), STRINGDECODE('\uc0b0\uc138\ub294 \ud06c\uc9c0 \uc54a\uc73c\ub098 \ubc14\uc704\uc640 \uc218\ubaa9\uc774 \uc5b4\uc6b0\ub7ec\uc9c4 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b4 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ub2e4\uc12f \uac1c\uc758 \ubc14\uc704 \ubd09\uc774 \uc5f0\uc774\uc5b4 \uc19f\uc544\uc788\ub294 \ub370\uc11c \uc0b0\uc774\ub984\uc774 \uc720\ub798. \uc2e0\ub77c\ub54c \uc544\ub3c4\ud654\uc0c1\uc774 \ucc3d\uac74\ud558\uc600\ub2e4\uace0 \uc804\ud574\uc9c0\ub294 \uccad\ud3c9\uc0ac(\u6df8\u5e73\u5bfa)\uc640 \uad6c\uc131\ud3ed\ud3ec\uac00 \uc720\uba85. \uccad\ud3c9\uc0ac \uacbd\ub0b4\uc5d0 \uc788\ub294 \ubcf4\ubb3c \uc81c164\ud638\uc778 \ud68c\uc804\ubb38\uc774 \uc720\uba85'), STRINGDECODE('\ub3d9\uc591\ud654\uc18d\uc5d0 \ub2e4\uc12f \uac1c \ubc14\uc704'), STRINGDECODE('\u25cf\uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uccad\ud3c9\uc0ac \uc785\uad6c\uc5d0 \uc2dd\uc0ac\uac00 \uac00\ub2a5\ud55c \ubbfc\ubc15\uc9d1\uc774 \uc990\ube44\ud558\ub2e4. <BR>\uccad\ud3c9\uc0b0\uc7a5\u260e033-244-0580, \uc624\ubd09\uc0b0\uc7a5\u260e033-244-6606 \uace0\ub824\uc0b0\uc7a5\u260e033-243-1188 \ubd80\uc6a9\uac00\ub4e0\u260e033-244-5662 \uc1a1\ud559\uac00\ub4e0\u260e011-9799-1561 \ud558\ub298\uc18c\ubbfc\ubc15\u260e033-254-3494 <BR><BR><BR><BR>\u25cf\uc774\uc6a9\ubb38\uc758<BR>\ucd98\ucc9c\uc2dc\uccad \uad00\uad11\uc9c4\ud765\uacfc \u260e033-250-3545<BR>\uc18c\uc591\ud638 \uad00\ub9ac\uc0ac\ubb34\uc18c\u260e033-241-9251'), STRINGDECODE('\uae30\uc810\uc740 \ucd98\ucc9c\uc774\ub2e4. \uc2dc\ub0b4\uc5d0\uc11c \uc18c\uc591\ub310\ud589 \uc77c\ubc18\ubc84\uc2a4(11\ubc88)\uc640 \uc88c\uc11d\ubc84\uc2a4(12-1\ubc88)\uac00 30\ubd84 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2c8\uba70 \uccad\ud3c9\uc0ac\ud589 \uc120\ud3b8\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\ub2e4. \uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110\uc5d0\uc11c\ub294 \ubc30\ud6c4\ub839 \uac00\ub294 \ubc84\uc2a4\uac00 14\ud68c \uc788\ub2e4. \ucd98\ucc9c\ud589 \uc9c1\ud589\uc740 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 15\ubd84 \uac04\uaca9, \uc0c1\ubd09\ud130\ubbf8\ub110\uc5d0\uc11c 47\ud68c, \ubd80\uc0b0\uc885\ud569\ud130\ubbf8\ub110\uc5d0\uc11c 14\ud68c \uc788\uace0 \uacbd\ucd98\uc120 \uc5f4\ucc28\ub3c4 20\ud68c \ub2e4\ub2cc\ub2e4. \uac15\uc6d0\ub3c4 \uc774\uc678 \uc9c0\uc5ed\uc73c\ub85c\ub294 \ub300\uad6c, \uc6b8\uc0b0, \ud3ec\ud56d, \uc548\ub3d9, \uc804\uc8fc, \ub300\uc804, \uc628\uc591, \ucc9c\uc548, \uccad\uc8fc, \uc131\ub0a8, \uc218\uc6d0, \uc548\uc0b0, \uc778\ucc9c, \uace0\uc591, \uc758\uc815\ubd80\uc5d0\uc11c \ubc84\uc2a4\uac00 \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(61, TIMESTAMP '2019-08-14 15:46:34.93', TIMESTAMP '2019-08-14 15:47:15.871', '41', STRINGDECODE('\uc6a9\ubb38\uc0b0\uc740 \uacbd\uae30\ub3c4\uc5d0\uc11c \ub124 \ubc88\uc9f8\ub85c \ub192\uc740 \uc0b0\uc73c\ub85c \uae30\uc554\uad34\uc11d\uacfc \uace0\uc0b0\uc900\ub839\uc744 \uace0\ub8e8 \uac16\ucd94\uace0 \uc788\ub294 \uc0b0\uc774\ub2e4. \ubcf8\ub514 \ubbf8\uc9c0\uc0b0\uc774\ub77c\ub294 \uc774\ub984\uc73c\ub85c \ubd88\ub9ac\uc5c8\ub294\ub370, \uc870\uc120\uc744 \uac1c\uad6d\ud558\uace0 \uc774\ud0dc\uc870\uac00 \ub4f1\uadf9\ud558\uba74\uc11c ''\uc6a9\ubb38\uc0b0''\uc774\ub77c \ubc14\uafd4 \ubd80\ub974\uac8c \ub418\uc5c8\ub2e4\uace0 \ud55c\ub2e4. <BR><BR>\uc815\uc0c1\uc740 \uc785\uc0b0\ud1b5\uc81c \uc9c0\uc5ed\uc73c\ub85c \uc6b0\ud68c\ud558\ub294 \ub4f1\uc0b0\ub85c\uac00 \uc774\uc6a9\ub418\uace0 \uc788\ub2e4. \uc2e0\ub77c \uc120\ub355\uc5ec\uc655\ub54c \ucc3d\uac74\ud55c \uc6a9\ubb38\uc0ac \uc0ac\ucc30\uc55e\uc5d0\ub294 \ub192\uc774 62m, \ub458\ub808 14m\uc5d0 \ub2ec\ud558\ub294 \uc740\ud589\ub098\ubb34(\ucc9c\uc5f0\uae30\ub150\uc6b8 \uc81c 30\ud638)\uac00 \ubc1c\uac78\uc74c\uc744 \uba48\ucd94\uac8c \ud55c\ub2e4.'), STRINGDECODE('\u2460 \uc6a9\ubb38\uc0ac-\uc808\uace0\uac1c-\uc0c1\uc6d0\uc0ac-1064\ubd09-\uad6c\ub984\uc7ac-\ubc31\uc6b4\ubd09-\uc5f0\uc548\ub9c8\uc744 (\ucd1d 4\uc2dc\uac04 40\ubd84)<BR>\u2461 \uc6a9\ubb38\uc0ac-\uc911\uc6d0\uc0b0-\ub2e8\uc6d4\ubd09-\ubb38\ub840\ubd09-\ubb38\ub840\uc7ac-\uc6a9\ubb38\uc0ac (\ucd1d 7\uc2dc\uac04 25\ubd84)'), '37.518209', '127.456154', STRINGDECODE('\uacbd\uae30\ub3c4 \uc591\ud3c9\uad70 \uc6a9\ubb38\uba74\u318d\uc625\ucc9c\uba74'), 2130103000, 1157, STRINGDECODE('\uc6a9\ubb38\uc0b0'), STRINGDECODE('&lt;\ud480\uaf43 \ucc9c\uc9c0 \uc228\uae30\uace0\uc788\ub294 \ub450\ub8e8\ubb49\uc2e4\ud55c \uac70\uc0b0&gt;<BR>\uc6a9\ubb38\uc0b0\uc740 \uacbd\uc778\uc9c0\uc5ed\uc5d0\uc11c \uc124\uc545\uc0b0 \uac08 \ub54c \ubcf4\uac8c\ub418\ub294 \ud070 \uc0b0\uc774\ub2e4. 44\ubc88 \uad6d\ub3c4\ubcc0\uc5d0\ub294 \uc774\ubcf4\ub2e4 \ub354 \ub192\uc740 \uc0b0\ub3c4 \ub9ce\uc9c0\ub9cc \ub300\uac1c\ub294 \uace8\uc9dc\uae30\ub97c \ub530\ub77c \uc774\uc5b4\uc9c4 \ub3c4\ub85c\uc5d0\uc11c \uc62c\ub824\ub2e4\ubcf4\uac8c \ub418\uc5b4\uc788\uc5b4 \uaddc\ubaa8\ub97c \ub290\ub084 \uc218 \uc5c6\ub2e4. \uadf8\ub7ec\ub098 \uc6a9\ubb38\uc0b0\uc740 \ube44\uc0b0\ube44\uc57c\uc758 \uc6a9\ubb38\uba74 \ubd81\ucabd \ub05d\uc5d0 \ubcd1\ud48d\ucc98\ub7fc \uc19f\uc544\uc788\ub294 \uae4c\ub2ed\uc5d0 \uc704\uc6a9\uc774 \uc27d\uc0ac\ub9ac \uc7a1\ud78c\ub2e4. <BR><BR>\ud558\uc9c0\ub9cc \uc0c1\ubd09\uc740 \ubc14\ub78c\uc744 \ub123\uc5b4 \ubd80\ud480\ub9b0 \uc5ec\uc758\ub3c4 \uc885\ud569\uc804\uc2dc\uc7a5\ucc98\ub7fc \ub450\ub8e8\ubb49\uc2e4, \uc7a5\uc5c4\ud55c \ub290\ub08c\ubc16\uc5d0 \uc5c6\ub2e4. \ubc18\uba74 \ubfb0\uc871\ud55c \uc0bc\uac01\ubfd4\uc774 \ud558\ub298\uc744 \ucc0c\ub974\ub294 \ub0a8\ub989\uc758 \ubc31\uc6b4\ubd09\uc740 \ub450\uace0\ub450\uace0 \uc9c0\uc6cc\uc9c0\uc9c0 \uc54a\ub294 \uc778\uc0c1\uc744 \ub0a8\uae34\ub2e4. \uac00\uc744\uc774\ub098 \uaca8\uc6b8 \uc774\ub978 \uc0c8\ubcbd\uc5d0 \ubb3c\uc548\uac1c \uc704\ub85c \ubcf4\uc774\ub294 \uadf8 \ubaa8\uc2b5\uc740 \uc815\ub9d0 \uc18c\uc2ac\ud558\ub2e4. <BR><BR>\uac00\ud30c\ub978 \ubc14\uc704\ud22c\uc131\uc774 \ub0a8\uba74\uacfc \ub2ec\ub9ac \ubd80\ub4dc\ub7ec\uc6b4 \uc721\uc0b0\uc744 \uc774\ub8e8\ub294 \ubd81\uba74\uc740 \ud480\uaf43 \ucc9c\uc9c0\ub2e4. \uc774\ub978 \ubd04\uc758 \uc5bc\ub808\uc9c0, 5\uc6d4\uc758 \ud53c\ub098\ubb3c \uaf43\ubc2d\uc744 \uc778\uc801 \uc5c6\ub294 \ud638\uc813\ud568 \uc18d\uc5d0 \ub9cc\ub07d\ud560 \uc218 \uc788\ub2e4. \uc774\ub294 \uad6d\uac00\uc2dc\uc124\ubb3c \ub54c\ubb38\uc5d0 \uc815\uc0c1\uc5d0 \uc11c\ubcf4\uc9c0 \ubabb\ud55c \uc544\uc26c\uc6c0\uc744 \ubc8c\ucda9\ud558\uace0\ub3c4 \ub0a8\ub294\ub2e4. <BR>\uac00\uc744\uc5d0\ub294 \uc6a9\ubb38\uc0ac\uc758 \uc740\ud589\ub098\ubb34\uac00 \ubcfc\uac70\ub9ac\ub2e4. \uc218\ub9ce\uc740 \ucc9c\ub144\uc218(\u5343\u5e74\u6a39)\ub4e4\uc774 \uac70\uc758 ''\ub9c8\uc774\uac00\ub9ac''\uc9c0\ub9cc \uc774\uac74 \uc9c4\uc9dc\ub2e4.'), STRINGDECODE('\uc61b\ubd80\ud130 \uacbd\uae30\uc758 \uae08\uac15\uc0b0\uc774\ub77c \ubd88\ub9ac\uc6cc\uc9c8 \ub9cc\ud07c \uae30\uc554\uad34\uc11d\uacfc \uace0\uc0b0\uc900\ub839\uc744 \uace0\ub8e8 \uac16\ucd98 \uacbd\uad00\uc774 \ub6f0\uc5b4\ub09c \uc0b0\uc774\uba70, \ud2b9\ud788 \uc2e0\ub77c \uc120\ub355\uc5ec\uc655\ub54c \ucc3d\uac74\ud55c \uc6a9\ubb38\uc0ac\uc640 \ub192\uc774 62m, \ub458\ub808 14m\uc5d0 \ub2ec\ud558\ub294 \uc740\ud589\ub098\ubb34(\ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c30\ud638)\uac00 \uc788\ub294 \ub4f1 \uc5ed\uc0ac, \ubb38\ud654\uc801 \uac00\uce58\uac00 \ub192\uc740 \uc810\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uacbd\uae30\ub3c4\uc5d0\uc11c \ub124 \ubc88\uc9f8\ub85c \ub192\uc740 \uc0b0\uc73c\ub85c \ubbf8\uc9c0\uc0b0\uc774\ub77c\ub294 \uc774\ub984\uc73c\ub85c \ubd88\ub9ac\uc5c8\ub294\ub370 \uc870\uc120\uc744 \uac1c\uad6d\ud55c \uc774\ud0dc\uc870\uac00 \ub4f1\uadf9\ud558\uba74\uc11c `\uc6a9\ubb38\uc0b0\"\uc774\ub77c \ubc14\uafd4 \ubd80\ub974\uac8c \ub418\uc5c8\ub2e4\ub294 \uc804\uc124\uc774 \uc788\uc74c'), STRINGDECODE('\uc870\uc120 \uac1c\uad6d\uc73c\ub85c \ub2e4\uc2dc \ud0dc\uc5b4\ub09c \ubbf8\uc9c0\uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\ucd5c\uadfc \uc591\ud3c9\uad70\uc5d0\uc11c \uc9c1\uc601\ud558\ub294 \uc6a9\ubb38\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc591\ud3c9\uc74d \ubc31\uc548\ub9ac \uc0bf\uac13\ubd09 \uc544\ub798 \uac1c\uc7a5\ub410\ub2e4. \ud1b5\ub098\ubb34\uc9d1\uacfc \uc57c\uc601\uc7a5, \ub4f1\uc0b0\ub85c\uc640 \uc0b0\ucc45\ub85c\ub97c \uac16\ucdc4\ub2e4. <BR>\uc608\uc57d\ubb38\uc758 \uc0b0\ub9bc\uacfc \u260e031-775-4005<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc591\ud3c9\uad70 \ubb38\ud654\uad00\uad11\uacfc \u260e031-770-2710'), STRINGDECODE('\ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uacfc \uc0c1\ubd09\ud130\ubbf8\ub110\uc5d0\uc11c \uc6a9\ubb38\uc0ac\ud589 \ubc84\uc2a4\uac00 \ud558\ub8e8 2\ud68c\uc529 \uc788\ub2e4. <BR>\uac00\uc77c\ub9ac\ucabd\uc740 \uc0c1\ubd09\ud130\ubbf8\ub110\uc5d0\uc11c 8\ud68c \uc788\ub294 \uc720\uba85\uc0b0\ud589\uc744 \uc774\uc6a9\ud55c\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(62, TIMESTAMP '2019-08-14 15:46:34.93', TIMESTAMP '2019-08-14 15:47:16.475', '42', STRINGDECODE('\ud574\ubc1c 878m\uc758 \uc6a9\ud654\uc0b0\uc740 \uac04\ub3d9\uba74 \uc720\ucd0c\ub9ac, \ud558\ub0a8\uba74 \uc0bc\ud654\ub9ac\uc640 \uac70\ub840\ub9ac, \ucd98\ucc9c\uc2dc \uc0ac\ubd81\uba74 \uace0\ud0c4\ub9ac\uc5d0 \uac78\uccd0\uc788\ub2e4. \ubd81\ucabd\uc73c\ub85c\ub294 \ud30c\ub85c\ud638\ub97c, \uc11c\ucabd\uc73c\ub85c\ub294 \ucd98\ucc9c\ud638\ub97c, \ub0a8\ucabd\uc73c\ub85c\ub294 \uc18c\uc591\ud638\uc758 \uc911\uc2ec\uc5d0 \uc704\uce58\ud55c \uc0b0\uc774\ub2e4. <br><br>\uc6a9\ud654\uc0b0 \uc900\ub839\uacfc \uc624\ubd09\uc0b0 \uc0ac\uc774\uc5d0 \uc131\ubd88\ub839\uc774\ub77c\ub294 \uace0\uac1c\uac00 \uc788\uace0 \uc5ec\uae30\uc5d0 \uc131\ubd88\uc0ac \ud130\uac00 \uc788\ub2e4. \uc61b\ubd80\ud130 \uc131\ubd88\uc0ac \uc800\ub141 \uc885\uc18c\ub9ac\uc640 \uc6a9\ud654\uc0b0 \uc548\uac1c\uc640 \uad6c\ub984, \uae30\uad34\ud55c \ub3cc, \uc6d0\ucc9c\ub9ac \uacc4\uace1\uc758 \ub9d1\uc740 \ubb3c, \ubd80\uc6a9\uc0b0\uc758 \ubc1d\uc740 \ub2ec, \uc8fd\uc5fd\uc0b0\uc758 \ub2e8\ud48d, \uad6c\uc6b4\uc18c\uc758 \ubb3c\uace0\uae30 \ub4f1\uc744 \uc61b\ubd80\ud130 \ud314\uacbd\uc774\ub77c \ubd88\ub800\ub2e4. \uc0b0\uc5d0\ub294 \uad11\ubc14\uc704, \uc2ec\ubc14\uc704, \uaf2d\uc9c0\ubc14\uc704, \uc8fc\uc804\uc790\ubc14\uc704, \ub9c8\uadc0\ud560\ubbf8\ubc14\uc704, \ubc14\ub451\ubc14\uc704 \ub4f1 \uc2e4\ubb3c\uc744 \ubc29\ubd88\ucf00\ud558\ub294 \uae30\uc554\uad34\uc11d\uc774 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc591\ud1b5\ub9c8\uc744-\ud070\uace0\uac1c-\uc815\uc0c1-858\ubd09-\uae54\ub531\uace0\uac1c-\uc591\ud1b5 (\ucd1d 4\uc2dc\uac04 20\ubd84)<br>\u2461 \uc0bc\ud654\ub9ac \uace0\uac1c \uc815\uc0c1-\uc6a9\ud654\uc0b0 \uc815\uc0c1-\uc131\ubd88\uce58-\ud30c\ub85c\ud638 (\ucd1d 4\uc2dc\uac04)'), '38.063713', '127.818375', STRINGDECODE('\uac15\uc6d0\ub3c4 \ud654\ucc9c\uad70 \uac04\ub3d9\uba74\u318d\ud558\ub0a8\uba74, \ucd98\ucc9c\uc2dc \uc0ac\ubd81\uba74'), 2217011600, 878, STRINGDECODE('\uc6a9\ud654\uc0b0'), STRINGDECODE('&lt;\ud638\uc218\uc5d0 \ud3ec\uc704\ub41c \ucc9c\ud61c\uc758 \ubc14\uc704 \uc694\uc0c8 &gt;<br>\uc6a9\ud654\uc0b0\uc740 \uac15\uc6d0\ub3c4 \ucd98\ucc9c\uc2dc \uc0ac\ubd81\uba74\uacfc \ud654\ucc9c\uad70 \ud558\ub0a8\uba74 \uacbd\uacc4\uc5d0 \uc19f\uc740 \ubc14\uc704\uc0b0\uc774\uc790 \ucd98\ucc9c\uc758 \uc758\uc554\ub310, \uc18c\uc591\ub310, \ucd98\ucc9c\ub310, \ud654\ucc9c\ub310\uc5d0 \ud3ec\uc704\ub41c \ucc9c\ud61c\uc758 \uc131\ubcbd\uc774\ub2e4. \ubcf4\ud1b5 \ub4e4\uba38\ub9ac\ub85c \uc7a1\ub294 \uace0\uc1312\ub9ac \uc591\ud1b5\uace8\ub85c \uc6a9\ud654\uc0b0\uc5d0 \ub4e4\uc5b4\uc11c\uba74 \uac00\uc7a5 \uba3c\uc800 \ubc14\uc704\ubd09\uc6b0\ub9ac\ub4e4\uc774 \ub208\uc5d0 \ub4e4\uc5b4\uc628\ub2e4. \ucd98\ucc9c\uc2dc \ubc29\uba74\uc73c\ub85c\ub294 \uae4e\uc544\uc9c0\ub978 \ubc14\uc704 \uc808\ubcbd\uc744 \uc774\ub8e8\uace0 \uc788\ub294\ub370, \u2018\uc0c8\uac00 \ub09c\ub2e4\u2019\ud558\uc5ec \uc774\ub984 \ubd99\uc5ec\uc9c4 \uc0c8\ub0a8\ubc14\uc6b0\ub97c \ube44\ub86f\ud574 \ud558\ub298\ubcbd \ucd1b\ub300\ubc14\uc704, \uce35\uce35\ubc14\uc704, \ub4dd\ub0a8\ubc14\uc704 \ub4f1\uc758 \uae30\uc554\uacfc \ud568\uaed8 \uc6a9\ud654\uc0b0 \uc815\uc0c1\uc5d0\uc11c \uc11c\ucabd\uc73c\ub85c \ud758\ub7ec\ub0b4\ub9ac\ub294 \ub9cc\uc7a5\ubd09 \uc77c\uc6d0\uc758 \uc554\uace8\ubbf8\ub294 \uc6a9\ud654\uc0b0\uc758 \ubc31\ubbf8\ub77c \ud560 \uc218 \uc788\ub2e4. \ud2b9\ud788 \ub178\uc1a1\uad70\ub77d\uc744 \ud488\uace0 \uc788\ub294 \ub9cc\uc7a5\ubd09 \ub108\ub7ed\ubc14\uc704\uc5d0\uc11c \ubc14\ub77c\ubcf4\ub294 \ud558\ub298\ubcbd \ucd1b\ub300\ubc14\uc704\uc758 \uacbd\uce58\uac00 \uc77c\ud488\uc774\ub2e4. \ub9cc\uc7a5\ubd09\uc5d0\ub294 1976\ub144\ub3c4\uc5d0 \uc554\ubcbd\ub4f1\ubc18 \ucf54\uc2a4\uac00 \uac1c\ucc99\ub418\uc5b4 \uc9c0\uae08\ub3c4 \ub4f1\ubc18\uac00\ub4e4\uc774 \uc885\uc885 \ucc3e\uace0 \uc788\ub2e4. <br><br>\ub9cc\uc7a5\ubd09\uacfc \uc815\uc0c1 \uc0ac\uc774\uc758 \ubc14\uc704\ub2a5\uc120\uc5d0\uc11c\ub294 \ucd98\ucc9c\uc758 \uc0b0\uad70\uc774 \uc798 \ubcf4\uc774\ub294\ub370, \uc815\uc0c1\uc5d0 \ub2e4\ub2e4\ub97c \uc988\uc74c\uc774\uba74 \ub300\ub8e1\uc0b0(899.3m)\uacfc \uc0bc\uc545\uc0b0(655.8m) \uc815\uc0c1\uc774 \ubcf4\uc774\ub294 \uac83\uc740 \ubb3c\ub860 \uc0bc\uc545\uc0b0 \ubd81\uc11c\ucabd\uc73c\ub85c \ubed7\uc740 \ubd81\ubc30\uc0b0, \uac00\ub355\uc0b0, \uc11c\ucabd\uc758 \uc0bf\uac13\ubd09\uacfc \uc751\ubd09\uc774 \ubcf4\uc778\ub2e4. \uc774\ubc16\uc5d0\ub3c4 \uc6a9\ud654\uc0b0\uc5d0\ub294 \uc61b\ub0a0 \ucd98\ucc9c\uc9c0\ubc29\uc5d0 \uc788\ub358 \uace0\ub300\uad6d\uac00 \ub9e5\uad6d(\u8c8a\u570b)\uc758 \uc131\ubb38\uc5ed\ud560\uc744 \ud558\ub358 \ubc30\ud6c4\ub839, \uc131\ubd88\ub839, \uc0ac\uc57c\ub839, \ud070\uace0\uac1c, \ubaa8\ub798\uc7ac \ub4f1\uc758 \uace0\uac2f\uae38 10\uc5ec \uacf3\uc758 \ud754\uc801\uc774 \ub0a8\uc544\uc788\ub2e4.'), STRINGDECODE('\ud30c\ub85c\ud638, \ucd98\ucc9c\ud638, \uc18c\uc591\ud638 \ub4f1\uacfc \uc5f0\uc811\ud574 \uc788\uc73c\uba70 \uc0b0\ub9bc\uacfc \uae30\uc554\uad34\uc11d\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70 \uc870\ub9dd\uc774 \uc88b\uc740 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc131\ubd88\uc0ac\ud130\uac00 \uc788\uc73c\uba70 \uad11\ubc14\uc704, \uc8fc\uc804\uc790\ubc14\uc704, \ubc14\ub451\ubc14\uc704 \ub4f1 \uac16\uac00\uc9c0 \uc804\uc124\uc744 \uac04\uc9c1\ud55c \uc2e4\ubb3c\uc744 \ub2ee\uc740 \ubc14\uc704\uac00 \uc720\uba85'), STRINGDECODE('\uc774\uc57c\uae30\uac00 \uc788\ub294 \ubc14\uc704 \uc804\uc2dc\uc7a5'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<br>\uc591\ud1b5\ub9c8\uc744\uc5d0\ub294 \uc219\ubc15\uc2dc\uc124\uc774 \uc5c6\ub2e4. \ucd98\ucc9c\uc774\ub098 \ud654\ucc9c\uc758 \uc2dc\uc124\uc744 \uc774\uc6a9\ud574\uc57c \ud55c\ub2e4.<br><br>\u25cf \uc774\uc6a9\ubb38\uc758<br>\ud654\ucc9c\uad70\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e033-440-2543'), STRINGDECODE('\ucd98\ucc9c\uc774 \uae30\uc810\uc774\ub2e4. <br>\ubc84\uc2a4 \uc6b4\ud58