INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(63, TIMESTAMP '2019-08-14 15:46:34.931', TIMESTAMP '2019-08-14 15:47:17.061', '47', STRINGDECODE('\ub3d9\uc73c\ub85c \uac00\uc9c0\uc0b0\uacfc \uc774\uc5b4\uc838 \uc788\ub294 \uc6b4\ubb38\uc0b0\uc740 \uc0b0\uc138\uac00 \uc6c5\uc7a5\ud558\uba70 \ub098\ubb34\ub4e4\uc774 \uc6b8\ucc3d\ud558\uc5ec \ub4f1\uc0b0\uac1d\uc774 \ub9ce\uc774 \ucc3e\ub294 \uc0b0\uc774\ub2e4. \uc774 \uacf3\uc5d0\ub294 \uc6b4\ubb38\uc0ac\ub97c \ube44\ub86f\ud55c \ud06c\uace0 \uc791\uc740 \uc808\uacfc \uc554\uc790\uac00 \uc788\uace0, \uc8fc\ubcc0 \uacbd\uce58\uac00 \ub9e4\uc6b0 \uc544\ub984\ub2f5\ub2e4.'), STRINGDECODE('\u2460 \uc6d0\uc11c\ub9ac-\uc11d\uace8\uc0ac-\uc11c\ubd81\ub989-\uc815\uc0c1-\ub531\ubc2d\uc7ac-\ubc94\ubd09-\ud314\ud48d\uc7ac-\uc5b5\uc0b0-\uc11d\uace8\uc0ac-\uc6d0\uc11c\ub9ac(\ucd1d 6\uc2dc\uac04 30\ubd84)<BR>\u2461 \uc6d0\uc11c\ub9ac-\uc11d\uace8\uc0ac-\ub300\ube44\uace8-\ud314\ud48d\uc7ac-\ubc94\ubd09-\ub531\ubc2d\uc7ac-\uc815\uc0c1-\uc544\ub7ab\uc7ac-\ub0a8\uba85\ub9ac(\ucd1d 6\uc2dc\uac0410\ubd84)<BR>\u2462 \ud638\ubc15\uc18c-\uad6c\ub8e1\uc18c\ud3ed\ud3ec-\uac00\uc9c0\uc0b0 \uc11c\ub0a8\ub989-\uc544\ub7ab\uc7ac-\uc815\uc0c1-\uc0c1\uc6b4\uc554-\uc11d\uace8\uc0ac-\uc6d0\uc11c\ub9ac(\ucd1d 6\uc2dc\uac04)'), '35.71144', '128.975286', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uccad\ub3c4\uad70 \uc6b4\ubb38\uba74, \uacbd\uc0c1\ub0a8\ub3c4 \ubc00\uc591\uc2dc \uc0b0\ub0b4\uba74'), 2720052300, 1195, STRINGDECODE('\uc6b4\ubb38\uc0b0'), STRINGDECODE('&lt;\uc601\ub0a8\uc54c\ud504\uc2a4 \uc804\ub9dd\uc774 \uc77c\ud488\uc778 \uc6b4\ubb38\uc0ac\uc758 \uc0b0&gt;<BR>\uac70\uc6b8\uc5d0 \ube44\uce5c \u4e0b\uc790\ud615 \uc0b0\uc904\uae30 \uc601\ub0a8\uc54c\ud504\uc2a4\uc758 \uc717\uae08 \uc11c\ucabd\ub05d\uc744 \uc774\ub8e8\ub294 \uc0b0\uc774\ub2e4. \uac70\ub300\ud55c \ubd09\ubd84 \uac19\uc740 \ub2e8\uc21c\ud55c \ubaa8\uc591\uc0c8\uc774\uc9c0\ub9cc \uc5b5\uc0b0\uacfc \uac00\uc9c0\uc0b0\uc758 \uc88c\uc6b0 \uc900\ubd09\uc5d0\uc11c \ubd81\uc73c\ub85c \ubed7\uc5