INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(64, TIMESTAMP '2019-08-14 15:46:34.931', TIMESTAMP '2019-08-14 15:47:17.646', '41', STRINGDECODE('\uccad\ud3c9\uc5d0\uc11c \ubd81\ub3d9\ucabd\uc73c\ub85c \uc57d 20km \ub5a8\uc5b4\uc9c4 \uc6b4\uc545\uc0b0\uc740 \uc77c\uba85 \uacbd\uae30 \uc18c\uae08\uac15\uc774\ub77c\uace0 \ubd88\ub9ac\uc6b8 \ub9cc\ud07c \ub6f0\uc5b4\ub09c \ubaa8\uc2b5\uc744 \uc9c0\ub2c8\uace0 \uc788\ub2e4. \uc8fc\ubd09\uc778 \ub9dd\uacbd\ub300\ub97c \ub458\ub7ec\uc2f8\uace0 \ucee4\ub2e4\ub780 \ubc14\uc704\ub4e4\uc774 \ubd09\uc6b0\ub9ac\ub9c8\ub2e4 \uad6c\ub984\uc744 \ub6ab\uace0 \uc19f\uc544 \uc788\uc73c\uba70, \uc624\ub798\ub41c \uc808 \ud604\ub4f1\uc0ac\uc640 \ubb3c \ub9d1\uc740 \uc870\uc885\ucc9c\uc740 \ub9e4\ub825\uc744 \ud55c\uce35 \ub354\ud574\uc900\ub2e4.'), STRINGDECODE('\u2460 \uc6b4\uc8fc\uc0ac-\uc2e0\uc120\ub300-\uc560\uae30\ubc14\uc704-\uc815\uc0c1-\uc808\uace0\uac1c-\ud604\ub4f1\uc0ac-\uc11d\uac70\ub9ac (\ucd1d 4\uc2dc\uac04 20\ubd84)'), '37.831325', '127.509571', STRINGDECODE('\uacbd\uae30\ub3c4 \uac00\ud3c9\uad70 \ud558\uba74, \ud3ec\ucc9c\uc2dc \ud654\ud604\uba74'), 2101062700, 935, STRINGDECODE('\uc6b4\uc545\uc0b0'), STRINGDECODE('&lt;\uc870\uc885\ud604 \uc9c4\uc0b0\uc774\ub358 \uacbd\uae30\uc758 \uae08\uac15&gt;<BR>\uacbd\uae305\uc545\uc758 \ud558\ub098\uc778 \uc6b4\uc545\uc0b0\uc740 \uc774\ub984\uc5d0 \ub4dc\ub7ec\ub098 \uc788\ub4ef \ubc14\uc704\uac00 \ub9ce\uc740 \uba85\uc0b0\uc774\ub2e4. \uc0b0\ubd09\uacfc \ub2a5\uc120\uc774 \uc815\uc0c1\uc73c\ub85c \uc218\ub834\ud558\ub294 \uc0b0\uc758 \uad6c\ub3c4\ub3c4 \uc88b\uace0 \ubc14\uc704\uc640 \uac00\uc744 \ub2e8\ud48d\uc774 \uc5b4\uc6b0\ub7ec\uc9c4 \ubaa8\uc591\ub3c4 \uc544\ub984\ub2f5\ub2e4. \uadf8\ub798\uc11c ''\uacbd\uae30\uc758 \uae08\uac15''\uc73c\ub85c \ubd88\ub9b0\ub2e4. \ub3d9\ucabd \uae30\uc2ad\uc5d0\ub294 \ubcf4\uc870\uad6d\uc0ac \uc9c0\ub20c\uc758 \uc0ac\ub9ac\ud0d1\uc774 \uc788\ub294 \ud604\ub4f1\uc0ac\uac00 \uc790\ub9ac\ud588\ub2e4. \uc11c\ucabd\uc5d0\ub294 \uc218\uc9c1\uc758 \uc18c\uaf2c\ub9ac\ud3ed\ud3ec, 30m \uae38\uc774\uc758 \ubb34\uc9c0\uac1c\ud3ed\ud3ec\uac00 \uc788\uc5b4 \ube59\ubcbd\ub4f1\ubc18\uc744 \uc990\uae30\ub294 \uc0b0\uc545\uc778\ub4e4\uc774 \uc990\uaca8 \ucc3e\ub294\ub2e4. <BR><BR>\ud55c\ubd81\uc815\ub9e5\uc758 \ub9c8\uc9c0\ub9c9 900m\uae09\uc73c\ub85c \uc774 \uc11c\ub0a8\ucabd\uc73c\ub85c\ub294 \uae30\uaecf\ud574\uc57c 600m\uae09\uc758 \uc8fd\uc5fd\uc0b0, \uacc4\uba85\uc0b0\uc774 \uc788\uc744 \ubfd0\uc774\ub2e4(\uc0b0\uacbd\ud45c\uac00 \uc544\ub2c8\ub77c \ub300\ub3d9\uc5ec\uc9c0\ub3c4\ub97c \ub530\ub97c \uacbd\uc6b0\ub2e4). \uc544\ub798\ucabd \uacbd\uae30\ub3c4\uc5d0\ub3c4 \uc774\ub9cc\ud55c \uc0b0\uc774 \uc5c6\ub2e4. \uc218\uacc4(\u6c34\u7cfb)\ub294 \ub3d9\ucabd\uc774 \uccad\ud3c9 \uc548\uc804\uc720\uc6d0\uc9c0\ub97c \uc774\ub8e8\ub2e4 \ubd81\ud55c\uac15\uc73c\ub85c \ud758\ub7ec\ub4dc\ub294 \uc870\uc885\ucc9c, \uc11c\ucabd\uc740 \ud3ec\ucc9c\ucc9c\uc758 \uc9c0\ub958 \uba85\ub355\ucc9c\uc774\ub2e4. \ub530\ub77c\uc11c \uc870\uc885\ucc9c \uc720\uc5ed, \uc61b\uc774\ub984\uc774 \uc870\uc885\ud604(\u671d\u5b97\u7e23)\uc774\uc5c8\ub358 \uc0c1\uba74\uacfc \ud558\uba74\uc774 \uc55e\ucabd\uc774 \ub418\ub2c8 \uc0b0\uc138 \ub610\ud55c \uccad\ud3c9\uc5d0\uc11c \ud604\ub9ac\ub85c \ub118\uc5b4\uac00\ub294 \uc11c\ub300\uace0\uac1c\uc5d0\uc11c \ubcf4\uc544\uc57c \ub9de\ub2e4. \uc6b4\uc545\uc0b0\uc740 \uc870\uc885\ud604\uc758 \uce58\uc18c(\u6cbb\u6240)\uc600\ub358 \ud604\ub9ac\uc758 \uc9c4\uc0b0\uc774\uc5c8\ub358 \uac83\uc774\ub2e4.'), STRINGDECODE('\uc8fc\ubd09\uc778 \ub9dd\uacbd\ub300\ub97c \ub458\ub7ec\uc2fc \uacbd\uad00\uc774 \uacbd\uae30 \uc18c\uae08\uac15\uc774\ub77c\uace0 \ubd88\ub9ac\uc6b8 \ub9cc\ud07c \ub6f0\uc5b4\ub09c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ucc9c\ub144\uace0\ucc30\uc778 \ud604\ub4f1\uc0ac \ubc0f \ubc31\ub144\ud3ed\ud3ec, \uc624\ub791\uce90\uc18c, \ub208\uc379\ubc14\uc704, \ucf54\ub07c\ub9ac\ubc14\uc704, \ub9dd\uacbd\ub300, \ubb34\uc6b0\ud3ed\ud3ec, \ud070\uace8\ub0b4\uce58\uae30\uc554\ubcbd, \ub