INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(65, TIMESTAMP '2019-08-14 15:46:34.932', TIMESTAMP '2019-08-14 15:47:18.262', '45', STRINGDECODE('\uc6b4\uc7a5\uc0b0\uc740 \uc804\ub77c\ubd81\ub3c4 \uc9c4\uc548\uad70 \uc8fc\ucc9c\uba74, \uc815\ucc9c\uba74, \ubd80\uadc0\uba74, \uc644\uc8fc\uad70 \ub3d9\uc0c1\uba74\uc5d0 \uac78\uccd0 \uc788\uc73c\uba70 \ub0a8\ucabd\uacfc \ubd81\ucabd\uc73c\ub85c \ud750\ub974\ub294 \ubb3c\uc740 \uae08\uac15\uc73c\ub85c \uc720\uc785\ub418\uace0, \uc11c\ucabd \uacc4\uace1\uc73c\ub85c \ud750\ub974\ub294 \ubb3c\uc740 \uc644\uc8fc\uad70 \ub3d9\uc0c1\uba74\uc744 \uc9c0\ub098 \ub9cc\uacbd\uac15\uc73c\ub85c \ud758\ub7ec \uae08\uac15\uacfc \ub9cc\uacbd\uac15\uc758 \ubc1c\uc6d0\uc9c0 \uad6c\uc2e4\ub3c4 \ud558\uace0 \uc788\ub2e4. \ubd81\ub450\uce60\uc131\uc758 \uc804\uc124\uc774 \ub2f4\uaca8 \uc788\ub294 \uce60\uc131\ub300\ub97c \uc9c0\ub098 \ud55c\ucc38 \ub354 \uc62c\ub77c\uac00\uba74 \uc624\uc131\ub300\uac00 \uc788\ub294\ub370 \uc870\uc120\uc870 \uc911\uc885 \ub54c\uc758 \uc11c\ucd9c \uc131\ub9ac\ud559\uc790 \uc1a1\uc775\ud544\uc774 \uc740\uac70\ud558\uc600\ub358 \uacf3\uc73c\ub85c \uc804\ud574\uc9c0\uace0 \uc788\ub2e4.<BR><BR>\uc8fc\uc694 \ub4f1\uc0b0\ucf54\uc2a4\ub294 \ubd80\uadc0\uba74 \uad81\ud56d\ub9ac\ub098 \ud669\uae08\ub9ac\ub85c \uc624\ub974\ub294 \ucf54\uc2a4\uc640 \uc644\uc8fc\uad70 \ub3d9\uc0c1\uba74 \uc2e0\uc6d4\ub9ac\ub85c \uc624\ub974\ub294 \ucf54\uc2a4\uac00 \uc788\uace0 \uc8fc\ucc9c\uba74 \uc911\uc0ac, \uc678\ucc98\uc0ac\ub3d9 \ub9c8\uc744\uc5d0\uc11c \uc2dc\uc791\ud558\uc5ec \ub3d9\ubd09\uc73c\ub85c \uc624\ub974\ub294 \ucf54\uc2a4\ub3c4 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc6b4\uc7a5\uc0b0\ud734\uac8c\uc18c-\ud65c\ubaa9\uc7ac-\uc815\uc0c1-\ub3d9\ubd09-\ub3d9\ubd09 \ubd81\ub989-\uc678\ucc98\uc0ac\ub3d9(\ucd1d 3\uc2dc\uac04 5\ubd84)<BR>\u2461 \uc815\uc218\uc554-\uc11c\ubd09-\uc815\uc0c1-\ub3d9\ubd09-\uac01\uc6b0\ubaa9\uc7ac-\ub0b4\ucc98\uc0ac\ub3d9-\uc678\ucc98\uc0ac\ub3d9(\ucd1d 4\uc2dc\uac04 20\ubd84)<BR>\u2462 \uc5f0\uc11d\uc0ac-\uc5f0\uc11d\uc0b0-\uc815\uc0c1-\ub3d9\ubd09-\ub0b4\ucc98\uc0ac\ub3d9-\uc678\ucc98\uc0ac\ub3d9(\ucd1d 5\uc2dc\uac04)'), '35.967244', '127.40027', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \uc9c4\uc548\uad70 \uc8fc\ucc9c\uba74\u318d\ubd80\uadc0\uba74\u318d\uc815\ucc9c\uba74, \uc644\uc8fc\uad70 \ub3d9\uc0c1\uba74'), 2515042400, 1126, STRINGDECODE('\uc6b4\uc7a5\uc0b0'), STRINGDECODE('&lt;\uc815\uc5ec\ub9bd\uc0ac\uac74 \ubc30\ud6c4\uc870\uc885\uc790 \uc1a1\uc775\ud544\uc758 \uc0b0&gt;<BR>\uae08\ub0a8\uc815\ub9e5\uc758 \ucd5c\uace0\ubd09\uc774\uba74\uc11c \uc804\ub77c\ubd81\ub3c4 \ub0b4\ub959 \ucd5c\uace0\ubd09\uc774\ub2e4. \ubcf8\uc774\ub984\uc740 \uad6c\uc808\uc0b0\uc774\uc5c8\ub294\ub370 \uc870\uc120 \uc911\uae30\uc5d0 \uc11c\uc778\uc758 \ub9c9\ud6c4\uc2e4\ub825\uc790 \uc1a1\uc775\ud544\uc758 \uc790(\u5b57) \uc6b4\uc7a5\uc744 \ub530\ub77c \uc6b4\uc7a5\uc0b0\uc73c\ub85c \ubc14\ub00c\uc5c8\ub2e4. \uadf8\uac00 \ubc30\ud6c4\uc870\uc885\ud588\ub2e4\uace0 \ud558\ub294 \uae30\ucd95\uc625\uc0ac\ub85c \uc815\uc5ec\ub9bd\uacfc \uadf8 \ucd94\uc885\uc790(\ub300\ubd80\ubd84\uc774 \uc804\ub77c\ubd81\ub3c4 \uc120\ube44\ub4e4\uc774\uc5c8\ub2e4) \ucc9c\uc5ec \uba85\uc774 \uc8fd\uc784\uc744 \ub2f9\ud588\ub294 \ubc14 \uadf8 \ud1b5\uc5d0 \uc0b0\uc774\ub984\uae4c\uc9c0 \ucc3d\uc528\uac1c\uba85\ud55c \ub4ef\ud558\ub2e4. <BR><BR>\uc815\uc0c1 \uc88c\uc6b0\uc5d0 \uc11c\ubd09\uacfc \ub3d9\ubd09\uc774 \uc788\ub294\ub370 \uc11c\ubd09 \uc544\ub798 \uc624\uc131\ub300\ub294, \ucc9c\ubbfc\uc73c\ub85c\uc11c \uc8fc\uc778\uc774 \uc5ed\ubaa8\ub97c \uaf80\ud588\ub2e4\uace0 \uac70\uc9d3 \ubc00\uace0\ud574 \uc77c\uc57d \uc8853\ud488\uae4c\uc9c0 \uc62c\ub77c\uac04 \uc1a1\uc775\ud544\uc758 \uc544\ubc84\uc9c0\uac00 \ub2e4\uc2dc \ucc9c\ubbfc\uc774 \ub418\uba74\uc11c \ub3c4\ud53c\uc0dd\ud65c\uc5d0 \ub4e4\uc5b4\uac04 \uc1a1\uc775\ud544\uc774 \uba38\ubb3c\ub800\ub358 \uacf3\uc774\ub77c\uace0 \ud55c\ub2e4. \uadf8\ub294 \uac70\uae30\uc11c, \ube64\ud788 \ubcf4\uc774\ub294 \uc8fd\ub3c4\uc758 \uc815\uc5ec\ub9bd\uc744 \uc5bd\uc5b4\ub9ec \uc74c\ubaa8\ub97c \uc644\uc131\ud588\ub358 \ub4ef\ud558\ub2e4. \uc0b0 \ub3d9\ucabd\uc758 \uac00\uc7a5 \ube7c\uc5b4\ub09c \ubd80\ubd84\uc740 \uc1a1\uc775\ud544\uc758 \ud638\uc640 \uc74c\uc774 \uac19\uc740 \uad6c\ubd09\uc0b0\uc774\ub2e4.'), STRINGDECODE('\uc6b4\uc77c\uc554(\u96f2\u65e5\u5ca9), \ubc18\uc77c\uc554(\u534a\u65e5\u5ca9)\uc73c\ub85c \uc720\uba85\ud55c \ub300\ubd88\ucc9c(\u5927\u4f5b\u5ddd) \uacc4\uace1\uc774 \uc788\uc73c\uba70, \ubb3c\uc774 \ub9d1\uace0 \uc554\ubcbd\uacfc \uc232\uc73c\ub85c \ub458\ub7ec\uc2f8\uc5ec \uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0 \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubd81\ub450\uce60\uc131\uc758 \uc804\uc124\uc774 \ub2f4\uaca8\uc788\ub294 `\uce60\uc131\ub300\"\uc640 \uc870\uc120\uc2dc\ub300 \uc1a1\uc775\ud544\uc758 \uc804\uc124\uc774 \uc5bd\ud600 \uc788\ub294 `\uc624\uc131\ub300\"\uac00 \uc720\uba85'), STRINGDECODE('\uae08\uac15\uacfc \ub9cc\uacbd\uac15\uc758 \ubc1c\uc6d0\uc9c0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc6b4\uc77c\uc554\uacfc \ubc18\uc77c\uc554\uc774 \uc788\ub294 \uc8fc\ucc9c\uba74 \ub300\ubd88\ub9ac\uc640 \uc8fc\uc591\ub9ac \uc8fc\ubcc0, \ub300\ubd88\ub9ac\uc5d0\uc11c \ubb34\ub989\ub9ac\ub85c \uac00\ub294 \uc0bc\uac70\ub9ac\uc5d0 \uc798 \ub370\uc640 \uba39\uc744 \ub370\uac00 \ub9ce\ub2e4. <BR>\uc54c\ud504\uc2a4\uc0b0\uc7a5(\u260e063-432-7024), \uc5d0\ub85c\uc2a4\uc0b0\uc7a5(\u260e063-432-7025), \uc804\uc8fc\uc5ec\uad00(\u260e063-432-7026), \uce58\ud0a8\uc804\ubb38\uc810 \uba38\ub8e8\ub791\ub2e4\ub798\ub791(\u260e063-432-0080) \ub4f1\uc774 \uc788\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc9c4\uc548\uad70\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e063-430-2390'), STRINGDECODE('\uae30\uc810\uc740 \uc9c4\uc548\uc774\ub2e4. \uc9c4\uc548\uc5d0\uc11c \ub0b4\ucc98\uc0ac\ub3d9\uc73c\ub85c \uac00\ub294 \ubc84\uc2a4\ub294 \ud558\ub8e8 7\ud68c \uc788\uc9c0\ub9cc \uc6b4\uc7a5\uc0b0\ud734\uac8c\uc18c\uae4c\uc9c0\ub294 \ubc84\uc2a4\ub294 \uc5c6\ub2e4. \uc6b4\uc77c\uc554\ubc18\uc77c\uc554\ud589\uc740 1\uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2cc\ub2e4. \uc9c4\uc548\ud589 \ubc84\uc2a4\ub294 \uc11c\uc6b8 \uac15\ub0a8\ud130\ubbf8\ub110\uacfc \ubd80\uc0b0 \uc0ac\uc0c1\ud130\ubbf8\ub110\uc5d0\uc11c 4\ud68c\uc529 \uc788\ub2e4. \uc804\uc8fc\uc5d0\uc11c\ub294 10\ubd84, \ub300\uad6c\uc5d0\uc11c\ub294 30\ubd84, \ub300\uc804\uc5d0\uc11c\ub294 \ud55c \uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2cc\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(66, TIMESTAMP '2019-08-14 15:46:34.932', TIMESTAMP '2019-08-14 15:47:18.859', '43', STRINGDECODE('\uc6d4\uc545\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\uc740 \ucda9\ubd81 \uc81c\ucc9c\uc2dc \ud55c\uc218\uba74\uacfc \ub355\uc0b0\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c \ubc31\ub450\ub300\uac04\uc774 \uc774\ud654\ub839\uc744 \uc9c0\ub098 \uc870\ub839\uc0b0, \uc870\ub839, \ub9c8\ud328\ubd09, \ubd80\ubd09, \uc6d4\ud56d\uc0bc\ubd09, \ud558\ub298\uc7ac, \ud3ec\uc554\uc0b0\uc744 \uac70\uccd0 \ub300\ubbf8\uc0b0\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \uc911\uac04\uc120 \ubd81\ucabd\uc5d0 \uc19f\uc544\uc788\ub294 \ubd09\uc6b0\ub9ac\uc774\ub2e4. \ub113\uc774 373\u33a2\uc5d0 \uac78\uccd0 \uc788\ub294 \uc6d4\uc545\uc0b0 \ub2a5\uc120\uc740 \ubc31\ub450\ub300\uac04\ubcf4\ub2e4 \ub192\uace0 \ubb34\uc5c7\ubcf4\ub2e4\ub3c4 \ubc14\uc704\uc640 \uc554\ub989\uc73c\ub85c \ud615\uc131\ub418\uc5b4 \uc788\uc5b4 \ub300\uac04\uc5d0 \ube44\ud574 \ub2a5\uc120\uc774 \ud6e8\uc52c \ub450\ub4dc\ub7ec\uc838 \uc788\ub2e4.<BR><BR>\uc81c\ucc9c\uc2dc \ud55c\uc218\uba74\uacfc \ucda9\uc8fc\uc2dc \uc0b4\ubbf8\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc9c0\uc810\uc5d0 \uc790\ub9ac\ud55c \uc6d4\uc545\uc218\ub9ac\ubd09\uc740 \uc77c\ubc18\uc778\ub4e4\uc5d0\uac8c\ub294 \uc0dd\uc18c\ud55c \uc774\ub984\uc774\uc9c0\ub9cc \uc54c\uace0\ubcf4\uba74 \uc228\uaca8\uc9c4 \ube44\uacbd\ub4e4\uc774 \ub9ce\uc740 \uba85\uc0b0\uc774\ub2e4. \uc0b0\ud589\uc744 \ub098\uc120 \ud6c4 \uacf3\uacf3\uc5d0\uc11c \ud3bc\uccd0\uc9c0\ub294 \uae30\uc554\uc808\ubcbd\uc758 \ubcc0\ud654\ubb34\uc30d\ud568\uc740 \uc0b0\uc545\uc778\ub4e4\uc758 \ud0c4\uc131\uc744 \uc790\uc544\ub0b8\ub2e4. \uc228\uc740 \uc808\uacbd\ub4e4\uc740 \ub458\ub7ec\ubcf4\uba70 \uc815\uc0c1\uc5d0 \uc624\ub974\uba74 \uc5b4\ub290\uc0c8 \uc6d4\uc545\uc218\ub9ac\ubd09\uc5d0 \ub300\ud55c \uc560\uc815\uc774 \ub4ec\ubfcd \uc0dd\uaca8 \ub2e4\uc2dc\ud55c\ubc88 \uc774 \uacf3\uc744 \ucc3e\uace0 \uc2f6\uc740 \ub9c8\uc74c\uc774 \uc808\ub85c \ub4e0\ub2e4.<BR><BR>\uc6d4\uc545\uc0b0(\u6708\u5cb3\u5c71 1095.3m) \ub4a4\uc548\uae38\uc5d0\ub294 \uc6a9\ud558\uad6c\uace1\uc774 30\ub9ac\ub098 \ub298\uc5b4\uc11c \uc788\ub294\ub370 \uc6d4\uc545\uc2dc\ub8e8\ubd09\uc740 \uc6a9\ud558\uad6c\uace1\uc758 \uc11c\ucabd\uc5d0 \ubcd1\ud48d\ucc98\ub7fc \uc790\ub9ac\ud55c \uc0b0\uc774\ub2e4. \uc2dc\ub8e8\ubd09\uc744 \uac00\ub824\uba74 \uc77c\ub2e8 \ub355\uc0b0\uba74\uc744 \uac70\uccd0\uc57c \ud55c\ub2e4. \ub355\uc0b0(\u5fb7\u5c71)\uc740 \uc774\ub984 \uadf8\ub300\ub85c \uc0b0\uc758 \ub355\uc744 \ubcf8\ub2e4\ub294 \ub73b\uc73c\ub85c \ub355\uc0b0\uba74\uc740 \uc608\ubd80\ud130 \uc778\uc0bc\uc7ac\ubc30 \uc801\uaca9\uc9c0\ub85c \uc720\uba85\ud558\ub2e4. \uc0b0 \uacf3\uacf3\uc5d0 \ube7c\uc5b4\ub09c \uacbd\uad00\uc774 \uc0b0\uc7ac\ud574 \uc788\ub294\ub370 \ud2b9\ud788 \uc804\ub9dd\ub300 \ubc14\uc704\uc5d0\uc11c\uc758 \uc870\ub9dd\uc774 \uc77c\ud488\uc774\ub2e4. \uc804\ub9dd\ub300 \ubc14\uc704\uc5d0 \uc624\ub974\uba74 \uc11c\ubd81\ucabd\uc758 \uc6d4\uc545\uc0b0 \uc815\uc0c1\uc774 \uc6b8\ubd80\uc9d6\ub294 \ud638\ub791\uc774 \ud615\uc0c1\uc744 \ud558\uace0 \uc788\uc5b4 \ubcf4\ub294 \uc774\uc758 \ub9c8\uc74c\uc744 \uc555\ub3c4\ud558\uba70 \ub3d9\ucabd\uc5d0\ub294 \uc6a9\ud558\uad6c\uace1 \uac74\ub108\ub85c \ud558\uc124\uc0b0, \ub9e4\ub450\uc0b0, \ubb38\uc218\ubd09 \uc0b0\ub989\uc774 \uc544\ub984\ub2e4\uc6b4 \ud48d\uad11\uc744 \uc5f0\ucd9c\ud558\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ub355\uc8fc\uace8\ud734\uac8c\uc18c-\ub9c8\uc560\ubd88-\uc1a1\uacc4\uc0bc\uac70\ub9ac-\uc601\ubd09-\uc911\ubd09 \ud558\ubd09 \uac70\uccd0 \ubcf4\ub355\uc554 (\ucd1d 5\uc2dc\uac04 20\ubd84). *\ubcf4\ub355\uc554~\uc601\ubd09\uad6c\uac04\uc740 \uc0b0\ubd88\ud1b5\uc81c\uae30\uac04 \uc785\uc0b0\uae08\uc9c0<BR>\u2461 \uc6d4\uc545\ub9ac \ub355\uc0b0\ub9e4\ud45c\uc18c-\uc2e0\ub975\uc0ac-\uc808\uace8 \uc601\ubd09-\uc1a1\uacc4\ub9ac \ub3d9\ucc9c\uad50\ub9e4\ud45c\uc18c (\ucd1d 4\uc2dc\uac04 20\ubd84)'), '36.90346', '128.087468', STRINGDECODE('\ucda9\uccad\ubd81\ub3c4 \uc81c\ucc9c\uc2dc \ud55c\uc218\uba74, \ub355\uc0b0\uba74'), 2307083700, 1095, STRINGDECODE('\uc6d4\uc545\uc0b0'), STRINGDECODE('&lt;\uccad\ud48d\uba85\uc6d4 \uc911\uc6d0\uc758 \uba85\uc0b0&gt;<BR>\uc6d4\uc545\uc0b0(1095.3m)\uc740 \uc124\uc545\uc0b0\uc758 \ud604\ub780\ud55c \uc544\ub984\ub2e4\uc6c0\uacfc \uc9c0\ub9ac\uc0b0\uc758 \uc7a5\uc5c4\ud568\uc744 \uc870\uae08\uc529 \uac00\uc838\ub2e4 \ub193\uc740 \ub4ef\ud55c \uc0b0\uc138\ub97c \uc9c0\ub154\ub2e4. \uae4e\uc544\uc9c0\ub978 \uc808\ubcbd\uc774 \uac00\uc2b4 \uc870\uc774\uac8c \ud558\uc9c0\ub9cc \ubd80\ub4dc\ub7ec\uc6b4 \ub2a5\uc120\uc758 \ud478\uadfc\ud568\ub3c4 \ud568\uaed8 \uc8fc\ub294 \uc0b0\uc774\ub2e4. \uad6d\uc0ac\ubd09\uc73c\ub85c\ub3c4 \ubd80\ub974\ub294 \uc6d4\uc545\uc0b0\uc758 \uc815\uc0c1\uc740 \uc608\ub85c\ubd80\ud130 \uc2e0\ub839\uc2a4\ub7f0 \uc0b0\uc73c\ub85c \uc5ec\uaca8 ''\uc601\ubd09''\uc774\ub77c\uace0 \ubd80\ub974\uace0 \uc788\ub2e4. \uac70\ub300\ud55c \uc554\ubd09\uc778 \uc601\ubd09 \uaf2d\ub300\uae30\uc5d0 \uc11c\uba74 \uc6d4\uc545\uc0b0 \uc0b0\uadf8\ub9bc\uc790\ub97c \ub2f4\uace0 \uc788\ub294 \ucda9\uc8fc\ud638\ubc18\uc774 \ud55c\ub208\uc5d0 \ub4e4\uc5b4\uc640 \uc7a5\uad00\uc744 \uc774\ub8ec\ub2e4. <BR><BR>\ubb38\uc218\ubd09, \ud558\uc124\uc0b0, \ub9e4\ub450\ub9c9, \ub300\ubbf8\uc0b0, \ud669\uc815\uc0b0 \ub9c8\ud3d0\ubd09 \ub4f1 \uc0b0\uc138\uac00 \uc544\ub984\ub2e4\uc6b4 \uc0b0\ub4e4\uacfc \ud568\uaed8 1984\ub144 \uc6d4\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\uc73c\uba70, \uace0\ubd09\uc900\ub839 \uc0ac\uc774\ub85c \uc1a1\uacc4\uacc4\uace1, \uc6a9\ud558\uad6c\uace1, \uc120\uc554\uacc4\uace1\uc774 \uad7d\uc774\uc838 \ud750\ub974\uba70 \uc0b0\uc545\uacf5\uc6d0\uc758 \uc7a5\uad00\uc744 \ubcf4\uc5ec\uc8fc\uace0 \uc788\ub2e4. \ub610\ud55c \uc2e0\ub77c\ub9d0 \ub9c8\uc758\ud0dc\uc790\uc640 \ub355\uc8fc\uacf5\uc8fc\uac00 \ub9c8\uc8fc\ubcf4\uace0 \ub9dd\uad6d\uc758 \ud55c\uc744 \ub2ec\ub798\uace0 \uc788\ub2e4\ub294 \uc911\uc6d0\ubbf8\ub975\uc0a