INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(67, TIMESTAMP '2019-08-14 15:46:34.933', TIMESTAMP '2019-08-14 15:47:19.482', '46', STRINGDECODE('\uc6d4\ucd9c\uc0b0\uc740 \uad6d\ub0b4\uc5d0\uc11c \uaddc\ubaa8\uac00 \uc81c\uc77c \uc791\uc740 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \ucc9c\ud0dc\ub9cc\uc0c1\uc758 \uae30\uc554\uad34\uc11d\uc774 \uc218\uc11d \uc804\uc2dc\uc7a5\uc744 \uc5f0\uc0c1\ucf00\ud55c\ub2e4. \ub0a8\uc131\uc801\uc778 \uc6c5\uc7a5\ud568\uc744 \uac16\ucd98 \ubd81\ucabd\uc758 \uac00\ud30c\ub978 \ub3cc\uc0b0\uacfc \uc5ec\uc131\uc801\uc778 \uc12c\uc138\ud568\uc744 \uac16\ucd98 \uc644\ub9cc\ud55c \ub0a8\ucabd\uc0b0\uc774 \uc870\ud654\ub97c \uc774\ub904 \uc9c0\ub9ac\uc0b0, \ubcc0\uc0b0, \ucc9c\uad00\uc0b0, \ub0b4\uc7a5\uc0b0\uacfc \ud568\uaed8 \ud638\ub0a8\uc758 5\ub300 \uba85\uc0b0\uc73c\ub85c \uaf3d\ud788\uace0 \uc788\ub2e4. \uc2e0\ub77c\uc2dc\ub300\uc5d0\ub294 \uc6d4\ub098\uc0b0(\u6708\u5948\u5c71), \uace0\ub824\uc2dc\ub300\uc5d0\ub294 \uc6d4\uc0dd\uc0b0(\u6708\u751f\u5c71) \uadf8\ub9ac\uace0 \uc870\uc120\uc2dc\ub300\ubd80\ud130 \uc6d4\ucd9c\uc0b0\uc774\ub77c\uace0 \ubd88\ub9ac\uc5b4\uc84c\ub2e4.'), STRINGDECODE('\u2460 \ub3c4\uac11\uc0ac-\ubbf8\uc655\uc7ac-\uad6c\uc815\ubd09-\uc815\uc0c1-\uae08\ub8e1\uacbd\ud3ec\ub300-\uacbd\ud3ec\ub300 \ub9e4\ud45c\uc18c(\ucd1d4\uc2dc\uac04 40\ubd84)<BR>\u2461 \ucc9c\ud669\uc0ac-\uad6c\ub984\ub2e4\ub9ac-\uc815\uc0c1-\ubc14\ub78c\ud3ed\ud3ec-\ucc9c\ud669\uc0ac(\ucd1d 2\uc2dc\uac04 20\ubd84)<BR>\u2462 \ubd88\ud2f0\uc7ac-\ub204\ub9bf\uc7ac-\ub2ec\uad6c\ubd09-\uc815\uc0c1-\uad6c\uc815\ubd09-\ub9c8\uc560\uc5ec\ub798\uc88c\uc0c1-\uad6c\uc815\ubd09-\ubbf8\uc655\uc7ac-\ub3c4\uac11\uc0ac(\ucd1d 10\uc2dc\uac04 30\ubd84)'), '34.791121', '126.724923', STRINGDECODE('\uc804\ub77c\ub0a8\ub3c4 \uc601\uc554\uad70 \uc601\uc554\uc74d\u318d\uad70\uc11c\uba74\u318d\ud559\uc0b0\uba74, \uac15\uc9c4\uad70 \uc131\uc804\uba74'), 2601072300, 811, STRINGDECODE('\uc6d4\ucd9c\uc0b0'), STRINGDECODE('&lt;\ub3cc\ubd88\uaf43\ucc98\ub7fc \uce58\uc19f\uc544\uc624\u