INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(68, TIMESTAMP '2019-08-14 15:46:34.933', TIMESTAMP '2019-08-14 15:47:20.077', '41', STRINGDECODE('\uad6d\ub9bd\uc9c0\ub9ac\uc6d0 \ubc1c\ud589 \uc9c0\ud615\ub3c4\uc5d0\ub3c4 \ub9c8\uc720\uc0b0\uc73c\ub85c \ud45c\uae30\ud558\ub2e4\uac00, 1973\ub144 \uad6d\ud1a0\uc790\uc624\uc120 \uc885\uc8fc\ub300\uac00 \uc774 \uc0b0\uc744 \ud1b5\uacfc\ud558\uba74\uc11c \ub2f9\uc2dc \ub300\uc6d0 \uc911 \ud64d\uc77c\uc810\uc774\uc5c8\ub358 \uc9c4\uc720\uba85\uc528\uc758 \uc774\ub984\uc744 \ub530\uc11c \uc720\uba85\uc0b0\uc73c\ub85c \ubd80\ub974\uac8c \ub418\uc5c8\ub2e4.<BR><BR>\uc774 \uc0b0\uc740 \uc815\uc0c1\uc5d0 \uc774\ub974\ub294 \ub2a5\uc120\uc774 \uc644\ub9cc\ud558\uace0 \ubd80\ub4dc\ub7ec\uc6b0\uba70, \uc0b0 \uc804\uccb4\uc5d0 \uac16\uac00\uc9c0 \ub098\ubb34\uac00 \uc6b8\ucc3d\ud558\uc5ec \ubd04\ubd80\ud130 \uc5ec\ub984\uae4c\uc9c0 \ud558\ub298\uc744 \uac00\ub9b4 \uc815\ub3c4\uc774\uace0 \uc5ec\ub984\uc774\uba74 \uae30\uc554\uad34\uc11d\uacfc \uc6b0\uac70\uc9c4 \uc232\uc774 \uc870\ud654\ub97c \uc774\ub8ec \uacc4\uace1\uc740 \uc218\ub7c9\uc774 \ud48d\ubd80\ud574 \uc808\uacbd\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4.'), STRINGDECODE('\uc8fc\ucc28\uc7a5\uc5d0\uc11c \ub2a5\uc120\uc73c\ub85c \uc9c1\ub4f1\ud558\ub294 \ucf54\uc2a4\uac00 \uc788\uc9c0\ub9cc \uc774\ud6c4 \uba54\uc778\ucf54\uc2a4\uc640 \uc911\ubcf5\ub41c\ub2e4. 1\uc2dc\uac04 20\ubd84\uc774 \uac78\ub9b0\ub2e4.'), '37.653344', '127.499145', STRINGDECODE('\uacbd\uae30\ub3c4 \uac00\ud3c9\uad70 \uc124\uc545\uba74, \uc591\ud3c9\uad70 \uc625\ucc9c\uba74'), 2130093200, 864, STRINGDECODE('\uc720\uba85\uc0b0'), STRINGDECODE('&lt;\uc6d0\uc774\ub984\uc740 \ub9c8\uc720\uc0b0\uc774\ub2e4&gt;<BR>\ub300\ub3d9\uc5ec\uc9c0\ub3c4\uc5d0 \ub9c8\uc720\uc0b0\uc73c\ub85c \ub098\uc624\ub294 \uc0b0\uc778\ub370 \uc5b8\uc81c\ubd80\ud134\uac00 \uc720\uba85\uc0b0\uc73c\ub85c \ubd88\ub9ac\uac8c \ub418\uc5c8\ub2e4. \uc18c\ubb38\uc73c\ub85c\ub294, \uc0b0\uc774\ub984\uc774 \uc54c\ub824\uc9c0\uc9c0 \uc54a\uc558\uc744 \ub54c \uc774 \uc0b0\uc744 \ucc3e\uc740 \uc0ac\ub78c\ub4e4\uc774 \uc77c\ud589 \uc911 \uae40\uc720\uba85\uc774\ub77c\ub294 \uc5ec\uc790 \uc774\ub984\uc744 \ub530\uc11c \ubd99\uc600\ub2e4\uace0 \ud55c\ub2e4. ''\uc785\uad6c\uc5d0 \uc788\ub294 \uac00\uc9c0\uacc4\uace1''\uc774\ub77c\ub294 \ub73b\uc758 \uc785\uad6c\uc9c0(\u5165\u53e3\u679d)\uacc4\uace1\ub3c4 \uadf8\ub4e4\uc758 \uc791\uba85\uc778 \ub4ef\ud558\ub2e4. <BR><BR>\ubc14\ub85c \uadf8 \uc785\uad6c\uc9c0\uacc4\uace1\uc774 \ub110\ub9ac \uc54c\ub824\uc838 \uc788\ub2e4. \uc6a9\ubb38\uc0b0\uc5d0\uc11c \ud758\ub7ec\uc624\ub294 \uadf8 \uacc4\uace1\uc5d0 \ub9c8\ub2f9\uc18c, \uc6a9\uc18c, \ubc15\uc950\uc18c \uac19\uc740 \uba85\uc18c\uac00 \uc5ec\ub7ff \uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \uc5b5\uc0c8\ubc2d\uc774 \ud3bc\uccd0\uc9c0\ub294 \uc815\uc0c1\uc5d0\uc11c\uc758 \uc804\ub9dd\ub3c4 \uc88b\uc740\ub370 \ub098\ubb34\uac00 \uac70\uc758 \uc5c6\ub294 \uae4c\ub2ed\uc5d0 \ud328\ub7ec\uae00\ub77c\uc774\ub529 \ud65c\uacf5\uc7a5\uc73c\ub85c \uac01\uad11\ubc1b\uace0 \uc788\ub2e4. \ud328\ub7ec\uae00\ub77c\uc774\ub354\ub4e4\uc740 \uc124\ub9e4\uc7ac \uc790\uc5f0\ud734\uc591\ub9bc\ucabd(\ub3d9\ucabd)\uc5d0\uc11c \uc62c\ub77c\uc624\ub294 \uc0b0\ud310\ub3c4\ub85c\ub97c \uc774\uc6a9\ud55c\ub2e4. <BR><BR>550\ubbf8\ud130\ucbe4\uc758 \uc120\uc5b4\uce58\uace0\uac1c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub2a5\uc120\ucf54\uc2a4\ub294 \uc815\uc0c1\uacfc\uc758 \uace0\ub3c4\ucc28\uac00 300m \ub0a8\uc9d3\ubc16\uc5d0 \uc548 \ub41c\ub2e4. \uadf8\ub798\uc11c \uc27d\uac8c \uc815\uc0c1\uc5d0 \uc124 \uc218 \uc788\uc73c\ubbc0\ub85c \uc120\uc5b4\uce58\ub97c \uacbd\uacc4\ub85c \uc774\uc6c3\ud55c \uc911\ubbf8\uc0b0\uae4c\uc9c0 \uc5f0\uacc4\uc0b0\ud589\uc744 \ud558\ub294 \uc774\ub4e4\ub3c4 \ub9ce\ub2e4. \uc774 \uacbd\uc6b0 \uc6d0\uc810\ud68c\uadc0\ub3c4 \uc27d\ub2e4.'), STRINGDECODE('\ub2a5\uc120\uc774 \uc644\ub9cc\ud558\uace0 \ubd80\ub4dc\ub7ec\uc6b0\uba70, \uc218\ub7c9\uc774 \ud48d\ubd80\ud55c \uacc4\uace1\uacfc \uae30\uc554\uad34\uc11d \ubc0f \uc6b8\ucc3d\ud55c \