INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(69, TIMESTAMP '2019-08-14 15:46:34.934', TIMESTAMP '2019-08-14 15:47:20.698', '42', STRINGDECODE('\uc751\ubd09\uc0b0\uc740 \uac15\uc6d0\ub3c4 \uc0bc\ucc99\uad70\uacfc \uacbd\ubd81 \uc6b8\uc9c4\uad70\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\uba70 \uc0b0 \uc8fc\ubcc0\uc5d0 \uc804\uc778\ubbf8\ub2f5\uc758 \uc5ec\ub7ec \uacc4\uace1\ub4e4\uc744 \ub07c\uace0 \uacc4\uace1\ud0d0\ud5d8\ucf54\uc2a4\ub85c \uc801\ud569\ud55c \uc0b0\uc774\ub2e4. \uc751\ubd09\uc0b0\uc744 \uc911\uc2ec\uc73c\ub85c \ubd81\ucabd\uc758 \uae30\uace1\ucc9c\uacfc \uc7ac\ub791\ubc15\uace8, \uc11c\ucabd\uc758 \uc6a9\uc18c\uace8, \ubb38\uc9c0\uace8, \uac31\uc774\uace8, \ubcf4\ub9ac\uace8 \ub4f1\uc774 \uc788\uc73c\uba70 \ub0a8\ucabd \uc6b8\uc9c4\uad70\ub0b4\uc5d0 \ub300\uad11\ucc9c\uacfc \ub3d9\ud574\uc548\ucabd \ud3ed\ud3ec\uace8, \uc131\uc6b0\uace8\ub4f1\uc774 \uc990\ube44\ud558\uac8c \ub4e4\uc5b4\uc11c \uc788\ub2e4. \ud638\uc0b0\uc5d0\uc11c \ubc84\uc2a4\uc885\uc810\uc744 \uc9c0\ub098 1km\ucbe4 \ub4e4\uc5b4\uc11c\uba74 \ud3d0\uad11\ud130\uac00 \ub098\uc624\ub294\ub370 \uc5ec\uae30\uc11c \uc870\uae08 \ub354 \uc62c\ub77c\uac00\uba74 \uc5f0\uc774\uc5b4\uc9c4 2\uac1c\uc758 \ud3ed\ud3ec\uac00 \ud798\ucc28\uac8c \ud750\ub978\ub2e4. \uc870\uae08 \ub354 \uc62c\ub77c\uac00\uba74 \uc624\ub978\ucabd \uae4a\uc740 \uacc4\uace1 \uc544\ub798\ub85c \ud5e4\uc544\ub9b4 \uc218 \uc5c6\ub294 \ud3ed\ud3ec\uc9c0\ub300\ub97c \ub9cc\ub0a0 \uc218 \uc788\ub2e4. \uae08\uac15\uc0b0\uc758 \ucd95\uc18c\ud310\uc744 \uc5f0\uc0c1\ucf00 \ud558\ub294 \uc774\uacf3\uc740 3\ub2e8\uae4c\uc9c0\ub294 \uc2dc\uc57c\uc5d0 \ub4e4\uc5b4\uc624\ub098 \uadf8 \uc544\ub798\ub85c\ub294 \uc6cc\ub099 \ud611\uace1\uc774\uc5b4\uc11c \uba87\ub2e8\uae4c\uc9c0 \ud3ed\ud3ec\uac00 \uaebd\uc5b4\uc838 \ub0b4\ub9ac\ub294\uc9c0 \uc140 \uc218 \uc5c6\uc744 \uc815\ub3c4\ub2e4. \uc6a9\uc18c\uace8\uc744 \ud0d0\ud5d8\ud560 \uacbd\uc6b0\uc5d0\ub294 \uc554\ubcbd\ub4f1\ubc18 \uacbd\ud5d8\uc790\uac00 \ub3d9\ud589\ud574\uc57c \ud558\uba70 20m\uc790\uc77c \ub450\ub3d9\ucbe4\uc740 \uc788\uc5b4\uc57c \uc704\ud5d8\uc9c0\ub300\ub97c \ud1b5\uacfc\ud560 \uc218 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ub355\ud48d\ub9c8\uc744-\uc6a9\uc18c\uace8-\uc791\uc740\ub2f9\uadc0\uace8-\uc815\uc0c1-\ub355\uad6c\uc628\ucc9c(\ucd1d 9\uc2dc\uac04 30\ubd84) <BR>\u2461 \uc0ac\uace1\ubd84\uad50(\ud3d0\uad50)-\uc7ac\ub7c9\ubc15\uace8-\uc751\ubd09\uc9c0 \ub0a8\ub989-\uc751\ubd09\uc0b0 \uc11c\ubd81\ub989-\uc815\uc0c1-\ub355\uad6c\uc628\ucc9c(\ucd1d 6\uc2dc\uac04 10\ubd84) <BR>\u2462 \ubcf4\ub9ac\uad50-\ubcf4\ub9ac\uace8-862\ubd09-\uc751\ubd09\uc0b0 \uc11c\ubd81\ub989-\uc815\uc0c1-\ub355\uad6c\uc628\ucc9c(\ucd1d 6\uc2dc\uac04)'), '37.124032', '129.168571', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc0bc\ucc99\uc2dc \uac00\uace1\uba74\u318d\uc6d0\ub355\uc74d, \uacbd\uc0c1\ubd81\ub3c4 \uc6b8\uc9c4\uad70 \ubd81\uba74'), 2204113300, 1000, STRINGDECODE('\uc751\ubd09\uc0b0'), STRINGDECODE('&lt;\ub3d9\ud574\ub97c \ud5a5\ud574 \ube44\uc0c1\uc744 \uafc8\uafb8\ub294 \ub9e4\ubd09&gt; \uac15\uc6d0\ub3c4 \uc0bc\ucc99\uacfc \uacbd\ubd81 \uc6b8\uc9c4\uc758 \uacbd\uacc4\uc5d0 \uc19f\uc740 \uc751\ubd09\uc0b0\uc740 \ube44\uc0c1\ud558\ub294 \ub9e4\uc758 \ud615\uc0c1\uc744 \ud558\uace0\uc788\ub294 \uc900\ubd09\uc774\ub2e4. \uac8c\ub2e4\uac00 \uc6b8\uc9c4\ucabd\uc758 \uc628\uc815\uace8\uacfc \uc0bc\ucc99\ucabd\uc758 \uc6a9\uc18c\uace8\uc774 \ub2e4 \ube44\ubc94\ud574 \ucc3e\ub294 \uc774\ub4e4\uc774 \ub04a\uc774\uc9c0 \uc54a\ub294\ub2e4. \uc628\uc815\uace8\uc740 \ub2ec\ud33d\uc774\uad00\ucc98\ub7fc \ud68c\ud68c\ub3cc\uba70 \ub5a8\uc5b4\uc9c0\ub294 \uc6a9\uc18c\ud3ed\ud3ec\uc640 \uadf8 \uc544\ub798\uc758 \uc6a9\uc18c, \ub355\uad6c\uc628\ucc9c\uc774 \uba85\uc18c\ub2e4. \ub355\uad6c\uc628\ucc9c\uc740 \uc6d0\ub798 \ub208 \ub36e\uc778 \uace8\uc9dc\uae30\uc5d0\uc11c \uc628\ucc9c\uc695\uc744 \ud560 \uc218 \uc788\ub294 \ub178\ucc9c\uc628\ucc9c\uc774\uc5c8\ub294\ub370 \ud64d\uc218\ub85c \uc720\uc2e4\ub41c \uc774\ud6c4 \uad00\uc815(\u7ba1\u4e95)\uc744 \ub6ab\uc5b4 \uc9c0\uae08\uc758 \uc628\ucc9c\ud0c0\uc6b4\uc73c\ub85c \uc7ac\uac1c\ubc1c\ub418\uc5c8\ub2e4. 14km\uc5d0 \uc774\ub974\ub294 \uc6a9\uc18c\uace8 \uad6c\uc808\uc591\uc7a5\uc740 \ud759 \ud55c \uc90c \uc5c6\ub294 \uae34 \ud1b5\ubc14\uc704\uace8\uacfc \uae4a\uc774\ub97c \ud5e4\uc544\ub9b4 \uc218 \uc5c6\ub294 \uc18c, \ud06c\uace0 \uc791\uc740 \ud3ed\ud3ec\ub4e4\uc774 \uc7a5\uad00\uc744 \uc774\ub8ec\ub2e4. \uadf8\ub9cc\ud07c \uae38\uc774 \ud5d8\ud558\uace0 \uc704\ud5d8\ud574 90\ub144\ub300\ub9cc \ud574\ub3c4 \uc77c\ubc18\uc778\ub4e4\uc740 \ub4e4\uc5b4\uac00\ubcfc \uc5c4\ub450\ub97c \ub0b4\uc9c0 \ubabb\ud558\ub358 \uacf3\uc774\uc5c8\ub2e4. \uc774\ud6c4 \uc8fc\ubbfc\ub4e4\uc5d0 \uc758\ud574 \uae38\uc774 \ub2e4\ub4ec\uc5b4\uc9c0\uace0 \uacf3\uacf3\uc5d0 \uc548\ub0b4\ud310\uc774 \uc124\uce58\ub418\uba74\uc11c \ub9ce\uc740 \uc0ac\ub78c\ub4e4\uc774 \ucc3e\uac8c \ub418\uc5c8\uc9c0\ub9cc \uc544\uc9c1\ub3c4 \ucd08\ubcf4\uc790\uc5d0\uac8c\ub294 \ubb34\ub9ac\ub2e4.'), STRINGDECODE('\uc544\ub984\ub2e4\uc6b4 \uc5ec\ub7ec \uacc4\uace1\ub4e4\uc744 \ub07c\uace0 \uc788\uc5b4 \uacc4\uace1\ud0d0\ud5d8\ucf54\uc2a4\ub85c \uc801\ud569\ud558\uba70, \uc0b0\ub9bc\uc774 \uc6b8\ucc3d\ud558\uace0 \ucc9c\uc5f0\ub178\ucc9c\uc628\ucc9c\uc778 \ub355\uad6c\uc628\ucc9c\uacfc \uc6a9\uc18c\uace8\uc758 \ud3ed\ud3ec\uc640 \uc18c\uac00 \ub9ce\uc740 \ub4f1 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b4 \uc810\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc6b8\uc9c4\uc870\uc528\uac00 \ub9e4\uc0ac\ub0e5\uc744 \ud558\ub2e4\uac00 \uc783\uc5b4\ubc84\ub9b0 \ub9e4\ub97c \uc774 \uc0b0\uc5d0\uc11c \ucc3e\uace0\ub294 \uc0b0 \uc774\ub984\uc744 \uc751\ubd09\uc774\ub77c \ud55c \ub4a4 \uadfc\ucc98\uc5d0 \ubd80\ubaa8\uc758 \ubb18\uc790\ub9ac\ub97c \uc4f0\uc790 \uc9d1\uc548\uc774 \ubc88\uc131\ud558\uc600\ub2e4\ub294 \uc804\uc124\uc774 \uc804\ud574\uc9c0\uace0 \uc788\uc74c. \uc815\uc0c1\uc5d0\uc11c \uba40\ub9ac \ubc31\uc554\uc0b0, \ud1b5\uace0\uc0b0, \ud568\ubc31\uc0b0, \ud0dc\ubc31\uc0b0\uc744 \uc870\ub9dd\ud560 \uc218 \uc788\ub294 \uacf3\uc73c\ub85c \uc720\uba85'), STRINGDECODE('\ud5e4\uc544\ub9b4\uc218 \uc5c6\ub294 \uacc4\uace1 \uc904\uc904\uc774 \uc774\uc5b4\uc838'), STRINGDECODE('\u25cf \uc219\ubc15 \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub355\uad6c\ub9ac\uc640 \ud48d\uace1\ub9ac\uc758 \uc219\ubc15\uc2dc\uc124\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR>\ub355\uad6c\ub9ac\uc5d0\ub294 \ud638\ud154\ub355\uad6c\uc628\ucc9c \u260e054-782-0677, \ubcbd\uc0b0\ub355\uad6c\uc628\ucc9c\ucf58\ub3c4 \u260e054-783-0811 \uc678\uc5d0 \ubbfc\ubc15\uc774 20\uc5ec \uac00\uad6c \uc788\uace0 \ud48d\uace1\ub9ac\uc5d0\ub294 \ud48d\uace1\ud1b5\ub098\ubb34\uc9d1 \u260e033-573-0777, \uacc4\uace1\ubbfc\ubc15 \u260e572-7130, \uace0\ud5a5\ubbfc\ubc15 \u260e572-2133, \ubaa8\ub85c\uc1e0\ub18d\uc6d0 \u260e572-4244\uc774 \uc788\ub2e4. \uadfc\ucc98\uc758 \uc0bc\ucc99\uad6d\uc720\ub9bc\uad00\ub9ac\uc18c\uc5d0\uc11c \uc6b4\uc601\ud558\ub294 \uac00\uace1\uc790\uc5f0\ud734\uc591\ub9bc\ub3c4 \uc88b\uc744 \uac83\uc774\ub2e4. \u260e573-4657 <BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc0bc\ucc99\uc2dc\uccad \uad00\uad11\uac1c\ubc1c\uacfc \u260e033-570-3530'), STRINGDECODE('\uc628\uc815\ub9ac \uae30\uc810\uc740 \uc6b8\uc9c4, \ub355\ud48d\ub9ac \uae30\uc810\uc740 \ud0dc\ubc31\uc774\ub2e4. \uc6b8\uc9c4\u2192\ub355\uad6c\uc628\ucc9c : 1\uc77c 15\ud68c \uc6b4\ud589, \ud0dc\ubc31\u2192\ud48d\uace1(\ub355\ud48d\ub9ac \uc785\uad6c) : 1\uc77c 4\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(70, TIMESTAMP '2019-08-14 15:46:34.934', TIMESTAMP '2019-08-14 15:47:21.294', '45', STRINGDECODE('\uc7a5\uc548\uc0b0\uc740 \uc7a5\uc218\uad70\uacfc \ud568\uc591\uad70\uc758 \uacbd\uacc4\uc120\uc5d0 \uc788\ub294 \ubc31\ub450\ub300\uac04\uc0c1\uc758 \uc601\ucde8\uc0b0\uc5d0\uc11c \uc11c\ucabd\uc73c\ub85c \uac08\ub77c\uc9c4 \ud638\ub0a8\uc815\ub9e5\uc758 \uccab\uba38\ub9ac\uc5d0 \uc19f\uc544 \uc788\ub294 \uae30\ubd09\uc774\ub2e4. \uae30\ubd09\uc778 \uc7a5\uc548\uc0b0\uc5d0\uc11c \uc12c\uc9c4\uac15\uacfc \uae08\uac15\uc744 \uacbd\uacc4\ub85c \ud638\ub0a8\uc9c0\ubc29\uacfc \ud638\uc11c\uc9c0\ubc29\uae4c\uc9c0 \ubee3\uc5b4 \uad6d\ud1a0\uc758 \uc57d 25%\ub97c \uc810\uc720\ud558\uace0 \ud48d\uc694\ub85c\uc6b4 \uac15\uc0b0\uc744 \uc774\ub8e8\uace0 \uc788\ub294 \uc870\uc0b0, \uc601\uc0b0\uc774\uae30\uc5d0 \ub354\uc6b1 \uc544\ub984\ub2f5\uac8c \uac00\uafb8\uc5b4 \ub098\uac00\uc57c \ud55c\ub2e4. \uc7a5\uc548\uc0b0\uc740 \uc774 \uc0b0\ub9e5\uc5d0 \uc19f\uc544 \uc788\ub294 \uc0b0\uc911\uc5d0\uc11c\ub3c4 \uc81c\uc77c \ub192\uace0 \ud638\ub0a8,\uc9c0\ubc29\uc5d0\uc11c\ub294 \uc9c0\ub9ac\uc0b0 \ub355\uc720\uc0b0 \ub0a8\ub355\uc720\uc0b0\uc5d0 \uc774\uc5b4 4\ubc88\uc9f8\ub85c \ub192\uc740 \uc0b0\uc774\ub2e4.<BR><BR>\uc774 \uc0b0\uc740 1986\ub144\uc5d0 \uad70\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\ub2e4. \uae30\uc554\uad34\uc11d\uacfc \uc6b8\ucc3d\ud55c \uc6d0\uc2dc\uc218\ub9bc\uc744 \uc790\ub791\ud558\ub294 \uc7a5\uc548\uc0b0\uc740 \ub355\uc0b0\uc6a9\uc18c\uc640 \ubc29\ud654\ub3d9, \uc9c0\uc9c0\uacc4\uace1 \uc9c0\uad6c\ub85c \ub098\ub258\uc5b4\uc838 \uc544\ub984\ub2e4\uc6b4 \uc808\uacbd\uc744 \uc774\ub8e8\ub294 \uad00\uad11\uc9c0\ub2e4.'), STRINGDECODE('\u2460 \ubb34\ub839\uace0\uac1c-\uc815\uc0c1-\uc911\ubd09-\ud558\ubd09-\uc5b4\uce58\uc7ac-\ubc95\ub144\ub3d9 (\ucd1d 3\uc2dc\uac04 50\ubd84)<BR>\u2461 \ubb34\ub839\uace0\uac1c-\uc815\uc0c1-\uc911\ubd09-\ub355\ucc9c-\ud569\uc218\uc9c0\uc810-\uc5f0\uc8fc (\ucd1d 4\uc2dc\uac04 55\ubd84)'), '35.623657', '127.511519', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \uc7a5\uc218\uad70 \uc7a5\uc218\uc74d, \uacc4\ub0a8\uba74'), 2511992400, 1237, STRINGDECODE('\uc7a5\uc548\uc0b0'), STRINGDECODE('&lt;\uc5ec\ub984\uc5d4 \uacc4\uace1, \uac00\uc744\uc5d4 \uc5b5\uc0c8\ub85c \uc720\uba85\ud55c \uc0b0&gt;<BR>\uc7a5\uc548\uc0b0\uc740 \uae08\ub0a8\uc815\ub9e5\uacfc \ud638\ub0a8\uc815\ub9e5\uc744 \uac70\ub290\ub9ac\uba70 \ubc31\ub450\ub300\uac04\uc758 \uae30\uc6b4\uc744 \ucda9\uccad\ub3c4\uc640 \uc804\ub77c\ub3c4\uc5d0 \uc804\ud558\ub294 \ud638\ub0a8\uc758 \uc885\uc0b0\uc774\ub2e4. \uc5ec\ub984\uc5d0\ub294 \ud53c\uc11c\uc9c0, \uac00\uc744\uc5d0\ub294 \uc5b5\uc0c8\uc640 \ub2e8\ud48d\uc73c\ub85c \uc720\uba85\ud55c\ub370 \uc774 \uc77c\ub300\uc758 \uacc4\uace1\uacfc \uc232\uc758 \uacbd\uad00\uc774 \ub6f0\uc5b4\ub098 1986\ub144 \uc7a5\uc218\uad70 \uad70\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\ub2e4. \uc774 \uc0b0\uc5d0\ub294 26\uac1c\uc758 \ud06c\uace0 \uc791\uc740 \uacc4\uace1\uacfc 7\uac1c\uc758 \uc5f0\ubabb, 14\uac1c\uc758 \uae30\uc554\uad34\uc11d, 5\uac1c\uc758 \uc57d\uc218\ud130 \ub4f1 \uc5f0\ubabb\uacfc \ud3ed\ud3ec\uac00 \uc808\uacbd\uc744 \uc774\ub8ec\ub2e4. \uc7a5\uc548\uc0b0 \ub3d9\ucabd\ub2a5\uc120\uc5d0\ub294 \uad11\ud65c\ud55c \uc5b5\uc0c8\ubc2d\uc774 \ud3bc\uccd0 \uc788\uc5b4 \uac00\uc744\uc774\uba74 \uc0b0\ub2a5\uc120 \uc804\uccb4\uac00 \uc5b5\uc0c8\ub85c \ub4a4\ub36e\uc778\ub2e4. <BR><BR>\uc7a5\uc548\uc0b0 \ub4e4\uba38\ub9ac\ub294 \uc5ec\ub7ec \uad70\ub370\uac00 \uc788\uc73c\ub098 \ub355\uc0b0\uacc4\uace1\uc5d0\uc11c \ubc94\ub144\ub3d9\uc73c\ub85c \uac00\ub294 \uae38\ubaa9\uc744 \ub4e4\uba38\ub9ac\ub85c \ud558\ub294 \uacf3\uc774 \uc88b\ub2e4. \ub4f1\uc0b0\ub85c \uc785\uad6c\uc5d0\ub294 \uc548\ub0b4\ub3c4\uc640 \ud654\uc7a5\uc2e4\uc774 \uc788\ub2e4.'), STRINGDECODE('\ub355\uc0b0\uacc4\uace1\uc744 \ube44\ub86f\ud55c \ud06c\uace0 \uc791\uc740 \uacc4\uace1\uacfc \uc717\uc6a9\uc18c, \uc544\ub7ab\uc6a9\uc18c \ub4f1 \uc5f0\ubabb \ubc0f \uae30\uc554\uad34\uc11d\uc774 \uc0b0\ub9bc\uacfc \uc5b4\uc6b0\ub7ec\uc838 \uad70\ub9bd\uacf5\uc6d0(1986\ub144)\uc73c\ub85c \uc9c0\uc815\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0b0\ub4f1\uc5d0\uc11c \ub3d9\ucabd \ub2a5\uc120\uc73c\ub85c \ud3bc\uccd0\uc9c4 \uad11\ud65c\ud55c \uac08\ub300\ubc2d\uacfc \ub355\uc0b0\uc6a9\uc18c\uacc4\uace1\uc774 \uc720\uba85'), ' ', STRINGDECODE('\u25cf \uc219\ubc15 \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub355\uc0b0\uacc4\uace1 \uadfc\ucc98\uc5d0 \ubbfc\ubc15\uacfc \uc0b0\uc7a5\uc774 \uc788\uace0 \uc57c\uc601\uc7a5\ub3c4 \uac16\ucd94\uc5b4\uc838 \uc788\ub2e4. <BR>\ub17c\uac1c\uc0dd\uac00\uc9c0 \ubd80\uadfc\uc758 \ub17c\uac1c\uc218\ub828\uc6d0(\u260e063-352-1374)\uc740 \ud3d0\uad50\ub97c \uac1c\uc870\ud55c \uc219\ubc15\uc2dc\uc124\ub85c \uaddc\ubaa8\uac00 \ud06c\ub2e4. <BR>\ub355\uc0b0\uacc4\uace1 \ucd08\uc785\uc758 \ubb34\ub4dc\ub9ac\uc0b0\uc7a5(\u260e063-351-8487)\uc740 \uc2dd\uc0ac\ub3c4 \uacb8\ud560 \uc218 \uc788\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc7a5\uc548\uc0b0\uad70\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c, \uc7a5\uc218\uad70\uccad \uc0b0\ub9bc\uacfc (\u260e063-351-8515, 350-2413~4)'), STRINGDECODE('\uae30\uc810\uc740 \uc7a5\uc218\ub2e4. \uc7a5\uc218\uc5d0\uc11c \ub355\uc0b0\ub9ac \ud589 \ubc84\uc2a4\uac00 08:35, 16:30 \ub450 \ucc28\ub840 \uc6b4\ud589, \ud0dd\uc2dc \uc774\uc6a9\uc2dc \uc694\uae08\uc740 1\ub9cc\uc6d0 \uc548\ud30e\uc774\ub2e4. \ubb34\ub839\uace0\uac1c\ub85c \uac00\ub294 \ub300\uc911\uad50\ud1b5\ud3b8\uc740 \uc5c6\uc73c\ubbc0\ub85c \uace0\uac1c \uc544\ub798\ub9c8\uc744\uc778 \uad34\ubaa9\uc774\ub098 \ub300\uace1\ub9ac\uae4c\uc9c0 \uac04 \ub2e4\uc74c \uace0\uac1c\uae4c\uc9c0\ub294 \uac78\uc5b4\uac00\uc57c \ud55c\ub2e4. \uacc4\ub0a8\uba74\uc5d0\uc11c \uad34\ubaa9\uae4c\uc9c0\ub294 \ud558\ub8e8 6\ud68c \uc6b4\ud589 \uad70\ub0b4\ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4. \ub300\uace1\ub9ac\uae4c\uc9c0\ub294 \uc7a5\uacc4\uba74\uc5d0\uc11c \ud558\ub8e8 4\ud68c \ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(71, TIMESTAMP '2019-08-14 15:46:34.935', TIMESTAMP '2019-08-14 15:47:21.896', '48', STRINGDECODE('\uc7ac\uc57d\uc0b0\uc740 \uc601\ub0a8 \uc54c\ud504\uc2a4 \uc0b0\uad70\uc911\uc758 \ud558\ub098\ub85c \uc601\ub0a8 \ubc00\uc591 \uccad\ub3c4 \uc77c\ub300\uc5d0 \uc704\uce58\ud574 \uc788\ub2e4. \ud574\ubc1c 1,000\ubbf8\ud130 \uc774\uc0c1\uc758 \uc900\ubd09\ub4e4\ub85c \uc774\ub8e8\uc5b4\uc9c4 \uc7ac\uc57d\uc0b0(\uc0ac\uc790\ubd09)\uc740 \uc0b0\uc138\uac00 \ubd80\ub4dc\ub7ec\uc6b0\uba74\uc11c\ub3c4 \uc815\uc0c1 \uc77c\ub300\uc5d0\ub294 \uac70\ub300\ud55c \uc554\ubcbd\uc744 \uac16\ucd94\uace0 \uc788\ub2e4. <BR><BR>125\ub9cc\ud3c9\uc5d0 \uc774\ub974\ub294 \uc7ac\uc57d\uc0b0 \ub3d9\ucabd\uc758 \uc0ac\uc790\ud3c9 \uace0\uc6d0\uc740 \uad11\ud560\ud55c \ubd84\uc9c0\uac00 \uc628\ud1b5 \uc5b5\uc0c8\ud480\ub85c \ub4a4\ub36e\ud600 \uc788\ub2e4. \uc6b0\ub9ac\ub098\ub77c\uc5d0\uc11c \uac00\uc7a5 \ub113\uc740 \uc5b5\uc0c8\ubc8c\ud310\uc774\ub2e4. \uc5b5\uc0c8\ud480\uc774 \ubc00\uc9d1\ud574 \uc790\ub77c\ub294 \uacf3\ub9cc\ub3c4 5\ub9cc\ud3c9\uc5d0 \uc774\ub978\ub2e4. \uc7ac\uc57d\uc0b0\uc740 \ud574\ubc1c 1,108m\uc758 \uc218\ubbf8\ubd09\uacfc 1,189m\uc758 \uc0ac\uc790\ubd09\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub2e4. \uc0ac\uc790\ud3c9\uace0\uc6d0\uc740 \ub450 \ubd09\uc6b0\ub9ac \uc0ac\uc774\uc758 \ud574\ubc1c 800m \uc9c0\uc810\ubd80\ud130 \uc644\ub9cc\ud55c \ud0c0\uc6d0\ud615\uc758 \uc5b8\ub355\ub4e4\ub85c \uc774\uc5b4\uc9c4\ub2e4.\uc0ac\uc790\ud3c9 \uc5b5\uc0c8\ub294 \uc5b4\ub978 \uac00\uc2b4\uc815\ub3c4 \ubc16\uc5d0 \uc548 \uc62c \uc815\ub3c4\ub85c \ud0a4\uac00 \uc791\ub2e4. <BR><BR>\uc0b0\uc544\ub798 \ubc2d\ub451\uc774\ub098 \uae38\uac00\uc758 \uc5b5\uc0c8\uc5d0 \ube44\ud558\uba74 \uc808\ubc18\ubc16\uc5d0 \ub418\uc9c0 \uc54a\ub294\ub2e4. \uc78e\uc0c8\ub3c4 \uac00\ub298\uace0 \ud22c\ubc15\ud558\ub2e4. \uaf43\uc774\uc0ad\uc740 \uac70\uce5c \uc0b0\uc815\uc758 \ubc14\ub78c\uc5d0 \ub2f3\uc544\uc11c\uc778\uc9c0 \ubb49\ud22d\ud558\uace0 \uc9e7\ub2e4. \uadf8\ub798\uc11c \uac00\ub294 \ubc14\ub78c\uc5d0\ub294 \uc774\uc0ad \ub05d\uc758 \ub0ad\ucc3d\uac70\ub9bc\uc744 \ubcf4\uae30 \uc5b4\ub835\ub2e4.'), STRINGDECODE('\u2460 \ubc30\ub0b4\uace8-\uc0ac\uc790\ud3c9\uace0\uc6d0-\uc0ac\uc790\ubd09-\uc218\ubbf8\ubd09-\uce35\uce35\ud3ed\ud3ec-\ud64d\ub8e1\ud3ed\ud3ec-\ud45c\ucda9\uc0ac(\ucd1d 4\uc2dc\uac04 10\ubd84)<BR>\u2461 \ub9e4\ud45c\uc18c-\ub9e4\ubc14\uc704\ub9c8\uc744-\ud544\ubd09-\uc0c1\ud22c\ubd09-\uc0ac\uc790\ubd09-\uc218\ubbf8\ubd09-\uce35\uce35\ud3ed\ud3ec-\ud64d\ub8e1\ud3ed\ud3ec-\ud45c\ucda9\uc0ac(\ucd1d 5\uc2dc\uac04)<BR>\u2462 \uc5bc\uc74c\uace8-\uc0ac\uc790\ubd09-\uc218\ubbf8\ubd09-\uce35\uce35\ud3ed\ud3ec-\ud64d\ub8e1\ud3ed\ud3ec-\ud45c\ucda9\uc0ac(\ucd1d 4\uc2dc\uac04 45\ubd84)'), '35.50549', '128.910686', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ubc00\uc591\uc2dc \ub2e8\uc7a5\uba74\u318d\uc0b0\ub0b4\uba74, \uc6b8\uc0b0\uad11\uc5ed\uc2dc \uc6b8\uc8fc\uad70 \uc0c1\ubd81\uba74'), 2807063600, 1119, STRINGDECODE('\uc7ac\uc57d\uc0b0'), STRINGDECODE('&lt;\ud45c\ucda9\uc0ac \uc5d0\uc6cc\uc2fc \ud0c0\uc6d0\ud615\uc758 \uc5ec\ub35f \ubd09\ub9cc&gt;<BR>\uc67c\uc190\uc73c\ub85c \uc4f4 \u4e0b\uc790 \ud615\uad6d \uc601\ub0a8\uc54c\ud504\uc2a4\uc758 \uc810\uc5d0 \ud574\ub2f9\ud558\ub294 \uc0b0\uc774\ub2e4. \uadf8\ub9cc\ud07c \uae4a\uc774 \uc228\uc5b4\uc788\ub294 \uc0b0\uc911 \uc18d\uc758 \uc0b0\uc73c\ub85c \ube59 \ub458\ub7ec \ud0c0\uc6d0\ud615 \ub2a5\uc120\uc774 \uae38\uc9c0\ub97c \ud488\uace0\uc788\ub2e4. \ud45c\ucda9\uc0ac. \uad6d\ubcf4 75\ud638 \uccad\ub3d9\ud568\uc740\ud5a5\uc644(\u542b\u9280\u9999\u73a9)\uacfc \ubcf4\ubb3c 467\ud638 \uc0bc\uce35\uc11d\ud0d1, \uc0ac\uba85\ub300\uc0ac \uc720\ubb3c 200\uc5ec\uc810\uc744 \ubcf4\uad00\ud558\uace0\uc788\ub294 \uc808\uc774\ub2e4. <BR><BR>\uc0b0\uc740 \ud761\uc0ac \uc774 \uae38\uc9c0\uc640 \uac70\uae30 \uc788\ub294 \ubcf4\ubb3c\uc744 \uc9c0\ud0a4\uae30 \uc704\ud574 \uc0dd\uaca8\ub09c \ub4ef\ud558\ub2e4. \ud544\ubd09, \uc0c1\ud22c\ubd09, \uc0c1\ubd09 \uc0ac\uc790\ubd09(1189m), \uc218\ubbf8\ubd09(1108m), \ucf54\ub07c\ub9ac\ubd09(899m), \uc7ac\uc57d\ubd09(953.8m), \ud5a5\ub85c\uc0b0(979.1m), \uc30d\ubd09(821.8m)\uc758 \uc5ec\ub35f \ubd09\ub9cc(\u5cf0\u5dd2)\uc774 \uc54c \ud488\uc740 \uc0c8\uc6b0\uc758 \ubaa8\uc591\uc744 \ud558\uace0\uc788\ub294 \uac83\uc774\ub2e4. 800\ubbf8\ud130 \ub4f1\uace0\uc120\uc744 \uc774\uc5b4\ubcf4\uba74 \uc790\uad81 \uc18d\uc758 \uc544\uc774\ucc98\ub7fc \ubcf4\uc774\uae30\ub3c4 \ud55c\ub2e4. \uc778\uac04\uc744 \uac10\ub3d9\uc2dc\ud0a4\ub294, \uc808\ubb18\ud55c \uc790\uc5f0\uc758 \uc0dd\uae40\uc774\ub2e4. <BR><BR>120\ub9cc\ud3c9\uc758 \uc5b5\uc0c8\ucd08\uc6d0 \uc0ac\uc790\ud3c9\uc73c\ub85c \uc720\uba85\ud558\ub2e4. \uadf8\ub7ec\ub098 \uac70\uae30 \ubc2d\uc744 \uce58\uace0 \uc5fc\uc18c\ub97c \ud0a4\uc6e0\ub358 \ud0d3\uc5d0 \ub300\ucd08\uc6d0\uc758 \ud48d\ubaa8\ub294 \uc5c6\ub2e4. \uba85\ubd88\ud5c8\uc804(\u540d\u4e0d\u865b\u50b3). \uadf8\ub7fc\uc5d0\ub3c4 \uac00\uc744\uc774\uba74 \ubb47 \uc0ac\ub78c\ub4e4 \uadf8 \ud48d\uacbd\ub9cc \ubcf4\ub7ec \ubab0\ub824\ub4e0\ub2e4. \ubcd1\ub4e0 \uc2e0\ub77c \uc655\uc790\uac00 \uc774 \uc0b0 \uc57d\uc218\ub97c \ub9c8\uc2dc\uace0 \ubcd1\uc774 \ub098\uc558\ub2e4 \ud558\uc5ec \uc7ac\uc57d\uc0b0\uc774\ub77c\uace0 \ud588\ub2e4\ub294 \uc804\uc124\uc774\ub2e4. \uc6d0\ub798\ub294 \uadf8 \uc57d\uc218 \uc774\ub984\uc744 \ub534 \uc601\uc815\uc0ac(\u9748\u4e95\u5bfa)\uc758 \uc0b0\uc774\uc5c8\ub294\ub370 \uc784\uc9c4\uc65c\ub780\uc758 \uc601\uc6c5 \uc0ac\uba85\ub300\uc0ac \uc720\ubb3c\uc744 \uae38\uc9c0\ub85c \uc62e\uae30\uba74\uc11c \uc808 \uc774\ub984\uacfc\ub294 \uc601 \uc5b4\uc6b8\ub9ac\uc9c0 \uc54a\uc740 \ud45c\ucda9\uc0ac(\u8868\u5fe0\u5bfa)\uac00 \uc8fc\uc778\uc774 \ub418\uc5c8\ub2e4.'), STRINGDECODE('\uc0b0\uc138\uac00 \ubd80\ub4dc\ub7ec\uc6b0\uba74\uc11c\ub3c4 \uc815\uc0c1 \uc77c\ub300\uc5d0\ub294 \uac70\ub300\ud55c \uc554\ubcbd\uc744 \uac16\ucd94\uace0 \uc788\uc5b4 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70 \uc6b0\ub9ac\ub098\ub77c\uc5d0\uc11c \uac00\uc7a5 \ub113\uc740 \uc5b5\uc0c8\ubc2d\uc778 \uc0ac\uc790\ud3c9\uc774 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0bc\ubcf5 \ub354\uc704\uc5d0 \uc5bc\uc74c\uc774 \uc5b4\ub294 \ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c224\ud638 \uc5bc\uc74c\uace8\uc774 \uc788\uc74c. \uc2e0\ub77c \uc9c4\ub355\uc5ec\uc655\ub54c \ucc3d\uac74\ud558\uace0 \uc11c\uc0b0\ub300\uc0ac\uac00 \uc758\ubcd1\uc744 \ubaa8\uc9d1\ud55c \uacf3\uc778 \ud45c\ucda9\uc0ac\uac00 \uc720\uba85'), STRINGDECODE('\ud558\ub298\uac70\ub9ac\ub294 \uc5b5\uc0c8 \ud488\uc5d0 \ub098\ub97c \ub9e1\uae34\ub2e4'), STRINGDECODE('\u25cf \uc219\ubc15 \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ud45c\ucda9\uc0ac \uc544\ub798 \uad6c\ucc9c\ub9ac\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. \ubc1c\ub80c\ud0c0\uc778\ubaa8\ud154 \u260e055-351-2388, \uc5d0\ucfe0\uc2a4\ubaa8\ud154 \u260e055-351-0347. \uc0ac\uc790\ubd09\uacfc \ub2a5\ub3d9\uc0b0 \uc0ac\uc774\uc758 \uc0d8\ubb3c\uc0b0\uc7a5(\u260e055-356-7664)\uc5d0\uc11c \ubbfc\ubc15\uc744 \ud560 \uc218\ub3c4 \uc788\ub2e4. \uc8fd\uc804\uc744 \ub4e4\uba38\ub9ac\ub85c \ud560 \uacbd\uc6b0\uc5d0\ub294 \uc2e0\ubd88\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uac00\uae5d\ub2e4. \u260e055-383-6493<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ubc00\uc591\uc2dc\uccad \ubb38\ud654\uccb4\uc721\uacfc \u260e055-359-5643, \ud45c\ucda9\uc0ac \u260e055-352-1070'), STRINGDECODE('\uae30\uc810\uc740 \ubc00\uc591\uc774\ub2e4. \uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110\uc5d0\uc11c \ud45c\ucda9\uc0ac\ud589 \uc9c1\ud589\uc774 30\ubd84 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud55c\ub2e4. \ubc00\uc591\uc73c\ub85c\ub294 \uacbd\ubd80\uc120 KTX\uac00 7\ud68c, \uc77c\ubc18\uc5f4\ucc28\ub294 26\ud68c(\uc11c\uc6b8\ubc1c \uae30\uc900) \ub2e4\ub2cc\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(72, TIMESTAMP '2019-08-14 15:46:34.935', TIMESTAMP '2019-08-14 15:47:22.486', '45', STRINGDECODE('\uc801\uc0c1\uc0b0\uc740 \uc0ac\ubc29\uc774 \uae4e\uc544\uc9c0\ub978 \ub4ef\ud55c \uc554\ubcbd\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uc73c\uba70, \uadf8 \uc808\ubcbd \uc8fc\ubcc0\uc5d0 \uc720\ub09c\ud788\ub3c4 \ube68\uac04 \ub2e8\ud48d\ub098\ubb34\uac00 \ub9ce\uc774 \uc11c\uc2dd\ud558\uc5ec \uac00\uc744\ucca0\uc774\uba74 \ub9c8\uce58 \uc628 \uc0b0\uc774 \ube68\uac04 \uce58\ub9c8\ub97c \uc785\uc740 \uc5ec\uc778\ub124\uc758 \ubaa8\uc2b5\uacfc \uac19\ub2e4 \ud558\uc5ec \ubd99\uc5b4\uc9c4 \uc774\ub984\uc774\ub2e4. \uc774 \uc0b0\uc5d0\ub294 \uc7a5\ub3c4\ubc14\uc704, \uc7a5\uad70\ubc14\uc704 \ub4f1 \uc790\uc5f0 \uba85\uc18c\uc640 \ud568\uaed8 \ucd5c\uc601 \uc7a5\uad70\uc774 \uac74\uc758\ud558\uc5ec \ucd95\uc870\ud588\ub2e4\ub294 \uc801\uc0c1\uc0b0\uc131(\uc0ac\uc801 \uc81c 146\ud638)\uc774 \uc788\ub2e4. <BR><BR>\uc774\ub294 \uace0\ub824 \uacf5\ubbfc\uc655 23\ub144(1374) \ucd5c\uc601 \uc7a5\uad70\uc774 \ud0d0\ub77c\ub97c \ud1a0\ubc8c\ud55c \ud6c4 \uadc0\uacbd\uae38\uc5d0 \uc774 \uacf3\uc744 \uc9c0\ub098\ub2e4\uac00 \uc0b0\uc758 \ud615\uc138\uac00 \uc694\uc0c8\ub85c\uc11c \uc801\uc9c0\uc784\uc744 \uc54c\uace0 \uc655\uc5d0\uac8c \ucd95\uc131\uc744 \uac74\uc758\ud558\uc5ec \uac74\uc124\ub41c \uac83\uc774\ub77c \ud55c\ub2e4. \ud604\uc7ac\uc758 \uc131\uc740 \uc870\uc120 \uc778\uc8706\ub144(1628\ub144)\uc5d0 \ub2e4\uc2dc \uc313\uc740 \uac83\uc73c\ub85c\uc11c \ub458\ub808\uac00 8.143\u339e\uc5d0 \uc774\ub978\ub2e4. \uc801\uc0c1\uc0b0\uc131 \uc548\uc5d0\ub294 \uace0\ucc30 \uc548\uad6d\uc0ac \ub4f1 \uc720\uc11c\uae4a\uc740 \ubb38\ud654\uc720\uc801\uc774 \uc788\uc5b4 \uc774 \uacf3\uc744 \ucc3e\uc544\uc628 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \uc6b4\uce58\ub97c \ub354\ud574 \uc900\ub2e4.\ud55c\ud3b8 \uace0\ub824 \ucda9\ub82c\uc655 3\ub144(1227\ub144) \uc6d4\uc778\ud654\uc0c1\uc774 \ucc3d\uac74\ud588\ub2e4\uace0 \uc804\ud574\uc9c0\ub294 \uc548\uad6d\uc0ac \ubc0f \uc870\uc120\uc2dc\ub300 \uc778\uc870 21\ub144(1643\ub144)\uc5d0 \ucc3d\uac74\ud55c \ud638\uad6d\uc0ac\ub3c4 \uc788\ub2e4.'), '&nbsp;', '35.934859', '127.672885', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \ubb34\uc8fc\uad70 \uc801\uc0c1\uba74'), 2505061400, 1031, STRINGDECODE('\uc801\uc0c1\uc0b0'), STRINGDECODE('&lt;\ubd89\uc740\ud654\uac15\uc554 \uc808\ubcbd \ub450\ub978 \ub355\uc720\uc0b0 \uc804\ub9dd\ub300&gt;<BR>\ucc9c\ubbf8\ud130\uac00 \ub118\ub294, \ub0ae\uc9c0 \uc54a\uc740 \uc0b0 \uc815\uc0c1 \uc77c\ub300\uac00 \ud3c9\ud3c9\ud55c \ud2b9\uc774\uc9c0\ud615\uc774\ub2e4. \uc11c\ucabd \uc0ac\uba74\uc740 \ub610 \ubd89\uc740 \ud654\uac15\uc554 \uc808\ubcbd\uc774 \ub760\ub97c \ub458\ub7ec \uc608\ub098 \uc624\ub298\uc774\ub098 \uc0ac\ub78c\ub4e4\uc758 \uad00\uc2ec\uc744 \ub04c\uc5b4\uc654\ub2e4. \uc61b\ub0a0\uc5d0\ub294 \uac70\uae30 \uc0b0\uc131\uc744 \uc313\uace0 \uc655\uc870\uc2e4\ub85d\uc744 \ub450\uc5c8\uc73c\uba70 \uc624\ub298\uc740 \uc591\uc218\ubc1c\uc804\uc18c \uc717\uc800\uc218\uc9c0\ub97c \ub9cc\ub4e4\uc5b4 \uc6d0\uc790\ub825\ubc1c\uc804\uc18c \ud558\ub098 \ub9cc\ud07c\uc758 \uc804\uae30\ub97c \uc0dd\uc0b0\ud55c\ub2e4(\uc0ac\uc2e4\uc740 \uadf8 \ud558\ub098\uac00 \uba48\ucdc4\uc744 \uacbd\uc6b0\ub97c \ub300\ube44\ud55c \uc608\ube44\uc6a9\uc774\ub2e4). <BR><BR>\uc0c1\ubd09 \uae30\ubd09\uacfc \ubd81\ucabd\uc758 \ud5a5\ub85c\ubd09\uc774 \ubc0b\ubc0b\ud558\uac8c \uc774\uc5b4\uc838 \ubd09\ub9cc\ubbf8\ub294 \uc5c6\ub2e4. \uc7a5\uad70\ubc14\uc704, \uc7a5\ub3c4\ubc14\uc704 \ub4f1\uc758 \uae30\uc554\uc5d0\ub2e4 \ucc9c\uc77c\ud3ed\ud3ec, \uc1a1\ub300\ud3ed\ud3ec\uac00 \uc788\uc9c0\ub9cc \uacc4\ub958\ubbf8\ub85c \uc5f0\uacb0\ub418\uc9c0\ub294 \uc54a\ub294\ub2e4. \uc0b0 \uc704\uc758 \uc808 \uc548\uad6d\uc0ac\ub294 \ub610 \uc800\uc218\uc9c0\ub97c \ub9cc\ub4e4\uba74\uc11c \uc790\ub9ac\ub97c \uc62e\uaca8 \ubcfc\ub9cc\ud55c \uad6c\uc11d\uc774 \uc5c6\ub2e4. \uc717\uc800\uc218\uc9c0\uac00 \uc788\ub294 \uae4c\ub2ed\uc5d0 \uc815\uc0c1\ubd80\uae4c\uc9c0 \ud3ec\uc7a5\ub3c4\ub85c\ub294 \uc798 \ub098\uc788\ub2e4. \uadf8\ub798 \uac70\uae30\uae4c\uc9c0 \ucc28\ub97c \ud0c0\uace0 \uc62c\ub77c\uac00 \ub355\uc720\uc0b0 \ubc14\ub77c\ubcf4\uae30\ub294 \uc88b\ub2e4. <BR><BR>\uac00\uc744 \ub2e8\ud48d\uc774 \uc5ec\uc778\uc758 \ubd89\uc740 \uce58\ub9c8 \uac19\uc544 ''\ubd89\uc744 \uc801''\uc790, ''\uce58\ub9c8 \uc0c1''\uc790 \uc801\uc0c1\uc0b0\uc774\ub77c\uace0 \ud588\ub2e4\ub294\ub370 \uc18c\uac00 \ud558\ud488\ud560 \uc18c\ub9ac\ub2e4. \uac00\uc744\uc5d0 \ubd89\uc9c0 \uc54a\uc740 \uc0b0\uc774 \uc5b4\ub514 \uc788\uc73c\uba70 \uac00\uc744 \uc544\ub2cc \uacc4\uc808\uc5d0\ub294 \uadf8\ub7fc \ubb50\ub77c \ubd88\ub7ec\uc57c\ud55c\ub2e8 \ub9d0\uc778\uac00? \uce58\ub9c8\ubc14\uc704\uac00 \uc788\uc5b4 \uc0c1\uc0b0\uc73c\ub85c \ubd88\ub838\ub294\ub370 \ub450 \uc790\ub85c \ubd88\uc548\ud574 \uc55e\uc5d0 ''\uc801''\uc790\ub97c \ub367\ubd99\uc600\ub2e4\ub294 \uac8c \ud569\ub9ac\uc801\uc778 \ud574\uc11d\uc77c \uac83\uc774\ub2e4.'), STRINGDECODE('\uac00\uc744\uc5d0 \ub9c8\uce58 \uc628 \uc0b0\uc774 \ube68\uac04 \uce58\ub9c8\ub97c \uc785\uc740 \uc5ec\uc778\ub124\uc758 \ubaa8\uc2b5\uacfc \uac19\ub2e4 \ud558\uc5ec \uc774\ub984\uc774 \ubd99\uc5ec\uc9c8 \uc815\ub3c4\ub85c \uacbd\uad00\uc774 \ub6f0\uc5b4\ub098\uba70 \ub355\uc720\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\uad6c\uc5ed\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uace0\ub824 \uacf5\ubbfc\uc655 23\ub144(1374) \ucd5c\uc601 \uc7a5\uad70\uc774 \ud0d0\ub77c\ub97c \ud1a0\ubc8c\ud55c \ud6c4 \uadc0\uacbd\uae38\uc5d0 \uc774 \uacf3\uc744 \uc9c0\ub098\ub2e4\uac00 \uc0b0\uc758 \ud615\uc138\uac00 \uc694\uc0c8\ub85c\uc11c \uc801\uc9c0\uc784\uc744 \uc54c\uace0 \uc655\uc5d0\uac8c \uac74\uc758\ud558\uc5ec \ucd95\uc131\ub41c \uc801\uc0c1\uc0b0\uc131(\uc0ac\uc801 \uc81c146\ud638)\uacfc \uc548\uad6d\uc0ac \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ub2e8\ud48d\uc774 \uc544\ub984\ub2e4\uc6b4 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ubb34\uc8fc\uc74d\ub0b4 \uc5ec\uad00\uc744 \uc774\uc6a9\ud55c\ub2e4. \ubb34\uc8fc\uadf8\ub9b0\ubaa8\ud154\u260e063-322-7231 \uc11c\uc6b8\ud30c\ud06c\uc7a5\u260e063-322-2233 \uc911\uc559\uc5ec\uad00\u260e063-322-2931<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub355\uc720\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-322-3174~5'), STRINGDECODE('\uae30\uc810\uc740 \ubb34\uc8fc\ub2e4. \uc804\uc8fc, \uc7a5\uacc4, \uc548\ucc9c, \ubd80\ub0a8\ud589 \ubc84\uc2a4\uac00 \ubaa8\ub450 \uc801\uc0c1\uc744 \uc9c0\ub098\ub294\ub370 \uc7a5\uacc4\ud589\uc758 \uacbd\uc6b0 28\ud68c\uac00 \uc788\ub2e4. \ubb34\uc8fc\ud589\uc740 \uacbd\ubd80\uc120 \uc5f0\uacc4\ud3b8\uc774 \uc88b\uc73c\ub2c8 \ub300\uc804\uc5d0\uc11c\ub294 54\ud68c, \uc601\ub3d9\uc5d0\uc11c\ub294 32\ud68c\ub098 \ub2e4\ub2cc\ub2e4. \uc11c\uc6b8 \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c\ub294 \uace0\uc18d\ubc84\uc2a4\uac00 5\ud68c \ub2e4\ub2c8\uba70 \ub300\uad6c, \uad11\uc8fc, \uccad\uc8fc\uc5d0\uc11c\ub3c4 \ubc84\uc2a4\uac00 \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(73, TIMESTAMP '2019-08-14 15:46:34.936', TIMESTAMP '2019-08-14 15:47:23.074', '42', STRINGDECODE('\ud55c\uacc4\ub839 \uc624\ub978\ucabd\uc73c\ub85c \ubcf4\uc774\ub294 \uc810\ubd09\uc0b0\uc740 \uc124\uc545\uc0b0 \ub300\uccad\ubd09\uacfc \ub0a8\ubd81\uc73c\ub85c \ub9c8\uc8fc\ubcf4\uba70 \uc124\uc545\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\uc758 \uc77c\ubd80\ub97c \uc774\ub8e8\uace0 \uc788\ub2e4.<BR><BR>\uc8fc\uc694 \uad00\uad11\uba85\uc18c\ub85c\ub294 12\ub2f4 \uad6c\uace1\uc73c\ub85c \ubd88\ub9ac\ub294 \uc8fc\uc804\uace8\uc774 \uc788\ub294\ub370, \uc88c\uc6b0\ub85c \uac16\uac00\uc9c0 \ubaa8\uc591\uc758 \ubc14\uc704\ubd09\uc6b0\ub9ac, \uc6d0\uc2dc\ub9bc, \ub9d1\uc740 \uacc4\uace1\ubb3c\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uc808\uacbd\uc744 \uc774\ub8e8\uba70, \ud2b9\ud788 \uc8fc\uc804\uace8 \uc785\uad6c\uc758 \uc624\uc0c9\uc57d\uc218\ud130\ub294 \ube7c\ub193\uc744 \uc218 \uc5c6\ub294 \uad00\uad11\uc9c0\ub2e4. \uad70\uc0ac\uc9c0\uc5ed\uc73c\ub85c \ucd9c\uc785\uc774 \ud1b5\uc81c\ub418\uace0 \uc788\ub294 \uac83\uc774 \uc548\ud0c0\uae4c\uc6b8 \ubfd0\uc774\ub2e4. \uc9c0\uc18c \uc870\uae08 \uc544\ub798\ucabd \uc548\uad6d\uc0ac\ub85c \uc624\ub974\ub294 \uae38\uc740 \ub3cc\ube44\ud0c8\uc744 \uc9c0\ub098\uc57c \ud558\uace0 \uae38\ub3c4 \uc560\ub9e4\ud558\ubbc0\ub85c \uc0b0\ud589\uae30\uc810\uc740 \uc77c\ubc18\uc801\uc73c\ub85c \uc11c\ucc3d\ub9ac\uc9c0\uc18c\ub97c \ud0dd\ud55c\ub2e4.'), STRINGDECODE('\u2460 \uc624\uc0c9\ub9e4\ud45c\uc18c-\uc6a9\uc18c\ud3ed\ud3ec \uac08\ub9bc\uae38-\uc2ed\uc774\ud3ed\ud3ec-\ub4f1\uc120\ub300-\ud758\ub9bc\uace8\ub9e4\ud45c\uc18c(\ucd1d 3\uc2dc\uac04 25\ubd84)'), '38.010978', '128.525045', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc591\uc591\uad70 \uc11c\uba74, \uc778\uc81c\uad70 \uae30\ub9b0\uba74\u318d\uc778\uc81c\uc74d'), 2210012400, 1426, STRINGDECODE('\uc810\ubd09\uc0b0'), STRINGDECODE('\uc624\uc0c9\uc57d\uc218 \ud488\uc740 \uc124\uc545\uc0b0 \uc804\ub9dd\ub300&gt;<BR>\uc810\ubd09\uc0b0\uc740 \uc624\uc0c9\uc628\ucc9c\uacfc \uc57d\uc218\ub97c \ub07c\uace0 \uc788\uae34 \ud558\uc9c0\ub9cc \uc124\uc545\uc758 \uadf8\ub298\uc5d0 \uac00\ub824 \uadf8 \uc704\ud48d\uc744 \ud3b4\uc9c0 \ubabb\ud588\uc5c8\ub2e4. \ud558\uc9c0\ub9cc \uac00\uc744\uc774\uba74 \uac00\uc57c\ub3d9\uc744 \ucd95\uc18c\uc2dc\ucf1c \ub193\uc740 \ub4ef\ud55c \uc8fc\uc804\uace8\uacfc \uc124\uc545\uc758 \ucc9c\ubd88\ub3d9\uc744 \uc62e\uaca8 \ub193\uc740 \ub4ef\ud55c \uae30\uc554\ub4e4\uc774 \uc5b4\uc6b8\ub824 \ub2e8\ud48d\uc0b0\ud589\uc9c0\ub85c \uac01\uad11\ubc1b\ub294 \uacf3\uc774\ub2e4. <BR><BR>\uc810\ubd09\uc0b0\uc758 \uc61b \uc774\ub984\uc740 \ub364\ubd95\uc0b0\uc774\ub77c \ud55c\ub2e4, \ub54c\ubb38\uc5d0 \uacf0\ubc30\ub839\uc5d0\uc11c \uc810\ubd09\uc0b0\uc73c\ub85c \uc62c\ub77c\uc624\ub2e4 \ub9cc\ub098\ub294 \uc791\uc740 \uc810\ubd09\uc0b0\uc744 \uc791\uc740\ub364\ubd95\uc774\ub77c \ubd80\ub974\uae30\ub3c4 \ud55c\ub2e4. \uc810\ubd09\uc0b0\uc744 \uae30\uace0 \uc788\ub294 \uc624\uc0c9\uc57d\uc218\ub294 \ucca0\ubd84\uc774 \ub9ce\uc774 \ud568\uc720\ub41c \ud0c4\uc0b0\uc218\ub85c \uc704\uc7a5\ubcd1, \uc2e0\uacbd\ud1b5, \ube48\ud608 \ub4f1\uc5d0 \ud6a8\ud610\uc774 \ub192\ub2e4\uace0 \uc54c\ub824\uc838 \uc788\ub2e4. \uc810\ubd09\uc0b0 \uc0b0\ud589\uc740 \uc624\uc0c9, \ud55c\uacc4\ub839, \uadc0\ub454, \uc9c4\ub3d9\ub9ac \ub4f1\uc5d0\uc11c \uc2dc\uc791\ud560 \uc218 \uc788\uc9c0\ub9cc \ud604\uc7ac\ub294 \uc624\uc0c9\uc57d\uc218 \uad6c\uac04\ub9cc \uc774\uc6a9 \uac00\ub2a5\ud558\ub2e4. \uc810\ubd09\uc0b0\uc77c\uc6d0\uc740 \uc57c\uc0dd\uc2dd\ubb3c\uad70\ub77d\uc9c0\uac00 \uc788\ub294 \ud2b9\ubcc4\ubcf4\ud638\uad6c\ub85c \uc9c0\uc815\ub418\uc5b4 2026\ub144\uae4c\uc9c0 \ucd9c\uc785\ud560 \uc218 \uc5c6\ub2e4.'), STRINGDECODE('\uc6d0\uc2dc\ub9bc\uc774 \uc6b8\ucc3d\ud558\uace0 \ubaa8\ub370\ubbf8\ud480 \ub4f1\uc774 \uc790\uc0dd\ud558\ub294 \ub4f1 \uc0dd\ud0dc\uc801 \uac00\uce58\uac00 \ucee4 \uc720\ub124\uc2a4\ucf54\uc5d0\uc11c \uc0dd\ubb3c\uad8c\ubcf4\uc874\uad6c\uc5ed\uc73c\ub85c \uc9c0\uc815\ud558\uace0, \uc0b0\ub9bc\uc720\uc804\uc790\uc6d0\ubcf4\ud638\ub9bc\uc73c\ub85c \uad00\ub9ac\ub418\uace0 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815. \ud2b9\ud788 \uc81c1\ud68c \uc544\ub984\ub2e4\uc6b4 \uc232 \uc804\uad6d\ub300\ud68c\uc5d0\uc11c \ubcf4\uc804\ub418\uc5b4\uc57c \ud560 \uc232\uc73c\ub85c \uc120\uc815. 12\ub2f4 \uad6c\uace1\uc73c\ub85c \ubd88\ub9ac\ub294 \uc624\uc0c9\uc57d\uc218\ud130 \ubc0f \uc8fc\uc804\uace8 \uc131\uad6d\uc0ac\ud130\uc5d0 \uc788\ub294 \ubcf4\ubb3c \uc81c497\ud638\uc778 \uc591\uc591 \uc624\uc0c9\ub9ac \uc0bc\uce35\uc11d\ud0d1\uc774 \uc788\uc74c'), STRINGDECODE('\uc6d0\uc2dc\ub9bc\uacfc \uacc4\uace1\uc774 \uc870\ud654\ub86d\uac8c \uc5b4\uc6b0\ub7ec\uc9c4 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc624\uc0c9\uc9c0\uad6c\uc758 \uc219\ubc15\uc2dc\uc124\uc744 \uc774\uc6a9\ud55c\ub2e4. \uadf8\ub9b0\uc57c\ub4dc\ud638\ud154\u260e033-672-8500, \uc624\uc0c9\uc628\ucc9c\uc7a5(\u260e033-672-3636), \uc124\uc545\uc628\ucc9c\uc7a5(\u260e033-672-3849). \uc624\uc0c9\uc9c0\uad6c\uc758 \uc2dd\ub2f9\ub4e4\uc740 \ub300\uac1c \uc0b0\ucc44\uc815\uc2dd\uc744 \uc8fc \uc694\ub9ac\ub85c \ud55c\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc124\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uad00\ub9ac\uc18c \uc624\uc0c9\ubd84\uc18c \u260e033-672-2883'), STRINGDECODE('\uae30\uc810\uc740 \uc591\uc591\uc774\ub2e4. &gt;\uc591\uc591\u2192\uc624\uc0c9: \uc2dc\ub0b4\uc678\ubc84\uc2a4(\ud55c\uacc4\ub839 \uacbd\uc720) 1\uc77c 15\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(74, TIMESTAMP '2019-08-14 15:46:34.936', TIMESTAMP '2019-08-14 15:47:23.743', '46', STRINGDECODE('\ub192\uc774 887m\uc758 \ube44\uad50\uc801 \ub0ae\uc740 \uc0b0\uc778 \uc870\uacc4\uc0b0\uc740 1979\ub144 12\uc6d4 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\ub294\ub370, \uc0b0\uc138\uac00 \ubd80\ub4dc\ub7fd\uace0 \uc544\ub291\ud55c \uac83\uc774 \ud2b9\uc9d5\uc774\ub2e4. \uc0b0 \uc18d \uae4a\uc740 \uacc4\uace1\uc5d0\ub294 \uc816\uc904\uacfc \uac19\uc740 \ub9d1\uc740 \ubb3c\uc774 \ud750\ub974\uba70, \ub9cc\uc218\ubd09\uacfc \ubaa8\ud6c4\uc0b0\uc774 \uc1a1\uad11\uc0ac \uc77c\ub300\ub97c \ubcd1\ud48d\ucc98\ub7fc \ub458\ub7ec\uc2f8\uace0 \uc788\uc5b4 \ub9c8\uce58 \uc5b4\uba38\ub2c8 \ud488\ucc98\ub7fc \ud3ec\uadfc\ud568\uc744 \ub290\ub084 \uc218 \uc788\ub2e4. \uc804\uad6d 3\ub300\uc0ac\ucc30\uc758 \ud558\ub098\uc778 \uc1a1\uad11\uc0ac\uc640 \uace0\ucc30\uc778 \uc120\uc554\uc0ac\uac00 \uc8fc\ub2a5\uc120\uc744 \uc911\uc2ec\uc73c\ub85c \ub3d9\uc11c\uc5d0 \uc790\ub9ac\ud558\uace0 \uc120\uc554\uc0ac \uacc4\uace1\uc744 \ud750\ub974\ub294 \ub3d9\ubd80\uacc4\uace1\uc740 \uc774\uc0ac\ucc9c\uc73c\ub85c \ub0a8\ubd80\uacc4\uace1\uc740 \ubcf4\uc131\uac15\uc73c\ub85c \ud758\ub7ec\ub4e4\uac8c \ub41c\ub2e4.<BR><BR>\uc120\uc554\uc0ac \ub458\ub808\uc5d0\ub294 \uc6d4\ucd9c\ubd09, \uc7a5\uad70\ubd09, \uae43\ub300\ubd09, \uc77c\uc6d4\uc11d \ub4f1\uc774 \uc904\uc9c0\uc5b4 \uc19f\uc544\uc788\uc5b4 \uc7a5\uad00\uc744 \uc5f0\ucd9c\ud55c\ub2e4. \uc870\uacc4\uc0b0 \uc0b0\ud589\uc740 \uc1a1\uad11\uc0ac\ub098 \uc120\uc554\uc0ac \uc5b4\ub290 \ucabd\uc5d0\uc11c \uc2dc\uc791\ud574\ub3c4 \ube44\uc2b7\ud55c \uc2dc\uac04\ub300\uc5d0 \ub2e4\uc591\ud55c \ucf54\uc2a4\ub97c \uc990\uae38 \uc218 \uc788\uc73c\uba70, \uc0b0\uc138\uac00 \ud5d8\ud558\uc9c0 \uc54a\uace0 \ud3c9\ud0c4\ud55c \uae38\uc774 \ub9ce\uc544 \uc5f0\uc778\ub07c\ub9ac \ub610\ub294 \uac00\uc871\ub2e8\uc704 \uc18c\ud48d\ucf54\uc2a4\ub85c\ub3c4 \uc54c\ub9de\ub2e4. \uc1a1\uad11\uc0ac 3\ub300 \uba85\ubb3c\uc911\uc758 \ud558\ub098\uc778 800\ub144\uc774 \ub118\uc740 \ucc9c\uc5f0\uae30\ub150\ubb3c \uc30d\ud5a5\uc218\ub3c4 \uc774\uacf3\uc744 \ucc3e\uc544\uc624\ub294 \uc0ac\ub78c\ub4e4\uc758 \ub208\uae38\uc744 \ub04c\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc1a1\uad11\uc0ac-\uc1a1\uad11\uad74\ubaa9\uc774\uc7ac-\uc120\uc554\uad74\ubaa9\uc774\uc7ac-\uc815\uc0c1-\uc120\uc554\uc0ac(\ucd1d 3\uc2dc\uac04)<BR>\u2461 \uc120\uc554\uc0ac-\uc815\uc0c1-\uc120\uc554\uad74\ubaa9\uc774\uc7ac-\uc120\uc554\uc9d1\ub2e8\uc2dc\uc124\uc9c0\uad6c(\ucd1d 2\uc2dc\uac04 40\ubd84)<BR>\u2462 \uc120\uc554\uc0ac-\ub300\uac01\uc554-\ubc30\ubc14\uc704-\uc815\uc0c1-\uc5f0\uc0b0\ubd09-\uc1a1\uad11\uc0ac(\ucd1d 3\uc2dc\uac04)'), '35.017602', '127.362112', STRINGDECODE('\uc804\ub77c\ub0a8\ub3c4 \uc21c\ucc9c\uc2dc \uc2b9\uc8fc\uc74d\u318d\uc1a1\uad11\uba74'), 2611193200, 887, STRINGDECODE('\uc870\uacc4\uc0b0'), STRINGDECODE('&lt;\uc591\ucabd\uc5d0 \ub300\uac00\ub78c \ub454 \uc88c\uc6b0 \ub300\uce6d\uc758 \uc0b0&gt;<BR>\ub4dc\ubb3c\uac8c\ub3c4 \uc88c\uc6b0\uac00 \ub300\uce6d\uc778 \uc0b0\uc774\ub2e4. \uac00\uc6b4\ub370\uc758 \uc7a5\ub9c9\uace8\uc744 \ucd95\uc73c\ub85c \uc591\ucabd \uc0b0\uc904\uae30\uc640 \ubb3c\uc904\uae30\ub4e4\uc774 \ubc18\ub300\ubc29\ud5a5\uc73c\ub85c, \uac19\uc740 \uc218\ub85c \ubed7\uc5b4\uc788\ub2e4. \uadf8 \uae30\uc2ad\uc5d0\ub294 \uac00\ub78c\uc774 \ub458 \uc788\ub2e4. \uc2b9\ubcf4\uc0ac\ucc30 \uc1a1\uad11\uc0ac\uc640 \ud0dc\uace0\ucd1d\ub9bc \uc120\uc554\uc0ac\ub2e4. <BR><BR>\uc0c1\ubd09 \uc7a5\uad70\ubd09 \ucf54\ubc11\uc758 \uc120\uc554\uc0ac\ub294 \uc625 \uac19\uc740 \ubbf8\ub140\uac00 \uc794\uc744 \uc62c\ub9ac\ub294 \uc625\ub140\ud5cc\ubc30\ud615(\u7389\u5973\u737b\u676f\u5f62) \uba85\ub2f9\uc774\ub77c\uace0 \ud55c\ub2e4. \uadf8\ub798 \ub4e4\uba38\ub9ac \uc720\ucc9c\ubd80\ub77d \uc55e\uc5d0\ub294 \uc625\ub140\ubd09\uc774, \ub4a4\uc5d0\ub294 \ubc30\ubd09(\u76c3\u5cef)\uc774 \uc788\ub294\ub370 \uadfc\ub798 \uc8fc\uc554\ub310\uc774 \uac74\uc124\ub418\uba74\uc11c \ub3d9\ub124 \uc55e\uc5d0 \uc870\uc815\uc9c0\ub310\uc758 \uc0c1\uc0ac\ud638\uac00 \ub9cc\ub4e4\uc5b4\uc838 \uc804\uc124\uc774 \ub354\uc6b1 \uc124\ub4dd\ub825\uc788\uac8c \ub418\uc5c8\ub2e4.<BR><BR>\ubc18\uba74 \uc1a1\uad11\uc0ac\ub294 \uc120\uc554\uc0ac\ub97c \ub458\ub7ec\uc2fc \ub300\uce6d \uc800\ucabd \uc0b0\ub4e4\uae4c\uc9c0 \uc678\uc5f0\uc73c\ub85c \ud3ec\uad04\ud558\ub294 \uacb9\uc0b0 \uba85\ub2f9\uc5d0 \uc790\ub9ac\uc7a1\uc558\ub2e4\uace0 \ud55c\ub2e4. \uac00\uae4c\uc6b4 \uc6b8\ud0c0\ub9ac \ub9dd\uc218\ubd09, \uc5f0\uc0b0\ubd09, \ub300\uc7a5\ubd09, \uc870\uacc4\ubd09\uc774 \ub0b4\uc0b0(\u5167\u5c71), \uc120\uc554\uc0ac\uc758 \uc6b8\ud0c0\ub9ac \uc2dc\ub8e8\uc0b0, \uc7a5\uad70\ubd09, \uae43\ub300\ubd09, \uace0\ub3d9\uc0b0\uc744 \uc678\uc0b0\uc73c\ub85c \uc5ec\uae30\ub294 \uac83\uc774\ub2e4. \uc774\ub7f0 \uae4c\ub2ed\uc5d0 \ub4b7\uc0b0 \uc1a1\uad11\uc0b0(\uc5f0\uc0b0\ubd09 \uc77c\ub300)\uc744 \ubc84\ub824\ub450\uace0 ''\uc870\uacc4\uc0b0 \uc1a1\uad11\uc0ac''\ub77c \uc774\ub984\ubd99\uc77c \uc218 \uc788\uc5c8\ub2e4. <BR><BR>1979\ub144\uc5d0 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c, 1998\ub144\uc5d0\ub294 \uc0ac\uc801 \ubc0f \uba85\uc2b9 8\ud638\ub85c \uc9c0\uc815\ub418\uc5c8\ub2e4. \uc1a1\uad11\uc0ac\ub294 \ubcf4\uc870\uad6d\uc0ac \uc9c0\ub20c, \uc120\uc554\uc0ac\ub294 \ub300\uac01\uad6d\uc0ac \uc758\ucc9c\uc758 \uc790\ucde8\uac00 \uc11c\ub824\uc788\uc73c\ub2c8 \uadf8\ub798\ub3c4 \ub420 \ub9cc\ud558\ub2e4. \uc870\uacc4\uc0b0\uc740, \uc720\uba85\ud55c \ub450 \ub3c4\ub7c9\uc73c\ub85c \ud558\uc5ec \ub355\uc744 \ubcf4\ub294 \uc0b0\uc774\ub2e4.'), STRINGDECODE('\uc608\ub85c\ubd80\ud130 \uc18c\uac15\ub0a8(\u5c0f\u6c5f\u5357)\uc774\ub77c \ubd80\ub978 \uba85\uc0b0\uc73c\ub85c \uae4a\uc740 \uacc4\uace1\uacfc \uc6b8\ucc3d\ud55c \uc232, \ud3ed\ud3ec, \uc57d\uc218 \ub4f1 \uc790\uc5f0\uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0, \ubd88\uad50 \uc0ac\uc801\uc9c0\uac00 \ub9ce\uc73c\uba70, \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1979\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubaa9\uc870\uc0bc\uc874\ubd88\uac10(\uad6d\ubcf4 \uc81c42\ud638), \uace0\ub824\uace0\uc885\uc81c\uc11c(\u9ad8\u9e97\u9ad8\u5b97\u5236\u66f8 : \uad6d\ubcf4 \uc81c43\ud638), \uc1a1\uad11\uc0ac\uad6d\uc0ac\uc804(\uad6d\ubcf4 \uc81c56\ud638) \ub4f1 \ub9ce\uc740 \uad6d\ubcf4\ub97c \ubcf4\uc720\ud55c \uc1a1\uad11\uc0ac\uc640 \uacf1\ud5a5\ub098\ubb34(\ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c88\ud638)\uac00 \uc720\uba85'), STRINGDECODE('\uc0b0\uc138\uac00 \ubd80\ub4dc\ub7ec\uc6b4 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uc1a1\uad11\uc0ac\uc640 \uc120\uc554\uc0ac \uc55e\uc5d0 \ubbfc\ubc15\uc9d1\uc774 \ub9ce\ub2e4. <BR>\uc120\uc554\uc0ac \uc55e \uc815\ubb38\ubbfc\ubc15 \u260e061-754-6161 \uc9c4\uc194\uac00\ub4e0 \u260e061-754-5242 \uc1a1\uad11\uc0ac \uc55e \uc870\uacc4\uc0b0\uc7a5\uc5ec\uad00 \u260e061-755-2150 \uc1a1\uad11\uc0ac\ubbfc\ubc15 \u260e061-755-5338 \uace0\ud5a5\ubbfc\ubc15 \u260e061-755-2152 <BR>\uae08\uc804\uc0b0 \uae30\uc2ad\uc5d0 \uc788\ub294 \ub099\uc548\uc790\uc5f0\ud734\uc591\ub9bc\uc5d0\uc11c \uc57c\uc601\ud558\ub294 \uac83\ub3c4 \uc88b\uc744 \uac83\uc774\ub2e4. \u260e061-754-4402<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc21c\ucc9c\uc2dc\uccad \ubb38\ud654\ud64d\ubcf4\uacfc \uad00\uad11\ub2f4\ub2f9 \u260e061-749-3328'), STRINGDECODE('\uae30\uc810 \uc21c\ucc9c\uc740 \ucca0\ub85c\uad50\ucc28\uc810\uc774\ub2c8 \uc5f4\ucc28\ub97c \uc774\uc6a9\ud558\uba74 \uc88b\ub2e4. \uc6a9\uc0b0\ubc1c \uc804\ub77c\uc120 \uc5f4\ucc28\uac00 13\ud68c, \uad11\uc8fc\ubc1c \uacbd\uc804\uc120\uc740 7\ud68c, \ubd80\uc0b0 \ubd80\uc804\ubc1c\uc740 4\ud68c, \uc9c4\uc8fc\uc5d0\uc11c \uc21c\ucc9c\uc744 \uac70\uccd0 \uc6a9\uc0b0\uc73c\ub85c \uac00\ub294 \uc57c\uac04\uc5f4\ucc28\uac00 \uc788\ub2e4. <BR>&gt; \uc21c\ucc9c\u2192\uc120\uc554\uc0ac : 1\ubc88, 16\ubc88, 100\ubc88 \ubc84\uc2a4 05:50\u223c22:30\uae4c\uc9c0 \uc218\uc2dc\ub85c \uc6b4\ud589<BR>&gt; \uc21c\ucc9c\u2192\uc1a1\uad11\uc0ac : 06:20\u223c20:20\uae4c\uc9c0 40\ubd84 \uac04\uaca9 \uc6b4\ud589<BR>\uc1a1\uad11\uc0ac\ud589 \ubc84\uc2a4\ub294 \uad11\uc8fc\uc885\ud569\ud130\ubbf8\ub110\uc5d0\ub3c4 \uc788\ub2e4. &gt; \uad11\uc8fc\u2192\uc1a1\uad11\uc0ac : 1\uc77c 9\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(75, TIMESTAMP '2019-08-14 15:46:34.937', TIMESTAMP '2019-08-14 15:47:24.347', '47', STRINGDECODE('\uad6d\ub9bd\uacf5\uc6d0 \uc8fc\uc655\uc0b0\uc740 \ubcd1\ud48d\ucc98\ub7fc \ud3bc\uccd0\uc9c4 \uae30\uc554\uc808\ubcbd\uc774 \uc7a5\uad00.'), STRINGDECODE('\u2460 \ub300\uc804\uc0ac-\uc81c1\ud3ed\ud3ec-\uc81c3\ud3ed\ud3ec-\uae08\uc740\uad11\uc774\uad50-\uae08\uc740\uad11\uc774 \uc0bc\uac70\ub9ac-\uc6d4\ubbf8\uae30-\uc7a5\uad70\ubd09-\ub300\uc804\uc0ac(\ucd1d 3\uc2dc\uac04 45\ubd84)<BR>\u2461 \ub300\uc804\uc0ac-\uc81c1\ud3ed\ud3ec-\uc81c3\ud3ed\ud3ec-\ub0b4\uc6d0\ub3d9-\uac00\uba54\ubd09-\uc808\uace8 \uac08\ub9bc\uae38-\uc0ac\ucc3d\uace8-\uc81c2\ud3ed\ud3ec-\ub300\uc804\uc0ac(\ucd1d 5\uc2dc\uac04)'), '36.423019', '129.098377', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uccad\uc1a1\uad70 \uccad\uc1a1\uc74d\u318d\ubd80\ub3d9\uba74, \uc601\ub355\uad70 \uc9c0\ud488\uba74\u318d\ub2ec\uc0b0\uba74'), 2721022000, 722, STRINGDECODE('\uc8fc\uc655\uc0b0'), STRINGDECODE('&lt;\uc9c0\uae08\uc740 \uc804\uc124\ub85c\ub9cc \ub0a8\uc740 \ubb34\ud611\uc18c\uc124\uc758 \ub3d9\ucc9c&gt;<BR>\ud558\ub298\uc744 \ub098\ub294 \uc7ac\uc8fc \uc5c6\uc774\ub294 \ub4e4\uc5b4\uac08 \uc218 \uc5c6\ub294 \ub3d9\ucc9c(\u6d1e\u5929). \ud611\uace1\uc758 \uc785\uad6c\ub85c\ub294 \uae09\ub958\uac00 \uc3df\uc544\ub098\uc624\uace0 \uae4e\uc544\uc9c0\ub978 \uc591\ucabd \uc808\ubcbd\uc740 \ub192\uc774\ub97c \uac00\ub2a0\ud560 \uc218 \uc5c6\ub2e4. \ubb34\ud611\uc18c\uc124\uc5d0\ub098 \ub4f1\uc7a5\ud568\uc9c1\ud55c \ud48d\uacbd. \uadf8\ub807\uc9c0\ub9cc \uc774\ub545\uc5d0\ub3c4 \uadf8\ub7f0 \uc0b0\uc774 \uc788\uc5c8\ub2e4. <BR><BR>70\ub144\ub300\uc758 \uc5b4\ub9ac\uc11d\uc740 \uc0ac\ub78c\ub4e4\uc774 \uc81c1\ud3ed\ud3ec \uc55e\ub4a4 \uc808\ubcbd\uc744 \ud30c \uae38\uc744 \ub0b4\uc9c0 \uc54a\uc558\ub354\ub77c\uba74 \uc8fc\uc655\ub3d9\ucc9c\uc740 \uc544\uc9c1\uaecf \ub4e4\uc5b4\uac00\ubcf8 \uc0ac\ub78c\uc774 \uc5c6\uc5c8\uc73c\ub9ac\ub77c. \uae08\uc740\uad11\uc774\ub098 \uac00\uba54\ubd09 \uc606 \uc798\ub8e8\ubaa9\uc744 \ub118\uc5b4\uac08 \uc218\ub294 \uc788\uc5c8\uc5b4\ub3c4 \uc815\uba74 \ub3d9\uad6c\ub85c \ub4e4\uc5b4\uac04 \uc774\ub294 \uc804\ubb34\ud588\uc744 \uac83\uc774\ub2e4. \uc5c4\uccad\ub09c \uac00\ubb44\uc73c\ub85c \uc8fc\uc655\uacc4\uace1 \ubb3c\uc774 \ubaa8\ub450 \ub9d0\ub77c\ubd99\uae30 \uc804\uc5d0\ub294. <BR><BR>\uadf8\ub798\uc11c \uc804\uc124 \uc5b4\ub9b0 \uba85\uc18c\ub4e4\uc740 \ubaa8\ub450 \ub3d9\uad6c \uc544\ub798 \ubaa8\uc5ec\uc788\ub2e4. \uc804\uc124\uc758 \uc2dc\ub300\uac00 \ub05d\ub09c \ub4a4\uc5d0 \ubc1c\uacac\ub41c \ud130\ub77c \ud3ed\ud3ec \uc774\ub984 \ub610\ud55c \uadf8\ub0e5 \uc81c1\u00b7\uc81c2\u00b7\uc81c3\ud3ed\ud3ec\ub2e4. \ub098\uc544\uac00 \uacc4\uace1 \ub05d\uc758 907\ubd09\uc744 \ubb34\uc2dc\ud558\uace0 \uc544\uc9c1\uaecf 721\ubd09\uc5d0 \ub300\ud45c\uc131\uc744 \ubd80\uc5ec\ud558\uace0 \uc788\ub2e4. \ub3d9\ucc9c\uc73c\ub85c \ub4e4\uc5b4\uac00\uc9c0 \ubabb\ud55c \uc774\ub4e4\uc774 \uc62c\ub77c\uac08 \uc218 \uc788\ub294 \uac00\uc7a5 \ub192\uc740 \ubd09\uc6b0\ub9ac\uc600\uc73c\ub2c8\uae4c. <BR><BR>\uc8fc\ub098\ub77c \uc655\uc744 \uac16\ub2e4\ubd99\uc778 \uc804\uc124\ub3c4 \uadf8\ub807\ub2e4. \uc2e0\ub77c \uc5ed\uc0ac\uc5d0\ub294, \uc655\uc704 \uacc4\uc2b9 0\uc21c\uc704\uc600\uc73c\ub098 \ub73b\uc744 \uc774\ub8e8\uc9c0 \ubabb\ud55c \uc8fc\uc6d0\uc655 \uae40\uc8fc\uc6d0\uc774 \uc788\uc5c8\uace0 \uc774\uc5d0 \ubd88\ub9cc\uc744 \ud488\uc740 \uc544\ub4e4 \ud5cc\ucc3d\uc758 \ubc18\ub780\uc774 \ub098\ub77c\ub97c \uac70\ub35c\ub0b8 \uc0ac\uc2e4\uc774 \ubd84\uba85\ud55c\ub370 \uc5c9\ub6b1\ud558\uac8c\ub3c4 \ucc9c\uc5ec\ub144 \uc804\uc5d0 \ub9dd\ud55c \uba3c \ub098\ub77c \ud6c4\uc8fc \uc774\uc57c\uae30\ub85c \ub3c4\ubc30\ub97c \ud588\ub2e4.'), STRINGDECODE('\uc11d\ubcd1\uc0b0\uc73c\ub85c \ubd88\ub9ac\uc6b8 \ub9cc\ud07c \uae30\uc554\uad34\ubd09\uacfc \uc11d\ubcbd\uc774 \ubcd1\ud48d\ucc98\ub7fc \ub458\ub7ec\uc11c \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70 \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1976\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ub300\uc804\uc0ac(\u5927\u5178\u5bfa), \uc8fc\uc655\uc554\uc774 \uc788\uc74c. \uc8fc\uc655\uad74\uc744 \uc911\uc2ec\uc73c\ub85c \ub0a8\uc544\uc788\ub294 \uc790\ud558\uc131\uc758 \uc794\ud574\ub294 \uc8fc\uc655\uacfc \uace0\ub824\uad70\uc758 \uc2f8\uc6c0\uc758 \uc804\uc124\uc774 \uae43\ub4e4\uc5ec \uc788\ub294 \uacf3\uc73c\ub85c \uc720\uba85'), STRINGDECODE('\ud6c4\uc8fc\ucc9c\uc655\uc758 \uc740\ub454\uc9c0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uccad\uc1a1\uc74d\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. <BR>\uc8fc\uc655\uc0b0\uc628\ucc9c\uad00\uad11\ud638\ud154 \u260e054-874-7000 <BR>\uc8fc\uc655\uc0b0\uac00\ub4e0\uc5ec\uad00 \u260e054-874-4992 <BR>\ub098\ub4e4\uc774\ud669\ud1a0\ubc29\uac08\ub85c \u260e054-874-5200 <BR>\ub300\uc804\uc0ac \uc785\uad6c\uc758 \uc0c1\uc758\uc57c\uc601\uc7a5\uc744 \uc774\uc6a9\ud574\ub3c4 \uc88b\ub2e4. <BR><BR>\uad00\ub9ac\uc0ac\ubb34\uc18c \u260e054-873-0015<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc8fc\uc655\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e054-873-0015'), STRINGDECODE('\ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uacfc \ubd80\uc0b0 \ub3d9\ubd80\ud130\ubbf8\ub110, \uc548\ub3d9 \uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110\uc5d0\uc11c \uc9c1\ud589\ubc84\uc2a4\uac00 5\ud68c\uc529 \uc788\ub2e4. \ub300\uad6c\uc5d0\uc11c 40\ubd84 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud558\ub294 \uccad\uc1a1\ud589\uc744 \uc774\uc6a9\ud560 \uc218\ub3c4 \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(76, TIMESTAMP '2019-08-14 15:46:34.937', TIMESTAMP '2019-08-14 15:47:24.936', '47', STRINGDECODE('\uc11c\uc6b8\uc758 \uc9c4\uc0b0 \uc790\ub9ac\ub97c \ub193\uace0 \uc0bc\uac01\uc0b0\uacfc \ub2e4\ud22c\ub2e4\uac00 \ud718\uc801\ud718\uc801 \ub0b4\ub824\uc640 \ubc84\ub838\ub2e4\ub294 \uc804\uc124\uc774 \uc788\uc744 \ub9cc\ud07c \ube7c\uc5b4\ub09c \uc0b0\uc774\ub2e4. \uc815\uc0c1\uc5d0\uc11c \ub0a8\ubd09\uc73c\ub85c \ubed7\uc5b4\ub0b4\ub9b0 1000m\ub97c \ub118\ub098\ub4dc\ub294 \ub2a5\uc120\uc774 \ud2b9\ud788 \uc900\uac78\ud574 \ud761\uc0ac \uc0bc\uac01\uc0b0 \ubc31\uc6b4\ub300\uc5d0\uc11c \ubcf4\ud604\ubd09\uc5d0 \uc774\ub974\ub294 \uc7a5\ucf8c\ud55c \ub2a5\uc120\uc744 \ube7c\ub2e4 \ub193\uc740 \ub4ef\ud558\ub2e4. \uc77c\ubc18\ub4f1\uc0b0\ub85c\ub294 \uc815\uc0c1\uc73c\ub85c \uc54c\ub824\uc9c4 1075\ubd09\uc744 \uc911\uc2ec\uc73c\ub85c 6\ubc29\uc73c\ub85c \ub098\uc788\ub294\ub370 \uc774 \uc911 \uc0c1\ubd09\uc778 \uc8fc\ud758\uc601\ubd09\uacfc \ubd80\ubd09\uc744 \uc9c0\ub098 \ub3d9\ud654\uc6d0\ub85c \uc774\uc5b4\uc9c0\ub294 \uc904\uae30\uc758 \ub2a5\uc120\ubbf8\uac00 \uc77c\ud488\uc774\ub2e4. \uc6d4\ud56d\uc0bc\ubd09\uc73c\ub85c \ud574\uc11c \ud558\ub298\uc7ac\ub85c \ub0b4\ub824\uc11c\ub294 \ucf54\uc2a4\ub3c4 \uc88b\uc740\ub370 \uc6d4\uc545\uc0b0\uc5d0\uc11c\ub294 \uc774 \ub458\uc758 \ud558\ub298\uae08\uc774 \ub9ac\ub4ec\uccb4\uc870\uc758 \ub9ac\ubcf8\ucc98\ub7fc \uc5ed\ub3d9\uc801\uc73c\ub85c \ubcf4\uc778\ub2e4. \uc0b0\uc774 \uc19f\uc74c\uc774 \uc6b0\uc138\ud55c \ud615\uad6d\uc774\ub77c \uc369 \ubc1c\ub2ec\ud55c \uacc4\uace1\uc740 \uc5c6\ub2e4. \ub300\uc2e0 \uace1\ucda9\uace8\uc758 \uc5ec\uad81\ud3ed\ud3ec\uc640 \ud30c\ub791\uc18c, \uc870\uace1\uace8\uc758 \uaf43\ubc2d\uc11c\ub35c\uc774 \uc774\ucc44\ub86d\ub2e4. \ub108\ub35c \uc0ac\uc774\ub97c \ub4ec\uc131\ub4ec\uc131 \ub6ab\uace0 \uc62c\ub77c\uc628 \uc9c4\ub2ec\ub798\uac00 \uaf43\uc744 \ud53c\uc6b4 \ubaa8\uc2b5\uc740 \uc5b4\ub514\uc11c\ub3c4 \ucc3e\uc544\ubcf4\uae30 \ud798\ub4e4\ub2