INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(77, TIMESTAMP '2019-08-14 15:46:34.937', TIMESTAMP '2019-08-14 15:47:25.517', '45', STRINGDECODE('\uba85\uc0b0\uc77c\uc218\ub85d \uc231\ud55c \uc774\ub984\uc18d\uc5d0 \ub458\ub7ec\uc313\uc5ec \uc788\uae30 \ub9c8\ub828\uc774\ub2e4. \uc608\ub85c\ubd80\ud130 \uc9c0\ub9ac\uc0b0\uc740 \ubc31\ub450\ub300\uac04\uc758 \ub9e5\uc774 \ud06c\uac8c \ub05d\ub098\ub294 \uacf3\uc73c\ub85c ''\ud0dd\ub9ac\uc9c0''\uc5d0\uc11c\ub294 \ub450\ub958\uc0b0\uc774\ub77c\uace0 \ud558\uc600\uc73c\uba70 \uc9c4\uc2dc\ud669 \uc2dc\uc808 \uc0bc\uc2e0\uc0b0\uc758 \ud558\ub098\ub85c ''\ubc29\uc7a5\uc0b0''\uc774\ub780 \uc774\ub984\uc744 \uc4f0\uae30\ub3c4 \ud588\ub2e4. \uadf8\ub9ac\uace0 ''\uc9c0\uc774\uc0b0''\uc740 ''\uc9c0\ud61c\uac00 \ub2e4\ub978 \uc0b0'' ''\ucc9c\uc7ac\uc9c0\ubcc0\uc744 \ubbf8\ub9ac \uc544\ub294 \uc9c0\ud61c \uc788\ub294 \uc0b0''\uc774\ub77c \ud45c\ud604\ud558\ub294 \ub4f1 \uc774\ub984\uc758 \uc231\ud55c \uc804\uc124\uc774 \uc2a4\uba70 \uc788\ub2e4. <BR><BR>\u300c\uae08\uac15\uc0b0\uc740 \ube7c\uc5b4\ub098\ub418 \uc6c5\uc7a5\ud558\uc9c0 \ubabb\ud558\uace0, \uc9c0\ub9ac\uc0b0\uc740 \uc6c5\uc7a5\ud558\ub418 \ube7c\uc5b4\ub098\uc9c0 \ubabb\ud558\uace0\u300d\ub77c\ub294 \uc11c\uc0b0\ub300\uc0ac\uc758 \ube44\uc720\uac00 \uc788\ub4ef \uc9c0\ub9ac\uc0b0\uc740 \ub0a0\uce74\ub86d\uace0 \ube7c\uc5b4\ub0a8\uc740 \ubd80\uc871\ud558\ub098 \uc6c5\uc7a5\ud558\uace0 \ub450\ub9ac\ubb49\uc2e4\ud55c \uae30\uc6b4\uc774 \ub3cb\ubcf4\uc778\ub2e4. \ud589\uc815\uad6c\uc5ed\uc0c1 \uacbd\ub0a8 \uc0b0\uccad\uad70 \uc2dc\ucc9c\uba74 \uc911\uc0b0\ub9ac \u5c71 208\ubc88\uc9c0\uc5d0 \uc18c\uc7ac\ud55c \ucc9c\uc655\ubd09(1,915.4m)\uc744 \uc8fc\ubd09\uc73c\ub85c \ubc18\uc57c\ubd09(1,731.8m), \ub178\uace0\ub2e8(1,507m)\uc774 \ub300\ud45c\uc801\uc774\uba70, \ucc9c\uc655\ubd09\uc5d0\uc11c \ub178\uace0\ub2e8\uc744 \uc787\ub294 100\ub9ac \ub2a5\uc120\uc5d0\ub294 1\ucc9c 5\ubc31\ubbf8\ud130\uac00 \ub118\ub294 \uace0\ubd09\uc774 10\uac1c, 1\ucc9c\ubbf8\ud130\uac00 \ub118\ub294 \ubd09\uc6b0\ub9ac\uac00 20\uc5ec\uac1c\ub098 \uc788\uc744 \uc815\ub3c4\ub85c \ub192\uace0 \ud06c\ub2e4. \ud3c9\ud3c9\ud55c \uace0\uc6d0\uc9c0\ub300\ub3c4 \ub9ce\uc774 \ubc1c\ub2ec\ud574 \uc57c\uc0dd\ud654\ub098 \ucca0\ucb49 \ub4f1\uc774 \uc7a5\uad00\uc744 \uc774\ub8e8\uae30\ub3c4 \ud55c\ub2e4. \uc0b0\uc138\uac00 \ud5d8\ud558\uc9c0 \uc54a\uc73c\uba74\uc11c \ubd09\uc6b0\ub9ac\uac00 80\uac1c\uc5d0 \ub2ec\ud558\ub2e4\ubcf4\ub2c8 \ubd09\uc6b0\ub9ac \uc0ac\uc774\ub85c \uacc4\uace1\uc774 \ubc1c\ub2ec\ud588\ub2e4. <BR><BR>\uacc4\uace1\ubb3c\uc774 \ub9ce\uace0 \uacbd\uc0ac\ub3c4\uac00 \uc2ec\ud558\uc9c0 \uc54a\ub294 \ud2b9\uc9d5\uc774 \uc788\ub2e4. \uc911\uc0b0\ub9ac\uacc4\uace1, \ub300\uc6d0\uc0ac\uacc4\uace1, \uac70\ub9bc\uacc4\uace1, \uc7a5\ub2f9\uacc4\uace1, \ubc40\uc0ac\uace8\uacc4\uace1, \ud654\uc5c4\uc0ac\uacc4\uace1, \uc2ec\uc6d0\uacc4\uace1, \ud53c\uc544\uace8\uacc4\uace1, \ud55c\uc2e0(\ubc31\ubb34\ub3d9)\uacc4\uace1, \uce60\uc120\uacc4\uace1 \ub4f1 \uc720\uba85 \uacc4\uace1\ub9cc\ub3c4 20\uc5ec\uac1c\uac00 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc911\uc0b0\ub9ac-\uce7c\ubc14\uc704-\ub9dd\ubc14\uc704-\ubc95\uacc4\uc0ac(\ub85c\ud0c0\ub9ac\ub300\ud53c\uc18c)-\ucc9c\uc655\uc0d8-\ucc9c\uc655\ubd09-\uc911\uc0b0\ub9ac(\ucd1d 8\uc2dc\uac04)<BR>\u2461 \uc131\uc0bc\uc7ac-\ub178\uace0\ub2e8-\uc784\uac78\ub839-\ub178\ub8e8\ubaa9-\ubc18\uc57c\ubd09-\ud654\uac1c\uc7ac-\ubc40\uc0ac\uace8-\ubc18\uc120(\ucd1d 7\uc2dc\uac04 30\ubd84)<BR>\u2462 \uac70\ub9bc-\uc138\uc11d\ub300\ud53c\uc18c-\uc5f0\ud558\ubd09-\uc81c\uc11d\ubd09-\ucc9c\uc655\ubd09-\uc911\uc0b0\ub9ac(\ucd1d 9\uc2dc\uac04)'), '35.416378', '127.390382', STRINGDECODE('\uc804\ub77c\ubd81\ub3c4 \ub0a8\uc6d0\uc2dc, \uc804\ub77c\ub0a8\ub3c4 \uad6c\ub840\uad70, \uacbd\uc0c1\ub0a8\ub3c4 \ud558\ub3d9\uad70\u318d\uc0b0\uccad\uad70\u318d\ud568\uc591\uad70'), 2809040100, 1915, STRINGDECODE('\uc9c0\ub9ac\uc0b0'), STRINGDECODE('&lt;3\ub3c4 \uc5ec\uc12f\uace0\uc744\uc5d0 \uac78\uce5c \uac70\ub300\ud55c \uc0b0\uad6d&gt;<BR>\uc9c0\ub9ac\uc0b0\uc740 \ud06c\ub2e4. \ub192\uace0\ub3c4 \ub113\uace0 \uadf8\ub9ac\uace0 \uae4a\ub2e4. 3\ub3c4 \uc5ec\uc12f \uace0\uc744\u2015\ud568\uc591, \uc0b0\uccad, \ud558\ub3d9, \uad6c\ub840, \ub0a8\uc6d0, \uc6b4\ubd09[\ud604]\uc5d0 \uadf8\ub9bc\uc790 \ub4dc\ub9ac\uc6b4 \uac70\ub300\ud55c \uc0b0\uad6d(\u5c71\u570b)\uc774\ub2e4. 1000\ubbf8\ud130 \uc774\uc0c1\uc758 \uc8fc\ub989\ub9cc \ud574\ub3c4 110\ub9ac\ub098 \ub41c\ub2e4. \uc2e4\uc0c1\uc0ac \uc11c\ucabd \ub355\ub450\uc0b0(1151.5m)\uc5d0\uc11c \ucc9c\uc655\ubd09 \ubd81\ucabd \uc465\ubc2d\uc7ac(1315.4m)\uae4c\uc9c0\ub2e4(\ub3c4\uc0c1\uac70\ub9ac \uae30\uc900\uc774\uba70 \ub178\uace0\ub2e8\u223c\ucc9c\uc655\ubd09 \uc904\uc790 \uc2e4\uce21 \uacb0\uacfc\ub294 34.2km\ub2e4). \uc0bc\ub0a8\uc758 \uc9c0\ubd95 \uc6a9\ub9c8\ub8e8\uac00 \ub41c\ub2e4. <BR><BR>\uc6a9\ub9c8\ub8e8\uc5d0\uc11c\ub294 \uc5f4\ub313 \uac1c\uc758 \ub0b4\ub9bc\ub9c8\ub8e8\uac00 \ud758\ub7ec\ub0b4\ub9ac\uace0 \uc788\ub2e4. \ub0b4\ub9bc\ub9c8\ub8e8 \uc0ac\uc774\uc5d0\ub294 \ub610 \uadf8\ub9cc\ud07c\uc758 \uace8\uc9dc\uae30\ub4e4\uc774 \uc788\ub2e4. \ud558\uc5ec \ubaa8\ub450 1\uc5b54\ucc9c\ub9cc\ud3c9\uc758, \uc7a5\uc5c4\ud55c \uc0b0\ub369\uc774\ub97c \uc774\ub8ec\ub2e4. \uadf8 \uc548\uc5d0\ub294 10\uacbd\uc774 \uc788\ub2e4. \ub178\uace0\ub2e8 \uc6b4\ud574, \ud53c\uc544\uace8 \ub2e8\ud48d, \ubc18\uc57c\ubd09 \ub099\uc870, \ubcbd\uc18c\ub839 \uba85\uc6d4, \uc138\uc11d\ud3c9\uc804 \ucca0\ucb49, \ubd88\uc77c\ud3ed\ud3ec, \uc5f0\ud558\ucc9c \uc120\uacbd(\u4ed9\u666f), \ucc9c\uc655\ubd09 \uc77c\ucd9c, \uce60\uc120\uacc4\uace1, \uc12c\uc9c4\uac15 \ub9d1\uc740 \ubb3c\uc774\ub2e4. <BR><BR>\ucd5c\uace0 \uc804\ub9dd\ub300\ub294 \uc655\uc2dc\ub8e8\ubd09(1240.2m)\uc774\ub2e4. \uac70\uae30\uc11c \uc12c\uc9c4\uac15\uc740 \ubaa8\ub798\ubc2d\uc758 \uc0b4\uc5bc\uc74c\ucc98\ub7fc \ubc18\uc9dd\uc774\uace0 \ucc9c\uc655\ubd09\uc740 \ud558\ub298 \uc800 \ub192\uc740 \uacf3\uc5d0\uc11c \uc81c\uc11d\ubd09\uacfc \ucd1b\ub300\ubd09\uc744 \uac70\ub290\ub9ac\uace0 \ub0b4\ub824\ub2e4\ubcf4\uace0 \uc788\ub2e4. \ubd88\uad50\ubb38\ud654\uc758 \ubcf4\uace0\uc774\uae30\ub3c4 \ud558\ub2e4. \uc30d\uacc4\uc0ac, \uc5f0\uace1\uc0ac, \ud654\uc5c4\uc0ac, \ucc9c\uc740\uc0ac, \uc2e4\uc0c1\uc0ac, \ubcbd\uc1a1\uc0ac, \uadf8\ub9ac\uace0 \ub3cc\ud0d1 \ub458\ub9cc \ub385\uadf8\ub7f0 \ub2e8\uc18d\uc0ac\uc9c0\ub2e4. \ubaa8\ub450 \ud569\ud574 \uad6d\ubcf4 \uc77c\uacf1\uc5d0 \uc2a4\ubb3c\ub2e4\uc12f \uac1c\uc758 \ubcf4\ubb3c\uc744 \uac16\uace0\uc788\ub2e4. <BR><BR>\uae08\uac15\uc0b0, \ud55c\ub77c\uc0b0\uacfc \ud568\uaed8 \uc0bc\uc2e0\uc0b0\uc758 \ubc18\uc5f4\uc5d0 \uc62c\ub77c\uc788\ub2e4. \ubc29\uc7a5\uc0b0, \ub450\ub958\uc0b0, \ubd88\ubcf5\uc0b0\uc774\ub77c \ubd88\ub9ac\uae30\ub3c4 \ud588\ub2e4. \ub73b\uc73c\ub85c\ub294 \uc9c0\ud61c(\u667a\u6167)\ub85c\uc6b4 \uc774\uc778(\u7570\u4eba)\uc774 \ub098\ud0c0\ub0a0 \uc0b0\uc774\ub2e4.'), STRINGDECODE('\uc2e0\ub77c 5\uc545\uc911 \ub0a8\uc545\uc73c\ub85c \ub0a8\ud55c \ub0b4\ub959\uc758 \ucd5c\uace0\ubd09\uc778 \ucc9c\uc655\ubd09(1,915.4m)\uc744 \uc8fc\ubd09\uc73c\ub85c \ub178\uace0\ub2e8(1,507m), \ubc18\uc57c\ubd09(1,731.8m) \ub4f1 \ub3d9\uc11c\ub85c 100\uc5ec\ub9ac\uc758 \uac70\ub300\ud55c \uc0b0\uc545\uad70\uc744 \uc774\ub904 \"\uc9c0\ub9ac\uc0b0 12\ub3d9\ucc9c\"\uc744 \ud615\uc131\ud558\ub294 \ub4f1 \uacbd\uad00\uc774 \ub6f0\uc5b4\ub098\uace0 \uc6b0\ub9ac\ub098\ub77c \ucd5c\ub300\uc758 \uc790\uc5f0\uc0dd\ud0dc\uacc4 \ubcf4\uace0\uc774\uba70 \uad6d\ub9bd\uacf5\uc6d0 \uc81c1\ud638\ub85c \uc9c0\uc815(1967\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc5b4\ub9ac\uc11d\uc740 \uc0ac\ub78c\uc774 \uba38\ubb3c\uba74 \uc9c0\ud61c\ub85c\uc6b4 \uc0ac\ub78c\uc73c\ub85c \ub2ec\ub77c\uc9c4\ub2e4\uace0 \ud55c\ub370\uc11c \uc0b0\uc774\ub984\uc774 \uc720\ub798.\ud654\uc5c4\uc0ac, \ucc9c\uc740\uc0ac, \uc5f0\uace1\uc0ac, \uc30d\uacc4\uc0ac \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ubc31\ub450\uc758 \ub05d, \uc5ed\uc0ac\uc758 \ud53c\uc640 \ub208\ubb3c\uc774 \ud568\uaed8 \ubb3b\ud600\uc788\ub294 \uc774\uc0c1\ud5a5'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc9c0\ub9ac\uc0b0 \uc77c\ub300\uc5d0\ub294 \ubcc4\ubbf8\uac00 \ub9ce\uc73c\ub2c8 \ub9db\uc9d1\ubd80\ud130 \ucc3e\uc544\ubcf8\ub2e4. \ud654\uc5c4\uc0ac\ub85c \ub4e4\uc5b4\uac00\ub294 \uae38\ubaa9\uc758 \ub0a8\uc545\uac00\ub4e0(\u260e061-782-2715)\uc740 \uc624\ub9ac\uc804\ubb38\uc810\uc73c\ub85c \uc815\ud3c9\uc774 \ub098\uc788\ub2e4. \uc30d\uacc4\uc0ac \uc785\uad6c \ud654\uac1c\uc5d0\uc11c\ub294 \uc12c\uc9c4\uac15\uc758 \ucc38\uac8c\uc640 \uc7ac\ucca9\uad6d, \uc740\uc5b4\ud280\uae40\uc744 \uaf2d \ub9db\ubd10\uc57c\ud55c\ub2e4. \ud0dc\ubd09\uc2dd\ub2f9(\u260e055-883-2466) \ub3d9\ud765\uc2dd\ub2f9(\u260e055-883-8333) \ubc40\uc0ac\uace8\uc5d0\ub294 \uccad\uc815 \uc0b0\ub098\ubb3c\ub85c \ub9cc\ub4e0 \uc0b0\ucc44\ube44\ube54\ubc25\uacfc \ub2ed\ubc31\uc219\uc774 \uc720\uba85\ud55c \uccad\uc1a1\uac00\ub4e0(\u260e063-636-3563)\uc774 \uc788\ub2e4.<BR>\uc0b0\uae30\uc2ad\uc758 \uc798 \uacf3\uc740 \uad6c\ub840\ucabd\uc744 \uc81c\uc678\ud558\uace0\ub294 \ub2e4 \ubbfc\ubc15\uc774\ub2e4. \ud654\uc5c4\uc0ac \uc8fc\ubcc0\uc5d0\ub294 \uc9c0\ub9ac\uc0b0\ud504\ub77c\uc790\ud638\ud154 \u260e061-782-2171 \ud654\uc5c4\uac01\uc5ec\uad00 \u260e061-782-0997 \uc30d\uacc4\uc0ac\ucabd\uc740 \uc30d\uacc4\ubcc4\uc7a5 \u260e055-883-1665 \uc12c\uc9c4\uac15\uc2dd\ub2f9 \u260e055-883-2345 \uc911\uc0b0\ub9ac\ub294 \ucc9c\uc655\ubd09\uc758 \uc9d1 \u260e055-972-1155 \ub450\ub958\uc0b0\uc7a5 \u260e055-972-1289 \ubc31\ubb34\ub3d9\uc740 \ubc31\ubb34\uc0b0\uc7a5\ud734\uac8c\uc18c \u260e055-962-5211 \ucc38\uc0d8\uc9d1 \u260e055-962-5332 <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc9c0\ub9ac\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \ub3d9\ubd80\uad00\ub9ac\uc0ac\ubb34\uc18c \u260e055-942-7771~2 \ub0a8\ubd80\uad00\ub9ac\uc0ac\ubb34\uc18c \u260e061-783-9100~1 \ubd81\ubd80\uad00\ub9ac\uc0ac\ubb34\uc18c \u260e063-625-8911~2'), STRINGDECODE('\uae30\uc810\uc740 \uad6c\ub840, \uc9c4\uc8fc, \ud568\uc591\uc774\ub2e4. \uad6c\ub840\uc5d0\uc11c \ud654\uc5c4\uc0ac\ud589 \ubc84\uc2a4\ub294 \uc218\uc2dc\ub85c \uc788\uc73c\uba70 \uc30d\uacc4\uc0ac\ud589\uc740 13\ud68c, \uc131\uc0bc\uc7ac\ud589\uc740 8\ud68c \uc788\ub2e4. \uc9c4\uc8fc\uc5d0\uc11c \uc911\uc0b0\ub9ac\ud589\uc740 16\ud68c, \ud568\uc591\uc5d0\uc11c \ubc31\ubb34\ub3d9\ud589\uc740 18\ud68c \uc788\ub2e4. \uad6c\ub840\ub294 \ud558\ub8e8 13\ud68c(\uc6a9\uc0b0\ubc1c \uae30\uc900) \ub2e4\ub2c8\ub294 \uc804\ub77c\uc120 \uc5f4\ucc28\ub97c \uc774\uc6a9\ud558\ub294 \uac83\uc774 \uc88b\uc73c\uba70 \uc9c4\uc8fc\ub098 \ud568\uc591\uc73c\ub85c\ub294 \ubc84\uc2a4\uac00 \ud3b8\ud558\ub2e4. \uc9c4\uc8fc\ub294 \uc11c\uc6b8 \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c \uc6b0\ub4f1\ubc84\uc2a4\uac00 30\ubd84 \uac04\uaca9(\uc2ec\uc57c 3\ud68c), \ubd80\uc0b0 \ub178\ud3ec\ub3d9\ud130\ubbf8\ub110\uc5d0\uc11c 20\ubd84 \uac04\uaca9, \ub300\uad6c \uc11c\ubd80\uc815\ub958\uc7a5\uc5d0\uc11c 45\ubd84 \uac04\uaca9, \ub300\uc804 \ub3d9\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c \ud55c \uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2cc\ub2e4. <BR><BR>\uc778\ucc9c, \uccad\uc8fc, \uc6b8\uc0b0, \uc804\uc8fc, \uad11\uc8fc, \uc5ec\uc218\uc5d0\uc11c\ub3c4 \ubc84\uc2a4\uac00 \uc788\ub2e4. \ud568\uc591\uc73c\ub85c\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 12\ud68c(\uc2ec\uc57c 2\ud68c), \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c 8\ud68c \uc6b4\ud589\ud55c\ub2e4. \ubd80\uc0b0\uc5d0\uc11c\ub294 15\ubd84, \ub300\uad6c\uc5d0\uc11c\ub294 30\ubd84 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2c8\uba70 \uc778\ucc9c, \uc218\uc6d0, \uad11\uc8fc, \uc804\uc8fc\uc5d0\uc11c\ub3c4 \ubc84\uc2a4\uac00 \uc788\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(78, TIMESTAMP '2019-08-14 15:46:34.938', TIMESTAMP '2019-08-14 15:47:26.105', '48', STRINGDECODE('\"\ud658\uc0c1\uc758 \uc12c\" \uc0ac\ub7c9\ub3c4 \uadf8 \uc911\uac04\uc5d0 \uc6b0\ub69d\uc19f\uc740 \ub610 \ub2e4\ub978 \"\uc9c0\ub9ac\uc0b0\"(399.3m) \ud798\uacb9\uac8c \uc624\ub978 \uc625\ub140\ubd09\uc11c \ubc14\ub77c\ubcf4\ub294 \ud55c\ub824\uc218\ub3c4\uc640 \ud558\uc0b0\ud6c4\uc758 \uc2f1\uc2f1\ud55c \ud68c\ub9db \ub208\uacfc \uc785\uc774 \uc990\uac81\ub2e4 \ud658\uc0c1\uc758 \uc12c\uff01 \uc0ac\ub7c9\ub3c4\uac00 \ub354\uc6b1 \uc544\ub984\ub2f5\uac8c \uc0c8\ub85c\uc774 \ud0dc\uc5b4\ub0ac\ub2e4.<BR><BR>\uac10\ucd94\uc5b4\uc84c\ub358 \uc0ac\ub7c9\ub3c4\uc758 \ube44\uacbd\ub4e4\uc774 \uc774\uc81c \uc218\ub9ce\uc740 \uc0b0\uafbc\ub4e4\uacfc \uc5ec\ud589\uc790\ub4e4\uc5d0\uac8c \ud750\ubb47\ud558\uac8c \uadf8 \ub9e4\ud639\uc801\uc778 \uc790\ud0dc\ub97c \ub4dc\ub7ec\ub0b4\uac8c \ub41c \uac83\uc774\ub2e4. \ud1b5\uc601\uc2dc\uc5d0\uc11c \ud3bc\uce5c \uad00\uad11\uac1c\ubc1c\ub85c \ub300\ud56d\ud574\uc218\uc695\uc7a5\uc774 \uc870\uc131\ub418\uace0 \uc5ec\uac1d\uc120 \ud130\ubbf8\ub110\uc774 \uc0c8\ub86d\uac8c \ub2e8\uc7a5\ub418\uace0 \uc704\ud5d8\ud588\ub358 \ub4f1\uc0b0\ub85c\uac00 \uc57c\ubb34\uc9c0\uac8c \uc815\ube44\ub418\ub294 \ub4f1 \ucc3e\ub294 \uc774\ub4e4\uc758 \uc548\uc804\uacfc \ud3b8\uc758\ub97c \uac1c\uc120\ud558\uc5ec \uc0ac\ub7c9\ub3c4\ub294 \uc218\ub9ce\uc740 \uad00\uad11\uac1d\ub4e4\ub85c \ubd90\ube44\uace0 \uc788\ub2e4.<BR><BR>\uc0ac\ub7c9\ub3c4\ub294 \ud1b5\uc601\uc2dc \uc0ac\ub7c9\uba74\uc5d0 \ud574\ub2f9\ud558\ub294 \uc12c\uc73c\ub85c \uc6b0\ub9ac\ub098\ub77c \ub0a8\ub2e8 \ub2e4\ub3c4\ud574 \ud55c\ub824\ud574\uc0c1\uacf5\uc6d0\uc5d0 \ub458\ub7ec\uc2f8\uc778 \uc544\ub984\ub2e4\uc6b4 \uc12c\uc774\ub2e4. \uc0ac\ub7c9\ub3c4\ub294 \uc12c \uc790\uccb4\uac00 \ubc40 \ubaa8\uc591\uc73c\ub85c \uc0dd\uacbc\uace0 \ubc40\uc774 \ub9ce\ub2e4\uace0 \ud574\uc11c \ubd99\uc778 \uc774\ub984\uc774\ub77c \ud558\ub294\ub370, \ud55c \ub0a8\uc790\uac00 \uc774\ub8f0 \uc218 \uc5c6\ub294 \uc0ac\ub791\uc5d0 \uad34\ub85c\uc6cc\ud558\ub2e4 \uc0c1\uc0ac\ubcd1\uc73c\ub85c \uc8fd\uc5b4\ubc40\uc774 \ub418\uc5c8\ub2e4\ub294 \uc774\uc57c\uae30\ub3c4 \uc804\ud574\uc628\ub2e4.\uc0bc\ucc9c\ud3ec\uc2dc \uc55e\ubc14\ub2e4\uc758 \uc794\uc794\ud55c \ubb3c\uacb0\uc744 \uac00\ub974\uace0 \uc0ac\ub7c9\ub3c4 \uc0c1\ub3c4\uc5d0 \ub3c4\ucc29\ud558\uba74 \uc12c \uac00\uc6b4\ub370 \uc6b0\ub69d \uc19f\uc740 \uc9c0\ub9ac\uc0b0\uc744 \ubcfc \uc218 \uc788\ub2e4.<BR><BR>\ubcf8\ub798\ub294 \uc6b0\ub9ac\uc5d0\uac8c \uc798 \uc54c\ub824\uc838 \uc788\ub294 \uc804\ub77c\ub3c4\uc640 \uacbd\uc0c1\ub3c4\uc5d0 \uac78\uce5c \uc7a5\ub300\ud55c \uc9c0\ub9ac\uc0b0\uc774 \ubc14\ub77c\ub2e4\ubcf4\uc5ec \u300c\uc9c0\uc774\ub9dd\uc0b0\u300d\uc774\ub77c \ubd88\ub9ac\ub2e4\uac00 \uadf8 \ub9d0\uc774 \uc904\uc5b4 \u300c\uc9c0\ub9ac\uc0b0\u300d\uc774 \ub41c \uac83\uc774\ub2e4. \ub192\uc774\ub294 \uc5bc\ub9c8\ub418\uc9c0 \uc54a\uc9c0\ub9cc \ud55c\ub824\uc218\ub3c4\uc758 \ube7c\uc5b4\ub09c \uacbd\uad00\uacfc \uc5b4\uc6b0\ub7ec\uc838 \uadf8 \uc5b4\ub290 \uba85\uc0b0 \ubabb\uc9c0 \uc54a\uac8c \uc808\ubb18\ud55c \uacbd\uad00\uc744 \uac04\uc9c1\ud558\uace0 \uc788\ub2e4.<BR><BR>\uc0b0\uc5d0 \uc624\ub974\ub294 \uae38\uc740 \uc5ec\ub7ff \uc788\uc73c\ub098 \ub3c8\uc9c0\ud3ec\uad6c\ub97c \uc2dc\ubc1c\uc810\uc73c\ub85c \uc9c0\ub9ac\uc0b0 \uc625\ub140\ubd09\uc744 \uac70\uce58\ub294 \ub2a5\uc120\uc744 \ud0c0\uace0 \uc9c4\ucd0c\uc73c\ub85c \ube60\uc9c0\ub294 \ucf54\uc2a4\ub97c \ud0dd\ud558\ub294 \uac83\uc774 \uc774 \uc0b0\uc758 \uc9c4\uba74\ubaa9\uc744 \uac00\uc7a5 \uc798 \uc990\uae38 \uc218 \uc788\ub294 \ubc29\ubc95\uc774\ub2e4.\ub3c8\uc9c0\ub294 \uc218\ub824\ud55c \uacbd\uad00\uc758 \ubc14\uc704\uc0b0\uacfc \ud478\ub978 \ubb3c\uc0b4\uc774 \ub118\uc2e4\ub300\ub294 \ubc14\ub2e4\ub97c \ubc30\uacbd\uc73c\ub85c\ud55c \ud55c\ud3ed\uc758 \uadf8\ub9bc\uac19\uc740 \uc21c\ubc15\ud55c \uc12c\ub9c8\uc744\uc774\ub2e4. \ub9c8\uc744\uc744 \ube60\uc838\ub098\uc640 \uc0b0\uc744 \uc624\ub974\uae30 \uc2dc\uc791\ud558\uba74 \ub3cc\ubc2d\uae38\uc774 \ub098\uc624\ub294\ub370 \uc2dc\uc57c\uc5d0 \ub4e4\uc5b4\uc624\ub294 \ubc14\ub2e4 \ud48d\uacbd \ub355\uc5d0 \ub3cc\uae38\uc774 \uc9c0\ub8e8\ud558\uc9c0 \uc54a\uace0 \uc544\uae30\uc790\uae30\ud558\uae30\ub9cc \ud558\ub2e4. \ud589\uc5ec \uc8fc\uc704 \uacbd\uad00\uc5d0 \uc2dc\uc120\uc744 \ube7c\uc557\uaca8 \ubc1c\uc774\ub77c\ub3c4 \ud5db\ub514\ub51c\uae4c \uac71\uc815\uc774 \ub420 \uc815\ub3c4\ub2e4.'), STRINGDECODE('\u2460 \uae08\ubd81\uac1c-\uc9c0\ub9ac\uc0b0-\ub2ec\ubc14\uc704-\uac00\ub9c8\ubd09-\uc625\ub140\ubd09-\uc9c4\ucd0c (\ucd1d 4\uc2dc\uac04 40\ubd84)<BR>\u2461 \ub300\ud56d-\ub2ec\ubc14\uc704-\uac00\ub9c8\ubd09-\uc625\ub140\ubd09-\uc9c4\ucd0c (\ucd1d 2\uc2dc\uac04 50\ubd84)'), '34.830446', '128.211962', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ud1b5\uc601\uc2dc \uc0ac\ub7c9\uba74'), 2816132100, 399, STRINGDECODE('\uc9c0\ub9ac\uc0b0(\ud1b5\uc601)'), STRINGDECODE('&lt;\uc9c0\ub9ac\uc0b0 \ubc14\ub77c\ubcf4\uc774\ub294 \uc0ac\ub7c9\ub3c4 \uc9c0\ub9ac\uc0b0&gt;<BR>\uc9c0\ub9ac\uc0b0\uc740 \ud55c\ub824\ud574\uc0c1\uad6d\ub9bd\uacf5\uc6d0\uc5d0 \uc788\ub294 \uc0ac\ub7c9\ub3c4\uc5d0 \uc704\uce58\ud55c \uc0b0\uc73c\ub85c \ubcf8\ub798 \uc774\ub984\uc740 \uc9c0\ub9ac\ub9dd\uc0b0\uc774\ub2e4. \uc774 \uba85\uce6d\uc740 \uad6d\ub9bd\uacf5\uc6d0 1\ud638\uc778 \uc9c0\ub9ac\uc0b0\uc774 \uc870\ub9dd\ub41c\ub2e4\ub294 \ub73b\uc73c\ub85c \ubd99\uc5ec\uc84c\ub2e4\uace0 \ud55c\ub2e4. \uc717\uc12c\uacfc \uc544\ub7ab\uc12c\uc73c\ub85c \ub098\ub258\uc5b4\uc838 \uc788\ub294\ub370 \uc774\ub4e4 \ub450 \uc12c \uc0ac\uc774\ub97c \ub3d9\uac15\uc774\ub77c\uace0 \ubd80\ub978\ub2e4. \uc9c0\ub9ac\uc0b0\uc740 \uc0ac\ub7c9\ub3c4\uc758 \uc8fc\uc12c\uc778 \uc717\uc12c\uc5d0 \uc790\ub9ac\ud558\uace0 \uc788\ub2e4. \uc9c0\ub9ac\uc0b0\uc758 \uc5ec\ub7ec \ubd09\uc6b0\ub9ac \uc911 \uc625\ub140\ubd09\uc774 \ube7c\uc5b4\ub09c \uc0b0\uc138\uc640 \uc870\ub9dd\uc744 \uc81c\uacf5\ud574 \uac00\uc7a5 \uc720\uba85\ud558\ub2e4. \uadf8\ub7ec\ub098 \uc554\ubd09\uc73c\ub85c \uc774\uc5b4\uc9c4 \ub2a5\uc120\uae38\uc740 \ub2e4\uc18c \uc704\ud5d8\ud55c\ub370 \ucd08\ubcf4\uc790\ub97c \uc704\ud574 \uc6b0\ud68c\ub85c\uac00 \uc788\ub2e4. \uc625\ub140\ubd09\uc740 \uc720\uba85\ud55c \uc804\uc124\uc744 \uac04\uc9c1\ud55c \ubd09\uc6b0\ub9ac\uae30\ub3c4 \ud558\ub2e4.<BR><BR>\uc5b4\ub9b4 \ub54c \uc5b4\uba38\ub2c8\ub97c \uc5ec\uc75c \uc625\ub140\uac00 \uc608\uc058\uac8c \uc131\uc7a5\ud558\uc790 \ucc9c\ub95c\uc744 \uc800\ubc84\ub9b0 \uc544\ubc84\uc9c0\uac00 \uc625\ub140\ub97c \ubc94\ud558\ub824 \ud574 \uc625\ub140\uac00 \ud5d8\ud55c \ubd09\uc6b0\ub9ac\uc5d0\uc11c \ubab8\uc744 \ubc14\ub2e4\ub85c \ub358\uc84c\ub2e4\uace0 \ud55c\ub2e4. \ubd09\uae0b\ud55c \uc0b0\ubd09\uc6b0\ub9ac \ud615\uc0c1\uc774 \uc5ec\uc778\uc758 \uac00\uc2b4\uc744 \ub2ee\uc558\uc744 \ubfd0 \uc544\ub2c8\ub77c \ud48d\uc218\uc9c0\ub9ac\uc124\ub85c\ub3c4 \uc0b0\uc138\uac00 \uc5ec\uc778\uc774 \uac70\ubb38\uace0\ub97c \ud0c0\ub294 \uc625\ub140 \ud0c4\uae08\ud615\uc774\ub77c \uc625\ub140\ubd09\uc758 \uc9c0\uba85 \uc720\ub798\uc5d0 \ud798\uc744 \ubcf4\ud0dc\uc900\ub2e4.'), STRINGDECODE('\ud55c\ub824\uc218\ub3c4\uc758 \ube7c\uc5b4\ub09c \uacbd\uad00\uacfc \uc870\ud654\ub97c \uc774\ub8e8\uace0 \ud2b9\ud788 \ubd88\ubaa8\uc0b0, \uac00\ub9c8\ubd09, \ud5a5\ubd09, \uc625\ub140\ubd09 \ub4f1 \uc0b0 \uc815\uc0c1\ubd80\uc758 \ubc14\uc704\uc0b0\uc774 \uae30\uc554\uad34\uc11d\uc744 \ud615\uc131\ud558\uace0 \uc870\ub9dd\uc774 \uc88b\uc740 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815,\"\uc9c0\ub9ac\uc0b0\uc774 \ubc14\ub77c \ubcf4\uc774\ub294 \uc0b0\"\uc774\ub780 \ub73b\uc5d0\uc11c \uc0b0\uc774\ub984\uc774 \uc720\ub798\ud558\uc600\uc73c\uba70, \ud604\uc9c0\uc5d0\uc11c\ub294 \uc9c0\ub9ac\uc0b0\uc774\ub77c\uace0\ub3c4 \ubd88\ub9ac\uc6cc\uc9c0\uace0 \uc788\uc74c. \ub2e4\ub3c4\ud574\uc758 \uc12c\uc744 \uc870\ub9dd\ud560 \uc218 \uc788\uc73c\uba70 \uae30\ubb18\ud55c \ubc14\uc704 \ub2a5\uc120\uc774 \ud2b9\ud788 \uc720\uba85'), STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ud1b5\uc601\uc2dc \uc0ac\ub7c9\uba74(\u86c7\u6881\u9762)\uc5d0 \uc788\ub294 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\uc0ac\ub7c9\ub3c4 \uac01 \ub9c8\uc744\uc5d0\uc11c \ubbfc\ubc15\uc9d1\uc744 \uc6b4\uc601\ud55c\ub2e4. <BR>\uc0ac\ub7c9\uc720\uc2a4\ud638\uc2a4\ud154 (\u260e 055-641-8247)<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc0ac\ub7c9\uba74\uc0ac\ubb34\uc18c \u260e 055-642-6119'), STRINGDECODE('\uae30\uc810\uc740 \ud1b5\uc601\uc774\ub2e4. \ub3c4\uc0b0 \uac00\uc624\uce58\uc120\ucc29\uc7a5\uc5d0\uc11c \uc5ec\uac1d\uc120\uc744 \ud0c0\uace0 \uac04\ub2e4. \uace0\uc131 \uc6a9\uc554\ud3ec, \uc0bc\ucc9c\ud3ec\uc5ec\uac1d\ud130\ubbf8\ub110\uc5d0\ub3c4 \uc0ac\ub7c9\ub3c4 \uac00\ub294 \ubc30\uac00 \uc788\ub2e4.<BR>\ud1b5\uc601\u2192\uc0ac\ub7c9\ub3c4 \uae08\ud3c9\ub9ac : \uce74\ud398\ub9ac \uc5ec\uac1d\uc120\uc774 1\uc77c 6\ud68c \uc6b4\ud589(\ub3d9\uacc4\uc2dc 5\ud68c \uc6b4\ud589), \ucc28\ub7c9 \uc218\uc1a1 \uac00\ub2a5.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(79, TIMESTAMP '2019-08-14 15:46:34.938', TIMESTAMP '2019-08-14 15:47:26.695', '46', STRINGDECODE('\uc0b0\uc774 \ubc14\uc704\ub85c \uc774\ub8e8\uc5b4\uc838 \ubd09\uc6b0\ub9ac\ub9c8\ub2e4 \ud558\ub298\uc744 \ucc0c\ub97c \ub4ef \uc19f\uc544\uc788\ub294 \ucc9c\uad00\uc0b0\uc740 \uc9c0\ub9ac\uc0b0, \uc6d4\ucd9c\uc0b0, \ub0b4\uc7a5\uc0b0, \ub0b4\ubcc0\uc0b0\uacfc \ud568\uaed8 \ud638\ub0a8\uc758 \uff15\ub300 \uba85\uc0b0\uc911 \ud558\ub098\ub2e4.<BR><BR>\uc544\uae30\ubc14\uc704, \uc0ac\uc790\ubc14\uc704, \uc885\ubd09, \ucc9c\uc8fc\ubd09, \uad00\uc74c\ubd09, \uc120\uc7ac\ubd09, \ub300\uc138\ubd09, \uc11d\uc120\ubd09, \ub3db\ub300\ubd09, \uad6c\ub8e1, \uac08\ub300\ubd09, \ub3c5\uc131\uc554, \uc544\uc721\ud0d1 \ub4f1\uc744 \ube44\ub86f \uc218\uc2ed\uac1c\uc758 \uae30\uc554\uad34\uc11d\uacfc \uae30\ubd09\uc774 \uaf2d\ub300\uae30 \ubd80\ubd84\uc5d0 \ube44\uc8fd\ube44\uc8fd \uc19f\uc544 \uc788\ub294\ub370, \uadf8 \ubaa8\uc2b5\uc774 \uc8fc\uc625\uc73c\ub85c \uc7a5\uc2dd\ub41c \ucc9c\uc790\uc758 \uba74\ub958\uad00 \uac19\ub2e4\ud558\uc5ec \ucc9c\uad00\uc0b0\uc774\ub77c \ubd88\ub800\ub2e4\uace0 \ud55c\ub2e4.'), STRINGDECODE('\u2460 \uc7a5\ucc9c\uc7ac-\uc591\uadfc\uc554-\uc815\uc6d0\uc554-\uc5f0\ub300\ubd09-\ud658\ud76c\ub300-\uad6c\uc815\ubd09-\ucc9c\uad00\uc0ac(\ucd1d 2\uc2dc\uac04 35\ubd84)<BR>\u2461 \ucc9c\uad00\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-\uc9c0\uc7a5\ubd09-\ud658\ud76c\ub300-\uc5f0\ub300\ubd09-\uc6b0\ub450\ubd09-\uc0c1\ucd0c(\ucd1d 2\uc2dc\uac04 55\ubd84)<BR>\u2462 \uc7a5\ucc9c\uc7ac-\uae08\uc218\uad74-\ud5ec\uae30\uc7a5-\uc5f0\ub300\ubd09-\ud5ec\uae30\uc7a5-\ud658\ud76c\ub300-\ud0d1\uc0b0\uc0ac(\ucd1d 3\uc2dc\uac04)'), '34.552747', '126.939024', STRINGDECODE('\uc804\ub77c\ub0a8\ub3c4 \uc7a5\ud765\uad70 \uad00\uc0b0\uc74d, \ub300\ub355\uc74d'), 2618021900, 724, STRINGDECODE('\ucc9c\uad00\uc0b0'), STRINGDECODE('&lt;\ub2ed\uc758 \uc7a5\ud0dc \ubaa8\uc591\uc758 \ud558\ub2ac\ubc14\ub78c\uc0b0&gt;<BR>\ube44\uc0ac\ub9bf\ub300\ub85c \ub9