INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(80, TIMESTAMP '2019-08-14 15:46:34.939', TIMESTAMP '2019-08-14 15:47:27.279', '41', STRINGDECODE('\ub9c8\uc11d\uc5d0\uc11c \uc11c\ubd81\ucabd\uc73c\ub85c 4.5km \ub5a8\uc5b4\uc838 \uc788\ub294 \uc0b0\uc73c\ub85c 1983\ub144 \uad70\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5b4 \ud734\uc77c\uc774\uba74 \ub9ce\uc740 \uc0ac\ub78c\uc774 \ubab0\ub9ac\uace0 \uc788\ub2e4.<BR><BR>\ud2b9\ud788 \uc774 \uacf3\uc740 \uc2a4\ud0a4\uc7a5\uacfc \uccad\uc18c\ub144 \uc2ec\uc2e0\uc218\ub828\uc7a5 \ub4f1\uc774 \uc788\uc73c\uba70, \uc0b0\uc758 \ud615\uc138\uac00 \ud5d8\ud558\uc9c0 \uc54a\uace0 \ub098\ubb34\uac00 \uc6b8\ucc3d\ud558\uc5ec \ub2f9\uc77c \ub4f1\uc0b0\ucf54\uc2a4\ub85c \uc54c\ub9de\ub2e4. 1990\ub144 11\uc6d4 1\uc77c\ubd80\ud130 \uc57c\uc601\uc7a5\uc678 \uc9c0\uc5ed\uc5d0\uc11c\ub294 \ucde8\uc0ac\uac00 \uae08\uc9c0\ub410\ub2e4.'), STRINGDECODE('\uac00\uace1\ucd08\uad50-\ub3d9\ub989-\uc815\uc0c1-\uad18\ub77c\ub9ac\uace0\uac1c-\ubcf4\uad11\uc0ac\uac00\uace1\ucd08\uad50(\ucd1d 4\uc2dc\uac04 10\ubd84)'), '37.693812', '127.231459', STRINGDECODE('\uacbd\uae30\ub3c4 \ub0a8\uc591\uc8fc\uc2dc \uc624\ub0a8\uc74d, \ud654\ub3c4\uc74d'), 2111091500, 810, STRINGDECODE('\ucc9c\ub9c8\uc0b0'), STRINGDECODE('&lt;\uc11d \uc790\ub9cc \ub354 \uae38\uba74 \ud558\ub298\uc744 \ub9cc\uc9c4\ub2e4&gt;<BR>\ucc9c\ub9c8\uc0b0\uc740 \ud558\ub298 \ub192\uc774 \uc19f\uc544\uc624\ub978 \uc0b0\uc138\uac00 \ube7c\uc5b4\ub09c \uba85\uc0b0\uc774\ub2e4. \uacbd\ucd98\uad6d\ub3c4\ub97c \ud0c0\uace0 \uc11c\uc6b8\ub85c \uac08 \ub54c\ub098 \uc591\ud3c9 \uc11c\uc885\uba74\uc758 \ubd81\ud55c\uac15\ubcc0\uae38\uc744 \uac00\uba74\uc11c \ubcf4\uba74 \uac00\ud788 \uad70\uacc4\uc77c\ud559\uc774\ub2e4. \uc790\ub77d\uc758 \ud3c9\ub0b4\uc640 \ub9c8\uc11d, \uc624\ub0a8\ub9ac\uc5d0 \uc544\ud30c\ud2b8\uc232\uc774 \ub4e4\uc5b4\ucc28 \ud638\uc813\ud55c \ub9db\uc740 \uc0ac\ub77c\uc84c\uc9c0\ub9cc \ubcf8\ubc14\ud0d5\uc740 \uc5b4\ub290 \uc0b0\uc5d0 \ub300\ub3c4 \uafc0\ub9ac\uc9c0 \uc54a\uc744 \ub9cc\ud07c \uc900\uc218\ud558\ub2e4. \uadf8\ub798 \uc774\uacf3\uc744 \uc9c0\ub098\ub358 \uc774\uc131\uacc4 \"\"\uac00\ub294 \uacf3\ub9c8\ub2e4 \uccad\uc0b0\uc740 \ub9ce\uc9c0\ub9cc \uc800\uac74 \uaf2d \ud478\ub978 \ud558\ub298\uc5d0 \ud640(\u7b0f)\uc744 \uaf42\uc544\ub193\uc740 \uac83 \uac19\ub3c4\ub2e4. \uc190\uc774 \uc11d \uc790\ub9cc \ub354 \uae38\ub2e4\uba74 \uc800 \ub05d\uc5d0\uc11c \ud558\ub298\uc744 \ub9cc\uc9c8 \uc218 \uc788\uaca0\ub2e4(\u624b\u9577\u4e09\u5c3a\u53ef\u6469\u5929)\"\"\uace0 \uc74a\uc5c8\ub2e4. \ucc9c\ub9c8\uc0b0\uc774\ub77c\ub294 \ub118\uce58\ub294 \uc774\ub984\uc744 \uc5bb\uac8c \ub41c \uc720\ub798\ub2e4. <BR><BR>\uc815\uc0c1\ubd80\ub294 \ubc14\uc704\ub85c \ub418\uc5b4\uc788\uc9c0\ub9cc \uc790\ub77d\uc740 \uc721\uc0b0\uc774\ub77c \ubd04\ucca0 \uc2e0\ub85d\uacfc \uac00\uc744 \ub2e8\ud48d\uc774 \uacf1\ub2e4. \"\"\ud654\ub824\ud55c \uc2ac\ud514\"\"\uc774\ub77c\uace0 \ud560 \ub9cc\ud07c \uc544\ub984\ub2e4\uc6b4 \ub2e8\ud48d\uc774\ub2e4. \uc2a4\ud0a4\uc7a5\uc774 \ub4e4\uc5b4\uc130\uc744 \uc815\ub3c4\ub85c \ub208\uc774 \ub9ce\uc544 \uaca8\uc6b8\uc5d0\ub294 \uc124\uacbd\ub3c4 \ubcfc \ub9cc\ud558\ub2e4. \uac70\uae30\ub2e4 \ubd81\ubc29\uc2dd\ubb3c\uc778 \ub9cc\uc8fc\ubc14\ub78c\uaf43\uc774 \uc0b4 \ub9cc\ud07c \uc2dd\uc0dd\uc774 \ub2e4\uc591\ud558\ub2e4. <BR><BR>\ud654\ub3c4\uc74d\uc758 \ucc9c\ub9c8\uc0b0 \uc2ec\uc2e0\uc218\ub828\uc7a5 \uc785\uad6c\ub294 \uc804\ub098\ubb34\uc232\uc774 \uc88b\ub2e4. \uc815\uc0c1 \uc554\ub989\uc5d0\ub294 \ub178\uc1a1\uc774 \ud718\ub298\uc5b4\uc838 \uc78a\uc9c0 \ubabb\ud560 \ud48d\uacbd\uc73c\ub85c \ub0a8\ub294\ub2e4.'), STRINGDECODE('\uc0b0\uaf2d\ub300\uae30\ub97c \uc911\uc2ec\uc73c\ub85c \ub2a5\uc120\uc774 \uc0ac\ubc29\uc5d0 \ubed7\uc5b4\uc788\uc5b4 \uc5b4\ub290 \uc9c0\uc810\uc5d0\uc11c\ub098 \uc815\uc0c1\uc744 \ubcfc\uc218 \uc788\ub294 \ud2b9\uc774\ud55c \uc0b0\uc138\uc640 \uc2dd\ubb3c\uc0c1\uc774 \ud48d\ubd80\ud558\uc5ec \uc2dd\ubb3c\uad00\ucc30 \uc0b0\ud589\uc9c0\ub85c \uc774\ub984\ub098 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0b0 \ub0a8\ucabd\uc5d0 \ucc9c\ub9c8\uc0b0\uc2a4\ud0a4\uc7a5\uc774 \uc788\uc74c'), STRINGDECODE('\uc2a4\ud0a4\uc7a5\uc73c\ub85c, \uccad\uc18c\ub144 \uc2ec\uc2e0\uc218\ub828\uc7a5\uc73c\ub85c'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0 \uc815\ubcf4<BR>\ubb35\ud604\ub9ac\uc5d0 \ucc9c\ub9c8\uc0b0\uc2ec\uc2e0\uc218\ub828\uc7a5(\u260e031-593-1060)\uc744 \uc774\uc6a9\ud560 \uc218 \uc788\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ub0a8\uc591\uc8fc\uc2dc\uccad \ub3c4\uc2dc\uacc4\ud68d\uad6d \uacf5\uc6d0\ub179\uc9c0\uacfc \u260e031-590-2733'), STRINGDECODE('\ub9c8\uc11d\ud589 \ubc84\uc2a4\ub294 \uccad\ub7c9\ub9ac\uc5d0\uc11c \uc140 \uc218 \uc5c6\uc774 \ub9ce\ub2e4. \ud55c \uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2c8\ub294 \uacbd\ucd98\uc120 \uc5f4\ucc28\ub97c \uc774\uc6a9\ud574\ub3c4 \ub41c\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(81, TIMESTAMP '2019-08-14 15:46:34.939', TIMESTAMP '2019-08-14 15:47:27.872', '48', STRINGDECODE('\ud1b5\ub3c4\uc0ac \uc778\ud130\uccb4\uc778\uc9c0\uc5d0\uc11c \ubd80\uc0b0\ucabd\uc73c\ub85c 7km \ub5a8\uc5b4\uc9c4 \uacf3\uc5d0 \uc788\ub294 \uc0b0\uc73c\ub85c, \uacbd\ubd80\uace0\uc18d\ub3c4\ub85c \uc704\ub85c \uac00\uc124\ub41c \uc6a9\uc5f0 \uc721\uad50\ub97c \uc9c0\ub098 3km\ucbe4 \uac00\uba74 \ub0b4\uc6d0\uc0ac\ub85c \uac00\ub294 \uace8\uc9dc\uae30\uc5d0 \ub2e4\ub2e4\ub974\uac8c \ub41c\ub2e4. \uc5ec\uae30\uc11c\ubd80\ud130 \uae30\uc554\uad34\uc11d\uacfc \uc6b8\ucc3d\ud55c \uc232\uc774 \uc6b0\uac70\uc838 \uae08\uac15\uc0b0\uc758 \uacc4\uace1\uc744 \ubc29\ubd88\ucf00\ud558\ub294 \ucc9c\uc131\uc0b0\uc774 \uc2dc\uc791\ub41c\ub2e4.'), STRINGDECODE('\u2460 \ub9e4\ud45c\uc18c-\uc131\ubd88\uc554\uacc4\uace1 \uc785\uad6c-\uacf5\ub8e1\ub2a5\uc120-\uc9d1\ubd81\uc7ac-\ubd81\ubd09-\uc740\uc218\uace0\uac1c-\uc6d0\ud6a8\uc554 \uc8fc\ucc28\uc7a5-\ubb34\uc9c0\uac1c\ud3ed\ud3ec -\uc7a5\ud765(6\uc2dc\uac04 20\ubd84)<BR>\u2461\ubc31\ub3d9-\ubc95\uc218\uc6d0-\ubd81\ubd09-\uc740\uc218\uace0\uac1c-\uc6d0\ud6a8\uc554-\ud64d\ub8e1\uc0ac \uc8fc\ucc28\uc7a5(4\uc2dc\uac04 20\ubd84)<BR>\u2462 \ub9e4\ud45c\uc18c-\uc0b0\ud558\ub3d9\uacc4\uace1-\uc9d1\ubd81\uc7ac-\ubd81\ubd09-\uc740\uc218\uace0\uac1c-\ud654\uc5c4\ub2aa-\uc0c1\ubd81(\ucd1d 6\uc2dc\uac04 30\ubd84)'), '35.462135', '129.084748', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \uc591\uc0b0\uc2dc \ud558\ubd81\uba74, \uc0c1\ubd81\uba74, \uc6c5\uc0c1\uc74d'), 2810181300, 920, STRINGDECODE('\ucc9c\uc131\uc0b0'), STRINGDECODE('&lt;\ubd09\ub9cc\u00b7\uacc4\ub958\ubbf8\uc5d0 \uace0\uce35\uc2b5\uc6d0\uae4c\uc9c0 \uc788\ub294 \uac16\ucda4\uaf34&gt;<BR>\"\"\uac00\ud30c\ub978 \uc5f0\ubd09\ub4e4 \ucca9\ucca9\uc774\uc694 \uae4a\uace0 \uae4a\uc740 \uace8\uc9dc\uae30 \uad74 \uac19\ub2e4(\u9023\u5cf0\u758a\u3000 \u6d1e\u5e9c\u6df1\u9083)\"\"\ub294 \uc0b0\uc774\ub2e4. \uc6c5\uc0c1\ucabd\uc5d0\uc11c \ubcf4\uba74 \uae4e\uc544\uc9c0\ub978 \ub4ef\ud55c \uc0b0\uc138\uac00 \ub2e8\uc21c\u00b7\uc6c5\uc7a5\ud55c \ud651\uc0b0\uc778\ub370 \uc591\uc0b0\ucabd\uc73c\ub85c\ub294 \ud798\ucc2c \uc0b0\uc904\uae30\ub97c \uc5ec\ub7ff \ubed7\uc5b4\ub0b4\ub9ac\uba74\uc11c \uadf8 \uc704\uc5d0 \uc218\ub9ce\uc740 \uc554\ubd09\uc744 \uc62c\ub824\ub193\uc558\ub2e4. \ub2a5\uc120\ub4e4 \uc0ac\uc774 \uace8\uc9dc\uae30\ub4e4\uc740 \uc554\ubc18\uacc4\ub958 \ub2f4\uc18c(\u6f6d\u6cbc)\uac00 \uc990\ube44\ud558\ub2e4. &lt;\uc2e0\uc99d\ub3d9\uad6d\uc5ec\uc9c0\uc2b9\ub78c&gt;\uc758 \uc800 \ud45c\ud604\uc774 \ud558\ub098\ub3c4 \uc5b4\uae0b\ub0a8 \uc5c6\ub294, \ubd09\ub9cc\ubbf8\uc640 \uacc4\ub958\ubbf8\uac00 \ub2e4 \uac19\uc774 \ub6f0\uc5b4\ub09c \uba85\uc0b0\uc778 \uac83\uc774\ub2e4. <BR><BR>\uc608\uc804 \uc9c0\ub3c4\uc5d0 \"\"\ucc9c\uc131\uc0b0\"\"\uc774 \ubd99\uc5b4\uc788\uc5c8\ub358 \ubd81\ubd09(\uc57d 830m) \uc77c\ub300\ub294 \ub610 \uc5b5\uc0c8\uc758 \ucc9c\uad6d\uc774\ub2e4. \uadf8 \ucc9c\uad6d \uc5ec\uae30\uc800\uae30 \ubc8c\uc5ec\uc788\ub294 \uace0\uce35\uc2b5\uc6d0\uc5d0\ub294 \ub098\ub77c\ub97c \ub5a0\ub4e4\uc369\ud558\uac8c \ud588\ub358 \uaf2c\ub9ac\uce58\ub808\ub3c4\ub871\ub1fd\uacfc \ub048\ub048\uc774\uc8fc\uac71 \uac19\uc740 \ud76c\uadc0 \ub3d9\uc2dd\ubb3c\uc774 \uc0b0\ub2e4. T\uc790 \ubaa8\uc591\uc73c\ub85c \ud750\ub974\ub294 \ud2b9\uc774\uc9c0\ud615 \ubc95\uc218\uacc4\uace1 \uc0c1\ub958\uc758 \uc774\uc0c9\uc9c0\ub300\ub2e4. <BR><BR>\uc774\ub545\uc5d0\uc11c \uc77c\ucd9c\uc744 \uac00\uc7a5 \uba3c\uc800 \ubcfc \uc218 \uc788\ub294 \uacf3\uc73c\ub85c\ub3c4 \uc774\ub984\ub098 \uc788\ub2e4. \uadf8\ub7f0 \ub9cc\ud07c \uc870\ub9dd\uc774 \ub6f0\uc5b4\ub098 \uc601\ub0a8\uc54c\ud504\uc2a4 \uc544\ud649 \uc900\ubd09\uc774 \ud558\ub098\ud558\ub098 \ub610\ub837\ub610\ub837 \ubcf4\uc774\uace0 \ub0a8\uc73c\ub85c \ubd80\uc0b0 \uae08\uc815\uc0b0\uc740 \ubb3c\ub860\uc774\ub824\ub2c8\uc640 \uba40\ub9ac \uae40\ud574 \uc2e0\uc5b4\uc0b0, \ub9c8\uc0b0 \ubb34\ud559\uc0b0\uae4c\uc9c0 \ub450\ub8e8\ub450\ub8e8 \uac00\ub2a0\ud560 \uc218 \uc788\ub2e4. <BR><BR>\ucc9c\uc131\uc0b0\uc774\ub77c\uace0 \ubd88\ub9ac\uac8c\ub41c \uc778\uc5f0\uc740 \uc6d0\ud6a8\ub300\uc0ac\uac00 \uc774 \uc0b0\uc73c\ub85c \ucc9c \uba85\uc758 \uc911\uc0dd\uc744 \uc785\uc0b0\uc2dc\ucf1c \ubaa8\ub450 \uc131\uc778\uc774 \ub418\uac8c\ud588\ub2e4\ub294 \ub370\uc11c \ube44\ub86f\ub410\ub2e4. \ud55c\ud3b8\uc73c\ub85c\ub294 \uadf8\ub4e4\uc774, \ubc25\uc0c1\uc744 \ub358\uc838 \ubaa9\uc228\uc744 \uad6c\ud574\uc900 \uc740\ud61c\ub97c \uc78a\uc9c0 \ubabb\ud574 \ucc3e\uc544\uc628 \ub2f9\ub098\ub77c \uc2b9\ub824\ub4e4\uc774\ub77c\ub294 \uc124\ub3c4 \uc788\ub294\ub370 \ub2f9\ub098\ub77c\ub97c \uac00\uc9c0\ub3c4 \uc54a\uc740 \uc6d0\ud6a8\ub300\uc0ac\ub97c \uac00\uc9c0\uace0 \ub108\ubb34 \uc7a5\ub09c\uc744 \uce5c \ub4ef\ud558\ub2e4. \uc0c1\ubd09\uc740 \ucd5c\uadfc\uae4c\uc9c0 \uc6d0\ud6a8\uc0b0\uc774\ub77c\uace0 \ud588\ub2e4.'), STRINGDECODE('\uae08\uac15\uc0b0\uc758 \ucd95\uc18c\ud310\uc774\ub77c\uace0 \ubd88\ub9b4 \uc815\ub3c4\ub85c \uacbd\uad00\uc774 \ub6f0\uc5b4\ub098\uace0, \ud2b9\ud788 \uc0b0\uc815\uc0c1\ubd80\uc5d0 \ub4dc\ub113\uc740 \ucd08\uc6d0\uacfc \uc0b0\uc9c0\uc2b5\uc9c0\uac00 \ubc1c\ub2ec\ud558\uc5ec \ub048\ub048\uc774\uc8fc\uac71 \ub4f1 \ud76c\uadc0\uc2dd\ubb3c\uacfc \uc218\uc11c\uace4\ucda9\uc774 \uc11c\uc2dd\ud558\ub294 \ub4f1 \uc0dd\ud0dc\uc801 \uac00\uce58\uac00 \ub192\uc740 \uc810\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ubd04\uc5d0\ub294 \uc9c4\ub2ec\ub798\uc640 \ucca0\ucb49, \uac00\uc744\uc5d0\ub294 \ub2a5\uc120\uc758 \uc5b5\uc0c8\uac00 \uc7a5\uad00\uc744 \uc774\ub8e8\uba70, \uc6d0\ud6a8\ub300\uc0ac\uac00 \ucc3d\uac74\ud588\ub2e4\ub294 \ub0b4\uc6d0\uc0ac\uac00 \uc788\uc74c'), STRINGDECODE('\uae30\uc554\uad34\uc11d\uc774 \uc870\uac01\ud55c \uae08\uac15\uc0b0\uc758 \ucd95\uc18c\ud310'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ub0b4\uc6d0\uc0ac \uc785\uad6c\uc5d0 \ubbfc\ubc15\uc9d1\uacfc \uc5ec\uad00\uc774 \ub9ce\ub2e4. VIP\ubaa8\ud154(\u260e055-375-6256), \ud504\ub9b0\uc2a4\ubaa8\ud154(\