INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(82, TIMESTAMP '2019-08-14 15:46:34.94', TIMESTAMP '2019-08-14 15:47:28.467', '43', STRINGDECODE('\ucda9\ubd81 \uc601\ub3d9\uad70 \uc591\uc0b0\uba74\uacfc \ucda9\ub0a8 \uae08\uc0b0\uad70 \uc81c\uc6d0\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \ucc9c\ud0dc\uc0b0\uc740 \uae30\uc554\uc808\ubcbd\uacfc \uc218\ub9bc\uc774 \uc870\ud654\ub97c \uc774\ub8e8\uace0 \uc788\uc5b4 \uc77c\uba85 ''\ucda9\ubd81\uc758 \uc124\uc545''\uc774\ub77c \ubd88\ub9ac\uace0 \uc788\ub294 \uc0b0\uc774\ub2e4. \ub4f1\uc0b0\ub85c\ub294 \ubc14\uc704\uc9c0\ub300\uc5d0\ub2e4 \uc548\ub0b4\ud45c\uc9c0\uc640 \ub85c\ud504\uac00 \uc124\uce58\ub418\uc5b4 \uc788\uc5b4 \uc544\uae30\uc790\uae30\ud55c \ubc14\uc704\uc0b0\ud589\uc744 \ub9cc\ub07d\ud560 \uc218 \uc788\ub294 \ubb18\ubbf8\uac00 \uc788\ub294 \uc0b0\uc774\uba70 \ub6f0\uc5b4\ub09c \uc790\uc5f0\uacbd\uad00\uc744 \uc790\ub791\ud558\ub294 \uc0b0\uc774\uae30\ub3c4 \ud558\ub2e4. <BR><BR>\uc774\ub984\ub09c \uba85\uc18c\uac00 \uc0b0\uc7ac\ud574 \uc788\uc5b4 \uac00\uc871\ub3d9\ubc18 \ub4f1\uc0b0\uc9c0\ub85c\ub3c4 \uc88b\ub2e4. \uacc4\uace1\uc740 \ube44\ub85d \uc9e7\uc73c\ub098 \uc2dc\uc6d0\ud558\uac8c \uc3df\uc544\uc9c0\ub294 \uc6a9\ucd94\ud3ed\ud3ec\uc640 \uc9c4\uc8fc\ud3ed\ud3ec\uac00 \uc788\uace0, \ubd04\uc5d0\ub294 \uc9c4\ub2ec\ub798, \ubc9a\uaf42\uc774 \uc628 \uc0b0\uc744 \ub4a4\ub36e\uace0 \uac00\uc744\uc5d0\ub294 \ub2e8\ud48d \ub610\ud55c \uc88b\ub2e4.<BR><BR>\ucc9c\ud0dc\uc0b0\uc5d0\ub294 \uc591\uc0b0 8\uacbd \uc911 \uc81c1\uacbd\uc778 \uc601\uad6d\uc0ac\uac00 \uc788\uc73c\uba70, 10km \ub5a8\uc5b4\uc9c4 \uacf3\uc5d0 \uc5ec\uc758\uc815, \uac15\uc120\ub300, \uc6a9\uc554 \ub4f1\uc774 \uc788\uc5b4 \ubcfc\uac70\ub9ac\uac00 \ub9ce\uc740 \uacf3\uc774\ub2e4. \uc601\uad6d\uc0ac \uc55e \ub4f1\uc0b0\ub85c \uc785\uad6c\uc5d0\ub294 \ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c233\ud638\ub85c \uc9c0\uc815\ub41c \uc218\ub839 \ucc9c\ub144\uc774 \ub118\ub294 \uc740\ud589\ub098\ubb34\uac00 \uc11c \uc788\ub2e4. <BR><BR>\uc774 \ub098\ubb3