INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(83, TIMESTAMP '2019-08-14 15:46:34.94', TIMESTAMP '2019-08-14 15:47:29.06', '47', STRINGDECODE('\ubd09\ud654\uc74d\uc5d0\uc11c \ub3d9\ub0a8\ucabd\uc73c\ub85c 26km \ub5a8\uc5b4\uc9c4 \uccad\ub7c9\uc0b0(869.7m)\uc5d0\ub294 \uae08\ud0d1\ubd09\uc744 \ube44\ub86f\ud558\uc5ec \uc544\ub984\ub2e4\uc6b4 \ubd09\uc6b0\ub9ac 12\uac1c\uc640 8\uac1c\uc758 \ub3d9\uad74\uc774 \uc788\ub2e4.<BR><BR>\uc790\uc5f0\uacbd\uad00\uc774 \uc218\ub824\ud558\uc5ec \uc61b\ubd80\ud130 \uc18c\uae08\uac15\uc774\ub77c \uc804\ud558\uc5ec\uc9c0\ub294 \uba85\uc0b0\uc73c\ub85c\uc368 \ud0dc\ubc31\uc0b0\uc5d0\uc11c\ubd80\ud130 \uc2dc\uc791\ub418\ub294 \ub099\ub3d9\uac15 \uc904\uae30\uac00 \uc808\ubcbd\uc744 \uac10\uc544\ub3cc\uc544 \uc808\uacbd\uc744 \ube5a\uc5b4\ub0b4\uace0 \uc788\uc73c\uba70, \uc2e0\uc120\uc774 \ub0b4\ub824\uc640 \ubc14\ub451\uc744 \ub450\uc5c8\ub2e4\ub294 \uc2e0\uc120\ub300\uc640 \uc120\ub140\uac00 \uac00\ubb34\uc720\ud76c\ub97c \uc990\uacbc\ub2e4\ub294 \uc120\ub140\ubd09\uc744 \ube44\ub86f\ud558\uc5ec 12\ubd09\uc758 \uae30\uc554\uad34\uc11d\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\uace0, \uc2e0\ub77c\ub54c\ubd80\ud130 \uadfc\uc138\uc5d0 \uc774\ub974\uae30\uae4c\uc9c0 \uae40\uc0dd, \ud1f4\uacc4\uc774\ud669\ub4f1 \uc120\ud604\ub4e4\uc774 \uc218\ub3c4\ub97c \ud558\ub358 \uc720\ubd88\uc120\uad50 \ubc1c\uc0c1\uc9c0\ub85c \ub110\ub9ac \uc54c\ub824\uc838 \uc788\uc744\ubfd0\ub9cc \uc544\ub2c8\ub77c \uace0\ub824 \uacf5\ubbfc\uc655 10\ub144\uc5d0 \uc81c2\ucc28 \ud64d\uac74\uc801\uc758 \ub09c\uc744 \ud53c\ud558\uc5ec \uc655\uc774 \uc774\uacf3\uc5d0 \uc640 \ub9c8\uc9c0\ub9c9\uc694\uc0c8\ub85c\uc368 \uc0b0\uc131\uc744 \ucd95\uc870\ud558\uc600\uace0, \ub09c\uc774 \ub05d\ub09c\ud6c4 \uc655\uc774 \uc774\uacf3\uc744 \ub5a0\ub098\uc790 \uc8fc\ubbfc\ub4e4\uc774 \uc655\uc744 \ucd94\ubaa8\ud558\uae30 \uc704\ud558\uc5ec \uacf5\ubbfc\uc655\ub2f9\uc744 \uc138\uc6b0\uace0 \ubd04, \uac00\uc744\ub85c \uacf5\uc744 \ub4dc\ub9ac\uc790 \ub300\ub780\uc774 \uc788\uc744\ub54c \ub9c8\ub2e4 \uc18c\ub9ac\uac00 \uc6b8\ub824 \uc7ac\ub09c\uc744 \ud53c\ud560\uc218 \uc788\uc5c8\ub2e4\ub294 \uc804\uc124\uc774 \uc788\uc73c\uba70, \uc774\uacf3 \uacf5\ubbfc\uc655\ub2f9\uc744 \uc624\ub974\ub294 \ub4f1\uc0b0\ub85c\ub294 \ub9d0 \ub2e4\uc12f \ub9c8\ub9ac\uac00 \ud55c\uaebc\ubc88\uc5d0 \uc9c0\ub098\ub2e4\ub154\ub2e4\ub294 \u4e94\u99ac\u9053\uac00 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc717\ub4b7\uc2e4-795\ubd09-\uc815\uc0c1-795\ubd09-\uccad\ub7c9\uc0ac-\uccad\ub7c9\ub9c8\uc744 (\ucd1d 3\uc2dc\uac04 50\ubd84)<BR>\u2461 \uc774\ub984\uc2e4-\ubb3c\ud2f0\uc7ac-\uccad\ub7c9\uc0b0\ud734\uac8c\uc18c-\ucd95\uc735\ubd09-\uccad\ub7c9\uc0b0\ud734\uac8c\uc18c-\uccad\ub7c9\ub9c8\uc744 (\ucd1d 3\uc2dc\uac04)'), '36.83925', '128.888156', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \ubd09\ud654\uad70 \uba85\ud638\uba74'), 2708013300, 870, STRINGDECODE('\uccad\ub7c9\uc0b0'), STRINGDECODE('&lt;\uacfc\uc7a5\uc774 \uc808\ub300 \uc544\ub2cc \ud55c \uc1a1\uc774 \uc5f0\uaf43&gt;<BR>\ud55c\uc1a1\uc774 \uc5f0\uaf43\uc73c\ub85c \uc77c\uceec\uc5b4\uc9c0\ub294 \ub099\ub3d9\uac15\ubcc0\uc758 \uba85\uc0b0\uc774\ub2e4. \uc774\ub7f0 \ube44\uc720\ub294 \ud754\ud788 \uacfc\uc7a5\uc774\uae30 \uc77c\uc468\ub370 \uc774 \uc0b0\ub9cc\ud07c\uc740 \ud5c8\ud22c\ub8e8 \ud55c \uc18c\ub9ac\ub85c \uc5ec\uaca8\uc9c0\uc9c0 \uc54a\ub294\ub2e4. \uae08\ud0d1\ubd09\uc758 \uc220\ub300\ub97c \uc0ac\ubc29\uc5d0\uc11c \uc5d0\uc6cc\uc2fc \ucd95\uc735\ubd09, \uacbd\uc77c\ubd09, \ubcf4\uc0b4\ubd09, \uc815\uc0c1 \uc7a5\uc778\ubd09\uc758 \uaf43\uc78e\ub4e4 \uac00\uc6b4\ub370 \uc549\ud600\uc9c4 \uccad\ub7c9\uc0ac\uac00 \ub354\ud560 \ub098\uc704 \uc5c6\uc774 \ud3ec\uadfc\ud558\ub2e4. <BR><BR>\ub192\uc774\uac00 \uc11c\uc6b8 \uc0bc\uac01\uc0b0\ubcf4\ub2e4 \ub354 \ub192\ub2e4. \uac8c\ub2e4\uac00 \uc8fc\uc694 \ubd09\uc6b0\ub9ac\ub4e4\uc774 \uc587\uc740 \ud478\ub098\ubb34\uc637\uc744 \uac78\uce5c \ub4ef\ud55c \ubc14\uc717\ub369\uc774\ub77c \ubd09\ub9cc\ubbf8(\u5cf0\u5dd2\u7f8e)\uac00 \uadf8\ub9cc\uc774\ub2e4. \ubcf4\uc0b4\ubd09\uc758 \uacbd\uc6b0 \uc554\ubcbd\ub4f1\ubc18 \uc7a5\ube44 \uc5c6\uc774\ub294 \uc624\ub97c \uc218 \uc5c6\uc744 \uc815\ub3c4\ub85c \uac00\ud30c\ub974\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \uc0b0\uc5ed\uc774 \uc881\uc544 \ubc18\ub098\uc808\uc774\uba74 \ub3cc\uc544\ubcfc \uc218 \uc788\ub2e4. <BR><BR>\ub2a5\uc120\uc744 \ubc18\uc2dc\uacc4\ubc29\ud5a5\uc73c\ub85c \ub3cc \ub54c \uc885\ucc29\uc9c0\uac00 \ub418\ub294 \uc815\uc0c1\uc5d0\uc11c\uc758 \ub099\ub3d9\uac15 \uacbd\uce58\uac00 \uc2dc\uc6d0\uc2a4\ub7fd\ub2e4. \ubc1c \uc544\ub798 \uad11\uc11d\ub098\ub8e8\ub294 \uc61b\ub0a0\uc740 \ubb3c\ub860 \ub2e4\ub9ac\uac00 \ub193\uc778 \uc9c0\uae08\ub3c4 \ub099\ub3d9\uac15 \ucd5c\uace0 \uc808\uacbd\uc73c\ub85c \uaf3d\ud78c\ub2e4. <BR>\ubc16\uc5d0\uc11c \ubcf4\ub294 \uc0b0\uc758 \ubaa8\uc2b5\uc740 \uadf8\ub0e5 \uc0b0\uc218\ud654\ub2e4. \ubd81\uace1\ub9ac \uc717\ub4b7\uc2e4\ub9c8\uc744\uc774 \uc804\ub9dd\ub300\uc778\ub370 \uc5ed\uad11 \ubc1b\uc544 \ube5b\ub098\ub294 \ud558\ub298\uae08 \uc544\ub798 \uc5b4\ub451\ud55c \ubcbd\uc758 \ud478\ub974\uc2a4\ub984\ud55c \uc0c9\uae54\uc774 \ub1cc\ub9ac\uc5d0\uc11c \uc0ac\ub77c\uc9c0\uc9c0 \uc54a\ub294\ub2e4.'), STRINGDECODE('\uc0b0\uc138\ub294 \ud06c\uc9c0 \uc54a\uc73c\ub098 \uc5f0\uc774\uc5b4 \uc19f\ub294 \ubc14\uc704 \ubd09\uc6b0\ub9ac\uc640 \uae30\uc554\uc808\ubcbd\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uc608\ub85c\ubd80\ud130 \uc18c\uae08\uac15\uc73c\ub85c \uaf3d\ud790 \ub9cc\ud07c \uc0b0\uc138\uac00 \uc218\ub824\ud558\uace0, \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1982\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc6d0\ud6a8\ub300\uc0ac\uac00 \ucc3d\uac74\ud55c \uc720\ub9ac\ubcf4\uc804, \uc2e0\ub77c\uc2dc\ub300\uc758\uc678\uccad\ub7c9\uc0ac, \ucd5c\uce58\uc6d0\uc758 \uc720\uc801\uc9c0\uc778 \uace0\uc6b4\ub300\uc640 \ub3c5\uc11c\ub2f9, \uacf5\ubbfc\uc655\uc774 \ud64d\uac74\uc801\uc758 \ub09c\uc744 \ud53c\ud574 \uc740\uc2e0\ud55c \uc624\ub9c8\ub300(\u4e94\u99ac\u81fa)\uc640 \uccad\ub7c9\uc0b0\uc131, \uae40\uc0dd\uc77