INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(84, TIMESTAMP '2019-08-14 15:46:34.941', TIMESTAMP '2019-08-14 15:47:29.648', '46', STRINGDECODE('\ub2f4\uc591\uc74d\uc5d0\uc11c 13km \uc815\ub3c4 \ub5a8\uc5b4\uc9c4 \ub192\uc774 731.2m\uc758 \ucd94\uc6d4\uc0b0\uc740 \uc804\ub77c\ub0a8\ub3c4 \uae30\ub150\ubb3c 4\ud638\uc774\uc790 \uc804\ub77c\ub0a8\ub3c4 5\ub300 \uba85\uc0b0\uc911\uc758 \ud558\ub098\ub85c \uc190\uaf3d\ud78c\ub2e4. <BR><BR>\ub2f4\uc591\uad70\uc758 \ucd5c\ubd81\ub2e8\uc778 \uc6a9\uba74 \uc6d4\uacc4\ub9ac\uc640 \uc804\ub77c\ubd81\ub3c4 \uc21c\ucc3d \ubd81\ud765\uba74\uacfc \ub3c4\uacbd\uacc4\ub97c \uc774\ub8ec\ub2e4. \ub9ce\uc740 \uc218\ub9bc\uacfc \uae30\uc554\uad34\uc11d, \uae4e\uc544\uc138\uc6b4 \ub4ef\ud55c \uc11d\ubcbd\uc774 \ub9c8\uce58 \uc131\uc744 \uc313\uc740 \ub4ef\uc774 \ub458\ub7ec\uc788\uace0 \uc11c\ucabd\uc5d0 \uaca8\uc6b0 \uc0ac\ub78c \ud558\ub098 \ud1b5\ud589 \ud560 \uc815\ub3c4\uc758 \uae38\uc774 \ud2b8\uc5ec \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc6d4\uacc4\ub9ac \ud0dc\uc6c5\uc0b0\uc7a5-\ubd81\ub989 \uc548\ubd80-\uc815\uc0c1-697\ubd09(\ubcf4\ub9ac\uc554 \uc815\uc0c1)-\ubcf4\ub9ac\uc554-\uad00\ub9ac\uc0ac\ubb34\uc18c \uc8fc\ucc28\uc7a5 (\ucd1d 3\uc2dc\uac04)<BR>\u2461 \ubc00\uc7ac-\uc815\uc0c1-\ubcf4\ub9ac\uc554-\uad74\uc554\ubc14\uc704-\uad00\ub9ac\uc0ac\ubb34\uc18c \uc8fc\ucc28\uc7a5 (\ucd1d 3\uc2dc\uac04)<BR>\u2462 \uc30d\ud0dc\ub9ac-697\ubd09(\ubcf4\ub9ac\uc554 \uc815\uc0c1)-\uc815\uc0c1-\ubd81\ub989 \uc548\ubd80-\uc6d4\uacc4\ub9ac \ud639\uc740 697\ubd09(\ubcf4\ub9ac\uc554 \uc815\uc0c1)-\ubcf4\ub9ac\uc554-\uad00\ub9ac\uc0ac\ubb34\uc18c \uc8fc\ucc28\uc7a5 (\ucd1d 3\uc2dc\uac04)'), '35.406576', '127.005489', STRINGDECODE('\uc804\ub77c\ub0a8\ub3c4 \ub2f4\uc591\uad70 \uc6a9\uba74, \uc804\ub77c\ubd81\ub3c4 \uc21c\ucc3d\uad70 \ubcf5\ud765\uba74'), 2607102100, 731, STRINGDECODE('\ucd94\uc6d4\uc0b0'), STRINGDECODE('&lt;\ub2e8\ud48d\uc774 \uc544\ub984\ub2e4\uc6b4 \uc0b0&gt;<BR>\uac00\uc744\uc774\uba74 \uc0b0\ubd09\uc6b0\ub9ac\uac00 \ubcf4\ub984\ub2ec\uc5d0 \ub9de\ub2ff\uc744 \uc815\ub3c4\ub85c \ub192\ub2e4\uace0 \ud574 \uc774\ub984\uc9c0\uc5b4\uc9c4 \ucd94\uc6d4\uc0b0\uc740 \ub9c8\uce58 \ucda9\ubd81\uc758 \uc6d4\uc545\uc0b0\uc744 \ub2f4\uc591\uc5d0 \uc62e\uaca8\ub193\uc740 \ub4ef \uae30\uc554\uc808\ubcbd\uc758 \uc808\uacbd\uacfc \ud568\uac8c \uc808\ubcbd\ub05d\uc5d0 \uc544\uc2ac\uc544\uc2ac \ub9e4\ub2ec\ub9b0 \ub4ef\ud55c \ubcf4\ub9ac\uc554\uc740 \ubcf4\ub294 \uac83\ub9cc\uc73c\ub85c\ub3c4 \uac00\uc2b4\uc774 \uc6c0\uce20\ub7ec\ub4e4\uac8c \ub9cc\ub4e0\ub2e4.<BR><BR>\ubcf4\ub9ac\uc554\uc758 \ucc3d\uac74\uc790\ub098 \uc5f0\ub300\ub294 \ubd84\uba85\ud558\uc9c0 \uc54a\uc9c0\ub9cc \uc804\uc124\uc5d0 \ub530\ub974\uba74 \ubcf4\uc870\uad6d\uc0ac \uc9c0\ub20c\uc774 \uc9c0\ub9ac\uc0b0 \ucc9c\uc655\ubd09\uc5d0\uc11c \ub098\ubb34\ub85c \uae4e\uc740 \uc138\ub9c8\ub9ac\uc758 \uc0c8 \uc911 \ud55c\ub9c8\ub9ac\uac00 \ub0a0\uc544\uc640 \uc549\uc740 \uacf3\uc774 \ucd94\uc6d4\uc0b0 \ubcf4\ub9ac\uc554\uc774\ub77c\uace0 \ud55c\ub2e4. \ub610\ud55c \uc784\uc9c4\uc65c\ub780\ub54c \uc808\uba85\ud55c \uae40\ub355\ub839 \uc7a5\uad70\uc758 \ubd80\uc778 \ud765\uc591 \uc774\uc528\uac00 \uc65c\ubcd1\uc5d0\uac8c \ucad3\uae30\uc790 \ubcf4\ub9ac\uc554\uc758 \uc808\ubcbd \uc544\ub798\ub85c \ubab8\uc744 \ub358\uc838 \uc21c\uc808\ud588\ub2e4\ub294 \uacf3\uc774\ub2e4.<BR><BR>\uc9c0\uba85\uc5d0\uc11c \uc54c \uc218 \uc788\ub4ef\uc774 \ub2e8\ud48d\ub098\ubb34\uac00 \ub9ce\uc544 \uc628\uc0b0\uc774 \ubd89\uac8c \ubb3c\ub4dc\ub294 \uac00\uc744\uc774 \uac00\uc7a5 \uc544\ub984\ub2e4\uc6b0\uba70 \uc0b0\uc744 \uc624\ub974\ub2e4 \uc544\ub798\ub85c \ubcf4\uc774\ub294 \ub2f4\uc591\ud638\uc758 \ud478\ub978 \ubb3c\uacb0\uc740 \ubcf4\ub294 \uc774\uc758 \uac00\uc2b4\uc744 \uc2dc\uc6d0\ud558\uac8c \ub6ab\uc5b4\uc900\ub2e4. \uc6d4\uacc4\ub9ac,\ubc00\uc7ac, \uc30d\ud0dc\ub9ac \uae30\uc810\uc73c\ub85c \ub4f1\uc0b0\ub85c\uac00 \ub098 \uc788\uc73c\uba70 \uc5b4\ub290 \ucf54\uc2a4\ub4e0 3\uc2dc\uac04 \uac00\ub7c9\uc774\uba74 \ucda9\ubd84\ud558\ub2e4. \uc774\uc911 \uc6d4\uacc4\ub9ac\uc5d0\uc11c \ubcf4\ub9ac\uc554\uc744 \uac70\uccd0 \uc815\uc0c1\uc5d0 \uc624\ub978 \ub2e4\uc74c \ub3d9\uad74\ub300\ud53c\uc18c \ucabd\uc73c\ub85c \ub3cc\uc544 \uc6d4\uacc4\ub9ac\ub85c \uc6d0\uc810\ud68c\uadc0\ud558\ub294 \ucf54\uc2a4\uac00 \uac00\uc7a5 \uc778\uae30 \uc788\ub2e4.'), STRINGDECODE('\uc6b8\ucc3d\ud55c \uc0b0\ub9bc\uacfc \ub2f4\uc591\ud638\uac00 \uc5b4\uc6b0\ub824\uc838 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70 \ucd94\uc6d4\ub09c\uc774 \uc790\uc0dd\ud558\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0b0 \uc815\uc0c1\uc5d0\uc11c 65m \uc815\ub3c4 \uc544\ub798 \uc9c0\uc810\uc5d0 \uc788\ub294 \ubcf4\ub9ac\uc554(\u83e9\u63d0\u5eb5)\uacfc \uc804\ub77c\ubd81\ub3c4 \uc21c\ucc3d\uc744 \uacbd\uacc4\ub85c \ud55c \uc0b0\ub85d\uc5d0 \uc788\ub294 \uc6a9\ucd94\uc0ac\uac00 \uc720\uba85'), STRINGDECODE('\ub9c8\ub974\uc9c0 \uc54a\ub294 \uc57d\uc218\ud130\ub97c \ud488\uc740 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uac00\ub9c8\uace8 \uc77c\ub300\uc5d0 50\uc5ec\uac1c\uc758 \uc2dd\ub2f9\uc774 \uc788\ub2e4. <BR>\ucd94\uc6d4\uc0b0 \ubc14\ub85c \uc544\ub798 \ucd94\uc6d4\uc0b0\uc7a5(\u260e061-383-0816), \uc218\uc815\ud69f\uc9d1(\u260e061-383-3116)\uc774 \uc788\uc73c\uba70 \ubbfc\ubc15\ub3c4 \uac00\ub2a5\ud558\ub2e4. \ubd04\uc5d0 \uba39\ub294 \ube59\uc5b4\uc758 \ub9db\uc774 \ub9e4\uc6b0 \uc88b\ub2e4.<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ucd94\uc6d4\uc0b0 \ub9e4\ud45c\uc18c \u260e061-382-2469, \uac00\ub9c8\uace8 \uc0dd\ud0dc\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e061-380-3374'), STRINGDECODE('\uae30\uc810\uc740 \ub2f4\uc591\uc774\ub2e4. \ucd94\uc6d4\uc0b0 \uc544\ub798 \ub2f4\uc591\ud638\ubc18\uc744 \uc9c0\ub098 \uac00\ub9c8\uace8 \uc6a9\uba74\uae4c\uc9c0 \uc624\uac00\ub294 \ubc84\uc2a4\uac00 \uc624\uc804 6\uc2dc 30\ubd84\ubd80\ud130 \uc624\ud6c4 7\uc2dc 20\ubd84\uae4c\uc9c0 \ud558\ub8e8 12\ud68c, \uc6a9\uba74 \uac00\ub9c8\uace8\uae4c\uc9c0\ub294 \uc624\ud6c4 8\uc2dc\uae4c\uc9c0 \uc6b4\ud589