INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(85, TIMESTAMP '2019-08-14 15:46:34.941', TIMESTAMP '2019-08-14 15:47:30.239', '41', STRINGDECODE('\ud654\ub3c4\uc74d(\ub9c8\uc11d)\uc5d0\uc11c \ubd81\ub3d9\ucabd\uc73c\ub85c 12km \uac70\ub9ac\uc5d0 \uc788\ub294 \ucd95\ub839\uc0b0(887.1m)\uc740 \uc870\uc885\ucc9c\uacfc \uc218\ub3d9\ucc9c\uc758 \uc0ac\uc774\uc5d0 \uc19f\uc544 \uc788\ub2e4. \uc774 \uc0b0\uc5d0\ub294 \uc77c\uba85 ''\ub0a8\uc774\ubc14\uc704''\ub77c\uace0 \ud558\ub294 \ubc14\uc704\uac00 \uc788\ub294\ub370, \uc774\ub294 \uc870\uc120\uc2dc\ub300 \ub54c \ub0a8\uc774\uc7a5\uc774 \uc774 \uacf3\uc5d0\uc11c \uc2ec\uc2e0\uc744 \uc218\ub828\ud558\uc600\ub2e4 \ud558\uc5ec \ubd99\uc5ec\uc9c4 \uc774\ub984\uc774\ub77c\uace0 \ud55c\ub2e4. <BR><BR>\ucd95\ub839\uc0b0\uc758 \uc6b8\ucc3d\ud55c \uc218\ub9bc\uacfc \uacc4\uace1\uc744 \uc774\uc6a9\ud558\uc5ec \uc790\uc5f0 \ud734\uc591\ub9bc\uc744 \uc870\uc131, \uc0bc\ub9bc\uc695\uc7a5, \ud734\uacc4\uc18c, \uccb4\uc721\uc2dc\uc124, \ub180\uc774\uc2dc\uc124, \uc57c\uc601\uc7a5 \ub4f1 \ud3b8\uc758 \uc2dc\uc124\uc774 \ub450\ub8e8 \uac16\ucd94\uc5b4 \uc9c4 \uacf3\uc774\ub2e4.'), STRINGDECODE('\u2460 \ub2f9\uc7ac\ub9c8\uc744-\uc7a3\ucc3d\uace0-\uc815\uc0c1-\uc808\uace0\uac1c-\ub0a8\uc774\ubc14\uc704-\uc218\ub9ac\ubc14\uc704-\ub9e4\ud45c\uc18c (\ucd1d 3\uc2dc\uac04 40\ubd84)<BR>\u2461 \uc218\ub808\ub118\uc774\uace0\uac1c-\ub0a8\uc774\ubc14\uc704-\uc815\uc0c1-\uc808\uace0\uac1c-\uc11c\ub9ac\uc0b0-\uc9c8\ub9c8\uc7ac-\ub9e4\ud45c\uc18c (\ucd1d 3\uc2dc\uac04 5\ubd84)<BR>\u2462 \ub9e4\ud45c\uc18c-\uc9c8\ub9c8\uc7ac-\uc11c\ub9ac\uc0b0-\uc808\uace0\uac1c-\uc815\uc0c1-\ub3d9\ubd81\ub989-287\ubd09\ub989-\ube44\ub839\uc774 (\ucd1d 3\uc2dc\uac04 10\ubd84)'), '37.726344', '127.304084', STRINGDECODE('\uacbd\uae30\ub3c4 \ub0a8\uc591\uc8fc\uc2dc \uc218\ub3d9\uba74, \uac00\ud3c9\uad70 \uc0c1\uba74'), 2101033200, 887, STRINGDECODE('\ucd95\ub839\uc0b0'), STRINGDECODE('&lt;\uc6d0\uc774\ub984\uc774 \ube44\ub839\uc0b0\uc778 \uc218\ub3d9\uacc4\uace1 \uba85\ubd09&gt;<BR>\uacbd\ucd98\uac00\ub3c4 \ub9c8\uc11d\uc744 \uc9c0\ub098 \uc0c8\ud130\uace0\uac1c\ub97c \ub118\uc73c\uba74 \uc78a\uc5c8\ub358 \uc804\uc124\ucc98\ub7fc \ubd81\ud55c\uac15\uc774 \ub098\ud0c0\ub09c\ub2e4. \uc774\uc5b4 \uc815\ud0dc\ucd98 \ub178\ub798 ''\ub300\uc131\ub9ac\uc5d0\uc11c''\uc758 \ub300\uc131\ub9ac\uac00 \ub098\uc624\ub294\ub370 \uadf8 \uc9c1\uc804 \uad70\uacc4\ub97c \uc9c0\ub0a0 \ub54c \ubcf4\uba74 \uaf64 \ud070 \ub0b4\ub97c \uac74\ub108\uace0\uc788\ub2e4. \uc218\ub3d9\uc720\uc6d0\uc9c0\ub85c \uc54c\ub824\uc9c4 \uc218\ub3d9\ucc9c\uc774\ub2e4. <BR><BR>\uc774 \uc218\ub3d9\ucc9c\uacfc \uac00\ud3c9 \uc870\uc885\ucc9c \uc0ac\uc774\uc5d0 \uc788\ub294 \ucd95\ub839\uc0b0\uc740 \uad6d\ub3c4\uc5d0\uc11c \ubcf4\uc774\uc9c0 \uc54a\uace0 \uad50\ud1b5\ub3c4 \ubd88\ud3b8\ud574 \uc624\ub798\ub3c4\ub85d \uc54c\ub824\uc9c0\uc9c0 \uc54a\uc740 \uc0b0\uc774\uc5c8\ub2e4. \uadf8\ub7ec\ub2e4 80\ub144\ub300 \ubbfc\uc8fc\uc0b0\uc545\ud68c \uac19\uc740, \uc0ac\ub78c\uc774 \ubcc4\ub85c \uc5c6\ub294 \ud638\uc813\ud55c \ub300\uc0c1\uc9c0\ub9cc \ucc3e\ub358 \uc774\ub4e4\uc774 \uc990\uaca8 \ub2e4\ub2c8\uba74\uc11c \ubc1c\uae38\uc774 \uc7a6\uc544\uc9c0\uae30 \uc2dc\uc791\ud588\ub2e4. <BR><BR>\uc218\ub3d9\uacc4\uace1\uc73c\ub85c \ub4e4\uc5b4\uac00 \ubcf4\uba74 \ucd95\ub839\uc0b0\uc740 \uc721\uc0b0 \uc904\uae30 \uc704\ub85c \uc465 \uc62c\ub77c\uc120, \uc900\uac78\ud55c \ubaa8\uc2b5\uc774\ub2e4. \uc815\uc0c1\ubd80\uac00 \ubc14\uc704\ub85c \ub418\uc5b4\uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \uadf8\ub798 \uc554\ub989\uc0b0\ud589\uc758 \uc544\uc2ac\uc544\uc2ac\ud55c \ubb18\ubbf8\uac00 \uc788\uace0 \uc815\uc0c1 \uc870\ub9dd\ub3c4 \uc88b\ub2e4. \uacbd\uae30 \uc911\ubd81\ubd80\uc758 \uac70\uc758 \ubaa8\ub4e0 \uc0b0\uacfc \uc0bc\uac01\uc0b0, \ub3c4\ubd09\uc0b0\uc758 \ud30c\ub178\ub77c\ub9c8\uac00 \ud3bc\uccd0\uc9c4\ub2e4. <BR>\uc6d0\uc774\ub984\uc740 \ube44\ub839\uc0b0\uc774\uc5c8\ub2e4. ''\ube4c \ucd95''\uc790\uc744 \uc0c8\uae40\uc73c\ub85c \uc77d\uac8c \ub418\uc5b4\uc788\ub294 \uc774\ub450\uc600\ub294\ub370 \uc774\ub97c \ubaa8\ub974\uace0 \uc77c\uc81c\uc2dc\ub300\ubd80\ud130 \ucd95\ub839\uc0b0\uc73c\ub85c \uc368\uc654\ub2e4. \uc815\uc0c1 \ub3d9\ubd81\ucabd\uacfc \ub3d9\ucabd\uc5d0\ub294 \uc544\uc9c1\ub3c4 \ube44\ub839\uc774\ub77c\ub294 \uc9c0\uba85\uc774 \ub0a8\uc544\uc788\ub2e4.'), STRINGDECODE('\uc18c\ub098\ubb34\uc640 \uc7a3\ub098\ubb34 \uc7a5\ub839\ub9bc\uc774 \uc6b8\ucc3d\ud55c \uc232\uc744 \uc774\ub8e8\uace0 \ub2e8\uc560\uac00 \ud615\uc131\ub418\uc5b4 \uc788\uc73c\uba70, \uc0b0 \uc815\uc0c1\uc5d0\uc11c \ubd81\uc73c\ub85c\ub294 \uc6b4\uc545\uc0b0, \uba85\uc9c0\uc0b0, \ud654\uc545\uc0b0\uc774 \ubcf4\uc774\uace0, \ub3d9\ub0a8\ucabd\uc73c\ub85c \uccad\ud3c9\ud638\uac00 \ubcf4\uc774\ub294 \ub4f1 \uc870\ub9dd\uc774 \ub6f0\uc5b4\ub09c \uc810\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uac00\ud3c9 7\uacbd\uc758 \ud558\ub098\uc778 \ucd95\ub839\ubc31\ub9bc\uacfc \ub0a8\uc774\uc7a5\uad70\uc758 \uc804\uc124\uc774 \uae43\ub4e0 \ub0a8\uc774\ubc14\uc704, \uc218\ub9ac\ubc14\uc704 \ucd95\ub839\ubc31\ub9bc \ub4f1\uc774 \uc720\uba85. \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc788\uc74c'), STRINGDECODE('\ub0a8\uc774\uc7a5\uc774 \uc2ec\uc2e0\uc744 \ub2e6\ub358 \ub0a8\uc774 \ubc14\uc704'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ucd95\ub839\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc\uc758 \ud1b5\ub098\ubb34\uc9d1\uc744 \ube4c\ub9ac\uac70\ub098 \ud150\ud2b8\ub97c \uce5c\ub2e4. \ud1b5\ub098\ubb34\uc9d1\uc758 \uacbd\uc6b0 \ubc18\ub4dc\uc2dc \uc608\uc57d\uc744 \ud574\uc57c\ud55c\ub2e4. \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e031-592-0681<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ucd95\ub839\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e031-592-0681'), STRINGDECODE('\ub9c8\uc11d\ud589 \uc88c\uc11d\ubc84\uc2a4\ub294 \uccad\ub7c9\ub9ac \uc870\ub839\uc57d\uad6d \uc55e\uacfc \uc7a0\uc2e4\uc5ed 7\ubc88\ucd9c\uad6c\uc5d0\uc11c 5\ubd84 \uac04\uaca9\uc73c\ub85c \uc788\ub2e4. <BR>\uacbd\ucd98\uc120 \uc5f4\ucc28\ub294 \ud55c \uc2dc\uac04 \ua