INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(86, TIMESTAMP '2019-08-14 15:46:34.942', TIMESTAMP '2019-08-14 15:47:30.813', '42', STRINGDECODE('\uce58\uc545\uc0b0\uc740 \uac15\uc6d0\ub3c4 \uc6d0\uc8fc\uc2dc\uc640 \ud6a1\uc131\uad70\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \uc0b0\uc73c\ub85c, \uc0b0\uc138\uac00 \ub6f0\uc5b4\ub09c \ub370\ub2e4 \uc601\ub3d9\uace0\uc18d\uad6d\ub3c4\uc640 \uc911\uc559\uace0\uc18d\uad6d\ub3c4\uc5d0 \uc778\uc811\ud574 \uc788\uc5b4 \uad50\ud1b5\uc774 \ud3b8\ub9ac\ud574 \uc911\ubd80\uad8c \uc0b0\ud589\uc9c0\ub85c \uc778\uae30\uac00 \ub192\ub2e4.'), STRINGDECODE('\u2460 \uad6c\ub8e1\uc0ac-\uacc4\uace1\uae38-\ube44\ub85c\ubd09-\uc950\ub108\ubbf8\uace0\uac1c-\ubc94\uace8-\ubc95\ubb38\uc0ac(\ucd1d 5\uc2dc\uac04)<BR>\u2461 \ud669\uace8\ub9e4\ud45c\uc18c-\uc785\uc11d\ub300-\uc785\uc11d\uc0ac \uc0bc\uac70\ub9ac-\ube44\ub85c\ubd09-1003\ubd09-\ubd80\uace1\ub9e4\ud45c\uc18c(\ucd1d 4\uc2dc\uac04 10\ubd84)<BR>\u2462 \ubd80\uace1\ub9ac-\uace0\ub454\uce58-\ud5a5\ub85c\ubd09-\ub0a8\ub300\ubd09-\uc30d\ub8e1\uc218-\uc601\uc6d0\uc0ac-\uae08\ub300\ubd84\uc18c(\ucd1d 6\uc2dc\uac04 40\ubd84)'), '37.341995', '127.919679', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc6d0\uc8fc\uc2dc, \ud6a1\uc131\uad70, \uc601\uc6d4\uad70'), 2209132600, 1282, STRINGDECODE('\uce58\uc545\uc0b0'), STRINGDECODE('&lt;\uc7a5\ubcbd\ucc98\ub7fc \uc19f\uc740, \ucc99 \ubd10\uc11c \uba85\uc0b0&gt;<BR>\uba85\uc0b0\uc740 \uac00\uae4c\uc774 \uac00\ubcf4\uba74 \uc548\ub2e4. \uc601\ub3d9\uace0\uc18d\uad6d\ub3c4 \uc6d0\uc8fc\ub97c \uc9c0\ub0a0 \ubb34\ub835 \ubb47 \uc0b0\uc744 \uc81c\uc555\ud558\ub294 \ud6e4\uce60\ud55c \uc0b0\uc774 \ubcf4\uc778\ub2e4\uba74 \ubb3c\uc5b4\ubcfc \uac83\ub3c4 \uc5c6\uc774 \uce58\uc545\uc0b0\uc778 \uac83\uc774\ub2e4. <BR><BR>\ub4e4\uace0\uc744 \uc6d0\uc8fc(\u539f\u5dde)\uc5d0\uc11c \ubcf4\uba74 \uadf8\uac74 \uac70\ub300\ud55c \uc7a5\ubcbd\uc774\ub2e4. 17\ud0ac\ub85c\ubbf8\ud130\uc758 \uc7a5\ub300\ud55c \uc8fc\ub989\uc774 \uadf8\ub300\ub85c \ucc9c\uc5ec\ubbf8\ud130\ub97c \uc19f\uc544\uc62c\ub790\ub2e4. \uc774\ub7f0 \uc555\ub3c4\uc801 \uc0b0\uc138\uc758 \ub4a4\ud3b8\uc73c\ub85c\ub294 \ub610 \ub9e4\ud654\uc0b0(1083.1m)\uacfc \ub9e4\ubd09\uc0b0(1095m) \uc904\uae30\ub97c \uc2e4\ud30d\ud558\uac8c \ubed7\uccd0 \uc0b0\ubb3c\uacb0 \ucca9\ucca9\uc758 \uacb9\uc0b0\uc744 \ub9cc\ub4e4\uace0\uc788\ub2e4. \ud558\uc5ec \uc0ac\uc774\uc0ac\uc774 \uad6c\ub8e1\uc0ac \ud070\uace8, \ubd80\uace1\ubd84\uc9c0, \uc0c1\uc6d0\uace8\uc758 \uae4a\uc740 \uad6c\ub801\uc744 \ud615\uc131\ud55c\ub2e4. \ud55c\ucabd\uc740 \uac1c\ud65c\uc9c0 \ub05d\uc758 1\uc790\ud615 \uc0b0\uc904\uae30\ub85c \ubc14\uae65 \uc138\uc0c1\uc5d0 \uc120\uc744 \ubcf4\uc774\uace0 \ubc18\ub300\ud3b8\uc740 \uacb9\uc0b0 \ucca9\ucca9, \uc6c5\uc22d\uae4a\uc740 \ub9db\uc744 \ub4e4\uc600\ub2e4. \ub4dc\ub7ec\ub0c4\uacfc \uac10\ucda4\uc774 \ubc18\ubc18\uc529\uc778 \uc774\uc0c1\uc801\uc778 \uc0b0\uc138\uc778 \uac83\uc774\ub2e4. <BR><BR>\uad6c\ub8e1\uc0ac \ud070\uace8\uc758 \uac10\ucda4\uc740 \uc18c\ub098\ubb34\uc758 \uc655 \ud669\uc7a5\ubaa9\uc774\ub2e4. \uc99d\uac70\ucc98\ub7fc \uc808 \uc785\uad6c\uc5d0\ub294 \ud669\uc7a5\uae08\ud45c\uac00 \uc11c\uc788\ub2e4. \uc0c1\uc6d0\uace8\uc5d0\ub294 \"\"\ub099\uc5fd\ud65c\uc5fd\uc218\uc758 \ubc15\ubb3c\uad00\"\"\uc774\ub77c\ub294 \ucc9c\uc5f0\uae30\ub150\ubb3c(93\ud638) \uc232\uc774 \uc788\ub2e4. \ubd80\uace1\ubd84\uc9c0\ub85c\ub294 \uace0\ub824 \uc120\ube44 \uc6d0\ucc9c\uc11d\uc744 \uc228\uaca8 \uce7c\ub85c \ub098\ub77c\ub97c \uc138\uc6b4 \uc870\uc120 \ud0dc\uc885\uc744 \ud5c8\ud0d5\uce58\uace0 \ub3cc\uc544\uac00\uac8c \ud588\ub2e4. \uba85\uc0b0 \uce58\uc545\uc774 \uac00\uc9c4 \ub355\uc774\ub2e4.'), STRINGDECODE('\uc8fc\ubd09\uc778 \ube44\ub85c\ubd09\uc744 \uc911\uc2ec\uc73c\ub85c \ub0a8\ub300\ubd09 (1,180m)\uacfc \ub9e4\ud654\uc0b0(1,083.1m) \ub4f1 1\ucc9c\uc5ec \ubbf8\ud130\uc758 \uace0\ubd09\ub4e4\uc774 \uc5f0\uc774\uc5b4 \uc788\uc5b4 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70 \uacf3\uacf3\uc5d0 \uc0b0\uc131\uacfc \uc0ac\ucc30, \uc0ac\uc801\uc9c0\ub4e4\uc774 \ub1