INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(87, TIMESTAMP '2019-08-14 15:46:34.942', TIMESTAMP '2019-08-14 15:47:31.402', '44', STRINGDECODE('\uce60\uac11\uc0b0\uc740 \ud574\ubc1c 561m\uc758 \ub0ae\uc740 \uc0b0\uc774\uae30\ub294 \ud558\ub098 \uc0b0\uc138\uac00 \uac70\uce60\uace0 \uac00\ud30c\ub974\ub2e4. \uc544\uc9c1\uc740 \ub110\ub9ac \uc54c\ub824\uc9c0\uc9c0 \uc54a\uc544\uc11c \uc790\uc5f0 \uadf8\ub300\ub85c\uc758 \uc6b8\ucc3d\ud55c \uc232\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4. \uc0b0\uc704\uc5d0 \uc624\ub974\uba74 \uc11c\ud574\uac00 \ubc14\ub77c\ub2e4 \ubcf4\uc774\uace0 \uace8\uc9dc\uae30\ub9c8\ub2e4 \ud750\ub974\ub294 \ubb3c\uc774 \ubaa8\uc5ec \uc9c0\ucc9c\uacfc \uc0ac\ucc9c\uc744 \uc774\ub8ec\ub2e4.'), STRINGDECODE('\u2460 \uac00\ub9ac\uc810-\ub9c8\uc7ac\uace0\uac1c-\uc0bc\ud615\uc81c\ubd09-\uc815\uc0c1-\ub0c9\ucc9c\uacc4\uace1-\uce60\uac11\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\ucd1d 3\uc2dc\uac04)<BR>\u2461 \uce60\uac11\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-395\ubd09-\uc11c\ub989 \uc0bc\uac70\ub9ac-\uc815\uc0c1-\uc810\uc2ec\uad50(\ucd1d 3\uc2dc\uac04)<BR>\u2462 \ud55c\uce58\uace0\uac1c-\uc815\uc0c1-465\ubd09-\uc7a5\uace1\uc0ac-\uc7a5\uace1\ub9ac \uc8fc\ucc28\uc7a5(\ucd1d 2\uc2dc\uac04 10\ubd84)'), '36.447198', '126.859704', STRINGDECODE('\ucda9\uccad\ub0a8\ub3c4 \uccad\uc591\uad70 \ub300\uce58\uba74, \uc815\uc0b0\uba74, \uc7a5\ud3c9\uba74'), 2414022100, 560, STRINGDECODE('\uce60\uac11\uc0b0'), STRINGDECODE('&lt;\uc77c\uacf1 \uba85\ub2f9 \ud488\uc740 \ucda9\ub0a8\ub3c4\ub9bd\uacf5\uc6d0&gt;<BR>\uce60\uac11\uc0b0. \uc720\uc7a5\ud55c \ub2a5\uc120 \uace8\uace8\uc5d0 \uba85\ub2f9\uac11\ud130 \uc77c\uacf1\uc744 \uac10\ucd94\uace0\uc788\ub2e4\ub294 \uc0b0\uc774\ub2e4. 600\ubbf8\ud130\ub3c4 \uc548 \ub418\ub294 \ub192\uc774\uc5d0 \uc218\ub354\ubd84\ud55c \uc721\uc0b0\uc778\ub370\ub3c4 \uc774\ub807\uac8c \ub300\ub2e8\ud55c \uc7a0\uc7ac\ub825\uc744 \uc9c0\ub154\ub2e4\ub294 \uac83\uc774\ub2e4. \uc8fc\ub989\uc774 5\ubc29\uc73c\ub85c \ubed7\uc5b4\ub098\uac04 \uc0b0\uc138\ub098 \uccad\uc591 \ubd84\uc9c0\uc5d0\uc11c \ube44\ub86f\ud558\ub294 \uc544\ud754\uc544\ud649\uace8 \uc9c0\ucc9c(\u829d\u5ddd)\uc774 \ubc94\uc0c1\ud574\ubcf4\uc774\uc9c0\ub294 \uc54a\ub294\ub2e4. \uadf8 \uccad\uc591\uc740 \uc784\uc9c4\uc65c\ub780\uacfc \ub0a8\ubd81\uc804\uc7c1\ub54c \ucd1d\uc18c\ub9ac \ud55c \ubc88 \ub098\uc9c0 \uc54a\uc558\uace0 \uc2b9\uc9c0\ub85c \uc774\ub984\ub09c \ub9c8\uace1\uc0ac\uc758 \uc720\uad6c\ucc9c\uc744 \uc774\uc6c3\uc5d0 \ub454 \uac83\uc744 \ubcf4\uba74 \uc77c\ub2e8\uc740 \uace0\uac1c\uac00 \ub044\ub355\uc5ec\uc9c4\ub2e4. <BR><BR>\uad6d\ubcf4 \ub458\uacfc \ubcf4\ubb3c \uc14b\uc744 \ud488\uc740 \uc7a5\uace1\uc0ac\uc5d0 \uc774\ub974\uba74 \ud655\uc2e0\uc5d0 \uac00\uae4c\uc6cc\uc9c4\ub2e4. \uc77c\uacf1 \uc911 \uc5ec\uc12f\uc740 \ubab0\ub77c\ub3c4 \uc801\uc5b4\ub3c4 \ud558\ub098\ub294 \ubd84\uba85\ud558\uae30 \ub54c\ubb38\uc774\ub2e4. \uad6d\ubcf4 58\ud638\uc758 \ucca0\uc870\uc57d\uc0ac\uc5ec\ub798\uc88c\uc0c1(\uc11d\uc870 \ub300\uc88c \ud3ec\ud568), \ucca0\uc870\ube44\ub85c\uc790\ub098\ubd88\uc88c\uc0c1(\ubcf4\ubb3c 174\ud638), \uc774\ub4e4\uc774 \ub4e4\uc5b4\uc788\ub294 \uc0c1\ub300\uc6c5\uc804(\ubcf4\ubb3c 162\ud638), \uae08\ub3d9\uc57d\uc0ac\uc5ec\ub798\uc88c\uc0c1(\ubcf4\ubb3c 337\ud638)\uacfc \uadf8 \uc9d1\uc778 \ud558\ub300\uc6c5\uc804(\ubcf4\ubb3c 181\ud638), \uadf8\ub9ac\uace0 \uc0bc\ubca0\uc5d0 \uadf8\ub9b0 \ubbf8\ub975\ubd88\uad18\ubd88\ud0f1(\uad6d\ubcf4 300\ud638)\uc744, \ubcf8\uc0b0\ub3c4 \uc544\ub2cc \uc774 \uc791\uc740 \uc808\uc774 \uac16\uace0\uc788\ub2e4. <BR><BR>\ubd09\ub9cc\ubbf8(\u5cf0\u5dd2\u7f8e)\ub3c4 \uacc4\uace1\ubbf8\ub3c4 \uc5c6\ub294 \uc0b0\uc774\uc9c0\ub9cc \uc804\ub9dd\uc740 \uaf3d\uc544\uc904 \ub9cc\ud558\ub2e4. \uae08\uac15 \uc774\ubd81\uc758 \ucda9\ub0a8 \ucd5c\uace0\ubd09 \uc624\uc11c\uc0b0(789.9m)\uc740 \ubb3c\ub860 \uc11c\ub0a8\ucabd\uc758 \uc131\uc8fc\uc0b0(677m), \ub3d9\ubd81\ucabd\uc758 \uad11\ub355\uc0b0(699.3m)\uc5d0 \uc774\ub974\uae30\uae4c\uc9c0 \ub0b4\ub178\ub77c \ud558\ub294 \uc0b0\ub4e4\uacfc \ud638\ud615\ud638\uc81c\ud560 \uc218 \uc788\ub294 \uc218\uc900\uc774\ub2e4. \uadf8\ub9ac\uace0 \ub0a8\ucabd\uc73c\ub85c\ub294, \uc720\uc720\ud788 \ud750\ub974\ub294 \uae08\uac15\uacfc \ubd80\uc5ec\uac00 \uc190\uc5d0 \uc7a1\ud790 \ub4ef \uac00\uae5d\ub2e4.'), STRINGDECODE('\ubc31\uc6b4\ub3d9 \uacc4\uace1 \ub4f1 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1973\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uacc4\uace1\uc740 \uae4a\uace0 \uae09\ud558\uba70 \uc9c0\ucc9c\uacfc \uacc4\uace1\uc744 \uc2f8\uace0 \ub3cc\uc544 7\uacf3\uc5d0 \uba85\ub2f9\uc774 \uc0dd\uacbc\ub2e4\ub294 \ub370\uc11c \uc0b0\uc774\ub984\uc774 \uc720\ub798. \uc2e0\ub77c \ubb38\uc131\uc655 \ub54c \ubcf4\uc870(\u666e\u7167) \uc2b9\ub824\uac00 \ucc3d\uac74\ud55c \uc7a5\uace1\uc0ac(\u9577\u8c37\u5bfa)\uc5d0 \uc788\ub294 \ucca0\uc870\uc57d\uc0ac\uc5ec\ub798\uc88c\uc0c1(\ubcf4\ubb3c \uc81c174\ud638) \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\uc190 \ub300\uc9c0 \uc54a\uc740 \uc790\uc5f0 \uadf8\ub300\ub85c\uc758 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uccad\uc591 \uc74d\ub0b4\ub098 \uc7a5\uace1\uc0ac \ubd80\uadfc\uc5d0 \uc219\ubc15 \uc2dc\uc124\uc774 \ub9ce\ub2e4. \uce60\uac11\uc0b0\uc0ec\ub808\ud638\ud154 \u260e041-942-2000, \ud55c\ub9c8\uc74c\ubaa8\ud154 \u260e041-942-1655, \ub300\uce58\ub9ac\uc758 \ubbfc\ubc15 \u260e041-942-6772, \uce60\uac11\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e041-943-4510)<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uccad\uc591\uad70\uccad \uad00\uad11\uae30\ud68d\uacfc \u260e041-940-2446'), STRINGDECODE('\uae30\uc810\uc740 \uccad\uc591\uc774\ub2e4. \uc7a5\uace1\uc0ac\ud589 \ubc84\uc2a4\uac00 8\ud68c \uc6b4\ud589\ud55c\ub2e4. \uccad\uc591\uc73c\ub85c\ub294 \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c 7\ud68c, \ub0a8\ubd80\ud130\ubbf8\ub110\uc5d0\uc11c 8\ud68c \ubc84\uc2a4\uac00 \uc788\ub2e4. \ucda9\uccad\ub0a8\ub3c4 \uc678\uc758 \ub3c4\uc2dc\ub85c\ub294 \uc778\ucc9c, \uc548\uc591, \uad70\uc0b0\uc5d0\uc11c \ubc84\uc2a4\ud3b8\uc774 \uc788\ub2e4. \ud55c\uce58\uace0\uac1c\ub97c \ub4e4\uba38\ub9ac\ub85c \ud560 \uacbd\uc6b0\uc5d0\ub294 \ub300\uc804\uc5d0\uc11c 40\ud68c \uc788\ub294 \uccad\uc591\ud589\uc744 \uc774\uc6a9, \uace0\uac1c\uc5d0\uc11c \ub0b4\ub9b0\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(88, TIMESTAMP '2019-08-14 15:46:34.942', TIMESTAMP '2019-08-14 15:47:31.979', '42', STRINGDECODE('\ud0dc\ubc31\uc0b0\uc740 \uacbd\uc0c1\ubd81\ub3c4 \ubd09\ud654\uad70 \uc11d\ud3ec\uba74 \ub300\ud604\ub9ac\uc640 \ud0dc\ubc31\uc2dc \ubb38\uace1\uc18c\ub3c4\ub3d9 \uadf8\ub9ac\uace0 \uac15\uc6d0\ub3c4 \uc601\uc6d4\uad70 \uc0c1\ub3d9\uba74 \ucc9c\ud3c9\ub9ac\uc640 \uc811\uacbd\uc744 \uc774\ub8e8\uba70 \ub3d9\uacbd 128\u300256\" \ubd81\uc704 37\u300205\"\uc5d0 \uc790\ub9ac\uc7a1\uc740 \ud574\ubc1c 1,566.7m\uc758 \uba85\uc0b0\uc774\ub2e4. \uc774 \uc0b0\uc5d0\uc11c \ubc1c\uc6d0\ud558\ub294 \ubb3c\uc774 \uc601\ub0a8\ud3c9\uc57c\uc758 \uc816\uc904\uc778 \ub099\ub3d9\uac15\uacfc \uc6b0\ub9ac\ubbfc\uc871\uc758 \uc5ed\uc0ac\uc640 \ud568\uaed8\ud55c \ud55c\uac15, \uc0bc\ucc99\uc758 \uc624\uc2ed\ucc9c\uc744 \uc774\ub8e8\ub2c8 \uad6d\ud1a0\uc758 \uc885\uc0b0\uc774\uc790 \ubc18\ub3c4 \uc774\ub0a8\uc758 \ubaa8\ub4e0 \uc0b0\uc758 \ubaa8\ud0dc\uac00 \ub418\ub294 \ubfcc\ub9ac\uc0b0\uc774\ub2e4.<BR><BR>\ud0dc\ubc31\uc0b0\uc740 \ucc9c\uc81c\ub2e8\uc774 \uc788\ub294 \uc601\ubd09\uc744 \uc911\uc2ec\uc73c\ub85c \ubd81\ucabd\uc5d0 \uc7a5\uad70\ubd09(1567m) \ub3d9\ucabd\uc5d0 \ubb38\uc218\ubd09(1,514.9m), \uc601\ubd09\uacfc \ubb38\uc218\ubd09\uc0ac\uc774\uc758 \ubd80\uc1e0\ubd09(1,549.4m)\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub2e4. \uc554\ubcbd\uc774 \uc801\uace0 \uacbd\uc0ac\uac00 \uc644\ub9cc\ud558\uc5ec \ub0a8\ub140\ub178\uc18c \ub204\uad6c\ub098 \uc27d\uac8c \uc624\ub97c \uc218 \uc788\ub294 \uc0b0\uc73c\ub85c \uc815\uc0c1\uc5d0\ub294 \uace0\uc0b0\uc2dd\ubb3c\uc774 \uc790\uc0dd\ud558\uace0 \ubd04\uc774\uba74 \uc0b0\ucca0\ucb49, \uc9c4\ub2ec\ub798\uc758 \uad70\ub77d\uc9c0\uac00 \ub4f1\uc0b0\uac1d\uc744 \ub9de\uc774\ud558\uace0 \uc5ec\ub984\uc5d0\ub294 \uc6b8\ucc3d\ud55c \uc218\ubaa9\uacfc \ucc28\uace0 \uae68\ub057\ud55c \uacc4\uace1\ubb3c\uc774 \ud55c\uc5ec\ub984 \ub354\uc704\ub97c \uc78a\uae30\uc5d0 \ucda9\ubd84\ud558\uba70 \uac00\uc744\uc740 \ud615\ud615\uc0c9\uc0c9\uc758 \ub2e8\ud48d\uc73c\ub85c \uc218\ub193\uc73c\uba70 \uaca8\uc6b8\uc740 \ud770 \ub208\uc73c\ub85c \ub4a4\ub36e\uc778 \uc8fc\ubaa9\uad70\ub77d\uc758 \uc124\uacbd\uc744 \ubcf4\uc5ec \uc8fc\ub294 \uacf3\uc73c\ub85c \ub0a8\uc131\ub2e4\uc6b4 \uc911\ud6c4\ud55c \uc6c5\uc7a5\ud568\uacfc \ud3ec\uc6a9\ub825\uc744 \uc9c0\ub2cc \uc721\uc0b0\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub2e4. <BR><BR>\ub610\ud55c \uc815\uc0c1\uc5d0\uc11c \ubc14\ub77c\ubcf4\ub294 \uc77c\ucd9c\uacfc \ub099\uc870\ub294 \uc7a5\uc5c4\ud558\uc5ec \uc138\uc18d\uc744 \ub5a0\ub09c \ucc9c\uc0c1\uacc4\ub97c \uc5f0\uc0c1\ucf00 \ud558\uace0 \ub9d1\uc740 \ub0a0 \uba40\ub9ac \ub3d9\ud574 \ubc14\ub2e4\ub97c \ubcfc \uc218 \uc788\ub294 \uac83\ub3c4 \ud0dc\ubc31\uc0b0\uc774 \uac00\uc9c0\uace0\uc788\ub294 \uc790\ub791\uac70\ub9ac\uc774\ub2e4. \uc774 \ubc16\uc5d0\ub3c4 \ucd5c\uace0\ub192\uc740 \uacf3\uc5d0 \uc704\uce58\ud55c \ud55c\uad6d\uba85\uc218\uc911 \uc73c\ub738\uc218 \uc6a9\uc815, \uc6a9\ub2f4\uc774 \uc788\ub2e4.1989\ub144 5\uc6d4 13\uc77c 17.44\u33a2\uc758 \uba74\uc801\uc774 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub418\uc5c8\uc73c\uba70 \uc18c\ub3c4\uc9d1\ub2e8\uc2dc\uc124\uc9c0\uad6c\uc5d0 \ucf58\ub3c4\ud615\uc778 \ud0dc\ubc31\uc0b0 \ubbfc\ubc15\ucd0c\uc744 \ube44\ub86f\ud558\uc5ec \uc219\ubc15\uc2dc\uc124,\uc74c\uc2dd\uc810,\uc57c\uc601\uc7a5 \ub4f1\uc774 \ub9c8\ub828\ub418\uc5b4 \uc788\uc73c\uba70 \uc11d\ud0c4\uc758 \uc5ed\uc0ac\ub97c \ud55c \ub208\uc5d0 \ubcfc \uc218 \uc788\ub294 \uc11d\ud0c4\ubc15\ubb3c\uad00\uc774 \uc788\uace0, \uaca8\uc6b8\ucca0\uc5d0\ub294 \ub300\uaddc\ubaa8\uc758 \ub208\uc370\ub9e4\uc7a5\uc774 \uac1c\uc7a5\ub41c\ub2e4.'), STRINGDECODE('\u2460 \ub2f9\uace8-\uc0b0\uc81c\ub2f9\uace8-\ubb38\uc218\ubd09-\ubb38\uc218\ubd09 \uc548\ubd80-\ubd80\uc1e0\ubd09 \uc0bc\uac70\ub9ac-\uc815\uc0c1-\uc720\uc77c\uc0ac-\uc720\uc77c\uc0ac \ub9e4\ud45c\uc18c(\ucd1d 4\uc2dc\uac04 5\ubd84)<BR>\u2461 \ubc31\ub2e8\uc0ac \ub9e4\ud45c\uc18c-\ubc31\ub2e8\uc0ac-\uc815\uc0c1-\ubd80\uc1e0\ubd09 \uc0bc\uac70\ub9ac-\ubb38\uc218\ubd09-\ub450\ub9ac\ubd09-\uc9c0\uc9c0\ub9ac\uace8-\uc7a3\ubc2d(\ucd1d 4\uc2dc\uac04 35\ubd84)<BR>\u2462 \ub2f9\uace8-\uc815\uac00\ubc14\uc6b0\uace8 \uc544\uc6b0\ub77c\uc9c0-\ubc18\uc7ac-\uc815\uc0c1-\ubb38\uc218\ubd09-\uc18c\ubb38\uc218\ubd09-\ub3c4\uc7a5\uace8-\ubd88\ubb34\uc2e4\uace8(\ucd1d 4\uc2dc\uac04 15\ubd84)'), '37.164106', '128.985751', STRINGDECODE('\uac15\uc6d0\ub3c4 \ud0dc\ubc31\uc2dc, \uacbd\uc0c1\ubd81\ub3c4 \ubd09\ud654\uad70 \uc11d\ud3ec\uba74'), 2214211300, 1567, STRINGDECODE('\ud0dc\ubc31\uc0b0'), STRINGDECODE('&lt;\uc2e0\ub77c \uc784\uae08 \uc21c\ud589\ud588\ub358 \uc131\uc0b0\uc774\uba70 \uc601\uc0b0, \uba85\uc0b0&gt;<BR>\uc2e0\ub77c \uc784\uae08\uc774 \ub458\uc529\uc774\ub098 \uc21c\ud589\uc744 \uac14\ub358 \uc131\uc0b0(\u8056\u5c71)\uc774\ub2e4. \uadf8\ub54c\ub294 \ubb3c\ub860 \ubd09\ud654 \ud0dc\ubc31\uc0b0\uc774\uc5c8\ub294 \ubc14 \uae30\uc2e4\uc740 \uadf8\ucabd\uc774 \ud0dc\ubc31\uc0b0\uc758 \uc815\uba74\uc774\ub2e4. \uc0b0\uc758 \ub300\ubd80\ubd84\uc774 \uadf8 \uc601\uc5ed\uc778 \ubd81\ucabd\uc758 \ud0dc\ubc31\uc2dc\ub294 \uad6c\ud55c\ub9d0 \ube44\uacb0\ud30c\ub4e4\uc774 \ubab0\ub824\ub4e4\uae30 \uc804\uae4c\uc9c0\ub294 \ubb34\uc778\uc9c0\uacbd\uc774\uc5c8\ub2e4. <BR><BR>\ub0a8\ucabd\uc5d0\uc11c \ubcf8 \uc0b0\uc758 \ubaa8\uc2b5\uc740 \uc131\uc2a4\ub7fd\uae30 \uadf8\uc9c0\uc5c6\ub2e4. \ud2b9\ud788 \ubd80\uc1e0\ubd09 \uc0b0\ud5c8\ub9bf\uae38\uc744 \uac08 \ub54c\uc758 \uc0c1\ubd09 \uc7a5\uad70\ubd09\uc740 \uc2a4\ud22c\ud30c(\u5854\u5a46)\uc758 \uc6d0\uc870\ub77c\ub294 \uc778\ub3c4 \uc0b0\uce58\uc758 \ub300\ud0d1 \uac19\ub2e4. \ubd09\ubd84(\u5c01\u58b3)\ucc98\ub7fc \ub465\uadfc \uc0b0\ub369\uc774 \uc704\uc758 \ub124\ubaa8\uaf34 \ub2e8\uc774 \uc800 \uc720\ub124\uc2a4\ucf54\uac00 \uc815\ud55c \uc138\uacc4\ubb38\ud654\uc720\uc0b0\uacfc \ub2ee\uc740\uaf34\uc744 \uc774\ub8e8\uace0\uc788\ub294 \uac83\uc774\ub2e4. <BR><BR>\ubbfc\uc18d\uc2e0\uc559\uc758 \uc131\uc9c0\uae30\ub3c4 \ud558\ub2e4. \uc8fc\ub4f1\uc0b0\ub85c \uc785\uad6c\uc758 \ub2f9\uace8\uc740 \ubb34\ub2f9\ub4e4\uc774 \uc0ac\ucca0 \ub04a\uc774\uc9c0 \uc54a\ub294\ub2e4. \uc815\uc0c1\uc5d0\uc11c \uc0b0(\u5c71)\uae30\ub3c4\ub97c \ud558\uba74 \uc2e0\uae30(\u795e\u6c23) \ucda9\uc804\uc774 \uadf8\ub807\uac8c \uc798 \ub41c\ub2e4\uace0 \ud55c\ub2e4. \uc81c3\uacf5\ud654\uad6d \uc774\ub798 \"\"\uc720\uc0ac\uc885\uad50 \uc815\ud654\uc791\uc5c5\"\"\uc744 \uc218\ub3c4 \uc5c6\uc774 \ud588\ub294\ub370\ub3c4 \uadf8\ub4e4\uc774 \ubab0\ub824\ub4dc\ub294 \uc774\uc720\ub2e4. <BR><BR>\uc774\ub984\uae4c\uc9c0 \uc0b0\uc744 \ubcf8\ub534 \ud0dc\ubc31\uc2dc\ub294 \uc0b0\uc5d0 \uae30\ub304 \ucd95\uc81c\ub97c \ub137\uc529\uc774\ub098 \ubc8c\uc774\uace0\uc788\ub2e4. 6\uc6d4 \ucca0\ucb49\uc81c, \uac1c\ucc9c\uc808 \ucc9c\uc81c(\u5929\u969b)\ub97c \ud558\uc774\ub77c\uc774\ud2b8\ub85c \ud558\ub294 \ud0dc\ubc31\uc81c, \uc0c8\ud574 \uccab\ub0a0\uc758 \ud574\ub9de\uc774\ucd95\uc81c, 1\uc6d4\uc758 \ub208\ucd95\uc81c\uac00 \uadf8\uac83\uc774\ub2e4. \ucca0\ucb49\uacfc \ub208\uaf43\uc774\ub77c\ub294 \uc790\uc5f0\ud658\uacbd\uc5d0\ub2e4 \uc2e0\ub77c \uc774\ub798\uc758 \uc81c\uc0ac\ubb38\ud654\ub85c \uad00\uad11\uc0c1\ud488\uc744 \ub9cc\ub4e0 \uac83\uc774\ub2e4. \ud0dc\ubc31\uc0b0\uc740 \uadf8\ub9cc\ud55c \uac00\uce58\uac00 \uc788\ub2e4.'), STRINGDECODE('\uc608\ub85c\ubd80\ud130 \uc0bc\ud55c\uc758 \uba85\uc0b0\uc774\ub77c \ubd88\ub9ac\uc6e0\uc73c\uba70 \uc0b0 \uc815\uc0c1\uc5d0\ub294 \uace0\uc0b0 \uc2dd\ubb3c\uc774 \uc790\uc0dd\ud558\uace0 \uaca8\uc6b8 \ud770 \ub208\uc73c\ub85c \ub36e\uc778 \uc8fc\ubaa9\uad70\ub77d\uc758 \uc124\uacbd \ub4f1 \uacbd\uad00\uc774 \ub6f0\uc5b4\ub098\uba70 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1989\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc0bc\uad6d\uc0ac\uae30\uc5d0 \ub530\ub974\uba74 \uc0b0 \uc815\uc0c1\uc5d0 \uc788\ub294 \ucc9c\uc81c\ub2e8\uc5d0\uc11c \uc655\uc774 \uce5c\ud788 \ucc9c\uc81c\ub97c \uc62c\ub838\ub2e4\ub294 \uae30\ub85d\uc774 \uc788\uc74c. \ub9dd\uacbd\uc0ac, \ubc31\ub2e8\uc0ac \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ub2e8\uad70\uc81c \uc62c\ub9ac\ub294 \uc2e0\uc131\ud55c \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\ud0dc\ubc31\uc2dc \uc911\uc2ec\uc9c0\uc5d0 \uc219\ubc15\uc5c5\uc18c\uac00 \ub9ce\ub2e4. <BR>\uba54\ub974\ub514\uc559\ud638\ud154 \u260e033-553-1366 \uc2a4\uce74\uc774\ud638\ud154 \u260e033-552-9977 \ub3d9\uacbd\uc5ec\uad00 \u260e033-552-6624 <BR>\ub2f9\uace8 \uc785\uad6c\uc5d0\ub294 \uae38\ubaa9\ubbfc\ubc15 \u260e033-553-1147 \ud1b5\ub098\ubb34\uc9d1 \u260e033-552-9966<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud0dc\ubc31\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e033-550-2828'), STRINGDECODE('\uae30\uc810\uc740 \ud0dc\ubc31\uc2dc\ub2e4. \ud0dc\ubc31\uc5ed\uacfc \uc2dc\uc678\ubc84\uc2a4\ud130\ubbf8\ub110\uc774 \uc788\ub294 \ud669\uc9c0\uc5d0\uc11c \ub2f9\uace8\ud589 \ubc84\uc2a4\uac00 30\ubd84\uc5d0\uc11c 1\uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \uc788\ub2e4. \ud0dc\ubc31\uc73c\ub85c\ub294 \uc11c\uc6b8 \uccad\ub7c9\ub9ac\ubc1c \ud0dc\ubc31\uc120 \uc5f4\ucc28\uac00 9\ud68c, \uacbd\ubd80\u00b7\uacbd\ubd81\u00b7\uc601\ub3d9\uc120 \uacbd\uc720 \ubd80\uc0b0\ubc1c, \uc911\uc559\u00b7\uc601\ub3d9\uc120\uc73c\ub85c \uac00\ub294 \ubd80\uc804\ubc1c\uacfc \ub3d9\ub300\uad6c\ubc1c, \ud638\ub0a8\u00b7\ucda9\ubd81\u00b7\ud0dc\ubc31\uc120\uc744 \ud0c0\ub294 \uad11\uc8fc\ubc1c \uc5f4\ucc28\uac00 \ub2e4\ub2cc\ub2e4. \uaca8\uc6b8\uc5d0\ub294 \uc601\ub3d9\uc120 \ub208\uaf43\uc5f4\ucc28\uac00 \ucd94\uac00\ub41c\ub2e4. \ub3d9\uc11c\uc6b8\ud130\ubbf8\ub110\uc5d0\uc11c \uace0\uc18d\ubc84\uc2a4\ub294 17\ud68c(\uc2ec\uc57c 1\ud68c), \uc9c1\ud1b5\uc740 15\ud68c, \uc9c1\ud589\uc740 11\ud68c \uc788\ub2e4. \uac15\uc6d0\ub3c4 \uc774\uc678\uc758 \ub3c4\uc2dc\ub85c\ub294 \ub300\uad6c, \ud3ec\ud56d, \uc548\ub3d9, \uc778\ucc9c, \uc218\uc6d0, \uc131\ub0a8, \uc548\uc0b0, \uc758\uc815\ubd80, \ub300\uc804\uc5d0\uc11c \ubc84\uc2a4\uac00 \ub2e4\ub2cc\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(89, TIMESTAMP '2019-08-14 15:46:34.943', TIMESTAMP '2019-08-14 15:47:32.568', '42', STRINGDECODE('\uc2e0\uc99d\ub3d9\uad6d\uc5ec\uc9c0\uc2b9\ub78c\uc5d0 \ub300\ud654\uc0b0\uc774\ub77c \uc9c0\uce6d\ud55c \ud0dc\ud654\uc0b0\uc740 \uac15\uc6d0 \uc601\uc6d4\uad70 \uc601\uc6d4\uc74d \ub0a8\ucabd\uc5d0 \uc790\ub9ac\ud574 \uc788\ub2e4. \uc815\uc0c1\uc5d0\uc11c \ubd81\uc11c\ucabd\uc73c\ub85c \ubed7\uc740 \ub2a5\uc120 \ub05d\uc5d0\ub294 \ub0a8\ud55c\uac15\uc774 \uad7d\uc774\uccd0 \ud750\ub974\uace0 \uc815\uc0c1\uc73c\ub85c \ud5a5\ud558\ub294 \uae38\uc5d0 \uc601\uc6d4\uc74d\uc744 \ub450\ub8e8 \uad7d\uc5b4\ubcfc \uc218 \uc788\ub294 \ud0dc\ud654\uc0b0\uc131\ud130\uac00 \ub0a8\uc544 \uc788\ub2e4. <BR><BR>\uace0\uad6c\ub824 \uc2dc\ub300\uc5d0 \uc313\uc558\ub358 \ud1a0\uc131, \ud0dc\ud654\uc0b0\uc131\uc5d0\uc11c\ub294 \uac04\ud639 \ub2f9\uc2dc\uc758 \uae30\uc640 \ud30c\ud3b8\uc774 \ubc1c\uacac\ub418\uae30\ub3c4 \ud55c\ub2e4. \ud0dc\ud654\uc0b0 \uc815\uc0c1\uc5d0\uc11c\ub294 \uba40\ub9ac \ub0a8\uc73c\ub85c \uc18c\ubc31\uc0b0\uacfc \ubc31\ub450\ub300\uac04 \uc904\uae30\uac00 \uc870\ub9dd\ub41c\ub2e4.'), STRINGDECODE('\u2460 \ud314\uad34\ub9ac-\uc0bc\uac70\ub9ac \uc548\ubd80-\uc815\uc0c1-\uc601\ucd98(\ucd1d 3\uc2dc\uac04 40\ubd84)'), '37.201939', '128.510815', STRINGDECODE('\uac15\uc6d0\ub3c4 \uc601\uc6d4\uad70 \uc601\uc6d4\uc74d, \ucda9\uccad\ubd81\ub3c4 \ub2e8\uc591\uad70 \uc601\ucd98\uba74'), 2208064100, 1028, STRINGDECODE('\ud0dc\ud654\uc0b0'), STRINGDECODE('&lt;\ub0a8\ud55c\uac15 \ud718\uac10\uc544 \ub3c4\ub294 \uc5b5\uc0c8\uc758 \uc0b0&gt;<BR>\uac15\uc6d0\ub3c4\uc640 \ucda9\ubd81\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\ub294 \ud0dc\ud654\uc0b0\uc740 \uc774\ub984\uc5d0 \uac78\ub9de\ub294 \ud06c\uace0 \uc544\ub984\ub2e4\uc6b4 \uc0b0\uc774\ub2e4. \uc544\uc6b8\ub7ec \uc544\ub2f4\ud55c \uc5b5\uc0c8\ubc2d\uc744 \uac00\uc9c0\uace0 \uc788\uc5b4 \uac00\uc744\uc0b0\ud589\uc9c0\ub85c \uc54c\ub9de\ub2e4. \uadf8\ub807\uc9c0\ub9cc \uc8fc\uc704\uc5d0 \uc788\ub294 \uc6d0\uc8fc\uc758 \uce58\uc545\uc0b0\uacfc \ub2e8\uc591\uc758 \uc18c\ubc31\uc0b0, \uc6d4\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc758 \uadf8\ub298\uc5d0 \uac00\ub824 \ub9ce\uc774 \uc54c\ub824\uc9c0\uc9c0\ub294 \ubabb\ud55c \uc0b0\uc774\ub2e4. \uc774\ub7f0 \uc5f0\uc720\ub85c \ud0dc\ud654\uc0b0\uc5d0\uc11c\ub294 \uc544\uc9c1\ub3c4 \uc0ac\ub78c\uc758 \ubc1c\uae38\uc774 \ub2ff\uc9c0 \uc54a\uc740 \ud0dc\uace0\uc758 \uc790\uc5f0\ubbf8\ub97c \ubcfc \uc218\uac00 \uc788\ub2e4. \uac8c\ub2e4\uac00 \ub0a8\ud55c\uac15\uc774 \uc0b0\uc790\ub77d\uc744 \ud718\uac10\uc544 \ud750\ub974\uace0 4\uc5b5\ub144\uc758 \uc2e0\ube44\ub97c \uac04\uc9c1\ud55c \uace0\uc528\ub3d9\uad74\ub97c \ud488\uc5d0 \uc548\uace0 \uc788\uc5b4 \uc0b0\ud589\uc758 \ubb18\ubbf8\ub97c \ub354\ud55c\ub2e4. <BR><BR>\uc0ac\uacc4\uc808 \ubcc0\ud654\ubb34\uc30d\ud55c \ubd80\ub4dc\ub7ec\uc6b4 \ub2a5\uc120\uae38\uc740 \uc544\ub984\ub2e4\uc6b4 \ube44\uacbd\uc744 \ubcf4\uc5ec\uc8fc\uace0 \uac00\uc871\ub2e8\uc704 \uc0b0\ud589\uc9c0\ub85c \uc801\uaca9\uc778 \uc0b0\uc774\ub2e4. \ud0dc\ud654\uc0b0 \uc0b0\ud589\uc758 \ub4e4\uba38\ub9ac\ub294 \uc8fc\ub85c \uc601\ucd98, \ud314\uad34\ub9ac, \uace0\uc528\ub3d9\uad74 \ub4f1 3\uad70\ub370\uc774\uc9c0\ub9cc \uace0\uc528\ub3d9\uad74 \ucabd\uc740 \uac00\ud314\ub77c \uc8fc\ub85c \uc601\ucd98\uc774\ub098 \ud314\uad34\ub9ac\ub97c \ub4e4\uba38\ub9ac\ub85c \ub9ce\uc774 \uc7a1\ub294\ub2e4'), STRINGDECODE('\uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0 \uace0\uad6c\ub824 \uc2dc\ub300\uc5d0 \uc313\uc558\ub358 \ud1a0\uc131\uc778 \ud0dc\ud654\uc0b0\uc131 \ub4f1 \uc5ed\uc0ac\uc801 \uc720\uc801\uc774 \uc788\uace0, \uace0\uc528\ub3d9\uad74(\u9ad8\u6c0f\u6d1e\u7a9f : \ucc9c\uc5f0\uae30\ub150\ubb3c \uc81c219\ud638) \ub4f1\uc774 \uc18c\uc7ac\ud558\uace0 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815'), STRINGDECODE('\ud0dc\ud654\uc0b0\uc131\uc5d0\uc11c \uc601\uc6d4\uc74d\uc744 \ub450\ub8e8 \uad7d\uc5b4\ubcf4\ub2c8'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\u