INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(90, TIMESTAMP '2019-08-14 15:46:34.943', TIMESTAMP '2019-08-14 15:47:33.156', '47', STRINGDECODE('\uacbd\ubd81 \uacbd\uc0b0\uc2dc\uc758 \ubd81\ucabd\uc5d0 \uc704\uce58\ud55c \ud574\ubc1c1,192.3m \uc0b0\uc774\uace0. \uad00\ubd09\uc740 852m \ubd09\uc6b0\ub9ac\ub85c\uc368 \uc544\uc8fc \ud5d8\uc900\ud55c \uc0b0\uc740 \uc544\ub2c8\uace0 \ubcf4\ud1b5\uc778\uc740 \ub4f1\uc0b0\ud558\uae30\uc5d0 \uc54c\ub9de\uace0 \ub2a5\uc120\ubd80\uc704\uc5d0 \uc554\ubc18\uc774 \ub9ce\uc740 \uacf3\uc73c\uba70, \uc790\uc5f0\uacbd\uad00\uc774 \uc88b\uace0 \uad00\ubd09\uc5d0\uc11c\ub294 \uc804\ub9dd\ub3c4 \uc88b\uc73c\uba70. \uc77c\ub144\ub0b4\ub0b4 \ubd88\uc790\uc2e0\ub3c4\uc640 \ub4f1\uc0b0\uac1d\uc774 \uac00\uc7a5\ub9ce\uc740 \uc7a5\uc18c\uc774\ub2e4.'), STRINGDECODE('\u2460 \ud559\uba85\ub9ac-\uac00\uc0b0-\ud55c\ud2f0\uc7ac-\ud30c\uacc4\uc7ac-\ud30c\uacc4\uc0ac (\ucd1d 7\uc2dc\uac04)<BR>\u2461 \uac13\ubc14\uc704\uc8fc\ucc28\uc7a5-\uac13\ubc14\uc704-\ub2a5\uc131\uc7ac-\uc2e0\ub839\uc7ac-\ub3d9\ubd09-\uc5fc\ubd88\uc554-\ub3d9\ud654\uc0ac (\ucd1d 6\uc2dc\uac04 10\ubd84)<BR>\u2462 \ub3d9\ud654\uc0ac-\uc5fc\ubd88\uc554-\ub3d9\ubd09-\uc218\ub3c4\uc0ac-\uc2e0\uc2dc\uc554 (\ucd1d 4\uc2dc\uac04 30\ubd84)'), '36.062346', '128.660272', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uad70\uc704\uad70 \ubd80\uacc4\uba74, \uc601\ucc9c\uc2dc \uc2e0\ub155\uba74, \ub300\uad6c\uad11\uc5ed\uc2dc \ub3d9\uad6c'), 1304461200, 1192, STRINGDECODE('\ud314\uacf5\uc0b0'), STRINGDECODE('&lt;\uc0bc\uad6d\ud1b5\uc77c\uc758 \ub3c4\uc7a5, \ub300\uad6c\uc758 \uc9c4\uc0b0&gt;<BR>\uc2e0\ub77c\uc758 \uc911\uc545(\u4e2d\u5cb3) \ud314\uacf5\uc0b0\uc740 \ub300\uad6c\uc0b0\uc545\uc778\uc758 \ubaa8\uc0b0\uc774\uba70 \uc57d\uc0ac\uc2e0\uc559\uc758 1\ubc88\uc9c0\ub2e4. <BR>\uc774\ub984\uc740 \ub300\uad6c, \uce60\uace1, \uc778\ub3d9(\ud604 \uad6c\ubbf8 \uc778\uc758\ub3d9 \uc77c\ub300), \uc2e0\ub155, \uc758\ud765, \uc601\ucc9c, \ud558\uc591, \uacbd\uc0b0 \ub4f1 \uc5ec\ub35f \ub9c8\uc744\uc5d0 \uac78\u