INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(91, TIMESTAMP '2019-08-14 15:46:34.944', TIMESTAMP '2019-08-14 15:47:33.751', '42', STRINGDECODE('\uc8fc\ub2a5\uc120\uc774 \ub9c8\uce58 \ubcd1\ud48d\uc744 \ud3bc\uce5c \ub4ef\ud55c \uc0b0\uc138\ub85c, \uc608\ubd80\ud130 ''\uc18c\uae08\uac15'' \uc774\ub77c \ubd88\ub9ac\uc5b4\uc9c8 \ub9cc\ud07c \uc544\ub984\ub2f5\ub2e4. \uac8c\ub2e4\uac00 \uc8fc\ub2a5\uc120 \uc88c\uc6b0\ub85c \ud64d\ucc9c\uac15\uc774 \ud750\ub974\uace0 \uc788\uc5b4 \uc815\uc0c1\uc5d0 \uc62c\ub77c\uc11c \ubc14\ub77c\ubcf4\ub294 \uc804\ub9dd\uc774 \ub354 \uc5c6\uc774 \uc88b\uc73c\uba70, \ubb3c\ub180\uc774\ub3c4 \uacb8\ud560 \uc218 \uc788\ub294 \uacf3\uc774\ub2e4.<BR><BR>\ud314\ubd09\uc0b0\uc740 \ud754\ud788 \ub450 \ubc88 \ub180\ub77c\uac8c \ud558\ub294 \uc0b0\uc73c\ub85c \uc54c\ub824\uc838 \uc788\ub2e4. \ub0ae\uc740 \uc0b0\uc774\uc9c0\ub9cc, \uc0b0\uc138\uac00 \uc544\ub984\ub2e4\uc6cc \ub180\ub77c\uace0 \uc77c\ub2e8 \uc0b0\uc5d0 \uc62c\ub77c \ubcf4\uba74 \uc554\ub989\uc774 \uc904\uc9c0\uc5b4 \uc788\uc5b4 \uc0b0\ud589\uc774 \ub9cc\ub9cc\uce58 \uc54a\uc544 \ub180\ub780\ub2e4\ub294 \uac83\uc774\ub2e4.'), ' ', '37.667227', '127.635068', STRINGDECODE('\uac15\uc6d0\ub3c4 \ud64d\ucc9c\uad70 \uc11c\uba74 \ud314\ubd09\ub9ac'), 2216075500, 328, STRINGDECODE('\ud314\ubd09\uc0b0'), STRINGDECODE('&lt;\ud64d\ucc9c\uac15 \uc548\uace0\ub3c4\ub294 \ud1b1\ub0a0 \uc218\uc11d&gt;<BR>\ud314\ubd09\uc0b0\uc740 \uc0b0 \uac19\uc9c0\ub3c4 \uc54a\uc740, 300\ubbf8\ud130\uac00 \uc870\uae08 \ub118\ub294 \ub3d9\uc0b0\uc774\ub2e4. \uadf8\ub7fc\uc5d0\ub3c4 \uac19\uc740 \uc774\ub984\uc758 \uc5b4\ub5a4 \uc0b0\ubcf4\ub2e4 \uc0ac\ub78c\ub4e4\uc774 \ub9ce\uc774 \ucc3e\ub294, \uc720\uba85\ud55c \uc0b0\uc774\ub2e4. \uc5ec\ub35f \ubd09\uc6b0\ub9ac\uac00 \ubaa8\ub450 \ubc14\uc704\ub85c \ub418\uc5b4\uc788\uc5b4 \ud558\ub298\uae08\uc774 \ub465\uadfc \ud1b1\ub0a0\ucc98\ub7fc \uc5ed\ub3d9\uc801\uc774\uace0 \uc2a4\ub9b4\uc774 \ub118\uce58\uae30 \ub54c\ubb38\uc774\ub2e4. <BR>\uadf8\ub798 \uc791\uc740 \uc0b0\uc774\uc9c0\ub9cc \ub300\uac1c\uc758 \ub4f1\uc0b0\uac1d\ub4e4\uc740 \ud314\ubd09 \ub118\uae30\ub97c \ub2e4 \ud558\uc9c0 \ubabb\ud55c\ub2e4. \ucca0\uacc4\ub2e8\uacfc \uc1e0\uc904\uae4c\uc9c0 \uccd0\uc788\ub294\ub370\ub3c4 \ub9c8\uc9c0\ub9c9 \ubd09\uc6b0\ub9ac\ub294 \ud3ec\uae30\ud558\uace0 \ub3cc\uc544\uc11c\uae30 \uc77c\uc464\ub2e4. \uc791\uc544\ub3c4 \uac15\ub2e8(\u525b\u65b7) \uc788\ub294 \uc0b0\uc774\ub2e4. <BR><BR>\ubc1c\uce58 3\uba74\uc740 \ud64d\ucc9c\uac15\uc774 \uc2f8\uace0\ub3c8\ub2e4. \uac15\ubb3c\uc740 \uae4a\ud478\ub974\ub098 \ubb3c\uac00\uc5d0\ub294 \ubaa8\ub798\ubc8c\uc774 \ud3bc\uccd0\uc84c\ub2e4. \uadf8\ub0e5 \ubaa8\ub798 \ud558\uc580 \uc218\ubc18(\u6c34\u76e4)\uc5d0 \ubb3c\uc774 \uc798\ubc15\uac70\ub9ac\ub294 \uc218\uc11d(\u58fd\u77f3)\uc778 \uac83\uc774\ub2e4. \uc694\uc0b0\uc694\uc218\ub97c \uacb8\ud558\uae30\uc5d0 \uc774\ubcf4\ub2e4 \ub098\uc740 \ub370\uac00 \uc788\uaca0\ub294\uac00? <BR><BR>\uac15 \uac74\ub108 \uc11c\ucabd\uc758, \ub2aa\ub454\uc9c0\ub77c\ub294 \uc6b0\uac01\ud638(\u725b\u89d2\u6e56) \uac00\uc5d0\ub294 \ubc24\ub098\ubb34\uc232\uc774 \uc6b8\ucc3d\ud558\ub2e4. \uadfc\ub798 \uc624\ud1a0\ucea0\ud551\uc7a5\uc744 \ub9cc\ub4e0\ub2e4\uba70 \ub9ce\uc774 \ubca0\uc5b4\ubc84\ub838\uc9c0\ub9cc \uc544\uc9c1\ub3c4 \uc61b\ubaa8\uc2b5\uc774 \uaf64 \ub0a8\uc544\uc788\uace0 \uc61b \uac15\uae38\ub3c4 \ub744\uc5c4\ub744\uc5c4 \uadf8\ub300\ub85c \uc788\ub2e4. \uc774\ub7f0 \uba85\uc0b0(\u540d\u5c71)\uc601\uc218(\u9748\u6c34)\uae30 \ub54c\ubb38\uc5d0 \uc608\ub098 \uc9c0\uae08\uc774\ub098 \uad50\ud1b5\uc774 \ubd88\ud3b8\ud55c\ub370\ub3c4 \uc0ac\ub78c\ub4e4\uc740 \ucca0\uc744 \uac00\ub9ac\uc9c0 \uc54a\uace0 \ucc3e\uc544\ub4e0\ub2e4.'), STRINGDECODE('\uc0b0\uc740 \ub098\uc9c0\ub9c9\ud558\uace0 \uaddc\ubaa8\ub3c4 \uc791\uc73c\ub098 \uc5ec\ub35f\uac1c\uc758 \ubc14\uc704\ubd09\uc774 \ud314\uc9f1 \ub080 8\ud615\uc81c\ucc98\ub7fc \uc774\uc5ec\uc838 \uc788\uace0 \ud64d\ucc9c\uac15\uacfc \uc5f0\uc811\ud558\uc5ec \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b4 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uad6d\ubbfc\uad00\uad11\uc9c0\ub85c \uc9c0\uc815\ub418\uc5b4 \uc788\uc74c'), STRINGDECODE('\uc0b0\uc138\uc640 \uc554\ub989\uc5d0 \ub450 \ubc88 \ub180\ub77c\ub294 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc6cc\ud130\uc6d4\ub4dc\ub97c \uc774\uc6a9\ud560 \uacc4\ud68d\uc774\uba74 \ube44\ubc1c\ub514\ud30c\ud06c \ucf58\ub3c4\ub97c \uc608\uc57d\ud55c\ub2e4. \u260e033-434-8311 <BR>\uc18c\ub9ac\uc0b0 \uc22f\uac00\ub9c8\ucc1c\uc9c8\ubc29\uc5d0\uc11c \uc798 \uc218\ub3c4 \uc788\ub2e4. \u260e063-581-4644 <BR>\uc5ec\ub984\ucca0\uc5d4 \ud64d\ucc9c\uac15 \ubaa8\uace1\uc720\uc6d0\uc9c0 \uc57c\uc601\ub3c4 \uac00\ub2a5\ud558\ub2e4. <BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud64d\ucc9c\uad70 \ud314\ubd09\uc0b0\uad00\ub9ac\uc0ac\ubb34\uc18c \u260e033-434-0813'), STRINGDECODE('\ud64d\ucc9c\uc5d0\ub3c4 \ubc84\uc2a4\uac00 \uc788\uc9c0\ub9cc \ucd98\ucc9c \uae30\uc810\uc774 \ud3b8\ud558\ub2e4. \ubc18\uace1(\uc11c\uba74)\ud589 \ubc84\uc2a4\ub97c \ud0c0\uace0\uac00\ub2e4 \uc5b4\uc720\ud3ec\ub9ac\uc5d0\uc11c \ub0b4\ub9b0\ub2e4. <BR>- \ucd98\ucc9c\u2192\uc5b4\uc720\ud3ec\ub9ac : 30\ubd84 \uac04\uaca9 \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(92, TIMESTAMP '2019-08-14 15:46:34.944', TIMESTAMP '2019-08-14 15:47:34.352', '46', STRINGDECODE('\uace0\ud765\uc74d\uc5d0\uc11c \ub3d9\ucabd\uc73c\ub85c 25km \ub5a8\uc5b4\uc9c4 \uc18c\ubc31\uc0b0\ub9e5\uc758 \ub9e8 \ub05d\ubd80\ubd84\uc5d0 \uc704\uce58\ud55c \uc0b0\uc73c\ub85c 8\uac1c\uc758 \ubd09\uc6b0\ub9ac\uac00 \ub0a8\ucabd\uc744 \ud5a5\ud574 \uc77c\uc9c1\uc120\uc73c\ub85c \uc19f\uc544 \uc788\ub2e4.'), STRINGDECODE('\u2460\ub2a5\uac00\uc0ac \uc785\uad6c-\ud314\uc601\uc0b0\uc7a5-\ud754\ub4e4\ubc14\uc704-1\u223c8\ubd09-\uae43\ub300\ubd09-\ub3d9\ub0a8\ub989-\uc601\ub0a8\uba74\uc18c(\ucd1d 3\uc2dc\uac04 40\ubd84)<BR>\u2461\uc6b0\ucc9c\ub9ac \ub9e4\ud45c\uc18c-\uc790\uc5f0\ud734\uc591\ub9bc-2\ubd09 \ub3d9\ub989-2\u223c8\ubd09-\ud314\uc601\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc-\uc6b0\ucc9c\ub9ac \ub9e4\ud45c\uc18c(\ucd1d 3\uc2dc\uac04 20\ubd84)<BR>\u2462\uac15\uc0b0\ub9ac-\uc2e0\uc120\ub300-2\ubd09 \ub3d9\ub989-2\u223c8\ubd09-\ud0d1\uc7ac-\ud314\uc601\uc0b0\uc7a5-\ub2a5\uac00\uc0ac \uc785\uad6c(\ucd1d 3\uc2dc\uac04 30\ubd84)'), '34.604734', '127.455602', STRINGDECODE('\uc804\ub0a8 \uace0\ud765\uad70 \uc601\ub0a8\uba74 \uc6b0\ucc9c\ub9ac'), 2602132900, 607, STRINGDECODE('\ud314\uc601\uc0b0'), STRINGDECODE('&lt;\ube7c\uc5b4\ub098\uba74\uc11c\ub3c4 \uc6c5\uc7a5\ud55c \ubc14\uad6c\ub2e4\ub9ac\uc0b0&gt;<BR>\ud130\uc9c8 \ub4ef\ud55c \ubd09\ub9cc\ubbf8\ub97c \uc790\ub791\ud558\ub294 \uc0b0\uc774\ub2e4. ''\ud314''\uc790 \ub4e4\uc5b4\uac04 \uc0b0 \uce58\uace0 \ubd09\ub9cc\ubbf8 \ub6f0\uc5b4\ub098\uc9c0 \uc54a\uc740 \uac83\uc774 \uc5c6\uc9c0\ub9cc \ud64d\ucc9c\uc774\ub098 \uc11c\uc0b0 \ud314\ubd09\uc0b0\uc740 \uc791\uc544\uc11c \uc6c5\uc7a5\ud568\uc774 \uc5c6\ub294\ub370 600\ubbf8\ud130\uc758 \ub192\uc774\uc5d0 \uc5ec\ub35f \uc554\ubd09\uc774 \uc19f\uc740 \uc774 \uace0\ud765 \ucd5c\uace0\ubd09\uc740 \ube7c\uc5b4\ub09c \uc704\uc5d0 \uc6c5\uc7a5\ud558\uae30\uae4c\uc9c0 \ud558\ub2e4. 2\ubd09\uc758 \ub3d9\ubd81\ub3d9\ub989\uacfc 8\ubd09 \ub0a8\ub989\uc758 \uade0\ud615 \uc7a1\ud78c \uc591 \uc5b4\uae68\uc5d0\ub2e4 \uc55e\uc5d0\ub294 \uc0bc\uac01\ubfd4 \ucc9c\uc8fc\ubd09\uc774 \uc9c0\ud0a4\uace0\uc788\uc5b4 \uad6c\ub3c4 \ub610\ud55c \uc644\ubcbd\ud558\ub2e4. \uacbd\uc778\uc9c0\uc5ed\uc5d0\uc11c \uac00\uc7a5 \uba40\ub9ac \uc788\ub294 \uc0b0 \uc911 \ud558\ub098\uc778\ub370\ub3c4 \uc0ac\ub78c\ub4e4\uc774 \ub04a\uc784\uc5c6\uc774 \ucc3e\ub294 \uc774\uc720\ub2e4. <BR><BR>\uace0\ud765\uad70\uc5d0\uc11c 1\ubd09\uc5d0\ub294 \uc720\uc601\ubd09(\u5e7c\u5f71\u5cef), 2\ubd09\uc740 \uc131\uc8fc\ubd09, 3\ubd09 \uc0dd\ud669\ubd09, 4\ubd09 \uc0ac\uc790\ubd09, 5\ubd09 \uc624\ub85c\ubd09, 6\ubd09 \ub450\ub8e8\ubd09, 7\ubd09 \uce60\uc131\ubd09, 8\ubd09 \uc801\ucde8\ubd09\uc774\ub77c\uace0 \ub3cc\uba85\ud328\ub97c \uc138\uc6cc\ub193\uc558\ub294\ub370 \uc21c\uc11c\uc640 \uc774\ub984\uc758 \uc0c1\uad00\uad00\uacc4\uc5d0\uc11c \ubcf4\ub4ef \ub2e4\ubd84\ud788 \uc791\uc704\uc801\uc774\ub2e4. \uc6d0\ub798\ub294 \uc880 \ub5a8\uc5b4\uc9c4 1\ubd09\uc5d0\ub9cc \uc774\ub984\uc774 \uc788\uc5c8\uace0 \ub098\uba38\uc9c0\ub294 \ubb36\uc5b4\uc11c \uc131\uc8fc\ubd09(\u8056\u4e3b\u5cef)\uc774\ub77c\uace0 \ubd88\ub800\ub358 \uac83\uc744 \ud558\ub098\ud558\ub098 \uc791\uba85\uc744 \ud55c \uac83\uc774\ub2e4. <BR>\uc804\uacbd \ucd2c\uc601 \ud3ec\uc778\ud2b8\ub294 \uc810\uc554(\u5360\u5ca9)\uba74\uc18c\uac00 \uc788\ub294 \ubaa8\ub8e1\ub9ac\ub2e4. \uadf8 \uc810\uc554\uc740 \uace0\ud765\uc0ac\ub78c\ub4e4\uc774 \"\"\ubc14\uad6c\"\"\ub77c \ubd80\ub974\ub294 \ubc14\uc704\uac00 \uc810\uc810\uc774 \uc788\ub2e4\ub294 \ub73b\uc778\ub370 \uc810\uc554\uba74\uc5d0 \uc774\ub7f0 \uc9c0\ud615\uc740 \ud314\uc601\uc0b0 \uc678\uc5d0 \ub2ec\ub9ac \uc5c6\ub2e4. \uadf8\ub9ac\uace0 \ubaa8\ub8e1\ub9ac\uc5d0\ub294 \ud68c\uacc4\ub9c8\uc744\uc774 \uc788\ub294 \ubc14 \uc6b0\ub9ac\ub9d0\ub85c \"\"\ubc14\uad6c\ub2e4\ub9ac\"\"\ub77c\uace0 \ud55c\ub2e4. \ud314\uc601\uc0b0\uc758 \uc6d0\uc774\ub984\uc740 \ubc14\uad6c\ub2e4\ub9ac\ubafc\ub098 \ubc14\uad6c\ub2e4\ub9ac\uc0b0\uc774\uc5c8\ub358 \uac83\uc774\ub2e4. <BR><BR>\uc19f\uc74c\uc774 \uc6b0\uc138\ud574 \uacc4\uace1\uc740 \uc369 \ubc1c\ub2ec\ud558\uc9c0 \ubabb\ud588\ub2e4. \ub300\uc2e0 \ubc14\ub2e4\uac00 \uc788\ub2e4. \uc721\uc9c0\uc5d0 \ub2ec\ub9b0 \uace0\uad6c\ub9c8 \ubaa8\uc591\uc758 \ubc18\ub3c4 \uace0\ud765\uc758 \uc0b0\uc774\ub77c \uc0ac\ubc29\uc73c\ub85c \ud638\uc218 \uac19\uc740 \ubc14\ub2e4\uac00 \ud3bc\uccd0\uc9c4\ub2e4.'), STRINGDECODE('\uc5ec\ub35f\uac1c\uc758 \uc554\ubd09\uc73c\ub85c \uc774\ub8e8\uc5b4\uc9c4 \uc0b0\uc138\uac00 \ud5d8\uc900\ud558\uace0 \uae30\uc554\uad34\uc11d\uc774 \ub9ce\uc73c\uba70 \uc870\ub9dd\uc774 \uc88b\uace0 \ub3c4\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815(1998\ub144)\ub41c \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc608\uc804\uc5d0 \ud654\uc5c4\uc0ac, \uc1a1\uad11\uc0ac, \ub300\ud765\uc0ac\uc640 \ud568\uaed8 \ud638\ub0a8 4\ub300 \uc0ac\ucc30\ub85c \uaf3d\ud788\ub358 \ub2a5\uac00\uc0ac\uac00 \uc788\uc74c. \uc2e0\uc120\ub300, \uac15\uc0b0\ud3ed\ud3ec \ubc0f \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc788\uc74c. \uc815\uc0c1\uc5d0\uc11c \ub300\ub9c8\ub3c4\uae4c\uc9c0 \ubcf4\uc77c \uc815\ub3c4\ub85c \uc870\ub9dd\uc774 \uc88b\uc74c'), STRINGDECODE('\ub300\ub9c8\ub3c4\ub97c \uc870\ub9dd\ud558\ub294 \ucc9c\ub9ac\uc548'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uc8fc\ubcc0\uc815\ubcf4<BR>\ub2a5\uac00\uc0ac \uc548\ucabd\uc758 \ud314\uc601\uc0b0\uc7a5(\u260e061-833-8080)\uc774\ub098 \ud314\uc601\uc0b0\uc790\uc5f0\ud734\uc591\ub9bc(\u260e061-833-8779)\uc744 \uc774\uc6a9\ud55c\ub2e4. <BR>\uc5ec\uad00\uc740 \uace0\ud765\uc74d\ub0b4\uc5d0 \ub098\uac00\uc57c \uc788\ub2e4. \ud589\uc6b4\uc7a5\uc5ec\uad00 \u260e061-834-6101 \uadf8\ub9b0\ud30c\ud06c\uc5ec\uad00 \u260e061-835-3364<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud314\uc601\uc0b0\ub3c4\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e061-835-9566'), STRINGDECODE('\uae30\uc810\uc740 \uace0\ud765\uc74d\uc774\ub2e4. \uc810\uc554\uba74\uc18c, \ub2a5\uac00\uc0ac \uc785\uad6c, \uc6b0\ucc9c\ub9ac, \