INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(93, TIMESTAMP '2019-08-14 15:46:34.945', TIMESTAMP '2019-08-14 15:47:34.935', '50', STRINGDECODE('\ud55c\ub77c\uc0b0\uc740 \uc6b0\ub9ac\ub098\ub77c \ucd5c\ub0a8\ub2e8\uc5d0 \uc704\uce58\ud574 \uc788\uc73c\uba74\uc11c \ub0a8\ud55c\uc5d0\uc11c \uac00\uc7a5 \ub192\uc740 \uc0b0\uc774\ub2e4. \ud574\ubc1c 1950m, \uba74\uc801 151.35\u33a2 \ub85c 1970\ub144 3\uc6d4 24\uc77c \uad6d\ub9bd\uacf5\uc6d0\uc73c\ub85c \uc9c0\uc815\ub41c \uc774 \uc0b0\uc740 3\ub300 \uc601\uc0b0\uc911\uc758 \ud558\ub098\ub85c \ub2e4\uc591\ud55c \uc2dd\uc0dd\ubd84\ud3ec\ub97c \uc774\ub904 \ud559\uc220\uc801 \uac00\uce58\uac00 \ub192\uc740 \ub3d9\uc2dd\ubb3c\uc758 \ubcf4\uace0\uc774\uae30\ub3c4 \ud558\ub2e4.'), STRINGDECODE('\u2460 \uc5b4\ub9ac\ubaa9\uad11\uc7a5-\uc0ac\uc81c\ube44\ub3d9\uc0b0-\ub9cc\uc138\ub3d9\uc0b0-\uc717\uc138\uc624\ub984(\ucd1d 2\uc2dc\uac04 \uc18c\uc694)<BR>\u2461 1100\ub3c4\ub85c-\uc601\uc2e4\ub9e4\ud45c\uc18c-\uc601\uc2e4\ud734\uac8c\uc18c-\ubcd1\ud48d\ubc14\uc704-\uc717\uc138\uc624\ub984(\ucd1d 3\uc2dc\uac04 \uc18c\uc694)<BR>\u2462 \uc5b4\ub9ac\ubaa9\uad11\uc7a5-\uc5b4\uc2b9\uc0dd\uc545(\ucd1d 1\uc2dc\uac04 \uc18c\uc694)'), '33.499614', '126.531193', STRINGDECODE('\uc81c\uc8fc\ub3c4 \uc81c\uc8fc\uc2dc, \uc11c\uadc0\ud3ec\uc2dc'), 2902194100, 1947, STRINGDECODE('\ud55c\ub77c\uc0b0'), STRINGDECODE('&lt;\ud55c\ub77c\uc0b0\uc774 \uace7 \uc81c\uc8fc\ub3c4\ub2e4&gt;<BR>\ud55c\ubc18\ub3c4 \ub0a8\ucabd\uc758 \ucd5c\uace0\ubd09, \ud574\ubc1c 1950m\uc758 \ud55c\ub77c\uc0b0\uc740 \uc81c\uc8fc\ub3c4 \uc0ac\ub78c\ub4e4\uc758 \uc228\uacb0\uacfc \uc5ed\uc0ac\ub97c \uadf8\ub300\ub85c \uc548\uace0 \uc788\ub294 \uc0b0\uc774\ub2e4.<BR>\ud55c\ub77c\uc0b0\uc774\ub780 \uc774\ub984\uc740 \uc6d0\ub798 ''\uc740\ud558\uc218\ub97c \ub04c\uc5b4\ub2f9\uae38 \uc218 \uc788\ub2e4(\u96f2\u6f22\u53ef\u62cf\u5f15\u4e5f)''\ub294 \ub73b\uc73c\ub85c \ubd99\uc5ec\uc9c4 \uc774\ub984\uc774\ub2e4. \uadf8\ub9cc\ud07c \uc0b0\uc774 \ub192\ub2e4\ub294 \uc810\uc744 \uac15\uc870\ud55c \