INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(94, TIMESTAMP '2019-08-14 15:46:34.945', TIMESTAMP '2019-08-14 15:47:35.524', '41', STRINGDECODE('\uacbd\uae30 5\uc545 \uc911\uc758 \uc73c\ub738\uc778 \ud654\uc545\uc0b0(1,468m)\uc740 \uac15\uc6d0\ub3c4\uc640 \uacbd\uae30\ub3c4\ub97c \uac00\ub974\ub294 \ubd84\uae30\uc810\uc5d0 \uc6b0\ub69d \uc19f\uc544 \uc788\ub294 \uc0b0\uc774\ub2e4. \uacbd\uae30\ub3c4\uc758 \ucd5c\uace0\ubd09\uc774\ub2e4. \ud654\uc545\uc0b0\uc744 \uc911\uc2ec\uc73c\ub85c \ub3d9\ucabd\uc5d0 \ub9e4\ubd09, \uc11c\ucabd\uc5d0 \uc911\ubd09\uc774 \uc704\uce58\ud558\uba70 \uc774\ub4e4\uc744 \uc0bc\ud615\uc81c\ubd09\uc774\ub77c\uace0\ub3c4 \ud55c\ub2e4. \uc5ec\uae30\uc11c \ubc1c\uc6d0\ud558\ub294 \ubb3c\uc740 \ud654\uc545\ucc9c\uc744 \uc774\ub8e8\uba70 \uac00\ud3c9\ucc9c\uc758 \uc8fc\ucc9c\uc774 \ub418\uc5b4 \ubd81\ud55c\uac15\uc73c\ub85c \ud758\ub7ec\ub4e0\ub2e4. <BR><BR>\uc815\uc0c1 \uc8fc\ubcc0\uc740 \uad70\uc0ac\uc9c0\uc5ed\uc73c\ub85c \ucd9c\uc785\uc774 \uae08\uc9c0\ub418\uc5b4 \uc788\uc5b4 \uac00\uae4c\uc6b4 \uacf3\uc5d0\uc11c \ubcf4\ub294 \uac83\uc73c\ub85c \ub9cc\uc871\ud574\uc57c \ud558\ub294 \uc810\uc774 \uc544\uc27d\ub2e4. \uc9c0\uae08\uc740 \uc815\uc0c1 \uc11c\ub0a8\ucabd 1km \uac70\ub9ac\uc5d0 \uc788\ub294 \uc911\ubd09 \uc0b0\ud589\uc73c\ub85c \ud654\uc545\uc0b0 \uc815\uc0c1\uc744 \ub300\uc2e0\ud558\uace0 \uc788\ub2e4. \ud654\uc545\uc0b0 \uc8fc\ub2a5\uc120\uc5d0 \uc624\ub974\uba74 \uac00\ud3c9, \ucd98\ucc9c \uc77c\uc6d0\uc744 \uad7d\uc5b4\ubcfc \uc218 \uc788\uc5b4 \uc0b0\ud589\uc758 \uc7ac\ubbf8\ub97c \ub354\ud574 \uc900\ub2e4.'), STRINGDECODE('\u2460 \ucc9c\ub3c4\uad50\ud654\uc545\uc0b0\uc218\ub3c4\uc6d0-\uc624\ub9bc\uacc4\uace1-\uc911\ubd09-1142-\uc560\uae30\ubd09 \uc804 \ub2a5\uc120-\uc624\ub3d9\uace8(\ucd1d 5\uc2dc\uac04 40\ubd84) <BR>\u2461 \uc624\ub3d9\uace8 -\uc8fc\ub2a5\uc120-\uc911\ubd09 -\uc911\ubd09 \uc11c\ub989- \uac08\ub9bc\uae38-\ud070\uace8 -\uad00\uccad\ub9ac(5\uc2dc\uac04 20\ubd84)'), '37.944525', '127.494128', STRINGDECODE('\uacbd\uae30\ub3c4 \uac00\ud3c9\uad70 \ubd81\uba74, \uac15\uc6d0\ub3c4 \ud654\ucc9c\uad70 \uc0ac\ub0b4\uba74'), 2101023600, 1468, STRINGDECODE('\ud654\uc545\uc0b0(\uc911\ubd09)'), STRINGDECODE('&lt;\uacbd\uae30 \uc81c\uc77c\ubd09 \ud654\uc545\uc0b0&gt;<BR>\uacbd\uae30\ub3c4 \uc81c\uc77c\ubd09\uc73c\ub85c \uacbd\uae30 \ub3d9\ubd81\ubd80\uc640 \uac15\uc6d0\ub3c4\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\uba70 \uc19f\uc544\uc788\ub2e4. \ud55c\ubd81\uc815\ub9e5\uc758 \ub3c4\ub9c8\uce58\ubd09(937m)\uc5d0\uc11c \ub3d9\ub0a8\ucabd\uc73c\ub85c \ubed7\uc740 \uc0b0\uc904\uae30\uac00 \uc11d\ub8e1\uc0b0(1155m)\uc744 \uc9c0\ub098 \uc19f\uad6c\uccd0 \uc774\ub8e8\uc5c8\uace0 \uc774\ud6c4 \uc751\ubd09(1436.3m), \ucd09\ub300\ubd09(1125m)\uc73c\ub85c \uc774\uc5b4\uc9c0\uba70 ''\uacbd\uae30\uc758 \uc54c\ud504\uc2a4''\ub77c \ubd88\ub9b4 \ub9cc\ud55c \uc0b0\uad70\uc744 \ud615\uc131\ud558\uace0\uc788\ub2e4. \ub9c8\ub8e8\uae08\ub9cc\uc774 \uc544\ub2c8\ub77c \uacc1\uac00\uc9c0\uae4c\uc9c0 \uc815\ub9e5\uc73c\ub85c \ubcf8\ub2e4\uba74 \ud55c\ubd81\uc815\ub9e5 \ucd5c\uace0\ubd09\uc774\ub2e4. <BR><BR>\uc591\uac10 \uc788\ub294 \ub369\uce58\uc5d0 \uc815\uc0c1\ubd80\uac00 \ubd88\ub8e9\ud55c \uc7a5\uc5c4\ud55c \uc678\uad00\uc774\ub2e4. \ud2bc\uc2e4\ud55c \uc591 \uc5b4\uae68\uc5d0 \uc0c1\uace0\ub300\ub77c\ub3c4 \ub0b4\ub824\uc549\uc73c\uba74 \uc544\uc608 \uc131\uc0b0\uc758 \ubd84\uc704\uae30\ub97c \ud48d\uae34\ub2e4. \uad00\uccad\ub9ac \ud070\uace8 \uc785\uad6c\uc5d0\uc11c \ubcf4\uba74 \ub0a8\ub989\uc758 \uac00\uc9c0 \ud558\ub098 \uac00\uc2b4\uaed8\uc5d0\uc11c \uc19f\uc544\uc62c\ub77c \ub454\uc911\ud55c \uc721\uc0b0 \ub369\uc5b4\ub9ac\uc5d0 \uc5ed\ub3d9\uc131\uc744 \ubd80\uc5ec\ud55c\ub2e4. \uc815\uc0c1 \uad70\ubd80\ub300 \ub54c\ubb38\uc5d0 \uc911\ubd09\uae4c\uc9c0\ubc16\uc5d0 \ubabb \uac04\ub2e4\ub294 \uc0ac\uc2e4 \uc78a\uace0 \ud55c\ub2ec\uc74c\uc5d0 \uce58\uc624\ub974\uace0 \uc2f6\uc5b4\uc9c4\ub2e4. <BR><BR>\ub0a8\ucabd \uc624\ub9bc\uacc4\uace1\uc5d0\ub294 \uc2b9\uc6d0\ud3ed\ud3ec\uc640 \uc625\ub140\ud0d5\uc774 \uc788\ub2e4. \uc11c\ucabd \uc870\ubb34\ub77d\uace8\uc5d0\ub294 \uc30d\ub8e1\ud3ed\ud3ec\uc640 \ubcf5\ud638\ub4f1\ud3ed\ud3ec, \uadf8\ub9ac\uace0 \uc640\ud3ed\uc774 \ud558\ub098 \uc788\ub2e4. \uc774\ub9cc\ud558\uba74 \uacc4\uace1\ubbf8\ub3c4 \uac16\ucd98 \uc148. \ubaa9\ub3d9\uc5d0\uc11c \ud569\uccd0\uc9c4 \ub450 \ubb3c\uc740 \uac00\ud3c9\ucc9c\uc744 \ub9cc\ub4e0\ub2e4. \ud754\ud788 \uacbd\uae30 5\uc545\uc758 \ud558\ub098\ub85c \uce58\ub294\ub370 \uc0ac\uc2e4\uc740 \uc544\ub2c8\ub2e4. \uc545(\u5cb3)\uc790\ub294 \ub4e4\uc5b4\uac00\uc788\uc9c0\ub9cc \uc815\uc0c1\uc774 \ubc14\uc704\ub85c \ub418\uc9c0 \uc54a\uc558\ub2e4. \ud654\uc545 \uc790\ub9ac\uc5d0\ub294 \uc11c\uc6b8\uc758 \ubb34\uc545\uc774 \ub4e4\uc5b4\uac00\uc57c \ub9de\ub2e4.'), STRINGDECODE('\uacbd\uae30 \uc81c\uff11\uc758 \uace0\ubd09\uc73c\ub85c \uc560\uae30\ubd09\uc744 \uac70\uccd0 \uc218\ub355\uc0b0\uae4c\uc9c0 \uc57d 10\u339e\uc758 \ub2a5\uc120 \uacbd\uad00\uc774 \ub6f0\uc5b4\ub098\uba70 \uc2dc\uacc4\uac00 \uac70\uc758\uff11\ubc31\u339e\uc5d0 \ub2ec\ud558\ub294 \ub4f1 \uc870\ub9dd\uc774 \uc88b\uc740 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc9d1\ub2e4\ub9ac\uace8 \uc790\uc5f0\ud734\uc591\ub9bc\uc774 \uc788\uc73c\uba70, \uc815\uc0c1\uc5d0\uc11c \uc911\uc11c\ubd80\uc9c0\uc5ed \ub300\ubd80\ubd84\uc758 \uc0b0\uc744 \uc870\ub9dd\ud560 \uc218 \uc788\uc74c'), STRINGDECODE('\uac15\uc6d0\ub3c4\uc640 \uacbd\uae30\ub3c4\ub97c \uac00\ub974\ub294 \uacbd\uae30\ub3c4 \uc81c 1 \uace0\ubd09'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc0b0 \uc591\ucabd\uc5d0 \ubbfc\ubc15\uacfc \uc2dd\ub2f9\uc744 \uacb8\ud558\ub294 \uacf3\uc774 \ub9ce\ub2e4. <BR>\uc801\ubaa9\ub9ac\ucabd\uc740 \uad00\uccad\ub9ac\uc758 \ud654\uba85\uc27c\ud130(\u260e031-582-0515), \uc6a9\uc218\ub3d9 \uac1c\ub098\ub9ac\uc27c\ud130(\u260e031-582-0487), \uac00\ub9bc\uc57d\uc218\ubbfc\ubc15(\u260e031-582-0730), <BR>\ud654\uc545\ub9ac\ucabd\uc740 \ub300\ucd94\ub098\ubb34\ubbfc\ubc15(\u260e031-582-8826), \ub450\ub985\ub098\ubb34\ubbfc\ubc15(\u260e031-582-2395), \uc655\uc18c\ub098\ubb34\ubbfc\ubc15\u260e(031-582-5257).<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uac00\ud3c9\uad70\uccad \uc0b0\ub9bc\uacfc \uc0b0\ub9bc\ubcf4\ud638\ub2f4\ub2f9 \u260e031-580-2346 12840'), STRINGDECODE('\uae30\uc810\uc740 \uac00\ud3c9\uc774\ub2e4. \uac00\ud3c9\uc73c\ub85c\ub294 \uacbd\ucd98\uc120 \uc5f4\ucc28\uac00 1\uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \ub2e4\ub2cc\ub2e4. <BR>&gt;\uac00\ud3c9\u2192\ud654\uc545\ub9ac: \ud558\ub8e8 4\ud68c \uc6b4\ud589. \uac00\ud3c9\u2192\uc870\ubb34\ub77d\uace8 \uc785\uad6c \uc6a9\uc218\ub3d9: \ud558\ub8e8 5\ud68c \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(95, TIMESTAMP '2019-08-14 15:46:34.946', TIMESTAMP '2019-08-14 15:47:36.134', '48', STRINGDECODE('\uacbd\ub0a8 \ucc3d\ub155\uc5d0 \uc790\ub9ac\ud55c \ud654\uc655\uc0b0\uc740 \ud5d8\uc900\ud55c \uc0b0\uc774\ub2e4.<BR><BR>\ud2b9\ud788 \ubd04\uc774\uba74 \uc9c4\ub2ec\ub798\uc640 \ucca0\ucb49, \uc5ec\ub984\uc5d0\ub294 \ub179\uc74c\uacfc \uacc4\uace1\ubb3c, \uac00\uc744\uc5d0\ub294 \uc5b5\uc0c8, \uaca8\uc6b8\uc5d0\ub294 \uc124\uacbd\uc774 \uc720\uba85\ud558\ub2e4. \ubd04\ucca0 \uc9c4\ub2ec\ub798 \uacbd\uce58\uc640 \uac00\uc744\ucca0 \uc5b5\uc0c8\uac00 \uc720\uba85\ud55c\ub370, \ubd04\ucca0\uc774\uba74 \uc9c4\ub2ec\ub798 \uad70\ub77d\uc9c0\ub97c \uc774\ub8e8\uace0 \uc788\ub294 \ud654\uc655\uc0b0\uc131 \uc8fc\uc704\uc758 \ube44\ud0c8\uacfc \uad00\ub8e1\uc0b0\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub2a5\uc120 \uc77c\ub300\ub294 \ub9c8\uce58 \ubd84\ud64d\ubb3c\uac10\uc744 \uc3df\uc544\ubd80\uc740 \ub4ef\ud558\ub2e4. \ub9e4\ub144 4\uc6d4 \ud558\uc21c\uc5d0\uc11c 5\uc6d4 \ucd08\uc21c\uae4c\uc9c0 \uadf8\uc57c\ub9d0\ub85c \uc0b0 \uc804\uccb4\uac00 \ubd88\ud0c0\uc624\ub974\ub4ef \ub9cc\ubc1c\ud55c \uc9c4\ub2ec\ub798\uc758 \ubd89\uc740 \uae30\uc6b4\uc73c\ub85c \ub4a4\ub36e\uc778\ub2e4.<BR><BR>\ud654\uc655\uc0b0 \ucd5c\ub300\uc758 \uba85\ubb3c\uc774\ub77c\uba74 \uc815\uc0c1 \uc8fc\ubcc0\uc758 \ub113\uace0 \ud3c9\ud3c9\ud55c \uc5b5\uc0c8\ubc2d\uc778 ''\uc2ed\ub9ac\uc5b5\uc0c8\ubc2d''\uc774\ub2e4.\uadf8 \uc2ed\ub9ac \uc5b5\uc0c8\ubc2d\uc774 \ud3c9\uc9c0\uc5d0\uc11c \uae09\uacbd\uc0ac \ubcbd\uc73c\ub85c \ub69d \ub5a8\uc5b4\uc9c0\ub294 \uacbd\uacc4\uc120\uc778 \ub2a5\uc120\uc744 \ub530\ub77c \ud654\uc655\uc0b0 \uc131\ubcbd\uc774 \uc313\uc5ec \uc788\uc73c\uba70, \uadf8 \ubc14\uae65 \uacbd\uc0ac\uba74 \uac70\uc758 \ubaa8\ub450\uac00 \uc9c4\ub2ec\ub798\ubc2d\uc744 \uc774\ub8e8\uace0 \uc788\ub2e4. \ub9e4\ub144 10\uc6d4\uc774\uba74 \uc774\uacf3\uc5d0\uc11c \ud654\uc655\uc0b0 \uc5b5\uc0c8\uc81c\uac00 \uac1c\ucd5c\ub41c\ub2e4. \ud654\uc655\uc0b0\uc740 \uc120\uc0ac\uc2dc\ub300 \ud654\uc0b0\uc73c\ub85c \ucd94\uc815\ub418\uba70, 3\uac1c\uc758 \ubabb(\u9f8d\u6c60)\uac00 \uc788\uc73c\uba70, \ucc3d\ub155\uc870(\u66fa)\uc528\uac00 \ub4dd\uc131\ud558\uc600\ub2e4\ub294 \ub4dd\uc131\ube44\uac00 \ud654\uc655\uc0b0\uc131(\uc0ac\uc801\uc81c64\ud638)\uac00 \uc788\ub2e4. \ub610 \uc11c\ucabd \ub2a5\uc120\uc73c\ub85c\ub294 \uc0ac\uc801 65\ud638\uc778 \ubaa9\ub9c8\uc0b0\uc131\uc774 \ubcf4\uc778\ub2e4. \ub2a5\uc120\uc758 \ub3d9\ucabd 5km \uac70\ub9ac\uc5d0 \uad00\ub8e1\uc0ac\uac00 \uc788\ub2e4.'), STRINGDECODE('\u2460 \ucc3d\ub155\uc74d-\ub3c4\uc131\uc554-\ud658\uc7a5\uace0\uac1c-\uc815\uc0c1-\ubaa9\ub9c8\uc0b0\uc131-\ucc3d\ub155\uc5ec\uace0 (\ucd1d 3\uc2dc\uac04)<BR>\u2461 \ucc3d\ub155\uc74d-\ub3c4\uc131\uc554-\uc815\uc0c1-\ud658\uc7a5\uace0\uac1c-755.8\ubd09-\uc815\uc790-\ub9e4\ud45c\uc18c (\ucd1d 2\uc2dc\uac04 50\ubd84)'), '35.524203', '128.520195', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ucc3d\ub155\uad70 \ucc3d\ub155\uc74d \uc625\ucc9c\ub9ac \uc77c\ub300'), 2814140100, 758, STRINGDECODE('\ud654\uc655\uc0b0'), STRINGDECODE('&lt;\ubd04 \uc9c4\ub2ec\ub798 \uac00\uc744 \uc5b5\uc0c8 \ud5a5\uc5f0\uc758\uc0b0&gt;<BR>\ucc3d\ub155\uc74d\uc758 \uc9c4\uc0b0\uc73c\ub85c \uad00\ub8e1\uc0b0\uacfc \ub354\ubd88\uc5b4 \ud654\uc655\uc0b0 \uad70\ub9bd\uacf5\uc6d0\uc744 \uc774\ub8ec\ub2e4. \ud654\uc655\uc0b0 \ud558\uba74 \uc5b5\uc0c8\ub97c \ub5a0\uc62c\ub9b4 \ub9cc\ud07c \uc5b5\uc0c8\uc758 \ub300\uba85\uc0ac\uc774\uae30\ub3c4 \ud558\ub2e4. \uff16\ub9cc\uc5ec\ud3c9\uc758 \ub300\ud3c9\uc6d0\uc5d0 \uc2ed\ub9ac \uc5b5\uc0c8\ubc2d. \uc5b5\uc0c8\uc758 \ud06c\uae30\uac00 \uc0ac\ub78c \ud0a4 \ub192\uc774\ub97c \ud6e8\uc52c \ub118\ub294\ub2e4. \uc774\uac83\uc744 \ud55c \ubc14\ud034 \ub3c4\ub294 \ub370 \ud55c \uc2dc\uac04 \ub0a8\uc9d3 \uac78\ub9b0\ub2e4\uff0e<BR><BR>\uac00\uc744\uc5d0\ub294 \uc774\uacf3\uc5d0\uc11c \uad6d\ub0b4 \ucd5c\ub300\uc758 \uc57c\uac04\ucd95\uc81c\uac00 \ubc8c\uc5b4\uc9c4\ub2e4. \uc804\uad6d \uac01\uc9c0\uc5d0\uc11c 1\ub9cc\uc5ec\uba85\uc774 \uc6b4\uc9d1\ud55c \uac00\uc6b4\ub370 \uc0b0\uc2e0\uc81c\uc640 \uc758\ubcd1\ucd94\ubaa8\uc81c\ub97c \ube44\ub86f, \ud1b5\uc77c\uae30\uc6d0 \ud683\ubd88\ud589\uc9c4\uc744 \ud3bc\uccd0 \uc77c\ub300 \uc7a5\uad00\uc744 \uc774\ub8ec\ub2e4. \ud654\uc655\uc0b0\uc740 \uc9c4\ub2ec\ub798 \uba85\uc0b0\uc774\uae30\ub3c4 \ud558\ub2e4. \uad00\ub8e1\uc0b0 \uc815\uc0c1\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 6.5km \ub2a5\uc120 \ub4f1\uc0b0\ub85c\ub97c \ub530\ub77c \uc9c4\ub2ec\ub798\uac00 \uc774\uc5b4\uc9c4\ub2e4.'), STRINGDECODE('\uc5b5\uc0c8\ubc2d\uacfc \uc9c4\ub2ec\ub798 \uad70\ub77d \ub4f1 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70 \ud654\uc655\uc0b0\uc131, \ubaa9\ub9c8\uc0b0\uc131 \ub4f1\uc774 \uc788\uace0 \uad70\ub9bd\uacf5\uc6d0\uc778 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ud574\ub9c8\ub2e4 \uc815\uc6d4\ub300\ubcf4\ub984\uc774 \ub418\uba74 \uc815\uc0c1 \uc77c\ub300\uc758 \uc5b5\uc0c8\ud3c9\uc804\uc5d0\uc11c \ub2ec\ub9de\uc774 \ud589\uc0ac\uac00 \uc5f4\ub9bc. \uc815\uc0c1\uc5d0 \ud654\uc0b0\ud65c\ub3d9\uc73c\ub85c \uc0dd\uae34 \ubd84\ud654\uad6c \ubabb(\uc6a9\uc9c0)\uc774 3\uac1c \uc788\uc74c. \uc1a1\ud604\ub3d9 \uace0\ubd84\uad70 \ubc0f \uc11d\ubd88\uc88c\uc0c1, \ub300\uc6c5\uc804 \ub4f1 4\uc810\uc758 \ubcf4\ubb3c\uc774 \uc788\ub294 \uad00\ub8e1\uc0ac \ub4f1\uc774 \uc720\uba85'), STRINGDECODE('\ubd04\uc5d0\ub294 \ucca0\ucb49 \ud654\uc6d0, \uac00\uc744\uc5d0\ub294 \uc5b5\uc0c8\uaf43\ubc2d'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uad00\ub8e1\uc0b0\uc758 \ub4e4\uba38\ub9ac\uc778 \uc625\ucc9c\ub9ac\uc5d0\ub294 \uc219\ubc15\uc2dc\uc124\uc774 \uc5c6\uace0 \ud654\uc655\uc0b0\uc758 \ub4e4\uba38\ub9ac\uc778 \ucc3d\ub155\uc74d\ub0b4\uc5d0\ub294 \uc5ec\uad00\uc774 \ub9ce\ub2e4. <BR>\ubaa9\ud654\uc7a5\uc5ec\uad00(\u260e055-532-0566)<BR>\uc2e0\uc77c\uc7a5(\u260e055-532-1255)<BR>\ud654\uc655\uc0b0\uc7a5(\u260e055-532-1069)<BR>\ube44\uc0ac\ubc8c\uc5ec\uad00(\u260e055-532-0609)<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\uc625\u