INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(96, TIMESTAMP '2019-08-14 15:46:34.946', TIMESTAMP '2019-08-14 15:47:36.721', '48', STRINGDECODE('\ud569\ucc9c\ud638 \ud478\ub978\ubb3c\uc18d\uc5d0 \uc0b0\uc790\ub77d\uc744 \ub2f4\uadf8\uace0 \ud558\ubd09, \uc911\ubd09, \uc0c1\ubd09 \ub4f1 \uc138 \ubd09\uc6b0\ub9ac\ub85c \uc815\uc0c1\uc744 \uc774\ub8e8\uc5b4 \uc218\uc911\ub9e4\ub85c \ubd88\ub9ac\ub294 \ud669\ub9e4\uc0b0(1,108m)\uc740 \ud569\ucc9c\uc74d\uc5d0\uc11c \uc11c\ub0a8\ucabd\uc73c\ub85c 20km \uc9c0\uc810\uc5d0 \uc704\uce58\ud574 \uc788\ub2e4.<BR><BR>\uc0b0\uc544\ub798\uc758 \ud669\ub9e4 \ud3c9\uc804\uc740 \ubaa9\uc7a5 \uc9c0\ub300\uc640 \uace0\uc0b0 \ucca0\ucb49 \uc790\uc0dd\uc9c0\uac00 \uc788\uc73c\uba70, \ud1b5\uc77c \uc2e0\ub77c\uc2dc\ub300\uc758 \uace0\ucc30\uc778 \uc5fc\uc554\uc0ac\uc9c0(\uc0ac\uc801 131\ud638)\uac00 \uc788\ub2e4. \ud669\ub9e4\uc0b0\uc740 \uad70\ub9ac \uacf5\uc6d0\uc73c\ub85c 1983\ub144 \uc9c0\uc815\ub418\uc5b4, \uac00\ud68c\uba74 \ub454\ub0b4\ub9ac\uc5d0\uc11c \uc601\uc554\uc0ac\uc9c0\uc5d0 \uc774\ub974\ub294 \ub4f1\uc0b0\ub85c\ub97c \uac1c\uc124\ud558\uc600\ub2e4. \ub300\ubcd1\uba74 \ud558\uae08\ub9ac \ud558\uae08\ucc9c\uc5d0\ub294 \uc57c\uc601\uc7a5\uc744 \uac1c\uc124\ud558\uc5ec \ud569\ucc9c\ud638\uc640 \uc774 \uc9c0\uc5ed\uc744 \ucc3e\ub294 \uad00\uad11\uac1d\uc758 \ud3b8\uc758\ub97c \ub3c4\ubaa8\ud558\uace0 \uc788\ub2e4.'), STRINGDECODE('\u2460 \uc7a5\ubc15\ub9ac-\ub5a1\uac08\uc7ac\ub2a5\uc120 \uc0bc\uac70\ub9ac-\ub108\ubc31\uc774-\uc815\uc0c1-\ud669\ub9e4\uc0b0\uc81c\ub2e8-\ubca0\ud2c0\ubd09-\ucca0\ucb49\uc81c\ub2e8-\ubaa9\uc7a5-\ub355\ub9cc\ub9ac (\ucd1d 4\uc2dc\uac04 25\ubd84)<BR>\u2461 \ud558\uae08\uc57c\uc601\uc7a5-\ub5a1\uac08\uc7ac-\ub108\ubc31\uc774-\uc815\uc0c1-\ud669\ub9e4\uc0b0\uc81c\ub2e8-\ubca0\ud2c0\ubd09-\ucc9c\ud669\uc7ac-\uac10\uc554\uc0b0-\uc131\uc9c0\ub3d9\ucc9c-\uc2e0\ucd0c\ub9ac (\ucd1d 4\uc2dc\uac04 50\ubd84)<BR>\u2462 \ub355\ub9cc\ub9ac-\uc0bc\ubd09-\ud558\ubd09-\uc911\ubd09-\ud669\ub9e4\ubd09-\uc11c\ubd81\ub989 \uc0bc\uac70\ub9ac-\uc601\ud654\uc8fc\uc81c\uacf5\uc6d0 (\ucd1d4\uc2dc\uac04)'), '35.527267', '128.009367', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ud569\ucc9c\uad70 \ub300\ubcd1\uba74, \uac00\ud68c\uba74, \uc0b0\uccad\uad70 \ucc28\ud669\uba74.'), 2820020300, 1113, STRINGDECODE('\ud669\ub9e4\uc0b0'), STRINGDECODE('&lt;\uc601\ud654 \ub458 \ucc0d\uc5c8\ub358 \uc5b5\uc0c8\u00b7\ucca0\ucb49\u00b7\ud638\ubc18\uba85\uc0b0&gt;<BR>\ud569\ucc9c\ud638\ubc18\uc744 \uc9c0\ub098\ub2e4 \ubcf4\uba74 \ub0a8\ucabd \ud558\ub298\uae08\uc5d0 \uad49\uc7a5\ud55c \uc0b0 \ud558\ub098\uac00 \uc19f\uc544\uc788\ub2e4. \uc774 \uadfc\ucc98\uc5d0\ub3c4 \uc800\ub7f0 \ub192\uc740 \uc0b0\uc774 \uc788\uc5c8\ub098 \uc2f6\uc744 \ub9cc\ud07c \uc900\uac78\ud55c \uc554\ubd09\uc774 \ud638\uc22b\ubb3c\uc5d0 \uc5b4\ub9b0\ub2e4. \uc9c0\ub3c4\ub97c \ud3b4\ubcf4\uba74\u2026 \ud669\ub9e4\uc0b0\uc774\ub2e4. \uc790\ub8cc\ub97c \ucc3e\uc544\ubcf4\uba74\u2026 \ud558\ub298\uae08\ub9cc \uc774\uc05c \uac8c \uc544\ub2c8\uace0 \uc5b5\uc0c8\uc640 \ucca0\ucb49\ub3c4 \uc720\uba85\ud55c \uad70\ub9bd\uacf5\uc6d0\uc774\ub2e4. <BR><BR>\uc5bc\ub9c8\ub098 \ub300\ub2e8\ud55c \uc0b0\uc774\uba74 \uc601\ud654\ub97c \ub450 \ud3b8\uc774\ub098 \ucc0d\uc5c8\ub2e4. \uacf5\uc804\uc758 \ud788\ud2b8\ub97c \uae30\ub85d\ud588\ub358 &lt;\ud0dc\uadf9\uae30 \ud718\ub0a0\ub9ac\uba70&gt;\uc640 &lt;\ub2e8\uc801\ube44\uc5f0\uc218&gt;\ub2e4. \uc804\uc790\ub294 \ub099\ub3d9\uac15 \ubc29\uc5b4\uc120\uacfc \ub450\ubc00\ub9ac \uc804\ud22c\uc7a5\uba74\uc774\uc5c8\uc73c\ub2c8 \uac00\uc7a5 \ud55c\uad6d\uc801\uc778 \uc9c0\ud615\uc744 \ud488\uace0\uc788\ub2e4\ub294 \uc598\uae30\uace0 \ud6c4\uc790\ub294 \ud658\ud0c0\uc9c0 \ubb34\ud611\uc601\ud654\ub2c8 \ud48d\uad11\uc774 \ud658\uc0c1\uc801\uc774\uc5b4\uc11c\uc600\uc73c\ub9ac\ub77c. <BR><BR>\uc8fc\ub989 \uc704\uc5d0\ub294 \uc2e0\ubd88\ud3c9\uc6d0\ubcf4\ub2e4 \ub354 \ub108\ub978 \ud669\ub9e4\ud3c9\uc804\uc774 \uc788\ub2e4. \uc5f4\ub450\ubaa9\uc7a5\uc774 \ub458\ub9cc \ub0a8\uc544 \ub300\ubd80\ubd84\uc774 \ud669\ubb34\uc9c0\ucc98\ub7fc \ubc84\ub824\uc838\uc788\uc9c0\ub9cc \uc5d8\ub9ac\uc5b4\ud2b8\uc758 ''\ud669\ubb34\uc9c0''\ub97c \uc88b\uc544\ud558\ub294 \uc774\ub77c\uba74 \uc624\ud788\ub824 \uc774\ub7f0 \ud48d\uacbd\uc774 \ub354 \ub9c8\uc74c\uc5d0 \ub4e4\ub9ac\ub77c. \"\"\uc794\uc778\ud55c \uacc4\uc808\"\"\uc774 \uc624\uba74 \uac70\uae30\uc5d0 \ubb34\ub354\uae30\ubb34\ub354\uae30 \ucca0\ucb49\uaf43 \ud540\ub2e4. <BR>\ub9e4\ud654\uac00 \uc788\uc744 \ub9ac \uc5c6\ub294 \uc704\ub3c4\uc640 \uace0\ub3c4. \uc6d0\ub798 \uc774\ub984\uc740 ''\uac70\uce5c \ubafc(\u8352\u5c71)''\uc600\uc744 \uac83\uc774\ub2e4. \ucd08\ubaa9\uc5d0 \ubb3c \uc548 \uc624\ub978 \uc774\ub978 \ubd04\ub0a0\uc5d4, \ubc11\uc5d0\uc11c \ubcf4\ub098 \uc704\ub85c \uc62c\ub77c\uac00 \ubcf4\ub098 \uac70\uce5c \uc0b0\uc774\ub2e4.'), STRINGDECODE('\ud654\uac15\uc554 \uae30\uc554\uad34\uc11d\uacfc \uc18c\ub098\ubb34, \ucca0\ucb49, \ud65c\uc5fd\uc218\ub9bc\uc774 \uc5b4\uc6b0\ub7ec\uc838 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b4 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ud569\ucc9c\ud638 \ud478\ub978\ubb3c\uc5d0 \ud558\ubd09, \uc911\ubd09, \uc0c1\ubd09\uc758 \uc0b0 \uadf8\ub9bc\uc790\uac00 \uc7a0\uae30\uba74 \uc138\uc1a1\uc774 \ub9e4\ud654\uaf43\uc774 \ubb3c\uc5d0 \uc7a0\uae34 \uac83 \uac19\ub2e4\uace0 \ud558\uc5ec \uc218\uc911\ub9e4\ub77c\ub294 \ubcc4\uce6d\uc73c\ub85c\ub3c4 \ubd88\ub9bc. \uc0b0 \uc544\ub798\uc758 \ud669\ub9e4\ud3c9\uc804\uc5d0\ub294 \ubaa9\uc7a5\uc9c0\ub300\uc640 \uace0\uc0b0 \ucca0\ucb49 \uc790\uc0dd\uc9c0\uac00 \uc788\uc73c\uba70, \ud1b5\uc77c\uc2e0\ub77c\uc2dc\ub300\uc758 \uace0\ucc30\uc778 \uc5fc\uc554\uc0ac\uc9c0(\uc0ac\uc801131\ud638)\uac00 \uc720\uba85'), STRINGDECODE('\ubaa9\uc7a5\uae38 \ub530\ub77c \uac00\ub294 \uc138 \ubd09\uc6b0\ub9ac'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uad50\ud1b5\ud3b8\uc5d0\uc11c \ubcf4\ub4ef \uc6cc\ub099 \uc624\uc9c0\ub77c \uc0b0\uae30\uc2ad\uc758 \uc2dd\ub2f9\uc5d0\uc11c \ubbfc\ubc15\ud558\ub294 \uc218\ubc16\uc5d0 \uc5c6\ub2e4. <BR>\uac00\ud68c\uba74 \ucabd\uc740 \ud669\ub9e4\uc0b0\uc2dd\ub2f9 \u260e931-1367/011-853-4342, \ubaa8\uc0b0\uc7ac\uc2dd\ub2f9 \u260e933-1101/016-9616-1101<BR>\ucc28\ud669\uba74 \ucabd\uc740 \ud669\ub9e4\uc0b0\uc7a5 \u260e973-8817/011-879-3304, \ud1a0\uad81\uc0b0\uc7a5 \u260e016-598-1262<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud669\ub9e4\uc0b0\uad70\ub9bd\uacf5\uc6d0 \uad00\ub9ac\uc0ac\ubb34\uc18c \u260e055-930-4151'), STRINGDECODE('\uac00\ud68c\uba74\uc774\ub098 \ub300\ubcd1\uba74\uc740 \ud569\ucc9c, \ucc28\ud669\uba74\uc740 \uc0b0\uccad\uc774 \uae30\uc810\uc774\ub2e4. <BR>\uac00\ud68c\uba74\uc740 \ud569\ucc9c\uc5d0\uc11c \ud558\ub8e8 14\ud68c \uc788\ub294 \uc9c4\uc8fc\ud589 \uc9c1\ud589\uc744 \ud0c0\uace0 \uc0bc\uac00\uc5d0\uc11c \ub0b4\ub824 \ud0dd\uc2dc\ub97c \uc774\uc6a9\ud55c\ub2e4. \ub300\ubcd1\uba74\uc740 \uc644\ud589\ubc84\uc2a4\uac00 50\ubd84 \uac04\uaca9\uc73c\ub85c \uc788\ub2e4. \ucc28\ud669\uba74\uc740 \uc0b0\uccad\uc5d0\uc11c \uad70\ub0b4\ubc84\uc2a4\uac00 15\ud68c \uc788\ub294\ub370 \uc774 \uc911 \uc7a5\ubc15\ub9ac\ud589\uc740 9\ubc88, \uc2e0\ucd0c\ud589\uc740 4\ubc88\uc774\ub2e4.'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(97, TIMESTAMP '2019-08-14 15:46:34.947', TIMESTAMP '2019-08-14 15:47:37.363', '48', STRINGDECODE('\ud669\uc11d\uc0b0\uc774 \uc704\uce58\ud574 \uc788\ub294 \ud568\uc591\uc740 ''\uc369\uc740 \uac08\uce58\uac00 \ub2e4 \ubaa8\uc774\ub294 \uacf3'' \uc774\ub77c \ud560\ub9cc\ud07c \uc0c1\ub300\uc801\uc73c\ub85c \uad50\ud1b5\uc774 \ubd88\ud3b8\ud55c \uacf3\uc73c\ub85c \uc0ac\ub78c\uc758 \ubc1c\uae38\uc744 \ub35c \ud0c0\ub294 \uc624\uc9c0\uc774\ub2e4. \uadf8\ub798\uc11c \uc774 \uc0b0\uc740 \uc190\ub54c \ubb3b\uc9c0 \uc54a\uc740 \uc790\uc5f0\ubbf8\ub97c \uadf8\ub300\ub85c \ubcf4\uc874\ud558\uace0 \uc788\ub294 \uacf3\uc774\ub2e4. <BR><BR>\ud669\uc11d\uc0b0 \uc815\uc0c1\ubd80\ub85c \uac00\ub294 \uae38\uc740 \uc5b5\uc0c8\ub2a5\uc120\uc774 \uc7a5\uad00\uc744 \uc774\ub8e8\uace0 \uc815\uc0c1\ubd80\uc5d0 \uc774\ub974\uba74 \uc30d\ub9bd\ud55c \uc554\ubd09\uc758 \uba4b\uc2a4\ub7ec\uc6c0\uc774 \ub4f1\uc0b0\uc758 \ud53c\ub85c\ub97c \uc5b4\ub8e8\ub9cc\uc838\uc900\ub2e4. \uc778\uc811\ud55c \uac70\ub9dd\uc0b0 \uc885\uc8fc\uae38\ub3c4 \uacbd\uad00\uc774 \uc77c\ud488\uc778\ub370 \uadf8 \uc0ac\uc774\uc0ac\uc774\uc5d0 \uc704\ud5d8\ud55c \uc554\ub989\uae38\uc774 \uc788\uc73c\ubbc0\ub85c 20m\uc815\ub3c4\uc758 \ubcf4\uc870\uc790\uc77c\uc744 \uc900\ube44\ud558\ub294 \uac83\uc774 \uc548\uc804\ud558\ub2e4.'), STRINGDECODE('\u2460 \uc6b0\uc804\ub9c8\uc744-\uc2dc\uad6c\ubaa9\uae38-\ud669\uc11d\uc0b0-\ub9dd\uc6d4\ub300-\uc720\ub3d9\ub9c8\uc744 (\ucd1d 4\uc2dc\uac04 )<BR>\u2461 \uc0bc\uac70\ub9ac-\ud0c1\uace0\uac1c-\uc0b0\ub0b4\uace8-\ud669\uc11d\uc0b0-\uc11c\ud558\uad50 (\ucd1d4\uc2dc\uac04 30\ubd84)<BR>\u2462 \uc6a9\ucd94\uad50-\uc6a9\ucd94\ud3ed\ud3ec-\uc6a9\ucd94\uc0ac-\uc815\uc790\ubc8c \uc785\uad6c-\ub2a5\uc120-\uc8fc\ub2a5\uc120-\uac70\ub9dd\uc0b0 \uc815\uc0c1 -\ud669\uc11d\uc0b0-\uc6b0\uc804-\ubd09\uc804\uac70\uc5f0\uc815 (\ucd1d 6\uc2dc\uac04 40\ubd84)<BR><BR>'), '35.520563', '127.725158', STRINGDECODE('\uacbd\uc0c1\ub0a8\ub3c4 \ud568\uc591\uad70'), 2819072400, 1193, STRINGDECODE('\ud669\uc11d\uc0b0'), STRINGDECODE('&lt;\uc554\ub989\uacfc \uc5b5\uc0c8\uac00 \uc870\ud654\ub85c\uc6b4 \uc0b0&gt;<BR>\ud669\uc11d\uc0b0\uc740 \uac70\ucc3d \ub0a8\ub158\uc5d0 \uc19f\uc740 \ubc94\uc0c1\uce58 \uc54a\uc740 \ubc14\uc704\uc0b0\uc774\ub2e4. \ubc31\ub450\ub300\uac04 \uc904\uae30\uc5d0\uc11c \ubed7\uc5b4 \ub0b4\ub9b0 \ub124 \uac1c\uc758 \uc0b0 \uae30\ubc31\u00b7\uae08\uc6d0\u00b7\uac70\ub9dd\u00b7\ud669\uc11d \uac00\uc6b4\ub370 \uac00\uc7a5 \ub05d \uc790\ub77d\uc5d0 \uc19f\uad6c\uce5c \uc774 \ubd09\uc6b0\ub9ac\ub294 \ub355\uc720\uc0b0\uc5d0\uc11c\ub3c4 \uc120\uba85\ud558\uac8c \ubcf4\uc778\ub2e4\uff0e\uac00\uc744\ucca0\uc5d0\ub294 \uac70\ub9dd\uc5d0\uc11c \ud669\uc11d\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub2a5\uc120\uc5d0 \uad11\ud65c\ud55c \uc5b5\uc0c8\ubc2d\uc774 \uc7a5\uad00\uc774\ub2e4.<BR><BR>\uae08\uc6d0, \uae30\ubc31\uc0b0\uacfc\uc758 \uc0ac\uc774\uc5d0\ub294 \uc720\uba85\ud55c \uc6a9\ucd94\uacc4\uace1\uc774 \uc788\ub2e4. \uff16\u00b7\uff12\uff15 \ub54c \ube68\uce58\uc0b0 \uc5ec\uc7a5\uad70 \uc815\uc21c\ub355\uc774 \ud65c\uc57d\ud588\ub358 \uacf3\uc774 \ubc14\ub85c \uc774\uc6c3\ud558\uace0 \uc788\ub294 \uac70\ub9dd\uc0b0\uc774\ub2e4. \ud669\uc11d\uc0b0\uc131\uc740 \ud568\uc591 \uc548\uc758 \uc0ac\ub78c\ub4e4\uc758 \uc9c0\uc870\uc640 \uc808\uac1c\ub97c \uc0c1\uc9d5\ud558\ub294 \uc911\uc694\ud55c \uc720\uc801\uc9c0\uc774\ub2e4. \uc815\uc720\uc7ac\ub780 \ub2f9\uc2dc \uc65c\uad70\uc5d0\uac8c \ub9c8\uc9c0\ub9c9\uae4c\uc9c0 \ud56d\uac70\ud558\ub358 \uc774\ub4e4\uc774 \uc131\uc774 \ubb34\ub108\uc9c0\uc790 \uc8fd\uc74c\uc744 \ub2f9\ud558\uace0 \ubd80\ub140\uc790\ub4e4\uc740 \ucc9c\uae38 \uc808\ubcbd\uc5d0\uc11c \ubab8\uc744 \ub0a0\ub824 \uc9c0\uae08\uaecf \ud669\uc11d\uc0b0 \ubd81\ucabd \ubc14\uc704 \ubcbc\ub791\uc740 \ud54f\ube5b\uc73c\ub85c \ubb3c\ub4e4\uc5b4 \uc788\ub2e4\uff0e<BR><BR>\uc548\uc758\uc5d0\uc11c\ub294 \ud654\ub9bc\ub3d9 \uc5ec\ub35f \uac1c\uc758 \ubabb\uacfc \uc5ec\ub35f \uac1c\uc758 \uc815\uc790\uac00 \uc720\uba85\ud558\ub2e4\uff0e\ud2b9\ud788 \ub18d\uc6d4\uc815, \ub3d9\ud638\uc815, \uac70\uc5f0\uc815, \uad70\uc790\uc815 \ub4f1\uc740 \uc804\ub77c\ub3c4 \ubabb\uc9c0 \uc54a\uc740 \uc815\uc790 \ubb38\ud654\ub97c \ub300\ud45c\ud55c\ub2e4\uff0e'), STRINGDECODE('\uac70\ub9dd\uc5d0\uc11c \ud669\uc11d\uc73c\ub85c \uc774\uc5b4\uc9c0\ub294 \ub2a5\uc120\uc5d0 \uc788\ub294 \uad11\ud65c\ud55c \uc5b5\uc0c8\ubc2d \ub4f1 \uacbd\uad00\uc774 \uc544\ub984\ub2f5\uace0 \ud669\uc11d\uc0b0\uc131 \ub4f1 \uc5ed\uc0ac\uc801 \uc720\uc801\uc774 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \uc815\uc720\uc7ac\ub780 \ub2f9\uc2dc \uc65c\uad70\uc5d0\uac8c \ub9c8\uc9c0\ub9c9\uae4c\uc9c0 \ud56d\uac70\ud558\ub358 \uc0ac\ub78c\ub4e4\uc774 \uc131\uc774 \ubb34\ub108\uc9c0\uc790 \uc8fd\uc74c\uc744 \ub2f9\ud558\uace0 \ubd80\ub140\uc790\ub4e4\uc740 \ucc9c\uae38 \uc808\ubcbd\uc5d0\uc11c \ubab8\uc744 \ub0a0\ub824 \uc9c0\uae08\uaecf \ud669\uc11d\uc0b0 \ubd81\ucabd \ubc14\uc704 \ubcbc\ub791\uc774 \ud54f\ube5b\uc774\ub77c\ub294 \uc804\uc124\uc774 \uc788\ub294 \ud669\uc11d\uc0b0\uc131\uc774 \uc788\uc74c.'), STRINGDECODE('\uc369\uc740 \uac08\uce58 \ub2e4 \ubaa8\uc774\ub294 \uc624\uc9c0\uc758 \uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc6a9\ucd94\uc0ac\uc640 \uc6a9\ucd94\ud3ed\ud3ec \uadfc\ucc98\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\ub2e4. <BR>\uc6a9\ucd94\uc790\uc5f0\ud734\uc591\ub9bc \u260e055-963-9611<BR>\ucd08\ub85d\uc0b0\uc7a5 \u260e055-963-5009<BR>\uc6a9\ucd94\ud39c\uc158 \u260e055-964-0708<BR>\uc7a5\uc218\uc0b0\uc7a5 \u260e055-963-8708<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ud568\uc591\uad70\uccad \uad00\uad11\uacfc \u260e055-960-0544'), STRINGDECODE('\uae30\uc810\uc740 \ud568\uc591\uc774\ub2e4. <BR>\uac70\ucc3d\uc5d0\uc11c\ub3c4 \uc548\uc758\ud589 \ubc84\uc2a4\uac00 30\ubd84 \uac04\uaca9, \uc6a9\ucd94\uc0ac\ud589 \ubc84\uc2a4\uac00 1\uc2dc\uac04 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud55c\ub2e4.<BR>- \ud568\uc591\u2192\uc548\uc758 : \ud558\ub8e8 12\ud68c \uc6b4\ud589 &gt;\ud568\uc591\u2192\uc6a9\ucd94\uc0ac : 1\uc2dc\uac04 \uac04\uaca9 \uc6b4\ud589'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(98, TIMESTAMP '2019-08-14 15:46:34.947', TIMESTAMP '2019-08-14 15:47:37.955', '47', STRINGDECODE('\ud669\uc545\uc0b0\uc740 \uae40\ucc9c\uc2dc\uc5d0\uc11c \uc11c\ucabd\uc73c\ub85c 12km \ub5a8\uc5b4\uc9c4 \uacf3\uc5d0 \uc704\uce58\ud55c \uc0b0\uc774\ub2e4. <BR><BR>\uc61b\ubd80\ud130 \ud559\uc774 \ub9ce\uc774 \ucc3e\uc544\uc640 \ud669\ud559\uc0b0\uc774\ub77c \ubd88\ub9ac\uc5c8\uc73c\ub098, \uc9c1\uc9c0\uc0ac\uc758 \ud604\ud310 \ubc0f \ud0dd\ub9ac\uc9c0\uc5d0 \ud669\uc545\uc0b0\uc73c\ub85c \ub418\uc5b4\uc788\ub2e4. \uc6b8\ucc3d\ud55c \uc18c\ub098\ubb34 \uc232\uacfc \uae4a\uc740 \uacc4\uace1\uc5d0 \uc625\uac19\uc774 \ub9d1\uc740 \ubb3c, \uac00\uc744\uc758 \ub2e8\ud48d\uacfc \uaca8\uc6b8\uc758 \uc124\ud654\uac00 \uc544\ub984\ub2f5\ub2e4. \uc77c\ub300\ub294 \uad6d\ubbfc\uad00\uad11\uc9c0\ub85c \uc9c0\uc815\ub418\uc5b4 \ub354\uc6b1 \uac1c\ubc1c\ub418\uace0 \uc788\ub2e4. <BR><BR>\uc804\uccb4\uc801\uc778 \uc0b0\uc138\ub294 \ud2b9\uc9d5\uc5c6\uc774 \uc644\ub9cc\ud55c \ud3b8\uc774\ub098 \uc628 \uc0b0\uc5d0 \uc218\ub9bc\uc774 \uc6b8\ucc3d\ud558\uace0 \uc0b0 \ub3d9\ucabd\uc73c\ub85c \ud758\ub7ec\ub0b4\ub9ac\ub294 \uacc4\uace1\uc740 \uacf3\uacf3\uc5d0 \ud3ed\ud3ec\uc640 \uc18c\ub97c \uc774\ub904 \uadf8\uc73d\ud55c \uacc4\uace1\ubbf8\ub97c \uc774\ub8e8\uace0 \uc788\ub2e4. \ud2b9\ud788 \uc9c1\uc9c0\uc0ac \uc11c\ucabd200m \uc9c0\uc810\uc5d0 \uc788\ub294 \ucc9c\ub8e1\ub300\uc5d0\uc11c\ubd80\ud130 \ud3bc\uccd0\uc9c0\ub294 \ub2a5\uc5ec\uacc4\uace1\uc740 \uc774\uc0b0\uc758 \ub300\ud45c\uc801\uc778 \uacc4\uace1\uc73c\ub85c \ubd04\ucca0\uc5d0\ub294 \uc9c4\ub2ec\ub798, \ubc9a\uaf43, \uc0b0\ubaa9\ub828\uc774 \ubcfc\ub9cc\ud558\uace0 \uac00\uc744\ucca0 \ub2e8\ud48d \ub610\ud55c \uc808\uacbd\uc744 \uc774\ub8ec\ub2e4.<BR><BR>\ud669\uc545\uc0b0 \uc815\uc0c1\uc5d0\uc11c \uc9c1\uc9c0\uc0ac\ub97c \ub0b4\ub824\ub2e4 \ubcf4\uba74 \ubb34\uc218\ud55c \uc9c0\ub2a5\uc120\uc774 \ud558\ub098\uc529 \uacc4\uace1\uc548\uc5d0\uc11c \uc18c\uba78\ub41c \ub4a4 \ub9c8\uc9c0\ub9c9 \ub0a8\uc740 \ub450\uac00\ub2e5 \ub2a5\uc120\uc774 \uc88c\uc6b0\uc5d0\uc11c \uc9c1\uc9c0\uc0ac\ub97c \ud06c\uac8c \uc2f8\uc548\uc73c\uba74\uc11c \uc0b0\uacfc \uc808\uc758 \ud654\ud569\uc740 \uc644\uc131\ub41c\ub2e4. 1111m\uc5d0 \uc774\ub974\ub294 \ud669\uc545\uc0b0\uc758 \ub192\uc740 \ubd09\uc6b0\ub9ac\uc640 \uadf8 \uc544\ub798 \ud559\uc758 \ub0a0\uac1c\ucc98\ub7fc \ud3bc\uccd0\uc9c0\ub294 \uacc4\uace1\uc774 \uc881\uc740 \uc218\ub85c\ub97c \ud1b5\ud558\uc5ec \ub3d9\uc73c\ub85c \ube60\uc838\ub098\uac00\uace0 \uadf8 \uae38\ubaa9\uc5d0 \uc9c1\uc9c0\uc0ac\ub294 \uc790\ub9ac\uc7a1\uace0 \uc788\ub294 \uac83\uc774\ub2e4.<BR><BR>\uc9c1\uc9c0\uc0ac\ub294 \uc5b4\ub5bb\uac8c \ubcf4\uba74 \ud669\uc545\uc0b0 \uc815\uae30\uac00 \ub9fa\uc740 \uc804\ud600 \ub2e4\ub978 \uc885\ub958\uc758 \uaf43\ubd09\uc624\ub9ac \uac19\uc544 \ubcf4\uc778\ub2e4. \uc9c1\uc9c0\uc0ac\ub294 \uc0c8\ub85c \uc138\uc6b4 \ub300\ud615 \uc77c\uc8fc\ubb38\uc5d0 \ub3d9\uad6d\uc81c\uc77c\uac00\ub78c\uc774\ub77c\ub294 \ucee4\ub2e4\ub780 \ud3b8\uc561\uc744 \ubd99\uc5ec\ub193\uc740 \ub300\ub85c \uad6d\uc911\uc758 \uc0ac\ucc30 \uac00\uc6b4\ub370\uc11c\ub3c4 \uc5f4\uc190\uac00\ub77d\uc5d0 \ub4e4\ub9cc\ud55c \uac70\ucc30\uc774\ub2e4.'), STRINGDECODE('\u2460 \uc9c1\uc9c0\uc0ac-\uc6b4\uc218\uc554-\ubc31\uc6b4\ubd09-\ube44\ub85c\ubd09-\ud615\uc81c\ubd09-\uc2e0\uc120\ubd09-\ub9dd\ubd09 \uc9c1\uc804 \uc548\ubd80-\ub2a5\uc5ec\uacc4\uace1-\uc9c1\uc9c0\uc0ac (\ucd1d 4\uc2dc\uac04 25\ubd84)<BR>\u2461 \ubb18\ub0b4-\uc6b4\uc218\ubd09-\ubc31\uc6b4\ubd09-\ube44\ub85c\ubd09-\ud615\uc81c\ubd09-\uc2e0\uc120\ubd09-\ub9dd\ubd09 \uc9c1\uc804 \uc548\ubd80-\ub2a5\uc5ec\uacc4\uace1-\uc9c1\uc9c0\uc0ac (\ucd1d 4\uc2dc\uac04 30\ubd84)'), '36.100539', '128.007596', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \uae40\ucc9c\uc2dc \ub300\ud56d\uba74 \uc6b4\uc218\ub9ac'), 2706133700, 1111, STRINGDECODE('\ud669\uc545\uc0b0'), STRINGDECODE('&lt;\ub3d9\uad6d\uc81c\uc77c\uac00\ub78c \uc9c1\uc9c0\uc0ac \ud488\uc740 \ub300\uac04\uc758 \uc904\uae30&gt;<BR>\ud669\uc545\uc0b0\uc740 \ube44\ub85c\ubd09(1111.4m)\uc744 \uc911\uc2ec\uc73c\ub85c \ubc31\uc6b4\ubd09(770m), \uc2e0\uc120\ubd09(944m), \uc6b4\uc218\ubd09(740m)\uc774 \uce58\uc19f\uc544 \uc9c1\uc9c0\uc0ac\ub97c \ud3ec\uadfc\ud788 \uac10\uc2f8\uace0 \uc788\ub2e4. \uc0b0\uc138\ub294 \ud3c9\ud3c9\ud558\uace0 \uc644\ub9cc\ud558\uc5ec \ube44\ub2e8\uac19\uc774 \ubd80\ub4dc\ub7ec\uc6b4 \ud3b8\uc774\uba70, \ucd08\uc785 \uc77c\ub300 \uacc4\uace1 \uc591\ucabd\uc73c\ub85c \ub298\uc5b4\uc120 \ub178\uc1a1\uacfc \ucc38\ub098\ubb34\uac00 \ud558\ub298\uc744 \uac00\ub824 \uc7a5\uad00\uc744 \uc774\ub8ec\ub2e4. \uc774 \uc0b0\uc740 \uae40\ucc9c\uc2dc\uc5d0\uc11c \uc11c\ucabd\uc73c\ub85c 12km \ub5a8\uc5b4\uc9c4 \ubc31\ub450\ub300\uac04 \uac00\uc6b4\ub370 \uc704\uce58\ud55c\ub2e4. \uc608\ubd80\ud130 \ud559\uc774 \ub9ce\uc774 \ucc3e\uc544\uc640 \ud669\ud559\uc0b0\uc774\ub77c\uace0\ub3c4 \ubd88\ub800\uc73c\ub098, \uc9c1\uc9c0\uc0ac \ud604\ud310\uacfc \ud0dd\ub9ac\uc9c0\uc5d0\ub294 \ud669\uc545\uc0b0\uc73c\ub85c \ud45c\uae30\ub418\uc5b4 \uc788\ub2e4. <BR><BR>\uc6b8\ucc3d\ud55c \uc232\uacfc \uae4a\uc740 \uacc4\uace1\uc5d0 \uc625\ucc98\ub7fc \ub9d1\uc740 \ubb3c, \uac00\uc744 \ub2e8\ud48d\uacfc \uaca8\uc6b8 \uc124\ud654\uac00 \uc544\ub984\ub2f5\ub2e4. \uc815\uc0c1\uc5d0 \uc11c\uba74 \uc11c\ucabd\uc73c\ub85c \ubbfc\uc8fc\uc9c0\uc0b0, \ub0a8\ucabd\uc73c\ub85c \uc218\ub3c4\uc0b0\uacfc \uac00\uc57c\uc0b0, \ub3d9\ucabd\uc73c\ub85c \uae08\uc624\uc0b0, \ubd81\ucabd\uc73c\ub85c \ud3ec\uc131\ubd09\uc774 \ubcf4\uc778\ub2e4. \ud669\uc545\uc0b0\uc758 \uc8fc\ubd09\uc744 \ube44\ub86f\ud55c \ubd09\uc6b0\ub9ac\ub4e4\uc740 \uc800\ub9c8\ub2e4 \uc0b0\uc904\uae30\ub97c \ub3d9\ucabd\uc73c\ub85c \ubed7\uc5b4\ub0b4\ub824 \ub0b4\uc6d0\uacc4\uace1, \uc6b4\uc218\uacc4\uace1\uc744 \ud615\uc131\ud558\uace0 \uc788\uc73c\uba70, \ub450 \uacc4\uace1\uc774 \ud569\uc218\ub418\ub294 \uc9c0\uc810\uc5d0 \uc9c1\uc9c0\uc0ac\uac00 \uc704\uce58\ud574 \ub9c8\uce58 \ud669\uc545\uc0b0\uc758 \ubaa8\ub4e0 \uc815\uae30\ub97c \ubc1b\uace0 \uc788\ub294 \ub290\ub08c\uc744 \uc900\ub2e4.'), STRINGDECODE('\uc804\uccb4\uc801\uc778 \uc0b0\uc138\ub294 \ud2b9\uc9d5 \uc5c6\uc774 \uc644\ub9cc\ud55c \ud3b8\uc774\ub098 \uc0b0\ub9bc\uc774 \uc6b8\ucc3d\ud558\uace0 \uc0b0 \ub3d9\ucabd\uc73c\ub85c \ud758\ub7ec\ub0b4\ub9ac\ub294 \uacc4\uace1\uc740 \uacf3\uacf3\uc5d0 \ud3ed\ud3ec\uc640 \uc18c\ub97c \uc774\ub904 \uacc4\uace1\ubbf8\uac00 \uc544\ub984\ub2e4\uc6b4 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ud2b9\ud788 \uc9c1\uc9c0\uc0ac \uc11c\ucabd 200m \uc9c0\uc810\uc5d0 \uc788\ub294 \ucc9c\ub8e1\ub300\ubd80\ud130 \ud3bc\uccd0\uc9c0\ub294 \ub2a5\uc5ec\uacc4\uace1\uc740 \ub300\ud45c\uc801\uc778 \uacc4\uace1\uc73c\ub85c \ubd04\ucca0\uc5d0\ub294 \uc9c4\ub2ec\ub798, \ubc9a\uaf43, \uc0b0\ubaa9\ub828\uc774 \uc720\uba85.'), STRINGDECODE('\ud559\uc774 \uba3c\uc800 \uc54c\uc544 \ubcf8 \uad6d\ubbfc \uad00\uad11\uc9c0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<BR>\uc9c1\uc9c0\uc0ac \uc8fc\ubcc0\uc0c1\uac00\uc5d0 \uc219\uc2dd\ud560 \uacf3\uc774 \ub9ce\ub2e4. <BR>\uccad\uc0b0\uace0\uc744 \u260e054-436-8030<BR>\uc0b0\ucc44\uacbd\ubd81\uc2dd\ub2f9 \u260e054-436-2455<BR>\uc0b0\ub9c8\uc744\ubbfc\ubc15 \u260e054-436-6811<BR>\uccad\uc194\ubbfc\ubc15 \u260e054-436-3408<BR><BR>\u25cf \uc774\uc6a9\ubb38\uc758<BR>\ubb38\ud654\uacf5\ubcf4\uc2e4 \u260e054-420-6065'), STRINGDECODE('\uae30\uc810\uc740 \uae40\ucc9c\uc774\ub2e4. <BR>\uae40\ucc9c\uc5ed\uc5d0\uc11c \uc9c1\uc9c0\uc0ac \uc785\uad6c\uae4c\uc9c0 \uc77c\ubc18\ubc84\uc2a4(11\ubc88)\uc640 \uc88c\uc11d\ubc84\uc2a4(111\ubc88)\uac00 06:00\ubd80\ud130 22:40\uae4c\uc9c0 10\ubd84 \uac04\uaca9\uc73c\ub85c \uc6b4\ud589\ud55c\ub2e4. 20\ubd84 \uc18c\uc694'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(99, TIMESTAMP '2019-08-14 15:46:34.948', TIMESTAMP '2019-08-14 15:47:38.545', '47', STRINGDECODE('<p>\ubc31\ub450\ub300\uac04\uc758 \ud55c \ubf08\ub300\ub97c \uc774\ub8e8\uba70 \ub192\uc774 \uc19f\uc544\uc788\ub294 \uc774 \uc0b0\uc740 \uc774\ub984\uc774 \uc5ec\ub7ff \uc788\ub2e4. \ud669\uc815\uc0b0(\u9ec3\u5ead\u5c71)\uc774\ub77c\uace0\ub3c4 \ud558\uace0 \uc791\uc131\uc0b0(\u9d72\u57ce\u5c71), \ub610\ub294 \ud669\uc7a5\ubd09\uc0b0 \ub4f1\uc73c\ub85c \ud45c\uae30\ub418\uace0 \uc788\uc73c\ub098 \ud669\uc7a5\ubaa9\uc774 \ub9ce\uc544 \ud669\uc7a5\uc0b0\uc73c\ub85c \ud1b5\uce6d\ub418\uace0 \uc788\ub2e4.<br><br>\uc18c\ub098\ubb34\uc758 \ud55c \uc885\ub958\uc778 \ud669\uc7a5\ubaa9\uc740 \uade0\uc5f4\uc774 \uc801\uace0 \ub2e8\ub2e8\ud574 \uc784\uae08\uc758 \uad00(\u68fa)\uc774\ub098 \ub300\uad90\uc744 \ub9cc\ub4dc\ub294\ub370 \ub9ce\uc774 \uc4f0\uc778 \uadc0\ud55c \ub098\ubb34\uc774\ub2e4. \uc774 \ub54c\ubb38\uc5d0 \uc870\uc120\uc870 \uc219\uc885 \ub54c\uc778 1680\ub144\uc5d0 \uc774 \uc0b0\uc5d0\uc11c\uc758 \ubc8c\ubaa9\uacfc \uac1c\uac04\uc744 \uae08\uc9c0\ud558\ub294 \ubd09\uc0b0(\u5c01\u5c71) \ud45c\uc11d\uc774 \ub3d9\ub85c\uba74 \uba85\uc804\ub9ac \ubc8c\ucc9c\uacc4\uace1 \ud558\ub958\uc5d0 \uc138\uc6cc\uc84c\ub2e4. \ub610 \uc0b0 \uae4a\uc219\ud55c \ubb38\uc548\uace8 \uacc4\uace1\uc5d0\ub294 \uc6b0\ub78c\ud55c \uc11d\ubb38\uc774 \uc788\ub294 \uc791\uc131\uc0b0\uc131\uc774 \uc788\ub294\ub370 \ucd95\uc870\ubc29\uc2dd\uc73c\ub85c \ubcf4\uc544 \uace0\uad6c\ub824 \uc2dc\ub300 \uac83\uc73c\ub85c \ucd94\uc815\ub41c\ub2e4. \uc0b0 \uc544\ub798 \uae08\ucc9c\uc5d0\ub294 \ubc84\ub4e4\uce58 \ub4f1 1\uae09\uc218 \uc5b4\ub958\uac00 \uc0b4\uace0 \uc788\ub2e4.</p>'), STRINGDECODE('<p>\u2460\uc548\uc0b0\ub2e4\ub9ac-\uc791\uc740 \ucc28\uac13\uc7ac-\uba67\ub4f1\ubc14\uc704-\uc815\uc0c1-\uac10\ud22c\ubd09-\uce58\ub9c8\ubc14\uc704-\ud3d0\ub9e5\uc774\uc7ac-929\ubd09-\ubc8c\uc7ac(\ucd1d 5\uc2dc\uac04)<br>\u2461\uc548\uc0b0\ub2e4\ub9ac-\uc9c0\uc740\uad11\uc0b0-\ubca0\ubc14\uc704-\uc815\uc0c1-\uac10\ud22c\ubd09-\ud669\uc7a5\uc7ac-\ubb38\uc548\uace8 \ubc18\uc11d-\uc791\uc131-\ubc29\uace1\ub9ac(\ucd1d 4\uc2dc\uac04 50\ubd84)<br>\u2462\uc0dd\ub2ec\ubd84\uad50-\ud1a0\uc2dc\uace8-\ud669\uc7a5\uc7ac-\uac10\ud22c\ubd09-\uc815\uc0c1-\uc791\uc740 \ucc28\uac13\uc7ac-\ubca0\ucc3d\uace8-\uc548\uc0b0\ub2e4\ub9ac(\ucd1d 4\uc2dc\uac04 10\ubd84)</p>'), '36.783007', '128.296252', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \ubb38\uacbd\uc2dc \ub3d9\ub85c\uba74'), 2707040400, 1079, STRINGDECODE('\ud669\uc7a5\uc0b0'), STRINGDECODE('<p>\ud669\uc7a5\ubaa9\uc774 \ub9ce\uc544 \ud669\uc7a5\uc0b0\uc774\ub2e4. \ud669\uc7a5\ubaa9\uc740 \uc1a1\uc9c4\uc774 \uc548\uc73c\ub85c \uc2a4\uba70\ub4e4\uc5b4 \uc2ec\uc7ac(\u5fc3\u6750)\uac00 \ub178\ub780 \uc18c\ub098\ubb34\ub85c \uc870\uc120\uc2dc\ub300\uc5d0\ub294 \uad6d\uac00\uc5d0\uc11c \uad00\ub9ac\ud588\uc73c\uba70 \uadf8 \uc99d\uac70, \ud669\uc7a5\uae08\ud45c(\u9ec3\u8178\u7981\u8868) \ud45c\uc11d\uc774 \ubd81\ucabd \uc0b0\uc790\ub77d \uba85\uc804\ub9ac\uc5d0 \uc788\ub2e4. <br><br>\ubc31\ub450\ub300\uac04 \ub9c8\ub8e8\uae08\uc774 \uc9c0\ub098\uac00\ub294 \uc0b0\uc73c\ub85c \uc678\uad00\uc740 \uc369 \ube7c\uc5b4\ub098\uc9c4 \uc54a\uc9c0\ub9cc \ub2a5\uc120 \uc5ec\uae30\uc800\uae30\uc5d0 \ubc14\uc704\uac00 \ub9ce\ub2e4. \ubca0\ubc14\uc704, \uba67\ub4f1\ubc14\uc704, \ud22c\uad6c\ubd09, \uac10\ud22c\ubd09, \uc218\ub9ac\ubd09, \uce58\ub9c8\ubc14\uc704 \ub4f1\uc774\uba70 \uc218\ub9ac\ubd09\uc5d0\ub294 \uae34 \uc554\ub989\ub3c4 \uc788\ub2e4. \ub3d9\uc11c\ub85c \uae38\uac8c \ubed7\uc740 \ud651\uc0b0\uc778\ub370\ub3c4 \ubb38\uc548\uace8, \uc6b0\ub9dd\uace8, \ud1a0\uc2dc\uace8 \ub4f1\uc758 \uc88b\uc740 \uacc4\uace1\uc774 \uc788\ub2e4. \ud2b9\ud788 \ubb38\uc548\uace8\uc740 \uc218\ub7c9\uc774 \ub9ce\uc744 \ubfd0 \uc544\ub2c8\ub77c 50\ud3c9\ucbe4\uc758 \ubc18\uc11d\ub3c4 \uc788\ub2e4. \uc0c1\uc120\uc554, \uc911\uc120\uc554, \ud558\uc120\uc554\uc774 \uc788\ub294 \ubd81\ucabd \ub2e8\uc591\ucc9c \uc720\uc5ed\uacfc \ud568\uaed8 \uc6d4\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc758 \ub3d9\ucabd \ub044\ud2b8\uba38\ub9ac\ub2e4. <br><br>\uc791\uc131(\u9d72\u57ce)\uc774 \uc788\uc5b4 \uc791\uc131\uc0b0\uc774\ub77c\uace0\ub3c4 \ud55c\ub2e4. \ud3ec\uace1\uc2dd(\u62b1\u8c37\u5f0f)\uc0b0\uc131\uc73c\ub85c \uc131\ubb38\uc774 \ube44\uad50\uc801 \uc798 \ub0a8\uc544\uc788\uc73c\uba70 \uc774\ub7f0 \uae4c\ub2ed\uc5d0 \uadf8 \uace8\uc9dc\uae30\uc5d0 \ubb38\uc548\uace8\uc774\ub77c\ub294 \uc774\ub984\uc774 \ubd99\uc5c8\ub2e4. \uc0b0\uc740 \uc0bc\uad6d\uc2dc\ub300\uc5d0 \uace0\uad6c\ub824\uc640 \uc2e0\ub77c\uc758 \uacbd\uacc4\uc600\uace0 \uc0b0\uc744 \uc628\uc804\ud788 \ud488\uc740 \ub3d9\ub85c\uba74\uc740 \uace0\ub824\uc2dc\ub300\uae4c\uc9c0 \uc791\uc131\ud604\uc73c\ub85c \ubd88\ub838\ub2e4.</p>'), STRINGDECODE('<p>\uc6b8\ucc3d\ud55c \uc0b0\ub9bc\uc774 \uc554\ubcbd\uacfc \uc5b4\uc6b0\ub7ec\uc838 \uacbd\uad00\uc774 \uc544\ub984\ub2e4\uc6b0\uba70 \ud669\uc7a5\ubaa9\uc774 \uc720\uba85\ud558\uace0 \uc870\uc120\uc2dc\ub300 \ubd09\uc0b0 \ud45c\uc9c0\uc11d\uc774 \uc788\ub294 \ub4f1 \uacbd\uad00 \ubc0f \uc0b0\ub9bc\ubb38\ud654\uc801 \uce21\uba74\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815, \ub3d9\uad6d\uc5ec\uc9c0\uc2b9\ub78c, \ub300\ub3d9\uc9c0\uc9c0, \uc608\ucc9c\uad70 \uc74d\uc9c0 \ub4f1\uc5d0\ub294 \uc791\uc131\uc0b0\uc73c\ub85c \ud45c\uae30</p>'), STRINGDECODE('\uc61b \ub300\uad90 \uc9c0\uc5c8\ub358 \ud669\uc7a5\ubaa9 \uc8fc\uc0b0\uc9c0'), STRINGDECODE('<p>\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<br>\ub3d9\ub85c\uba74\uc5d0\ub294 \uc798 \ub370\uac00 \uc5c6\ub2e4. <br>\uac00\uc7a5 \uac00\uae4c\uc6b4 \ub370\ub294 \uacbd\ucc9c\ub310 \ub0b4 \ubc29\uac08\ub85c(\u260e054-552-8811)\uc774\uace0 \ub2e4\uc74c\uc740 \ubb38\uacbd\uc0c8\uc7ac\uc720\uc2a4\ud638\uc2a4\ud154(\u260e054-571-5533)\uc774\ub098 \ud0b9\ubaa8\ud154(\u260e054-571-5558) \uac19\uc740 \ubb38\uacbd\uc74d\ub0b4 \uc219\ubc15\uc2dc\uc124, \uadf8\ub9ac\uace0 \uc911\uc120\uc554 \uadfc\ucc98\uc758 \uc0bc\uc9c4\ubbfc\ubc15(\u260e043-421-4411), \uc0ac\uc778\uc554 \uc55e\uc758 \uc885\ub85c\ubbfc\ubc15(\u260e043-421-0912), \ub300\uad6c\ubbfc\ubc15(\u260e043-421-0913) \ub4f1\uc774\ub2e4. <br><br>\u25cf \uc774\uc6a9\ubb38\uc758<br>\ubb38\uacbd\uc2dc\uccad \ubb38\ud654\uad00\uad11\uacfc \u260e054-430-2390</p>'), STRINGDECODE('<p>\ucd9c\ubc1c\uc810 : \uc810\ucd0c<br>\uc810\ucd0c\uc2dc\uc678\ubc84\uc2a4\uc815\ub958\uc7a5\u2192\ub3d9\ub85c : 1\uc77c 10\ud68c \uc6b4\ud589<br>\ub3d9\ub85c\u2192\uc0dd\ub2ec : 1\uc77c 4\ud68c \uc6b4\ud589</p>'));
INSERT INTO "PUBLIC"."MOUNTAIN" VALUES(100, TIMESTAMP '2019-08-14 15:46:34.948', TIMESTAMP '2019-08-14 15:47:39.138', '47', STRINGDECODE('\ud76c\uc591\uc0b0\uc740 \ucda9\ubd81\uc758 \uad34\uc0b0\uacfc \uacbd\ubd81\uc758 \ubb38\uacbd\uc5d0 \uac78\uccd0 \uac70\ub300\ud55c \ud558\ub098\uc758 \ubc14\uc704\ub369\uc774\ub85c \uc774\ub8e8\uc5b4\uc9c4 \ub4ef \ub2f9\ub2f9\ud55c \uc704\uc138\ub97c \ubf50\ub0b4\uace0 \uc788\ub294 \uc0b0\uc774\ub2e4. \uc815\uc0c1\uc5d0\uc11c \ubd81\ucabd\uc740 \uc2dc\ub8e8\ubd09, \uc11c\ucabd\uc73c\ub85c\ub294 \uad6c\uc655\ubd09\uc73c\ub85c \uc774\uc5b4\uc838 \ub098\uac00\uba70 \uae30\uc138\ub97c \uc9c4\uc815\uc2dc\ud0a4\uc9c0\ub9cc, \ub3d9\ub0a8\uc11c\ucabd\uc73c\ub85c \ub178\ucd9c\ub41c \uc554\uc7a5\ub2a5 \uace1\ud074\ub77c\uc774\ubc0d \ucf54\uc2a4\ub85c \ub2e4\uc2dc \uc5c6\uc774 \uc88b\uc544 \uc774\ubbf8 \uc5ec\ub7ec\uac1c\uc758 \ucf54\uc2a4\uac00 \uac1c\ubc1c\ub418\uc5b4 \uc788\ub2e4. <br><br>\ubcd1\ud48d\ucc98\ub7fc \ub458\ub7ec\uc313\uc778 \uac70\ub300\ud55c \ud654\uac15\uc554\ubcbd\uc740 \uc124\uc545\uc0b0 \uc6b8\uc0b0\ubc14\uc704\uc5d0 \ud544\uc801\ud560 \ub9cc \ud558\uba70, \uc554\ubcbd \ud558\ub2e8\ubd80\uc778 2\ubc31\uc5ecm\uc758 \uc2ac\ub7a9\uacfc \uc554\ubcbd\uc740 \uc704\uc555\uac10\uc744 \ub290\ub07c\uae30\uc5d0 \ucda9\ubd84\ud558\ub2e4. \uc815\uc0c1 \ub0a8\ucabd \uc544\ub798 \uc720\uc11c\uae4a\uc740 \ubd09\uc554\uc0ac\uac00 \uc788\uace0, \uc625\uc11d\ub300\uc640 \uadf8 \uc8fc\ubcc0 \uc77c\ub300\uc5d0 \ud3bc\uccd0\uc9c4 \uc625\uc11d\uacc4\uace1\uc758 \ub6f0\uc5b4\ub09c \uc815\uacbd\uc740 \ub4f1\uc0b0\uc758 \ub610 \ub2e4\ub978 \ub9db\uc744 \uc900\ub2e4.<br><br> <span class=\"bu\">\u203b \ubd09\uc554\uc0ac\ub97c \uc9c0\ub098\ub294 \ub4f1\uc0b0\ucf54\uc2a4\ub294 \ubd09\uc554\uc0ac\uc5d0\uc11c \uc77c\ubc18\uc778\ub4e4\uc758 \ucd9c\uc785\uc744 \uc5c4\uaca9\ud788 \uc81c\ud55c\ud558\uace0 \uc788\uc74c</span>'), STRINGDECODE('\uc740\ud2f0\ub9c8\uc744-\uc2dc\ub8e8\ubd09 \uc548\ubd80-\uc131\ud130-\uc815\uc0c1 (\ucd1d 3\uc2dc\uac04 \uc18c\uc694)'), '36.65997', '128.010944', STRINGDECODE('\uacbd\uc0c1\ubd81\ub3c4 \ubb38\uacbd\uc2dc \uac00\uc740\uc74d, \ucda9\uccad\ubd81\ub3c4 \uad34\uc0b0\uad70 \uc5f0\ud48d\uba74,'), 2301072800, 996, STRINGDECODE('\ud76c\uc591\uc0b0'), STRINGDECODE('&lt;\ubc31\ub450\ub300\uac04\uc758 \uc2e0\ub839\uc2a4\ub7f0 \uc554\ubd09&gt;<br>\ud76c\uc591\uc0b0(999m)\uc740 \uacbd\ubd81 \ubb38\uacbd\uc2dc \uac00\uc740\uc74d\uacfc \ucda9\ubd81 \uad34\uc0b0\uad70 \uc5f0\ud48d\uba74\uc758 \uacbd\uacc4\ub97c \uc774\ub8e8\uba70 \ubc31\ub450\ub300\uac04\uc5d0 \uc6b0\ub69d \uc19f\uc740 \uc2e0\ub839\uc2a4\ub7f0 \uc554\ubd09\uc774\ub2e4. \uc778\uadfc\uc758 \uc870\ub839\uc0b0(1026m)\uc774\ub098 \ubc31\ud654\uc0b0(1064m)\ubcf4\ub2e4 \ub0ae\uc73c\ub098 \ube7c\uc5b4\ub098\uae30\ub85c \ubcf4\uc790\uba74 \ub2e8\uc5f0 \uc73c\ub738\uc774\ub2e4. \ud76c\uc591\uc0b0 \ubd09\uc554\uc0ac \uac1c\uc0b0\uc870\uc778 \uc2e0\ub77c\uc2dc\ub300\uc758 \uc2b9\ub824 \uc9c0\uc99d\ub300\uc0ac(824~884\ub144)\ub294 \"\"\uc0b0\uc740 \uc0ac\ubc29\uc5d0 \ubcd1\ud48d\ucc98\ub7fc \ub458\ub7ec\uccd0\uc838 \uc788\uc73c\ub2c8 \ub9c8\uce58 \ubd09\ud669\uc758 \ub0a0\uac1c\uac00 \uad6c\ub984\uc744 \uce58\uba70 \uc62c\ub77c\uac00\ub294 \ub4ef\ud558\uace0, \uacc4\uace1\ubb3c\uc740 \ubc31\uacb9\uc73c\ub85c \ub760\ucc98\ub7fc \ub418\uc5b4\uc788\uc73c\ub2c8 \uc6a9\uc758 \ud5c8\ub9ac\uac00 \ub3cc\uc5d0 \uc5ce\ub4dc\ub824 \uc788\ub294 \ub4ef\ud558\ub2e4\"\"\uace0 \ud76c\uc591\uc0b0 \uc790\ud0dc\ub97c \uce6d\uc1a1\ud588\ub2e4\uace0 \uc804\ud55c\ub2e4. <br><br>\uc815\uc0c1\uc5d0\uc11c \uc870\ub9dd\uc740 \uc7a5\ucf8c\ud558\uace0 \uc544\ub984\ub2f5\ub2e4. \ubc1c \uc544\ub798\ub85c \ubd09\uc554\uc0ac\uc640 \ubd09\uc554\uc6a9\uace1\uc774 \ubcf4\uc774\uace0, \uadf8 \ub108\uba38\ub85c \ub300\uc57c\uc0b0\uacfc \uc18d\ub9ac\uc0b0 \uc904\uae30\uac00 \ub0a0\uce74\ub86d\uac8c \uc19f\uc544\uc788\ub2e4. \uc11c\ucabd\uc73c\ub85c\ub294 \uc7a5\uc131\ubd09, \uc545\ud76c\ubd09, \uad70\uc790\uc0b0 \ub4f1 \ucda9\ubd81 \ub0b4\ub959\uc758 \uc0b0\ub4e4\uc774 \ubcd1\ud48d\ucc98\ub7fc \ub458\ub7ec\uc838 \uc788\ub2e4. \ub3d9\ubd81\ucabd \uc870\ub9dd\uc740 \ub05d\uc5c6\uc774 \ud3bc\uccd0\uc9c0\ub294 \ubc31\ud654\uc0b0, \uc6b4\ub2ec\uc0b0, \uc8fc\ud758\uc0b0 \uc904\uae30\uac00 \uc5f0\uaf43\uc78e\uc774 \ud53c\uc5b4\ub09c \ub4ef \uc544\ub984\ub2f5\uae30 \uadf8\uc9c0\uc5c6\ub2e4.'), STRINGDECODE('\uc0b0 \uc804\uccb4\uac00 \ud558\ub098\uc758 \ubc14\uc704\ucc98\ub7fc \ubcf4\uc774\uace0 \ubc14\uc704 \ub0ad\ub5a0\ub7ec\uc9c0\ub4e4\uc774 \ud558\uc597\uac8c \ub4dc\ub7ec\ub098 \uc788\uc5b4 \uc8fc\ubcc0\uc758 \uc0b0\uc5d0\uc11c\ubfd0\ub9cc \uc544\ub2c8\ub77c \uba3c \uc0b0\uc5d0\uc11c\ub3c4 \uc27d\uac8c \uc54c\uc544\ubcfc \uc218 \uc788\uc73c\uba70 \uae30\uc554\uad34\uc11d\uacfc \ud48d\ubd80\ud55c \uc218\ub7c9\uc774 \uc5b4\uc6b0\ub7ec\uc9c4 \ubc31\uc6b4\uace1 \ub4f1 \uacbd\uad00\uc774 \uc218\ub824\ud558\uace0 \ub9c8\uc560\ubcf8\uc88c\uc0c1 \ub4f1 \uc5ed\uc0ac\uc720\uc801\uc774 \uc788\ub294 \uc810 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc120\uc815'), STRINGDECODE('\uc124\uc545\uc0b0 \uc6b8\uc0b0\ubc14\uc704\uc5d0 \ud544\uc801\ud560 \uac70\ub300\ud55c \ubc14\uc704\uc0b0'), STRINGDECODE('\u25cf \uc219\uc2dd \ubc0f \uae30\ud0c0\uc815\ubcf4<br>\uc740\ud2f0\ub9c8\uc744 \ubd80\ub140\ud68c\uad6c\ud310\uc7a5(\u260e043-833-5708)\uc5d0\uc11c \uc2dd\uc0ac\uc640 \ubbfc\ubc15\uc744 \ud560 \uc218 \uc788\ub2e4. <br>\uc6d0\ud48d\ub9ac\uc5d0 \uc219\ubc15\uc2dc\uc124\uc774 \ub9ce\uc774 \uc788\ub2e4. <br>\uc0c8\uc7ac\ud30c\ud06c \u260e043-833-8901<br>\uc27c\ud130\ubbfc\ubc15 \u260e043-833-6869<br>\uc0b0\uadf8\ub9bc\ud638\ud154 \u260e043-833-8818<br><br>\u25cf \uc774\uc6a9\ubb38\uc758<br>\uc0b0\ub9bc\uad00\uad11\uacfc \u260e043-830-3727'), STRINGDECODE('\uae30\uc810\uc740 \uc5f0\ud48d\uc774\ub2e4. <br>\uc5f0\ud48d\uc5d0\uc11c \uc740\ud2f0\ub9c8\uc744\ud589 \uc2dc\ub0b4\ubc84\uc2a4\ub294 08:10, 13:10, 18:50 1\uc77c 3\ud68c \uc6b4\ud589\ud55c\ub2e4. \uc774 \ubc84\uc2a4\ub294 \uad34\uc0b0(\u260e\uc544\uc131\uad50\ud1b5 043-834-3025)\uc5d0\uc11c \ucd9c\ubc1c\ud55c\ub2e4.<br>- \ub3d9\uc11c\uc6b8\u2192\ucda9\uc8fc : 1\uc77c 20\ud68c \uc6b4\ud589 &gt;\ucda9\uc8fc\u2192\uc5f0\ud48d :1\uc77c 15\ud68c \uc6b4\ud589<br>- \ub3d9\uc11c\uc6b8\u2192\uad34\uc0b0 1\uc77c 12\ud68c \uc6b4\ud589'));
